INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 1, 'Table genealogique de Jesus-Christ, fils de David, fils d\'Abraham');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 2, 'Abraham engendra Isaac ; Isaac engendra Jacob ; Jacob engendra Juda et ses freres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 3, 'Juda engendra Phares et Zara quil eut de Thamar ; Phares engendra Esrom ; Esrom engendra Aram ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 4, 'Aram engendra Aminadab ; Aminadab engendra Naasson ; Naasson engendra Salmon ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 5, 'Salmon engendra Boes, qu\'il eut de Rachab ; Boes engendra Jobed, qu\'il eut de Ruth ; Jobed engendra Jessai ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 6, 'Jessai engendra David, le roi David engendra Salomon, quil eut de la femme d\'Urie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 7, 'Salomon engendra Roboam ; Roboam engendra Abia ; Abia engendra Asaph ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 8, 'Asaph engendra Josaphat ; Josaphat engendra Joram ; Joram engendra Ozeia ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 9, 'Ozeia engendra Joatham ; Joatham engendra Achaz ; Achaz engendra Ezechias ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 10, 'Ezechias engendra Manasse ; Manasse engendra Amon ; Amon engendra Josias ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 11, 'Josias engendra Jechonias et ses freres (c\'etait au temps de la deportation a Babylone).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 12, 'Apres la deportation a Babylone : Jechonias engendra Salathiel ; Salathiel engendra Zorobabel ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 13, 'Zorobabel engendra Abioud ; Abioud engendra Eliakeim ; Eliakeim engendra Azor ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 14, 'Azor engendra Sadok ; Sadok engendra Acheim ; Acheim engendra Elioud ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 15, 'Elioud engendra Eleazar ; Eleazar engendra Matthan ; Matthan engendra Jacob ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 16, 'Jacob engendra Joseph, l\'epoux de Marie, de laquelle est ne Jesus, dit le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 17, 'En tout donc, d\'Abraham jusqu\'a David : quatorze generations.  De David jusqu\'a la deportation a Babylone : quatorze generations.  De la deportation a Babylone jusqu\'au Christ: quatorze generations.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 18, 'Voici comment Jesus-Christ vint au monde : Marie, sa mere, apres avoir ete fiancee a Joseph et avant qu\'ils eussent vecu ensemble, se trouva enceinte par l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 19, 'Or Joseph, son mari, etait un juste. Ne voulant pas lui faire affront, il resolut de rompre avec elle sans bruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 20, 'Comme il etait en cette pensee, un ange du Seigneur lui apparut en songe et lui dit : Joseph, fils de David, n\'hesite pas a recevoir chez toi, ta femme, Marie, car ce qu\'elle a concu est de l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 21, 'Elle mettra un fils au monde et tu lui donneras le nom de Jesus ; c\'est lui, en effet, qui sauvera son peuple de ses peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 22, '(Or tout cela arriva afin que fut accompli ce que le Seigneur avait dit par le prophete en ces mots :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 23, 'Voici la vierge sera enceinte ; elle mettra un fils au monde ; Et on lui donnera le nom d\'Emmanuel ce qui signifie : Dieu avec nous.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 24, 'Joseph, a son reveil, agit d\'apres les ordres de l\'ange du Seigneur : il recut sa femme chez lui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 1, 25, 'et il ne la connut point avant qu\'elle eut mis au monde un fils auquel il donna le nom de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 1, 'Jesus etant ne a Bethleem, dans la Judee, aux jours du roi Herode, des mages arriverent d\'Orient a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 2, 'Ou est, demanderent-ils, le nouveau-ne, roi des Juifs? Nous avons vu son etoile en Orient et nous sommes venus pour l\'adorer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 3, 'En entendant de telles paroles, le roi Herode fut trouble et, avec lui, toute la ville de Jerusalem ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 4, 'il convoqua tous les chefs des pretres et scribes du peuple et s\'informa d\'eux ou devait naitre le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 5, 'Ils lui repondirent : A Bethleem dans la Judee. (Voici, en effet, ce qui a ete ecrit par le prophete :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 6, 'Et toi, Bethleem, terre de Juda, Tu n\'es certainement pas le plus petit des chefs-lieux de Juda, Car de toi sortira un conducteur Qui paitra mon peuple, Israel.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 7, 'La dessus, Herode fit appeler les mages en secret et s\'informa aupres d\'eux de l\'epoque ou l\'etoile avait paru.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 8, 'Puis il les envoya a Bethleem. Allez, leur dit-il, prenez des informations exactes sur cet enfant et, quand vous l\'aurez trouve, faites-le moi savoir, pour que, moi aussi, j\'aille l\'adorer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 9, 'Sur ces paroles du roi, ils partirent ; et l\'etoile qu\'ils avaient vue en Orient les precedait jusqu\'a ce que, parvenue au-dessus du lieu ou etait l\'enfant, elle s\'arreta.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 10, 'A la vue de l\'etoile, ils se rejouirent d\'une grande joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 11, 'Ils entrerent dans la maison, ils virent l\'enfant avec Marie sa mere ; et, se prosternant, ils l\'adorerent. Ensuite, ouvrant leurs tresors, ils lui offrirent en present de l\'or, de l\'encens et de la myrrhe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 12, 'Ayant ete miraculeusement avertis en songe de ne pas revenir vers Herode, ils regagnerent leur pays par un autre chemin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 13, 'Apres leur depart, un ange du Seigneur apparut en songe a Joseph et lui dit : Leve-toi! prends l\'enfant et sa mere, et fuis en Egypte ou tu resteras jusqu\'a ce que je te reparle ; car Herode va rechercher l\'enfant afin de le faire perir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 14, 'Cette nuit meme, Joseph se leva, prit l\'enfant et sa mere et partit pour l\'Egypte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 15, 'Il y resta jusqu\'a la mort d\'Herode. (C\'etait afin que fut accompli ce que le Seigneur avait dit par le prophete en ces mots : J\'ai rappele mon fils d\'Egypte.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 16, 'Quant a Herode, se voyant joue par les mages, il se mit fort en colere, et envoya tuer tous les enfants de Bethleem et de ses environs ages de deux ans et au-dessous suivant l\'epoque dont il s\'etait informe aupres des mages.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 17, '(C\'est alors que fut accompli ce qui avait ete dit par le prophete Jeremie en ces mots :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 18, 'Une voix a ete entendue dans Rama, Des pleurs et de longs sanglots ; C\'est Rachel pleurant ses enfants, Et elle ne veut pas etre consolee parce qu\'ils ne sont plus.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 19, 'Quand Herode fut mort, un ange du Seigneur apparut en songe a Joseph en Egypte');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 20, 'et lui dit : Leve-toi, prends l\'enfant et sa mere et retourne dans le pays d\'Israel ; car ceux qui en voulaient a la vie de l\'enfant sont morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 21, 'Joseph se levant donc prit l\'enfant et sa mere et rentra dans le pays d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 22, 'Mais ayant appris qu\'Archelaus regnait en Judee a la place d\'Herode son pere, il apprehenda d\'y aller. Miraculeusement averti en songe, il partit pour la province de Galilee');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 2, 23, 'et, y etant arrive, il habita une ville appelee Nazareth. (C\'etait afin que fut accompli ce qui a ete dit par les prophetes : Il sera appele Nazareen).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 1, 'En ces jours-la, parut Jean-Baptiste, prechant dans le desert de la Judee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 2, 'Il disait : Repentez-vous, car le Royaume des cieux est proche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 3, '(C\'est Jean que le prophete Esaie a annonce quand il a dit: Une voix crie dans le desert : Preparez la voie du Seigneur ; Aplanissez ses sentiers.  )');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 4, 'Il avait un vetement de poil de chameau, et une ceinture de cuir autour des reins. Sa nourriture etait des sauterelles et du miel sauvage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 5, 'Alors accouraient a lui et la ville de Jerusalem et toute la Judee et toute la region du Jourdain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 6, 'Tous etaient baptises par lui dans le fleuve, en confessant leurs peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 7, 'Mais, voyant beaucoup de Pharisiens et de Sadduceens venir a son bapteme, il leur dit : Engeance de viperes, qui vous a appris a fuir la colere a venir?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 8, 'Produisez donc un fruit digne de la repentance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 9, 'et ne pretendez pas dire en vous-memes : nous avons pour pere Abraham ; car, je vous le dis, Dieu peut faire surgir des pierres que voici des enfants a Abraham.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 10, 'Deja la cognee touche la racine des arbres. Tout arbre donc qui ne produit pas de bon fruit sera coupe et jete au feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 11, 'Moi je vous baptise avec l\'eau pour la repentance, mais celui qui doit venir apres moi est plus puissant que moi et je ne suis pas meme digne d\'etre le porteur de ses sandales ; lui vous baptisera avec l\'Esprit saints et le feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 12, 'Il a le van dans la main, et il nettoiera son aire, et rassemblera son froment dans le grenier, mais la paille, il la brulera au feu inextinguible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 13, 'Ce fut alors que Jesus vint de la Galilee au Jourdain, vers Jean, pour etre baptise par lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 14, 'Mais Jean s\'en defendait vivement : C\'est moi, disait-il, qui ai besoin d\'etre baptise par toi, et c\'est toi qui viens a moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 15, 'Jesus lui repondit par ces paroles : (Laisse-moi faire pour l\'heure presente, car c\'est ainsi qu\'il nous convient d\'accomplir toute justice. Alors il le laissa faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 16, 'Jesus, ayant ete baptise, sortit aussitot de l\'eau, et voila que les cieux furent ouverts, et il vit l\'Esprit de Dieu descendre sous la figure d\'une colombe et venir sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 3, 17, 'En meme temps une voix vint des cieux disant : Celui-ci est mon Fils, le bien-aime en qui je me complais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 1, 'Jesus ensuite fut conduit par l\'Esprit dans le desert pour y etre tente du diable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 2, 'Lorsqu\'il eut jeune pendant quarante jours et quarante nuits, il finit par avoir faim.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 3, 'Si tu es Fils de Dieu, lui dit le tentateur en s\'approchant, dis que ces pierres deviennent des pains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 4, 'Mais Jesus lui fit cette reponse : Il est ecrit : Ce n\'est pas de pain seulement que vivra l\'homme, mais de toute parole qui sort de la bouche de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 5, 'Alors le diable le mena avec lui dans la ville sainte et le placa sur le faite du Temple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 6, 'Si tu es Fils de Dieu, lui dit-il, jette-toi en bas ! car il est ecrit : A ses anges il donnera des ordres a ton egard ; Ils te soutiendront de leurs mains De peur que ton pied ne se heurte contre une pierre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 7, 'Jesus lui repondit : Il est egalement ecrit : Tu ne tenteras point le Seigneur ton Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 8, 'Le diable le mena encore avec lui sur une montagne d\'une immense hauteur et lui montra tous les royaumes du monde et leur gloire');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 9, 'et lui dit : Tout cela, je te le donnerai, si, tombant a mes pieds, tu m\'adores.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 10, 'Jesus lui repondit : Arriere, Satan! car il est ecrit : Tu adoreras le Seigneur ton Dieu et c\'est a lui seul que tu rendras un culte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 11, 'Alors le diable le laissa ; et voila que des anges s\'approcherent et ils le servaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 12, 'Ayant appris que Jean avait ete livre, Jesus se retira en Galilee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 13, 'et, ayant quitte Nazareth, il vint s\'etablir a Capharnaum, ville du rivage sur les confins de Zabulon et de Nephthali');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 14, '(C\'etait afin que fut accompli ce qui avait ete dit par le prophete Esaie :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 15, 'Terre de Zabulon et terre de Nephthali sur le chemin de la mer, au dela du Jourdain, Galilee des paiens!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 16, 'Le peuple assis dans les tenebres a vu une grande lumiere! Et sur ceux qui etaient assis dans la region et dans l\'ombre de la mort une lumiere s\'est levee !)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 17, 'Des lors, Jesus commenca a parler en public et a dire : Repentez-vous ; car le Royaume des cieux est proche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 18, 'Marchant le long de la mer de Galilee, il vit deux freres, Simon (celui qu\'on appelle Pierre) et Andre son frere, jetant leur filet dans la mer ; car ils etaient pecheurs ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 19, 'et il leur dit : Venez a ma suite, et je ferai de vous des pecheurs d\'hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 20, 'Eux aussitot, laissant les filets, le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 21, 'Continuant son chemin, il vit deux autres freres, Jacques, fils de Zebedee, et Jean son frere, dans leur barque avec Zebedee leur pere, raccommodant leurs filets ; il les appela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 22, 'Eux aussitot, laissant la barque et leur pere, le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 23, 'Jesus parcourait toute la Galilee ; enseignant dans leurs synagogues, prechant l\'Evangile! du Royaume, et guerissant toute maladie et toute infirmite parmi le peuple,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 24, 'et sa renommee se repandit dans la Syrie entiere. On lui apporta tous les malades, tous ceux qui etaient atteints de souffrances et de douleurs quelconques, des demoniaques, des lunatiques, des paralytiques et il les guerit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 4, 25, 'Et de grandes multitudes le suivirent venant de la Galilee, de la Decapole, de Jerusalem, de la Judee et de l\'autre rive du Jourdain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 1, 'Voyant ces multitudes, il gravit la montagne, il y fit sa demeure ; ses disciples s\'approcherent de lui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 2, 'et, ouvrant la bouche, il les enseignait, disant :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 3, 'Heureux les pauvres en esprit, parce que le Royaume des cieux est a eux !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 4, 'Heureux ceux qui sont dans l\'affliction, parce qu\'ils seront consoles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 5, 'Heureux ceux qui sont doux, parce quils auront la terre en heritage!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 6, 'Heureux ceux qui ont faim et soif de la justice, parce qu\'ils seront rassasies!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 7, 'Heureux les misericordieux, parce qu\'ils obtiendront misericorde!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 8, 'Heureux ceux qui ont le coeur pur, parce qu\'ils verront Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 9, 'Heureux les pacifiques, parce qu\'ils seront appeles fils de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 10, 'Heureux ceux qui sont persecutes a cause de la justice, parce que le Royaume des cieux est a eux !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 11, 'Heureux serez-vous quand on vous outragera et vous persecutera et qu\'on dira faussement toute sorte de mal de vous a cause de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 12, 'Rejouissez-vous, soyez transportes de joie, parce que votre recompense sera grande dans les cieux ; c\'est ainsi, en effet, qu\'on a persecute les prophetes qui vous ont precedes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 13, 'Vous etes le sel de la terre ; si le sel s\'affadit, avec quoi lui rendra-t-on sa saveur? Il n\'est plus bon a rien, sinon a etre jete dehors et foule aux pieds par les passants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 14, 'Vous etes la lumiere du monde ; une ville ne peut etre cachee quand elle est situee sur une montagne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 15, 'On n\'allume pas une lampe pour la mettre sous le boisseau, mais on la met sur le pied-de-lampe et elle luit a tous ceux qui sont dans la maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 16, 'Qu\'ainsi votre lumiere luise devant les hommes, afin qu\'ils voient vos bonnes oeuvres et glorifient votre Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 17, 'Ne pensez pas que je sois venu detruire la Loi ou les Prophetes ; je ne suis pas venu pour detruire, mais pour accomplir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 18, 'car je vous le dis en verite : jusqu\'a ce que le ciel et la terre aient passe, il ne disparaitra de la Loi, ni la plus petite lettre, ni un seul petit traita qui n\'ait recu sa pleine realisation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 19, 'Et celui qui annulera un de ces commandements, meme le moindre et enseignera les hommes a le faire, sera repute le moindre dans le Royaume des cieux ; mais celui qui les pratiquera et les enseignera, celui-la sera repute grand dans le Royaume des cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 20, 'Aussi je vous le dis : Si votre justice n\'est pas superieure a celle des Scribes et des Pharisiens vous n\'entrerez point dans le Royaume des cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 21, 'Vous avez entendu qu\'il a ete dit aux hommes d\'autrefois : Tu ne tueras point ; celui qui aura tue sera passible du jugement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 22, 'Eh bien, je vous dis, moi : Quiconque se met en colere contre son frere sera passible du jugement. Celui qui dira a son frere : Raca, sera passible de la justice du Sanhedrin. Celui qui lui dira : fou, sera passible de la Gehenne du feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 23, 'Lors donc que tu presentes ton offrande a l\'autel et que la tu te souviens que ton frere a quelque chose contre toi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 24, 'laisse la ton offrande, devant l\'autel, et va d\'abord te reconcilier avec ton frere ; et puis, viens presenter ton offrande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 25, 'Mets-toi d\'accord avec ton adversaire, promptement, pendant que tu es encore en chemin avec lui, de peur que cet adversaire ne te livre au juge, et le juge a celui qui execute ses sentences, et que tu ne sois jete en prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 26, 'Tu n\'en sortiras pas, je te le dis en verite, que tu n\'aies paye jusqu\'au dernier quadrant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 27, 'Vous avez entendu qu\'il a ete dit : Tu ne commettras point d\'adultere');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 28, 'Eh bien, je vous dis, moi : quiconque regarde une femme avec convoitise a deja, dans son coeur, commis l\'adultere avec elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 29, 'Si ton oeil droit est pour toi une cause de chute, arrache-le et jette-le loin de toi. Il vaut mieux pour toi qu\'un de tes membres perisse et que ton corps tout entier ne soit pas jete dans la Gehenne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 30, 'Et si ta main droite est pour toi une cause de chute, coupe-la et jette-la loin de toi ; il vaut mieux pour toi qu\'un de tes membres perisse et que ton corps tout entier n\'aille pas dans la Gehenne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 31, 'Il a ete dit : Celui qui repudie sa femme doit lui remettre un acte de divorce.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 32, 'Eh bien, je vous dis, moi : quiconque repudie sa femme  excepte pour cause d\'infidelite  l\'expose a commettre un adultere et celui qui epouse une femme divorcee, commet un adultere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 33, 'Vous avez encore entendu qu\'il a ete dit aux hommes d\'autrefois : Tu ne te parjureras pas, mais tu t\'acquitteras envers le Seigneur de tes serments.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 34, 'Eh bien, je vous dis, moi, de ne preter aucune sorte de serment ; ne jurez point par le ciel, car il est le trone de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 35, 'par la terre, car elle est l\'escabeau de ses pieds ; par Jerusalem, car elle est une ville du Grand Roi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 36, 'Ne jure pas non plus sur ta tete, car tu ne peux faire blanc ou noir un seul de tes cheveux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 37, 'Que votre langage soit: oui, oui ; ou : non, non. Ce qu\'on ajoute vient du Malin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 38, 'Vous avez entendu qu\'il a ete dit : Oe il pour oeil, dent pour dent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 39, 'Eh bien, je vous dis, moi : ne resistez point au mechant ; au contraire, si quelqu\'un te frappe a la joue droite, presente-lui aussi l\'autre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 40, 'Si quelqu\'un veut t\'appeler en justice et t\'enlever ta tunique, abandonne-lui aussi le manteau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 41, 'et si quelqu\'un veut te faire faire une corvee d\'un mille, fais-en deux pour lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 42, 'A qui te demande, donne ; de qui veut t\'emprunter, ne te detourne pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 43, 'Vous avez entendu qu\'il a ete dit : Tu aimeras ton prochain et tu hairas ton ennemi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 44, 'Eh bien, je vous dis, moi : aimez vos ennemis, et priez pour ceux qui vous persecutent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 45, 'afin que vous deveniez fils de votre Pere qui est dans les cieux, car il fait lever son soleil sur les mauvais et sur les bons, et descendre la pluie sur les justes et sur les injustes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 46, 'Si vous aimez ceux qui vous aiment quelle recompense avez-vous? Les publicains ne le font-ils pas aussi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 47, 'Et si pour vos freres seuls, vous avez un bon accueil, en quoi depassez-vous l\'ordinaire? Les paiens ne le font-ils pas aussi ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 5, 48, 'Soyez donc, vous, parfaits comme votre Pere celeste est parfait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 1, 'Gardez-vous de pratiquer votre justice devant les hommes pour en etre regardes ; autrement il n\'y a pas pour vous de recompense de votre Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 2, 'Quand donc tu feras l\'aumone, ne fais pas sonner de la trompette devant toi, comme font les hypocrites dans les synagogues et dans les rues pour etre glorifies des hommes. En verite, je vous le dis, ils ont recu leur recompense.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 3, 'Pour toi, quand tu fais l\'aumone, que ta main gauche ignore ce que fait ta main droite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 4, 'en sorte que ton aumone se fasse dans le secret, et ton Pere, qui voit dans le secret, te donnera ce qui t\'est du.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 5, 'Lorsque vous prierez, ne soyez pas comme ces hypocrites qui aiment, faisant leur priere, a stationner debout dans les synagogues et dans les angles des places publiques pour etre bien en evidence. En verite, je vous le dis, ils ont recu leur recompense.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 6, 'Toi quand tu pries, entre dans ta chambre, ferme ta porte, prie ton Pere qui est la, dans le secret, et ton Pere, qui voit dans le secret, te donnera ce qui t\'est du.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 7, 'En priant, ne multipliez pas les paroles, comme font les paiens ; ils s\'imaginent, en effet, que c\'est a force de paroles qu\'ils se feront exaucer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 8, 'Ne les imitez point ; car votre Pere sait de quoi vous avez besoin avant que vous le lui demandiez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 9, 'Voici donc comment vous devez prier : Notre Pere, qui es dans les cieux ! Que ton nom soit sanctifie !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 10, 'Que ton Regne vienne! Que ta volonte soit faite sur la terre comme dans le ciel!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 11, 'Donne-nous aujourd\'hui notre pain quotidien !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 12, 'Et remets-nous nos dettes comme nous-memes remettons les leurs a nos debiteurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 13, 'Et ne nous induis pas en tentation, mais delivre-nous du Malin ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 14, 'Si, en effet, vous remettez aux hommes leurs offenses, votre Pere celeste vous remettra aussi les votres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 15, 'mais si vous ne remettez point aux hommes leurs offenses, votre Pere, non plus, ne vous remettra pas vos offenses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 16, 'Quand vous jeunez, ne prenez pas, comme les hypocrites, un air accable ; ils se font des visages tout defaits, afin que leur jeune attire les regards. En verite, je vous le dis : ils ont recu leur recompense!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 17, 'Toi, quand tu jeunes, parfume ta tete et lave ton visage');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 18, 'afin que les hommes ne s\'apercoivent pas que tu jeunes, mais seulement ton Pere qui est la, dans le secret, et ton Pere, qui voit dans le secret te donnera ce qui t\'est du.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 19, 'Ne vous amassez point de tresors sur la terre ou le ver et la rouille rongent et ou les voleurs penetrent et derobent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 20, 'Mais amassez-vous des tresors dans le ciel, ou il n\'y a ni ver, ni rouille qui rongent, ni voleurs qui penetrent et derobent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 21, 'Car la ou est ton tresor, la sera aussi ton coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 22, 'La lampe du corps est l\'oeil ; si ton oeil n\'a rien qui le trouble, ton corps entier sera dans la lumiere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 23, 'si ton oeil est en mauvais etat, ton corps entier sera dans les tenebres. Si donc la lumiere qui est en toi est tenebres, combien seront profondes ces tenebres!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 24, 'Nul ne peut servir deux maitres ; ou bien, en effet, il haira l\'un et aimera l\'autre ; ou bien il s\'attachera au premier et meprisera le second. Vous ne pouvez servir Dieu et Mamon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 25, 'Voila pourquoi je vous dis de ne pas vous inquieter : pour votre vie de ce que vous mangerez ou de ce que vous boirez ; pour votre corps de quoi vous vous vetirez. La vie n\'est-elle pas plus que la nourriture, et le corps plus que le vetement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 26, 'Regardez les oiseaux du ciel ; ils ne sement ni ne moissonnent, ni ne recueillent en des greniers ; et votre Pere celeste les nourrit. Ne valez-vous pas beaucoup plus qu\'eux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 27, 'Qui de vous peut, par toutes ses preoccupations, ajouter une seule coudee a la duree de sa vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 28, 'Et quant au vetement, pourquoi vous en inquieter? Observez comment croissent les lis des champs. Ils ne travaillent ni ne filent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 29, 'Cependant, je vous le declare, Salomon lui-meme dans toute sa gloire, n\'etait pas vetu comme l\'un deux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 30, 'Si donc cette herbe des champs qui existe aujourd\'hui et qui demain sera jetee au four, Dieu la revet de la sorte, combien plutot vous-memes, hommes de petite foi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 31, 'Donc, ne vous inquietez pas, disant : Que mangerons-nous? ou : que boirons-nous? ou : comment nous vetirons-nous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 32, 'Oui, tout cela les paiens s\'en enquierent. Or, votre Pere celeste sait bien que vous avez besoin de tout cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 33, 'Cherchez premierement le Royaume et sa justice et toutes ces choses vous seront donnees par surcroit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 6, 34, 'Donc, ne vous inquietez pas du lendemain ; le lendemain aura soin de lui-meme. A chaque jour suffit sa peine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 1, 'Ne jugez point pour que vous ne soyez point juges ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 2, 'car en telle forme que vous jugez vous serez juges, et la mesure que vous faites aux autres sera votre mesure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 3, 'Pourquoi regardes-tu la paille qui est dans l\'oeil de ton frere? Et la poutre qui est dans ton oeil, tu ne la remarques pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 4, 'Et comment dis-tu a ton frere : laisse-moi oter cette paille de ton oeil et cela quand il y a une poutre dans le tien!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 5, 'Hypocrite, ote d\'abord de ton oeil la poutre et alors tu verras a oter la paille de l\'oeil de ton frere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 6, 'Ne donnez pas aux chiens ce qui est sacre, et ne repandez pas vos perles devant les pourceaux, de peur qu\'ils ne les foulent aux pieds et que, se tournant contre vous, ils ne vous dechirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 7, 'Demandez et il vous sera donne ; cherchez et vous trouverez ; frappez, et il vous sera ouvert ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 8, 'car quiconque demande recoit, et qui cherche trouve, et a celui qui frappe on ouvrira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 9, 'Quel est celui d\'entre vous, quand son fils lui demandera du pain, qui lui donnera une pierre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 10, 'et quand il lui demandera du poisson, lui donnera-t-il un serpent?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 11, 'Si donc, tout mauvais que vous etes, vous savez donner de bonnes choses a vos enfants, combien plus votre Pere qui est dans les cieux, en donnera-t-il de bonnes a ceux qui les lui demandent?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 12, 'Tout ce que vous voulez que les hommes vous fassent, faites-le leur aussi : c\'est la Loi et les Prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 13, 'Entrez par la porte etroite, parce que large est la porte et spacieuse la voie qui menent a la perdition, et nombreux sont ceux qui entrent par elle!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 14, 'et parce que etroite est la porte et resserree la voie qui menent a la vie, et petit est le nombre de ceux qui la trouvent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 15, 'Prenez garde aux faux prophetes qui viennent a vous, deguises en brebis et, au dedans, sont des loups rapaces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 16, 'Vous les reconnaitrez a leurs fruits. Est-ce sur des epines que l\'on cueille le raisin? sur des chardons les figues?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 17, 'Ainsi tout bon arbre donne de bons fruits ; et le mauvais arbre donne de mauvais fruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 18, 'Un bon arbre ne peut donner de mauvais fruits, ni un mauvais arbre donner de bons fruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 19, 'Tout arbre qui ne donne pas de bons fruits est coupe et jete au feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 20, 'Donc, vous les reconnaitrez a leurs fruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 21, 'Ce ne sont pas tous ceux qui me disent : Seigneur! Seigneur ! qui entreront dans le Royaume des cieux, mais celui qui fait la volonte de mon Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 22, 'Plusieurs me diront en ce jour-la: Seigneur! Seigneur! n\'est-ce pas en ton nom que nous avons prophetise? en ton nom que nous avons chasse les demons? en ton nom que nous avons fait beaucoup de miracles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 23, 'Et alors je leur dirai hautement : Je ne vous ai jamais connus! Retirez-vous de moi vous qui commettiez l\'iniquite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 24, 'Celui donc qui entend de moi ces paroles et les met en pratique c\'est l\'homme sage qui a bati sa maison sur le roc ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 25, 'et la pluie est tombee, et les fleuves sont arrives, et les vents ont souffle et ont fondu sur cette maison, et elle n\'est pas tombee parce qu\'elle etait fondee sur le roc.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 26, 'Et celui qui entend de moi ces paroles et ne les met pas en pratique, c\'est l\'homme insense qui a bati sa maison sur le sable,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 27, 'et la pluie est tombee, et les fleuves sont arrives et les vents ont souffle et ont fondu sur cette maison, et elle est tombee, et sa ruine a ete grande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 28, 'Jesus avait acheve de parler ; les multitudes etaient extremement frappees de son enseignement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 7, 29, 'en effet, il le donnait comme ayant autorite et non comme leurs Scribes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 1, 'Quand il descendit de la montagne, de grandes troupes le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 2, 'Survint un lepreux qui s\'approcha et se prosterna.devant lui : Seigneur, dit-il, si tu le veux, tu peux me guerir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 3, 'Jesus etendit la main, le toucha et dit : Je le veux, sois gueri ; et a l\'instant il fut gueri de sa lepre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 4, 'Jesus ajouta : Garde-toi de parler de ceci a personne ; mais va te montrer au pretre et offre le don prescrit par Moise ; que ce leur soit un temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 5, 'Comme il entrait dans Capharnaum, un centurion s\'approcha de lui, lui adressant cette priere :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 6, 'Seigneur, mon serviteur est dans ma maison, etendu sur son lit, paralytique et en proie a de cruelles souffrances.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 7, 'Jesus lui repondit : J\'irai, et je le guerirai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 8, 'Alors le centurion reprit : Seigneur, je ne suis pas digne que tu entres sous mon toit, mais donne seulement un ordre et mon serviteur sera gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 9, 'Car moi (qui ne suis pourtant qu\'un subordonne) j\'ai sous mes ordres des soldats, et si je dis a l\'un : va-t\'en  il s\'en va ; a l\'autre : viens  il vient ; a mon esclave : fais cela  il le fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 10, 'Jesus fut dans l\'admiration de ce langage ; et s\'adressant a ceux qui le suivaient : Je vous le dis en verite, chez personne, meme en Israel, je n\'ai trouve une foi aussi grande?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 11, 'Je vous declare que plusieurs viendront de l\'Orient et de l\'Occident et se mettront a table avec Abraham, Isaac et Jacob dans le Royaume des cieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 12, 'tandis que les fils du Royaume seront jetes dehors dans les tenebres. La seront les pleurs et le grincement des dents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 13, 'Ensuite Jesus dit au centurion ; Va et qu\'il te soit fait comme tu as cru. Et, a cette heure meme, le serviteur fut gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 14, 'Arrive a la maison de Pierre, Jesus vit la belle-mere de celui-ci, couchee ; elle avait la fievre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 15, 'Il lui toucha la main et la fievre disparut ; aussi se leva-t-elle et se mit-elle a les servir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 16, 'Le soir venu, on lui apporta beaucoup de demoniaques, et d\'un mot il chassait les Esprits. Il guerit aussi tous ceux qui etaient malades,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 17, 'afin que fut accompli ce qu\'avait annonce le prophete Esaie disant : C\'est lui qui a pris nos infirmites et porte nos maladies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 18, 'Voyant de nombreuses troupes autour de lui, Jesus donna l\'ordre de passer sur l\'autre rive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 19, 'Alors un Scribe, s\'avancant, lui dit : Maitre, je te suivrai partout ou tu iras.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 20, 'Jesus lui repond : Les renards ont des tanieres et les oiseaux du ciel ont des nids ; mais le Fils de l\'homme n\'a pas ou reposer sa tete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 21, 'Seigneur, lui dit un autre des disciples, permets-moi d\'aller d\'abord ensevelir mon pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 22, 'Suis-moi! Lui repond Jesus, et laisse les morts ensevelir leurs morts!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 23, 'Il monta dans la barque, suivi de ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 24, 'Soudain une grande tempete s\'eleva sur la mer, a tel point que la barque etait couverte par les vagues. Lui, il dormait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 25, 'Les disciples s\'approcherent de lui et le reveillerent. Sauve-nous, Seigneur, lui dirent-ils, nous perissons !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 26, 'Il leur repondit : Pourquoi vous effrayez-vous, hommes de peu de foi? Alors il se leva, fit des menaces aux vents et a la mer, et il se fit un grand calme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 27, 'Ces hommes dirent alors remplis d\'admiration : Qui est-il pour que lui obeissent et les vents et la mer?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 28, 'Quand il fut arrive a l\'autre bord, dans le pays des Gadareniens, vinrent a sa rencontre deux demoniaques, sortant des sepulcres. Ils etaient si dangereux que personne n\'osait passer par ce chemin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 29, 'Qu\'y a-t-il entre nous et toi, fils de Dieu? se mirent-ils a crier. Es-tu venu ici pour nous torturer avant le temps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 30, 'Or, il y avait, dans l\'eloignement, un nombreux troupeau de pourceaux qui paissaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 31, 'Les demons lui adressaient cette demande : Si tu nous chasses, envoie-nous dans ce troupeau de pourceaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 32, 'Allez ! leur dit Jesus. Alors ils partirent et entrerent dans les pourceaux ; et voila que, d\'une course impetueuse, le troupeau tout entier se precipita dans la mer et perit dans les flots.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 33, 'Les gardeurs prirent la fuite ; ils allerent a la ville tout raconter, et ce qui etait arrive aux demoniaques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 8, 34, 'La dessus, la ville entiere sortit au devant de Jesus, et, quand ces gens le virent ils le prierent de s\'eloigner de leur pays.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 1, 'Montant dans une barque, Jesus repassa l\'eau et vint dans sa ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 2, 'On lui presenta un paralytique etendu sur un lit. Voyant leur foi, il dit au paralytique : Courage! mon enfant, tes peches sont pardonnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 3, 'Il y avait la quelques Scribes. Cet homme blaspheme, se dirent-ils en eux-memes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 4, 'mais Jesus vit leurs pensees et dit : Pourquoi pensez-vous le mal dans vos coeurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 5, 'Lequel est le plus facile de dire : Tes peches sont pardonnes ou de dire : Leve toi et marche?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 6, 'Or, afin que vous sachiez que le Fils de l\'homme a, sur la terre, le pouvoir de pardonner des peches leve-toi, dit-il au paralytique, emporte ton lit et retourne dans ta maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 7, 'Il se leva et s\'en alla en sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 8, 'Les multitudes, voyant cela, furent saisies de crainte et rendirent gloire a Dieu de ce qu\'il avait donne une telle puissance aux hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 9, 'Jesus, parti de la, vit, en passant, un homme, appele Matthieu, assis au bureau du peage. Il lui dit ; Suis-moi. Matthieu se leva et le suivit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 10, 'Jesus etant a table dans la maison de Matthieu, il arriva que beaucoup de publicains et de pecheurs, etant survenus, s\'y trouverent avec lui et avec ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 11, 'Ce que voyant, les Pharisiens dirent a ses disciples : Pourquoi votre Maitre mange-t-il avec les publicains et les pecheurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 12, 'Jesus les entendit : Ce ne sont pas, dit-il, les gens bien portants qui ont besoin de medecin ; ce sont les malades.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 13, 'Allez apprendre le sens de cette parole : Je veux misericorde et non pas sacrifice ; car je ne suis pas venu appeler des justes, mais des pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 14, 'Alors les disciples de Jean s\'approcherent de lui et lui demanderent : Pourquoi, lorsque nous et les Pharisiens nous jeunons, tes disciples ne jeunent-ils pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 15, 'Jesus leur repondit : Les amis de l\'epoux peuvent-ils s\'attrister durant le temps que l\'epoux est avec eux? Des jours viendront ou l\'epoux leur sera enleve, ils jeuneront alors.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 16, 'Personne ne met un morceau d\'etoffe neuve a un vieux vetement ; car ce morceau emporte une partie du vetement qu\'il recouvre et fait une plus grande dechirure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 17, 'Ce n\'est pas non plus dans de vieilles outres que l\'on verse du vin nouveau ; sans quoi les outres se rompent, le vin se repand et les outres sont perdues. Mais on verse le vin nouveau dans des outres neuves, et tous deux se conservent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 18, 'Il leur parlait encore lorsqu\'un chef de synagogue entra et se prosterna devant lui. Ma fille, dit-il, vient de mourir ; mais viens, pose ta main sur elle, et elle vivra.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 19, 'Jesus se leva et le suivit avec ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 20, 'Or une femme, malade d\'une perte de sang depuis douze annees, s\'approcha par derriere et toucha la frange de son vetement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 21, 'Car elle se disait a elle-meme : Si seulement je touche son vetement, je serai guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 22, 'Jesus se retournant et la voyant, lui dit: Courage, ma fille, ta foi t\'a guerie. Et a l\'instant meme la femme fut guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 23, 'Parvenu a la maison du chef, Jesus dit, en voyant tes joueurs de flutes et la foule qui se livrait a de bruyantes lamentations :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 24, 'Retirez-vous, cette jeune fille n\'est pas morte, mais elle dort. On se moquait de lui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 25, 'mais quand on eut renvoye tout le monde, il entra, saisit la main de la jeune fille, et elle se reveilla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 26, 'Le bruit s\'en repandit dans tout le pays.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 27, 'Comme Jesus partait de la, deux aveugles le suivirent, criant et disant : Aie pitie de nous, fils de David!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 28, 'Il entra dans la maison, les aveugles vinrent l\'y trouver : Croyez vous, leur dit Jesus, que j\'aie la puissance de faire cela?  Ils repondirent: Oui, Seigneur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 29, 'Qu\'il vous soit fait selon votre foi! dit-il alors en leur touchant les yeux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 30, 'Et leurs yeux s\'ouvrirent. Jesus ajouta d\'un ton severe : Veillez a ce que nul ne le sache!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 31, 'Mais eux, s\'en etant alles, publierent sa renommee dans tout ce pays.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 32, 'Apres leur depart, on lui presenta un muet qui avait un demon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 33, 'Et le demon ayant ete chasse, le muet parla. Les multitudes disaient dans leur admiration : On n\'a jamais rien vu de semblable en Israel !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 34, 'Quant aux Pharisiens, ils disaient : C\'est par le Prince des demons qu\'il chasse les demons!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 35, 'Jesus cependant parcourait toutes les villes et tous les villages, enseignant dans leurs synagogues, prechant l\'Evangile du Royaume et guerissant toute sorte de maux et d\'infirmites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 36, 'Voyant les multitudes, il fut emu de compassion, car elles etaient comme des brebis sans berger, epuisees et gisantes ca et la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 37, 'Alors il dit a ses disciples : La moisson est grande et les ouvriers en petit nombre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 9, 38, 'Priez donc le maitre de la moisson d\'envoyer des, ouvriers dans sa moisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 1, 'Et ayant reuni ses douze disciples, il leur donna puissance sur les Esprits impurs pour les chasser, et sur toute maladie et toute infirmite pour la guerir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 2, 'Voici les noms des douze apotres : le premier est Simon appele Pierre, et Andre, son frere ;  Jacques, fils de Zebedee, et Jean, son frere ; ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 3, 'Philippe et Barthelemi ;  Thomas et Matthieu le, publicain ;  Jacques, fils d\'Alphee, et Thaddee ; ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 4, 'Simon le Cananite et Judas l\'Iskariote,  celui-la meme qui le trahit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 5, 'Tels sont les douze que Jesus envoya en mission apres leur avoir donne ces instructions : Ne prenez pas un chemin menant chez les paiens, et n\'entrez pas dans les villes des Samaritains ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 6, 'allez de preference vers les brebis perdues de la maison d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 7, 'Allez dire dans vos predications que le Royaume des cieux est proche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 8, 'Rendez la sante aux malades, ressuscitez des morts, guerissez des lepreux, chassez des demons. Vous avez recu gratuitement, donnez gratuitement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 9, 'Ne vous munissez ni d\'or, ni d\'argent, ni de monnaie quelconque dans vos ceintures,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 10, 'ni de sac pour la routes, ni de second vetement, ni de chaussures, ni d\'un baton, car l\'ouvrier est digne de sa nourriture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 11, 'Dans toute ville, dans tout village ou vous entrerez, informez-vous qui est digne de vous recevoir, et demeurez chez lui jusqu\'a votre depart.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 12, 'En entrant dans la maison, saluez-la, et si vraiment c\'est une digne maison, que votre paix vienne sur elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 13, 'Si elle n\'est pas digne, que votre paix revienne a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 14, 'Si l\'on ne vous recoit pas et si l\'on n\'ecoute pas vos paroles, sortez de cette maison ou de cette ville, en secouant la poussiere de vos pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 15, 'En verite, je vous le dis, il y aura, au jour du jugement, moins de rigueur pour la terre de Sodome et de Gomorrhe que pour cette cite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 16, 'Voici que je vous envoie comme des brebis au milieu de loups. Soyez donc prudents comme les serpents et simples comme les colombes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 17, 'Soyez sur vos gardes avec les hommes, car ils vous livreront aux tribunaux et ils vous flagelleront dans leurs synagogues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 18, 'Et, a cause de moi, vous serez traduits devant des procurateurs et des rois pour etre en temoignage a eux et aux paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 19, 'Quand on vous livrera, ne vous inquietez nullement ni de ce que vous direz, ni comment vous le direz ; car ce que vous devrez dire vous sera donne au moment meme ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 20, 'ce ne sera pas vous qui parlerez, mais l\'Esprit de votre Pere qui parlera en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 21, 'Un frere livrera son frere a la mort et un pere son enfant. Des enfants se souleveront contre leurs parents et les feront mourir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 22, 'et vous serez en haine a tout le monde a cause de mon nom. Mais celui qui aura persevere jusqu\'a la fin, celui-la sera sauve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 23, 'Quand ils vous persecuteront dans une ville, fuyez dans une autre ; car, je vous le dis en verite, vous n\'aurez point acheve de parcourir les villes d\'Israel que le Fils de l\'homme sera venu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 24, 'Un disciple n\'est pas au-dessus de son maitre, ni un esclave au-dessus de son seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 25, 'Il suffit au disciple d\'etre comme son maitre et a l\'esclave comme son seigneur. S\'ils ont appele Beelzeboul le maitre de la maison, combien plutot encore ceux de sa famille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 26, 'Ne les craignez donc point, car il n\'y a rien de cache qui ne doive etre revele, ni de secret qui ne doive etre connu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 27, 'Ce que je vous dis dans les tenebres, dites-le dans la lumiere, et ce que vous entendez a l\'oreille, prechez-le sur les toits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 28, 'Ne craignez pas ceux qui tuent le corps, mais ne peuvent tuer l\'ame. Craignez plutot Celui qui peut faire perir et l\'ame et le corps dans la Gehenne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 29, 'Deux passereaux ne se vendent-ils pas un as! Et l\'un d\'eux, ne tombe pas sur la terre sans votre Pere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 30, 'Jusqu\'aux cheveux de votre tete, tous, ils sont comptes !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 31, 'Ne craignez donc point ; vous etes de plus de valeur que bien des passereaux!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 32, 'Qui donc me confessera devant les hommes, je le confesserai moi aussi devant mon Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 33, 'Qui me reniera devant les hommes, je le renierai moi aussi devant mon Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 34, 'Ne pensez pas que je sois venu repandre la paix sur la terre ; je ne suis pas venu repandre la paix, mais jeter le glaive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 35, 'Je suis venu mettre la division entre l\'homme ... Et son pere, La fille et sa mere, La belle-fille et sa belle-mere');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 36, 'et l\'homme aura pour ennemis ceux de sa propre maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 37, 'Qui aime son pere ou sa mere plus que moi n\'est pas digne de moi. Qui aime son fils ou sa fille plus que moi n\'est pas digne de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 38, 'Qui ne prend pas sa croix et ne marche pas a ma suite n\'est pas digne de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 39, 'Sauver sa vie, c\'est la perdre! Perdre sa vie a cause de moi, cest la sauver!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 40, 'Qui vous recoit me recoit, qui me recoit, recoit Celui qui m\'envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 41, 'Qui recoit un prophete a titre de prophete recevra la recompense d\'un prophete. Qui recoit un juste a titre de juste, recevra la recompense d\'un juste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 10, 42, 'Qui aura donne a boire, ne fut-ce qu\'un verre d\'eau froide a l\'un de ces petits qui sont la, a titre de disciple, je vous le dis en verite, ne perdra point sa recompense.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 1, 'Apres avoir acheve de donner ces instructions aux douze disciples, Jesus partit de la pour aller enseigner et precher dans leurs villes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 2, 'Jean, ayant appris dans sa prison les oeuvres du Christ, lui envoya dire par ses disciples :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 3, 'Es-tu celui qui doit venir ou devons-nous en attendre un autre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 4, 'Pour leur repondre, Jesus leur dit : Allez annoncer a Jean ce que vous entendez et voyez :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 5, 'des aveugles voient, des estropies marchent, des lepreux sont gueris, des sourds entendent, des morts ressuscitent, des pauvres entendent l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 6, 'Heureux est celui pour qui je ne serai pas une occasion de chute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 7, 'Comme ils s\'en allaient, Jesus se mit a parler de Jean aux multitudes : Au desert, qu\'etes-vous alles voir? Est-ce un roseau agite par le vent?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 8, 'Qu\'etes-vous alles voir? Est-ce un homme aux vetements effemines? Mais ceux qui portent des vetements effemines sont dans les demeures des rois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 9, 'Qu\'etes-vous alles voir? Est-ce un prophete? Oui, vous dis-je, et plus qu\'un prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 10, 'C\'est celui dont il est ecrit : Voici que j\'envoie mon messager pour te preceder et preparer ton chemin devant toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 11, 'En verite, je vous le dis, parmi ceux que les femmes ont enfantes, nul ne s\'est leve plus grand que Jean-Baptiste ; mais le plus petit dans le Royaume des cieux est plus grand que lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 12, 'Depuis les jours de Jean-Baptiste jusqu\'a maintenant le Royaume des cieux est pris par violence et les violents s\'en emparent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 13, 'Tous les prophetes et la Loi ont exerce, jusqu\'a Jean, leur ministere prophetique,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 14, 'et si vous voulez admettre cette parole, lui-meme est Elie qui doit venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 15, 'Que celui qui a des oreilles entende,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 16, 'A qui assimilerai-je cette generation-ci : Elle est semblable a des enfants assis sur la place publique, qui crient a leurs camarades :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 17, 'Nous vous avons joue de la flute Et vous n\'avez pas danse ; Nous avons entonne des chants lugubres Et vous ne vous etes pas frappe la poitrine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 18, 'Jean est venu en effet, pratiquant labstinence du manger et du boire, et on dit : Il est possede d\'un demon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 19, 'Le Fils de l\'homme est venu, mangeant et buvant comme tout le monde, et on dit: Voila un amateur de bonne chere, un buveur de vin, un ami des publicains et des pecheurs. A la sagesse, les oeuvres qu\'elle accomplit rendent justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 20, 'Il commenca alors a faire des reproches aux villes dans lesquelles il avait fait le plus grand nombre de ses miracles, pour ne pas s\'etre repenties');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 21, 'Malheur a toi, Chorazein ! Malheur a toi, Bethsaida car si les miracles faits au milieu de vous l\'avaient ete dans Tyr et dans Sidon, elles se seraient depuis longtemps repenties, en se revetant d\'un sac et dans la cendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 22, 'Aussi, je vous le dis, il y aura moins de rigueur pour Tyr et pour Sidon, au jour du jugement, que pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 23, 'Et toi, Capharnaum, crois-tu que tu seras elevee jusqu\'au ciel? Tu seras abaissee jusqu\'a la Demeure-des-morts, parce que si dans Sodome avaient ete faits les miracles faits au milieu de toi, elle serait encore debout aujourdhui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 24, 'Aussi, je vous le dis, il y aura moins de rigueur pour la terre de Sodome, au jour du jugement, que pour toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 25, 'En ce temps-la, Jesus prononca ces paroles : Je te benis, o Pere, o Seigneur du ciel et de la terre, de ce que tu as cache ces choses a des sages et a des savants et de ce que tu les a revelees a de petits enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 26, 'Oui, Pere, je te benis de ce que tel a ete ton bon plaisir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 27, 'Tout m\'a ete confie par mon Pere, et personne ne connait le Fils, excepte le Pere, et personne ne connait le Pere, excepte le Fils et celui a qui il plait au Fils de le reveler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 28, 'Venez a moi, vous tous qui etes fatigues et charges, et c\'est moi qui vous soulagerai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 29, 'Prenez mon joug sur vous et apprenez de moi, parce que je suis doux et humble de coeur et vous trouverez du soulagement pour vos ames ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 11, 30, 'car mon joug est doux et mon fardeau est leger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 1, 'En ce temps-la,  un jour de sabbat,  Jesus passa par les bles. Ses disciples avaient faim ; ils se mirent a cueillir des epis et a manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 2, 'Les Pharisiens les virent. Voila tes disciples, dirent-ils a Jesus, qui font ce qu\'il n\'est pas permis de faire un jour de sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 3, 'Il leur repondit : N\'avez-vous pas lu ce que fit David lorsqu\'il eut faim, et ce que firent ses compagnons?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 4, ' comment il entra dans la maison de Dieu et mangea les pains de proposition, qu\'il n\'etait permis de manger ni a lui, ni a ses compagnons, mais aux pretres seuls?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 5, 'N\'avez-vous pas aussi lu dans la Loi que, le jour du sabbat, les pretres violent dans le Temple le repos sabbatique et cependant sont innocents?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 6, 'Or, je vous le dis, il y a ici quelque chose de plus grand que le Temple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 7, 'Si vous saviez ce que signifie : Je veux misericorde et non sacrifice vous n\'auriez pas condamne des innocents :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 8, 'le Fils de l\'homme est maitre du sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 9, 'Il partit de la et se rendit dans leur synagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 10, 'Il s\'y trouvait un homme qui avait une main paralysee ; pour avoir un pretexte a accusation, ils lui poserent une question : Est-il permis, dirent ils, de guerir les jours de sabbat?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 11, 'Il leur repondit : Si lun de vous n\'a qu\'une brebis et que, le jour du sabbat, cette brebis tombe dans une fosse, est-ce qu\'il ne la prendra pas, est-ce qu\'il ne la retirera pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 12, 'De quel prix cependant n\'est pas un etre humain en comparaison d\'une brebis ? Donc, il est permis les jours de sabbat d\'etre bienfaisant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 13, 'La-dessus, il dit a l\'homme : Etends ta main. Il letendit et elle redevint aussi saine que lautre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 14, 'Quant aux Pharisiens, ils sortirent et allerent se concerter entre eux sur les moyens de le perdre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 15, 'Jesus l\'apprit et partit de la. Beaucoup le suivirent ; il les guerit tous');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 16, 'et, par des menaces, il leur interdit de le faire connaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 17, '(C\'etait pour que fut accompli ce qu\'avait dit le prophete Esaie:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 18, 'Voici mon serviteur, celui que j\'ai elu, Mon bien-aime, celui en qui mon ame s\'est complu ; Je mettrai m on Esprit sur lui Et il annoncera le jugement aux nations ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 19, 'Il ne disputera pas ; il ne criera pas ; On n\'entendra pas sa voix dans les places publiques ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 20, 'Il ne brisera pas le roseau froisse Et il n\'eteindra pas la meche encore fumante, Jusqu\'a ce qu\'il ait fait triompher sa cause');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 21, 'Et les nations espereront en son nom.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 22, 'On lui presenta alors un demoniaque aveugle et muet ; il le guerit de sorte que le muet parlait et voyait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 23, 'Et la multitude etait toute transportee et disait: Celui-la n\'est-il point le Fils de David ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 24, 'Les Pharisiens, l\'entendant, dirent : Celui-la ne chasse les demons que par le prince des demons, Beelzeboul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 25, 'Mais Jesus, connaissant leurs pensees, leur dit : Tout Royaume divise contre lui-meme tombe en ruines ; nulle ville, nulle maison divisee contre elle-meme ne subsistera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 26, 'Or, si Satan chasse Satan, il est divise contre lui-meme, comment alors son Royaume subsistera-t-il ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 27, 'Et si, moi, je chasse les demons par Beelzeboul, par qui vos fils les chassent-ils? C\'est pourquoi ils seront eux-memes vos juges !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 28, 'Mais si c\'est par l\'Esprit de Dieu que je chasse les demons, le Royaume de Dieu est donc venu jusqu\'a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 29, 'Comment quelqu\'un peut-il entrer dans la maison du guerrier et lui ravir ce qu\'il possede s\'il n\'a tout d\'abord charge de liens le guerrier. Ce n\'est qu\'alors qu\'il pourra piller sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 30, 'Qui n\'est point avec moi est contre moi, et qui n\'amasse point, avec moi, disperse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 31, 'C\'est pourquoi je vous dis ceci : tout peche, tout blaspheme sera pardonne aux hommes, mais le blaspheme contre l\'Esprit ne sera point pardonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 32, 'Si quelqu\'un prononce un mot contre le Fils de l\'homme, il lui sera pardonne ; mais si quelqu\'un parle contre l\'Esprit saint, il ne lui sera pardonne ni dans ce siecle, ni dans l\'autre..');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 33, 'Dites l\'arbre bon et son fruit bon, ou dites l\'arbre mauvais et son fruit mauvais, car au fruit on connait l\'arbre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 34, 'Engeance de viperes! Comment, etant mechants, pourriez-vous dire de bonnes choses ; c\'est de l\'abondance du coeur que la bouche parle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 35, 'De son bon tresor l\'homme bon tire de bonnes choses ; de son mauvais tresor l\'homme mauvais tire de mauvaises choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 36, 'Je vous le declare, au jour du Jugement les hommes rendront compte de toute parole oiseuse qu\'ils auront dite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 37, 'Par tes paroles, en effet, tu seras justifie et par tes paroles condamne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 38, 'Alors quelques-uns des Scribes et des Pharisiens vinrent lui dire: Maitre, nous voulons voir un signe de toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 39, 'Il repondit par ces paroles : Mauvaise et adultere generation ! elle demande un signe ! et nul signe ne lui sera donne, sinon celui du prophete Jonas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 40, 'De meme, en effet, que Jonas fut trois jours et trois nuits dans le ventre de la baleine, de meme le Fils de l\'homme sera trois jours et trois nuits dans le sein de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 41, 'Lors du jugement, les hommes de Ninive se leveront avec cette generation et la condamneront, car, eux, ils se repentirent a la predication de Jonas. Et voici, sous vos yeux, plus que Jonas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 42, 'Lors du jugement, la reine du Midi se levera avec cette generation et la condamnera, car cette reine vint des extremites de la terre pour ecouter la sagesse de Salomon. Et voici, sous vos yeux, plus que Salomon!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 43, 'Lorsque l\'Esprit impur est sorti d\'un homme, il erre par des lieux arides, cherchant du repos, et il n\'en trouve pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 44, 'Il dit alors: Je retournerai dans ma maison, d\'ou je suis parti ; il y revient et la trouve vide, nettoyee et paree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 45, 'La-dessus, il va s\'associer sept autres Esprits plus mechants que lui-meme ; ils y penetrent et y demeurent ; et le dernier etat de cet homme devient pire que le premier. Ainsi en sera-t-il de cette generation mauvaise !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 46, 'Comme il parlait encore a la foule, sa mere et ses freres, qui se tenaient dehors, demanderent a l\'entretenir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 47, '(Quelqu\'un lui dit : voila ta mere et tes freres, qui se tiennent dehors et demandent a te parler.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 48, 'Il repondit a celui qui le lui disait : Qui est ma mere et qui sont mes freres ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 49, 'Puis, etendant la main sur ses disciples, il ajouta :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 12, 50, 'Voici ma mere et mes freres ! Car faire la volonte de mon Pere, qui est dans les cieux, c\'est etre mon frere, c\'est etre ma soeur, c\'est etre ma mere !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 1, 'Ce meme jour, Jesus sortit de la maison et s\'assit au bord de la mer');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 2, 'et on se rassembla en si grand nombre autour de lui qu\'il monta dans une barque et y prit place pour parler de la a toute la multitude restee sur le rivage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 3, 'Il leur dit beaucoup de choses en paraboles : Voila que le semeur est sorti pour semer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 4, 'Il jette sa semence, et des grains sont tombes le long du chemin et les oiseaux sont venus et les ont manges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 5, 'D\'autres sont tombes sur un sol pierreux, ou il n\'y avait pas beaucoup de terre, et ils ont immediatement pousse, parce que la terre etait sans profondeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 6, 'Mais le soleil s\'est leve et a brule la plante qui, n\'ayant point de racine, s\'est dessechee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 7, 'D\'autres grains sont tombes parmi les epines et les epines ont grandi et les ont etouffes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 8, 'D\'autres sont tombes dans la bonne terre et ils ont donne du fruit, tel grain en a produit cent, tel autre soixante, tel autre trente.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 9, 'Que celui qui a des oreilles entende !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 10, 'Ses disciples s\'approcherent et lui dirent : Pourquoi leur parles-tu en paraboles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 11, 'Il leur repondit : Parce qu\'a vous il est donne de penetrer les mysteres du Royaume des cieux, mais a ceux-la, non point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 12, 'Car a celui qui a, il sera donne, et il sera dans labondance ; mais a celui qui n\'a pas, meme ce qu\'il a lui sera ote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 13, 'C\'est pour cela que je leur parle en paraboles, c\'est parce que tout en voyant, ils ne voient point ; tout en entendant, ils n\'entendent ni ne comprennent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 14, 'En eux s\'accomplit la prophetie d\'Esaie, qui dit : De vos oreilles vous entendrez et vous ne comprendrez point ; De vos yeux vous regarderez et vous ne verrez point ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 15, 'Car le coeur de ce peuple s\'est epaissi, Leurs oreilles sont devenues sourdes Et leurs yeux se sont fermes, De peur que leurs yeux ne voient, De peur que leurs oreilles n\'entendent, De peur que leur coeur ne comprenne, De peur qu\'ils ne se convertissent, De peur que je ne les guerisse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 16, 'Heureux vos yeux, a vous, parce quils voient ; heureuses vos oreilles, parce qu\'elles entendent !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 17, 'car, je vous le dis en verite, nombre de prophetes et de justes ont desire voir ce que vous voyez et ne l\'ont pas vu, entendre ce que vous entendez et ne l\'ont pas entendu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 18, 'Pour vous donc, ecoutez le sens de la parabole du semeur :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 19, 'Chaque fois qu\'un homme entend la parole du Royaume et ne s\'en penetre pas, survient le Malin et il enleve ce qui a ete seme dans son coeur ; voila celui qui a recu la semence le long du chemin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 20, 'Celui qui l\'a recue sur un sol pierreux, c\'est l\'homme qui entend la parole et qui tout d\'abord l\'accueille avec joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 21, 'Mais il n\'a pas de racine en lui-meme ; il n\'est que pour un temps ; une affliction ou une persecution survenant a cause de la parole, il y trouve aussitot une occasion de chute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 22, 'Celui qui a recu la semence parmi les epines, c\'est l\'homme qui ecoute la parole, mais les sollicitudes de ce monde et la seduction de la richesse etouffent la parole et la rendent sterile. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 23, 'Celui qui a recu la semence dans la bonne terre, c\'est l\'homme qui ecoute la parole et s\'en penetre ; celui-la porte des fruits et en donne, tantot cent, tantot soixante, tantot trente.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 24, 'Il leur proposa une autre parabole. Voici, dit-il, une similitude du Royaume des cieux : Un homme avait seme du bon grain dans son champ ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 25, 'mais pendant que ses gens dormaient, son ennemi vint semer de l\'ivraie au milieu du froment et s\'en alla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 26, 'Lorsque l\'herbe poussa et porta du fruit, on vit paraitre l\'ivraie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 27, 'Les serviteurs s\'adresserent au maitre de la maison ; ils lui dirent : Seigneur, n\'est-ce pas du bon grain que tu as seme dans ton champ? D\'ou provient donc livraie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 28, 'Il leur repondit : C\'est un ennemi qui a fait cela. Ils lui dirent alors : Veux-tu que nous allions l\'arracher? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 29, 'Non, repondit-il, de peur qu\'en arrachant l\'ivraie, vous ne deraciniez en meme temps le froment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 30, 'Laissez-les croitre l\'un et l\'autre jusqu\'a la moisson ; au temps de la moisson, je dirai aux moissonneurs : arrachez d\'abord l\'ivraie et liez-la en gerbes pour la bruler ; quant au froment, vous le recueillerez dans mon grenier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 31, 'Il leur proposa une autre parabole : Le Royaume des cieux est semblable a un grain de seneve qu\'un homme prend et seme dans son champ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 32, 'C\'est bien la plus petite de toutes les semences, et quand il a pris sa croissance, il est plus grand que les plantes potageres ; il devient un arbre, tellement que les oiseaux du ciel viennent faire leurs nids dans ses branches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 33, 'Il leur raconta une autre parabole : Le Royaume des cieux est semblable a du levain qu\'une femme prend et qu\'elle cache dans trois mesures de farine jusqu\'a ce que le levain ait partout penetre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 34, 'Toutes ces choses, Jesus les dit en paraboles aux multitudes ; il ne leur parlait qu\'en paraboles');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 35, '(afin que fut accomplie la parole du prophete : J\'ouvrirai ma bouche pour parler en paraboles ; Je revelerai des choses restees cachees depuis la creation.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 36, 'Lorsqu\'il eut congedie la foule et fut rentre dans la maison, ses disciples vinrent le trouver et lui dirent : Explique-nous la parabole de l\'ivraie dans le champ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 37, 'Il leur repondit : Celui qui seme le bon grain, c\'est le Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 38, 'Le champ, c\'est le monde. Le bon grain, ce sont les enfants du Royaume. L\'ivraie, ce sont les enfants du Malin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 39, 'L\'ennemi qui l\'a semee, c\'est le diable. La moisson, c\'est la fin du monde ; les moissonneurs, ce sont les anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 40, 'De meme donc qu\'on arrache l\'ivraie et qu\'on la brule dans le feu, de meme, a la fin du monde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 41, 'le Fils de l\'homme enverra ses anges et ils arracheront de son Royaume tous les scandales et tous ceux qui operent l\'iniquite');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 42, 'et ils les precipiteront dans l\'ardente fournaise. La seront les pleurs et le grincement des dents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 43, 'C\'est alors que les justes, dans le Royaume de leur Pere, resplendiront comme le soleil.  Que celui qui a des oreilles entende');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 44, 'Le Royaume des cieux est semblable a un tresor enfoui dans un champ. L\'homme qui le trouve le tient cache et puis, debordant de joie, il s\'en va vendre tout ce qu\'il a et il achete ce champ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 45, 'Le Royaume des cieux est encore semblable a un marchand en quete de belles perles');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 46, 'et qui, en ayant trouve une d\'un grand prix, est alle vendre tout ce qu\'il avait et a achete cette perle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 47, 'Le Royaume des cieux est encore semblable a un filet que l\'on a jete en mer et qui a ramasse des poissons de toutes sortes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 48, 'Quand il a ete plein, les pecheurs l\'ont tire a eux ; puis, s\'asseyant sur le rivage, ils ont mis tous ceux qui etaient bons dans des jarres et ils ont jete les mauvaise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 49, 'Il en sera de meme a la fin du monde: les anges viendront separer les mechants du milieu des justes');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 50, 'et ils precipiteront ces mechants dans l\'ardente fournaise. La seront les pleurs et le grincement des dents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 51, 'Avez-vous compris tout cela?  Oui, repondirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 52, 'Alors il leur dit : Ainsi donc tout scribe instruit sur le Royaume des cieux est semblable a un homme, chef de maison, qui tire de son tresor des choses nouvelles et des choses anciennes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 53, 'Quand Jesus eut acheve de raconter ces paraboles, il partit de la');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 54, 'et revint chez ses compatriotes. Il se mit a enseigner dans leur synagogue, ce dont ils furent extremement surpris ; ils disaient : D\'ou lui viennent cette sagesse et ces miracles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 55, 'N\'est-ce pas la le fils du charpentier? Sa mere ne s\'appelle-t-elle pas Marie? Et Jacques, et Joseph, et Simon et Jude ne sont-ils pas ses freres?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 56, 'Ses soeurs ne demeurent-elles pas toutes avec nous? D\'ou lui vient donc tout cela?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 57, 'Pour eux il etait une occasion de chute. Alors Jesus leur dit : Un prophete n\'est sans honneur que dans sa patrie et dans sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 13, 58, 'Aussi ne fit-il la que peu de miracles, a cause de leur incredulite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 1, 'C\'est vers ce moment que le tetrarque Herode entendit parler de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 2, 'Cet homme, dit-il, a ses courtisans, c\'est Jean-Baptiste! Il est ressuscite d\'entre les morts! De la, ces puissances miraculeuses qui agissent en lui!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 3, 'Herode, en effet, avait arrete Jean, l\'avait garrotte et jete en prison : et cela a cause d\'Herodiade, la femme de son frere Philippe,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 4, 'et parce que Jean lui disait : Il ne t\'est pas permis d\'avoir cette femme-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 5, 'Herode eut bien voulu le mettre a mort, mais il avait peur du peuple, lequel tenait Jean pour prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 6, 'Or, au jour anniversaire de la naissance d\'Herode, la fille d\'Herodiade ayant danse au milieu de la salle, avait tellement plu au tetrarque');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 7, 'qu\'il lui avait jure, par serment, de lui accorder tout ce qu\'elle lui demanderait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 8, 'Donne-moi, lui avait-elle dit alors, poussee par sa mere, ici meme, sur un plat, la tete de Jean Baptiste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 9, 'Le roi en avait ete attriste ; cependant a cause de son serment, a cause aussi des convives, il avait commande de la lui donner');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 10, 'et d\'aller decapiter Jean dans la prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 11, 'Sa tete avait ete apportee sur un plat et donnee a la jeune fille, qui l\'avait remise a sa mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 12, 'Les disciples de Jean etaient venus prendre son corps et l\'ensevelir ; puis ils etaient alles en informer Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 13, 'Ayant appris cet evenement, Jesus partit de la en barque pour se retirer a l\'ecart dans quelque endroit solitaire. Mais les troupes, l\'ayant su, sortirent des villes et le suivirent a pied,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 14, 'et en debarquant il vit une foule immense, il en eut compassion et il guerit leurs malades.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 15, 'Sur le soir, les disciples s\'approcherent de lui et lui dirent : L\'endroit est desert et l\'heure est deja avancee, renvoie donc les foules, afin que les uns et les autres aillent dans les villages acheter de quoi se nourrir.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 16, 'Mais Jesus leur dit : Il n\'est pas necessaire qu\'ils s\'en aillent ; donnez-leur vous-memes a manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 17, 'Mais, repondirent-ils, nous n\'avons ici que cinq pains et deux poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 18, 'Il dit : Apportez-les-moi ici,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 19, 'et, apres avoir commande que la multitude s\'assit sur l\'herbe, il prit les cinq pains et les deux poissons, et, levant les yeux au ciel, il prononca la benediction, puis il rompit les pains, les donna aux disciples et les disciples aux foules.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 20, 'Tous mangerent, tous furent rassasies, et on emporta les morceaux qui restaient : douze paniers pleins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 21, 'Quant au nombre de ceux qui avaient mange, il etait de cinq mille hommes environ, sans compter des femmes et des enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 22, 'Aussitot apres, il pressa les disciples de remonter dans la barque et de le preceder sur la rive opposee, tandis qu\'il renverrait les multitudes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 23, 'Quand il les eut renvoyees, il gravit la montagne afin de prier dans la solitude. Le soir vint et il etait seul en ce lieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 24, 'Pendant ce temps la barque, deja au milieu de la mer, etait battue des flots, ayant le vent contraire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 25, 'Or, a la quatrieme veille de la nuit, il vint a eux en marchant sur la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 26, 'Quand les disciples le virent, marchant sur la mer, ils furent bouleverses. C\'est un fantome, dirent-ils, en jetant des cris de terreur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 27, 'Mais aussitot il leur parla, il leur dit : Rassurez-vous ; c\'est moi, soyez sans crainte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 28, 'Pierre alors s\'adressa a lui: Si c\'est toi, Seigneur, commande que je vienne a toi sur les flots. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 29, 'Viens,dit Jesus. Descendant de la barque, Pierre marcha sur les flots et alla vers Jesus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 30, 'mais quand il sentit le vent il fut pris de peur et commenca a s\'enfoncer, alors il s\'ecria : Seigneur, sauve-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 31, 'Jesus etendit immediatement la main et lui dit en le saisissant : Homme de peu de foi, pourquoi as-tu doute?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 32, 'Ils entrerent dans la barque et le vent cessa.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 33, 'Alors ceux qui s\'y trouvaient se prosternerent devant lui, disant : Tu es vraiment Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 34, 'Ayant passe l\'eau, ils arriverent au pays de Gennesaret.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 35, 'Les gens de l\'endroit, l\'ayant reconnu, firent prevenir tout le voisinage et lui presenterent tous leurs malades,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 14, 36, 'le priant de les laisser seulement toucher la frange de son vetement. Et tous ceux qui la toucherent furent gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 1, 'Alors des Pharisiens et des Scribes qui arrivaient de Jerusalem s\'approcherent de Jesus et lui dirent :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 2, 'Pourquoi tes disciples transgressent-ils la tradition des anciens? Ils ne pratiquent pas l\'ablution des mains lorsquils prennent leur repas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 3, 'Il leur repondit ainsi : Et vous, pourquoi transgressez-vous, au profit de votre tradition, le commandement de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 4, 'En effet, Dieu a dit : Honore ton pere et ta mere et : qu\'il soit puni de mort, celui qui maudira son pere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 5, 'Mais vous, voici ce que vous enseignez : celui qui dira a son pere ou a sa mere : Je declare offrande a Dieu ce dont tu voudrais etre assiste par moi, ne sera pas tenu d\'honorer son pere ou sa mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 6, 'Et c\'est ainsi, qu\'au profit de votre tradition, vous avez reduit a neant la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 7, 'Hypocrites ! C\'est bien a vous que s\'applique la prophetie d\'Esaie :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 8, 'C\'est des levres que ce peuple m\'honore! Quant a son coeur, il est fort loin de moi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 9, 'C\'est en vain qu\'ils me rendent un culte ; Ils enseignent des doctrines qui ne sont que des ordonnances humaines ! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 10, 'Il appela a lui la multitude. Ecoutez et comprenez leur dit-il ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 11, 'Ce qui rend l\'homme impur ce n\'est pas ce qui entre dans la bouche ; ce qui rend l\'homme impur, c\'est ce qui sort de la bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 12, 'Les disciples s\'approcherent alors et lui dirent : Sais-tu que les Pharisiens, en entendant ces paroles, ont ete scandalises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 13, 'Il leur repondit : Toute plante que n\'a point plantee mon Pere celeste sera deracinee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 14, 'Laissez-les ; ce sont des conducteurs aveugles ! Quand un aveugle conduit un aveugle, ils tombent tous deux dans une fosse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 15, 'Pierre, reprenant, lui dit : Explique-nous la parabole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 16, '- Etes-vous encore, vous aussi, sans intelligence? leur repondit-il.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 17, 'Ne comprenez-vous pas que tout ce qui entre dans la bouche va au ventre et, de la, est rejete en quelque lieu secret?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 18, 'mais ce qui sort de la bouche vient du coeur, et voila ce qui rend l\'homme impur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 19, 'Pensees mauvaises, homicides, adulteres, fornications, vols, faux temoignages, calomnies viennent en effet du coeur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 20, 'oui, voila ce qui rend l\'homme impur! Quant a manger sans avoir pratique l\'ablution des mains, ce n\'est point contracter une impurete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 21, 'Jesus partit de la et se retira du cote de Tyr et de Sidon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 22, 'Une Chananeenne de ces contrees vint a lui en s\'ecriant : Seigneur, Fils de David, aie pitie de moi, ma fille est cruellement tourmentee par un demon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 23, 'Il ne lui repondit pas un mot. Ses disciples intervinrent pour le prier de renvoyer cette femme ;  elle nous poursuit de ses cris, disaient-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 24, 'Alors il prit la parole : C\'est seulement aux brebis perdues de la maison d\'Israel que j\'ai ete envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 25, 'Mais la femme vint se prosterner devant lui, disant : Seigneur, viens a mon secours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 26, 'Alors il repondit : Il n\'est pas bon de prendre le pain des enfants et de le jeter aux chiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 27, 'Elle repliqua : Si, Seigneur, car les chiens mangent quelques-unes des miettes qui tombent de la table de leurs maitres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 28, '- O femme! ta foi est grande! reprit alors Jesus, qu\'il te soit fait comme tu veux. Et a cette heure meme sa fille fut guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 29, 'Parti de la, Jesus longea la mer de Galilee. Puis il gravit la montagne et y demeura.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 30, 'Et des troupes nombreuses arriverent vers lui, ayant avec elles des paralytiques, des aveugles, des sourds-muets, des estropies et beaucoup d\'autres que l\'on deposa a ses pieds. Il les guerit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 31, 'De sorte que les multitudes etaient dans l\'admiration de voir les sourds-muets parler, les estropies se guerir, les paralytiques marcher, les aveugles voir. Et elles rendaient gloire au Dieu d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 32, 'Jesus, cependant, appela ses disciples et leur dit : J\'ai compassion de cette multitude ; voici deja trois jours qu\'ils ne me quittent pas, et ils n\'ont pas de quoi manger. Je ne veux pas les renvoyer a jeun, de peur qu\'ils ne defaillent sur la route.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 33, 'Les disciples lui repartirent : Ou donc trouver, en ce lieu solitaire, un assez grand nombre de pains pour rassasier une foule pareille?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 34, 'Combien de pains avez-vous? leur dit Jesus.  Sept, repondirent-ils, et quelques poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 35, 'Il donna ordre a la foule de s\'asseoir par terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 36, 'Puis il prit les sept pains et les poissons, et rendant graces, les rompit et les donna aux disciples ; et les disciples les donnerent a la foule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 37, 'Tous mangerent ; tous furent rassasies et des morceaux qui resterent on emporta sept corbeilles pleines.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 38, 'Quatre mille hommes furent ainsi nourris, sans compter des femmes et des enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 15, 39, 'Quand il eut congedie les multitudes, il monta dans la barque et se rendit au pays de Magadan.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 1, 'Pour le mettre a l\'epreuve, les Pharisiens et les Sadduceens vinrent lui demander de leur faire un signe qui vint du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 2, 'Il leur fit cette reponse : [Le soir, il vous arrive de dire : Il fera beau, car le ciel est rouge ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 3, 'et le matin : Aujourd\'hui il y aura un orage, car le ciel est d\'un rouge sinistre. Vous savez donc juger laspect du ciel, et les signes du temps ou vous etes vous ne le pouvez pas !]');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 4, 'Race mauvaise et adultere qui demande un signe!  Un signe! il ne lui en sera pas donne d\'autre que celui de Jonas! Et les laissant, il s\'en alla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 5, 'Les disciples, en passant l\'eau, oublierent de prendre des pains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 6, 'Or, Jesus leur dit : Faites bien attention, prenez bien garde au levain des Pharisiens et des Sadduceens. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 7, 'C\'est parce que nous n\'avons pas pris de pains! penserent et se dirent entre eux les disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 8, 'Jesus, le sachant, leur dit : Comment pouvez- vous penser ici aux pains que vous n\'avez pas, hommes de peu de foi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 9, 'Est-ce que vous ne comprenez pas encore? est-ce que vous ne vous souvenez plus des cinq pains pour les cinq mille hommes et du nombre de paniers que vous avez emportes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 10, 'ni des sept pains pour les quatre mille hommes et du nombre de corbeilles que vous avez emportees?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 11, 'Comment ne comprenez-vous pas quand je si dis : Prenez bien garde au levain des Pharisiens et des Sadduceens, que je ne vous parle pas de pains?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 12, 'Ils comprirent alors :il ne leur avait pas dit de se garder du levain qu\'on met dans le pain, mais de la doctrine des Pharisiens et des Sadduceens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 13, 'Etant alle aux environs de Cesaree de Philippe, Jesus interrogea ses disciples : Que dit-on qu\'est le Fils de l\'homme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 14, 'Ils repondirent : Les uns disent : c\'est Jean-Baptiste ; les autres : c\'est Elie ; d\'autres : c\'est Jeremie, ou : c\'est l\'un des prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 15, ' Et vous, continua-t-il, qui dites-vous que je suis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 16, 'Simon Pierre repondit par ces mots : Tu es le Christ, le Fils du Dieu vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 17, 'Jesus alors lui adressa ces paroles : Heureux es-tu, Simon Bar-Jona, parce que ce n\'est ni la chair ni le sang qui t\'ont revele cela ; mais mon Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 18, 'Eh bien, moi, je te dis : Tu es Pierre et sur cette pierre j\'edifierai mon Eglise et les portes de la Demeure-des-morts ne prevaudront point contre elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 19, 'Je te donnerai les clefs du Royaume des cieux et tout ce que tu lieras sur la terre sera lie dans les cieux, et tout ce que tu delieras sur la terre sera delie dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 20, 'En meme temps, il recommanda a ses disciples de ne dire a personne qu\'il etait, lui, le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 21, 'A partir de ce moment, Jesus-Christ commenca a expliquer a ses disciples qu\'il lui fallait :  aller a Jerusalem ;  beaucoup souffrir de la part des Anciens, des chefs des pretres et des Scribes ; - etre mis a mort ;  ressusciter le troisieme jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 22, 'Le tirant alors a l\'ecart, Pierre se mit a le reprendre : Que Dieu ait pitie de toi, Seigneur! non, il ne t\'arrivera rien de tout cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 23, 'Mais lui, se retournant vers Pierre : Va-t\'en ! Arriere de moi! Satan ! lui dit-il ; tu m\'es un scandale, parce que tes pensees ne sont pas de Dieu, mais des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 24, 'Jesus dit alors a ses disciples: Si quelqu\'un veut venir a ma suite, qu\'il renonce a lui-meme, qu\'il porte sa croix et qu\'il me suive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 25, 'Celui qui voudra sauver sa vie, la perdra ; et il trouvera la vie, celui qui l\'aura perdue a cause de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 26, 'A quoi servira-t-il a, un homme de gagner le monde entier s\'il perd sa vie? Qu\'est-ce qu\'il donnera en echange de sa vie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 27, 'Le Fils de l\'homme doit venir dans la gloire de son Pere et accompagne de ses anges et alors il rendra a chacun selon ses oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 16, 28, 'En verite, je vous le dis, quelques-uns sont ici presents qui ne gouteront point la mort avant d\'avoir vu le Fils de l\'homme venant en sa Royaute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 1, 'Six jours apres, emmenant avec lui Pierre, Jacques et Jean, son, frere, Jesus les conduisit sur une haute montagne, a l\'ecart.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 2, 'Et alors, devant eux, il fut transfigure : sa face resplendit comme le soleil ; ses vetements devinrent blancs comme la lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 3, 'Et voila que leur apparurent Moise et Elie, s\'entretenant avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 4, 'Pierre alors, s\'adressant a Jesus, lui dit : Seigneur, qu\'il nous est bon d\'etre ici! si tu veux, je dresserait trois tentes, une pour toi, une pour Moise, une pour Elie... ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 5, 'Il parlait encore, lorsqu\'une nuee lumineuse les enveloppa, et que de cette nuee sortit une voix disant : Celui-ci est mon Fils, le bien-aime en qui je me complais. Ecoutez-le.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 6, 'Quand les disciples entendirent cette voix, ils tomberent sur leur face, frappes de terreur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 7, 'Mais Jesus s\'approcha et, les touchant, leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 8, 'Levez-vous et soyez sans crainte. Levant alors les yeux, ils ne virent plus personne que lui, Jesus, seul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 9, 'Comme ils descendaient de la montagne, Jesus leur fit cette recommandation : Ne parlez a personne de cette vision jusqu\'a ce que le Fils de l\'homme soit ressuscite d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 10, 'Les disciples lui demanderent : Pourquoi les Scribes disent-ils : Il faut, avant tout, qu\'Elie vienne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 11, 'Il leur repondit : Oui, Elie doit venir et retablir toutes choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 12, 'Eh bien, je vous le declare, Elie est deja venu ; et ils ne l\'ont pas reconnu ; ils ont, au contraire, fait de lui tout ce qu\'ils ont voulu. De meme le Fils de l\'homme doit souffrir par leurs mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 13, 'Les disciples comprirent alors que c\'etait de Jean-Baptiste qu\'il leur parlait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 14, 'Lorsqu\'ils eurent rejoint la multitude, un homme s\'avanca vers lui et, se jetant a genoux, lui dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 15, 'Seigneur, aie pitie de mon fils ; il est lunatique, et ses souffrances sont grandes, car il lui arrive souvent de tomber dans le feu, souvent aussi dans l\'eau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 16, 'Je l\'ai apporte a tes disciples et ils ont ete impuissants a le guerir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 17, 'Jesus alors prononca ces paroles : generation incredule et perverse, jusqu\'a quand serai-je avec vous? jusqu\'a quand vous supporterai-je? Apportez-moi l\'enfant ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 18, 'Il lui fit des menaces ; et le demon sortit de lui. Cet enfant, des ce moment, fut gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 19, 'Les disciples, s\'approchant alors de Jesus, lui dirent en particulier : Pourquoi, nous, avons-nous ete impuissants a chasser ce demon?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 20, 'Il repondit: A cause de votre peu de foi, car je vous le dis en verite, si vous aviez de la foi gros comme un grain de seneve, vous diriez a cette montagne : Transporte-toi d\'ici la et elle s\'y transporterait ; rien ne vous serait impossible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 21, '(Mais ce genre de demon ne s\'expulse que par la priere et le jeune).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 22, 'Pendant qu\'ils sejournaient en Galilee, Jesus leur dit : Le Fils de l\'homme va etre livre entre les mains des hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 23, 'ils le tueront ; le troisieme jour il ressuscitera.) Ces paroles les affligerent extremement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 24, 'Quand ils arriverent a Capharnaum, ceux qui recevaient les didrachmes s\'adresserent a Pierre et lui dirent : Est-ce que votre maitre ne paye pas les didrachmes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 25, 'Il les paye, repondit-il. Et quand il entra dans la maison, Jesus le prevint en lui disant: Quel est ton avis, Simon? de qui les rois de la terre percoivent-ils limpot ou le cens? de leurs fils ou des etrangers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 26, 'Pierre repondit : des etrangers. Donc, reprit Jesus, les Fils, sont libres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 17, 27, 'cependant pour ne pas les scandaliser, va a la mer et jette l\'hamecon. Le premier poisson qui se prendra, tire-le de l\'eau, ouvre-lui la bouche, tu y trouveras un statere ; prends-le et donne-le-leur pour moi et pour toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 1, 'A ce moment, les disciples vinrent a Jesus et lui dirent : Qui est le plus grand dans le Royaume des cieux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 2, 'Alors Jesus appela a lui un enfant, le placa au milieu d\'eux');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 3, 'et leur dit : Je vous le dis en verite, si vous n\'avez ete changes et n\'etes devenus semblables aux enfants, vous n\'entrerez pas dans le Royaume des cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 4, 'Qui donc se fera humble comme cet enfant sera, lui, le plus grand dans le Royaume des cieux ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 5, 'et qui recevra, en mon nom, un enfant comme celui-ci, me recoit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 6, 'Mais celui qui aura ete une occasion de chute pour l\'un de ces petits qui croient en moi, il vaudrait mieux pour lui qu\'on lui pendit au cou une de ces meules que tournent les anes, et qu\'on le precipitat dans les profondeurs de la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 7, 'Malheur au monde a cause des scandales! Il faut bien qu\'il arrive des scandales ; mais malheur a l\'homme par qui le scandales arrive!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 8, 'Si ta main ou ton pied sont pour toi une occasion de chute, coupe-les et jette-les loin de toi ; il t\'est meilleur d\'entrer dans la vie mutile ou estropie, que d\'etre jete, avec tes deux mains et tes deux pieds, dans le feu eternel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 9, 'Si ton oeil est pour toi une occasion de chute, arrache-le et jette-le loin de toi ; il t\'est meilleur d\'entrer borgne dans la vie, que d\'etre jete, avec tes deux yeux, dans la gehenne du feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 10, 'Gardez-vous de mepriser un seul de ceux qui sont les petits! car, je vous le dis, leurs anges, dans les cieux, voient sans cesse la face de mon Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 11, '(Car le Fils de l\'homme est venu pour sauver ce qui etait perdu).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 12, 'Que pensez-vous de ceci : Si un homme a cent brebis et que l\'une d\'elles vienne a s\'egarer, ne laisse-t-il pas les quatre-vingt-dix-neuf autres dans les montagnes, pour aller chercher celle qui s\'est egaree ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 13, 'et s\'il arrive qu\'il la retrouve, en verite je vous le dis, il a plus de joie au sujet de celle-la qu\'au sujet des quatre-vingt-dix-neuf autres qui ne se sont point egarees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 14, 'De meme ce n\'est pas la volonte de votre Pere qui est dans les cieux que perisse un seul de ceux qui sont les petits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 15, 'Si ton frere a peche contre toi, va le trouver et reprends-le, toi, seul avec lui. S\'il t\'ecoute, tu auras gagne ton frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 16, 'S\'il ne t\'ecoute pas, amene avec toi une ou deux personnes, afin que tout soit decide d\'apres l\'avis de deux ou trois temoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 17, 'Puis, s\'il refuse de les ecouter, dis-le a l\'eglise. Et, s\'il refuse aussi d\'ecouter l\'eglise, qu\'il soit pour toi comme un paien et un publicain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 18, 'Je vous le dis en verite : tout ce que vous aurez lie sur la terre sera lie dans le ciel ; et tout ce que vous aurez delie sur la terre sera delie dans le ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 19, 'Je vous repete, en verite, que si, sur la terre, deux d\'entre vous s\'accordent pour demander une chose quelconque, ils l\'obtiendront de mon Pere qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 20, 'La, en effet, ou deux ou trois sont reunis en mon nom, je suis present au milieu d\'eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 21, 'Alors Pierre, s\'approchant, lui dit : Seigneur, si mon frere, peche contre moi, combien de fois lui pardonnerai-je? sera-ce jusqu\'a sept fois?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 22, 'Jesus lui repondit: Je ne te dis pas jusqu\'a sept fois, mais jusqu\'a soixante-dix fois sept fois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 23, 'A ce sujet, voici a quoi est semblable le Royaume des cieux : Un roi voulut faire rendre compte a ses serviteurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 24, 'Quand il eut commence a compter, on lui en presenta un, debiteur de dix mille talents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 25, 'Comme il n\'avait pas de quoi les rendre, son maitre ordonna qu\'on le vendit, lui, sa femme, ses enfants, tout ce qu\'il possedait, pour l\'acquittement de sa dette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 26, 'Ce serviteur, tombant a ses pieds, se prosterna devant lui et lui dit : Sois patient envers moi, et je te rendrai tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 27, 'Le maitre eut alors pitie de son serviteur, il le laissa aller et lui fit remise de sa dette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 28, 'En sortant, ce serviteur rencontra un de ses compagnons qui lui devait cent deniers, et alors, le tenant a la gorge jusqu\'a l\'etrangler, il lui dit : Rends ce que tu dois!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 29, 'Son compagnon, tombant a ses pieds, le supplia en disant : Sois patient envers moi et je te rendrai ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 30, 'mais lui ne voulut pas ; il s\'en alla et le jeta en prison jusqu\'a ce qu\'il eut paye sa dette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 31, 'Voyant ce qui se passait, les autres serviteurs en furent profondement affliges ; et ils vinrent raconter a leur maitre tout ce qui etait arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 32, 'Alors le maitre fit appeler celui qui avait ainsi agi et lui dit : Serviteur mechant! Je t\'ai remis ta dette parce que tu m\'as supplie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 33, 'Ne te fallait-il pas avoir pitie toi aussi de ton compagnon, comme moi-meme j\'avais eu pitie de toi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 34, 'Et son maitre, irrite, livra cet homme aux bourreaux, jusqu\'a ce qu\'il eut paye toute sa dette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 18, 35, 'Ainsi vous fera mon Pere celeste si chacun de vous ne pardonne pas a son frere de tout son coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 1, 'Quand Jesus eut acheve ces discours, il quitta la Galilee et se rendit sur la frontiere de la Judee, de l\'autre cote du Jourdain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 2, 'De grandes multitudes le suivirent et, il y fit des guerisons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 3, 'Les Pharisiens vinrent a lui et, pour le mettre a l\'epreuve, ils lui dirent : Est-il permis de repudier sa femme pour un motif quelconque?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 4, 'Il repondit par ces paroles : N\'avez-vous pas lu que, a l\'origine, le Createur a fait un couple, male et femelle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 5, 'et il a dit : A cause de cela l\'homme laissera son pere et sa mere pour s\'attacher a sa femme et ils seront deux dans une seule chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 6, 'Ainsi ils ne sont plus deux, mais une seule chair. Donc, ce que Dieu a uni, qu\'un homme ne le separe point. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 7, 'Alors pourquoi, dirent-ils, Moise a-t-il prescrit de donner a la femme un acte de divorce et de la repudier?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 8, 'Il repondit : C\'est a cause de la durete de vos coeurs que Moise vous a permis de repudier vos femmes. Mais, a l\'origine, il n\'en etait pas ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 9, 'Or, je vous le dis : qui repudie sa femme, si ce n\'est pour infidelite, et en epouse une autre, commet un adultere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 10, 'Les disciples lui dirent : Si telle est la condition de l\'homme vis-a-vis de la femme, il vaut mieux ne pas se marier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 11, 'Il leur dit : Tout le monde n\'est pas capable de cette resolution, mais seulement ceux a qui cela est donne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 12, 'Il y a des eunuques qui sont tels des leur naissance, des les entrailles de leur mere ; il en est d\'autres que les hommes ont faits eunuques ; et enfin il y en a qui se sont faits eunuques eux-memes en vue du Royaume des cieux. Que celui qui a la force d\'en arriver la, y arrive!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 13, 'Alors on lui amena des enfants pour qu\'il leur imposat les mains en priant. Comme les disciples blamaient ceux qui les lui amenaient,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 14, 'Jesus leur dit : Laissez les enfants et ne les empechez point de venir a moi, car c\'est a ceux qui leur ressemblent qu\'appartient le Royaume des cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 15, 'Puis il leur imposa les mains et partit de la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 16, 'S\'approchant de lui, quelqu\'un lui dit: Maitre, que dois-je faire e de bon pour avoir la vie eternelle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 17, 'Il lui repondit : Pourquoi m\'interroges-tu sur ce qui est bon ? Un seul etre est bon. Si tu veux entrer dans la vie, observe les commandements. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 18, 'Lesquels? dit-il. Jesus repondit : Ceux-ci : Tu ne seras point meurtrier ; tu ne seras point adultere ; tu ne seras point voleur ; tu ne rendras point de faux temoignage ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 19, 'honore ton pere et ta mere et : tu aimeras ton prochain comme toi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 20, 'Le jeune homme lui dit : Tout cela je l\'ai observe ; que me manque-t-il encore?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 21, 'Jesus lui repondit : Si tu veux etre parfait, va vendre ce que tu possedes, donnes-en le prix a des pauvres : tu auras alors un tresor dans les cieux ; viens ensuite et suis-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 22, 'Le jeune homme, entendant ces paroles, s\'en alla tout triste, car il avait beaucoup de biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 23, 'Jesus dit alors a ses disciples : Je vous le dis en verite, un riche entrera difficilement dans le Royaume des cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 24, 'Oui, je vous le repete, il est plus facile a un chameau de passer par le trou d\'une aiguille qu\'a un riche d\'entrer dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 25, 'Ces paroles consternerent les disciples : Qui donc pourra etre sauve? dirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 26, 'Jesus leur repondit en les regardant : Aux hommes, cela est impossible ; mais a Dieu, tout est possible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 27, 'Pierre s\'adressa alors a lui et lui dit : Et nous, nous avons tout quitte ; nous t\'avons suivi ; qu\'y aura-t-il pour nous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 28, 'Jesus leur repondit : le vous le dis en verite, lorsque, dans le Renouvellement de toutes choses le Fils de l\'homme, siegera sur le trone de sa gloire, vous aussi, vous, qui m\'avez suivi, vous siegerez sur douze trones, jugeant les douze tribus d\'Israel ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 29, 'et quiconque, a cause de mon nom, aura quitte des freres, des soeurs, un pere, une mere, des enfants, des terres, des maisons, recevra beaucoup plus et possedera la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 19, 30, 'Plusieurs des premiers seront derniers et des derniers premiers ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 1, 'Voici, en effet, a quoi est semblable le Royaume des cieux : Un maitre de maison sortit de tres grand matin, afin de louer des ouvriers pour sa vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 2, 'Il convint avec ces ouvriers d\'un denier pour la journee et les envoya a sa vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 3, 'En sortant de nouveau vers la troisieme heure, il en vit d\'autres qui restaient sur la place publique sans rien faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 4, 'Et il leur dit : Allez, vous aussi, a ma vigne, et je vous donnerai ce qui est juste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 5, 'Ils y allerent. Il sortit egalement vers la sixieme heure, puis vers la neuvieme, et fit de meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 6, 'Etant enfin sorti vers la onzieme heure, il en trouva encore d\'autres qui restaient la et il leur dit : Pourquoi restez-vous ici toute la journee a ne rien faire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 7, 'Ils repondirent : Parce que personne ne nous a loues, et il leur dit : Allez, vous aussi, a la vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 8, 'Le soir venu, le maitre de la vigne dit a son intendant : Appelle les ouvriers et remets-leur le salaire, en commencant par les derniers et finissant par les premiers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 9, 'Ceux de la onzieme heure se presenterent alors et recurent chacun un denier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 10, 'Les premiers vinrent a leur tour, et ils pensaient qu\'ils recevraient davantage ; mais ils ne recurent, eux aussi, qu\'un denier chacun.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 11, 'En le prenant, ils murmuraient contre le maitre de la maison ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 12, 'ils disaient : Ceux-ci, les derniers, ont fait seulement une heure et tu les egales a nous qui avons porte le poids du jour et de la chaleur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 13, 'Il repondit a l\'un d\'eux : Mon ami, je ne te fais aucun tort ; n\'es-tu pas convenu avec moi d\'un denier?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 14, 'Prends ce qui est a toi et va. Je veux donner a ce dernier autant qu\'a toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 15, 'Ne m\'est-il pas permis de faire de mes biens ce que je veux ? Pourquoi vois-tu de mauvais oeil que je sois bon ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 16, ' Ainsi les derniers seront premiers et les premiers derniers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 17, 'Jesus, montant a Jerusalem, prit, en particulier, les douze et, chemin faisant, il leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 18, 'Voila que nous montons a Jerusalem ; le Fils de l\'homme y sera livre aux chefs des pretres et aux Scribes, qui le condamneront a mort ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 19, 'puis ils le livreront aux paiens, pour qu\'il soit bafoue, flagelle et crucifie ; et le troisieme jour il ressuscitera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 20, 'Alors s\'approcha de lui la mere des fils de Zebedee et ceux-ci avec elle, et elle se prosterna pour lui faire une demande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 21, 'Il lui dit : Que veux-tu? Elle lui repondit : Ordonne que, dans ton Royaume, mes deux fils que voici siegent l\'un a ta droite, l\'autre a ta gauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 22, 'Jesus repartit : Vous ne savez ce que vous demandez. Pouvez-vous boire la coupe que je vais boire moi-meme?  Nous le pouvons lui dirent-ils. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 23, 'Et il repliqua : Oui, ma coupe, vous la boirez ; mais quant a sieger a ma droite ou a ma gauche, ce n\'est pas a moi de l\'accorder, ces places sont a ceux pour qui mon Pere les a preparees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 24, 'Les dix autres qui avaient entendu furent indignes contre les deux freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 25, 'Alors Jesus les appela et leur dit : Vous savez que les princes des nations en sont les dominateurs, que les grands exercent sur elles un pouvoir imperieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 26, 'Il n\'en sera pas ainsi parmi vous ; au contraire, qui voudra devenir grand parmi vous sera votre serviteur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 27, 'qui voudra etre le premier parmi vous sera votre esclave,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 28, 'comme le Fils de l\'homme, qui n\'est pas venu pour etre servi, mais pour servir et donner sa vie comme rancon pour plusieurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 29, 'A leur sortie de Jericho, Jesus fut suivi d\'une grande foule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 30, 'Et voici que deux aveugles, assis le long du chemin, entendant dire que c\'etait Jesus qui passait, se mirent a crier : Aie pitie de nous, Seigneur Fils de David !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 31, 'La foule les menacait pour les faire taire, mais ils crierent plus fort : Aie pitie de nous, Seigneur, Fils de David!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 32, 'Jesus s\'arreta, les appela et leur dit : Que voulez-vous que je fasse pour vous ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 33, 'Ils lui repondirent : Seigneur, que nos yeux soient ouverts !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 20, 34, 'Emu de compassion, Jesus toucha leurs yeux. Et subitement ils virent, et ils le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 1, 'Quand ils furent pres de Jerusalem et furent arrives a Bethphage, aux environs du mont des Oliviers, Jesus envoya deux disciples,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 2, 'en leur disant : Allez a ce village qui est devant vous, et vous trouverez tout de suite une anesse attachee et son anon avec elle. Deliez-les et amenez-les-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 3, 'Et si l\'on vous dit quelque chose, vous repondrez : Le Seigneur en a besoin ; on les enverra immediatement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 4, '(Or, tout cela eut lieu afin que fut accomplie la parole du prophete :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 5, 'Dites a la fille de Sion : Voici que ton Roi vient a toi, Plein de douceur, monte sur une anesse Et sur un anon, le petit d\'une bete de somme.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 6, 'Les disciples partirent et firent ce que Jesus leur avait ordonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 7, 'Ils amenerent l\'anesse et l\'anon ; ils placerent sur eux leurs manteaux et Jesus s\'assit dessus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 8, 'La plus grande partie du peuple etendait ses vetements sur le chemin ; d\'autres coupaient des branches aux arbres et en jonchaient la route.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 9, 'Et toute cette multitude, ceux qui le precedaient et ceux qui le suivaient, criait : Hosanna pour le Fils de David ! Beni soit celui qui vient au nom du Seigneur ! Hosanna au plus haut des cieux!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 10, 'A son entree dans Jerusalem, la ville entiere fut en emoi ; on disait : Qui est celui-ci ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 11, 'et les multitudes repondaient : C\'est le prophete Jesus de Nazareth de Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 12, 'Jesus entra dans le Temple, et il en chassa tous les vendeurs et tous les acheteurs ; il renversa les tables des changeurs, ainsi que les sieges des marchands de colombes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 13, 'et il leur dit : Il est ecrit : Ma maison s\'appellera maison de priere. Eh bien! vous en faites une caverne de brigands.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 14, 'Dans le Temple, aveugles et estropies s\'approcherent de lui, et il les guerit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 15, 'Voyant les merveilles qu\'il accomplissait et les enfants crier dans le Temple et dire : Hosanna pour le Fils de David! les chefs des pretres et les Scribes furent indignes :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 16, 'Est-ce que tu entends ce que ceux-la crient? lui dirent-ils. Oui, leur repondit Jesus, n\'avez-vous donc jamais lu cette parole : Dans la bouche des petits enfants et des nourrissons qu\'on allaite, Tu t\'es prepare des louanges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 17, 'Puis il les quitta et sortit de la ville pour aller a Bethanie, ou il passa la nuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 18, 'Le matin, en revenant a la ville, il eut faim,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 19, 'et voyant sur le chemin un figuier isole, il s\'en approcha ; mais il n\'y trouva rien, il n\'y avait que des feuilles. Il lui dit alors : Que jamais fruit ne naisse de toi desormais!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 20, 'A l\'instant meme le figuier secha. A cette vue, les disciples s\'etonnerent et ils dirent : Comment ce. figuier s\'est-il instantanement desseche?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 21, 'Jesus leur repondit par ces paroles : En verite, je vous le dis : si vous aviez de la foi et que vous ne doutiez point, non seulement vous feriez ce que j\'ai fait a ce figuier, mais quand meme vous diriez a cette montagne : Souleve-toi et jette-toi dans la mer, cela s\'accomplirait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 22, 'Tout ce que, dans la priere, vous demanderez avec foi, vous le recevrez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 23, 'Quand il fut entre dans le Temple, les chefs des pretres et les Anciens du peuple vinrent a lui, pendant qu\'il enseignait, et lui dirent : En vertu de quelle autorite fais-tu ces choses? et qui t\'a donne cette autorite ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 24, 'Jesus leur repondit par ces paroles : Je vous ferai, moi aussi, une question, une seule, et si vous me repondez, je vous dirai en vertu de quelle autorite je fais ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 25, 'Le bapteme de Jean, d\'ou venait-il? du ciel ou des hommes? Or ils faisaient, a part eux, ce raisonnement :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 26, 'Si nous repondons : du ciel,  il nous dira : Pourquoi donc ne l\'avez-vous pas cru? Et si nous repondons : des hommes,  nous avons a craindre la multitude, car tous tiennent Jean pour prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 27, 'Alors ils repondirent ainsi a Jesus : Nous ne savons. Et lui, il leur dit a son tour : Je ne vous dis pas, moi non plus, en vertu de quelle autorite je fais ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 28, 'Que pensez-vous de ceci : un homme avait deux fils. Il s\'adressa au premier et lui dit : Mon enfant, va travailler aujourd\'hui a ma vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 29, 'Celui-ci repondit : J\'y vais, seigneur  et n\'y alla pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 30, 'Venant ensuite a un autre fils, le pere lui parla de meme. Celui-la repondit : Je ne veux pas  et plus tard, touche de repentir, il y alla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 31, 'Lequel des deux a fait la volonte de son pere ?  C\'est le dernier repondirent-ils. Et Jesus ajouta : En verite, je vous le dis, les publicains et les femmes de mauvaise vie vous precedent dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 32, 'Jean, en effet, est venu a vous dans la voie de la justice et, vous ne l\'avez pas cru. Les publicains et les femmes de mauvaise vie l\'ont cru ; et vous, qui avez vu cela, vous ne vous etes pas repentis, vous n\'avez pas fini par le croire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 33, 'Ecoutez une autre parabole : Il y avait un homme, un maitre de maison, qui avait plante une vigne ; apres l\'avoir entouree d\'une haie, y avoir creuse une cuve et construit une tour, il la loua a des vignerons et fit une absence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 34, 'Lorsque vint la saison de la recolte, il envoya ses serviteurs aux vignerons pour recueillir le produit de sa vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 35, 'Or les vignerons s\'emparerent de ces serviteurs, ils battirent celui-ci, ils tuerent celui-la, ils en lapiderent un troisieme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 36, 'Le maitre alors leur en envoya d\'autres, plus nombreux que les premiers ; les vignerons les traiterent de meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 37, 'Enfin, il leur envoya son fils, disant : Ils respecteront mon fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 38, 'Mais quand ils virent le fils, ils se dirent a part eux : Celui-la, c\'est l\'heritier ; allons ! tuons-le et nous aurons son heritage !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 39, 'S\'etant donc empares de lui, ils le jeterent hors de la vigne et le tuerent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 40, 'Eh bien, lorsque le maitre de la vigne arrivera, que fera-t-il a ces vignerons?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 41, 'On lui repondit :  Il fera perir miserablement ces miserables et il louera la vigne a d\'autres vignerons qui lui en rendront les fruits a la saison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 42, 'Jesus leur dit : Est-ce que vous n\'avez jamais lu dans les Ecritures : La pierre qu\'ont rejetee ceux qui batissaient, Celle-la meme est devenue la tete de l\'angle ; C\'est l\'ouvrage du Seigneur, Ouvrage admirable a nos yeux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 43, 'Voila pourquoi je vous dis que le Royaume de Dieu vous sera enleve et quil sera donne a un peuple qui en produira les fruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 44, 'Celui qui tombera sur cette pierre s\'y brisera et celui sur qui elle tombera, elle le mettra en poussiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 45, 'En entendant ces paraboles, les chefs des pretres et les Pharisiens comprirent que c\'etait d\'eux qu\'il parlait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 21, 46, 'Ils cherchaient bien a l\'arreter, mais ils redoutaient la multitude, parce qu\'elle tenait Jesus pour prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 1, 'Il reprit la parole, et continuant de raconter des paraboles, il leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 2, 'Voici une similitude du Royaume des cieux : un roi, qui celebrait les noces de son fils,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 3, 'envoya ses serviteurs prevenir les convies de s\'y rendre ; mais ils ne voulurent pas venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 4, 'D\'autres serviteurs furent, une seconde fois, envoyes avec cet ordre : dites ceci aux convies : voici mon festin prepare ; mes boeufs et les animaux engraisses sont tues ; tout est pret ; venez aux noces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 5, 'Mais les convies n\'en tinrent nul compte et ils s\'en allerent, celui-ci a sa campagne, celui-la a ses affaires.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 6, 'Quant aux autres, ils s\'emparerent des serviteurs, les outragerent et les tuerent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 7, 'Se mettant en colere, le roi envoya ses troupes exterminer ces meurtriers et incendier leur ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 8, 'Il dit ensuite a ses serviteurs : Le festin de noces est pret, mais ceux qui y avaient ete invites n\'en ont pas ete dignes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 9, 'Parcourez donc les carrefours ; et tous ceux que vous trouverez, appelez-les au festin de noces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 10, 'Les serviteurs s\'en allerent alors par les rues, rassemblerent tous ceux qu\'ils rencontrerent, bons et mauvais, de sorte que la salle fut remplie de convives.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 11, 'Etant entre pour voir ceux qui etaient a table, le roi vit la un homme qui n\'avait point revetu la robe nuptiale.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 12, 'Mon ami, lui demanda-t-il, comment es-tu entre ici sans avoir de robe nuptiale? Il eut la bouche fermee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 13, 'Alors le roi dit aux gens de service: Liez-le, pieds et mains, et jetez-le dehors, dans les tenebres ; la seront les pleurs et le grincement des dents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 14, 'Beaucoup, en effet, sont appeles ; peu sont elus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 15, 'Alors les Pharisiens, en s\'en allant, se concerterent pour le prendre au piege par ses propres paroles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 16, 'et ils lui envoyerent leurs disciples, accompagnes des Herodiens, qui lui dirent : Maitre, nous savons que tu es veridique et que tu enseignes en toute verite la voie de Dieu, sans avoir souci de qui que ce soit, sans regarder a la figure des uns ou des autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 17, 'Dis-nous donc quel est ton avis : Est-il permis, oui ou non, de payer l\'impot a Cesar ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 18, 'Mais Jesus, connaissant leur malice, repondit : Pourquoi me mettez-vous a l\'epreuve, hypocrites !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 19, 'montrez-moi la monnaie de l\'impot. Ils lui presenterent un denier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 20, 'De qui est cette image? demanda-t-il, de qui cette inscription?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 21, 'Ils repondirent : de Cesar. Alors il leur dit : Rendez donc a Cesar ce qui est a Cesar et a Dieu ce qui est a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 22, 'Cette reponse les surprit et ils le laisserent, ils partirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 23, 'Ce meme jour, des Sadduceens, disant qu\'il n\'y a point de resurrection, vinrent lui poser une question.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 24, 'Maitre, dirent-ils, Moise a dit : Si quelqu\'un meurt sans laisser d\'enfants, que son frere epouse sa veuve et suscite a celui-ci une posterite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 25, 'Or, parmi nous, il y a eu sept freres. Le premier s\'est marie et est mort sans avoir de posterite ; il a laisse sa femme a son frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 26, 'Pareillement le second, puis le troisieme, jusqu\'au septieme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 27, 'Enfin, apres eux tous, la femme est morte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 28, 'Duquel des sept, ayant ete la femme detours, sera-t-elle donc l\'epouse a la resurrection ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 29, 'Jesus leur repondit par ces paroles : Vous etes dans l\'erreur, parce que vous ignorez et les Ecritures et la puissance de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 30, 'Les ressuscites, en effet, ne se marient pas et ne donnent pas en mariage, mais ils sont dans le ciel comme des anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 31, 'Et quant a la resurrection des morts, n\'avez-vous pas lu cette parole que Dieu vous a dite :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 32, 'Je suis le Dieu d\'Abraham, le Dieu d\' Isaac, le Dieu de Jacob. Or ce n\'est point de morts, c\'est de vivants qu\'il est Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 33, 'Les multitudes qui entendaient etaient extremement frappees de son enseignement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 34, 'Apprenant qu\'il avait ferme la bouche aux Sadduceens, les Pharisiens se reunirent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 35, 'et l\'un d\'eux, un legiste, lui demanda pour le mettre a l\'epreuve :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 36, 'Maitre, quel est, dans la Loi, le plus grand commandement?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 37, 'Jesus lui dit : Tu aimeras le Seigneur ton Dieu, de tout ton coeur, de toute ton ame, de toute ta pensee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 38, 'C\'est le grand et le premier commandement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 39, 'Il y en a un second qui lui est semblable : Tu aimeras ton prochain comme toi meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 40, 'De ces deux commandements dependent la Loi tout entiere, ainsi que les Prophetes .');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 41, 'Les Pharisiens se trouvant rassembles, Jesus leur posa cette question :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 42, 'Quelle est votre opinion sur le Christ? de qui est-il fils? Ils lui dirent : de David.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 43, 'Comment donc, reprit-il, David inspire l\'appelle-t-il Seigneur quand il dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 44, 'Le Seigneur a dit a mon Seigneur, Siege a ma droite Jusqu\'a ce que j\'aie mis tes ennemis sous tes pieds ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 45, 'Si David l\'appelle Seigneur comment donc est-il son fils? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 22, 46, 'Nul ne put lui repondre un mot et, depuis ce jour-la, personne n\'osa plus le questionner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 1, 'Jesus alors, parlant a la multitude en meme temps qu\'a ses disciples,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 2, 'dit : C\'est dans la chaire de Moise que sont assis tes Scribes et les Pharisiens ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 3, 'observez donc tout ce qu\'ils vous disent et faites-le. Mais n\'imitez pas leurs oeuvres, car ils disent et ne font pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 4, 'Ils lient en faisceau de pesants fardeaux et ils en chargent l\'epaule des hommes ; mais eux ils ne veulent pas les remuer du bout du doigt.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 5, 'Toutes leurs actions ils les font pour etre vus des autres ; en effet, ils portent d\'enormes phylacteres et leurs franges sont interminables ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 6, 'dans les festins ils aiment la premiere place, dans les synagogues les premiers sieges,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 7, 'sur les places publiques les salutations, et que chacun leur donne le nom de Rabbi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 8, 'Quant a vous, ne vous faites pas appeler : Rabbi ; car un seul est votre maitre, et pour vous, vous etes tous freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 9, 'Et ne donnez a personne sur terre le nom de pere : car un seul est votre Pere : le Pere celeste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 10, 'Qu\'on ne vous appelle pas non plus directeurs, car vous n\'avez qu\'un seul directeur : le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 11, 'Le plus grand parmi vous sera votre serviteur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 12, 'Qui s\'elevera lui-meme sera abaisse ; qui s\'abaissera lui-meme sera eleve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 13, 'Malheur a vous, Scribes et Pharisiens hypocrites ! parce que vous fermez la porte du Royaume des cieux au visage des hommes. Vous, vous n\'entrez pas, et ceux qui viennent pour entrer vous ne les laissez pas entrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 14, '(Malheur a vous, Scribes et Pharisiens hypocrites ! parce que vous devorez les ressources des veuves, en affectant de prier longuement, Voila pourquoi le jugement aura pour vous plus de rigueur).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 15, 'Malheur a vous, Scribes et Pharisiens hypocrites ! parce que vous parcourez terres et mers pour faire un seul proselyte : et, lorsqu\'il l\'est devenu, vous en faites un fils de la Gehenne, deux fois pire que vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 16, 'Malheur a vous, guides aveugles! qui dites : Si quelqu\'un jure par le Temple, ce n\'est rien du tout ; mais si quelqu\'un jure par l\'or du Temple, il est engage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 17, 'Insenses et aveugles ! lequel est donc le plus grand, ou de l\'or ou du Temple qui sanctifie l\'or?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 18, 'Et encore : Si quelqu\'un jure par l\'autel, ce n\'est rien du tout ; mais si quelqu\'un jure par l\'offrande qui est sur l\'autel, il est engage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 19, 'Aveugles, lequel est le plus grand ou de l\'offrande, ou de l\'autel qui sanctifie l\'offrande?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 20, 'Qui jure par l\'autel, jure et par l\'autel et par tout ce qu\'il porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 21, 'Qui jure par le Temple, jure et par le Temple et par Celui dont il est la demeure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 22, 'Qui jure par le ciel, jure et par le trone de Dieu et par Celui qui y est assis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 23, 'Malheur a vous, Scribes et Pharisiens hypocrites ! parce que vous acquittez la dime, et de la menthe, et de l\'aneth, et du cumin et que vous laissez de cote ce qu\'il y a de plus grave dans la Loi : la justice, la pitie, la bonne foi ! Il fallait faire ceci et ne pas omettre cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 24, 'Guides aveugles ! qui retenez au filtre le moucheron et qui engloutissez le chameau!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 25, 'Malheur a vous, Scribes et Pharisiens hypocrites, parce que vous purifiez le dehors de la coupe et du plat quand ils sont remplis au dedans de rapines et d\'immondices!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 26, 'Pharisien aveugle, purifie d\'abord l\'interieur de la coupe et du plat s, afin que le dehors devienne pur aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 27, 'Malheur a vous, Scribes et Pharisiens hypocrites, parce que vous etes pareils a des sepulcres blanchis ! Ils ont, a l\'exterieur, une belle apparence ; mais au dedans, ils sont remplis d\'ossements de morts et de toute sorte de pourriture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 28, 'Vous de meme ; a l\'exterieur, vous paraissez justes aux hommes ; mais, au dedans, vous etes pleins d\'hypocrisie et d\'iniquite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 29, 'Malheur a vous, Scribes et Pharisiens hypocrites, parce que vous elevez des tombeaux aux prophetes, que vous ornez les sepulcres des justes');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 30, 'et que vous dites : Si nous eussions ete la du temps de nos peres, nous n\'aurions pas ete leurs complices dans le meurtre des prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 31, 'Et par la, vous vous rendez a vous-memes le temoignage que vous etes bien les fils des meurtriers des prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 32, 'Ainsi, vous, vous comblez la mesure de vos peres !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 33, 'Serpents! Engeance de viperes ! comment eviterez-vous la condamnation de la Gehenne ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 34, 'C\'est pour cela que moi-meme je vous envoie prophetes, sages et docteurs. Les uns vous les tuerez, vous les crucifierez ; les autres vous les flagellerez dans vos synagogues ; vous les traquerez de ville en ville,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 35, 'afin que retombe sur vous tout sang innocent verse sur la terre depuis le sang d\'Abel le juste jusqu\'au sang de Zaccharie, fils de Barachie, que vous avez massacre entre le Temple et l\'autel !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 36, 'Je vous le dis en verite, cest sur cette generation-ci que tout cela retombera !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 37, 'Jerusalem ! Jerusalem ! qui tues les prophetes et qui lapides ceux qui te sont envoyes, combien de fois ai-je voulu rassembler tes enfants, comme la poules rassemble ses poussins sous ses ailes, et tu ne l\'as pas voulu !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 38, 'Eh bien, votre demeure vous est laissee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 23, 39, 'Car je vous le declare, vous ne me verrez plus, jusqu\'a ce que vous disiez : Beni soit celui qui vient au nom du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 1, 'Jesus sortait du Temple et s\'eloignait, lorsque ses disciples vinrent lui en faire remarquer les diverses constructions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 2, 'Alors il leur parla ainsi : De tout ce que vous voyez la, je vous le dis en verite, il ne restera pas pierre sur pierre. Tout sera renverse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 3, 'Puis il alla s\'asseoir sur le mont des Oliviers, les disciples s\'approcherent de lui et, le prenant a part, l\'interrogerent : Dis-nous quand ces choses arriveront et quel sera le signe de ton avenement et de la fin du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 4, 'Jesus leur repondit par ces paroles: Veillez a ce que personne ne vous seduise ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 5, 'car plusieurs viendront en prenant mon nom, ils diront : Je suis, moi, le Christ et ils seduiront bien des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 6, 'Vous entendrez parler de guerres et de bruits de guerres ; gardez-vous de vous en troubler ; car il faut que cela arrive ; mais ce ne sera pas encore la fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 7, 'Se soulevera, en effet, nation contre nation, royaume contre royaume. Il y aura, ici et la, des famines et des tremblements de terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 8, 'Tout cela ce seront les douleurs de l\'enfantement qui commencent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 9, 'On vous livrera alors aux tourments ; on vous mettra a mort ; vous serez, a cause de mon nom, en haine a toutes les nations.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 10, 'Et alors beaucoup seront pris au piege ; ils se trahiront les uns les autres, ils se hairont entre eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 11, 'Plusieurs faux prophetes surgiront et seduiront un grand nombre d\'hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 12, 'Et comme l\'iniquite augmentera, l\'amour du plus grand nombre se refroidira ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 13, 'mais celui-la sera sauve qui aura persevere jusqu\'a la fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 14, 'Cet Evangile du Royaume sera preche par toute la terre et atteste a toutes les nations. Et c\'est alors que viendra la fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 15, 'Quand vous verrez l\'abomination de la desolation, comme dit le prophete Daniel, installee dans le Lieu Saint (comprends bien, lecteur!),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 16, 'que ceux qui seront dans la Judee fuient aux montagnes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 17, 'que celui qui sera sur le toit n\'en descende point pour emporter ce qui est dans sa maison ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 18, 'que celui qui sera dans les champs ne revienne pas sur ses pas pour prendre son manteau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 19, 'Malheur aux femmes qui seront enceintes et a celles qui allaiteront en ces jours-la !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 20, 'Priez pour que votre fuite n\'arrive ni en hiver, ni en un jour de sabbat,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 21, 'car il y aura alors une grande tribulation, telle qu\'il n\'yen a pas eu de semblable depuis le commencement du monde jusqu\'a present, et qu\'il n\'y en aura plus jamais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 22, 'Et si ces jours n\'avaient pas ete abreges, nulle vie ne serait sauvee ; mais, a cause des elus, ces jours seront abreges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 23, 'Si l\'on vous dit alors : Voici le Christ est ici! ou : Le voila! ne le croyez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 24, 'Car il surgira de faux Christs, et de faux prophetes, faisant de grands signes et prodiges, afin de seduire, si possible, meme les elus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 25, 'Ainsi, je vous ai prevenus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 26, 'Si donc quelqu\'un vous dit : Voila qu\'il est dans le desert! n\'y allez pas. Le voila dans l\'interieur de la maison! ne le croyez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 27, 'Tel, en effet, l\'eclair part d\'Orient et brille jusqu\'a l\'Occident, tel sera l\'avenement du Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 28, 'Partout ou sera le cadavre, s\'assembleront les vautours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 29, 'Immediatement apres ces jours de tribulation, le soleil s\'obscurcira, la lune ne donnera plus sa lumiere, les etoiles tomberont du ciel, les puissances des cieux seront ebranlees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 30, 'C\'est alors qu\'apparaitra dans le ciel le signe du Fils de l\'homme, et alors se frapperont la poitrine toutes les tribus de la terre, et elles verront le Fils de l\'homme arrivant sur les nuees du ciel, en puissance et grande gloire,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 31, 'et il enverra ses anges, lesquels, au son eclatant de la trompette, rassembleront ses elus des quatre Vents de l\'horizon, d\'une extremite du ciel a l\'autre extremite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 32, 'Que le figuier vous serve de comparaison : quand ses branches deviennent tendres et que ses feuilles poussent, vous savez que l\'ete est proche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 33, 'de meme quand vous verrez toutes ces choses, sachez qu\'ils est proche, qu\'il est aux portes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 34, 'En verite, je vous le dis, cette generation ne passera pas que tout cela n\'arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 35, 'Le ciel et la terre passeront, mais mes paroles ne passeront point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 36, 'De ce jour et de l\'heure nul ne sait rien, ni les anges des cieux, ni le Fils ; personne, excepte le Pere, seul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 37, 'Tels les jours de Noe, tel l\'avenement du Fils de l\'homme :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 38, 'de meme, en effet, que, dans les jours precedant le deluge, les gens mangeaient, buvaient, se mariaient, mariaient les leurs, jusqu\'au moment ou Noe entra dans l\'arche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 39, 'de meme qu\'ils ne comprirent rien jusqu\'a ce que survint le deluge qui les emporta tous, de meme en sera-t-il de l\'avenement du Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 40, 'Deux hommes seront alors dans un champ : l\'un sera pris ; l\'autre sera laisse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 41, 'Deux femmes seront a tourner la meule : l\'une sera prise ; l\'autre sera laissee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 42, 'Veillez donc, parce que vous ne savez pas quel jour viendra votre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 43, 'Sachez ceci : si le maitre de la maison connaissait l\'heure de la nuit a laquelle doit venir le voleur, il veillerait, il ne laisserait pas forcer sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 44, 'Eh bien ! vous, soyez prets, parce que c\'est a une heure ou vous n\'y penserez pas que le Fils de l\'homme viendra.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 45, 'Quel est, par consequent, le serviteur fidele et prudent que son maitre a mis a la tete de ses gens pour donner a chacun sa nourriture au moment voulu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 46, 'C\'est celui que le maitre, a son arrivee, trouvera agissant ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 47, 'Heureux ce serviteur! En verite, je vous le dis, le maitre l\'etablira sur tous ses biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 48, 'Mais si le serviteur, etant mauvais, dit en son coeur : Mon maitre tarde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 49, 'et qu\'il se mette a battre ses compagnons de service, a manger et a boire avec les ivrognes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 50, 'son maitre surviendra au jour ou ce serviteur ne s\'y attend pas et a une heure qu\'il ignore,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 24, 51, 'et il le, dechirera a coups de verges et lui donnera le meme lot qu\'aux hypocrites. La seront les pleurs et le grincement des dents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 1, 'Alors le Royaume des cieux sera semblable a dix vierges qui, allerent, leurs lampes a la main, a la rencontre de l\'epoux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 2, 'Cinq d\'entre elles etaient folles, cinq etaient sages.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 3, 'Folles, car, en prenant leurs lampes, elles n\'avaient pas emporte d\'huile avec elles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 4, 'mais les sages avaient pris, avec leurs lampes, de l\'huile dans des vases.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 5, 'L\'epoux tardant a venir, elles s\'assoupirent toutes et s\'endormirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 6, 'Au milieu de la nuit un cri retentit : Voici l\'epoux ! sortez au-devant de lui!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 7, 'Alors toutes ces vierges se leverent et appreterent leurs lampes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 8, 'et les folles dirent aux sages : Nos lampes s\'eteignent ; donnez-nous de votre huile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 9, 'Les sages repondirent : Il n\'y en aurait peut-etre pas assez pour nous et pour vous ; allez plutot a ceux qui en vendent et vous en achetez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 10, 'Or, pendant qu\'elles allaient en acheter, l\'epoux vint, et celles qui etaient pretes entrerent avec lui au festin de noces ; et la porte fut fermee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 11, 'Enfin arrivent aussi les autres vierges, en disant : Seigneur! Seigneur! ouvre-nous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 12, 'mais il leur repondit : En verite, je vous le dis, je ne vous connais pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 13, 'Donc, veillez, parce que vous ne savez ni le jour ni l\'heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 14, 'Tel encore le Royaume des cieux, tel un homme qui, partant pour un voyage, appela ses serviteurs et leur confia ses biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 15, 'A celui-ci, il donna cinq talents ; a celui-la, deux ; a ce troisieme, un seul ; a chacun suivant sa capacite personnelle. Aussitot apres, il partit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 16, 'Celui qui avait recu les cinq talents alla les faire valoir et en gagna cinq autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 17, 'De meme celui qui en avait deux en gagna deux autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 18, 'Mais celui qui n\'en avait recu qu\'un, alla faire un trou dans la terre et il y enfouit l\'argent de son maitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 19, 'Apres un long temps ecoule, le maitre revint et il fit rendre compte a ses serviteurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 20, 'Celui qui avait recu les cinq talents se presenta, en apportant cinq autres. Seigneur, dit-il, tu m\'as confie cinq talents ; en voici cinq autres que j\'ai gagnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 21, 'Le maitre lui dit : Bien, serviteur bon et fidele ; c\'est dans peu de chose que tu t\'es montre fidele, c\'est sur beaucoup que je t\'etablirai. Entre dans la joie de ton seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 22, 'S\'approchant aussi, celui qui avait recu les deux talents, dit : Seigneur, tu m\'as confie deux talents ; en voici deux autres que j\'ai gagnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 23, 'Son maitre lui dit : Bien, serviteur bon et fidele ; c\'est dans peu de chose que tu t\'es montre fidele, c\'est sur beaucoup que je t\'etablirai. Entre dans la joie de ton seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 24, 'Mais celui qui avait recu un seul talent vint a son tour et dit : Seigneur, je savais que tu es un homme dur, tu moissonnes la ou tu n\'as pas seme, tu ramasses sur l\'aire ou tu n\'as pas etendu de gerbes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 25, 'et, plein de crainte, je suis alle enfouir en terre ton talent. Le voila ; tu es en possession de ce qui t\'appartient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 26, 'Mais son maitre lui repondit par ces paroles : Mauvais serviteur ! paresseux ! tu savais que je moissonne la ou je n\'ai pas seme et que je ramasse sur l\'aire ou je n\'ai pas etendu de gerbes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 27, 'Il te fallait en consequence placer ton argent chez les banquiers, et, a mon retour, j\'aurais retire ce qui est a moi avec un interet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 28, 'Donc, otez lui le talent et donnez-le a celui qui a les dix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 29, 'Car a tout homme qui a il sera donne, et il sera dans l\'abondance ; mais a celui qui n\'a pas, meme ce qu\'il a lui sera ote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 30, 'Quant au serviteur inutile, jetez-le dehors, dans les tenebres,  la seront les pleurs et le grincement des dents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 31, 'Lorsque le Fils de l\'homme viendra dans sa gloire, et, avec lui, tous les anges, il s\'assiera sur son trone de gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 32, 'Devant lui seront rassemblees toutes les nations ; et il separera les uns d\'avec les autres, comme le berger separe les brebis d\'avec les boucs ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 33, 'il placera les brebis a sa droite, les boucs a sa gauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 34, 'Apres quoi, le Roi dira a ceux qui seront a sa droite : Venez, les benis de mon Pere! entrez en possession du Royaume qui vous a ete prepare depuis la creation du monde ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 35, 'car j\'ai eu faim, et vous m\'avez donne a manger ; j\'ai eu soif, et vous m\'avez donne a boire ; j\'etais etranger, et vous m\'avez recueilli ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 36, 'nu, et vous m\'avez vetu ; malade, et vous m\'avez visite ; en prison, et vous etes venus pres de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 37, 'Les justes alors lui repondront par ces paroles: Seigneur, quand est-ce que nous t\'avons vu avoir faim et que nous t\'avons nourri? Avoir soif et que nous t\'avons donne a boire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 38, 'Quand est-ce que, te voyant etranger, nous t\'avons recueilli ; que, te voyant dans la nudite, nous t\'avons vetu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 39, 'Quand est-ce que nous t\'avons vu malade ou en prison et que nous sommes venus pres de toi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 40, 'Et le Roi leur fera cette reponse : En verite,. je vous le dis, toutes les fois que vous avez fait cela a un seul des plus petits parmi mes freres que voici, c\'est a moi que vous l\'avez fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 41, 'Puis il dira a ceux qui seront a sa gauche : Allez loin de moi, maudits, dans le feu eternel prepare pour le diable et ses anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 42, 'Car j\'ai eu faim et vous ne m\'avez pas donne a manger ; jai eu soif et vous ne m\'avez pas donne a boire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 43, 'j\'etais etranger et vous ne m\'avez pas recueilli ; nu et vous ne m\'avez pas vetu ; malade et en prison et vous ne m\'avez pas visite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 44, 'Alors ils repondront, eux aussi, par ces paroles : Seigneur, quand est-ce que nous t\'avons vu avoir faim, avoir soif, etre etranger ou nu, ou malade, ou en prison, et que nous ne t\'avons pas assiste ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 45, 'Mais il leur fera cette reponse : En verite, je vous le dis, toutes les fois que vous n\'avez pas fait cela a un seul de ces plus petits, que voici, c\'est a moi que vous ne l\'avez pas fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 25, 46, 'Et ceux-la s\'en iront a un chatiment eternel et les justes a une vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 1, 'Quand Jesus eut acheve tous ces discours, il dit a ses disciples :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 2, 'Vous savez que dans deux jours aura lieu la Paque, et le Fils de l\'homme va etre livre pour etre crucifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 3, 'Alors s\'assemblerent les chefs des pretres et les Anciens du peuple dans la coure du Grand-Pretre appele Kaiphe,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 4, 'et ils tinrent conseil sur les moyens de s\'emparer de Jesus par ruse et de le tuer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 5, 'Toutefois ils disaient : Rien pendant la fete, il pourrait y avoir un soulevement du peuple. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 6, 'Jesus etait a Bethanie, dans la maison de Simon le lepreux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 7, 'Comme il etait a table, une femme portant un vase d\'albatre rempli d\'un parfum de grand prix, s\'approcha de lui et repandit ce parfum sur sa tete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 8, 'Ce que voyant, les disciples s\'indignerent. A quoi bon cette perte? dirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 9, 'On aurait pu vendre cela fort cher et en donner le prix a des pauvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 10, 'Mais Jesus, le sachant, leur dit : Pourquoi faites-vous de la peine a cette femme? C\'est une bonne oeuvre qu\'elle a faite pour moi :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 11, 'car les pauvres, vous les avez toujours avec vous, tandis que moi, vous ne m\'avez pas pour toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 12, 'Si elle a repandu sur mon corps ce parfum, elle l\'a fait pour m\'ensevelir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 13, 'En verite, je vous le dis : Partout ou sera preche cet Evangile  dans le monde entier  on racontera aussi, en memoire d\'elle, ce qu\'elle a fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 14, 'C\'est alors que l\'un des douze, appele Judas l\'Iskariote, alla, trouver les chefs des pretres');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 15, 'et leur dit : Que voulez-vous me donner? je vous le livrerai. Ils lui payerent trente sicles d\'argent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 16, 'A partir de ce moment, il chercha une occasion favorable pour le livrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 17, 'Le premier jour des Azymes, les disciples vinrent a Jesus et dirent : Ou veux-tu que nous te preparions le repas pascal?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 18, 'Allez a la ville, leur repondit-il, chez un tel et parlez-lui ainsi : le maitre te fait dire : mon temps est proche ; c\'est chez toi que je celebre la Paque avec mes disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 19, 'Les disciples firent comme Jesus leur avait ordonne et ils preparerent la Paque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 20, 'Le soir venu, il se mit a table avec les douze,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 21, 'et il dit pendant quils mangeaient : En verite, je vous le dis, un de vous me livrera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 22, 'Extremement affliges, ils se mirent chacun a lui demander : Est-ce moi, Seigneur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 23, 'Il leur repondit par ces paroles : Celui qui a mis la main au plat avec moi, celui-la meme me livrera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 24, 'Le Fils de l\'homme cependant s\'en va, selon qu\'il est ecrit de lui ; mais malheur a cet homme par qui le Fils de l\'homme est livre ! Mieux vaudrait pour cet homme-la ne jamais etre ne !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 25, 'Judas, celui qui le livra, s\'adressa alors a lui : Est-ce que c\'est moi, Rabbi ? - Tu las dit, repondit Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 26, 'Pendant qu\'ils mangeaient, Jesus prit du pain et, ayant prononce la benediction, il le rompit et le donna aux disciples en disant : Prenez, mangez, ceci est mon corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 27, 'Prenant ensuite une coupe, il rendit graces, la leur donna et dit : Buvez-en tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 28, 'car ceci est mon sang, le sang de l\'alliance, lequel, en faveur de plusieurs, est repandu pour la remission des peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 29, 'Et desormais, je vous le dis, je ne boirai plus de ce fruit de la vigne jusqu\'en ce jour ou je le boirai avec vous, nouveau, dans le Royaume de mon Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 30, 'Apres le chant des Psaumes, ils partirent pour le mont des Oliviers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 31, 'Jesus leur dit alors : Cette nuit, je vous serai a tous une occasion de tomber. Il est ecrit en effet : Je frapperai le berger et les brebis du troupeau seront dispersees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 32, 'Mais, apres ma resurrection, je vous precederai en Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 33, 'Pierre lui adressa ces paroles : Quand tous viendraient a faillir a cause de toi, pour moi je ne succomberai jamais !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 34, 'Jesus lui repondit : En verite, je te dis que, durant cette nuit, avant qu\'un coq chante, tu me renieras trois fois!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 35, 'Pierre lui repliqua : Me fallut-il mourir avec toi, non, je ne te renierai pas! Tous les disciples parlerent de meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 36, 'Alors Jesus vint avec les disciples en un lieu appele Gethsemane et leur dit : Asseyez-vous ici pendant que je me retirerai la et que je prierai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 37, 'Puis, prenant avec lui Pierre et les deux fils de Zebedee, il commenca a etre dans la tristesse et dans l\'angoisse ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 38, 'il leur dit alors : Mon ame est accablee de tristesse jusqu\'a la mort. Restez ici et veillez avec moi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 39, 'Puis, s\'etant un peu avance, il se prosterna la face contre terre, et il pria en disant : Mon Pere, si cela est possible, que cette coupe passe loin de moi ! Toutefois, non pas comme je veux, mais comme tu veux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 40, 'Il retourna pres des disciples et il les trouva qui dormaient et il dit a Pierre : Ainsi vous n\'avez pas eu la force de veiller une seule heure avec moi ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 41, 'Veillez et priez, afin de ne pas succombera l\'epreuve ; certes, l\'esprit est prompt, mais la chair est faible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 42, 'De nouveau, une seconde fois, il s\'eloigna et pria, en disant : Mon Pere, si cette coupe ne peut passer sans que je la boive, que ta volonte soit faite !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 43, 'Il revint encore vers eux et les trouva qui dormaient ; leurs yeux, en effet, etaient appesantis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 44, 'Il les laissa, s\'en alla de nouveau et pria pour la troisieme fois, repetant les memes paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 45, 'Puis il vint aux disciples et leur dit : Dormez maintenant ; reposez-vous ; voici que l\'heure approche et le Fils de l\'homme va etre livre entre les mains des pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 46, 'Levez-vous ! allons ! le voici ! il approche celui qui me livre !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 47, 'Il parlait encore, lorsqu\'arriva Judas, l\'un des douze, et avec lui, envoyee par les chefs des pretres et les Anciens du peuple, une foule nombreuse armee d\'epees et de batons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 48, 'Le traitre etait convenu avec eux d\'un signal ; il leur avait dit : Celui auquel je donnerai un baiser, ce sera lui ; vous vous en saisirez,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 49, 'Il s\'approcha aussitot de Jesus et, en lui disant: Salut, Rabbi!, il l\'embrassa, avec effusion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 50, 'Mon ami, lui dit Jesus, pourquoi es-tu ici ? Ils s\'avancerent alors et, mettant la main sur lui, ils le saisirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 51, 'Aussitot un de ceux qui etaient avec Jesus etendit la main, tira son epee, frappa le serviteur du Grand-Pretre et lui coupa l\'oreille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 52, 'Jesus lui dit alors : Remets ton epee a sa place, car tous ceux qui prennent l\'epee periront par l\'epee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 53, 'Crois-tu qu\'il me serait impossible d\'invoquer mon Pere, qui enverrait a mon secours, maintenant meme, plus de douze legions d\'anges ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 54, 'mais comment s\'accompliraient les Ecritures, ou il est annonce qu\'il en doit etre ainsi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 55, 'Ce fut a ce moment que Jesus dit a cette troupe : Pour vous emparer de moi, vous etes venus comme contre un brigand, avec des epees et des batons. Tous les jours, assis dans le Temple, j\'enseignais et vous ne m\'avez pas arrete..');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 56, '(Tout cela arriva pour accomplir ce qu\'avaient ecrit les prophetes). L\'abandonnant alors, tous les disciples prirent la fuite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 57, 'Ceux qui avaient arrete Jesus le conduisirent chez Kaiphe, le grand-pretre, ou s\'etaient reunis les Scribes et les Anciens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 58, 'Pierre pourtant le suivait de loin. Il alla jusqu\'a la cour du Grand-Pretre, y entra et s\'assit parmi les gens de service pour voir comment cela finirait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 59, 'Les chefs des pretres et le Sanhedrin tout entier cherchaient contre Jesus quelque faux temoignage qui le fit condamner a mort ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 60, 'mais ils n\'en trouvaient pas, bien que plusieurs faux temoins se fussent presentes. Enfin, il en survint deux');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 61, 'qui parlerent ainsi : Cet homme a dit: Je peux renverser le Temple de Dieu et en trois jours le rebatir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 62, 'Le Grand-Pretre se leva et lui dit : Tu ne reponds rien a ce que ces gens-ci deposent contre toi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 63, 'Mais Jesus gardait le silence. Le Grand-Pretre lui dit : Par le Dieu vivant, je t\'adjure de nous dire si tu es le Christ, le Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 64, 'Jesus lui repondit : Tu l\'as dit ; et, de plus, je vous le declare, desormais vous verrez le Fils de l\'homme siegeant a la droite de la Puissance et venant sur les nuees du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 65, 'Alors le Grand-Pretre dechira ses vetements : Il a blaspheme! dit-il, qu\'avons-nous encore besoin de temoins? vous venez vous-meme d\'entendre le blaspheme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 66, 'Quel est votre avis? Ils repondirent : Il merite la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 67, 'Des ce moment ils lui cracherent au visage et lui donnerent des coups de poing ; d\'autres des coups de baton,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 68, 'en disant : Fais le prophete, Christ, en nous nommant celui qui t\'a frappe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 69, 'Pierre cependant etait dehors, assis dans la cour. Une servante, setant approchee, lui dit : Toi aussi tu etais avec Jesus de Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 70, 'Et lui de le nier devant tout le monde. Je ne sais pas, dit-il, de quoi tu parles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 71, 'Comme il repassait la porte, une autre servante l\'apercut et s\'adressant a ceux qui etaient presents : Celui-ci etait avec Jesus de Nazareth.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 72, 'Il le nia de nouveau : Je jure que je ne connais pas cet homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 73, 'Un moment apres, ceux qui etaient la vinrent a Pierre et lui dirent : Certainement, tu en es, toi aussi ; ta prononciation te trahit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 74, 'Il se livra alors a des imprecations ; il jura qu\'il ne connaissait pas cet homme. Soudain un coq chanta.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 26, 75, 'Et Pierre se souvint de la parole qu\'avait dite Jesus : Avant qu\'un coq chante, tu me renieras trois fois. Il sortit et, dehors, il pleura amerement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 1, 'Le jour s\'etant leve, tous les chefs des pretres et les Anciens du peuple tinrent conseil sur les moyens de mettre Jesus a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 2, 'Et l\'ayant charge de liens, ils lemmenerent et le livrerent a Pilate, le procurateur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 3, 'En voyant qu\'il etait condamne, celui qui l\'avait livre, Judas, pousse par le remords, rapporta aux chefs des pretres et aux Anciens les trente sicles d\'argent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 4, 'disant : J\'ai peche en livrant un sang innocent.  Ils repondirent : Que nous importe? c\'est ton affaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 5, 'Jetant alors les pieces d\'argent dans le sanctuaire, Judas partit et il alla se pendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 6, 'Mais les chefs des pretres ramasserent la somme, disant : Il n\'est pas permis de la verser dans le Korban, puisque c\'est le prix du sang.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 7, 'Et apres en avoir confere ensemble, ils en acheterent le Champ-du-Potier pour la sepulture des etrangers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 8, 'De la le nom de Champ-du-Sang qu\'on lui donne encore aujourd\'hui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 9, '(C\'est ainsi que fut accomplie la parole du prophete Jeremie : Et ils ont recu les trente pieces d\'argent, prix de celui qui avait ete evalue, evalue par les enfants d\'Israel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 10, 'et ils les ont donnees pour le champ du potier, ainsi que me l\'avait ordonne le Seigneur. )');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 11, 'Jesus comparut devant le procurateur, et celui-ci l\'interrogea : C\'est toi qui es le Roi des Juifs? demanda-t-il. Jesus lui repondit : Tu le dis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 12, 'Mais lorsque les chefs des pretres et les Anciens l\'acculerent, il ne repondit rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 13, 'Alors Pilate lui dit : Est-ce que tu n\'entends pas tout ce dont ils t\'accusent?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 14, 'Jesus ne lui repondit sur aucun point, ce dont le procurateur fut extremement surpris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 15, 'Or a chaque fete il avait coutume de remettre en liberte un prisonnier, celui que le peuple choisissait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 16, 'Il y en avait un fameux detenu a ce moment, du nom de Bar-Abbas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 17, 'Alors Pilate, qui savait que c\'etait par une haine jalouse que les chefs des pretres et les Anciens lui avaient livre Jesus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 18, 'leur dit, pendant qu\'ils etaient rassembles : Lequel voulez-vous que je vous delivre? Bar-Abbas ou Jesus, celui qu\'on appelle Christ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 19, '(Tandis qu\'il siegeait ainsi a son tribunal, sa femme lui envoya dire ceci : Qu\'il n\'y ait rien entre toi et ce juste ; car j\'ai eu aujourd\'hui, dans mon sommeil, un affreux cauchemar a son sujet.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 20, 'Les chefs des pretres et les Anciens persuaderent a la foule de demander Bar-Abbas et de perdre Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 21, 'Le procurateur reprit sa question : Lequel des deux voulez-vous que je vous delivre?  Bar-Abbas ! repondirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 22, 'Pilate leur dit : Et Jesus, celui qu\'on appelle Christ, qu\'en ferai-je donc? Ils repondirent tous : Qu\'il soit crucifie! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 23, 'Mais qu\'a-t-il fait de mal ? dit encore Pilate. Et ils n\'en criaient que plus fort : Qu\'il soit crucifie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 24, 'Voyant qu\'il n\'obtenait rien et que, au contraire, le tumulte augmentait, Pilate demanda de l\'eau, se lava les mains devant la foule et dit : Je ne suis pas responsable du sang de cet homme, a vous d\'en repondre !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 25, 'Et tout le peuple repliqua par ces mots : Le sang de cet homme! qu\'il retombe sur nous et sur nos enfants!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 26, 'Pilate leur delivra alors Bar-Abbas. Quant a Jesus, layant fait flageller, il l\'abandonna au supplice de la croix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 27, 'Les soldats du procurateur, emmenant Jesus dans le pretoire, assemblerent autour de lui toute la cohorte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 28, 'Ils lui oterent ses vetements, l\'affublerent d\'un manteau militaire de couleur rouge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 29, 'Puis, tressant une couronne avec des epines, ils la lui mirent sur la tete, ainsi qu\'un roseau dans la main droite ; apres quoi, se mettant a genoux devant lui, ils le bafouaient en disant : Salut, le roi des Juifs !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 30, 'Ils crachaient sur lui ; ils lui prenaient le roseau et lui en donnaient des coups sur la tete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 31, 'Quand ils eurent fini de se moquer de lui, ils lui oterent le manteau, lui remirent ses vetements et l\'emmenerent pour le crucifier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 32, 'En s\'en allant, ils rencontrerent un homme de Cyrene, nomme Simon ; et ils l\'obligerent a porter la croix de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 33, 'Ils arriverent a un endroit nomme Golgotha, c\'est-a-dire place du Crane.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 34, 'Ils lui donnerent a boire du vin mele de fiel, et l\'ayant goute, il refusa de le boire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 35, 'Apres l\'avoir mis en croix, les soldats se partagerent ses vetements, en les tirant au sort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 36, 'Ils s\'etaient assis et le gardaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 37, 'Au-dessus de sa tete ils avaient place une inscription indiquant son crime : CELUI-CI EST JESUS, LE ROI DES JUIFS.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 38, 'On crucifia avec lui deux brigands, l\'un a sa droite, l\'autre a sa gauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 39, 'Les passants l\'injuriaient ; ils lui disaient, en gesticulant de la tete :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 40, 'Toi qui detruis le Temple, et en trois jours le rebatis, sauve-toi donc toi-meme si tu es fils de Dieu!  et :  Descends de la croix!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 41, 'Les chefs des pretres, avec les Scribes et les Anciens, se moquaient aussi de lui et disaient :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 42, 'Il en a sauve d\'autres et il ne peut pas se sauver lui-meme! Ah! il est roi d\'Israel! que maintenant il descende de la croix et nous croirons en lui! -');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 43, 'Il a mis sa confiance en Dieu! que Dieu maintenant le delivre ! s\'il veut de lui! En effet, il a dit : Je suis fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 44, 'De meme, les brigands que l\'on avait crucifies avec lui l\'insultaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 45, 'Depuis la sixieme jusqu\'a la neuvieme heure, des tenebres se firent sur tout le pays.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 46, 'Vers la neuvieme heure Jesus jeta un cri et dit d\'une voix forte : Helei! Helei! Lema sabachthanei? C\'est-a-dire : Mon Dieu! Mon Dieu! pourquoi m\'as-tu abandonne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 47, 'Quelques-uns de ceux qui etaient la l\'entendant : Il appelle Elie, celui-la ! dirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 48, 'L\'un d\'entre eux courut aussitot prendre une eponge, la trempa dans du vinaigre, et l\'attachant a un roseau, il lui donna a boire. Mais les autres disaient :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 49, 'Laisse donc! voyons si Elie viendra le delivrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 50, 'Jesus, jetant de nouveau un grand cri, rendit l\'esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 51, 'Et voila que le rideau du Temples se dechira en deux depuis le haut jusqu\'en bas ; la terre trembla ; les rochers se fendirent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 52, 'les tombeaux s\'ouvrirent ; plusieurs des saints qui etaient morts ressusciterent en leurs corps ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 53, 'et sortant de leurs tombeaux apres la resurrection de Jesus, ils entrerent dans la ville sainte et apparurent a plusieurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 54, 'Voyant le tremblement de terre et tout ce qui se passait, le centurion et ceux qui, avec lui, gardaient Jesus, furent frappes d\'epouvante et dirent : Cet homme-la etait veritablement fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 55, 'Or il y avait la plusieurs femmes qui regardaient de loin c\'etaient celles qui, depuis la Galilee, avaient suivi Jesus afin de le servir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 56, 'Parmi elles, Marie-Magdeleine, Marie, mere de Jacques et de Joseph, ainsi que la mere des fils de Zebedee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 57, 'Sur le soir arriva un homme riche, originaire d\'Arimathee, nomme Joseph. Il etait, lui aussi, un disciple de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 58, 'Il alla trouver Pilate et demanda le corps de Jesus. Pilate ordonna de le lui remettre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 59, 'Et Joseph, prenant le corps, l\'ensevelit dans un linceul blanc');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 60, 'et le deposa dans un sepulcre neuf, qu\'il avait taille dans le roc, et qui lui appartenait. Il roula une pierre enorme contre la porte du tombeau et s\'en alla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 61, 'Or Marie-Magdeleine et l\'autre Marie etaient la, assises en face du sepulcre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 62, 'Le lendemain (qui etait le jour apres la Preparation) les chefs des pretres et les Pharisiens se rendirent ensemble chez Pilate');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 63, 'et lui dirent : Seigneur, nous nous sommes souvenus que, de son vivant, cet imposteur disait : Apres trois jours, je ressusciterai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 64, 'Ordonne donc que la tombe soit soigneusement surveillee jusqu\'au troisieme jour, de peur que ses disciples ne viennent voler le corps et ne disent ensuite au peuple : Il est ressuscite des morts. Imposture derniere qui serait pire que la premiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 65, 'Pilate leur repondit : Vous avez des gardes ; allez, surveillez comme vous l\'entendez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 27, 66, 'Alors ils allerent s\'assurer du sepulcre, en scellant la pierre en presence de la garde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 1, 'Le sabbat passe, a l\'aube du premier jour de la semaine, Marie-Magdeleine et l\'autre Marie vinrent pour regarder le sepulcre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 2, 'Tout a coup il se fit un violent tremblement de terre : c\'etait un ange du Seigneur qui descendait du ciel ; il s\'approcha de la pierre, l\'eloigna en la roulant et s\'assit dessus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 3, 'Son aspect etait foudroyant ; ses vetements blancs comme la neige.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 4, 'Tremblants d\'epouvante devant lui, les gardes etaient comme morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 5, 'Mais l\'ange s\'adressant aux femmes leur dit : Pour vous, soyez sans crainte ; vous cherchez, je le sais, Jesus, le crucifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 6, 'Il n\'est pas ici, car, ainsi qu\'il l\'avait dit, il est ressuscite. Venez, regardez la place ou il etait couche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 7, 'et allez vite dire a ses disciples qu\'il est ressuscite des morts ; il vous precede en Galilee ; la vous le verrez ; c\'est moi qui vous le dis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 8, 'Elles sortirent immediatement du tombeau, tremblantes et, remplies de joie, et coururent porter la nouvelle a ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 9, 'Et voila que Jesus s\'avanca a leur rencontre et leur dit : Salut! Elles s\'approcherent, embrasserent ses pieds, et l\'adorerent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 10, 'Alors Jesus leur dit : Soyez sans crainte ; allez dire de ma part a mes freres de partir pour la Galilee, la ils me verront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 11, 'Quand elles se furent eloignees, quelques-uns des gardes vinrent a la ville et apprirent aux chefs des pretres tout ce qui etait arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 12, 'Ceux-ci se reunirent avec les Anciens, et apres en avoir confere, ils donnerent une grosse somme aux soldats avec cet ordre :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 13, 'Vous direz que ses disciples sont venus pendant la nuit et qu\'ils l\'ont vole pendant que vous dormiez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 14, 'Si le procurateur devait entendre parler de cette affaire, nous nous chargeons de le convaincre et nous vous mettrons a couvert.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 15, 'Les soldats prirent l\'argent et agirent d\'apres ces instructions. Et le bruit qu\'ils repandirent parmi les Juifs dure encore aujourd\'hui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 16, 'Cependant les onze disciples se rendirent en Galilee, sur la montagne que Jesus leur avait designee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 17, 'Le voyant, ils l\'adorerent. Mais quelques-uns douterent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 18, 'Jesus alors s\'approcha d\'eux et leur parla ; il leur dit : A moi a ete donnee toute puissance au ciel et sur terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 19, 'allez faire de toutes les nations mes disciples, les baptisant au nom du Pere, et du Fils,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (40, 28, 20, 'et du saint Esprit et leur enseignant a garder tout ce que je vous ai commande. Voici, je suis avec vous tous les jours jusqu\'a la fin du monde. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 1, 'Commencement de lEvangile de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 2, 'Ainsi quil est ecrit dans le prophete Esaie: Voici j\'envoie mon messager pour te preceder Et te preparer le chemin ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 3, 'Une voix crie dans le desert : Preparez la voie du Seigneur, Aplanissez ses sentiers,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 4, 'Jean parut dans le desert, donnant et prechant un bapteme de repentance pour la remission des peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 5, 'Tout le pays de Judee et tous les habitants de Jerusalem accouraient a lui et ils etaient baptises par lui dans le Jourdain, en confessant leurs peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 6, 'Jean avait un vetement de poils de chameau et une ceinture de cuir autour des reins, et il se nourrissait de sauterelles et de miel sauvage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 7, 'Il disait dans sa predication : Il va venir, apres moi, celui qui est plus puissant que moi ! Je ne suis pas meme digne, en me baissant, de delier la courroie de ses sandales.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 8, 'Moi, je vous ai baptises d\'eau ; mais lui vous baptisera avec l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 9, 'Or, ce fut en ces jours memes que Jesus arriva de Nazareth en Galilee et il fut baptise par Jean dans le Jourdain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 10, 'Soudain, comme il sortait de l\'eau, il vit les cieux ouverts et l\'Esprit en forme de colombe descendre sur lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 11, 'et des cieux vint une voix : Tu es mon Fils bien-aime, en toi je me complais..');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 12, 'Et l\'Esprit aussitot entraina Jesus au desert ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 13, 'et il fut quarante jours dans le desert, tente par Satan, et il fut parmi les betes, et les anges le servaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 14, 'Apres que Jean eut ete livre, Jesus se rendit en Galilee et y precha l\'Evangile de Dieu :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 15, 'Les temps sont accomplis ; le Royaume de Dieu est proche ; repentez-vous et croyez a l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 16, 'Comme il longeait la mer de Galilee, il vit Simon et son frere Andre jetant leurs filets dans la mer, car ils etaient pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 17, 'Jesus leur dit : Venez apres moi et je vous ferai devenir des pecheurs d\'hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 18, 'Sur-le-champ ils abandonnerent les filets et le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 19, 'S\'etant avance un peu plus loin, il vit, dans leurs barques, Jacques, fils de Zebedee, et Jean, son frere, qui raccommodaient leurs filets.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 20, 'Il les appela aussitot ; et ceux-ci, laissant leur pere Zebedee dans la barque avec les hommes de service, partirent a sa suite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 21, 'Ils arriverent a Capharnaum. Il commenca immediatement a entrer les jours de sabbat dans la synagogue et a y enseigner ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 22, 'et ils etaient extremement frappes de son enseignement ; car il le donnait comme ayant autorite, et non comme les Scribes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 23, 'Il y avait a ce moment meme dans leur synagogue un homme possede d\'un esprit impur ; il s\'ecria :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 24, 'Qu\'y a-t-il entre nous et toi, Jesus de Nazareth? Tu es venu pour nous perdre ; je sais qui tu es : le Saint de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 25, 'Jesus alors le menaca : Tais-toi! sors de cet homme !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 26, 'Agitant violemment le possede, l\'Esprit impur sortit de lui en poussant un grand cri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 27, 'Tous les assistants, terrifies, s\'interrogeaient les uns les autres : Qu\'est-ce que ceci ? Voila un enseignement nouveau! il commande avec autorite meme aux Esprits impurs, et ils lui obeissent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 28, 'Sa renommee se repandit promptement et partout aux environs, dans la Galilee entiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 29, 'En sortant de la synagogue, ils se rendirent tout d\'abord, accompagnes de Jacques et de Jean, dans la maison de Simon et dAndre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 30, 'La belle-mere de Simon etait couchee ; elle avait la fievre. On s\'empressa de le dire a Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 31, 'Alors, s\'approchant d\'elle, il la prit par la main et la fit lever. La fievre disparut et elle se mit a les servir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 32, 'Le soir venu, apres le coucher du soleil, on lui apporta tous les malades et les demoniaques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 33, 'La ville entiere se pressait a la porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 34, 'Il guerit plusieurs malades atteints de maux divers ; il chassa aussi beaucoup de demons et il defendait aux demons de parler, parce qu\'ils savaient qui il etait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 35, 'S\'etant leve de grand matin, avant le jour, il alla dans un lieu solitaire et la il priait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 36, 'Simon et ceux qui etaient avec lui se mirent a sa recherche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 37, 'Quand ils l\'eurent trouve, ils lui dirent : Tout le monde te cherche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 38, 'Il leur repondit : Allons dans les villages voisins, afin que j\'y preche aussi, car c\'est pour cela que je suis sorti.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 39, 'Il alla donc precher dans leurs synagogues et dans toute la Galilee, en chassant les demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 40, 'Un lepreux vint a lui et, se jetant a ses genoux, lui adressa cette priere : Si tu le veux, tu peux me guerir !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 41, 'Il en eut compassion ; il etendit la main et le toucha en disant : Je le veux, sois gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 42, 'La lepre disparut a l\'instant ; cet homme fut gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 43, 'Jesus le congedia immediatement, en lui donnant cet avertissement severe :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 44, 'Garde-toi de parler de ceci a personne ; mais va te montrer au pretre et offre pour ta guerison ce que Moise a prescrit ; que ce leur soit un temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 1, 45, 'Mais, a peine parti, le lepreux se mit a repandre la nouvelle et a la proclamer ; de sorte que Jesus ne pouvait plus meme paraitre publiquement dans une ville ; mais il se tenait dehors dans des endroits deserts, et on venait a lui de toutes parts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 1, 'Quelques jours apres, il retourna a Capharnaum. On apprit qu\'il etait dans telle maison,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 2, 'et on y accourut en si grand nombre qu\'il n\'y avait plus de place meme devant la porte. Il leur annoncait la parole,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 3, 'lorsque survinrent des gens lui amenant un paralytique, porte par quatre hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 4, 'Comme il leur etait impossible de parvenir jusqu\'a lui a cause de la foule, ils demolirent le toit au-dessus de la place ou il se trouvait ; et, l\'ouverture faite, ils descendirent le grabat sur lequel etait couche le paralytique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 5, 'Jesus, voyant leur foi, dit a ce paralytique: Mon enfant, tes peches sont pardonnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 6, 'Il y avait la quelques Scribes qui se tenaient assis et ces pensees leur montaient au coeur :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 7, 'Comment cet homme peut-il parler ainsi? Il blaspheme! Qui peut pardonner des peches que Dieu seul ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 8, 'Jesus connut aussitot, par son esprit, qu\'ils faisaient interieurement ces reflexions, et il leur dit : Pourquoi de telles pensees dans vos coeurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 9, 'Lequel est le plus facile de dire, a ce paralytique : Tes peches sont pardonnes ; ou de lui dire : Leve toi, emporte ton grabat et marche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 10, 'Or, afin que vous sachiez que la le Fils de l\'homme a sur la terre le pouvoir de pardonner des peches... s\'adressant au paralytique : A toi, je dis :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 11, 'Leve-toi! emporte ton grabat et retourne dans ta maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 12, 'Et l\'homme se leva, prit immediatement son grabat et sortit devant tout le monde ; tous en etaient stupefaits et glorifiaient Dieu : Jamais, disaient-ils, nous n\'avons rien vu de pareil!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 13, 'De nouveau, il se dirigea vers la mer. Toutes les multitudes venaient a lui, et il les enseignait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 14, 'En passant, il vit Levi, fils d\'Alphee, assis au bureau du peage. Il lui dit : Suis-moi ! et il se leva et le suivit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 15, 'Jesus etait a table dans ta maison de Levi, et il arriva que beaucoup de publicains et de pecheurs s\'y trouverent avec lui et avec ses disciples ; car ils etaient nombreux et ils l\'avaient suivi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 16, 'ainsi que les Scribes d\'entre les Pharisiens. Ceux-ci, le voyant manger avec les publicains et les pecheurs, dirent a ses disciples : Pourquoi mange-t-il avec des publicains et des pecheurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 17, 'Ce qu\'ayant entendu, Jesus leur dit : Ce ne sont pas ceux qui se portent bien qui ont besoin de medecin, ce sont les malades. Je ne suis pas venu appeler des justes, mais des pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 18, 'Les disciples de Jean et les Pharisiens jeunaient. Ils vinrent lui demander : Pourquoi les disciples de Jean et ceux des Pharisiens pratiquent-ils le jeune, tandis que tes disciples ne le pratiquent pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 19, 'Jesus leur repondit : Les amis de l\'epoux peuvent-ils jeuner pendant que l\'epoux est avec eux. Tout le temps que l\'epoux est avec eux, ils ne peuvent jeuner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 20, 'Des jours viendront ou l\'epoux leur sera enleve ; ils jeuneront dans ces jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 21, 'Personne ne coud un morceau d\'etoffe neuve a un vieux vetement : autrement le morceau neuf emporte une partie du vieux vetement qu\'il recouvre et fait une plus grande dechirure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 22, 'Personne, non plus, ne verse du vin nouveau dans de vieilles outres : autrement le vin fera eclater les outres, et il sera perdu, les outres aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 23, 'Il arriva  un jour de sabbat  que Jesus passa par les bles. Ses disciples se mirent, chemin faisant, a cueillir des epis :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 24, 'Vois donc, lui dirent les Pharisiens, pourquoi font-ils ce qu\'aux jours de sabbat il n\'est pas permis de faire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 25, 'Il leur repondit : N\'avez-vous jamais lu ce que fit David quand il fut contraint par la necessite et qu\'il eut faim, et ce que firent ses compagnons?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 26, 'Comment, sous le grand-pretre Abiathar, il entra dans la maison de Dieu et mangea les pains de proposition, qu\'il n\'est permis qu\'aux pretres de manger, et en donna a ceux qui etaient avec lui?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 27, 'Et il ajouta : Le sabbat a ete fait pour l\'homme et non pas l\'homme pour le sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 2, 28, 'C\'est pourquoi le Fils de l\'homme est maitre aussi du sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 1, 'Une autre fois, il entra dans une synagogue, ou se trouvait un homme qui avait la main paralysee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 2, 'Ils l\'observerent pour voir si, un jour de sabbat, il le guerirait ; ils voulaient un pretexte a accusation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 3, 'Jesus alors s\'adressa a l\'homme dont la main etait paralysee : Leve-toi au milieu de nous tous !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 4, 'Puis il leur dit : Aux jours de sabbat, est-il permis de faire le bien ou de faire le mal ? de sauver une vie ou de la perdre? Ils garderent le silence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 5, 'Alors, jetant sur eux un regard de colere, afflige d\'un tel aveuglement de coeur, il dit a l\'homme : Etends la main! Il l\'etendit et sa main fut guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 6, 'Quant aux Pharisiens, ils sortirent et allerent immediatement se concerter avec les Herodiens sur les moyens de le perdre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 7, 'Jesus se retira, avec ses disciples, du cote de la mer ; et une foule considerable, venue de Galilee et de Judee, l\'y suivit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 8, 'C\'etait une multitude immense, arrivant de Jerusalem, de l\'Idumee, des terres situees au dela du Jourdain, des environs de Tyr et de Sidon, qui, a la nouvelle de ce qu\'il faisait, venait a lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 9, 'Il dit alors a ses disciples de lui procurer une barque a cause de la foule qui risquait de l\'etouffer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 10, 'Il en guerissait, en effet, un grand nombre, et alors tous ceux qui avaient quelque mal se precipitaient sur lui, afin de le toucher.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 11, 'Quant aux Esprits impurs, lorsqu\'ils le voyaient, ils tombaient devant lui et s\'ecriaient : Tu es le Fils de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 12, 'et, par de severes menaces, il leur interdisait de le faire connaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 13, 'Ensuite il gravit la montagne ; il appela ceux qu\'il lui plut de designer lui-meme et ils vinrent a lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 14, 'Alors il en choisit douze, auxquels il donna le nom d\'apotres, pour etre avec lui et pour recevoir mission de precher,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 15, 'avec le pouvoir de chasser les demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 16, 'Voici les douze qu\'il choisit : Simon, a qui il donna le nom de Pierre ; ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 17, 'Jacques, fils de Zebedee, et Jean, frere de Jacques, auxquels il donna le nom de Boanerges, c\'est-a-dire fils du tonnerre ; ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 18, 'Andre ;  Philippe ;  Barthelemy ;  Matthieu ;  Thomas ;  Jacques, fils d\'Alphee ;  Thaddee ;  Simon le Cananite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 19, 'Judas l\'Iskariote, celui-la meme qui le trahit. Ensuite il entra dans une maison');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 20, 'et la foule y accourut encore a tel point qu\'ils ne pouvaient pas meme manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 21, 'Ceux de sa parente, l\'ayant appris, vinrent pour l\'emmener avec eux. On disait, en effet, qu\'il avait perdu l\'esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 22, 'Quant aux Scribes, ceux qui etaient descendus de Jerusalem, ils disaient : Il a en lui Beelzeboul, et : C\'est par le prince des demons qu\'il chasse les demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 23, 'Alors il les appela a lui et leur dit en paraboles : Comment Satan peut-il chasser Satan ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 24, 'Si un royaume est divise contre lui, meme, ce royaume ne peut subsister.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 25, 'Si une maison est divisee contre elle-meme, cette maison ne pourra subsister ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 26, 'de meme si Satan se livre bataille a lui-meme et se divise, il ne peut subsister, il touche a sa fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 27, 'Nul ne peut entrer dans la maison du guerrier et piller ce qu\'il possede, s\'il n\'a tout d\'abord charge de liens le guerrier ; ce n\'est qu\'alors qu\'il pourra piller sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 28, 'En verite, je vous le dis, toutes sortes de peches seront pardonnes aux fils des hommes, ainsi que les blasphemes aux blasphemateurs ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 29, 'mais celui qui blaspheme contre l\'Esprit, qui est saint, ne recoit point de pardon, n\'en recevra jamais, mais il est coupable d\'un eternel peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 30, '(C\'etait parce qu\'ils disaient : Il a en lui un Esprit impur).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 31, 'Ce fut alors qu\'arriverent sa mere et ses freres, qui, se tenant dehors, le firent demander.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 32, 'Quantite de gens etaient assis autour de lui, lorsqu\'on lui dit : Voila dehors ta mere et tes freres qui te cherchent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 33, 'Il repondit ainsi : Qui est ma mere et qui sont mes freres?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 34, 'Puis il regarda ceux qui etaient assis en cercle autour de lui et dit : Voici ma mere et mes freres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 3, 35, 'faire la volonte de Dieu, c\'est etre mon frere, c\'est etre ma soeur, c\'est etre ma mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 1, 'De nouveau il se mit a enseigner au bord de la mer. Il se rassembla autour de lui une multitude si prodigieuse quil monta dans une barque ou il s\'assit, et il se tint sur la mer, tandis que la foule restait a terre au bord de l\'eau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 2, 'Il leur enseignait beaucoup de choses en paraboles et il leur disait dans son enseignement :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 3, 'Ecoutez! Voila que le semeur est sorti pour semer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 4, 'Il jette sa semence, et il est arrive qu\'un grain est tombe le long du chemin et les oiseaux sont venus et l\'ont mange.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 5, 'Un autre est tombe sur un sol pierreux, ou il n\'y avait pas beaucoup de terre, et il a immediatement pousse, parce que la terre etait sans profondeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 6, 'Mais le soleil, en se levant, a brule la plante, qui, n\'ayant point de racines, s\'est dessechee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 7, 'Un autre grain est tombe parmi les epines ; et les epines ont grandi et l\'ont etouffe, de sorte qu\'il n\'a point donne de fruit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 8, 'D\'autres grains sont tombes dans la bonne terre, et ceux-la, montant et croissant, ont donne du fruit, tel grain en a produit trente, tel autre soixante, tel autre cent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 9, 'Et il ajouta : Que celui qui a des oreilles pour entendre, entende!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 10, 'Lorsqu\'il fut seul, ceux qui l\'entouraient l\'interrogerent avec les douze sur les paraboles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 11, 'Il leur dit : A vous il est donne de penetrer le mystere du Royaume de Dieu ; mais a ceux-la, ceux du dehors, tout arrive sous forme de paraboles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 12, 'afin que tout en regardant parfaitement, ils ne voient point, tout en entendant parfaitement, ils ne comprennent point, et cela pour qu\'ils ne se convertissent pas et qu\'il ne leur soit point pardonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 13, 'Et il ajouta : Ne comprenez-vous pas cette parabole ? Comment donc comprendrez-vous toutes les paraboles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 14, 'Le semeur seme la parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 15, 'Les uns sont le long du chemin ou la parole est semee, et quand ils l\'ont entendue, Satan vient aussitot et enleve la parole semee en eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 16, 'Les autres sont semblables ; il est des grains qui sont semes sur un sol pierreux : ceux-la quand ils entendent la parole, l\'accueillent aussitot avec joie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 17, 'mais ils n\'ont pas de racine en eux memes ; ils ne sont que pour un temps ; une affliction ou une persecution survenant a cause de la parole, ils y trouvent aussitot une occasion de chute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 18, 'D\'autres sont semes parmi les epines : ceux-la ont ecoute, la parole,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 19, 'mais les sollicitudes du siecle present, la seduction de la richesse et les passions diverses font invasion, etouffent la parole et la rendent sterile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 20, 'Enfin il y a ceux qui sont semes sur la bonne terre : ils ecoutent la parole, ils la recoivent et donnent des fruits, l\'un trente, l\'autre soixante, l\'autre cent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 21, 'Il leur dit aussi : Est-ce qu\'on apporte la lampe pour la mettre sous le boisseau ou sous le lit? N\'est-ce pas pour la mettre sur le pied-de-lampe?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 22, 'Rien n\'est cache que ce qui doit etre manifeste ; rien n\'est secret que pour etre mis au jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 23, 'Si quelqu\'un a des oreilles pour entendre, qu\'il entende !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 24, 'Il leur dit aussi : Faites attention a ce que vous entendez ; la mesure que vous faites aux autres sera votre mesure, et il y sera ajoute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 25, 'Car a celui qui a, il sera donne, et a celui qui n\'a pas, meme ce qu\'il a lui sera ote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 26, 'Il dit aussi : Il se passe pour le Royaume de Dieu ce qui se passe lorsqu\'un homme jette la semence sur la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 27, 'Qu\'il dorme ou qu\'il veille, nuit et jour, la graine germe, la plante grandit, sans qu\'il sache comment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 28, 'D\'elle-meme la terre donne son fruit : c\'est d\'abord une herbe ; c\'est ensuite un epi ; c\'est enfin le ble remplissant cet epi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 29, 'Et quand la terre a ainsi donne son fruit, il y met aussitot la faucille, car est arrivee la moisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 30, 'Il dit aussi : A quoi assimilerons-nous le Royaume de Dieu? Ou par quelle parabole le representerons-nous ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 31, 'Il est comme un grain de seneve, la plus petite de toutes les semences lorsqu\'on la seme sur la terre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 32, 'mais qui monte lorsquil est seme et qui devient plus grand que toutes les plantes potageres et pousse de si vastes branches que, dans leur ombrage, les oiseaux du ciel peuvent faire leurs nids.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 33, 'C\'est par un grand nombre de paraboles de ce genre qu\'il leur annoncait ta parole, dans la mesure ou ils pouvaient comprendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 34, 'Il ne leur parlait qu\'en paraboles ; puis, en particulier, il expliquait tout a ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 35, 'Ce meme jour, le soir venu, il leur dit : Passons a lautre rive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 36, 'Ils renvoient la foule, et l\'emmenent dans la barque ou il se trouvait ; d\'autres barques les accompagnaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 37, 'Un grand tourbillon de vent s\'eleva ; les vagues se jetaient dans la barque avec une telle force que deja elle se remplissait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 38, 'Et lui, a la poupe, la tete sur le coussin, il dormait. Ils le reveillerent et lui dirent : Maitre, est-ce que tu ne te soucies pas que nous perissions?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 39, 'Alors il se leva, il fit des menaces au vent et il dit a la mer : Silence ! apaise-toi ! Et le vent tomba, il se fit un grand calme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 40, 'Puis il leur dit : Pourquoi vous effrayer? N\'avez vous point encore de foi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 4, 41, 'Et, frappes de terreur, ils se disaient l\'un a l\'autre : Qui donc est cet homme pour que le vent et la mer lui obeissent ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 1, 'Ils allerent de l\'autre cote de la mer, dans le pays des Geraseniens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 2, 'Au moment meme ou il debarquait, vint a sa rencontre, sortant des sepulcres, un homme possede d\'un esprit impur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 3, 'Il faisait sa demeure dans les tombeaux, et personne ne pouvait plus le lier meme avec une chaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 4, 'Plusieurs fois on lavait enchaine ; on lui avait mis les fers aux pieds ; mais il avait rompu les chaines et brise les fers, et nul n\'avait la force de s\'en rendre maitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 5, 'Il ne cessait nuit et jour d\'errer parmi les sepulcres et sur les montagnes, en jetant des cris et en se meurtrissant contre les rochers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 6, 'De loin, il apercut Jesus ; il courut se prosterner devant lui');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 7, 'et lui dit, en criant de toutes ses forces : Qu\'y a-t-il entre moi et toit, Jesus, Fils du Dieu Tres-Haut? Au nom de Dieu, je t\'en conjure, ne me torture point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 8, '(En effet, Jesus lui disait : Esprit impur, sors de cet homme.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 9, 'Puis il l\'interrogea : Quel est ton nom? Mon nom, repondit-il, est Legion, car nous sommes nombreux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 10, 'Et cette Legion le suppliait de ne pas l\'expulser du pays.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 11, 'Or il y avait la, paissant sur la montagne, un grand troupeau de pourceaux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 12, 'et les esprits impurs lui adressaient cette demande : Envoie-nous dans ces pourceaux, afin que nous entrions en eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 13, 'Jesus le leur permit. Alors ils partirent et entrerent dans les pourceaux, et, d\'une course impetueuse, le troupeau se precipita dans la mer ; il y en avait environ deux mille ; ils se noyerent dans les flots.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 14, 'Les gardeurs prirent la fuite et allerent repandre la nouvelle dans la ville et dans les campagnes. Les habitants vinrent voir ce qui s\'etait passe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 15, 'Ils s\'approcherent de Jesus et apercurent assis, avec ses vetements, et plein de bon sens, le demoniaque, celui, qui avait eu la Legion ; cela leur fit peur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 16, 'Les temoins oculaires leur raconterent ce qui etait arrive au demoniaque et aux pourceaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 17, 'Les habitants prierent alors Jesus de quitter leur pays.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 18, 'Comme il montait dans la barque, l\'homme qui avait ete delivre des demons lui demanda la permission de rester avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 19, 'Jesus n\'y consentit pas, mais lui dit : Va dans ta maison, vers les tiens ; apprends-leur les grandes choses que le Seigneur t\'a faites et comme il a eu pitie de toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 20, 'Cet homme partit et se mit a publier dans la Decapole les grandes choses que Jesus lui avait faites, et tous etaient dans l\'admiration.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 21, 'Lorsque Jesus, ayant repasse l\'eau dans la barque, fut de retour de l\'autre cote, une foule considerable s\'assembla autour de lui ; et comme il etait au bord de la mer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 22, 'arriva un chef de synagogue, du nom de Jairus, qui, en l\'apercevant, alla se jeter a ses pieds');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 23, 'et lui adressa d\'instantes prieres : Ma petite fille, lui dit-il, est a toute extremite ; viens, impose-lui les mains, afin qu\'elle soit guerie et qu\'elle vive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 24, 'Jesus partit avec lui, suivi d\'une multitude nombreuse qui le pressait de tous cotes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 25, 'Or il y avait la une femme, malade d\'une perte de sang depuis douze annees ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 26, 'elle avait beaucoup souffert entre les mains de plusieurs medecins ; elle y avait depense tout son avoir et cela n\'avait servi a rien ; au contraire, son etat avait plutot empire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 27, 'Elle avait entendu parler de Jesus, et venant dans la foule, par derriere, elle toucha son vetement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 28, 'car elle disait : Si seulement je touche ses vetements, je serai guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 29, 'Immediatement tarit la source du sang qu\'elle perdait : elle sentit en son corps qu\'elle etait guerie de son infirmite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 30, 'et, en meme temps, Jesus, ayant senti en lui-meme la vertu qui sortait de lui, se retourna vers la foule et dit : Qui a touche mes vetements?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 31, 'Ses disciples lui repondirent : Tu vois la foule te presser de tous cotes et tu demandes : Qui m\'a touche?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 32, 'Mais il regardait tout autour de lui pour voir celle qui avait fait cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 33, 'Alors la femme, toute craintive et tremblante, ayant conscience de ce qui lui etait arrive, vint se jeter a ses pieds et lui dit toute la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 34, 'Et lui, il lui dit : Ma fille, ta foi t\'a guerie ; va en paix et sois delivree de ton infirmite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 35, 'Il parlait encore lorsque survinrent des gens du chef de la synagogue: Ta fille est morte, lui dirent-ils, pourquoi fatiguer davantage le maitre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 36, 'Mais Jesus, sans tenir compte de cette parole, dit au chef de synagogue : Ne crains point, crois seulement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 37, 'Puis il ne permit a personne de l\'accompagner, si ce n\'est a Pierre, a Jacques et a Jean, frere de Jacques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 38, 'Ils arrivent a la maison du chef de synagogue. La, Jesus a le spectacle de tout un tumulte de gens pleurant tout haut et poussant des cris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 39, 'Il entre et leur demande : Pourquoi ce tumulte et ces pleurs? L\'enfant n\'est pas morte, mais elle dort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 40, 'Et on se moque de lui. Mais Jesus, faisant sortir tout le monde, ne prend avec lui que le pere, la mere et les disciples qui l\'accompagnaient ; puis il entre la ou etait l\'enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 41, 'Il lui saisit la main en disant : Talitha Koum, ce qui signifie : Jeune fille, je te le dis, reveille-toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 42, 'La jeune fille se leva soudain et se mit a marcher ; en effet, elle avait douze ans. Ils furent sur-le-champ frappes d\'une profonde stupeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 5, 43, 'Il leur recommanda, expressement de n\'en parler a personne ; puis il dit de donner a manger a la jeune fille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 1, 'Jesus partit de la et retourna dans son pays ; ses disciples le, suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 2, 'Le sabbat etant arrive, il se mit a enseigner dans la synagogue. Et beaucoup parmi ses auditeurs etaient extremement surpris et disaient : D\'ou lui viennent ces choses? Qu\'est-ce donc que cette sagesse qui lui est donnee? et que ces grands miracles accomplis par ses mains ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 3, 'N\'est-ce pas la le charpentier, le fils de Marie, fiere de Jacques, de Joses, de Judas, de Simon? Et ses soeurs ne demeurent-elles pas ici, avec nous? Pour eux il etait une occasion de chute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 4, 'Alors Jesus leur dit : Un prophete n\'est sans honneur que dans sa patrie, dans sa parente, dans sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 5, 'Et il ne put faire parmi eux aucun miracle, excepte quelques guerisons de malades par l\'imposition des mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 6, 'Il s\'etonnait de leur incredulite et parcourait, en enseignant, les villages d\'alentour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 7, 'Ayant reuni les douze, il commenca a les envoyer en mission, deux a deux, leur donnant puissance sur les Esprits impurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 8, 'Il leur ordonna de ne rien emporter pour la route ; un baton seulement ; ni pain, ni sac, ni monnaie quelconque dans la ceinture ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 9, 'pour toutes chaussures, leurs sandales, et de ne pas prendre de second vetement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 10, 'Et il leur disait : En quelque maison que vous entriez, demeurez-y jusqu\'a ce que vous partiez de l\'endroit ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 11, 'et si quelque part on ne vous recoit ni ne vous ecoute, sortez de la, secouant, en temoignage contre eux, la poussiere attachee a vos pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 12, 'Ils partirent donc, prechant qu\'on devait se repentira ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 13, 'et ils chassaient beaucoup de demons et, faisant des onctions d\'huile a beaucoup de malades, ils les guerissaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 14, 'Le roi Herode l\'apprit ; (en effet, le nom de Jesus etait deja celebre) et il dit : Jean-Baptiste est ressuscite d\'entre les morts! de la ces puissances miraculeuses qui agissent en cet homme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 15, 'Les uns disaient : C\'est Elie! d\'autres : C\'est un prophete semblable a l\'un de ceux d\'autrefois!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 16, 'Mais Herode, qui entendait tout cela, disait : C\'est celui que j\'ai decapite! c\'est Jean! c\'est lui qui est ressuscite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 17, 'En effet, cet Herode avait fait arreter Jean, l\'avait fait garrotter et emprisonner : et cela a cause d\'Herodiade, la femme de son frere Philippe, que lui, Herode, avait epousee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 18, 'Car Jean lui avait dit : Il ne t\'est pas permis d\'avoir la femme de ton frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 19, 'De sorte qu\'Herodiade etait acharnee a sa perte ; elle voulait sa mort ; cependant elle ne pouvait l\'obtenir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 20, 'parce qu\'Herode avait du respect pour Jean qu\'il savait etre un juste et un saint ; il veillait sur lui ; sur plusieurs points sa parole l\'inquietait, et volontiers il l\'ecoutait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 21, 'Mais Herodiade rencontra un jour opportun ; ce fut lorsque Herode, a l\'anniversaire de sa naissance, donna un banquet a ses dignitaires, a ses officiers et aux principaux personnages de la Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 22, 'Dans la salle entra la fille meme d\'Herodiade, et elle, dansa et plut a Herode et a ses convives. Le roi dit alors a la jeune fille : Demande-moi ce que tu voudras et je te le donnerai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 23, 'Il lui en fit le serment : Tout ce que tu demanderas, je te le donnerai, fut-ce la moitie de mon royaume!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 24, 'La jeune fille sortit et dit a sa mere : Que demanderai-je? La mere repondit : La tete de Jean-Baptiste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 25, 'Rentrant en toute hate aupres du roi, la fille, lui fit ainsi sa demande : Je veux qu\'a l\'instant meme tu me donnes sur un plat la tete de Jean-Baptiste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 26, 'Le roi fut accable de tristesse ; cependant, a cause de son serment, a cause aussi des convives, il ne voulut pas la refuser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 27, 'Et immediatement il fit appeler un executeur et lui donna ordre de rapporter la tete de Jean.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 28, 'Cet homme alla donc decapiter Jean dans sa prison, et, rapportant la tete sur un plat, il la donna a la jeune fille, et la jeune fille la donna a sa mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 29, 'Ce qu\'ayant appris, les disciples de Jean vinrent prendre son corps et lensevelirent dans un tombeau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 30, 'De retour aupres de Jesus, les apotres lui rendirent compte de tout : de ce qu\'ils avaient fait et de ce qu\'ils avaient enseigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 31, 'Il leur dit alors : Venez a l\'ecart, vous aussi, dans quelque endroit solitaire et reposez-vous un peu. (En effet, il y avait une telle foule d\'allants et de venants qu\'ils n\'avaient pas meme le temps de manger.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 32, 'Ils partirent donc dans la barque pour se retirer dans quelque endroit solitaire, a l\'ecart.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 33, 'Mais plusieurs les virent s\'eloigner et les reconnurent, et de toutes les villes ils accoururent a pied la ou ils allaient et y arriverent avant eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 34, 'En debarquant, il vit une foule immense ; il en eut compassion, parce qu\'ils etaient comme des brebis sans berger, et il se mit a leur donner de nombreux enseignements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 35, 'L\'heure s\'avancait beaucoup et ses disciples, s\'approchant, lui dirent : L\'endroit est desert et deja il se fait tard.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 36, 'Renvoie-les, afin qu\'ils aillent aux environs dans les maisons de la campagne et dans les villages acheter chacun quelque chose a manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 37, 'Mais Jesus leur repondit : Donnez-leur vous-memes a manger. Pour leur donner a manger, repliquerent-ils, nous faudra-t-il aller acheter des pains pour deux cents deniers !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 38, 'Jesus reprit : Combien de pain avez vous? Allez voir. Apres s\'en etre informes : Cinq, dirent-ils, et deux poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 39, 'Alors il leur commanda de les faire tous asseoir, en divers groupes, sur l\'herbe verte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 40, 'Ils se rangerent a terre par bandes de cent et de cinquante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 41, 'Puis Jesus, prenant les cinq pains et les deux poissons, leva les yeux au ciel, prononca la benediction, rompit les pains et les donna aux disciples pour qu\'ils les servissent a la foule ; il partagea aussi, entre tous, les deux poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 42, 'Tous mangerent, tous furent rassasies,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 43, 'et on emporta douze paniers pleins de restes de pain et de poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 44, 'Quant au nombre de ceux qui avaient mange, il etait de cinq mille hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 45, 'Aussitot apres, il pressa ses disciples de remonter dans la barque, de prendre les devants et de se rendre, sur la rive opposee, a Bethsaida, tandis que lui, il renverrait la foule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 46, 'Quand il l\'eut congediee, il s\'en alla sur la montagne, pour y prier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 47, 'Le soir vint au milieu de la mer etait la barque ; a terre, Jesus, demeure seul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 48, 'Les voyant qui se fatiguaient a ramer, car le vent leur etait contraire, il vint a eux, vers la quatrieme veille de la nuit, en marchant sur la mer, et il voulait les depasser ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 49, 'quand ils le virent, marchant sur la mer, ils crurent que c\'etait un fantome et jeterent des cris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 50, 'Tous, en effet, le voyaient et etaient bouleverses. Mais aussitot il leur parla ; il leur dit : Rassurez-vous ; c\'est moi ; soyez sans crainte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 51, 'Il monta avec eux dans la barque et le vent cessa. Leur stupeur allait grandissant ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 52, 'car ils n\'avaient pas meme compris le, miracle des pains, tellement leur coeur etait aveugle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 53, 'Ayant passe l\'eau, ils arriverent au pays de Gennesaret, et ils aborderent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 54, 'A leur sortie de la barque, Jesus fut immediatement reconnu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 55, 'Alors toute la contree fut en mouvement ; on se mit a lui apporter les malades sur leurs grabats, partout ou l\'on apprenait sa presence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 6, 56, 'Et partout ou il entrait, villages, villes ou bourgades, on deposait les malades sur les places publiques, et on le priait de les laisser seulement toucher la frange de son vetement ; et tous ceux qui la touchaient etaient gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 1, 'Les Pharisiens et certains Scribes qui arrivaient de Jerusalem, se reunirent pres de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 2, 'Ils avaient vu quelques-uns de ses disciples prendre leur repas avec des mains impures, c\'est-a-dire non purifiees par des ablutions. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 3, '(En effet, les Pharisiens et, en general, les Juifs ne mangent pas sans avoir pratique des ablutions sur les mains, y compris le poignet, conformement aux traditions des Anciens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 4, 'Lorsqu\'ils reviennent du marche, ils ne mangent pas avant de s\'etre plonges dans l\'eau. Ils ont egalement une foule d\'autres pratiques qu\'ils observent par tradition : des ablutions de coupes, de vases de terre et de vases de cuivre). ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 5, 'Les Pharisiens et les Scribes poserent donc une question a Jesus : Pourquoi tes disciples ne suivent-ils pas la tradition des Anciens et prennent-ils leur repas avec des mains impures?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 6, 'Il leur repondit : C\'est bien a vous, hypocrites !, que s\'applique la prophetie d\'Esaie : C\'est des levres, est-il ecrit, que ce peuple m\' honore ! Quant a son coeur, il est fort loin de moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 7, 'C\'est en vain qu\'ils me rendent un culte! Ils enseignent des doctrines qui ne sont que des ordonnances humaines');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 8, 'Vous laissez de cote le commandement de Dieu et vous observez la tradition des hommes! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 9, 'Puis il ajouta : Ah! que vous savez bien violer le commandement de Dieu pour observer votre tradition!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 10, 'Ainsi Moise a dit: Honore ton pere et ta mere. et: Qu\'il soit puni de mort, celui qui maudira son pere ou sa mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 11, 'Mais vous, voici ce que vous enseignez : Si un fils dit a son pere ou a sa mere : Je declare Korban, c\'est-a-dire offrande a Dieu, ce dont tu voudrais etre assiste par moi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 12, 'il a la permission de ne plus rien faire ni pour son pere ni pour sa mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 13, 'Et voila comment, par la tradition que vous enseignez, vous reduisez a neant la parole de Dieu! Et combien d\'autres choses pareilles vous faites!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 14, 'De nouveau, il appela a lui la multitude : Ecoutez-moi tous, leur dit-il, et comprenez!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 15, 'Il n\'est rien d\'exterieur a l\'homme qui, entrant en lui, puisse le rendre impur, c\'est, au contraire, ce qui sort de l\'homme qui le rend impur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 16, '(Si quelqu\'un a des oreilles pour entendre, qu\'il entende).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 17, 'Lorsque, rentre dans la maison, il fut loin de la foule, ses disciples l\'interrogerent sur cette parabole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 18, 'Il leur repondit : Eh quoi ! vous aussi, vous etes sans intelligence? Ne comprenez-vous pas que rien de ce qui est exterieur ne peut, en entrant dans l\'homme, le rendre impur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 19, 'puisque cela n\'entre pas dans son coeur, mais dans son ventre, puis s\'en va en quelque lieu secret, ce qui montre que tous les aliments sont egalement pure?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 20, 'Il dit aussi : Ce qui, sort de l\'homme, voila ce qui le rend impur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 21, 'car c\'est de l\'intime du coeur des hommes que sortent les pensees mauvaises : fornications, vols, homicides,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 22, 'adulteres, rapacites, mechancetes, tromperie, impudicite, envie, calomnie, orgueil, demence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 23, 'Toutes ces mauvaises choses sortent du dedans et rendent l\'homme impur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 24, 'Parti de la, Jesus se rendit vers les frontieres de Tyr et de Sidon. Il voulait, dans la maison ou il entra, rester inconnu ; mais il ne lui fut pas possible de demeurer cache,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 25, 'car aussitot qu\'elle apprit sa presence, une femme, dont la fille etait possedee d\'un Esprit impur, entra aupres de lui et se jeta a ses pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 26, 'Cette femme, syro-phenicienne de naissance, etait paienne. Elle lui demanda de chasser de sa fille le demon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 27, 'Il lui repondit : Laisse d\'abord se rassasier les enfants ; il n\'est pas bon de prendre le pain des enfants et de le jeter aux chiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 28, 'Elle repliqua en disant : Si, Seigneur, meme les chiens mangent, sous la table, quelques-unes des miettes des enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 29, 'Alors il lui dit : A cause de cette parole, va! le demon est sorti de ta fille!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 30, 'Elle retourna chez elle et trouva l\'enfant reposant sur son lit ; le demon etait chasse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 31, 'Il reprit sa route, partit des environs de Tyr et revint par Sidon vers la mer de Galilee en traversant le territoire de la Decapole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 32, 'On lui amena un sourd qui parlait difficilement et on le pria d\'etendre la main sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 33, 'Il le conduisit hors de la foule, a l\'ecart, lui mit les doigts dans les oreilles, et toucha sa langue avec de la salive ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 34, 'puis, levant les yeux au ciel, il poussa un soupir et lui dit : Ephphatah ; (ce mot signifie : ouvre-toi).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 35, 'Les oreilles de ce sourd s\'ouvrirent, et sa langue attachee se delia sur-le-champ : il parlait distinctement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 36, 'Il leur recommanda de n\'en rien dire a personne ; mais plus il le leur recommandait, plus ils le publiaient partout ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 7, 37, 'plus aussi etait profonde leur surprise : Ils disaient : Tout ce qu\'il a fait est parfait! les sourds, il les fait entendre! les muets, il les fait parler!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 1, 'Il arriva encore une fois, vers cette epoque, que la foule etant tres nombreuse et n\'ayant pas de quoi manger, Jesus appela les disciples et leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 2, 'J\'ai compassion de cette multitude ; voici deja trois jours qu\'ils ne me quittent pas et ils n\'ont pas de quoi manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 3, 'Si je les renvoie chez eux a jeun, ils tomberont en defaillance sur la route ; quelques-uns d\'entre eux sont venus de loin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 4, 'Ses disciples lui repondirent : Ou pourrait-on, au milieu de ce desert, trouver des pains pour les rassasier? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 5, 'Combien de pains avez-vous? leur demanda Jesus.  Sept, dirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 6, 'Il donna ordre a la foule de s\'asseoir par terre. Prenant ensuite les sept pains et rendant graces, il les rompit et les donna a ses disciples pour qu\'ils en fissent la distribution ; et ils les distribuerent a la multitude.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 7, 'Ils avaient encore quelques poissons. Jesus les benit aussi et les fit distribuer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 8, 'Tous mangerent et furent rassasies. Des morceaux qui resterent on emporta sept corbeilles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 9, 'or ils etaient quatre mille environ. Ensuite Jesus les congedia.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 10, 'Immediatement apres, il monta dans la barque avec ses disciples il et se rendit dans le pays de Dalmanutha,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 11, 'ou survinrent les Pharisiens qui se mirent a discuter avec lui, lui demandant un signe venant du ciel ; c\'etait pour le mettre a l\'epreuve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 12, 'Quant a lui, il dit en soupirant du plus profond de son coeur : Pourquoi cette race-la demande-t-elle un signe? Je vous le dis en verite, a cette race-la il ne sera point donne de signe!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 13, 'Il les laissa, remonta, dans la barque et passa sur l\'autre rive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 14, 'Or ils oublierent de prendre des pains ; ils n\'en avaient qu\'un seul avec eux dans la barque ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 15, 'et comme Jesus leur adressait cette recommandation : Faites bien attention, prenez bien garde au levain des Pharisiens et a celui d\'Herode, ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 16, 'ils se communiquerent leurs pensees : C\'est parce que nous n\'avons pas de pains !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 17, 'Jesus, le sachant, leur dit : Comment pouvez-vous penser aux pains que vous n\'avez pas? N\'avez-vous donc encore ni comprehension, ni intelligence? Votre coeur est-il toujours aveugles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 18, 'Vous avez des yeux, vous ne voyez pas! Vous avez des oreilles, vous n\'entendez pas ! Vous n\'avez pas de memoire!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 19, 'Quand j\'ai rompu les cinq pains pour les cinq mille hommes, combien de paniers pleins de morceaux avez-vous emportes?  Douze, repondirent-ils. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 20, 'Et quand j\'ai rompu les sept pains pour les quatre mille hommes, combien de corbeilles avez-vous remplies de morceaux et avez-vous emportees?  Sept, repondirent-ils. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 21, 'Il reprit : Est-ce que vous ne comprenez pas encore?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 22, 'Ils arriverent a Bethsaida ; on lui amena un aveugle et on le pria de le toucher.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 23, 'Prenant cet homme par la main, il le conduisit hors du village. Puis il lui mit de la salive sur les yeux, posa les mains sur lui et lui demanda s\'il commencait a voir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 24, 'Celui-ci regarda et repondit : J\'apercois des hommes, parce que je vois comme des arbres qui marchent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 25, 'Alors Jesus posa encore une fois ses mains sur les yeux de l\'aveugle, qui regarda : il etait gueri ; il apercevait distinctement toutes choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 26, 'Jesus le renvoya dans sa maison en lui disant : Ne rentre pas dans le village.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 27, 'Jesus s\'en alla, accompagne de ses disciples, vers les villages voisins de Cesaree de Philippe. Durant la route, il interrogea ses disciples ; il leur dit : Qui dit-on que je suis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 28, 'Ils lui repondirent : Les uns, que tu es Jean Baptiste ; les autres, Elie ; d\'autres encore, l\'un des prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 29, 'Mais vous, reprit-il, qui dites-vous que je suis? Pierre repondit par ces mots : Tu es le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 30, 'Il leur defendit expressement de parler de lui a qui que ce soit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 31, 'Puis il commenca a leur apprendre qu\'il lui fallait, lui, le Fils de l\'homme :  beaucoup souffrir ;  etre rejete par les Anciens, les chefs des pretres et les Scribes ;  etre mis a mort ;  ressusciter au bout de trois jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 32, 'Il exposait tout cela tres clairement. Le tirant alors a l\'ecart, Pierre se mit a le reprendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 33, 'Mais lui, se retournant et regardant ses disciples, reprit severement Pierre, lui disant : Va-t\'en ! Arriere de moi, Satan! parce que tes pensees ne sont pas de Dieu, mais des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 34, 'Ayant appele a lui la foule, ainsi que ses disciples, il leur dit : Si quelqu\'un veut venir derriere moi et me suivre, qu\'il renonce a lui-meme, qu\'il porte sa croix et qu\'il me suive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 35, 'Celui qui voudra sauver sa vie, la perdra ; et il sauvera sa vie, celui qui l\'aura perdue a cause de moi et de l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 36, 'A quoi servira-t-il a un homme de gagner le monde entier s\'il perd sa vie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 37, 'Qu\'est-ce qu\'il donnera en echange de sa vie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 8, 38, 'De celui qui, au sein de cette generation adultere et pecheresse, aura eu honte de moi et de mes paroles, le Fils de l\'homme aura honte a son tour, quand il viendra dans la gloire de son Pere avec les saints anges!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 1, 'Il ajouta : En verite, je vous le dis, quelques-uns sont ici presents qui ne gouteront point la mort avant d\'avoir vu venir, en puissance, le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 2, 'Six jours apres, emmenant seuls avec lui Pierre, Jacques et Jean, Jesus les conduisit sur une haute montagne, a l\'ecart. Et alors, devant eux, il fut transfigure :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 3, 'ses vetements devinrent resplendissants et d\'une eblouissante blancheur. Nul foulon ici-bas ne saurait en produire une semblable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 4, 'Elie leur apparut ; avec lui etait Moise ; et ils s\'entretinrent avec Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 5, 'Pierre alors, s\'adressant a Jesus, lui dit : Rabbi !, qu\'il nous est bon d\'etre ici! Dressons trois tentes, une pour toi, une pour Moise, une pour Elie...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 6, 'Il ne savait que dire ; ils etaient, en effet, terrifies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 7, 'Une nuee survint qui les enveloppa, et de cette nuee sortit une voix : Celui-ci est mon Fils, le bien-aime, ecoutez-le!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 8, 'Immediatement ils regarderent autour d\'eux, et ne virent plus personne ; Jesus seul etait avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 9, 'Comme ils descendaient de la montagne, il leur ordonna de ne raconter a personne ce qu\'ils venaient de voir, si ce n\'est lorsque le Fils de l\'homme serait ressuscite d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 10, 'Ils retinrent ces mots : ressuscite d\'entre les morts, et se demandaient, a part eux, ce qu\'ils signifiaient ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 11, 'et ils l\'interrogerent : Comment les Scribes disent-ils : Il faut, avant tout, qu\'Elie vienne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 12, 'Il leur repondit : Elie viendra avant tout et retablira toutes choses ; d\'ou vient alors qu\'il est ecrit du Fils de l\'homme : il doit beaucoup souffrir et etre meprise?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 13, 'Eh bien, je vous le declare, Elie est venu et ils lui ont fait tout ce qu\'ils ont voulu, conformement a ce qui est ecrit de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 14, 'De retour aupres des disciples, ils les trouverent entoures d\'une foule considerable ; des Scribes discutaient avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 15, 'Des que la multitude apercut Jesus, chacun fut en emoi ; on accourut a lui, on le saluait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 16, 'Il les interrogea : Qu\'est-ce que cette discussion ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 17, 'L\'un de ceux qui etait dans la foule repondit : Maitre, je t\'ai apporte mon fils ; un Esprit muet le possede,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 18, 'et partout ou il s\'empare de lui, il le renverse par terre ; alors l\'enfant ecume, grince des dents et devient tout raide. J\'ai demande a tes disciples de le chasser : ils n\'en ont pas eu la force. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 19, 'O generation sans foi, dit Jesus en leur repondant, jusqu\'a quand serai-je au milieu de vous? Jusqu\'a quand vous supporterai-je? Apportez-moi l\'enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 20, 'On le lui apporta. Mais a peine eut-il vu');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 21, 'Jesus que l\'Esprit lui donna des convulsions, et, tombant par terre, il se tordait en ecumant. Jesus interrogea son pere : Combien y a-t-il de temps que cela lui arrive?  Depuis sa premiere enfance, repondit-il ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 22, 'et souvent l\'Esprit l\'a jete tantot dans le feu, tantot dans l\'eau pour le faire perir ; si tu peux y faire quelque chose, aie pitie de nous, et viens a notre secours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 23, 'Jesus lui repondit : Tu me dis : si tu peux ! Tout est possible a celui qui croit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 24, 'Aussitot le pere de l\'enfant s\'ecria : Je crois! Viens en aide a mon incredulite !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 25, 'Jesus, voyant la foule accourir, fit des menaces a l\'Esprit impur : il lui dit : Esprit sourd et muet, c\'est moi qui te l\'ordonne, sors de cet enfant et desormais n\'y rentre plus!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 26, 'L\'Esprit jeta un cri et sortit, en agitant violemment l\'enfant, qui devint comme mort ; aussi un grand nombre disaient-ils : Il est mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 27, 'Mais Jesus le prit par la main, le releva ; il revint a lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 28, 'Quand Jesus fut entre dans une maison, ses disciples lui demanderent en particulier : Pourquoi, nous, avons-nous ete impuissants a expulser cet Esprit?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 29, 'Jesus leur repondit : Il est d\'une espece, qui ne peut absolument se chasser que par la priere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 30, 'Partis de la, ils traverserent la Galilee, et il voulait que personne ne le sut,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 31, 'car il instruisait ses disciples ; il leur disait : Le Fils, de l\'homme sera livres entre les mains des hommes ; ils le tueront ; puis, une fois mis a mort, apres trois jours, il ressuscitera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 32, 'Mais ils ne comprenaient pas ce langage et ils craignaient de l\'interroger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 33, 'Ils arriverent a Capharnaum ; et quand il fut a la maison, il leur demanda : De quoi vous entreteniez-vous pendant la route?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 34, 'Ils garderent le silence ; car ils avaient discute en chemin sur celui d\'entre eux qui etait le plus grand.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 35, 'Jesus s\'assit, appela les douze et leur dit : Si quelqu\'un veut etre le premier, qu\'il soit le dernier de tous et le serviteur de tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 36, 'Puis il prit un enfant, le placa au milieu d\'eux, et, apres l\'avoir embrasse, il leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 37, 'Qui recoit en mon nom un enfant tel que celui-ci me recoit. Et qui me recoit, recoit non pas moi, mais Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 38, 'Maitre, lui dit Jean, nous avons vu un homme chasser des demons en ton nom, et comme il ne nous suit pas, nous l\'en avons empeche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 39, 'Jesus repondit : Ne l\'en empechez point, car per sonne, apres avoir, en mon nom, fait un miracle, ne peut aussitot mal parler de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 40, 'En effet, qui n\'est pas contre nous est pour nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 41, 'Et quiconque, en mon nom et parce que vous etes au Christ, vous aura donne a boire un verre d\'eau, je vous le dis en verite, ne perdra point sa recompense.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 42, 'Quant a celui qui aura ete une occasion de chute pour l\'un de ces petits qui croient, il vaudrait mieux pour lui qu\'on lui attachat au cou une de ces meules que tournent les anes, et qu\'on le jetat dans la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 43, 'Si ta main est pour toi une occasion de chute, coupe-la ; il t\'est meilleur d\'entrer mutile dans la vie, que d\'aller avec tes deux mains dans la Gehenne, dans le feu inextinguible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 44, '(ou leur ver ne meurt point et ou le feu ne s\'eteint point).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 45, 'Si ton pied est pour toi une occasion de chute, coupe-le ; il t\'est meilleur d\'entrer estropie dans la vie que d\'etre jete avec tes deux pieds dans la Gehenne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 46, '(ou leur ver ne meurt point et ou le feu ne s\'eteint point).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 47, 'Si ton oeil est pour toi une occasion de chute, arrache-le ; il t\'est meilleur d\'entrer borgne dans le Royaume de Dieu que d\'etre jete, avec tes deux yeux, dans la Gehenne');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 48, 'ou : Leur ver ne meurt pas et le feu ne s\'eteint pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 49, 'Car tous seront sales par le feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 9, 50, 'Le sel est bon : mais si le sel devient insipide, avec quoi l\'assaisonnerez-vous? En vous-memes ayez du sel, et, entre vous, vivez en paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 1, 'Jesus, partant de la, se rendit sur les frontieres de la Judee et de l\'autre cote du Jourdain. De nouveau, des multitudes se rassemblerent autour de lui ; de nouveau, suivant sa coutume, il les enseignait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 2, 'Les Pharisiens vinrent a lui et lui demanderent: Est-il permis a un mari de repudier sa femme? C\'etait pour le mettre a l\'epreuve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 3, 'Il leur repondit par ces paroles : Qu\'est-ce que Moise vous a prescrit? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 4, 'Moise, dirent-ils, a permis d\'ecrire un acte de divorce et de la repudier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 5, 'Alors Jesus reprit: C\'est a cause de la durete de vos coeurs qu\'il vous a ecrit ce commandement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 6, 'Mais au commencement du monde Dieu a fait un couple, male et femelle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 7, 'a cause de cela l\'homme laissera son pere et sa mere');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 8, 'et ils seront deux dans une seule chair. Ainsi ils ne sont plus deux, mais une seule chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 9, 'Donc, ce que Dieu a uni, qu\'un, homme ne le separe point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 10, 'Dans la maison, les disciples l\'interrogerent encore sur le meme sujet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 11, 'Qui repudie sa femme, leur dit-il, et en epouse une autre, commet un adultere a l\'egard de la premiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 12, 'Et si une femme qui a quitte son mari en epouse un autre, elle commet un adultere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 13, 'Comme on lui apportait des enfants pour qu\'il les touchat, les disciples blamaient ceux qui les lui apportaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 14, 'Jesus le vit et s\'en indigna. Il leur dit : Laissez les enfants venir a moi ; ne les en empechez pas ; car c\'est a ceux qui leur ressemblent qu\'appartient le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 15, 'En verite, je vous le dis, qui ne recevra pas, comme un enfant, le Royaume de Dieu, n\'y entrera pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 16, 'Puis il les embrassa et, posant les mains sur eux, il les benit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 17, 'Comme il se mettait en route, quelqu\'un courut a lui, se jeta a ses genoux et lui fit cette question : Bon maitre, que dois-je faire pour acquerir la vie eternelle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 18, 'Jesus lui dit : Pourquoi m\'appelles tu bon? nul n\'est bon, si ce n\'est Dieu seul!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 19, 'Tu connais les commandements: Tu ne seras point adultere ; tu ne seras point meurtrier ; tu ne seras point voleur ; tu ne rendras point de faux temoignage ; tu ne commettras point de fraude ; honore ton pere et ta mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 20, 'Il repondit : Tout cela, maitre, je l\'ai observe des ma jeunesse. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 21, 'Jesus, le regardant, l\'aima et lui dit : Une chose te manque, va vendre ce que tu as ; donnes-en le prix aux pauvres ; tu auras alors un tresor dans le ciel ; viens ensuite et suis-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 22, 'Ce langage l\'affligea ; il s\'en alla tout triste, car il avait beaucoup de biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 23, 'Jesus, regardant autour de lui, dit a ses disciples : Combien difficilement entreront dans le Royaume de Dieu ceux qui ont des richesses!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 24, 'Ces paroles surprenaient les disciples. Jesus reprit alors en ces termes : Qu\'il est difficile, mes enfants, d\'entrer dans le Royaume de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 25, 'Il est plus facile a un chameau de passer par l\'ouverture d\'une aiguille \'qu\'a un riche d\'entrer dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 26, 'Ils furent absolument consternes : Qui pourra etre sauve? se dirent-ils l\'un a l\'autre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 27, 'Jesus leur repondit en les regardant : Aux hommes, cela est impossible ; mais non a Dieu ; car tout est possible a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 28, 'Pierre se mit alors a lui dire : Et nous, nous avons tout quitte ! nous t\'avons suivi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 29, 'Jesus repondit : En verite je vous le dis, personne, a cause de moi et a cause de l\'Evangile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 30, 'ne quittera une maison, des freres, des soeurs, une mere, un pere, des enfants, des terres, sans qu\'il ne recoive le centuple des maintenant, en ce temps present, des maisons, des freres, des soeurs, des meres, des enfants, des terres, avec des persecutions et, dans le siecle a venir, la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 31, 'Mais plusieurs des premiers seront derniers et les derniers premiers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 32, 'Ils etaient en chemin, montant a Jerusalem. Jesus marchait en avant des disciples effrayes ; ceux qui le suivaient etaient dans la terreur. De nouveau il reunit les douze et se mit a leur dire ce qui allait lui arriver :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 33, 'Voila que nous montons a Jerusalem ; le Fils de l\'homme y sera livre aux chefs des pretres et aux Scribes qui le condamneront a mort ; puis ils le livreront aux paiens');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 34, 'qui se joueront de lui, lui cracheront au visage, le flagelleront et le tueront et, apres trois jours, il ressuscitera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 35, 'Jacques et Jean (les fils de Zebedee) s\'approcherent de lui et lui dirent : Maitre, nous voudrions que tu nous accordes ce que nous allons te demander.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 36, 'Il repondit : Que voudriez-vous que je vous accorde? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 37, 'Promets-nous, reprirent-ils, que nous siegerons, au jour de ta gloire, l\'un a ta droite, l\'autre a ta gauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 38, 'Jesus repartit: Vous ne savez ce que vous demandez. Pouvez-vous boire la coupe que je bois? ou etre baptises du bapteme dont je suis baptise? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 39, 'Nous le pouvons, lui dirent-ils.  Et Jesus repliqua : La coupe que je bois, vous la boirez ; le bapteme dont je suis baptise, vous en serez baptises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 40, 'Mais quant a sieger a ma droite ou a ma gauche, ce n\'est pas a moi de l\'accorder ; ces places sont a ceux pour lesquels elles ont ete preparees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 41, 'Les dix autres qui avaient entendu commencaient a s\'indigner contre Jacques et Jean ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 42, 'Jesus alors les appela et leur dit : Vous savez que ceux qui passent pour regner sur les nations, en sont les dominateurs, et que les grands, dans ces nations, exercent sur elles un pouvoir imperieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 43, 'Il n\'en est pas ainsi parmi vous. Au contraire, qui voudra devenir grand parmi vous, sera votre serviteur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 44, 'qui voudra devenir le premier parmi vous, sera l\'esclave de tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 45, 'Car le Fils de l\'homme n\'est pas venu pour etre servi, mais pour servir et donner sa vie comme rancon pour plusieurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 46, 'Ils arriverent a Jericho. Lorsqu\'il sortit de la ville avec ses disciples et une foule nombreuse, un aveugle, Bartimee (fils de Timee) mendiait, assis sur le bord de la route.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 47, 'Entendant dire que c\'etait Jesus de Nazareth, il se mit a crier : Fils de David, Jesus, aie pitie de moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 48, 'Beaucoup le menacaient pour le faire taire, mais il n\'en criait que plus fort : Fils de David, aie pitie de moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 49, 'Jesus s\'arreta. Appelez-le, dit-il. On appela cet aveugle, on lui dit : Prends courage, leve-toi, il t\'appelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 50, 'Il se dressa aussitot, rejeta son manteau et s\'avanca vers Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 51, 'Jesus alors lui adressa ces paroles : Que veux-tu que je fasse pour toi? L\'aveugle repondit : Que j\'y voie! Rabbouni !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 10, 52, 'Va, lui dit Jesus, ta foi t\'a sauve. Et subitement il vit, et il suivit Jesus dans le chemin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 1, 'Quand ils furent pres de Jerusalem, de Bethanie et de Bethphage, du cote du mont des Oliviers, Jesus envoya deux de ses disciples, en leur disant :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 2, 'Allez a ce village qui est devant vous ; et tout de suite, a l\'entree, vous trouverez un anon attache, que personne n\'a jamais monte. Detachez-le et l\'amenez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 3, 'Si l\'on vous demande pourquoi vous faites cela, repondez : Le Seigneur en a besoin et on l\'enverra immediatement ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 4, 'Ils partirent et, au detour du chemin, ils trouverent l\'anon attache tout contre une porte exterieure ; ils le delierent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 5, 'Quelques-uns de ceux qui etaient la leur dirent : Que faites-vous? Pourquoi detachez-vous cet anon?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 6, 'Ils repondirent ce que Jesus leur avait dit ; et on les laissa faire. Ils amenerent donc l\'anon,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 7, 'sur lequel ils placerent leurs manteaux, a Jesus, qui s\'assit sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 8, 'Sur le chemin, un grand nombre etendirent leurs vetements et d\'autres des rameaux coupes dans les champs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 9, 'Et ceux qui le precedaient et ceux qui le suivaient criaient : Hosanna! Beni soit celui qui vient au nom du Seigneur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 10, 'Beni soit le Regne qui commence, le Regne de notre pere David! Hosanna au plus haut des cieux !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 11, 'Il entra a Jerusalem et penetra dans le Temple. Il promena autour de lui son regard sur toutes choses, et comme l\'heure s\'avancait, il partit avec les douze pour Bethanie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 12, 'Le lendemain, comme ils sortaient de Bethanie, il eut faim.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 13, 'Apercevant de loin un figuier couvert de feuilles, il y alla pourvoir s\'il n\'y decouvrirait pas quelque fruit ; mais quand il s\'en fut approche, il n\'y trouva que des feuilles. En effet, ce n\'etait pas encore le moment des figues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 14, 'Alors il s\'adressa a l\'arbre et lui dit : Que personne ne mange desormais de ton fruit! Ce mot fut entendu des disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 15, 'Quand ils furent arrives a Jerusalem, Jesus entra dans le Temple et se mit a chasser les vendeurs et les acheteurs ; il renversa les tables des changeurs, ainsi que les sieges des marchands de colombes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 16, 'de plus, il ne laissait personne, portant un objet quelconque, passer par le Temple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 17, 'Et voici ce qu\'il enseignait : N\'est-il pas ecrit : Ma maison s\'appellera, pour tous les peuples, maison de priere ? Et vous, vous en avez fait une caverne de brigands !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 18, 'En l\'entendant, les chefs des pretres et les Scribes cherchaient par quels moyens ils le feraient perir. En effet, ils avaient peur de lui a cause du peuple, qui, tout entier, etait extremement frappe de son enseignement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 19, 'Quand venait le soir, ils sortaient de la ville ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 20, 'or, en repassant le matin, ils virent le figuier desseche jusqu\'a la racine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 21, 'Se souvenant de ce qui s\'etait passe, Pierre dit a Jesus : Rabbi, voici le figuier que tu as maudit ; il s\'est desseche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 22, 'Jesus alors, s\'adressant a tous, leur dit : Ayez foi en Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 23, 'En verite, je vous le dis : Si quelquun dit a cette montagne, sans aucune hesitation dans son coeur, et en croyant a l\'accomplissement de sa parole : Souleve-toi, et jette-toi dans la mer, cela lui sera accorde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 24, 'C\'est pourquoi je vous dis : Croyez que vous recevrez tout ce que vous demandez dans votre priere, et tout vous sera accorde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 25, 'En outre, quand vous vous mettez a prier, pardonnez a toute personne contre qui vous pourriez avoir quelque chose, afin que votre Pere qui est dans les cieux vous pardonne aussi vos offenses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 26, '(Si vous ne pardonnez pas, votre Pere qui est dans les cieux ne vous pardonnera pas non plus vos offenses).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 27, 'Ils etaient retournes a Jerusalem ; et comme il allait et venait dans le Temple, les chefs des pretres, les Scribes et les Anciens s\'approcherent de lui');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 28, 'et lui dirent : En vertu de quelle autorite fais-tu ces choses ? ou qui t\'a donne l\'autorite de les faire ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 29, 'Jesus leur repondit : Je vous ferai, moi aussi, une question, une seule. Repondez-moi et je vous dirai en vertu de quelle autorite je fais ces choses :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 30, 'Le bapteme de Jean venait-il du ciel ou des hommes? Repondez-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 31, 'Or ils faisaient, a part eux, ce raisonnement : Si nous repondons : Du ciel, il dira : Pourquoi donc ne l\'avez vous pas cru?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 32, 'Repondrons-nous alors : Des hommes?.... Ici ils avaient a craindre la foule ; car tout le monde tenait Jean pour un prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 11, 33, 'Alors ils repondirent ainsi a Jesus : Nous ne savons. Jesus repliqua : Je ne vous dis pas, moi non plus, en vertu de quelle autorite je fais ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 1, 'Puis il se mit a leur parler en paraboles : Un homme avait plante une vigne ; apres l\'avoir entouree d\'une haie, y avoir creuse une cuve et construit une tour, il la loua a des vignerons et fit une absence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 2, 'La saison venue, il envoya, un serviteur aux vignerons pour recueillir de leurs mains une partie du produit de sa vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 3, 'Or ceux-ci s\'emparerent de lui, le battirent et le renvoyerent les mains vides.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 4, 'Le maitre alors leur envoya un autre serviteur. Celui-la, ils le blesserent a la tete et le traiterent avec mepris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 5, 'Il en envoya un autre : celui-la ils le tuerent ; plusieurs autres : ils battirent ceux-ci et tuerent ceux-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 6, 'Il avait encore un fils unique, un bien-aime. Il le leur envoya le dernier, disant : Ils respecteront mon fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 7, 'Mais ces vignerons se dirent de l\'un a l\'autre : Celui-la c\'est l\'heritier ; allons! tuons-le et l\'heritage sera a nous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 8, 'Et s\'etant empares de lui, ils le tuerent et le jeterent hors de la vigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 9, 'Que fera le maitre de la vigne? Il viendra, il fera perir les vignerons et donnera la vigne a d\'autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 10, 'N\'avez-vous pas lu ce passage de l\'Ecriture : La pierre qu\'ont rejetee ceux qui batissaient, Celle-la meme est devenue la tete de l\'angle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 11, 'C\'est l\'ouvrage du Seigneur, Ouvrage admirable a nos yeux!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 12, 'Ils cherchaient a l\'arreter ; car ils comprenaient bien que c\'etait eux qu\'il avait en vue dans cette parabole ; mais ils redouterent la multitude. Alors ils le laisserent et partirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 13, 'Ensuite ils lui envoyerent quelques Pharisiens et Herodiens pour le prendre au piege par ses propres paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 14, 'Ils arrivent et lui disent: Maitre, nous savons que tu es veridique ; que tu n\'as souci de qui que ce soit ; sans regarder a la figure des uns et des autres, tu enseignes en toute verite la voie de Dieu. Est-il permis, oui ou non, de payer l\'impot a Cesar? Le payerons-nous ou ne le payerons-nous pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 15, 'Connaissant leur perfidie, il leur dit : Pourquoi me mettez-vous a l\'epreuve?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 16, 'Apportez-moi un denier, que je le voie. Ils lui en apporterent un. De qui est cette image? demanda-t-il ; de qui est cette inscription? Ils lui repondirent : De Cesars.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 17, 'Alors Jesus leur dit : Rendez a Cesar ce qui est a Cesar et a Dieu ce qui est a Dieu. Et il les surprit extremement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 18, 'Ensuite des Sadduceens (ceux qui disent qu\'il n\'y a point de resurrection) vinrent lui poser une question :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 19, 'Maitre, dirent-ils, voici ce que Moise nous a prescrit : Si le frere de quelqu\'un meurt laissant sa femme sans enfants, celui-ci devra epouser la veuve, pour susciter a son frere une posterite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 20, 'Or il y a eu sept freres. Le premier a pris femme et est mort sans laisser de posterite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 21, 'Le second a epouse la veuve et est mort aussi sans avoir de posterite. De meme du troisieme ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 22, 'des sept aucun n\'a laisse de posterite ; enfin, apres eux tous, la femme aussi est morte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 23, 'Eh bien, a la resurrection, duquel d\'entre eux sera-t-elle l\'epouse? Les sept, en effet, l\'ont eue pour femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 24, 'Jesus leur repondit : N\'etes-vous pas dans une erreur qui provient de ce que vous ignorez et les Ecritures et la puissance de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 25, 'En effet, ceux qui ressuscitent d\'entre les morts ne se marient ni ne donnent en mariage, mais ils sont dans les cieux comme des anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 26, 'Et quant aux morts, quant a leur resurrection, n\'avez-vous pas lu dans le livre de Moise, au passage ou il est question du buisson, en quels termes Dieu lui a parle : Je suis, dit-il, le Dieu d\'Abraham, le Dieu d\'Isaac, le Dieu de Jacob.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 27, 'Or ce n\'est point des morts, c\'est des vivants qu\'il est Dieu. Vous etes donc dans une grande erreur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 28, 'Un Scribe s\'approcha ; il avait entendu leur discussion, et, remarquant qu\'il leur avait parfaitement repondu, il lui demanda: Quel est parmi les commandements le premier de tous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 29, 'Jesus, repondit: Voici le premier : Ecoute, Israel, le Seigneur notre Dieu est l\'Unique Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 30, 'et tu aimeras le Seigneur ton Dieu, de tout ton coeur, de toute ton ame, de toute ta pensee, de toute ta force ; voici le second :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 31, 'Tu aimeras ton prochain comme toi-meme. Aucun autre commandement n\'est plus grand que ceux la. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 32, 'Tres bien, maitre, lui dit le Scribe ; tu es dans la verite lorsque tu dis qu\'Il est Unique et qu\'il n\'y en a pas d\'autre que Lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 33, 'et lorsque tu dis que l\'aimer de tout son coeur, de toute son intelligence, de toute sa force, et qu\'aimer son prochain comme soi-meme l\'emporte sur tous les holocaustes et tous les sacrifices.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 34, 'Jesus, remarquant la sagesse de ses paroles, lui dit : Tu n\'es pas loin du Royaume de Dieu. Personne n\'osait plus le questionner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 35, 'Alors Jesus, prenant la parole, enseigna dans le Temple en ces termes : Comment les Scribes disent-ils que le Christ est fils de David?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 36, 'David lui-meme a ainsi parle par l\'Esprit saint : Le Seigneur a dit a mon Seigneur : Siege a ma droite Jusqu\'a ce que j\'aie mis tes ennemis sous tes pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 37, 'David lui-meme l\'appelle Seigneur ; d\'ou vient alors qu\'il est son fils ? La foule, qui etait nombreuse, prenait plaisir a l\'ecouter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 38, 'Voici encore ce qu\'il disait dans son enseignement : Mefiez-vous de ces Scribes qui se complaisent a se promener en robes solennelles, a recevoir des salutations sur les places publiques,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 39, 'a occuper les premiers sieges dans les synagogues, les premieres places dans les festins,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 40, 'qui devorent les ressources des veuves, et qui affectent de prier longuement. C\'est pour ces hommes-la que le jugement aura le plus de rigueur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 41, 'Il s\'etait assis vis-a-vis du tronc et il regardait comment chacun, dans la foule, y mettait son offrande. Nombre de riches donnaient beaucoup ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 42, 'survint une veuve pauvre qui mit deux leptes (ce qui fait un quadrant).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 43, 'Il appela ses disciples et leur dit : En verite je vous le dis : Cette veuve pauvre a mis dans le tronc plus que tous les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 12, 44, 'Car tous les autres ont puise dans leur superflu pour donner ; mais celle-ci a pris sur son indigence ; elle a donne tout ce qu\'elle possedait, tout ce qu\'elle avait pour vivre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 1, 'Comme Jesus sortait du Temple, un de ses disciples lui dit: Regarde, maitre ; quelles pierres! quelles constructions ! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 2, 'Tu vois, lui dit Jesus, ces grands edifices : il n\'en restera pas pierre sur pierre ; tout sera renverse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 3, 'Puis il alla s\'asseoir sur le mont des Oliviers en face du Temple ; et la Pierre, Jacques, Jean et Andre l\'interrogerent en particulier :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 4, 'Dis-nous quand ces choses arriveront, et quel sera le signe qu\'elles vont toutes s\'accomplir ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 5, 'Jesus alors se mit a leur dire : Veillez a ce que personne ne vous seduise :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 6, 'plusieurs viendront en prenant mon nom, disant : C\'est moi ! et ils seduiront bien des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 7, 'Quand vous entendrez parler de guerres et de bruits de guerres, ne vous alarmez point ; il faut que cela arrive ; mais ce ne sera pas encore la fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 8, 'Se soulevera, en effet, nation contre nation, royaume contre royaume ; il y aura ici et la des tremblements de terre ; il y aura des famines.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 9, 'Ce seront les douleurs de l\'enfantement qui commencent! Quant a vous, prenez garde a vous-memes! On vous livrera aux tribunaux ; dans les synagogues, vous serez frappes de verges, et a cause de moi vous comparaitrez devant des procurateurs et des rois, pour leur etre en temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 10, 'Il faut d\'abord que l\'Evangile soit preche dans toutes les nations.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 11, 'Quand vous serez ainsi emmenes et livres, ne vous preoccupez pas d\'avance de ce que vous aurez a dire ; mais parlez suivant ce qui vous sera donne au moment meme ; ce ne sera pas vous qui parlerez, ce sera l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 12, 'Un frere livrera son frere a la mort et un pere son enfant ; des enfants se souleveront contre leurs parents et les feront mourir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 13, 'et vous serez en haine a tout le monde a cause de mon nom. Mais celui qui aura persevere jusqu\'a la fin, celui-la sera sauve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 14, 'Quand vous verrez l\'abomination de la desolation installee au lieu ou elle ne doit pas etre (comprends bien, lecteur!), que ceux qui seront dans la Judee fuient dans les montagnes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 15, 'que celui qui sera sur le toit ne descende point, ne rentre point pour emporter quelque chose de sa maison ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 16, 'que celui qui sera dans les champs ne retourne point sur ses pas pour prendre son manteau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 17, 'Malheur aux femmes qui seront enceintes et a celles qui allaiteront en ces jours-la!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 18, 'Priez pour que tout cela n\'arrive pas en hiver.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 19, 'Car ce seront des jours d\'une tribulation telle qu\'il n\'y en a pas eu de pareille depuis la creation, depuis que Dieu commenca a creer jusqu\'a present, et qu\'il n\'y en aura plus jamais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 20, 'Et si le Seigneur n\'avait pas abrege ces jours, nulle vie ne serait sauvee ; mais a cause des elus, de ceux qu\'il a choisis, il a abrege ces jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 21, 'Si l\'on vous dit alors : Voyez! le Christ est ici ! ou : Voyez! il est la! ne le croyez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 22, 'Il surgira de faux Christs et de faux prophetes operant signes et prodiges afin, si possible, de seduire les elus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 23, 'Vous, soyez sur vos gardes : je vous ai tout predit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 24, 'Cependant, ces jours-la, apres cette tribulation, le soleil s\'obscurcira, la lune ne donnera plus sa lumiere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 25, 'les etoiles tomberont du ciel ; les puissances qui sont dans les cieux seront ebranlees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 26, 'Et c\'est alors qu\'on verra le Fils de l\'homme arrivant, au sein des nuees, en grande puissance et gloire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 27, 'c\'est alors qu\'il enverra les anges et rassemblera les elus des quatre vents de l\'horizon, depuis l\'extremite de la terre jusqu\'a l\'extremite du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 28, 'Que le figuier vous serve de comparaison : Quand ses branches deviennent tendres et que ses feuilles poussent, vous savez que l\'ete est proche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 29, 'de meme quand vous verrez arriver ces choses, sachez qu\'il est proche, qu\'il est aux portes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 30, 'En verite je vous le dis, cette generation ne passera pas que tout cela n\'arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 31, 'Le ciel et la terre passeront, mais mes paroles ne passeront point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 32, 'De ce jour ou de l\'heure, nul ne sait rien ; ni les anges dans le ciel, ni le Fils ; personne, excepte le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 33, 'Soyez sur vos gardes ; soyez vigilants ; puisque vous ne savez pas quand viendra le moment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 34, 'Lorsqu\'un homme part pour un voyage, qu\'il laisse sa maison, qu\'il donne pouvoir a ses serviteurs, a chacun son travail, il recommande au portier de veiller.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 35, 'Veillez donc  (puisque vous ne savez pas quand viendra le maitre de la maison, si ce sera le soir, ou au milieu de la nuit, ou au chant du coq, ou le matin) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 36, 'de peur qu\'arrivant a l\'improviste il ne vous trouve endormis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 13, 37, 'Or, ce que je vous dis a vous, je le dis a tous: Veillez!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 1, 'Deux jours apres, c\'etaient la Paque et les Azymes. Les chefs des pretres et les Scribes cherchaient un moyen de s\'emparer de Jesus par ruse et de le tuer ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 2, 'ils disaient cependant : Rien pendant la fete, le peuple pourrait se soulever.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 3, 'Jesus etait a Bethanie dans la maison de Simon le lepreux. Comme il etait a table, il vint une femme portant un vase d\'albatre rempli d\'un tres precieux parfum (de l\'huile de nard pur) ; et, brisant le vase d\'albatre, elle en repandit le contenu sur la tete de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 4, 'Or il s\'en trouva quelques-uns qui en furent, a part eux, indignes : A quoi bon perdre ainsi ce parfum? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 5, 'On aurait pu vendre ce parfum-la plus de trois cents denier set en donner le prix aux pauvres. Ils etaient exasperes contre cette femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 6, 'Mais Jesus dit: Laissez-la ; pourquoi lui faites-vous de la peine? C\'est une bonne oeuvre qu\'elle a faite pour moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 7, 'Car les pauvres vous les avez toujours avec vous ; et quand vous le voulez, vous pouvez leur faire du bien ; tandis que moi vous ne m\'avez pas pour toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 8, 'Elle a fait ce qu\'elle a pu. A l\'avance, elle a parfume mon corps pour la sepulture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 9, 'En verite, je vous le dis, partout ou sera preche l\'Evangile  dans le monde entier  on racontera aussi, en memoire d\'elle, ce qu\'elle a fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 10, 'Cependant Judas l\'Iskariote, un des douze, alla trouver les chefs des pretres pour le leur livrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 11, 'Ses paroles les mirent dans la joie, et ils lui promirent de lui donner de l\'argent. Il chercha alors une occasion pour le livrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 12, 'Le premier jour des Azymes, le jour ou l\'on sacrifie la Paque, les disciples dirent a Jesus : Ou veux-tu que nous allions te preparer ce qui est necessaire pour le repas pascal? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 13, 'Allez a la ville leur repondit-il en y envoyant deux d\'entre eux ; vous rencontrerez un homme portant une cruche d\'eau ; vous le suivrez');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 14, 'et voici comment vous parlerez au proprietaire de la maison ou il entrera : Le maitre dit : Ou est la chambre qui m\'est destinee et ou je mangerai la Paque avec mes disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 15, 'Lui-meme alors vous montrera une chambre haute, grande, garnie de tapis, toute prete. C\'est la que vous ferez pour nous les preparatifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 16, 'Les disciples partirent, allerent a la ville, trouverent toutes choses comme il les leur avait dites, et preparerent la Paque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 17, 'Le soir venu, il s\'y rendit avec les douze.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 18, 'Pendant qu\'ils etaient a table et qu\'ils mangeaient, Jesus dit : En verite je vous le dis, l\'un de vous  qui mange avec moi me livrera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 19, 'Ils devinrent tout tristes et lui demanderent l\'un apres l\'autre : Est-ce moi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 20, 'Il leur repondit : C\'est l\'un des douze, c\'est celui qui met la main au plat avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 21, 'Car le Fils de l\'homme s\'en va, selon qu\'il est ecrit de lui, mais malheur a cet homme par qui le Fils de l\'homme est livre! Mieux vaudrait pour cet homme-la ne jamais etre ne!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 22, 'Pendant qu\'ils mangeaient, il prit du pain et, ayant prononce la benediction, il le rompit et le leur donna en disant : Prenez! ceci est mon corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 23, 'Prenant ensuite une coupe, il rendit graces, la leur donna et ils en burent tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 24, 'Ceci est mon sang, leur dit-il, le sang de l\'alliance, lequel est repandu pour plusieurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 25, 'En verite, je vous le dis, je ne boirai plus jamais du fruit de la vigne jusqu\'en ce jour ou je le boirai nouveau dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 26, 'Apres le chant des Psaumes, ils partirent pour le Mont des Oliviers,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 27, 'et Jesus leur dit: Tous, vous rencontrerez une occasion de tomber, car il est ecrit : Je frapperai le berger et les brebis seront dispersees ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 28, 'Mais apres ma resurrection je vous precederai en Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 29, 'Pierre lui dit : Quand meme tous viendraient a faillir, moi, jamais !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 30, 'Jesus lui repondit : En verite je te dis que toi-meme, aujourdhui, durant cette nuit, avant qu\'un coq chante deux fois, tu me renieras trois fois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 31, 'Pierre cependant insistait toujours plus : Me fallut-il mourir avec toi, non, je ne te renierai pas! Et tous parlerent de meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 32, 'Ils vinrent en un lieu du nom de Gethsemane, et Jesus dit a ses disciples : Asseyez-vous ici pendant que je prierai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 33, 'Puis il prit avec lui Pierre, Jacques et Jean, et il commenca a etre dans l\'abattement et dans l\'angoisse ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 34, 'et il leur dit : Mon ame est accablee de tristesse jusqu\'a la mort. Restez ici et veillez!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 35, 'Puis, s\'etant un peu avance, il se prosternait la face contre terre, priant pour que cette heure, si cela etait possible, passat loin de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 36, 'Il disait : Abba ! Pere! tout t\'est possible a toi ! Ecarte loin de moi cette coupe ; cependant, non pas ce que je veux, mais ce que tu veux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 37, 'Il revint et il les trouva qui dormaient, et il dit a Pierre :Tu dors, Simon! n\'as-tu pas eu la force de veiller une seule heure?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 38, 'Veillez et priez pour ne pas succomber a l\'epreuve!. Certes l\'esprit est prompt, mais la chair est faible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 39, 'Il s\'eloigna de nouveau et pria, repetant les memes paroles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 40, 'puis il revint encore et les trouva qui s\'etaient endormis ; leurs yeux en effet etaient appesantis, et ils ne savaient que lui repondre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 41, 'La troisieme fois qu\'il revint, il leur dit: Dormez maintenant ; reposez-vous ; c\'est assez, l\'heure est venue! Le Fils de l\'homme va etre livre entre les mains des pecheurs!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 42, 'Levez-vous ! Allons! Voici celui qui me livre! Il approche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 43, 'Au meme instant, pendant qu\'il parlait encore, arrive Judas, l\'un des douze, et avec lui, envoyee par les chefs des pretres, les Scribes et les Anciens, une troupe armee d\'epees et de batons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 44, 'Le traitre etait convenu avec eux d\'un signal. Il leur avait dit : Celui auquel je donnerai un baiser, ce sera lui. Vous vous en saisirez et l\'emmenerez surement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 45, 'Donc, en arrivant, il s\'approcha de Jesus et l\'appelant : Rabbi, il l\'embrassa avec effusion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 46, 'Eux alors mirent la main sur lui et le saisirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 47, 'Mais un de ceux qui etaient presents, tirant l\'epee, frappa le serviteur du Grand-Pretre et lui coupa l\'oreille:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 48, 'Jesus alors prononca ces paroles : Pour vous emparer de moi, vous etes venus, comme contre un brigand, avec des epees et des batons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 49, 'Tous les jours, j\'etais au milieu de vous, dans le Temple, j\'enseignais et vous ne m\'avez pas arrete. Mais il en est ainsi pour que soient accomplies les Ecritures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 50, 'L\'abandonnant alors, tous les disciples prirent la fuite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 51, 'Seul, un jeune homme le suivait ; il n\'avait sur le corps qu\'un leger vetement. On saisit ce jeune homme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 52, 'mais il s\'echappa tout nu, abandonnant son vetement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 53, 'On conduisit Jesus chez le Grand-Pretre, ou tous s\'assemblerent en meme temps, les chefs des pretres, les Anciens, les Scribes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 54, 'Pierre, qui le suivait de loin, parvint jusque dans l\'interieur de la coure du Grand-Pretre. La, il s\'assit aupres du feu, mele aux gens de service et se chauffant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 55, 'Les chefs des pretres et le Sanhedrin tout entier cherchaient contre Jesus un temoignage qui le fit condamner a mort, mais ils n\'en trouvaient pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 56, 'Plusieurs, en effet, deposaient mensongerement contre lui ; mais leurs assertions etaient contradictoires.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 57, 'En surgit enfin quelques-uns qui porterent a sa charge ce faux temoignage :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 58, 'Nous lui avons entendu dire ceci : Je puis, moi, renverser ce Temple fait de main d\'homme, et, en trois jours, j\'en batirai un autre qui ne sera pas de main d\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 59, 'Mais ici meme leurs depositions ne concordaient pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 60, 'Alors le Grand-Pretre se leva au milieu de l\'assemblee et posa cette question a Jesus : Ne reponds-tu rien a ce que ces gens-ci deposent contre toi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 61, 'Jesus gardait le silence ; il ne repondit rien. Le Grand-Pretre lui posa une seconde question ainsi concue : Es-tu le Christ, le Fils de Celui qui est beni?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 62, 'Jesus repondit :Je le suis ; et vous verrez le Fils de l\'homme siegeant a la droite de la Puissance et venant avec les nuees du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 63, 'Alors le Grand-Pretre, dechirant ses vetements : Qu\'avons-nous encore besoin de temoins? dit-il ; vous avez entendu le blaspheme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 64, 'Quel est votre avis? Tous le declarerent coupable et le condamnerent a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 65, 'Alors ils se mirent, quelques-uns, a cracher sur lui, a lui couvrir le visage d\'un voile et a lui donner des coups de poing. Fais le prophete! lui disaient-ils. Quant aux gens de service, ils le recurent a coups de baton.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 66, 'Pierre etait en bas, dans la cour ; survint l\'une des servantes du Grand-Pretre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 67, 'Voyant Pierre qui se chauffait, elle lui dit en le regardant : Toi aussi tu etais avec Jesus de Nazareth.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 68, 'Et lui de le nier : Je ne sais pas, dit-il, et ne comprends pas de quoi tu parles. Il s\'en alla et sortit dans le vestibule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 69, 'Mais la servante l\'apercut : Celui-la en est, repeta-t-elle a ceux qui etaient presents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 70, 'De nouveau Pierre nia. Un moment apres, ceux qui se trouvaient la recommencerent a parler a Pierre : Certainement tu en es ; car tu es aussi Galileen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 71, 'Il se livra alors a des imprecations : Je ne connais pas, je le jure, cet homme dont vous parlez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 14, 72, 'Soudain un coq chanta pour la seconde fois. Et se souvenant de la parole que lui avait dite Jesus : Avant qu\'un coq chante deux fois, tu me renieras trois fois, Pierre se mit a pleurer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 1, 'Des le matin, les chefs des pretres, auxquels s\'etaient joints les Anciens et les Scribes, le Sanhedrin tout entier, qui avaient tenu conseil, chargerent Jesus de liens, l\'emmenerent et le livrerent a Pilate,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 2, 'qui l\'interrogea : C\'est toi qui es le Roi des Juifs? Jesus repondit ainsi : Tu le dis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 3, 'Les chefs des pretres cependant formulaient contre lui des accusations en quantite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 4, 'Pilate l\'interrogea une seconde fois : Tu ne reponds rien ! Vois tout ce dont on t\'accuse!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 5, 'Mais Jesus ne repondit plus rien, ce dont Pilate, fut tres surpris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 6, 'A chaque fete, il remettait en liberte un prisonnier, celui qu\'on lui demandait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 7, 'Or il y en avait un nomme Bar-Abbas, emprisonne avec les seditieux qui, dans une emeute, avaient commis un meurtre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 8, 'La foule, etant donc montee au tribunal, se mit a demander ce qu\'on lui accordait toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 9, 'Pilate alors, qui comprit que c\'etait par une haine jalouse que les chefs des pretres lui avaient livre Jesus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 10, 'fit a la foule cette proposition : Voulez-vous que je vous delivre le Roi des Juifs ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 11, 'Mais les chefs des pretres pousserent la foule a demander de preference la mise en liberte de Bar-Abbas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 12, 'Pilate se fit entendre de nouveau : Que ferai-je donc de celui que vous appelez le Roi des Juifs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 13, 'Ils crierent encore: Crucifie-le! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 14, 'Qu\'a-t-il donc fait de mal? reprit Pilate. Mais ils n\'en criaient que plus fort : Crucifie-le !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 15, 'Pilate alors, voulant contenter la multitude, lui delivra Bar-Abbas, et, apres avoir fait flageller Jesus, il l\'abandonna au supplice de la croix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 16, 'Les soldats emmenerent Jesus a l\'interieur de la cour, c\'est-a-dire dans le pretoire, ou ils reunirent toute la cohorte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 17, 'Ils l\'habillerent d\'un manteau couleur de pourpre et lui mirent une couronne d\'epines qu\'ils avaient tressee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 18, 'Puis ils lui rendirent leurs hommages : Salut! Roi des Juifs!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 19, 'Avec un roseau ils lui donnaient des coups sur la tete ; ils crachaient sur lui, et, se mettant a genoux, ils se prosternaient devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 20, 'Quand ils eurent fini de se moquer de lui, ils lui oterent le manteau de pourpre et lui remirent ses vetements. Ils l\'emmenerent pour le crucifier');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 21, 'et ils obligerent un passant, un certain Simon de Cyrene, le pere d\'Alexandre et de Rufus, qui revenait des champs, a porter sa croix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 22, 'Ils le conduisirent ainsi a Golgotha, c\'est un endroit dont le nom signifie : Le Crane.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 23, 'La ils lui donnerent du vin mele de myrrhe ; mais il n\'en prit pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 24, 'Ils le crucifierent, puis se partagerent ses vetements, en tirant au sort la part de chacun.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 25, 'C\'est a la troisieme heure qu\'ils le crucifierent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 26, 'et l\'inscription indiquant son crime etait ainsi concue : LE ROI DES JUIFS.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 27, 'Avec lui deux brigands furent crucifies, l\'un a sa droite, l\'autre a sa gauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 28, '(ainsi s\'accomplit la parole de l\'Ecriture: il a ete compte parmi les impies.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 29, 'Les passants l\'injuriaient ; ils lui disaient, en gesticulant de la tete : Ohe! toi qui detruis le Temple et le rebatis en trois jours,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 30, 'sauve-toi toi-meme en descendant de la croix !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 31, 'Les chefs des pretres avec les Scribes se moquaient aussi de lui et se disaient l\'un a l\'autre : Il en a sauve d\'autres et il ne peut pas se sauver lui-meme! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 32, 'Que le Christ, que le Roi d\'Israel descende maintenant de la croix, afin que nous voyions et que nous croyions! Ceux memes que l\'on avait crucifies avec lui l\'insultaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 33, 'Quand fut venue la sixieme heure, des tenebres se firent sur tout le pays jusqu\'a la neuvieme heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 34, 'A la neuvieme heure, Jesus jeta un cri et dit d\'une voix forte : Heloi ! lema sabachthanei ! ce qui signifie : Mon Dieu! Mon Dieu! pourquoi m\'as-tu abandonne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 35, 'Quelques-uns des assistants l\'entendant : Le voila qui appelle Elie, dirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 36, 'Il y en eut un qui courut tremper une eponge dans du vinaigre et, l\'attachant a un roseau, il lui donna a boire, en disant : Laissez et voyons si Elie va venir le descendre !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 37, 'Jesus cependant, ayant jete un grand cri, expira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 38, 'Et le rideau du Temple se dechira en deux depuis le haut jusqu\'en bas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 39, 'Voyant comment il avait expire, le centurion, qui se tenait en face de lui, dit : Cet homme-la etait veritablement Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 40, 'Il y avait la egalement des femmes qui regardaient de loin. Parmi elles, Marie Magdeleine, Marie, mere de Jacques le Petit et de Joses, et Salome,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 41, 'qui le suivaient et le servaient lorsqu\'il etait en Galilee ; plusieurs autres encore qui etaient montees avec lui a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 42, 'Deja il se faisait tard, et comme c\'etait un jour de Preparation, c\'est-a-dire une veille de sabbat,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 43, 'Joseph, celui qui est d\'Arimathee, membre estime du Grand-Conseil, et qui lui aussi attendait le Royaume de Dieu, alla hardiment se presenter a Pilate et lui demanda le corps de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 44, 'Surpris qu\'il fut mort sitot, Pilate fit appeler le centurion et lui demanda s\'il etait mort depuis longtemps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 45, 'Informe par le centurion, il donna le corps a Joseph.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 46, 'Celui-ci, ayant achete un linceul, descendit le corps de la croix, l\'enveloppa du linceul et le deposa dans un sepulcre taille dans le roc, puis il roula une pierre contre la porte du tombeau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 15, 47, 'Or, Marie Magdeleine et Marie, mere de Joses, regardaient ou on le mettait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 1, 'Quand le sabbat fut passe, Marie Magdeleine, Marie, mere de, Jacques, et Salome acheterent des aromates pour aller embaumer le corps de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 2, 'De tres grand matin, le premier jour de la semaine, elles se rendent au sepulcre ; le soleil se levait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 3, 'Qui donc, se demandaient-elles, nous roulera la pierre et l\'otera de l\'entree de la tombe ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 4, 'Elles regardent et s\'apercoivent que cette pierre, qui etait enorme, a ete enlevee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 5, 'Alors elles entrerent dans le tombeau et virent, assis a droite, un jeune homme revetu d\'une robe blanche. Elles furent saisies de terreur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 6, 'Ne vous effrayez pas, leur dit-il ; vous cherchez Jesus de Nazareth, le crucifie ; il est ressuscite ; il n\'est pas ici. Voyez la place ou on l\'avait depose.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 7, 'Et maintenant allez dire a ses disciples et a Pierre quil vous precede en Galilee ; la vous le verrez, comme il vous l\'a dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 8, 'A leur sortie du tombeau, elles prirent la fuite. Elles etaient toutes tremblantes et hors d\'elles, et ne dirent rien a personne Car elles avaient peur...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 9, '(Ressuscite de grand matin le premier jour de la semaine, il apparut tout d\'abord a Marie-Magdeleine, de laquelle il avait chasse sept demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 10, 'Celle-ci alla annoncer a ceux qui avaient ete avec lui et qui etaient dans le deuil et dans les larmes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 11, 'Eux, l\'entendant dire qu\'il vivait et qu\'elle l\'avait vu, ne la crurent pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 12, 'Ensuite il apparut sous une autre forme a deux d\'entre eux qui etaient en chemin et se rendaient a la campagne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 13, 'Ils vinrent egalement annoncer aux autres ; mais ils ne les crurent pas non plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 14, 'Enfin il apparut aux onze pendant qu\'ils etaient a table ; il leur reprocha leur incredulite et la durete de leur coeur, parce qu\'ils n\'avaient pas cru ceux qui l\'avaient vu ressuscite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 15, 'Et il leur dit: Allez dans tout le monde, prechez l\'Evangile a toute creature.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 16, 'Qui croira et recevra le bapteme sera sauve ; qui ne croira pas sera condamne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 17, 'Et voici les miracles qui accompagneront ceux qui auront cru: en mon nom ils chasseront des demons ; ils auront le don des langues ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 18, 'ils manieront des serpents, et les poisons qu\'ils pourront boire ne leur feront point de mal. Ils imposeront les mains a des malades et ils les gueriront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 19, 'Le Seigneur, apres avoir ainsi parle, fut enleve au ciel et s\'assit a la droite de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (41, 16, 20, 'Quant a eux, ils partirent et precherent partout, le Seigneur agissant avec eux et confirmant la parole par les miracles qui l\'accompagnaient.) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 1, 'Cher Theophile, puisque plusieurs autres ont essaye de rediger un recit des faits accomplis parmi nous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 2, 'tels que nous les ont transmis ceux qui en ont ete, des lorigine, temoins oculaires et qui sont devenus ministres de la Parole,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 3, 'jai cru devoir, moi aussi, apres metre soigneusement informe de tout, depuis le commencement, ten ecrire une narration suivie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 4, 'pour que tu reconnaisses la solidite des enseignements que tu as recus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 5, 'Aux jours d\'Herode, roi de Judee, vivait un pretre nomme Zaccharie. Il etait de la classe d\'Abia et sa femme etait d\'entre les filles d\'Aaron. Son nom etait Elisabeth.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 6, 'Tous deux etaient justes devant Dieu, marchant irreprochables dans tous les commandements et toutes les ordonnances du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 7, 'Ils n\'avaient point d\'enfants ; parce qu\'Elisabeth etait sterile ; et tous deux etaient avances en age.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 8, 'Or, le tour de sa classe etant venu de remplir devant Dieu les fonctions sacerdotales,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 9, 'il lui echut par le sort (qui etait le mode usite entre les pretres) d\'entrer dans le sanctuaire du Seigneur pour y offrir l\'encens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 10, 'Pendant cette heure de l\'encens, la foule du peuple restait au dehors et priait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 11, 'Or, a la droite de l\'autel de l\'encens, apparut a Zaccharie un ange du Seigneur, debout devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 12, 'A sa vue Zaccharie fut trouble, et la crainte le saisit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 13, 'Ne crains point, Zaccharie, lui dit l\'ange ; car ta priere a ete exaucee et ta femme Elisabeth t\'enfantera un fils ; tu lui donneras le nom de Jean.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 14, 'Il sera ta joie et ton allegresse, et beaucoup se rejouiront de sa naissance. Devant le Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 15, 'en effet, il sera grand ; il ne devra jamais boire ni vin, ni cervoise ; et, tout rempli de l\'Esprit saint des les entrailles de sa mere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 16, 'il ramenera au Seigneur leur Dieu, de nombreux enfants d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 17, 'Lui-meme, dans l\'esprit et la vertu d\'Elie, se mettra en marche devant le Seigneur, afin de faire revivre dans les enfants le coeur meme des aieux, de ramener les rebelles a la sagesse des justes, et de preparer au Seigneur un peuple bien dispose. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 18, 'A quoi reconnaitrai-je cela, dit a l\'ange Zaccharie, car je suis moi-meme un vieillard, et ma femme est avancee en age.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 19, 'L\'ange lui repondit par ces paroles : Je suis, Gabriel qui me tiens devant Dieu. Et j\'ai ete envoye pour te parler et t\'annoncer cette Bonne Nouvelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 20, 'Et a cause de ton manque de foi en mes paroles, qui s\'accompliront en leur temps, voila que tu seras muet et ne pourras parler jusqu\'au jour ou ces choses arriveront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 21, 'Le peuple cependant attendait Zaccharie, et s\'etonnait de le voir s\'attarder dans le sanctuaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 22, 'Lorsqu\'il sortit, il lui fut impossible de leur parler ; on comprit qu\'il avait eu une vision dans le sanctuaire. Lui-meme le leur faisait entendre par signes. Zaccharie resta sourd-muet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 23, 'Quand la periode de ses fonctions fut terminee, il retourna en sa maison ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 24, 'ce fut alors que sa femme Elisabeth devint enceinte. Pendant cinq mois elle vecut dans le secret de sa demeure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 25, 'Voila, disait-elle, ce qu\'a fait pour moi le Seigneur en ces jours ou il m\'a regardee pour mettre fin a mon opprobre parmi les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 26, 'Le sixieme mois, Dieu envoya l\'ange Gabriel dans une ville de Galilee, appelee Nazareth,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 27, 'vers une vierge, fiancee a un homme de la famille de David, nomme Joseph. Le nom de la vierge etait Marie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 28, 'L\'ange, etant entre ou elle etait, lui dit: Salut! une grace, t\'a ete faite ; le Seigneur est avec toi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 29, 'A ces paroles, Marie fut toute troublee ; et elle se demandait ce que pouvait etre une semblable salutation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 30, 'Ne crains point, Marie, reprit l\'ange, car tu as trouve grace pres de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 31, 'Sache que tu vas concevoir en ton sein et enfanter un fils auquel tu donneras le nom de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 32, 'Il sera grand ; on l\'appellera : Fils du Tres-Haut ; a lui le Seigneur Dieu donnera le trone de David son pere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 33, 'il regnera a jamais sur la maison de Jacob et son regne n\'aura point de fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 34, 'Alors Marie dit a l\'ange : Comment cela se fera-t-il, puisque je ne connais point, dhomme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 35, 'Et l\'ange lui repondit par ces paroles : L\'Esprit saint viendra sur toi, et une puissance du Tres-Haut t\'enveloppera de son ombre. C\'est pourquoi l\'Etre saint qui doit naitre sera appele Fils de Dieu !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 36, 'Sache egalement qu\'en sa vieillesse, ta parente Elisabeth, elle aussi, a concu un fils, et que celle que l\'on appelait sterile en est deja a son sixieme mois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 37, 'Rien n\'est impossible a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 38, 'Alors Marie dit : Voici la servante du Seigneur. Qu\'il me soit fait suivant ta parole. Et l\'ange la quitta.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 39, 'En ces jours-la, Marie se hata de partir, se dirigeant vers le pays des montagnes. Elle alla dans une ville de Juda.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 40, 'En entrant dans la maison de Zaccharie elle salua Elisabeth.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 41, 'Et des qu\'Elisabeth eut entendu la salutation de Marie, l\'enfant qu\'elle portait tressaillit, et elle-meme fut remplie d\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 42, 'Tu es benie entre les femmes, s\'ecria-t-elle en elevant la voix, et le fruit de tes entrailles est beni!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 43, 'D\'ou me vient que la mere de mon Seigneur vienne me visiter?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 44, 'Aussitot que ta voix, quand tu m\'as saluee, a frappe mon oreille, l\'enfant a tressailli de joie dans mon sein.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 45, 'Bienheureuse celle qui a cru a l\'accomplissement de ce qui lui a ete dit de la part du Seigneur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 46, 'Et Marie dit : Mon ame magnifie le Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 47, 'Et mon esprit est transporte de joie en Dieu mon Sauveur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 48, 'Parce qu\'il a abaisse son regard sur l\'humilite de sa servante. Voila que desormais toutes les generations m\'appelleront la bienheureuse,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 49, 'Parce que le Tout-Puissant m\'a fait de grandes choses! Son nom est saint,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 50, 'Sa misericorde se repand de generation en generation Sur ceux qui le craignent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 51, 'Il a deploye la force de son bras, Il a dissipe les pensees des coeurs des superbes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 52, 'Il a jete bas de leurs trones les puissants, Et il a exalte les humbles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 53, 'Il a comble de biens les affames, Et il a renvoye les riches a vide ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 54, 'Il a secouru Israel son serviteur, Il s\'est souvenu de sa misericorde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 55, 'Ainsi qu\'il l\'avait dit a nos peres, a Abraham et a sa posterite pour toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 56, 'Marie sejourna environ trois mois avec Elisabeth, et retourna ensuite en sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 57, 'Parvenue au terme de sa grossesse, Elisabeth enfanta un fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 58, 'Ayant appris que le Seigneur faisait ainsi eclater par elle sa misericorde, ses voisins et ses parents partagerent sa joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 59, 'Le huitieme jour, ils vinrent pour la circoncision de l\'enfant. Ils lui donnaient le nom de Zaccharie, celui de son pere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 60, 'quand sa mere prit la parole : Non, dit-elle, il se nommera Jean. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 61, 'Mais, lui dirent les autres, personne dans ta famille ne porte ce nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 62, 'Et l\'on demanda au pere, par signes, comment il voulait le nommer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 63, 'Alors, se faisant apporter des tablettes, Zaccharie ecrivit ceci : Jean est son nom. Tout le monde fut surpris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 64, 'Aussitot la banche de Zaccharie s\'ouvrit, sa langue se delia et il se mit a parler et a benir Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 65, 'Chacun dans le voisinage fut saisi de crainte et, dans la region des montagnes de Judee, il ne fut bruit que de toutes ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 66, 'Chacun en les apprenant les recueillait dans son coeur et disait : Que sera donc cet enfant? En effet, la main du Seigneur etait avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 67, 'Quant a Zaccharie, son pere, il fut rempli d\'Esprit saint et il prophetisa en ces termes :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 68, 'Beni soit le Seigneur, le Dieu d\'Israel! Car il a visite son peuple et opere sa redemption ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 69, 'Il nous a suscite un Sauveur puissant, De la maison de David, son serviteur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 70, '(Ainsi qu\'il l\'avait annonce par la bouche de ses saints prophetes qui jadis ont vecu.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 71, 'Il nous delivre de nos ennemis, Et de la main de tous ceux qui nous haissent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 72, 'Il montre sa misericorde envers nos peres ; Il se souvient de sa sainte alliance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 73, 'Du serment par lui jure a notre pere Abraham ; Il nous donne enfin a nous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 74, 'delivres de la main de nos ennemis, De le servir sans crainte, [de notre vie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 75, 'De marcher devant lui dans la saintete et la justice tous les jours');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 76, 'Et toi, enfant, tu seras appele Prophete du Tres-Haut ; Car tu iras devant le Seigneur ; Tu marcheras devant lui pour lui preparer les voies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 77, 'Tu donneras a son peuple la science du salut, Tu lui diras que ses peches lui sont remis ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 78, 'Que notre Dieu a des entrailles de misericorde, Qu\'il nous a regardes du haut des cieux, Que le soleil d\'Orient se leve ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 79, 'Qu\'il illuminera ceux qui sont assis dans les tenebres et l\'ombre de la mort ; Qu\'il dirigera nos pas dans un chemin de paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 1, 80, 'L\'enfant grandissait et son esprit se fortifiait, et jusqu\'au jour de sa manifestation a Israel, les deserts furent sa demeure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 1, 'Vers cette epoque, parut un edit de Cesar-Auguste ordonnant le recensement de tous les habitants de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 2, 'Ce recensement fut le premier, et Cyrenius etait alors legat imperial en Syrie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 3, 'Tout le monde allait se faire enregistrer ; et chacun en son lieu d\'origine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 4, 'Joseph (qui etait de la maison et de la famille de David) partit, lui aussi, de Nazareth, ville de la Galilee, pour monter au pays de Judee et se rendre a la cite de David, appelee Bethleem,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 5, 'afin d\'y etre inscrit avec Marie, sa fiancee, qui etait enceinte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 6, 'Or il advint, pendant qu\'ils etaient en cet endroit, que le moment d\'enfanter arriva ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 7, 'et elle mit au monde son fils premier-ne. Elle l\'emmaillota et le coucha dans une creche, parce qu\'il n\'y avait pas pour eux de place a l\'hotellerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 8, 'Il y avait, en ce meme lieu, des bergers qui vivaient dans les champs et gardaient leurs troupeaux durant les veilles de la nuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 9, 'Un ange du Seigneur leur apparut, et une gloire du Seigneur resplendit autour d\'eux ; et ils furent saisis d\'une grande crainte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 10, 'L\'ange leur dit : Ne vous effrayez point, car je vous apporte la Bonne Nouvelle d\'une grande joie, qui sera pour tout le peuple :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 11, 'Aujourd\'hui, en la cite de David, vous est ne un Sauveur ; c\'est le Christ, le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 12, 'Et, pour vous, voici quel sera le signe : vous trouverez un petit enfant emmaillote et couche dans une creche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 13, 'Soudain s\'unirent a l\'ange des multitudes appartenant a l\'armee du ciel. Et tous louaient Dieu et disaient :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 14, 'Gloire a Dieu au plus haut des cieux Et paix sur la terre aux hommes de bonne volonte');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 15, 'Quand les anges s\'eloignerent, remontant au ciel, les bergers se dirent l\'un a l\'autre : Allons jusqu\'a Bethleem, voyons ce qui, est arrive, ce que le Seigneur nous a fait connaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 16, 'Etant partis en toute hate, ils trouverent Marie, Joseph et le petit enfant couche dans la creche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 17, 'Quand ils l\'eurent vu, ils firent connaitre ce qui leur avait ete dit de cet enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 18, 'Chacun, en ecoutant les bergers, etait emerveille de ce qu\'ils racontaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 19, 'Quant a Marie, elle conservait toutes ces paroles et les repassait en son coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 20, 'Les bergers s\'en retournerent, glorifiant et louant Dieu de ce qu\'ils avaient entendu et vu, toutes choses en parfait accord avec ce qui leur avait ete dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 21, 'Lorsque huit jours apres, l\'enfant fut circoncis, on lui donna le nom de Jesus, celui que l\'ange lui avait donne avant qu\'il eut ete concu dans le sein maternel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 22, 'Quand furent acheves les jours que la Loi de Moise consacre a la purification, on porta l\'enfant a Jerusalem pour le presenter au Seigneur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 23, '(en execution de ce qui est ecrit dans sa Loi : Tout enfant male premier-ne sera dit consacre au Seigneur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 24, 'et pour offrir en sacrifice (selon la prescription de la Loi du Seigneur) soit une couple de tourterelles, soit deux jeunes colombes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 25, 'Or, a Jerusalem, il y avait alors un homme qui se nommait Simeon. Cet homme etait juste et pieux, il attendait la consolation d\'Israel, et sur lui reposait l\'Esprit saint ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 26, 'et cet Esprit saint lui avait revele qu\'il ne verrait point la mort avant qu\'il eut vu l\'Oint du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 27, 'Pousse par l\'Esprit, il vint au Temple, et comme les parents de l\'enfant Jesus l\'y apportaient afin de se conformer, en ce qui le concernait, a l\'usage legal,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 28, 'ce fut lui qui le recut dans ses bras, et il benit Dieu et dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 29, 'Maintenant, o mon Maitre, tu laisses ton serviteur s\'en aller, En paix, suivant ta parole ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 30, 'Car mes yeux ont vu ton salut ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 31, 'Salut que tu as prepare a la face de tous les peuples,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 32, 'Lumiere qui se revelera aux nations Et gloire de ton peuple d\'Israel !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 33, 'Le pere et la mere de l\'enfant s\'emerveillaient de ce que l\'on disait de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 34, 'Simeon les benit aussi et dit a Marie, sa mere : Sache que cet enfant est au monde pour etre la chute et le relevement de plusieurs en Israel et pour etre un signal de contradiction,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 35, 'et ton ame, a toi-meme, sera transpercee par un glaive ; c\'est ainsi que paraitront au jour les sentiments de bien des coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 36, 'Il y avait la egalement une prophetesse, Anne, fille de Phanuel, de la tribu d\'Aser. Elle etait toute chargee d\'annees. Apres avoir, depuis sa virginite, vecu sept ans avec son mari, elle etait devenue veuve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 37, 'Agee alors de quatre-vingt-quatre ans, elle ne quittait point le Temple, servant nuit et jour le Seigneur dans les jeunes et dans les prieres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 38, 'Elle aussi, survenant en cette meme heure, rendit gloire a Dieu et parla de l\'enfant a tous ceux qui attendaient la redemption de Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 39, 'Quand furent accomplies toutes les prescriptions de la Loi du Seigneur, ils rentrerent en Galilee, dans leur ville de Nazareth.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 40, 'L\'enfant grandissait et se fortifiait ; il etait plein de sagesse et la grace de Dieu etait sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 41, 'Chaque annee, a la fete de Paque, ses parents allaient a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 42, 'Ils y monterent pour la fete, comme de coutume, lorsqu\'il eut atteint l\'age de douze ans.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 43, 'Les jours consacres a la solennite etant passes, ils s\'en revinrent. Or l\'enfant Jesus etait reste a Jerusalem. Ses parents ne s\'en apercurent point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 44, 'Supposant qu\'il etait avec leurs compagnons de route, ils firent une journee de marche, le cherchant parmi ceux de leur parente et parmi leurs connaissances.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 45, 'Ne l\'ayant point trouve, ils retournerent a Jerusalem, le cherchant toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 46, 'Ce fut au bout de trois jours qu\'ils le trouverent, dans le Temple, assis au milieu des docteurs, les ecoutant et les interrogeant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 47, 'Tous ceux qui l\'entendaient etaient confondus de son intelligence et de ses reponses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 48, 'A sa vue, ses parents furent tres surpris, et sa mere lui dit : Mon enfant, pourquoi as-tu agi de la sorte a notre egard? Voila que ton pere et moi nous te cherchions dans une grande angoisse. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 49, 'Pourquoi me cherchiez? vous? leur repondit-il. Ne saviez-vous pas qu\'il faut que je sois aux affaires de mon Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 50, 'Ses parents ne comprirent pas cette parole qu\'il leur adressa.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 51, 'Descendant avec eux, il retourna a Nazareth ; il leur etait soumis ; sa mere conservait toutes ces choses en son coeur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 2, 52, 'et Jesus progressait en sagesse, en stature et en grace, devant Dieu et devant les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 1, 'En l\'an quinze du regne de Tibere Cesar,  Ponce-Pilate etant procurateur de la Judee,  Herode, tetrarque de la Galilee, Philippe, son frere, tetrarque de l\'Ituree et des terres de la Trachonite,  Lysanias, tetrarque de l\'Abilene, ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 2, 'sous le pontificat de Banne et de Kaiphe, la parole de Dieu fut adressee a Jean, fils de Zaccharie, dans le desert.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 3, 'Et Jean s\'en alla dans toute la region du Jourdain, prechant un bapteme de repentance pour la remission des peches,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 4, 'ainsi que cela est ecrit au livre des paroles du prophete Esaie : Une voix crie dans le desert : Preparez la voie du Seigneur, Aplanissez ses sentiers ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 5, 'Toute vallee sera comblee ; Toute montagne et toute colline seront abaissees ; Les voies tortueuses seront changees en droits chemins ; Et les voies rocailleuses en routes unies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 6, 'Et toute chair verra le salut de Dieu');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 7, 'Aux foules qui accouraient pour etre baptisees par lui, il disait : Engeance de viperes, qui vous a appris a fuir la colere a venir?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 8, 'Produisez donc des fruits dignes de la repentance, et n\'allez pas dire en vous-memes : Nous avons pour pere Abraham ; car je vous le dis, Dieu peut faire surgir, des pierres que voici, des enfants a Abraham.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 9, 'Deja la cognee touche la racine des arbres. Tout arbre donc qui ne produit pas de bon fruit, sera coupe et jete au feu. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 10, 'Qu\'avons-nous a faire? lui demanderent alors les multitudes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 11, 'Il leur repondit par ces paroles : Que celui qui possede deux tuniques en donne une a celui qui n\'en a pas et que celui qui a des aliments pour se nourrir fasse de meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 12, 'Des publicains vinrent pour etre baptises et lui dirent aussi : Maitre, qu\'avons-nous a faire? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 13, 'N\'exigez rien de plus, leur repondit-il, que ce qui vous a ete prescrit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 14, 'Des soldats aussi le questionnerent : Et nous, qu\'avons-nous a faire? A eux il dit : Ne faites violence a personne ; ne faites tort a personne ; contentez-vous de votre paye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 15, 'Le peuple etait en suspens ; et chacun se demandait, dans le secret de son coeur, si Jean n\'etait pas peut-etre le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 16, 'Pour repondre a tous, Jean dit : Moi, je vous baptise d\'eau ; mais il va venir celui qui est plus puissant que moi ; je ne suis pas meme digne de delier la courroie de ses sandales ; lui, vous baptisera avec l\'Esprit saint et le feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 17, 'Il a le van dans la main pour nettoyer son aire et rassembler le froment dans son grenier ; mais la paille il la brulera au feu inextinguible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 18, 'C\'est ainsi qu\'il annoncait la Bonne Nouvelle au peuples par des exhortations nombreuses et variees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 19, 'Le tetrarque Herode, cependant, ayant ete repris par lui tant au sujet d\'Herodiade, femme de son frere, qu\'au sujet des mille crimes qu\'il avait commis,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 20, 'ajouta encore celui-ci a tous les autres: il fit jeter Jean en prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 21, 'Or tout le peuple ayant recu le bapteme, et Jesus aussi ayant ete baptise, il advint que le ciel s\'ouvrit, pendant qu\'il priait,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 22, 'et que l\'Esprit saint descendit sur lui sous une forme corporelle, sous la figure d\'une colombe ; et une voix vint du ciel : Tu es mon Fils bien-aime, en toi je me complais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 23, 'Jesus, en ce commencement, avait environ trente ans et passait pour etre le fils de Joseph, Fils d\'Heli,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 24, 'Fils de Maththath, Fils de Levi, Fils de Melchi, Fils de Jannai, Fils de Joseph,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 25, 'Fils de Mattathias, Fils d\'Amos, Fils de Naoum, Fils d\'Eslei, Fils de Nangai,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 26, 'Fils de Maath, Fils de Mattathias, Fils de Semeein, Fils de Josech, Fils de Joda,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 27, 'Fils de Joanan, Fils de Resa, Fils de Zorobabel, Fils de Salathiel, Fils de Nerei,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 28, 'Fils de Melchei, Fils d\'Addei, Fils de Kosam, Fils de Elmadam, Fils de Her,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 29, 'Fils de Jesus, Fils d\'Eliezer, Fils de Jorem, Fils de Maththath, Fils de Levi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 30, 'Fils de Simeon, Fils de Judas, Fils de Joseph, Fils de Jonam, Fils d\'Eliakim, Fils de Melea,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 31, 'Fils de Menna, Fils de Mattatha, Fils de Natham, Fils de David,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 32, 'Fils de Jessai, Fils de Jobed, Fils de Boos, Fils de Sala, Fils de Naasson,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 33, 'Fils d\'Aminadab, Fils d\'Admein, Fils d\'Ami, Fils d\'Esrom, Fils de Phares, Fils de Judas,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 34, 'Fils de Jacob, Fils d\'Isaac, Fils d\'Abraham, Fils de Unira, Fils de Nachor, Fils de Serouch,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 35, 'Fils de Ragau, Fils de Phalek, Fils de Eber, Fils de Sala,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 36, 'Fils de Kainam, Fils d\'Arphaxad, Fils de Sein, Fils de Noe, Fils de Lamech,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 37, 'Fils de Mathousalem. Fils d\'Enoch, Fils de Jaret, Fils de Meleleel, Fils de Kainam');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 3, 38, 'Fils d\'Enos, Fils de Seth, Fils d\'Adam, Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 1, 'Jesus, rempli de l\'Esprit saint, revint des bords du Jourdain ; l\'Esprit le poussait au desert.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 2, 'Il y passa quarante jours ; il etait tente par le diable. Durant ces quarante jours, il ne mangea rien, et quand ils furent acheves, il eut faim.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 3, 'Le diable lui dit : Si tu es Fils de Dieu, dis a cette pierre de se changer en pain ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 4, 'et, Jesus lui repondit : Il est ecrit : Ce n\'est pas de pain seulement que vivra l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 5, 'Puis le diable l\'emmena, et lui montrant en un instant tous les royaumes de la terre :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 6, 'Je te donnerai, lui dit-il, toute cette puissance et la gloire de ces royaumes, car elfe m\'a ete livree, et je la donne a qui je veux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 7, 'Donc, prosterne-toi en adoration devant moi et tout cela est a toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 8, 'Et Jesus lui fit cette reponse : Il est ecrit : Tu adoreras le Seigneur ton Dieu et c\'est a lui seul que tu rendras un culte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 9, 'Le menant a Jerusalem, le diable le placa sur le faite du Temple et lui dit : Si tu es Fils de Dieu, jette-toi d\'ici en bas,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 10, 'car il est ecrit : A ses anges il donnera l\'ordre de te garder');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 11, 'Et de te soutenir de leurs mains, De peur que ton pied ne se heurte contre une pierre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 12, 'Jesus lui repondit par ces mots : Il est dit : Tu ne tenteras point le Seigneur ton Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 13, 'Toute espece de tentation etant achevee, te diable s\'eloigna de lai, jusqu\'a une autre occasion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 14, 'Puissant par l\'Esprit, Jesus retourna en Galilee ; sa renommee se repandit dans toute la contree ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 15, 'il enseignait dans les synagogues du pays et tous celebraient ses louanges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 16, 'Etant alle a Nazareth, ou il avait ete eleve, il entra dans la synagogue le jour du sabbat, selon sa coutume, et il se leva pour faire la lecture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 17, 'On lui presenta le livre du prophete Esaie. Il l\'ouvrit et trouva un passage ou il etait ecrit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 18, 'L\'Esprit du Seigneur est sur moi ; m\'a consacre par son onction pour annoncer l\'Evangile aux pauvres, Il m\'a envoye pour annoncer aux captifs leur delivrance, Aux aveugles qu\'ils vont recevoir la vue, Pour renvoyer les opprimes affranchis,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 19, 'Pour annoncer une annee de faveur de la part du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 20, 'Il ferma le livre, le rendit au servant et s\'assit. Tout le monde, dans la synagogue, avait les yeux fixes sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 21, 'Il commenca ainsi : Aujourd\'hui s\'accomplit ce passage de l\'Ecriture que vous venez d\'entendre...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 22, 'Tous lui rendaient temoignage et etaient ravis des paroles de grace qui sortaient de sa bouche. N\'est-ce pas la, disait-on, le fils de Joseph? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 23, 'Vous irez jusqu\'a m\'appliquer ce proverbe, reprit-il : Medecin, gueris-toi toi-meme. Tout ce que tu as fait a Capharnaum et dont nous avons entendu parler, fais-le donc egalement ici meme dans ta patrie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 24, 'Puis il ajouta : Je vous le dis en verite, nul prophete n\'est accepte dans sa patrie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 25, 'Il y avait, aux jours d\'Elie, ce que je vous dis la est la verite, beaucoup de veuves en Israel, lorsque le ciel fut ferme pendant trois ans et demi, et qu\'il y eut une grande famine sur toute la terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 26, 'et cependant Elie ne fut envoye a aucune d\'elles, mais bien a une veuve de Sarepta, dans le pays de Sidon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 27, 'Il y avait aussi, en Israel, beaucoup de lepreux au temps du prophete Elisee, et cependant aucun d\'eux ne fut gueri. Celui qui fut gueri ce fut un Syrien : Naaman.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 28, 'Ces paroles remplirent de colere tous ceux qui etaient dans la synagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 29, 'Ils se leverent et ils le chasserent hors de la cite ; puis ils le menerent jusqu\'au sommet de la montagne sur laquelle leur ville avait ete batie, pour le jeter en bas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 30, 'Mais lui, passant au, milieu d\'eux, s\'en alla');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 31, 'et descendit a Capharnaum, ville de Galilee. Il y enseignait aux jours de sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 32, 'Et ils etaient extremement frappes de son enseignement, car sa parole etait pleine d\'autorite');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 33, 'Dans la synagogue il y avait un homme possede de l\'Esprit d\'un demon impur. Il se mit a jeter de grands cris :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 34, 'Ah! qu\'y a-t-il entre nous et toi, Jesus de Nazareth? Tu es venu pour nous perdre ; je sais qui tu es : le Saint de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 35, 'Jesus alors le menaca : Tais-toi, dit-il, sors de cet homme! Le renversant par terre au milieu de l\'assemblee, le demon sortit de lui sans lui faire aucun mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 36, 'Tous furent terrifies, et ils se parlaient les uns aux autres : Qu\'est-ce donc, disaient-ils, que cette parole? Il commande avec autorite et puissance aux Esprits impurs, et ils sortent !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 37, 'Dans tout le pays environnant il n\'etait bruit que de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 38, 'En sortant de la synagogue, il entra dans la maison de Simon. Or, la belle-mere de Simon etait malade d\'une forte fievre. On implora Jesus en sa faveur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 39, 'Il se pencha vers elle, commanda a la fievre, et la fievre disparut. Immediatement cette femme se leva et se mit a les servir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 40, 'Apres le coucher du soleil, tous ceux qui avaient des malades, quels que fussent leurs maux, les lui amenerent. Imposant les mains a chacun d\'eux, Jesus les guerit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 41, 'Des demons sortaient aussi d\'un grand nombre, en s\'ecriant : Tu es le Fils de Dieu! Mais il les menacait et leur defendait de parler ainsi ; car ils savaient qu\'il etait le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 42, 'Lorsqu\'il fit jour, il sortit et s\'en alla dans un lieu solitaire. Les multitudes se mirent a sa recherche ; elles arriverent jusqu\'a, lui et elles le retenaient, ne voulant point qu\'il les quittat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 43, 'Mais il leur dit : Il me faut annoncer dans d\'autres villes aussi l\'Evangile du Royaume de Dieu, car c\'est pour cela que j\'ai ete envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 4, 44, 'Et il prechait dans les synagogues de la Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 1, 'Un jour, qu\'il se tenait sur le rivage du lac de Gennezareth, la foule se pressait autour de lui pour entendre la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 2, 'Il vit deux barques qui stationnaient pres du bord ; les pecheurs en etaient descendus et lavaient leurs filets.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 3, 'Montant alors dans l\'une de ces barques, il pria Simon, a qui elle appartenait, de l\'eloigner un peu de terre. Puis il s\'assit ; et de la barque, il instruisait les multitudes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 4, 'Lorsqu\'il eut fini de parler, il dit a Simon : Pousse au large, et vous lancerez vos filets pour la peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 5, 'Simon lui fit cette reponse: Maitre nous avons travaille toute la nuit sans rien prendre ; toutefois, sur ta parole je jetterai le filet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 6, 'Ce qu\'ils firent ; et ils ramenerent une telle quantite de poissons que les filets se dechiraient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 7, 'Alors ils appelerent a leur aide ceux de leurs camarades qui etaient dans l\'autre barque ; ceux-ci arriverent et les deux barques furent remplies au point qu\'elles s\'enfoncaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 8, 'A cette vue, Simon Pierre tomba aux genoux de Jesus disant : Eloigne toi de moi, Seigneur, car je suis un homme pecheur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 9, 'Un sentiment d\'effroi l\'avait, en effet, saisi, ainsi que tous ses compagnons, en presence de cette peche et des poissons qu\'ils venaient de prendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 10, 'Il en etait de meme de Jacques et de Jean, fils de Zebedee, qui etaient les camarades de Simon. Ne crains point, dit Jesus a Simon, desormais tu seras pecheur d\'hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 11, 'La-dessus, ramenant les barques a terre, ils quitterent tout et le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 12, 'Une autre fois, comme il etait dans une ville, survint un homme couvert de lepre. Voyant Jesus, il se prosterna, tout suppliant, la face contre terre, disant : Seigneur, si tu le veux, tu peux me guerir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 13, 'Jesus etendit la main, le toucha et dit : Je le veux, sois, guerit. La lepre disparut a l\'instant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 14, 'Jesus lui recommanda de n\'en parler a personne : Retire-toi, lui dit-il, va te montrer au pretre, et offre pour ta guerison ce que Moise a prescrit ; que ce leur soit un temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 15, 'Sa renommee se repandait de plus en plus ; d\'immenses multitudes accouraient pour l\'entendre et pour se faire guerir de leurs maladies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 16, 'Quant a lui, il se tenait retire dans les lieux solitaires et il y priait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 17, 'Voici ce qui arriva, un des jours ou il etait a enseigner, ayant assis devant lui des Pharisiens et des Docteurs de la Loi venus des bourgs de Galilee et de Judee et de la ville de Jerusalem : La puissance du Seigneur etait la pour guerir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 18, 'lorsque survinrent des gens portant sur un lit un homme qui etait paralyse. Ils chercherent a le faire entrer et a le placer devant Jesus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 19, 'et ne trouvant pas moyen de penetrer a cause de la foule, ils monterent sur la maison et descendirent le malade avec son petit lit par la toiture, au milieu de l\'assemblee et devant Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 20, 'Voyant leur foi, celui-ci dit a l\'homme : Tes peches te sont pardonnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 21, 'Alors les Scribes et les Pharisiens se mirent a faire leurs reflexions : Qui donc, disaient-ils, est celui-ci qui profere des blasphemes? Qui peut pardonner des peches que Dieu seul?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 22, 'Connaissant leurs raisonnements, Jesus leur adressa ces paroles : Pourquoi ces pensees dans vos coeurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 23, 'Lequel est le plus facile, de dire : tes peches te sont pardonnes, ou de dire : leve-toi et marche?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 24, 'Or, afin que vous sachiez que le Fils de l\'homme a sur ta terre le pouvoir de pardonner des peches... s\'adressant au paralytique : A toi, je dis : Leve-toi, emporte ton petit lit et, va dans ta maison !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 25, 'Aussitot, se levant devant eux, il emporta le petit lit sur lequel il etait couche et retourna en sa maison glorifiant Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 26, 'Transportes d\'enthousiasme, tous se mirent aussi a glorifier Dieu et, tout effrayes, ils disaient : Quelle incroyable chose nous avons vue aujourd\'hui!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 27, 'Apres cela, il sortit, et apercut, assis au bureau du peage, un publicain du nom de Levi. Il lui dit : Suis-moi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 28, 'et lui, abandonnant tout, se leva et le suivit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 29, 'Levi lui donna un grand festin en sa maison. Se trouvaient a table, entre autres convives, un grand nombre de publicains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 30, 'Cela fit murmurer les Pharisiens et leurs Scribes : Pourquoi, dirent-ils aux disciples, mangez-vous et buvez-vous avec des publicains et avec des pecheurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 31, 'Jesus alors leur adressa ces paroles : Ce ne sont pas ceux qui sont en bonne sante qui ont besoin de medecin, ce sont les malades.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 32, 'Je ne suis pas venu appeler a la repentance des justes, mais des pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 33, 'Ils lui dirent : Les disciples de Jean se livrent frequemment aux jeunes et aux prieres ; ceux des Pharisiens font de meme ; et les tiens mangent et boivent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 34, 'Jesus leur repondit : Pouvez-vous faire jeuner les amis de l\'epoux pendant que l\'epoux est avec eux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 35, 'Des jours viendront ou l\'epoux leur sera enleve, ils jeuneront dans ces jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 36, 'Il leur dit aussi une parabole : Personne, dans un vetement neuf ne dechire un morceau pour le coudre a un vetement vieux ; autrement il aura dechire le vetement neuf et aura mis au vieux vetement un morceau neuf qui ne s\'accorde pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 37, 'Personne non plus ne verse du vin nouveau dans de vieilles outres ; autrement le vin nouveau fera eclater les outres ; il se repandra et les outres seront perdues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 38, 'Mais on doit verser le vin nouveau dans des outres, neuves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 5, 39, 'Quand on vient de boire du vin vieux, on n\'en demande jamais du nouveau : Le vieux, dit-on, c\'est le bon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 1, 'Il arriva  un jour de sabbat  que Jesus passa par des bles ; ses disciples cueillaient les epis et, les froissant dans leurs mains, en mangeaient les grains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 2, 'Pourquoi faites-vous, dirent quelques-uns des Pharisiens, ce qu\'au jour du sabbat il n\'est pas permis de faire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 3, 'Jesus leur repondit par ces paroles : N\'avez-vous pas lu ce que fit David quand il eut faim et ce que firent ses compagnons?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 4, 'Comment il entra dans la maison de Dieu, prit les pains de proposition, en mangea et en donna a ceux qui etaient avec lui, quand il n\'est permis qu\'aux pretres seuls d\'en manger ? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 5, 'Le Fils de l\'homme, ajouta-t-il, est maitre aussi du sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 6, 'Un autre jour de sabbat, il entra dans la synagogue et il y enseigna. Il s\'y trouvait un homme dont la main droite etait paralysee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 7, 'Les Scribes et les Pharisiens l\'observaient pour voir si, un jour de sabbat, il le guerirait, et pour tirer de la quelque pretexte a accusation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 8, 'Quant a lui, il connaissait leurs pensees. Leve-toi, dit-il, a l\'homme dont la main etait paralysee, et tiens-toi debout au milieu de nous tous! L\'homme se leva et se tint debout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 9, 'Je vous le demande, leur dit alors Jesus, est-il permis aux jours de sabbat de faire le bien ou de faire le mal? de sauver une vie ou de la perdre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 10, 'Ayant sur eux tous promene son regard, il dit a l\'homme : Etends ta main.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 11, 'Il le fit et sa main fut guerie. Quant a eux, ils etaient fous de rage, et ils parlaient entre eux de ce qu\'ils pourraient bien faire a Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 12, 'En ces jours-la, il se retira sur la montagne pour prier et passa la nuit a prier Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 13, 'Quand il fit jour, il convoqua ses disciples ; et il choisit douze d\'entre eux, auxquels il donna le nom d\'apotres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 14, 'ce furent Simon, qu\'il nomma Pierre, et Andre son frere ;  Jacques et Jean ;  Philippe et Barthelemi ; ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 15, 'Matthieu et Thomas ;  Jacques, fils d\'Alphee, et Simon (celui qu\'on appelle le zelateur) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 16, 'Judas, frere de Jacques, et Judas l\'Iskariote, qui fut un traitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 17, 'Descendant de la montagne, avec eux, il s\'arreta sur un terrain uni. La se trouvait, avec une foule considerable de ses disciples, une multitude immense de peuple, venue pour l\'entendre et se faire guerir de ses maux, de toute la Judee, de Jerusalem et du littoral de Tyr et de Sidon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 18, 'Ceux qui etaient tourmentes par des Esprits impurs en etaient delivres');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 19, 'et chacun dans la foule cherchait a le toucher, parce qu\'il sortait de lui une vertu qui les guerissait tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 20, 'Jesus, levant les yeux sur ses disciples, dit: Heureux etes-vous, o pauvres, parce qu\'a vous appartient le Royaume de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 21, 'Heureux, o vous qui avez faim maintenant, parce que vous serez rassasies! Heureux, o vous qui pleurez maintenant, parce que vous rirez!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 22, 'Heureux serez-vous lorsque les hommes vous hairont, vous reprouveront, vous outrageront, proscriront votre nom comme maudit a cause du Fils de lhomme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 23, 'En ce jour-la rejouissez-vous et sautez de joie! parce que votre recompense sera grande dans le ciel ; car c\'est de meme facon que leurs peres traitaient les prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 24, 'Malheur a vous cependant, riches, parce que vous avez aujourd\'hui votre consolation!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 25, 'Malheur a vous qui maintenant avez tout a satiete, parce que vous aurez faim ! Malheur a vous qui maintenant riez, parce que vous tomberez dans le deuil et les larmes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 26, 'Malheur! lorsque tous les hommes diront du bien de vous, car c\'est de meme facon que leurs peres traitaient les faux prophetes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 27, 'Quant a vous qui m\'ecoutez, voici ce que je vous dis : Aimez vos ennemis ; faites du bien a ceux qui vous haissent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 28, 'benissez ceux qui vous maudissent ; priez pour ceux qui vous font injure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 29, 'Si quelqu\'un te frappe sur une joue, presente-lui aussi l\'autre ; et contre celui qui t\'enleve ton manteau ne defends pas meme ta tunique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 30, 'A qui te demande, donne ; et a qui s\'empare de ce qui est a toi, ne reclame rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 31, 'Comme vous voulez que les hommes agissent envers vous, agissez vous-memes pareillement envers eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 32, 'Si vous aimez ceux qui vous aiment, quel gre vous en saura-t-on? les pecheurs aussi aiment ceux qui les aiment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 33, 'Et si vous faites du bien a ceux qui vous en font a vous-memes, quel gre vous en saura-t-on? Les pecheurs le font aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 34, 'Et si vous pretez a ceux dont vous esperez recevoir quelque chose, quel gre vous en saura-t-on? Les pecheurs aussi font des prets aux pecheurs pour recevoir un jour le meme service.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 35, 'Vous cependant aimez vos ennemis ; faites du bien et pretez sans esperer quoi que ce soit. Et votre recompense sera grande et vous serez fils du Tres-Haut, parce qu\'il est bon pour les ingrats et pour les mechants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 36, 'Soyez misericordieux, comme votre Pere est misericordieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 37, 'Ne jugez point et vous ne serez point juges ; ne condamnez point et vous ne serez point condamnes. Pardonnez, et il vous sera pardonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 38, 'Donnez, et il vous sera donne ; il sera verse dans votre sein une bonne mesure, pressee, tassee, debordante, car la mesure que vous faites aux autres sera votre mesure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 39, 'Il leur dit aussi une parabole : Un aveugle peut-il conduire un aveugle? Ne tomberont-ils pas tous deux dans une fosse?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 40, 'Le disciple n\'est pas au-dessus du maitre ; il sera parfait quand il sera pareil a son maitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 41, 'Pourquoi regardes-tu la paille qui est dans l\'oeil de ton frere? Et la poutre qui est dans ton oeil a toi, tu ne la remarques pas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 42, 'Comment peux-tu dire a ton frere : Mon frere, laisse-moi oter cette paille qui est dans ton oeil, ne voyant pas toi-meme la poutre qui est dans le tien? Hypocrite, ote d\'abord de ton oeil la poutre et alors tu verras a oter la paille de l\'oeil de ton frere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 43, 'Un arbre qui produit de mauvais fruits n\'est pas bon ; a l\'inverse, un arbre qui produit de bons fruits n\'est pas mauvais ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 44, 'car c\'est par son fruit que chaque arbre se fait connaitre. Sur des epines on ne cueille pas de figues ; sur des ronces on ne vendange pas de raisin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 45, 'Du bon tresor de son coeur l\'homme bon tire le bien ; de son mauvais tresor l\'homme mauvais tire le mal ; car c\'est de l\'abondance du coeur que la bouche parle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 46, 'Pourquoi m\'appelez-vous : Seigneur ! Seigneur ! et ne faites-vous pas ce que je dis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 47, 'Quiconque vient a moi, entendant mes paroles et les mettant en pratique, je vais vous montrer a qui il ressemble.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 48, 'Il ressemble a un homme qui, en construisant une maison, a creuse tres avant et a etabli ses fondations sur le roc. L\'inondation est survenue ; le torrent a fait irruption sur cette maison et il n\'a pas eu la force de l\'ebranler, parce qu\'elle etait bien construite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 6, 49, 'Quant a celui qui entend et ne met pas en pratique, il ressemble a un homme qui a bati sa maison sur la terre sans fondations. Sur elle le torrent a fait irruption ; soudain elle s\'est ecroulee ; et la ruine de cette maison a ete grande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 1, 'Quand il eut acheve tous ces discours adresses au peuple, Jesus entra dans Capharnaum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 2, 'Or un centurion avait, malade et pres de mourir, un serviteur qui lui etait tres cher.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 3, 'Ayant entendu parler de Jesus, il lui envoya quelques Anciens d\'entre les Juifs, pour lui demander de venir et de guerir son serviteur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 4, 'Ceux-ci, ayant aborde Jesus, le prierent avec de grandes instances: Il merite, lui dirent-ils, que tu lui accordes cela,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 5, 'car il aime notre nation et c\'est lui qui nous a bati la synagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 6, 'Jesus etait parti avec eux ; comme il n\'etait plus qu\'a une o petite distance de la maison, le centurion envoya de ses amis lui dire : Seigneur, ne prends pas tant de peine, car je ne suis pas digne que tu entres sous mon toit,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 7, 'et c\'est pour cela que je ne me suis pas moi-meme juge digne d\'aller a toi. Mais donne un ordre et mon serviteur sera gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 8, 'Car moi (qui ne suis pourtant qu\'un subordonne) j\'ai sous mes ordres des soldats, et si je dis a l\'un : Va-t\'en, il s\'en va ; a l\'autre : Viens vient ; a mon, esclave : Fais cela, il le fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 9, 'Jesus fut dans l\'admiration de ce langage ; et se tournant vers la foule qui le suivait : Je vous le declare, dit-il, meme en Israel, je n\'ai pas trouve une foi aussi grande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 10, 'De retour a la maison, les envoyes du centurion trouverent le serviteur en pleine sante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 11, 'Le lendemain il se dirigea vers une ville appelee Nain ; avec lui allaient un grand nombre de ses disciples et une foule considerable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 12, 'Comme il arrivait a la porte de la cite, il se trouva que l\'on portait en terre un mort ; c\'etait un fils unique dont la mere etait veuve. Une grande quantite d\'habitants de la ville etaient avec elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 13, 'En la voyant, le Seigneur fut touche de compassions envers elle et lui dit : Ne pleure point!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 14, 'Puis il s\'approcha et toucha le cercueil. Les porteurs s\'arreterent, et il dit : Jeune homme, je te l\'ordonne, leve-toi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 15, 'Et le mort se dressa sur son seant et se mit a parler. Jesus le rendit a sa mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 16, 'La crainte les saisit tous et ils glorifiaient Dieu : Un grand prophete, disaient-ils, a surgi parmi nous, et Dieu a visite son peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 17, 'Le bruit de ce qui s\'etait passe se repandit partout en Judee et dans tout le pays environnant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 18, 'Les disciples de Jean ayant rapporte tout cela a leur maitre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 19, 'celui-ci en designa deux, et les envoya dire au Seigneur : Es-tu celui qui doit venir, ou devons-nous en attendre un autre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 20, 'S\'etant donc presentes a Jesus, ces hommes lui dirent : Jean-Baptiste nous a adresses a toi pour te dire : Es-tu celui qui doit venir ou devons-nous en attendre un autre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 21, 'Or, en cet instant meme, Jesus guerissait nombre de malades, d\'infirmes, de possedes ; a beaucoup d\'aveugles il faisait don de la vue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 22, 'Alors il fit aux messagers celte reponse : Allez annoncer a Jean ce que vous avez vu et entendu : des aveugles voient, des estropies marchent, des lepreux sont gueris, des sourds entendent, des morts ressuscitent, des pauvres entendent l\'Evangiles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 23, 'Heureux est celui pour qui je ne serai pas une occasion de chute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 24, 'Quand les messagers furent partis, Jesus se mit a parler de Jean aux multitudes : Au desert, qu\'etes-vous alles voir? Est-ce un roseau agite par le vent?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 25, 'Qu\'etes-vous alles voir? Est-ce un homme aux vetements effemines? Mais ceux qui se revetent de riches habits et qui vivent au milieu des delices vivent dans les palais des rois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 26, 'Qu\'etes-vous donc alles voir? Est-ce un prophete? Oui, vous dis-je, et plus qu\'un prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 27, 'C\'est celui dont il est ecrit: Voici que j\'envoie mon messager pour te preceder Et preparer ton chemin devant toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 28, 'Je vous le declare, parmi ceux que les femmes ont enfantes, nul n\'est plus grand que Jean. Mais le plus petit dans le Royaume de Dieu est plus grand que lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 29, 'En se faisant baptiser du bapteme de Jean, tout le peuple qui l\'ecoutait, meme les publicains ont reconnu la justice de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 30, 'mais en ne se faisant pas baptiser par lui, les Pharisiens et les legistes ont rejete le dessein de Dieu a leur egard.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 31, 'A qui donc assimilerai-je les hommes de cette generation-ci? et a qui ressemblent-ils?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 32, 'Ils ressemblent a des enfants assis sur la place publique qui se crient les uns aux autres : Nous avons joue de la flute Et vous n\'avez pas danse ; Nous avons entonne des chants lugubres Et vous n\'avez pas pleure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 33, 'Et, en effet, Jean-Baptiste est venu s\'abstenant de manger du pain et de boire du vin ; et vous dites : Il est possede d\'un demon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 34, 'Le Fils de l\'homme est venu mangeant et buvant comme tout le monde, et vous dites : Voila un amateur de bonne chere, un buveur de vin, un ami des publicains et des pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 35, 'A la sagesse, justice est rendue par tous ses enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 36, 'Un des Pharisiens le pria de venir manger chez lui. Jesus, etant donc entre dans la maison du Pharisien, se mit a table.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 37, 'Or, une femme de la ville qui vivait dans le peche, ayant appris qu\'il etait a table dans la maison de ce Pharisien, apporta un vase d\'albatre rempli de parfum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 38, 'Elle se placa derriere lui, en pleurant, et se tint a ses pieds, qu\'elle arrosa d\'abord de ses larmes et qu\'elle essuya ensuite de ses cheveux, a ses pieds qu\'elle baisa longtemps et qu\'elle oignit de son parfum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 39, 'Voyant cela, le Pharisien qui l\'avait invite pensait en lui-meme: Si cet homme etait prophete, il saurait qui est cette femme qui le touche ; et ce qu\'elle est : une pecheresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 40, 'Jesus alors s\'adressant a lui : Simon, j\'ai quelque chose a te dire.  Parle, maitre, repondit Simon. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 41, 'Un certain creancier avait deux debiteurs ; le premier lui devait cinq cents deniers ; et le second cinquante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 42, 'Ni l\'un ni l\'autre n\'ayant de quoi le payer, il fit a chacun d\'eux remise de sa dette. Lequel des deux l\'aimera le plus?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 43, 'Simon fit cette reponse : Celui, je suppose, auquel il a remis davantage.  Tu viens de juger en toute droiture, reprit Jesus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 44, 'puis, se tournant vers la femme et parlant toujours a Simon: Tu vois cette femme? Quand je suis entre dans ta maison tu ne m\'as pas apporte d\'eau pour mes pieds, mais elle, c\'est avec ses larmes qu\'elle les a arroses, c\'est avec ses cheveux qu\'elle les a essuyes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 45, 'Tu ne m\'as pas donne de baiser ; mais elle, depuis l\'instant ou elle est entree n\'a cesse d\'embrasser mes pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 46, 'Sur ma tete, tu n\'as pas fait d\'onction d\'huile ; mais elle, ce sont mes pieds qu\'elle a oints de parfum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 47, 'A cause de cela, je te le declare, ses nombreux peches lui sont pardonnes, parce qu\'elle a beaucoup aime. Celui a qui on pardonne peu, aime peu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 48, 'Puis il lui dit a elle : Tes peches te sont pardonnes. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 49, 'Qui est cet homme qui va jusqu\'a pardonner des peches? se demanderent entre eux les convives.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 7, 50, 'Lui, cependant, dit a la femme : Ta foi t\'a sauvee ; va en paix!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 1, 'Ensuite, il alla de ville en ville et de village en village prechant, et annoncant l\'Evangile du Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 2, 'Avec lui etaient les douze et quelques femmes qui avaient ete delivrees de mauvais Esprits, ou gueries de maladies : Marie, celle qu\'on appelle Magdelaine, de laquelle etaient sortis sept demons ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 3, 'Jeanne, femme de Chouza, intendant d\'Herode ; Suzanne, et beaucoup d\'autres qui l\'assistaient de leurs biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 4, 'Comme il s\'assemblait une foule enorme et que de toutes les villes on venait a lui, il dit cette parabole :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 5, 'Le semeur est sorti pour semer sa semence ; il jette sa semence, et des grains sont tombes le long du chemin ; on les a foules aux pieds ; et les oiseaux du ciel les ont manges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 6, 'D\'autres sont tombes sur des pierres ; ils ont leve, puis seche ; ils n\'avaient pas d\'humidite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 7, 'D\'autres grains sont tombes au milieu des epines, et en meme temps ont cru les epines ; et elles les ont etouffes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 8, 'D\'autres sont tombes dans la bonne terre ; ils ont leve ; ils ont porte du fruit au centuple. Cela disant, il s\'ecriait : Que celui qui a des oreilles pour entendre, entende!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 9, 'Ses disciples lui demanderent ce que signifiait cette parabole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 10, 'Il repondit : A vous il est donne de penetrer les mysteres du Royaume de Dieu, mais les autres ne les connaissent que par des paraboles, afin qu\'ils voient sans voir et entendent sans comprendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 11, 'Voici le sens de cette parabole : La semence, c\'est la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 12, 'Le long du chemin sont ceux qui ont ecoute ; mais ensuite vient le diable, qui enleve de leurs coeurs la parole, de peur qu\'ils ne croient et ne soient sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 13, 'Sur des pierres sont ceux qui, apres avoir ecoute la parole, l\'ont accueillie avec joie ; mais ils n\'ont pas de racine ; ils ont la foi momentanement et, au moment de l\'epreuve, ils se retirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 14, 'La semence tombee parmi les epines represente ceux qui ont ecoute la parole, et puis ils sont partis, et chez eux elle demeure sterile, etouffee par les soucis, par les richesses et par les plaisirs de la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 15, 'La semence tombee dans la bonne terre represente ceux qui retiennent dans un coeur excellent et bon la parole par eux entendue, et portent du fruit avec perseverance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 16, 'Personne, quand il allume une lampe, ne la recouvre d\'un vase ou ne la pose sous un lit ; on la place au contraire sur un pied-de-lampe, afin que ceux qui entrent voient la lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 17, 'Car rien n\'est cache qui ne doive etre decouvert, rien n\'est secret qui ne doive etre connu et mis au jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 18, 'Prenez donc garde comment vous ecoutez. Car a celui qui aura, il sera donne, et a celui qui n\'aura pas, meme ce qu\'il croit avoir lui sera ote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 19, 'Sa mere et ses freres, etant venus le trouver, ne pouvaient, a cause de la foule, penetrer jusqu\'a lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 20, 'On l\'en prevint : Ta mere et tes freres sont la dehors, qui voudraient te voir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 21, 'Il repondit, par ces paroles : Ma mere et mes freres, ce sont ceux-la, qui ecoutent la parole de Dieu et qui la mettent en pratique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 22, 'Il monta, un jour, dans une barque avec ses disciples. Faisons la traversee du lac, leur dit-il. Ils partirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 23, 'Tandis qu\'ils naviguaient, Jesus s\'endormit ; et un tourbillon de vent s\'abattit sur le lac ; la barque s\'emplissait d\'eau ; ils etaient en danger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 24, 'Les disciples allerent a lui, le reveillerent, lui disant : Maitre, maitre, nous perissons! Il se leva, se mit a menacer le vent et les flots souleves. Ils s\'arreterent ; il se fit un calme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 25, 'Il leur dit alors: Ou est votre foi? Terrifies, ils l\'admiraient et se disaient l\'un a l\'autre : Qui donc est cet homme qui commande aux vents et aux flots, et a qui ils obeissent?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 26, 'Ils aborderent au pays des Geraseniens, situe en face de la Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 27, 'Comme Jesus descendait a terre, vint a sa rencontre un homme de la ville, possede par des demons. Il etait depuis fort longtemps sans vetements ; il vivait en dehors de tout domicile ; et demeurait dans les sepulcres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 28, 'Apercevant Jesus, il tomba a ses pieds en jetant des cris, et dit de toutes ses forces : Qu\'y a-t-il entre moi et toi, Jesus, Fils du Dieu Tres-Haut? Je t\'en conjure, ne me torture point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 29, 'En effet, Jesus commandait a l\'Esprit impur de sortir de cet homme dont depuis tres longtemps il s\'etait empare. On l\'attachait avec des chaines, on lui mettait des fers aux pieds pour le retenir ; il rompait tous les liens et le demon le poussait vers les lieux deserts. Jesus l\'interrogea.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 30, 'Quel est ton nom? lui dit-il.  Il repondit : Legion. Une quantite de demons etaient, en effet, entres dans cet homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 31, 'Ceux-ci supplierent Jesus de ne pas ordonner qu\'ils allassent a l\'abime,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 32, 'mais de leur permettre d\'entrer dans un nombreux troupeau de pourceaux qui etaient la, paissant sur la montagne. Il le leur permit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 33, 'Les demons sortirent alors de l\'homme et entrerent dans les pourceaux et, d\'une course impetueuse, le troupeau se precipita dans le lac et s\'y noya.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 34, 'Voyant ce qui etait arrive, les gardeurs prirent la fuite et allerent repandre la nouvelle dans la ville et dans les campagnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 35, 'Les habitants sortirent pour voir ce qui s\'etait passe. Ils s\'approcherent de Jesus ; ils trouverent assis a ses pieds, avec ses vetements, et plein de bon sens, l\'homme de qui les demons etaient sortis. Cela leur fit peur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 36, 'Les temoins oculaires leur raconterent comment le demoniaque avait ete gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 37, 'Tout ce peuple du pays des Geraseniens pria alors Jesus de s\'eloigner d\'eux. En effet, ils etaient frappes de terreur. Il remonta donc dans la barque et s\'en retourna.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 38, 'L\'homme qui avait ete delivre des demons lui demandait la permission de rester avec lui. Mais Jesus le congedia et lui dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 39, 'Retourne en ta maison, et raconte tout ce que Dieu t\'a fait. Cet homme partit, publiant par toute la ville ce que lui avait fait Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 40, 'A son retour, Jesus fut recu par la foule ; tous etaient a lattendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 41, 'A ce moment un homme nomme Jairus, qui etait chef de la synagogue, vint se jeter a ses pieds, le suppliant d\'entrer dans sa maison,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 42, 'parce que sa fille unique, agee de douze ans environ, se mourait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 43, 'Il y allait presse par la foule, lorsqu\'une femme, malade d\'une perte de sang depuis douze annees, qui avait depense en medecins tout son avoir, sans qu\'aucun d\'eux eut pu la guerir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 44, 's\'approcha par derriere et toucha la frange de son vetement. Sa perte de sang s\'arreta immediatement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 45, 'Jesus demanda : Qui est-ce qui m\'a touche? Tous s\'en defendirent. C\'est la foule, maitre, qui te presse, qui t\'accable, lui dirent Pierre et ceux qui etaient avec luit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 46, 'Jesus reprit : Quelqu\'un m\'a touche, car j\'ai senti qu\'une vertu sortait de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 47, 'Se voyant decouverte, la femme, toute tremblante, vint se jeter a ses pieds et declara devant tout le monde pourquoi elle l\'avait touche, et comment elle avait ete immediatement guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 48, 'Et lui, il lui dit : Ma fille, ta foi t\'a guerie ; va en paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 49, 'Comme il parlait encore, arriva quelqu\'un de chez le chef de la synagogue disant: Ta fille est morte, ne fatigue pas davantage le maitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 50, 'Jesus, entendant ces paroles, dit a Jairus : Ne crains point, crois seulement, et elle sera sauvee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 51, 'Arrive a la maison, il ne permit a personne d\'entrer avec lui, sinon a Pierre, a Jean et a Jacques, ainsi qu\'au pere et a la mere de l\'enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 52, 'Tous ceux qui etaient la pleuraient a haute voix et se lamentaient sur elle. Il leur dit : Ne pleurez pas, elle n\'est pas morte, mais elle dort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 53, 'Et on se moquait de lui ; on savait bien qu\'elle etait morte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 54, 'Jesus, alors, lui saisit la main et prononca ces paroles : Mon enfant, reveille-toi ! Et son esprit revint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 55, 'Elle se leva aussitot, et Jesus commanda de lui donner a manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 8, 56, 'Ses parents etaient transportes ; mais il leur ordonna de ne dire a personne ce qui etait arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 1, 'Ayant reuni les douze, il leur donna force et puissance sur tous, les demons et le pouvoir de guerir des maladies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 2, 'Puis il les envoya en mission pour precher le Royaume de Dieu et faire des guerisons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 3, 'Voici ce qu\'il leur dit : N\'emportez rien pour la route, ni baton, ni sac, ni pain, ni argent, n\'ayez pas meme deux vetements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 4, 'En quelque maison que vous entriez, demeurez-y, et que, ce soit de la que vous partiez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 5, 'Quant a ceux qui ne vous recevront pas, sortez de leur ville en secouant, en temoignage contre eux, la poussiere de vos pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 6, 'Ils partirent et c\'est de village en village qu\'ils allaient, partout evangelisant et partout guerissant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 7, 'Le tetrarque Herode apprit tout ce qui se passait, et il etait rendu fort perplexe par ce qu\'on disait de Jesus. Pour les uns, Jean etait ressuscite d\'entre les morts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 8, 'pour les autres, Elie etait apparu ; pour ceux-ci, un des anciens prophetes etait revenu a la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 9, 'J\'ai fait couper la tete a Jean, disait Herode ; qui est donc cet homme dont j\'entends dire de pareilles choses? Et il etait desireux de le voir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 10, 'A leur retour, les apotres raconterent a Jesus tout ce qu\'ils avaient fait. Il les prit avec lui et il se retira a l\'ecart dans une ville appelee Bethsaida. Mais les multitudes l\'apprirent et le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 11, 'Il les accueillit en leur parlant du Royaume de Dieu et en rendant la sante a ceux qui avaient besoin d\'etre gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 12, 'Le jour cependant commencait a baisser, et les douze, s\'approchant, lui dirent : Congedie les foules, afin qu\'elles aillent dans les villages d\'alentour et dans les maisons de la campagne trouver logement et nourriture ; nous sommes ici dans un desert.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 13, 'Mais Jesus leur repondit : Donnez-leur vous-memes a manger.  Nous n\'avons ici, repliquerent-ils, que cinq pains et deux poissons, a moins que nous n\'allions acheter de quoi nourrir tout ce peuple !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 14, 'Or il y avait la a peu pres cinq mille hommes. Jesus dit alors a ses disciples : Faites-les asseoir par rangees de cinquante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 15, 'Ainsi firent-ils ; et tout le monde s\'assit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 16, 'Puis Jesus, prenant les cinq pains et les deux poissons, leva les yeux au ciel, prononca sur eux la benediction, les rompit et les donna aux disciples pour qu\'ils les servissent a la foule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 17, 'Tous mangerent, tous furent rassasies ; et on emporta les morceaux qui restaient ; il y en avait douze paniers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 18, 'Comme il etait un jour a prier dans un lieu solitaire, les disciples le rejoignirent et il leur fit cette question : Parmi ces foules, qui dit-on que je suis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 19, 'Ils lui repondirent ainsi : Jean-Baptiste ; d\'autres: Elie ; d\'autres encore : un des anciens prophetes revenu a la vie. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 20, 'Et vous, continua-t-il, qui dites-vous que je suis? Pierre repondit par ces mots : Le Christ de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 21, 'Il leur defendit avec les plus rigoureuses recommandations de le dire a personne,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 22, 'ajoutant qu\'il lui fallait, lui, le Fils de l\'homme :  beaucoup souffrir ;  etre rejete par les Anciens, les chefs des pretres et les Scribes ;  etre mis a mort ;  ressusciter le troisieme jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 23, 'A tous il disait : Si quelqu\'un veut venir a ma suite, qu\'il renonce a lui-meme, que chaque jour il porte sa croix, et qu\'il me suive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 24, 'Celui qui voudra sauver sa vie, la perdra ; et il sauvera sa vie, celui qui l\'aura perdue a cause de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 25, 'A quoi servirait-il a un homme de gagner le monde entier, si c\'est en se perdant lui-meme et en consommant sa ruine?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 26, 'De celui qui aura eu honte de moi et de mes paroles, le Fils de l\'homme aura honte a son tour quand il viendra dans sa gloire et dans celle du Pere et des saints anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 27, 'Je vous dis la verite : Quelques-uns sont ici presents qui ne gouteront point la mort, avant d\'avoir vu le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 28, 'Huit jours environ apres avoir ainsi parle, Jesus emmena avec lui Pierre, Jean et Jacques, et s\'en alla prier sur la montagne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 29, 'Tandis qu\'il priait, l\'aspect de son visage changea et ses vetements devinrent d\'une blancheur resplendissante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 30, 'Deux hommes etaient la qui s\'entretenaient avec lui ; c\'etaient Moise et Elie');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 31, 'apparaissant en grande gloire ; ils parlaient de sa sortie du monde qu\'il devait accomplir a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 32, 'Cependant Pierre et ses compagnons avaient succombe au sommeil. C\'est a leur reveil qu\'ils virent sa gloire et les deux hommes qui se tenaient pres de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 33, 'Au moment ou ceux-ci se separaient de Jesus, Pierre lui dit : Maitre, qu\'il nous est bon d\'etre ici! dressons trois tentes, une pour toi, une pour Moise, une pour Elie! Il ne savait ce qu\'il disait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 34, 'Pendant qu\'il parlait, survint une nuee qui les enveloppa. Les disciples furent frappes de terreur quand ils les virent entrer dans la nuee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 35, 'Or, de cette nuee sortit une voix disant : Celui-ci est mon Fils, l\'elu, ecoutez-le!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 36, 'Quand la voix se tut, Jesus se trouva seul. Ses disciples garderent le silence sur ce qu\'ils avaient vu, et, a ce moment-la, n\'en raconterent rien a personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 37, 'Lorsque le lendemain ils descendirent de la montagne, une nombreuse multitude vint a la rencontre de Jesus');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 38, 'et voila que du sein de la foule un homme s\'ecria : Maitre, je t\'en supplie, jette un regard sur mon fils ; c\'est mon seul enfant ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 39, 'et il y a un Esprit qui s\'empare de lui : tout a coup il lui arrache un cri, lui donne des convulsions, le fait ecumer et quand il se decide a le quitter, c\'est pour le laisser tout brise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 40, 'J\'ai prie tes disciples de le chasser, cela leur a ete impossible. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 41, 'O generation incredule et perverse, dit Jesus en lui repondant, jusqu\'a quand serai-je au milieu de vous et vous supporterai-je? Amene ici ton fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 42, 'L\'enfant s\'approchait, quand le demon le jeta contre terre et lui donna des convulsions. Mais Jesus, menacant l\'Esprit impur, guerit l\'enfant et le rendit a son pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 43, 'Tous furent confondus de la grandeur de Dieu. Comme tout le monde s\'emerveillait de tout ce que Jesus faisait, il dit a ses disciples :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 44, 'Pour vous, mettez bien dans votre memoire les paroles que voici : Le Fils de l\'homme va etre livre entre les mains des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 45, 'Les disciples ne comprirent pas ce langage ; il etait voile pour eux, afin qu\'ils n\'en sentissent pas la portee, et c\'etait un sujet sur lequel ils avaient peur de l\'interroger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 46, 'Entre eux une discussion s\'eleva : lequel etait le plus grand,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 47, 'Jesus, qui connaissait les pensees de leur coeur, prit un enfant, le placa pres de lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 48, 'et leur dit : Qui recoit en mon nom cet enfant-la, me recoit ; et qui me recoit, recoit Celui qui m\'a envoye. Car celui qui est le plus petit parmi vous tous, c\'est celui-la qui est grand!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 49, 'Jean prit la parole : Maitre, dit-il, nous avons vu un homme chasser des demons en ton nom, et nous len avons empeche, parce qu\'il ne te suit pas avec nous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 50, 'Ne l\'en empechez point! repondit Jesus ; car celui qui n\'est pas contre vous est pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 51, 'Cependant les jours ou il devait etre enleve de ce monde approchaient et, d\'un visage ferme, il partit pour Jerusalem,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 52, 'envoyant devant lui des messagers. Ceux-ci etant entres, en s\'en allant, dans un village de la Samarie pour lui preparer un logement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 53, 'on refusa de le recevoir, parce que c\'etait vers Jerusalem qu\'il tournait ses pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 54, 'Seigneur, dirent les disciples presents, Jacques et Jean, veux-tu que nous disions au feu du ciel de descendre et de reduire en cendres ces gens-la!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 55, 'Jesus se tourna vers eux et les reprimanda ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 56, 'et ils allerent dans un autre village.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 57, 'Pendant qu\'ils etaient en chemin, quelqu\'un lui dit : Je te suivrai partout ou tu iras.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 58, 'Jesus lui repondit : Les renards ont des tanieres et les oiseaux du ciel ont des nids ; mais le Fils de l\'homme n\'a pas ou reposer sa tete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 59, 'A un autre il dit : Suis-moi. Celui-ci lui repondit : Permets-moi d\'aller d\'abord ensevelir mon pere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 60, 'mais Jesus reprit : Laisse les morts ensevelir leurs morts. Pour toi, va annoncer le Regne de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 61, 'Un autre encore lui dit : Je te suivrai, Seigneur ; mais permets-moi d\'abord de prendre conge de ma famille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 9, 62, 'A celui-la Jesus repondit : Quiconque en posant la main sur la charrue regarde en arriere, n\'est pas propre au Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 1, 'Le Seigneur, apres cela, designa encore soixante et dix autres disciples, et il leur donna mission de le preceder deux par deux dans toute ville, dans tout endroit ou lui-meme devait aller.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 2, 'Il leur disait : La moisson est grande et les ouvriers en petit nombre ; priez donc le maitre de la moisson d\'envoyer des ouvriers dans sa moisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 3, 'Allez ; voici que je vous envoie comme des agneaux au milieu de loups.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 4, 'N\'emportez ni bourse, ni sac, ni chaussures, et en chemin ne saluez personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 5, 'Dans toute maison ou vous entrerez que votre premiere parole soit : Paix a cette maison!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 6, 'Et s\'il s\'y trouve quelque enfant de paix, sur lui reposera votre paix ; sinon elle reviendra a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 7, 'Demeurez dans cette maison-la, mangeant et buvant ce qui s\'y trouvera, car l\'ouvrier est digne de son salaire. Ne peregrinez pas de maison en maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 8, 'Dans toute ville ou vous entrerez, et ou l\'on vous recevra, mangez ce qui vous sera offert ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 9, 'guerissez les malades qui sont dans la ville, et dites-leur : Le Regne de Dieu s\'est approche de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 10, 'Dans toute ville ou vous entrerez et ou l\'on ne vous recevra pas, sortez sur les places publiques et dites :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 11, 'La poussiere meme de votre ville qui s\'est attachee a nos pieds, nous la secouons pour vous la rendre ; sachez cependant ceci : Le Regne de Dieu approche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 12, 'Je vous declare qu\'en ce jour-la! il y aura moins de rigueur pour Sodome que pour cette ville-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 13, 'Malheur a toi Chorazein, malheur a toi Bethsaida, car, si les miracles faits au milieu de vous l\'avaient ete dans Tyr et dans Sidon, elles se seraient depuis longtemps repenties en se revetant d\'un sac et assises dans la cendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 14, 'Aussi y aura-t-il au jugement moins de rigueur pour Tyr et pour Sidon que pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 15, 'Et toi, Capharnaum, crois-tu que tu seras elevee jusqu\'au ciel? Tu seras abaissee jusqu\'a la Demeure-des-morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 16, 'Qui vous ecoute, m\'ecoute ; qui vous meprise, me meprise ; or qui me meprise, meprise Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 17, 'Les soixante-dix revinrent pleins de joie ; ils disaient: Seigneur, les demons memes se soumettent a nous en ton nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 18, 'Il leur repondit : Je voyais Satan tomber du ciel comme un eclair !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 19, 'Oui, je vous ai donne la puissance de fouler aux pieds et des serpents et des scorpions et toutes les forces de l\'Ennemi, et aucun mal ne vous arrivera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 20, 'Toutefois ne vous rejouissez pas de ce que les Esprits vous sont soumis ; rejouissez-vous de ce que vos noms sont ecrits dans les cieux !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 21, 'En ce moment meme, il dit dans un transport de joie venu de lEsprit saint : Je te benis, o Pere, o Seigneur du ciel et de la terre, de ce que tu as cache ces choses a des sages et a des savants, et de ce que tu les as revelees a de petits enfants. Oui, Pere, je te benis de ce que tel a ete ton bon plaisir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 22, 'Tout m\'a ete confie par mon Pere, et personne ne sait qui est le Fils excepte le Pere, et qui est le Pere excepte le Fils et celui a qui il plait au Fils de le reveler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 23, 'Puis, se tournant vers les disciples, il leur dit en particulier: Heureux les yeux qui voient ce que vous voyez!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 24, 'Je vous declare, en effet, que nombre de prophetes et de rois ont voulu voir ce que vous, vous voyez, et ne l\'ont pas vu, entendre ce que vous entendez, et ne l\'ont pas entendu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 25, 'Voulant le mettre a l\'epreuve, un legiste se leva et lui dit: Maitre, que dois-je faire pour acquerir la vie eternelle? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 26, 'Qu\'y a-t-il d\'ecrit dans la Loi? lui demanda Jesus ; qu\'est-ce que tu y lis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 27, 'Le legiste lui fit cette reponse : Tu aimeras le Seigneur ton Dieu de tout ton coeur, de toute ton ame, de toute ta force, de toute ta pensee et ton prochain comme toi-meme. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 28, 'Parfaitement repondu! dit Jesus ; fais cela et tu vivras.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 29, 'Mais le legiste, voulant se justifier lui-meme, dit a Jesus : Qui donc est mon prochain? ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 30, 'Jesus reprit alors : Un homme descendait de Jerusalem a Jericho ; il tomba entre les mains de brigands qui le depouillerent et partirent, le laissant couvert de blessures et a moitie mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 31, 'Or un pretre descendait, par hasard, par la meme route ; il vit l\'homme, et il passa outre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 32, 'Un Levite fit de meme ; arrive a l\'endroit, il vit, et il passa outre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 33, 'Mais un certain Samaritain, qui etait en voyage, arriva pres de l\'homme et fut, en le voyant, touche de compassion ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 34, 'il s\'approcha de lui, il banda ses plaies apres y avoir verse de l\'huile et du vin, puis il le placa sur sa propre monture, l\'amena dans une hotellerie, et lui donna des soins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 35, 'Le lendemain, il tira deux deniers, les remit a l\'hotelier et lui dit : Tu prendras soin de cet homme ; et ce que tu depenseras de plus, moi-meme, a mon retour, je te le rembourserai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 36, 'Lequel de ces trois te parait avoir ete le prochain de celui qui etait tombe entre les mains des brigands? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 37, 'C\'est celui qui a ete compatissant envers lui, repondit le Legiste. Jesus lui dit alors : Va et agis de meme, toi aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 38, 'Dans un village ou Jesus entra pendant qu\'ils etaient en route, ce fut une femme nommee Marthe qui le recut dans sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 39, 'Elle avait une soeur appelee Marie, qui, assise aux pieds du Seigneur, ecoutait sa parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 40, 'Marthe cependant etait absorbee par toutes sorte de soins ; elle s\'arreta pour dire : Seigneur, n\'as-tu aucun souci de ce que ma soeur m\'a laissee servir toute seule? dis-lui donc de m\'aider.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 41, 'Mais le Seigneur lui fit cette reponse : Marthe, Marthe, tu t\'inquietes, tu te troubles pour une multitude de choses ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 10, 42, 'une seule chose est necessaire. Marie a choisi la bonne part qui ne lui sera point otee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 1, 'Quelque part, un jour, Jesus etait en priere. Quand il eut fini, l\'un de ses disciples lui dit : Seigneur, enseigne-nous a prier, de meme que Jean l\'a enseigne a ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 2, 'Il leur repondit : Quand vous priez, dites : Pere, que ton nom soit sanctifie! Que ton Regne vienne!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 3, 'Donne-nous chaque jour notre pain quotidien !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 4, 'Et remets-nous nos peches, car nous-memes nous faisons remise a quiconque nous doit. Et ne nous induis pas en tentation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 5, 'Il leur dit encore : Je suppose que l\'un de vous ait un ami et qu\'au milieu de la nuit il aille le trouver pour lui dire : Ami, prete-moi trois pains,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 6, 'car voila un de mes amis qui m\'arrive de voyage, et je n\'ai rien a lui offrir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 7, 'De l\'interieur, l\'autre lui repond : Ne m\'importune pas, ma porte est deja fermee, mes enfants sont couches comme moi ; je ne puis me lever pour te donner quoi que ce soit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 8, 'Eh bien, je vous le declare,  quand meme il ne se leverait ni ne lui donnerait rien en tant qu\'ami,  il se levera du moins a cause de son insistance importune, et lui donnera ce dont il a besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 9, 'Et moi aussi je vous dis : Demandez et il vous sera donne ; cherchez et vous trouverez ; frappez et il vous sera ouvert ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 10, 'car quiconque demande recoit ; et qui cherche trouve ; et a celui qui frappe on ouvrira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 11, 'Est-il un pere parmi vous, quand son fils lui demandera du pain, qui lui donnera une pierre! ou du poisson et qui, au lieu de poisson, lui donnera un serpent !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 12, 'Quand son fils lui demandera un oeuf, lui donnera-t-il un scorpion!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 13, 'Si donc, tout mauvais que vous etes, vous savez donner de bonnes choses a vos enfants, combien plus votre Pere, qui donne du ciel, donnera-t-il l\'Esprit saint a ceux qui le lui demandent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 14, 'Un jour il chassait un demon, un demon muet. Il arriva que le demon etant sorti, l\'homme muet parla ; et les multitudes etaient dans l\'admiration.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 15, 'Cependant quelques-uns des assistants dirent : C\'est par Beelzeboul, le prince des demons, qu\'il chasse les demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 16, 'D\'autres, pour le mettre a l\'epreuve, lui demanderent un signe qui vint du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 17, 'Mais Jesus, connaissant leurs pensees, leur dit: Tout royaume divise contre lui-meme tombe en ruines, et ses maisons s\'ecroulent l\'une sur l\'autre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 18, 'Si donc Satan est divise contre lui-meme, comment son royaume subsistera-t-il? Vous dites que c\'est par Beelzeboul que je chasse les demons :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 19, 'mais si, moi, je chasse les demons par Beelzeboul, par qui vos fils les chassent-ils? C\'est pourquoi ils seront eux-memes vos juges!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 20, 'Si, au contraire, c\'est par le doigt de Dieu que je chasse les demons, le Royaume de Dieu est donc venu jusqu\'a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 21, 'Lorsque le guerrier, armes en main, garde l\'entree de sa demeure, ce qu\'il possede est en surete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 22, 'Mais s\'il survient un guerrier, superieur en forces, qui le terrasse, il lui arrache cette armure dans laquelle il se confiait, et il jette ca et la ses depouilles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 23, 'Qui n\'est point avec moi est contre moi, et qui n\'amasse point avec moi, disperse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 24, 'Lorsque l\'Esprit impur est sorti d\'un homme, il erre par des lieux arides, cherchant du repos et n\'en trouvant pas, il dit : Je retournerai dans ma maison, d\'ou je suis parti.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 25, 'Il y revient et la trouve nettoyee et paree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 26, 'La-dessus il va prendre sept autres Esprits plus mechants que lui-meme ; ils y penetrent et y demeurent. Et le dernier etat de cet homme devient pire que le premier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 27, 'Pendant qu\'il parlait ainsi, du milieu de la foule, une femme eleva la voix : Heureuses, dit-elle, les entrailles qui te porterent, heureux le sein qui t\'allaita!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 28, 'Mais lui, il dit : Heureux plutot ceux qui ecoutent la Parole de Dieu et qui la gardent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 29, 'Comme le peuple s\'amassait en foule, il se mit a dire : Cette generation est une mauvaise generation! elle demande un signe et nul signe ne lui sera donne, sinon le signe de Jonas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 30, 'De meme, en effet, que Jonas devint un signe pour les Ninivites, de meme, pour cette generation-ci, en sera-t-il du Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 31, 'Lors du jugement, la reine du Midi se levera, avec les hommes de cette generation, et les condamnera, car cette reine vint des extremites de la terre pour ecouter la sagesse de Salomon ; et voici, sous vos yeux, plus que Salomon!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 32, 'Lors du jugement les hommes de Ninive se leveront avec cette generation et la condamneront ; car eux, ils se repentirent a la predication de Jonas ; et voici, sous vos yeux, plus que Jonas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 33, 'Personne n\'allume une lampe pour la cacher ou la mettre sous le boisseau, mais on la met sur le pied-de-lampe, afin que ceux qui entrent voient la lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 34, 'La lampe de ton corps, c\'est ton oeil. Quand ton oeil n\'a rien qui le trouble, ton corps entier est dans la lumiere ; si ton oeil est en mauvais etat, ton corps est dans les tenebres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 35, 'Veille en consequence a ce que la lumiere qui est en toi ne soit point tenebres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 36, 'Et si ton corps entier est dans la lumiere, sans aucune partie dans les tenebres, tout sera, pour toi, dans la lumiere, comme lorsque rayonne sur toi la clarte d\'une lampe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 37, 'Il parlait encore lorsqu\'un Pharisien le pria de venir manger chez lui ; il entra et se mit a table.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 38, 'Le Pharisien remarqua avec surprise qu\'il n\'avait pratique aucune ablution avant le repas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 39, 'Le Seigneur lui dit alors : Aujourd\'hui, vous autres Pharisiens, vous purifiez le dehors de la coupe et du plat, quand le dedans intime de vous-memes est plein de rapines et d\'iniquites!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 40, 'Insenses! est-ce que celui qui a fait le dehors n\'a pas fait aussi le dedans?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 41, 'Donnez plutot en aumones le contenu des coupes et des plats ; et aussitot tout vous sera pur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 42, 'Mais malheur a vous, les Pharisiens, parce que vous acquittez la dime et de la menthe, et de la rue, et de toute espece de legumes, et que vous negligez la justice et l\'amour de Dieu. Ce qu\'il fallait, c\'est faire ces choses, sans omettre les autres !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 43, 'Malheur a vous, les Pharisiens, parce que vous aimez, dans les synagogues les premiers sieges, sur les places publiques les salutations!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 44, 'Malheur a vous, parce que vous etes comme les sepulcres dissimules aux regards, et sur lesquels on marche sans le savoir!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 45, 'La-dessus, un des legistes lui dit : Maitre, en parlant ainsi, tu nous fais injure a nous aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 46, 'Jesus reprit alors : A vous aussi, les legistes, malheur! parce que vous chargez les hommes de fardeaux insupportables, et vous-memes vous ne touchez pas ces fardeaux du bout du doigt!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 47, 'Malheur a vous, parce que vous elevez des tombeaux aux prophetes que vos peres ont tues ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 48, 'donc, vous etes temoins, et vous approuvez les oeuvres de vos peres : eux, ils ont commis les meurtres, vous, vous elevez les tombeaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 49, 'Voila aussi pourquoi la sagesse de Dieu a dit : Je leur enverrai prophetes et apotres ; les uns ils les tueront, les autres ils les persecuteront,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 50, 'afin qu\'a cette generation il soit demande compte du sang de tous les prophetes, repandu depuis la creation du monde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 51, 'a partir du sang d\'Abel jusqu\'au sang de Zaccharie egorge entre l\'autel et le sanctuaire ; oui, je vous le declare, c\'est a cette generation quil en sera demande compte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 52, 'Malheur a vous, les legistes, parce que vous avez pris la clef de la science! Vous-memes vous n\'etes pas entres, et ceux qui venaient pour entrer, vous les en avez empeches!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 53, 'Quand il fut sorti de la, les Scribes et les Pharisiens entreprirent de le harceler de toutes leurs forces, de le faire parler sur toutes sortes de sujets,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 11, 54, 'en lui tendant des pieges et en cherchant a surprendre quelqu\'une de ses paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 1, 'Il se rassembla, cependant, une si enorme quantite de monde, qu\'on se foulait les uns les autres. Il se mit alors a dire a ses disciples : Avant tout gardez-vous du levain des Pharisiens qui est l\'hypocrisie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 2, 'Il n\'y a rien de cache qui ne doive etre revele, ni de secret qui ne doive etre connu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 3, 'Aussi, tout ce que vous aurez dit dans les tenebres sera repete dans la lumiere, et ce que vous aurez dit a l\'oreille en chambres closes sera preche sur les toits!.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 4, 'Et alors je vous dis ceci, a vous, mes amis : Ne craignez pas ceux qui tuent le corps, et qui, apres cela, ne peuvent rien faire de plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 5, 'Je vais vous dire qui vous devez craindre : Vous devez craindre celui qui, apres avoir envoye la mort, a le pouvoir de jeter dans la Gehenne ; oui, je vous le declare, c\'est celui-la que vous devez craindre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 6, 'Cinq passereaux ne se vendent-ils pas deux as? Et il n\'en est pas un seul qui soit oublie devant Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 7, 'Jusqu\'aux cheveux de votre tete, tous ils sont comptes! Ne craignez point ; vous, etes de plus de valeur que bien des passereaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 8, 'Quiconque, je vous le declare, me confessera devant les hommes, le Fils de l\'homme le confessera aussi devant les anges de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 9, 'Qui m\'aura renie devant les hommes, sera renie devant les anges de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 10, 'A quiconque prononcera un mot contre le Fils de l\'homme, il sera pardonne ; mais a celui qui blasphemera contre le Saint-Esprit, il ne sera pas pardonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 11, 'Quand on vous trainera dans les synagogues, devant les magistrats, devant les autorites, ne vous inquietez nullement ni de ce que vous direz pour votre defense ni comment vous parlerez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 12, 'Car le saint Esprit vous enseignera, a l\'heure meme, ce qu\'il faudra que vous disiez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 13, 'Maitre, lui dit quelqu\'un de la foule, dis a mon frere de partager avec moi notre heritage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 14, 'Il lui repondit : Qui m\'a constitue juge entre vous, ou faiseur de partages?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 15, 'Puis il s\'adressa aux autres : Soyez bien attentifs a vous garder de toute avarice, car, en quelque abondance qu\'on soit, la vie ne depend pas de ce qu\'on possede.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 16, 'Puis il leur raconta une parabole : Le champ d\'un homme riche avait beaucoup rapporte ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 17, 'et il songeait en lui-meme : Que ferai-je? car je n\'ai pas ou serrer ma recolte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 18, 'Ce que je ferai, dit-il, le voici : J\'abattrai mes greniers ; j\'en construirai de plus grands ; et j\'y ramasserai tous mes produits et tous mes biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 19, 'Et puis je dirai a mon ame : Mon ame, tu as la en reserve beaucoup de biens, pour beaucoup d\'annees ; repose-toi, mange, bois et te rejouis!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 20, 'Mais Dieu lui dit : Insense! c\'est cette nuit-ci que ton ame te sera redemandee. Et ce que tu t\'es prepare, a qui sera-t-il?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 21, 'Ainsi en sera-t-il de celui qui thesaurise pour lui-meme et qui n\'est pas riche pour Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 22, 'Voila pourquoi, ajouta-t-il, s\'adressant a ses disciples, je vous dis de ne pas vous inquieter :  pour votre vie de ce que vous mangerez,  pour votre corps de quoi vous vous vetirez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 23, 'La vie est plus que la nourriture, et le corps plus que le vetement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 24, 'Observez les corbeaux ; ils ne sement, ni ne moissonnent ;ils n\'ont ni cellier, ni grenier ; et Dieu les nourrit. Combien ne valez-vous pas plus que les oiseaux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 25, 'Qui de vous peut, par toutes ses preoccupations, ajouter une coudee a la duree de sa vie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 26, 'Si donc vous ne pouvez meme les moindres choses, pourquoi vous inquieter des autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 27, 'Observez comment croissent les lis ; ils ne travaillent ni ne filent. Cependant, je vous le declare, Salomon lui-meme dans toute sa gloire n\'etait pas vetu comme l\'un d\'eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 28, 'Si donc, dans un champ, l\'herbe qui existe aujourd\'hui et qui demain sera jetee au four, Dieu la revet de la sorte, combien plutot vous-memes, gens de petite foi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 29, 'Ne vous preoccupez donc pas de ce que vous aurez a manger, ou de ce que vous aurez a boire, et ne vous en tourmentez pas l\'esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 30, 'Oui, tout cela les paiens du monde s\'en enquierent ; mais, vous, votre Pere sait que vous en avez besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 31, 'Cherchez seulement son Royaume et ces choses vous seront donnees par surcroit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 32, 'Ne crains point, petit troupeau ; car il a plu a votre Pere de vous donner le Royaume.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 33, 'Vendez ce que vous possedez et le donnez en aumones. Faites-vous des bourses inusables ; faites-vous un tresor qui ne vous fasse point defaut dans les cieux, dont le voleur n\'approche pas, que le ver ne detruise pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 34, 'Car la ou est votre tresor, la sera aussi votre coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 35, 'Que vos reins soient ceints et vos lampes allumees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 36, 'Que vous soyez semblables a des hommes qui attendent leur maitre revenant du festin de noce, afin de lui ouvrir aussitot qu\'il arrivera et des qu\'il frappera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 37, 'Heureux ces serviteurs que le maitre, en arrivant, trouvera veillant de la sorte ; je vous le dis en verite, c\'est lui qui se ceindra, qui les fera asseoir a table et qui, passant de l\'un a l\'autre, se mettra a les servir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 38, 'Qu\'il rentre a la seconde veille, qu\'il rentre a la troisieme, heureux seront ces serviteurs, s\'il les trouve veillant ainsi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 39, 'Sachez ceci : Si le maitre de la maison savait a quelle heure doit venir le voleur, il veillerait et ne laisserait pas forcer sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 40, 'Vous aussi, soyez prets, parce que c\'est a une heure ou vous n\'y penserez pas que le Fils de l\'homme viendra.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 41, 'Pierre lui demanda : Est-ce pour nous, Seigneur, que tu dis cette parabole ou egalement pour tout le monde ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 42, 'Le Seigneur lui repondit : Quel est l\'administrateur. fidele et prudent que le maitre etablira sur ses domestiques pour donner a chacun, au moment voulu, sa mesure de froment?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 43, 'C\'est celui que le maitre, a son arrivee, trouvera agissant ainsi ; heureux ce serviteur !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 44, 'En verite, je vous le dis, le maitre l\'etablira sur tous ses biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 45, 'Mais si ce serviteur dit en son coeur : Mon maitre tarde a venir, et qu\'il se mette a battre valets et servantes, a manger et a boire, et a s\'enivrer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 46, 'son maitre surviendra au jour ou ce Serviteur ne s\'y attend pas et a une heure qu\'il ignore et il le dechirera a coups de verge, et lui donnera le meme lot qu\'aux infideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 47, 'Ce serviteur qui connaissait la volonte de son maitre et n\'a rien prepare ni agi selon sa volonte, recevra plusieurs coups.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 48, 'Quant a celui qui n\'a pas connu cette volonte et a fait des choses qui meritent des coups, il en recevra peu. A quiconque beaucoup a ete donne, beaucoup sera demande ; et de celui a qui on a beaucoup confie, il sera exige davantage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 49, 'Je suis venu mettre le feu sur la terre! Ah! que je voudrais quil fut deja allume!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 50, 'Il est un bapteme dont je dois etre baptise, et dans quelle angoisse je suis jusqu\'a ce qu\'il soit accompli !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 51, 'Croyez-vous que je sois venu donner la paix a la terre? Non, vous dis-je, c\'est la division.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 52, 'Car, desormais, cinq personnes etant dans une maison, elles seront partagees : trois contre deux et deux contre trois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 53, 'Seront divises : un pere contre son fils et un fils contre son pere ; une mere contre sa fille et une fille contre sa mere ; une belle-mere contre sa belle-fille et une belle-fille contre sa belle-mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 54, 'Il disait aussi aux multitudes : Lorsque vous voyez, au couchant, un nuage apparaitre, vous dites aussitot : la pluie arrive, et elle arrive en effet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 55, 'Lorsque c\'est le vent du midi qui souffle, vous dites : il fera chaud ; et il en est ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 56, 'Hypocrites, vous savez apprecier l\'aspect de la terre et du ciel, comment ne savez? vous pas apprecier aussi le temps ou vous etes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 57, 'et pourquoi ne jugez-vous pas aussi par vous-memes de ce qui est juste?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 58, 'Pendant que tu vas devant un magistrat avec ton adversaire, tache, en chemin, de sortir d\'affaire avec lui, de peur qu\'il ne te traine devant le juge, et que le juge ne te livre au reclamateur, et que le reclamateur ne te jette en prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 12, 59, 'Tu n\'en sortiras pas, je te le declare, que tu n\'aies paye jusqu\'a la derniere obole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 1, 'Vers cette meme epoque, quelques-uns vinrent lui raconter ce qui s\'etait passe au sujet des Galileens dont Pilate avait mele le sang a celui de leurs sacrifices.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 2, 'Voici ce qu\'il leur repondit : Croyez-vous que ces Galileens fussent de plus grands pecheurs que tous leurs compatriotes, parce qu\'il leur est arrive ce malheur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 3, 'Non, je vous le declare ; mais si vous ne vous repentez pas, vous perirez tous semblablement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 4, 'Comme ces dix-huit personnes sur qui s\'ecroula la tour pres du Siloam, et qui furent tuees, croyez-vous que leur dette depassat celle de tous les autres habitants de Jerusalem ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 5, 'non, je vous le declare, mais si vous ne vous repentez pas, vous perirez tous pareillement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 6, 'Il leur raconta cette parabole : Un homme avait un figuier plante dans sa vigne ; il vint y chercher du fruit, mais n\'en trouva point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 7, 'Voila trois ans, dit-il alors au vigneron, que je, viens chercher du fruit sur ce figuier, et que je n\'en trouve point ; coupe-le ; pourquoi epuise-t-il la terre inutilement? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 8, 'Seigneur, lui repondit le vigneron, laisse-le encore cette annee ; je becherai tout autour, je mettrai du fumier ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 9, 'peut-etre portera-t-il du fruit dans l\'avenir ; sinon, tu le couperas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 10, 'Un jour de sabbat, il enseignait dans une des synagogues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 11, 'Une femme etait la, possedee d\'un Esprit qui la tenait infirme depuis dix-huit ans. Elle etait toute courbee, et ne pouvait absolument pas se redresser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 12, 'Quand Jesus la vit, il l\'appela et lui dit : Femme, tu es delivree de ton infirmite, et il lui imposa les mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 13, 'Immediatement elle redevint droite. Cette femme rendait gloire a Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 14, 'lorsque le chef de la synagogue se mit a parler avec indignation de ce que Jesus guerissait pendant le sabbat. Il y a six jours pendant lesquels on doit travailler, disait-il a la foule : Venez, ces jours-la, vous faire guerir ; mais le jour du sabbat, non point. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 15, 'Hypocrites, dit le Seigneur en lui repondant, est-ce que chacun de vous, le jour du sabbat, ne detache pas du ratelier son boeuf ou son ane pour les mener boire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 16, 'Et cette femme, qui est une fille d\'Abraham, que, depuis dix-huit annees, Satan tenait garrottee, ne fallait-il pas, le jour du sabbat, la delivrer de sa chaine?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 17, 'Ces paroles couvrirent de confusion tous ses adversaires. Quant a la foule, elle etait dans la joie de toutes les choses glorieuses qu\'il accomplissait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 18, 'Alors il disait : A quoi est semblable le Royaume de Dieu, et a quoi le comparerai-je ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 19, 'Il est semblable a un grain de seneve qu\'un homme a pris et a jete dans son jardin ; il a cru, il est devenu un arbre et les oiseaux du ciel ont fait leurs nids dans ses branches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 20, 'Il dit encore: A quoi comparerai-je le Royaume de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 21, 'Il est semblable a du levain qu\'une femme prend, et qu\'elle cache dans trois mesures de farine, jusqu\'a ce que le levain ait partout penetre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 22, 'Il traversait villes et villages en enseignant, et s\'avancait vers Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 23, 'Quelqu\'un lui dit : Seigneur, sera-ce un petit nombre seulement qui sera sauve? Il repondit a lui et aux autres :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 24, 'Efforcez-vous d\'entrer par la porte etroite, car beaucoup, je vous le declare, chercheront a entrer et n\'y parviendront pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 25, 'Quand le maitre de maison se sera leve et aura ferme la porte, quand vous, restes dehors, vous vous mettrez a frapper et a dire : Seigneur, ouvre-nous, quand il vous repondra : Je ne sais d\'ou vous etes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 26, 'alors vous vous mettrez a dire: Nous avons mange et bu devant toi, et c\'est sur nos places publiques que tu as enseigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 27, 'Et il dira : Je vous declare que je ne sais d\'ou vous etes : Eloignez-vous de moi, vous tous, ouvriers d\'iniquite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 28, 'C\'est la que seront les pleurs et le grincement des dents, alors que vous verrez dans le Royaume de Dieu et Abraham, et Isaac, et Jacob, et tous les prophetes, et que, vous-memes, vous vous verrez chasses dehors.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 29, 'De l\'Orient, de l\'Occident, du Nord, du Midi, on viendra et on se mettra a table dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 30, 'Et il se trouvera des derniers qui seront premiers, et il se trouvera des premiers qui seront derniers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 31, 'A ce moment-la quelques Pharisiens vinrent lui dire : Eloigne-toi, pars d\'ici, Herode veut te mettre a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 32, 'Il leur repondit : Allez dire a ce renard, qu\'aujourd\'hui et demain je chasserai des demons et guerirai des malades, et que le troisieme jour tout pour moi se terminera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 33, 'Mais il faut qu\'aujourd\'hui, demain et le jour suivant je sois en marche, car il n\'est pas admissible qu\'un prophete perisse hors de Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 34, 'Jerusalem! Jerusalem! qui tues les prophetes et qui lapides ceux qui te sont envoyes, combien de fois ai-je voulu rassembler tes enfants comme la poule rassemble sa couvee sous ses ailes, et tu ne l\'as pas voulu !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 13, 35, 'Eh bien, votre demeure vous est laissee! Je vous declare que vous ne me verrez pas jusqu\'a ce qu\'il arrive que vous disiez : Beni soit celui qui vient au nom du Seigneur !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 1, 'Jesus etait entre dans la maison d\'un des principaux Pharisiens, pour y prendre un repas ; c\'etait un jour de sabbat. Aussi chacun l\'observait-il,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 2, 'car il y avait la, devant lui, un homme hydropique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 3, 'Jesus s\'adressa aux legistes et aux Pharisiens. Est-il permis, oui ou non, de guerir le jour du sabbat? leur demanda-t-il. Ils garderent le silence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 4, 'Alors il toucha de la main l\'hydropique, le guerit et le congedia.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 5, 'Puis, s\'adressant toujours a eux, il dit : Lequel d\'entre vous, si son fils ou son boeuf vient a tomber dans un puits, un jour de sabbat, ne l\'en retire aussitot?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 6, 'A cela ils ne surent que repondre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 7, 'Ayant remarque que les convives choisissaient les premieres places, il leur raconta une parabole :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 8, 'Quand tu seras invite par quelqu\'un a des noces, dit-il, ne va pas t\'installer a la premiere place, de peur qu\'un personnage plus considerable que toi, se trouvant parmi les invites,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 9, 'celui qui vous a convies l\'un et l\'autre ne vienne te dire : Donne-lui ta place et que tu n\'aies alors la confusion d\'aller occuper la derniere place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 10, 'Tout au contraire, quand tu seras invite, va te mettre a la derniere place, et alors, quand arrivera celui qui t\'a invite, il te dira : Mon ami, monte plus haut. Ce sera pour toi un honneur devant tous les convives ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 11, 'car quiconque s\'eleve lui-meme sera abaisse, et quiconque s\'abaisse lui-meme sera eleve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 12, 'Il disait aussi a son hote : Quand tu donnes a dejeuner ou a diner, ne convoque ni tes amis, ni tes freres, ni tes parents, ni tes riches voisins, de crainte qu\'ils ne t\'invitent a leur tour et ne te rendent ce qu\'ils auront recu de toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 13, 'Tout au contraire, quand tu fais un festin, appelles-y des pauvres, des infirmes, des estropies, des aveugles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 14, 'Heureux seras-tu, parce qu\'ils n\'ont pas de quoi te rendre ce festin ; et il te sera rendu a la resurrection des justes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 15, 'A ces mots, un des convives lui dit : Heureux qui sera du banquet dans le Royaume de Dieu! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 16, 'Un homme, lui dit Jesus, donna un grand diner, et y convia beaucoup de monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 17, 'A l\'heure du repas, il envoya son serviteur dire aux invites: Venez, parce que tout est pret.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 18, 'Mais tous, comme de concert, commencerent a s\'excuser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 19, 'J\'ai achete un champ, dit le premier, il est de toute necessite que j\'aille le voir. Je t\'en prie, tiens-moi pour excuse.  J\'ai achete cinq paires de boeufs, dit un autre, et je vais les essayer. Je t\'en prie, tiens-moi pour excuse. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 20, 'J\'ai pris femme, dit un autre encore, donc je ne puis venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 21, 'Le serviteur revint et raconta cela a son maitre. Se mettant en colere, le maitre de maison dit alors a son serviteur: Parcours a la hate les places publiques et les rues de la ville, et amene ici les pauvres, les infirmes, les aveugles, les estropies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 22, 'Quand le serviteur lui dit : Seigneur, on a fait ce que tu as ordonne, et il y a encore de la place,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 23, 'le maitre lui repondit : Va dans les chemins et le long des haies et contrains les gens d\'entrer, afin que ma maison soit pleine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 24, 'Je vous le declare, en effet, aucun de ces hommes qui etaient les invites ne prendra part a mon festin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 25, 'Il etait suivi de foules immenses ; il se tourna vers elles et leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 26, 'Si quelqu\'un vient a moi et ne hait pas et son pere et sa mere, et sa femme et ses enfants, et ses freres et ses soeurs, plus encore : sa propre vie, il ne peut etre mon disciple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 27, 'Celui qui ne porte pas sa croix, et ne marche pas a ma suite, ne peut etre mon disciple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 28, 'Quel est celui d\'entre vous qui, voulant batir une tour, ne reflechisse d\'abord, ne calcule la depense, ne voie s\'il a de quoi l\'achever?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 29, 'Il craindrait, apres avoir jete les fondements, de ne pouvoir achever. Tous ceux qui verraient cela se moqueraient de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 30, 'Le voila, diraient-ils, l\'homme qui a commence a batir et quia ete dans l\'impossibilite d\'achever !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 31, 'Quel est encore le roi, sur le point de faire la guerre a un autre roi, qui ne reflechisse d\'abord, qui n\'examine s\'il est capable, avec dix mille hommes, de marcher a la rencontre de celui qui s\'avance sur lui avec vingt mille?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 32, 'S\'il en est incapable, alors que l\'ennemi est encore loin, il lui envoie une ambassade avec des propositions de paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 33, 'Ainsi donc, quiconque ne renonce pas a tout ce qu\'il possede ne peut etre mon disciple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 34, 'Le sel est bon ; mais si le sel s\'affadit, avec quoi lui rendra-t-on sa saveur ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 14, 35, 'Il n\'est plus propre a rien, ni pour la terre, ni pour le fumier. On le jette dehors. Que celui qui a des oreilles pour entendre, entende.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 1, 'Les publicains et les pecheurs s\'approchaient tous de lui pour l\'entendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 2, 'Les Pharisiens et les Scribes en murmuraient : Cet homme, disaient-ils, fait accueil a des pecheurs, et mange avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 3, 'Alors il leur repondit par cette parabole:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 4, 'Si l\'un de vous a cent brebis et qu\'il en egare une, ne laisse-t-il pas dans le deserta les quatre-vingt-dix-neuf autres, pour aller chercher celle qui s\'est egaree, jusqu\'a ce qu\'il l\'ait retrouvee?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 5, 'Et quand il l\'a retrouvee, il la met, tout joyeux, sur ses epaules ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 6, 'et s\'en allant en sa maison, il appelle ses amis et ses voisins et leur dit : Rejouissez-vous avec moi, car j\'ai retrouve ma brebis perdue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 7, 'C\'est ainsi, je vous le declare, qu\'il y aura de la joie dans le ciel ; il y en aura davantage pour un seul pecheur repentant que pour quatre-vingt-dix-neuf justes qui n\'ont pas besoin de repentance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 8, 'Soit encore une femme qui possede dix drachmes. Si elle en perd une, est-ce qu\'elle n\'allume pas une lampe, ne balaye pas la maison, ne la cherche pas soigneusement jusqu\'a ce qu\'elle l\'ait retrouvee?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 9, 'Et quand elle l\'a retrouvee, elle appelle ses amies et ses voisines et leur dit: Rejouissez-vous avec moi, car j\'ai retrouve la drachme que j\'avais perdue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 10, 'C\'est ainsi, je vous le declare, qu\'il y a de la joie en presence des anges de Dieu pour un seul pecheur repentant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 11, 'Il dit encore : Un homme avait deux fils ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 12, 'le plus jeune dit a son pere : Pere, donne-moi la part de fortune qui me revient. Et le pere leur partagea son bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 13, 'Peu de jours apres, le plus jeune fils, rassemblant tout ce qu\'il avait, partit pour un pays lointain, et la il dissipa sa fortune dans une vie de debauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 14, 'Il avait tout depense quand survint, en ce pays-la, une tres rude famine. Il commenca a etre dans l\'indigence,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 15, 'et il alla se mettre au service de l\'un des habitants du pays, qui l\'envoya dans ses champs pour paitre des pourceaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 16, 'La, il desirait avidement se rassasier des siliques que mangeaient les pourceaux, mais personne ne lui en donnait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 17, 'Rentrant alors en lui-meme, il dit : Combien de gens a gages ont chez mon pere en abondance de quoi se nourrir, tandis que moi, ici, je meurs de faim!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 18, 'Je me leverai et j\'irai vers mon pere et je lui dirai: Pere, j\'ai peche contre le ciel et contre toi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 19, 'Je ne suis plus digne d\'etre appele ton fils! Traite-moi comme l\'un de tes mercenaires.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 20, 'Il se leva donc et retourna vers son pere. Comme il etait encore loin, son pere le vit ; il fut emu de compassion ; il accourut et, se laissant tomber a son cou, il le baisa longuement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 21, 'Le fils lui dit: Pere, j\'ai peche contre le ciel et contre toi! je ne suis plus digne d\'etre appele ton fils!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 22, 'Mais le pere dit a ses serviteurs : Apportez vite une robe de fete, la plus belle, et l\'en revetez ! mettez un anneau a sa main! des chaussures a ses pieds!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 23, 'et puis amenez le veau gras et le tuez! Faisons festin et rejouissance !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 24, 'parce que mon fils que voici etait mort, et il est revenu a la vie ! il etait perdu, et il est retrouve! Ils commencerent a se rejouir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 25, 'Or le fils aine etait dans les champs ; comme il revenait et approchait de la maison, il entendit de la musique et des danses ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 26, 'il appela un des domestiques et lui demanda ce que c\'etait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 27, 'Ton frere est de retour, lui repondit celui-ci, et ton pere a tue le veau gras, parce qu\'il l\'a recouvre sain et sauf.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 28, 'Alors il se mit en colere, et ne voulut pas entrer. Son pere sortit, et se mit a l\'en prier ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 29, 'mais lui, repondant a son pere, lui dit : Voila tant et tant d\'annees que je te sers ; je n\'ai jamais desobei a tes ordres, et tu ne m\'as jamais donne un chevreau pour faire fete avec mes amis ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 30, 'et quand est revenu ton autre fils, qui a devore son bien avec des femmes de mauvaise vie, pour lui tu as tue le veau gras!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 31, 'Mon enfant, lui dit le pere, tu es toujours avec moi, et tout ce qui est a moi est a toi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 15, 32, 'mais il fallait bien faire grande fete et rejouissance, parce que ton frere que voila etait mort, et il est revenu a la vie ! il etait perdu, et il est retrouve !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 1, 'Il disait aussi aux disciples : Un homme riche avait un econome ; cet econome lui fut denonce comme dissipant ses biens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 2, 'Il l\'appela et lui dit: Qu\'est-ce que j\'entends dire de toi ? Rends compte de ton administration ; car il n\'est plus possible que tu administres desormais. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 3, 'Que ferai-je? se dit en lui-meme l\'econome, puisque mon maitre me retire la gestion de ses biens. Travailler la terre, je n\'en ai pas la force ; mendier, j\'en aurais honte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 4, 'Je vois ce que je ferai pour que, destitue de mes fonctions, il se trouve des gens qui me recoivent dans leurs maisons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 5, 'Il convoqua l\'un apres l\'autre chacun des debiteurs de son maitre.Au premier il dit : Combien dois-tu a mon maitre ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 6, 'Cent mesures d\'huile,  Voici ton billet, reprit l\'econome, assieds-toi vite et ecris : cinquante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 7, 'Ensuite il dit a un autre : Et toi, combien dois-tu? Celui-la repondit : Cent mesures de froment. Voici ton billet, reprit encore l\'econome, et ecris: quatre-vingts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 8, 'Le maitre loua l\'econome infidele de ce qu\'il avait agi avec, habilete ; parce que les enfants de ce siecle sont, dans leur monde, plus avises que les enfants de la lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 9, 'Et moi, je vous dis : Du Mamon de l\'iniquite faites-vous des amis qui, lorsqu\'il viendra a manquer, vous recoivent dans les eternels tabernacles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 10, 'Qui est fidele dans une tres petite chose, l\'est egalement dans une grande ; qui est injuste dans une tres petite chose, l\'est egalement dans une grande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 11, 'Si donc vous n\'avez pas ete fideles dans le Mamon injuste, qui vous confiera le veritable?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 12, 'Et si vous n\'avez pas ete fideles dans le bien d\'un autre, qui vous donnera ce qui est a vous ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 13, 'Aucun domestique ne peut servir deux maitres ; ou bien, en effet, il haira l\'un et aimera l\'autre ; ou bien il s\'attachera au premier et meprisera le second. Vous ne pouvez servir Dieu et Mamon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 14, 'Les Pharisiens qui aiment l\'argent entendaient tout cela et tournaient Jesus en derision.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 15, 'Vous, leur dit-il, vous voulez vous faire passer pour justes devant les hommes, mais Dieu connait vos coeurs. Ce qui, d\'apres les hommes, est eleve, est, devant Dieu, une abomination.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 16, 'Jusques a Jean, la Loi et les Prophetes ; depuis, l\'Evangile du Royaume de Dieu est annonce et tous s\'efforcent de s\'en emparer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 17, 'Plus facilement passeront le ciel et la terre que ne tombera un seul petit trait de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 18, 'Quiconque repudie sa femme et en epouse une autre commet un adultere ; et celui qui epouse une femme repudiee par son mari commet un adultere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 19, 'Il y avait un riche qui etait vetu de pourpre et de lin, et qui menait tous les jours joyeuse et splendide vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 20, 'Un pauvre nomme Lazare gisait a sa porte, couvert d\'ulceres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 21, 'Il aurait bien voulu se rassasier de ce qui tombait de la table du riche ; mais les chiens eux-memes venaient lecher ses ulceres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 22, 'Or il arriva que le pauvre mourut, et qu\'il fut transporte par les anges dans le sein d\'Abraham. Le riche mourut aussi, et fut enseveli.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 23, 'Etant dans la Demeure-des-morts, et en proie aux tourments, il leva les yeux ; il vit de loin Abraham et, dans son sein, Lazare.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 24, 'Alors il eleva la voix ; il dit : Pere Abraham, aie pitie de moi ; et envoie Lazare tremper dans l\'eau le bout de son doigt pour rafraichir ma langue, car je suis torture dans ces flammes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 25, 'Mon enfant, lui repondit Abraham, souviens-toi que, en ta vie, tu as recu tes biens ; Lazare, dans la sienne, a eu des maux. Ici maintenant il est console, et toi tu es torture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 26, 'En outre, un abime immense est etabli entre nous et vous, de sorte qu\'il serait impossible a qui le voudrait soit d\'aller vers vous de la ou nous sommes, soit de venir vers nous de la ou vous etes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 27, 'Il reprit : Pere, envoie donc, je t\'en supplie, Lazare dans la maison de mon pere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 28, 'car j\'ai cinq freres ; il leur attestera ces choses afin qu\'ils ne viennent pas, eux aussi, dans ce lieu de tourments.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 29, 'Mais Abraham lui repondit : Ils ont Moise et les Prophetes ; qu\'ils les ecoutent! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 30, 'Non, pere Abraham, continua l\'autre, mais si, de chez les morts, quelqu\'un va vers eux, ils se repentiront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 16, 31, 'Abraham lui dit alors : S\'ils n\'ecoutent ni Moise, ni les Prophetes, quand meme quelqu\'un ressusciterait d\'entre les morts, ils ne seraient pas persuades.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 1, 'Il dit a ses disciples : Qu\'il n\'arrive pas de scandales, la chose est impossible, mais malheur a celui par qui le scandale arrive!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 2, 'Il vaudrait mieux pour lui qu\'on lui attachat au cou une meule de moulin, et qu\'on le jetat a la mer, que de scandaliser un de ces petits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 3, 'Prenez garde a vous : si ton frere a peche, reprends-le et s\'il se repent, pardonne-lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 4, 'Et s\'il a peche contre toi sept fois en un jour, et que sept fois en un jour, il se tourne vers toi et te dise : Je me repens, pardonne-lui. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 5, 'Augmente-nous la foi, dirent les apotres au Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 6, 'et le Seigneur leur repondit: Si vous aviez de la foi gros comme un grain de seneve, vous diriez a ce sycomore Deracine-toi, et plante-toi dans la mer, il vous obeirait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 7, 'Quand votre serviteur rentre des champs, apres le labour ou le paturage, lequel de vous lui dira : Viens vite te mettre a table.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 8, 'Ne lui dira-t-il pas au contraire: Prepare-moi a diner, ceins-toi, et me sers jusqu\'a ce que j\'aie mange et bu ; apres quoi,, tu mangeras et boiras toi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 9, 'Et parce que ce serviteur fait ce qui lui etait commande, son maitre lui doit-il quelque reconnaissance?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 10, 'De vous, il en est de meme: quand vous aurez fait tout ce qui vous est commande, dites ceci : Nous sommes des serviteurs inutiles ; ce que nous avons fait nous avions le devoir de le faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 11, 'Dans son voyage a Jerusalem, Jesus traversa la Samarie et la Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 12, 'Comme il arrivait a l\'entree d\'un village, se presenterent a lui dix lepreux qui, restant a une certaine distance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 13, 'eleverent la voix : Jesus! maitre! dirent-ils, aie pitie de nous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 14, 'Il les vit et leur dit : Allez vous montrer aux pretres. Et il arriva pendant qu\'il y allaient, qu\'ils furent guerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 15, 'L\'un d\'entre eux, se voyant gueri, revint sur ses pas, rendant a grands cris gloire a Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 16, 'puis il se prosterna aux pieds de Jesus, la face contre terre, lui rendant graces. C\'etait un Samaritain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 17, 'Alors Jesus prononca ces paroles : Est-ce que les dix n\'ont pas ete gueris? Les neuf autres ou sont-ils?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 18, 'Il ne s\'en est trouve aucun qui soit revenu pour rendre gloire a Dieu, sinon celui-ci: un etranger!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 19, 'Puis s\'adressant a lui: Leve-toi ; va, ta foi t\'a sauve');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 20, 'Les Pharisiens lui ayant demande quand viendrait le Royaume de Dieu il leur repondit ainsi : Le Royaume de Dieu ne vient pas avec des marques exterieures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 21, 'On ne dira pas : il est ici! ou: il est la! car sachez-le : le Royaume de Dieu est au milieu de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 22, 'Puis il dit aux disciples : Il viendra un temps ou vous desirerez voir un des jours du Fils de l\'homme, un seul, et vous ne le verrez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 23, 'On vous dira : Le voici!  le voila! N\'y allez pas ; ne le cherchez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 24, 'Tel, en effet, l\'eclair resplendissant a une extremite, du ciel brille jusqu\'a l\'autre, tel, en son jour, sera le Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 25, 'Mais il lui faut d\'abord beaucoup souffrir, et etre rejete par cette generation-ci.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 26, 'Ainsi etaient les choses aux jours de Noe, ainsi seront-elles aux jours du Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 27, 'Les hommes mangeaient, buvaient, se mariaient ou mariaient leurs enfants, jusqu\'au moment ou Noe entra dans l\'arche, et ou le deluge survint et les extermina tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 28, 'C\'est encore ce qui est arrive aux jours de Lot: les hommes mangeaient, buvaient, achetaient, vendaient, plantaient, batissaient ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 29, 'et au moment ou Lot sortit de Sodome, une pluie de feu et de soufre tomba du ciel et les extermina tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 30, 'Ainsi en sera-t-il le jour ou le Fils de l\'homme sera revele.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 31, 'Que celui qui sera, ce jour-la, sur le toit, et qui aura quelque objet dans la maison, ne descende pas pour l\'emporter. Que celui qui sera aux champs fasse de meme et ne revienne pas sur ses pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 32, 'Souvenez-vous de la femme de Lot!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 33, 'Qui cherchera a sauver sa vie la perdra, et qui la perdra, la rendra vraiment vivante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 34, 'Cette nuit-la, je vous le declare, de deux hommes qui seront dans un lit, l\'un sera pris, et l\'autre laisse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 35, 'De deux femmes qui moudront ensemble, l\'une sera prise, et l\'autre laissee. -');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 36, '(De deux hommes qui seront dans un champ, lun sera pris et lautre laisse).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 17, 37, '- Ou sera-ce Seigneur? demanderent les disciples. Il leur repondit : Ou sera le corps, la aussi les vautours se rassembleront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 1, 'Il leur raconta une parabole pour leur montrer qu\'il faut prier toujours, et ne point se lasser:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 2, 'Dans une certaine ville, dit-il, il y avait un juge qui n\'avait ni crainte de Dieu, ni egards pour les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 3, 'Or, dans cette meme ville, il y avait une veuve qui venait a lui et lui disait : Fais-moi justice de mon adversaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 4, 'Pendant longtemps, il ne le voulut pas. Enfin il se dit a lui-meme : Bien que je n\'aie ni crainte de Dieu, ni egards pour les hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 5, 'comme il est certain que cette veuve-la m\'est a charge, je lui ferai justice, de peur qu\'elle ne finisse par se livrer a quelque violence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 6, 'Vous entendez, ajouta le Seigneur, ce que dit ce juge inique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 7, 'Et Dieu ne ferait point justice a ses elus qui crient a lui nuit et jour? Il tarderait a les secourir?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 8, 'Il leur fera prompte justice, je vous le declare ; mais le Fils de l\'homme, a son retour, trouvera-t-il la foi sur la terre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 9, 'Il raconta aussi la parabole suivante a quelques-uns tres convaincus, a part eux, qu\'ils etaient justes, et qui meprisaient les autres:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 10, 'Deux hommes monterent au Temple pour prier ; l\'un Pharisien et l\'autre publicain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 11, 'Le Pharisien, debout, priait ainsi en lui-meme: O Dieu, je te rends graces de ce que je ne suis pas comme le reste des hommes, gens rapaces, iniques, adulteres, de ce que je ne suis pas comme ce publicain-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 12, 'Je jeune deux fois par semaine ; je donne la dime de tout ce que j\'acquiers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 13, 'Quant au publicain, il se tenait a distance, il n\'osait pas meme lever les yeux vers le ciel, mais il se frappait la poitrine en disant : Dieu, aie pitie de moi qui suis un pecheur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 14, 'Celui-ci, je vous le declare, rentra dans sa maison justifie, plutot que l\'autre ; car quiconque s\'eleve lui-meme sera abaisse, et quiconque s\'abaisse lui-meme sera eleve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 15, 'On lui apportait meme les tout petits enfants pour qu\'il les touchat. Voyant cela, les disciples blamerent ceux qui les lui apportaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 16, 'Mais Jesus appela ces enfants : Laissez, leur dit-il, les enfants venir a moi, et ne les en empechez pas, car c\'est a ceux qui leur ressemblent qu\'appartient le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 17, 'En verite, je vous le dis, qui ne recevra pas, comme un enfant, le Royaume de Dieu, n\'y entrera pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 18, 'Un chef de synagogue lui posa une question : Bon maitre, lui dit-il, que dois-je faire pour acquerir la vie eternelle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 19, 'Jesus lui dit : Pourquoi m\'appelles-tu bon ? Nul n\'est bon que Dieu seul!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 20, 'Tu connais les commandements: Tu ne seras point adultere ; tu ne seras point meurtrier ; tu ne seras point voleur ; tu ne rendras point de faux temoignages ; honore ton pere et ta mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 21, 'Il repondit : Tout cela je l\'ai observe des ma jeunesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 22, 'Sur cette reponse, Jesus reprit : Une chose te manque encore ;vends tout ce que tu as ; distribues-en le prix a des pauvres ; tu auras alors un tresor dans les cieux ; viens ensuite et suis-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 23, 'A ces mots, l\'autre devint tout triste, car il etait fort riche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 24, 'Voyant sa tristesse, Jesus dit : Combien difficilement entreront dans le Royaume de Dieu ceux qui ont des richesses !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 25, 'Il est plus facile a un chameau de passer par l\'ouverture d\'une aiguille qu\'a un riche d\'entrer dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 26, 'Qui donc pourra etre sauve ? dirent-ils en l\'entendant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 27, 'Jesus repondit : Ce qui est impossible aux hommes, est possible a Dieu. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 28, 'Et nous, dit Pierre, c\'est apres avoir laisse tous nos biens, que nous t\'avons suivi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 29, 'Il leur repondit: Je vous le dis en verite: personne, a cause du Royaume de Dieu, ne quittera une maison, une femme, des freres, des parents, des enfants,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 30, 'qu\'il ne recoive des le temps present bien davantage, et dans le siecle a venir la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 31, 'Prenant a part les douze, il leur dit: Voila que nous montons a Jerusalem et tout ce que les prophetes ont ecrit du Fils de l\'homme va s\'accomplir :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 32, 'il sera livre aux paiens, bafoue, outrage, couvert de crachats ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 33, 'apres l\'avoir flagelle, ils le tueront, et le troisieme jour il ressuscitera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 34, 'A cela les disciples ne comprirent rien ; le sens de ces paroles leur etait cache ; ils ne saisissaient pas ce qui leur etait dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 35, 'Il approchait de Jericho. Un aveugle, assis au bord du chemin, mendiait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 36, 'Entendant le bruit d\'une foule en marche, il demanda ce que c\'etait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 37, 'On lui repondit : C\'est Jesus de Nazareth qui passe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 38, 'Alors il s\'ecria : Jesus, Fils de David, aie pitie de moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 39, 'Ceux qui etaient en avant le menacaient pour le faire taire, mais lui n\'en criait que plus fort : Fils de David, aie pitie de moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 40, 'Jesus s\'arreta et donna l\'ordre de le lui amener. Quand l\'homme fut pres de lui, il lui demanda :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 41, 'Que veux-tu que je fasse pour toi ?  Seigneur, que j\'y voie ! repondit l\'aveugle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 42, 'Alors Jesus lui dit : Vois! ta foi t\'a sauve! Immediatement il vit, et il suivit Jesus en glorifiant Dieu. Tout le peuple, a ce spectacle, celebra les louanges de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 18, 43, '');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 1, 'Entre dans Jericho, Jesus traversait la ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 2, 'Il s\'y trouvait un personnage du nom de Zacchee : c\'etait un chef de publicains, un homme riche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 3, 'Il cherchait a voir qui etait Jesus ; mais, etant de petite taille, cela lui etait impossible, a cause de la foule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 4, 'Alors il courut en avant et, pour voir Jesus, monta sur un sycomore au pied duquel il devait passer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 5, 'Arrive en cet endroit, Jesus leva les yeux sur lui : Zacchee, dit-il, hate-toi de descendre ; car aujourd\'hui c\'est dans ta maison qu\'il faut que je m\'arrete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 6, 'Cet homme descendit a la hate et le recut plein de joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 7, 'Mais tous ceux qui voyaient cela murmuraient. Il va demeurer chez un pecheur ! disaient-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 8, 'Quant a Zacchee, il se tenait devant le Seigneur et lui disait : La moitie de mes biens, Seigneur, je la donne aux pauvres, et si j\'ai fait tort a quelqu\'un de quoi que ce soit, je lui en restitue quatre fois autant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 9, 'Alors Jesus dit, en se tournant vers Zacchee : Cette maison a recu aujourd\'hui le salut, parce que cet homme est, lui aussi, un fils d\'Abraham.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 10, 'Car le Fils de l\'homme est venu chercher et sauver ce qui est perdu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 11, 'On l\'ecoutait ; alors il continua en racontant une parabole ; et il comme il etait tout pres de Jerusalem et qu\'on croyait que le Royaume de Dieu allait tout a coup apparaitre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 12, 'voici ce qu\'il dit : Un homme de grande naissance avait a se rendre dans un pays eloigne pour recevoir l\'investiture d\'un royaume et revenir ensuite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 13, 'Il appela dix de ses serviteurs, leur remit dix mines, en leur disant: Faites valoir ceci jusqu\'a mon retour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 14, 'Les concitoyens de cet homme le haissaient et, apres son depart, ils envoyerent des delegues pour dire en leur nom : Nous ne voulons pas que cet homme regne sur nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 15, 'Cependant, quand il revint, il avait recu le pouvoir royal ; alors il fit appeler ses serviteurs, ceux a qui il avait donne l\'argent, pour savoir comment chacun l\'avait fait valoir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 16, 'Le premier se presenta et dit : Seigneur, ta mine en a produit dix. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 17, 'Bien, bon serviteur, lui repondit son maitre, pour avoir, en si peu de choses, ete fidele, tu auras la puissance sur dix villes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 18, 'Le second s\'approcha et dit : Ta mine, Seigneur, en a produit cinq. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 19, 'Eh bien, toi, repondit le maitre a celui-la, tu commanderas a cinq villes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 20, 'Un autre encore s\'approcha et dit : Seigneur, voici ta mine que j\'ai tenue enveloppee dans un linge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 21, 'Je te craignais, en effet, car tu es un homme terrible ; tu t\'empares de ce que tu n\'as pas, fonde ; tu moissonnes ce que tu n\'as pas seme. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 22, 'De ta propre bouche je tire ton jugement, mauvais serviteur, repondit le maitre ; ah ! tu savais que je suis un homme terrible, m\'emparant de ce que je n\'ai pas fonde, moissonnant ce que je n\'ai pas seme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 23, 'et pourquoi n\'as-tu pas place mon argent dans une banque ; a mon retour, je l\'aurais retire avec un interet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 24, 'Puis s\'adressant a ceux qui etaient la : Otez-lui la mine et donnez-la a celui qui en a dix. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 25, '(Seigneur, lui dirent-ils, il a dix mines).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 26, 'Je vous le declare, a tout homme qui a, il sera donne ; mais a celui qui n\'a pas, meme ce qu\'il a lui sera ote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 27, 'Quant a mes ennemis, a ceux qui n\'ont pas voulu que je regnasse sur eux, amenez-les ici et egorgez-les devant moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 28, 'Jesus parla ainsi, puis il partit devant, montant a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 29, 'Quand ils furent pres de Bethphage et de Bethanie, du cote du mont appele Bois d\'oliviers, Jesus envoya deux des disciples,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 30, 'en leur disant : Allez a ce village qui est devant vous ; a l\'entree, vous trouverez un anon attache, que personne n\'a jamais monte. Detachez-le et l\'amenez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 31, 'Si l\'on vous demande pourquoi vous le detachez, vous ferez cette reponse : Le Seigneur en a besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 32, 'Les messagers partirent et trouverent l\'anon, ainsi qu\'il le leur avait dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 33, 'Tandis qu\'ils le detachaient, ses maitres leur dirent : Pourquoi detachez-vous cet anon? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 34, 'Parce que le Seigneur en a besoin, repondirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 35, 'Ils conduisirent donc l\'anon, sur lequel ils jeterent leurs manteaux, a Jesus, qu\'ils firent monter sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 36, 'Quand il fut en marche, ils etendirent leurs vetements sur la route,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 37, 'et lorsqu\'il s\'approcha de la descente du mont des Oliviers, la multitude des disciples se mit tout entiere a jeter des cris de joie et a louer Dieu au sujet de tous les miracles qu\'ils avaient vus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 38, 'Ils disaient : Beni soit le Roi qui vient au nom du Seigneur! Paix dans le ciel et gloire au plus haut des cieux!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 39, 'Quelques Pharisiens qui etaient dans la foule lui dirent : Maitre, reprime tes disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 40, 'Il leur repondit par ces paroles : Je vous declare que si ceux-ci se taisent, les pierres crieront !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 41, 'Il approcha de la ville ; quand il la vit, il pleura sur elle, en disant :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 42, 'Si tu avais reconnu, toi aussi, du moins en ce jour, ce qui te pourrait apporter la paix ; mais, a l\'heure presente, tout cela est voile a tes regards.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 43, 'Des jours viendront sur toi ou tes ennemis t\'entoureront d\'une palissade, t\'enfermeront comme dans un cercle et te serreront de toutes parts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 44, 'ils te briseront contre le sol, toi et tes enfants qui sont dans ton sein, et ils ne laisseront pas en toi pierre sur pierre, parce que tu n\'as pas reconnu le temps de ta visitation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 45, 'Jesus entra dans le Temple et se mit a en chasser les vendeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 46, 'Il est ecrit, leur dit-il : Ma maison est une maison de prieres Et vous en avez fait une caverne de brigands.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 47, 'Chaque jour, dans le Temple, il enseignait. Les chefs des pretres et les Scribes cherchaient a le faire perir, ainsi que les principaux personnages de la nation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 19, 48, 'Mais ils ne trouvaient pas le moyen de le faire, a cause du peuple, qui, tout entier, etait suspendu a ses levres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 1, 'Un de ces jours-la, comme il instruisait le peuple dans le Temple et lui annoncait l\'Evangile, survinrent les chefs des pretres et les Scribes, reunis aux Anciens,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 2, 'qui lui tinrent ce langage : Dis-nous en vertu de quelle autorite tu fais ces choses, ou qui est celui qui t\'a donne cette autorite?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 3, 'Il leur fit cette reponse : Je vous poserai, moi aussi, une question : repondez-moi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 4, 'le bapteme de Jean venait-il du ciel ou des hommes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 5, 'Or ils faisaient, a part eux, ce raisonnement : Si nous disons : du ciel, il repliquera : Pourquoi alors ne l\'avez-vous pas cru ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 6, 'Si nous disons : des hommes, tout le peuple nous lapidera ; car il est convaincu que Jean etait un prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 7, 'Ils repondirent donc qu\'ils ne savaient d\'ou venait ce bapteme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 8, 'Et Jesus leur repondit a son tour : Je ne vous dis pas, moi non plus, en vertu de quelle autorite je fais ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 9, 'Puis il se mit a raconter au peuple cette parabole : Un homme planta une vigne, la loua a des vignerons et fit une longue absence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 10, 'La saison venue, il envoya un serviteur aux vignerons, charge de recevoir le produit de la vigne. Les vignerons, apres l\'avoir battu, le renvoyerent les mains vides.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 11, 'Le maitre alors leur envoya un autre serviteur. Celui-la, ils le battirent aussi, le traiterent avec mepris et le renvoyerent les mains vides.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 12, 'Il leur en envoya un troisieme. Celui-la ils le couvrirent de blessures et le jeterent dehors.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 13, ' Que dois-je faire? dit alors le maitre de la vigne. Je leur enverrai mon fils, mon bien-aime ; sans doute, ils le respecteront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 14, 'Mais quand les vignerons le virent, ils firent entre eux ce raisonnement : Celui-la c\'est l\'heritier ; tuons-le pour que l\'heritage soit a nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 15, 'Alors ils le jeterent hors de la vigne et le tuerent. Que leur fera donc le maitre de la vigne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 16, 'Il viendra, il fera perir ces vignerons et donnera la vigne, a d\'autres.  Que cela n\'arrive pas! dirent-ils a ces paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 17, 'Mais lui, les regardant en face, ajouta : Qu\'est-ce donc que ce passage de l\'Ecriture: La pierre qu\'ont rejetee ceux qui batissaient ; Celle-la meme est devenue la tete de l\'angle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 18, 'Quiconque tombera sur cette pierre s\'y brisera ; et celui sur qui elle tombera, elle le mettra en poussiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 19, 'Les Scribes et les chefs des pretres chercherent a mettre immediatement la main sur lui ; car ils comprenaient bien que c\'etait eux qu\'il avait en vue dans cette parabole ; mais ils redoutaient le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 20, 'Ils le surveillerent et lui envoyerent d\'habiles espions qui faisaient semblant d\'etre de bonne foi ; leur but etait de surprendre une parole de lui qui leur permit de le livrer aux autorites et au pouvoir du procurateur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 21, 'Ils lui poserent une question:Maitre, nous savons que tu es droit dans ta parole et dans ton enseignement, que tu ne fais acception de personne, que, tout au contraire, tu enseignes en toute verite la voie de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 22, 'Nous est-il permis, oui ou non, de payer un tribut a Cesar?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 23, 'Devinant leur ruse, Jesus leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 24, 'Montrez-moi un denier... De qui porte-t-il l\'image et l\'inscription?  De Cesar, repondirent-ils. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 25, 'Eh bien, leur dit-il alors, rendez a Cesar ce qui est a Cesar et a Dieu ce qui est a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 26, 'Il leur fut ainsi impossible de surprendre une parole de lui en presence du peuple, et tout etonnes de cette reponse, ils garderent le silence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 27, 'Survinrent ensuite quelques Saduceens (ceux qui affirment quil ny a point de resurrection) ; ils lui poserent aussi une question :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 28, 'Maitre, dirent-ils, voici ce que Moise nous a prescrit : Si le frere de quelqu\'un meurt laissant une femme sans enfants, celui-ci devra epouser la veuve, pour susciter a son frere une posterite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 29, 'Or, il y a eu sept freres. Le premier a pris femme et est mort sans enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 30, 'Le second a epouse la veuve ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 31, 'puis le troisieme ; tous les sept ; aucun n\'a laisse d\'enfants ; et tous sont morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 32, 'Enfin, la femme aussi est morte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 33, 'Eh bien, a la resurrection, duquel d\'entre eux cette femme sera-t-elle l\'epouse? Les sept, en effet, l\'ont eue pour femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 34, 'Jesus leur repondit : Les enfants de ce monde se marient et donnent en mariage ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 35, 'mais ceux qui ont ete juges dignes de participer au monde a venir et a la resurrection d\'entre les morts, ne se marient, ni ne sont donnes en mariage ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 36, 'car ils ne peuvent plus mourir : ils sont semblables aux anges, et ils sont fils de Dieu etant fils de la resurrection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 37, 'Or que les morts ressuscitent, Moise l\'a montre au passage du buisson, quand il appelle le Seigneur, le Dieu d\'Abraham, le Dieu d\'Isaac, le Dieu de Jacob.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 38, 'Or ce n\'est point de morts, c\'est de vivants qu\'il est Dieu. Pour lui ils vivent tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 39, 'Quelques Scribes intervinrent pour dire: Maitre, tu as parfaitement repondu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 40, 'Personne n\'osait plus lui poser une seule question.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 41, 'Il leur demanda alors: Comment dit-on que le Christ est Fils de David,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 42, 'quand David dit lui-meme au livre des Psaumes : Le Seigneur a dit a mon Seigneur : Siege a ma droite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 43, 'Jusqu\'a ce que j\'aie mis tes ennemis sous tes pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 44, 'Ainsi David l\'appelle Seigneur, comment donc est-il son fils?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 45, 'Devant tout le peuple qui entendait, il dit aux disciples :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 46, 'Soyez sur vos gardes avec les Scribes qui se complaisent a se promener en robes solennelles, qui aiment a recevoir des salutations sur les places publiques, a occuper les premiers sieges dans les synagogues, les premieres places dans les festins,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 20, 47, 'qui devorent, les ressources des veuves, et qui affectent de prier longuement. C\'est pour ces hommes-la que le jugement aura le plus de rigueur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 1, 'Tout en regardant, il vit des riches qui mettaient leurs offrandes, dans le tronc.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 2, 'Il vit aussi une veuve indigente qui y mit deux leptes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 3, 'et il dit : En verite, je vous le declare, cette veuve pauvre a donne plus que tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 4, 'Car tous ceux-la, pour leurs offrandes, ont puise dans leur superflu ; mais celle-ci a pris sur son indigence, elle a donne tout ce qu\'elle possedait, tout ce qu\'elle avait pour vivre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 5, 'Quelques-uns faisaient remarquer, a propos du Temple, les pierres magnifiques et les offrandes dont il etait orne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 6, 'Des jours viendront, dit-il, ou de ce que vous voyez, il ne restera pas ici pierre sur pierre ; tout sera renverse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 7, 'Alors ils le questionnerent: Maitre, lui dirent-ils, quand donc ces choses arriveront-elles? et quel sera le signe qu\'elles vont avoir lieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 8, 'Il repondit : Prenez garde de vous laisser seduire ; car plusieurs viendront en prenant mon nom, disant : C\'est moi ! et : Le moment approche!. Ne marchez pas a leur suite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 9, 'Quand vous entendrez parler de guerres et de revolutions, n\'en soyez pas effrayes. Il faut, en effet, que cela arrive d\'abord ; mais ce ne sera pas tout de suite la fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 10, 'Il continua ainsi : Se soulevera nation contre nation, royaume contre royaume ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 11, 'il y aura de vastes tremblements de terre ; ici et la des famines, des pestes, d\'effrayantes choses et de grands prodiges au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 12, 'Mais, avant tout, on mettra la main sur vous, et l\'on vous persecutera. Vous serez livres aux synagogues, jetes en prison ; et, a cause de mon nom, traines devant des rois et des procurateurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 13, 'Cela vous arrivera pour que vous rendiez temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 14, 'Gravez bien dans vos coeurs que vous n\'aurez pas a vous preoccuper de votre defense,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 15, 'car je vous donnerai, moi, un langage et une sagesse auxquels tous vos adversaires ne pourront ni resister ni repliquer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 16, 'Vous serez livres par vos peres et vos meres, par vos freres, par vos parents, par vos amis ; ils feront mourir nombre d\'entre vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 17, 'et vous serez en haine a tout le monde a cause de mon nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 18, 'Et cependant pas un cheveu de votre tete ne doit perir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 19, 'c\'est par votre patience que vous sauverez vos vies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 20, 'Lorsque vous verrez des armees camper autour de Jerusalem et l\'investir, sachez alors qu\'approche sa desolation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 21, 'Que ceux qui seront dans la Judee fuient dans les montagnes, que ceux qui seront au milieu du pays en sortent, que ceux qui seront dans les contrees voisines n\'y rentrent pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 22, 'Ces jours-la, en effet, seront des jours vengeurs ou s\'accomplira tout ce qui est ecrit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 23, 'Malheur aux femmes qui seront enceintes et a celles qui allaiteront en ces jours-la! car il y aura sur la terre une calamite immense et une colere contre ce peuple ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 24, 'les hommes tomberont sous le tranchant du glaive, ils seront emmenes captifs dans toutes les nations, Jerusalem sera foulee aux pieds par les paiens, jusqu\'a ce que les temps des paiens soient aussi accomplis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 25, 'Il y aura des prodiges dans le soleil, dans la lune et dans les etoiles ; il y en aura sur la terre : angoisse des peuples jetes dans l\'incertitude par le mugissement de la mer et des flots ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 26, 'des hommes expirant de terreur dans l\'attente de ce qui va arriver au monde, car les puissances des cieux seront ebranlees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 27, 'Et c\'est alors qu\'on verra le Fils de l\'homme arrivant, au sein d\'une nuee, en puissance et grande gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 28, 'Quand ces choses commenceront a arriver, regardez en haut, levez la tete, parce qu\'approchera votre delivrance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 29, 'Il se servit d\'une comparaison : Voyez le figuier, et tous les arbres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 30, 'Des qu\'ils commencent a pousser, vous savez de vous-memes, en les voyant, que l\'ete est proche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 31, 'De meme quand vous verrez arriver ces choses sachez que le royaume de Dieu est proche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 32, 'En verite, je vous le dis, cette generation ne passera pas que tout cela n\'arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 33, 'Le ciel et la terre passeront, mais mes paroles ne passeront point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 34, 'Veillez sur vous-memes, de peur que vos coeurs ne s\'appesantissent dans la bonne chere, dans les exces de boisson, dans les soucis de ce monde, et que ce jour-la ne vous surprenne inopinement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 35, 'Car il surviendra comme un filet sur tous ceux qui habitent la surface de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 36, 'Soyez donc vigilants en tout temps, et priez afin que vous soyez rendus dignes d\'echapper a toutes ces choses qui vont arriver et de subsister en presence du Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 37, 'Le jour, il restait au Temple et y enseignait. Mais il allait passer les nuits hors de la ville sur la montagne appelee Bois d\'Oliviers,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 21, 38, 'et tout le peuple, de grand matin, venait au Temple pour l\'entendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 1, 'La fete des Azymes, que l\'on appelle la Paque, approchait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 2, 'Les chefs des pretres et les Scribes cherchaient un moyen de se defaire de Jesus ; car ils redoutaient le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 3, 'Ce fut alors que Satan entra en Judas surnomme l\'Iskariote, qui etait du nombre des douze ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 4, 'il alla s\'entendre avec les chefs des pretres et avec les chefs des gardes sur ce qu\'il y avait a faire pour le leur livrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 5, 'Pleins de joie, ils convinrent de lui donner de l\'argent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 6, 'Il s\'engagea, et des lors chercha une occasion de le leur livrer a l\'insu de la multitude.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 7, 'Vint le jour de la fete ou il fallait sacrifier la Paque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 8, 'Jesus envoya Pierre et Jean en leur disant : Allez preparer ce qui nous est necessaire pour le repas pascal. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 9, 'Ou veux-tu, lui dirent-ils, que nous le preparions?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 10, 'Il leur repondit : En entrant dans la ville, vous rencontrerez un homme portant une cruche d\'eau. Vous le suivrez dans la maison ou il entrera ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 11, 'et voici ce que vous direz au proprietaire de cette maison : Le maitre te fait dire : Ou est la chambre ou je mangerai la Paque avec mes disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 12, 'Cet homme alors vous montrera une chambre haute, grande, garnie de tapis. C\'est la que vous ferez les preparatifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 13, 'Ils partirent et trouverent toutes choses comme il les leur avait dites et preparerent la Paque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 14, 'Quand vint l\'heure, il se mit a table, et les apotres avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 15, 'Il leur dit : J\'ai desire  d\'un grand desir  manger cette Paque avec vous avant de souffrir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 16, 'Je vous le declare, en effet, je ne mangerai plus semblable Paque, jusqu\'a ce qu\'elle s\'accomplisse dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 17, 'Tenant une coupe, il rendit graces. Prenez-la, dit-il, et partagez-la entre vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 18, 'car desormais, je vous le declare, je ne boirai plus du fruit de la vigne jusqu\'a ce que vienne le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 19, 'Prenant du pain, il rendit graces, il le rompit et le leur donna, en disant : Ceci est mon corps qui est donne pour vous ; faites ceci en memoire de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 20, 'Apres le repas, il prit de meme la coupe, en disant : Cette coupe est la nouvelle alliance en mon sang qui est repandu pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 21, 'Et voila cependant avec moi, a cette table, la main de celui qui me livre!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 22, 'car le Fils de l\'homme s\'en va selon ce qui a ete determine, mais malheur a cet homme par qui il est livre!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 23, 'Ils se mirent a se demander de l\'un a l\'autre lequel d\'entre eux devait faire cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 24, 'Il s\'eleva aussi un debat sur celui d\'entre eux qui devait etre estime le plus grand.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 25, 'Les rois des nations, leur dit Jesus, en sont les dominateurs ; et ceux qui exercent sur elles un pouvoir imperieux sont appeles leurs bienfaiteurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 26, 'Pour vous, n\'agissez point ainsi : au contraire, que le plus grand d\'entre vous se fasse comme le plus jeune, et que celui qui est le chef se considere comme le serviteur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 27, 'Lequel passe pour le plus grand? Celui qui est a table ou celui qui sert? N\'est-ce pas celui qui est a table? Moi, pourtant, je suis, au milieu de vous, comme celui qui sert.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 28, 'Quant a vous, vous m\'etes demeures fideles dans mes epreuves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 29, 'Mon Pere m\'a adjuge un Royaume ; eh bien, moi, je vous l\'adjuge a mon tour,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 30, 'afin que, dans ce Royaume qui sera le mien, vous mangiez et buviez a ma table, afin aussi que vous y siegiez sur des trones jugeant les douze tribus d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 31, 'Simon! Simon! voila que Satan vous a reclames pour vous passer tous au crible comme du froment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 32, 'Moi, cependant, j\'ai prie pour toi, afin que ta foi ne defaille pas. Et toi, quand tu seras un jour change, fortifie tes freres. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 33, 'Avec toi, Seigneur, lui dit Pierre, je suis pret a aller en prison, a la mort! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 34, 'Pierre, lui repondit Jesus, je te declare qu\'aujourd\'hui meme un coq n\'aura pas acheve de chanter que, trois fois, tu n\'aies nie me connaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 35, 'Il continua : Lorsque je vous ai envoyes sans bourse, sans sac, sans chaussures, vous a-t-il manque quelque chose?  Rien, repondirent-ils. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 36, 'Mais a present, leur dit-il, que celui qui a une bourse la prenne ; et un sac, de meme ; que celui qui n\'a pas d\'epee vende son manteau pour en acheter une ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 37, 'car, je vous le declare, il faut que cette parole de l\'Ecriture s\'accomplisse en mois : ... Et il a ete compte parmi les impies. En effet, ce qui me concerne tire a sa fin. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 38, 'Seigneur, dirent-ils, voici deux epees.  Cela suffit, leur repondit Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 39, 'Il sortit, et, selon sa coutume, il se dirigea vers le mont des Oliviers. Les disciples le suivirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 40, 'Arrive en ce lieu, il leur dit : Priez pour ne pas succomber a l\'epreuve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 41, 'Il s\'eloigna d\'eux, a la distance d\'un jet de pierre environ ; il se mit a genoux, et voici ce qu\'il disait dans sa priere :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 42, 'Pere, si tu voulais ecarter loin de moi cette coupe! toutefois que ce ne soit pas ma volonte qui se fasse, mais la tienne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 43, 'Alors il lui apparut un ange venu du ciel, qui le fortifia.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 44, 'Etant en agonie, plus ardente etait sa priere. Sa sueur etait comme de grosses gouttes de sang et tombait jusqu\'a terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 45, 'Apres avoir prie, il se releva et vint a ses disciples. Il les trouva qui s\'etaient endormis dans l\'accablement de leur douleur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 46, 'Pourquoi dormez-vous, leur dit-il, levez-vous, priez pour ne pas succomber a l\'epreuve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 47, 'Il parlait encore, lorsqu\'arriva toute une foule, en avant de laquelle marchait le nomme Judas, un des douze. Il s\'approcha de Jesus pour lui donner un baiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 48, 'Judas! lui dit Jesus, c\'est par un baiser que tu livres le Fils de l\'homme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 49, 'Voyant ce qui allait se passer, ceux qui entouraient Jesus lui dirent : Seigneur, si nous frappions de l\'epee?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 50, 'Et l\'un d\'entre eux frappa le serviteur du grand-pretre et lui coupa l\'oreille droite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 51, 'Mais Jesus prit la parole : Restez-en la! dit-il, et touchant l\'oreille de l\'homme blesse, il le guerit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 52, 'Jesus dit ensuite a ceux qui etaient venus a lui, chefs des pretres, chefs des gardes du Temple et Anciens : Vous etes venus comme contre un brigand, avec des epees et des batons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 53, 'Tous les jours j\'etais avec vous dans le Temple, et vous n\'avez pas mis la main sur moi. Mais cette heure-ci est votre, cette heure ou les tenebres regnent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 54, 'Ils s\'emparerent de lui, l\'emmenerent et le conduisirent a la demeure du grand-pretre. Pierre suivait de loin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 55, 'Au milieu de la cour ils allumerent du feu, ils s\'assirent tout autour, et Pierre aussi s\'assit parmi eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 56, 'Une servante le vit assis a la clarte du foyer, et, le regardant attentivement elle lui dit : Et celui-la, il etait avec lui!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 57, 'Et Pierre, reniant Jesus, dit a cette femme : Je ne le connais pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 58, 'Un instant apres, un autre dit en le voyant: Toi aussi, tu en es!  Non, je n\'en suis pas, dit Pierre a cet homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 59, 'Apres un intervalle d\'une heure environ, un autre devint tout a fait affirmatif : Il est certain, dit-il, que celui-ci etait avec lui, car c\'est egalement un Galileen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 60, 'Mais Pierre dit a cet homme : Je ne comprends pas ce que tu dis. A l\'instant meme, pendant qu\'il parlait encore, un coq chanta.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 61, 'Le Seigneur se retourna et regarda Pierre. Alors Pierre se souvint de la parole du Seigneur ; il se rappela comment il lui avait dit : Avant qu\'un coq chante aujourd\'hui, tu me renieras, trois fois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 62, 'Il sortit, et, dehors, il pleura amerement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 63, 'Ceux qui gardaient Jesus se faisaient un jeu de lui donner des coups ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 64, 'ils lui avaient couvert le visage d\'un voile et puis ils l\'interrogeaient : Fais le prophete! quel est celui qui t\'a frappe?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 65, 'et ils proferaient beaucoup d\'autres injures contre lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 66, 'Quand il fit jour, s\'assembla le corps des Anciens du peuple avec des chefs des pretres et des Scribes. Ils firent comparaitre Jesus par devant leur Sanhedrin et lui parlerent ainsi : Si tu es le Christ, dis-le-nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 67, 'Il leur repondit : Quand meme je vous le dirais, vous ne me croiriez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 68, 'Et si je vous interrogeais, vous ne me repondriez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 69, 'A partir de maintenant le Fils de l\'homme siegera a la droite de la puissance de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 70, 'Ils dirent tous : C\'est donc toi qui es le Fils de Dieu?  Vous dites vous-memes que je le suis, leur repondit Jesus. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 22, 71, 'Qu\'avons-nous encore besoin de temoignage, dirent-ils ; le temoignage, nous venons nous-memes de l\'entendre de sa propre bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 1, 'Tous alors se leverent en foule et le conduisirent a Pilate!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 2, 'Leur premier chef d\'accusation fut : Nous avons trouve cet homme excitant notre peuple a la revolte, l\'empechant de payer le tribut a Cesar, et se disant le Christ-roi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 3, 'Pilate l\'interrogea : C\'est toi qui es le roi des Juifs? lui demanda-t-il. Jesus repondit ainsi : Tu le dis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 4, 'Pilate, s\'adressant aux chefs des pretres et a la foule : Chez cet homme je ne trouve rien de coupable, leur dit-il.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 5, 'Eux pourtant insistaient avec force, disant : Il agitait le peuple, il parcourait toute la Judee, en enseignant ; et c\'est apres avoir commence par la Galilee qu\'il est arrive jusqu\'ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 6, 'A ces paroles, Pilate demande si l\'homme etait Galileen ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 7, 'et, apprenant qu\'il, etait sujet d\'Herode, il l\'envoya a celui-ci, lequel se trouvait a Jerusalem ces jours-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 8, 'Pour Herode, voir Jesus, fut une grande joie. Il y avait longtemps qu\'il souhaitait de le connaitre ; il avait entendu parler de lui ; et il esperait lui voir faire un miracle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 9, 'Aussi l\'interrogea-t-il longuement ; mais Jesus ne lui repondit rien. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 10, 'Quant aux chefs des pretres et aux Scribes, ils etaient la debout l\'accusant sans relache. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 11, 'Alors Herode ne fit plus aucun cas de Jesus ; ses soldats et lui le raillerent, et il le renvoya a Pilate apres l\'avoir affuble d\'une robe blanche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 12, 'Herode et Pilate, qui avaient vecu jusque-la en inimitie, devinrent amis, a dater de ce jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 13, 'Reunissant les chefs des pretres, les magistrats et le peuple,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 14, 'Pilate leur dit : Vous m\'avez presente cet homme comme excitant le peuple a la revolte, et voila qu\'apres enquete faite en votre presence, je ne l\'ai trouve en rien coupable de ce dont vous l\'accusez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 15, 'Herode, non plus, car il nous l\'a renvoye. Ainsi il n\'a rien fait qui merite la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 16, 'Je vais donc, apres l\'avoir fait frapper de verges, lui rendre la liberte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 17, '(A chaque fete, il etait oblige de leur relacher un prisonnier).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 18, 'Alors tous, d\'une seule voix, se mirent a crier A mort celui-la! delivre-nous Bar-Abbas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 19, '(C\'etait un homme qui avait ete incarcere pour avoir commis un meurtre a la suite d\'une emeute qui avait eu lieu dans la ville.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 20, 'Pilate, qui desirait delivrer Jesus, leur parla de nouveau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 21, 'Ils lui repondirent par des cris : Crucifie-le! crucifie-le!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 22, 'Pour la troisieme fois, il leur demanda : Mais quel mal a-t-il fait, cet homme? Je ne le trouve coupable de rien qui merite la mort. Je vais donc, apres l\'avoir fait frapper de verges, lui rendre la liberte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 23, 'Mais ils insistaient, ils demandaient a grands cris qu\'on le crucifiat, et ce furent leurs cris qui l\'emporterent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 24, 'Pilate prononca une sentence conforme a leur demande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 25, 'Il mit en liberte, a leur requete, celui qui avait ete incarcere pour emeute et pour meurtre, et il abandonna Jesus a leur volonte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 26, 'Comme ils l\'emmenaient, ils rencontrerent un certain Simon de Cyrene qui revenait des champs, et ils le chargerent de porter la croix derriere Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 27, 'Une immense multitude de peuple le suivait, ainsi que des femmes qui se lamentaient et le pleuraient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 28, 'Se tournant vers elles, Jesus dit : Filles de Jerusalem, ne pleurez pas sur moi, mais pleurez sur vous et sur vos enfants ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 29, 'voici, en effet, que viennent des jours ou l\'on dira : Heureuses les steriles ; heureuses les entrailles qui n\'ont point enfante, et les mamelles qui n\'ont point allaite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 30, 'On se mettra alors a dire aux montagnes : Tombez sur nous! Et aux collines : Recouvrez-nous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 31, 'Car si le bois vert est ainsi traite, qu\'arrivera-t-il au bois sec?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 32, 'On emmenait aussi deux autres hommes, des malfaiteurs, pour les mettre a mort avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 33, 'Ils arriverent a l\'endroit qui porte le nom de Crane. La on le crucifia, ainsi que les deux malfaiteurs, l\'un a sa droite, l\'autre a sa gauche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 34, 'Jesus disait : Pere! pardonne-leur, car ils ne savent ce qu\'ils font! Ses vetements furent partages apres avoir ete tires au sort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 35, 'Le peuple se tenait tout autour et regardait. Quant a ceux qui etaient les chefs de la nation, ils se moquaient, disant : Il en a sauve d\'autres ; qu\'il se sauve lui-meme s\'il est le Christ, l\'Elu de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 36, 'Les soldats aussi le raillaient ; ils, s\'approchaient, ils lui offraient du vinaigre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 37, 'ils lui disaient : Si tu es le Roi des Juifs, sauve-toi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 38, 'Au-dessus de lui, il y avait une inscription : CELUI-CI EST LE ROI DES JUIFS.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 39, 'Un des malfaiteurs suspendus en croix l\'injuriait : N\'es-tu pas le Christ? Sauve-toi et nous avec toi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 40, 'Mais l\'autre eleva la voix pour le reprendre : Est-ce que, toi non plus, tu ne crains pas Dieu, lui dit-il ; toi qui subis le meme supplice ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 41, 'et pour nous ce n\'est que justice ; nous recevons ce qu\'ont merite nos actes ; mais celui-ci n\'a rien fait de mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 42, 'Et il disait : Jesus, souviens-toi de moi quand tu viendras en ton Royaume!. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 43, 'En verite, je te le dis, lui repondit Jesus, aujourd\'hui tu seras avec moi dans le Paradis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 44, 'Il etait deja la sixieme heure environ et, le soleil s\'etant eclipse, des tenebres se firent sur tout le pays jusqu\'a la neuvieme heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 45, 'Le rideau du Temple se dechira par le milieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 46, 'et Jesus dit, en jetant un grand cri : Pere, Je remets mon esprit entre tes mains. En disant cela, il expira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 47, 'Voyant ce qui se passait, le centurion glorifia Dieu, disant : Certainement cet homme etait juste!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 48, 'Et tous ceux qui avaient assiste en foule a ce spectacle et avaient ete temoins de ce qui etait arrive, s\'en retournaient en se frappant la poitrine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 49, 'Quant a ceux qui l\'avaient connu, ils se tenaient tous a distance, ainsi que des femmes qui l\'avaient suivi depuis la Galilee, et ils regardaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 50, 'Survint un membre du Grand-Conseil appele Joseph. C\'etait un homme juste et bon,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 51, 'qui ne s\'etait associe ni aux projets ni aux actes des autres. (Il etait d\'Arimathee, ville des Juifs.) Lui aussi attendait le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 52, 'Il alla trouver Pilate et demanda le corps de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 53, 'Puis il le descendit de la croix, l\'ensevelit dans un linceul, et le deposa dans un sepulcre taille dans le roc, ou personne encore n\'avait ete place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 54, 'C\'etait un jour de Preparation et un sabbat allait commencera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 55, 'Des femmes, celles qui etaient venues avec Jesus de Galilee, ayant accompagne Joseph, virent le tombeau et comment y fut place le corps ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 23, 56, 'puis elles s\'en retournerent preparer des aromates et des parfums. Selon le commandement, elles se tinrent en repos le jour du, sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 1, 'Mais le premier jour de la semaine, de tres grand matin, elles se rendirent au tombeau, portant avec elles les aromates qu\'elles avaient prepares.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 2, 'Elles s\'apercurent que la pierre avait ete eloignee de l\'ouverture du sepulcre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 3, 'Elles entrerent et ne trouverent pas le corps du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 4, 'Elles en etaient consternees ; mais voila que deux hommes se presenterent a elles, vetus de robes resplendissantes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 5, 'Et comme, tout effrayees, elles inclinaient leurs visages vers la terre, ils leur dirent : Pourquoi cherchez-vous parmi les morts celui qui est vivant?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 6, 'Il n\'est pas ici ; tout au contraire, il est ressuscite. Souvenez-vous comment il vous a parle quand il etait encore en Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 7, 'Il faut, disait-il, que le Fils de l\'homme soit livre entre les mains des pecheurs, qu\'il soit crucifie et qu\'il ressuscite le troisieme jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 8, 'Elles se souvinrent alors de ses paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 9, 'De retour du tombeau, elles annoncerent tout cela aux onze et a tous les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 10, 'C\'etaient Marie Magdeleine, Jeanne, Marie mere de Jacques, et quelques autres avec elles qui raconterent cela aux apotres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 11, 'Et leurs dires leur firent l\'effet d\'une absurdite et ils ne les crurent pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 12, '(Pierre, cependant, se leva et courut au sepulcre. Setant baisse, il ne vit que des linges qui etaient a terre, puis il retourna chez lui, surpris de ce qui etait arrive.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 13, 'Ce meme jour, deux d\'entre les disciples s\'en allaient a un village situe a soixante stades de Jerusalem, nomme Emmaus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 14, 'et ils s\'entretenaient ensemble de tout ce qui venait de se passer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 15, 'Tandis qu\'ils s\'entretenaient ainsi et discutaient, Jesus lui-meme s\'approcha et se mit a marcher avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 16, 'Mais une force empechait leurs yeux de le reconnaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 17, 'Quelles sont, leur dit-il, ces paroles que vous echangez l\'un et l\'autre tout en marchant? Ils etaient pleins de tristesse');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 18, 'et l\'un d\'eux, appele Cleopas, lui repondit. Tu es bien seul, lui dit-il, et bien etranger a Jerusalem si tu ne sais pas ce qui y est arrive ces jours-ci. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 19, 'Quoi donc?, leur demanda-t-il. Ils lui repondirent: L\'affaire de Jesus de Nazareth qui fut un prophete puissant en oeuvres et en paroles devant Dieu et devant tout le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 20, 'Les chefs des pretres et nos magistrats l\'ont livre pour le faire condamner a mort et l\'ont crucifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 21, 'Nous, nous esperions qu\'il etait celui qui doit delivrer Israel. Mais, avec tout cela, voici le troisieme jour que ces choses sont arrivees!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 22, 'De plus, certaines femmes qui sont des notres nous ont fort effrayes. Elles sont allees de grand matin au tombeau');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 23, 'et n\'y ont pas trouve son corps ; et elles sont venues nous dire qu\'elles ont meme vu une apparition d\'anges. Ces anges leur ont dit qu\'il est vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 24, 'Alors quelques-uns des notres sont alles au tombeau ; ils ont trouve toutes choses comme les femmes avaient dit ; mais, lui, ils ne l\'ont pas vu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 25, 'O insenses! leur dit-il alors, coeurs lents a croire tout ce qu\'ont dit les prophetes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 26, 'Ne fallait-il pas que le Christ souffrit ces choses et entrat dans sa gloire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 27, 'Et commencant par Moise et par tous les Prophetes, il leur expliquait dans toutes les Ecritures ce qui le concernait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 28, 'Ils approchaient du village ou ils se rendaient et lui faisait semblant d\'aller plus loin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 29, 'Mais ils le retinrent. Demeure avec nous, lui dirent-ils, car il se fait tard ; le jour est deja sur son declin. entra pour demeurer avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 30, 'Comme ils etaient ensemble a table, il prit le pain, prononca la benediction, le rompit et le leur donna.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 31, 'Leurs yeux s\'ouvrirent, et ils le reconnurent, mais lui-meme avait disparu de devant eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 32, 'N\'est-ce pas, se dirent-ils l\'un a l\'autre, que notre coeur etait tout enflamme en nous-memes, lorsqu\'il nous parlait dans le chemin et qu\'il nous ouvrait le sens des Ecritures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 33, 'Ils se leverent a l\'heure meme, retournerent a Jerusalem et trouverent reunis les onze et ceux qui etaient des leurs ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 34, 'ceux-ci leur dirent : Le Seigneur est vraiment ressuscite et il est apparu a Simon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 35, 'Quant a eux, ils raconterent ce qui s\'etait passe dans le chemin, et comment ils l\'avaient reconnu a la fraction du pain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 36, 'Pendant qu\'ils parlaient, Jesus parut au milieu d\'eux et leur dit : La paix soit avec vous !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 37, 'Troubles, epouvantes, ils croyaient voir un Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 38, 'Pourquoi vous troublez-vous, leur dit-il, et pourquoi ces hesitations qui montent dans vos coeurs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 39, 'Voyez mes mains et mes pieds, c\'est bien moi. Touchez-moi ; regardez-moi ; un Esprit n\'a ni chair ni os comme vous voyez que j\'en ai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 40, '(En leur disant cela, il leur montra ses mains et ses pieds.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 41, 'Comme dans leur stupefaction et leur joie ils ne croyaient point encore, il leur demanda : Avez-vous ici quelque chose a manger?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 42, 'Ils lui presenterent un morceau de poisson grille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 43, 'Il le prit et le mangea devant eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 44, 'Il ajouta: Voila ce que je vous disais quand j\'etais encore avec vous et vous parlais. Je vous disais qu\'il fallait que fut accompli tout ce qui est ecrit de moi dans la Loi de Moise, dans les Prophetes et dans les Psaumes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 45, 'Et la-dessus il leur ouvrit l\'intelligence pour qu\'ils comprissent les Ecritures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 46, 'C\'est ainsi, leur dit-il, qu\'il est ecrit que le Christ souffrira et ressuscitera d\'entre les morts le troisieme jour,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 47, 'et que la repentance et la remission des peches seront prechees en son nom parmi toutes les nations, a commencer par Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 48, 'Vous, vous etes temoins de ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 49, 'Et moi, j\'enverrai sur vous ce que mon Pere a promis. Vous demeurerez donc dans la ville, jusqu\'a ce que vous ayez ete revetus d\'une force d\'en haut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 50, 'Puis il les emmena jusque vers Bethanie et, levant les mains, il les benit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 51, 'Et, tandis qu\'il les benissait, il se separa d\'eux et fut eleve au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 52, 'Ils tomberent en adoration, puis, le coeur plein de joie, ils retournerent a Jerusalem,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (42, 24, 53, 'ou ils etaient constamment dans le Temple, benissant Dieu. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 1, 'Au commencement etait la Parole ; et la Parole etait avec Dieu ; et la Parole etait Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 2, 'Cette Parole etait au commencement avec Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 3, 'Toutes choses sont nees par elle, et absolument rien de ce qui existe n\'a pris naissance sans elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 4, 'En elle il y avait vie, et la vie etait la lumiere des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 5, 'Et la lumiere eclaire dans les tenebres ; et les tenebres ne l\'ont pas acceptee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 6, 'Il a paru un homme, envoye de la part de Dieu ; il s\'appelait Jean ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 7, 'cet homme est venu pour etre temoin. Il est venu temoigner en faveur de la lumiere, pour que tous devinssent croyants par lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 8, 'Il n\'etait pas lui-meme la lumiere ; mais il etait la pour temoigner en faveur de la lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 9, 'Elle existait, cette parfaite lumiere qui eclaire tout homme venant au monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 10, 'Elle existait dans le monde ; et le monde avait pris naissance par elle et le monde ne l\'avait pas connue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 11, 'Elle est venue dans sa propre demeure, et les siens ne l\'ont pas accueillie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 12, 'mais a tous ceux qui l\'ont accueillie, elle a accorde le privilege de devenir enfants de Dieu ; elle l\'a accorde a ceux qui ont cru en son nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 13, 'Ceux-la ne sont pas nes au sens physique par la volonte de la chair et de l\'homme, mais ils sont nes de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 14, 'Oui, la Parole est devenue chair, et elle a dresse sa tente au milieu de nous, et nous avons contemple sa gloire, la gloire parfaite d\'un Fils unique envoye par son Pere, tout rempli de grace et de verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 15, 'Jean lui rend son temoignage dans ce cri qu\'il a jete : C\'etait de lui que je disais : Celui qui doit venir apres moi, m\'a devance, parce qu\'il a ete avant moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 16, 'En effet, de sa plenitude nous avons tous recu, et grace apres grace.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 17, 'Car la Loi a ete donnee par Moise ; la grace et la verite sont venues par Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 18, 'Personne n\'a jamais vu Dieu ; le Fils unique qui est dans le sein du Pere, voila celui qui l\'a explique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 19, 'Voici quel fut le temoignage de Jean lorsque les Juifs envoyerent de Jerusalem des pretres et des levites lui poser cette question : Qui es-tu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 20, 'Il s\'expliqua formellement et sans reserve ; il s\'expliqua ainsi : Je ne suis pas, moi, le Christ. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 21, 'Qui donc? lui demanderent-ils alors. Serais-tu Elie?  Il repondit : Je ne le suis pas.  Es-tu le Prophete?  Et il repondit : Non. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 22, 'Ils lui dirent alors : Qui es-tu? pour que nous rendions reponse a ceux qui nous ont envoyes. Que dis-tu de toi-meme? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 23, 'Moi, repondit-il, je suis Une voix qui crie dans le desert : Aplanissez la voie du Seigneur comme l\'a dit le prophete Esaie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 24, 'Les envoyes etaient des Pharisiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 25, 'Ils continuerent a l\'interroger : Pourquoi donc, lui dirent-ils, baptises-tu, si tu n\'es ni le Christ, ni Elie, ni le Prophete?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 26, 'La reponse de Jean fut celle-ci : Moi, je baptise avec l\'eau ; mais, au milieu de vous, se tient quelqu\'un que vous ne connaissez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 27, 'C\'est celui qui doit venir apres moi ; je ne suis pas digne de defier la courroie de sa sandale!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 28, 'Ceci se passait a Bethanie, au dela du Jourdain, la ou Jean baptisait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 29, 'Le lendemain, Jean voit Jesus qui s\'avance vers lui, et il dit : Voici l\'agneau de Dieu qui ote le peche du monde!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 30, 'c\'est de lui que j\'ai dit : Apres moi vient un homme qui m\'a devance, parce qu\'il a ete avant moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 31, 'Moi, je ne le connaissais pas, mais c\'est pour qu\'il fut manifeste a Israel que je suis venu baptiser avec l\'eau');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 32, 'Jean rendit encore ce temoignage : J\'ai vu, dit-il, l\'Esprit descendre du ciel sous la figure d\'une colombe et demeurer sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 33, 'Moi, je ne le connaissais pas, mais celui qui m\'a envoye baptiser avec l\'eau m\'a dit lui-meme : C\'est l\'homme sur lequel tu verras l\'Esprit descendre et demeurer, qui baptise avec l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 34, 'Or je l\'ai vu et j\'ai rendu ce temoignage : C\'est lui qui est le Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 35, 'Le lendemain Jean etait de nouveau au meme endroit avec deux de ses disciples');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 36, 'et, arretant son regard sur Jesus qui passait, il dit : Voici l\'agneau de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 37, 'Les deux disciples entendirent ces paroles et suivirent Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 38, 'Celui-ci, s\'etant retourne et s\'etant apercu qu\'ils le suivaient, leur dit : Qui cherchez-vous?  Ils lui repondirent : Rabbi (ce mot veut dire : maitre), ou demeures-tu? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 39, 'Venez, leur dit-il, et vous le verrez. Alors ils allerent avec lui, virent ou il demeurait et resterent aupres de lui ce jour-la ; c\'etait vers la dixieme heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 40, 'Andre, le frere de Simon Pierre, etait l\'un de ces deux qui, sur le mot de Jean, s\'etaient mis a suivre Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 41, 'Il fut le premier a trouver son propre frere Simon et lui dit : Nous avons trouve le Messie (ce mot se traduit : Christ).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 42, 'Il l\'amena a Jesus qui, arretant sur lui son regard, lui dit : Tu es Simon, le fils de Jean, tu seras appele Kephas (ce qui veut dire Pierre).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 43, 'Le lendemain, Jesus resolut de partir pour la Galilee. Il trouva Philippe et lui dit : Suis-moi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 44, 'Philippe etait de Bethsaida, de la meme ville qu\'Andre et Pierre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 45, 'Il trouva Nathanael et lui dit : Celui dont a parle Moise dans la Loin, dont ont parle les Prophetes, nous l\'avons trouve! C\'est Jesus de Nazareth, le fils de Joseph.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 46, 'Nathanael lui repondit : Peut-il venir quelque chose de bon de Nazareth?  Viens et vois, lui repliqua Philippe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 47, 'Apercevant Nathanael qui s\'avancait vers lui, Jesus dit de lui : Voici un veritable Israelite, un homme qui est sans fraude! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 48, 'D\'ou me connais-tu? lui demanda Nathanael. Jesus lui repondit, par ces paroles : Avant que Philippe t\'appelat, quand tu etais sous le figuier, je t\'ai vu. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 49, 'Rabbi, repliqua Nathanael, tu es le Fils de Dieu! tu es le Roi d\'Israel!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 50, 'Jesus reprit en ces termes : Parce que je t\'ai dit que je t\'ai vu sous le figuier, voila que tu crois! Tu verras de plus grandes choses que celles-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 1, 51, 'Et il ajouta : En verite, en verite, je vous le dis, vous verrez le ciel ouvert et les anges de Dieu montant et descendant sur le Fils de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 1, 'Le troisieme jour, il y avait un repas de noce a Cana en Galilee. La mere de Jesus s\'y trouvait,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 2, 'et Jesus egalement avait ete invite avec ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 3, 'Le vin etant venu a manquer, la mere de Jesus dit a celui-ci : Ils n\'ont plus de vin. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 4, 'Femme, lui repondit Jesus, qu\'y a-t-il entre toi et moi? mon heure n\'est pas encore venue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 5, 'Sa mere dit a ceux qui servaient : Faites ce qu\'il vous dira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 6, 'Or, il y avait la six urnes de pierre disposees pour les ablutions qui sont en usage parmi les Juifs. Chacune d\'elles contenait deux ou trois mesures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 7, 'Remplissez d\'eau les urnes, dit Jesus aux serviteurs. Ils les remplirent jusqu\'au bord.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 8, 'Puisez-y maintenant, leur dit Jesus, et portez-en a l\'ordonnateur du repas. Ils lui en porterent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 9, 'Quand celui-ci eut goute l\'eau devenue vin,  n\'en sachant point la provenance, comme la savaient les serviteurs qui avaient puise l\'eau,  il appelle l\'epoux');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 10, 'et lui dit : Tout le monde offre d\'abord le bon vin a ses convives et, quand ils sont en ebriete, il leur en donne de qualite inferieure ; quant a toi, tu as reserve le bon vin jusqu\'a maintenant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 11, 'Tel fut, a Cana en Galilee, le commencement des miracles faits par Jesus. Ainsi il manifesta sa gloire et ses disciples crurent en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 12, 'Apres cela, il descendit a Capharnaum, avec sa mere, ses freres et ses disciples. Il n\'y demeura que peu de jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 13, 'La Paque des Juifs etait proche et il monta a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 14, 'Dans le Temple, il trouva les marchands de boeufs, de brebis, de colombes, les changeurs installes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 15, 'Se faisant alors un fouet avec des cordes, il les chassa tous du Temple ainsi que les brebis et les boeufs ; il repandit sur le sol la monnaie des changeurs et renversa leurs tables');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 16, 'et, quant aux marchands de colombes, il leur dit: Emportez tout cela et ne faites pas de la maison de mon Pere une maison de trafic.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 17, 'Ses disciples se souvinrent qu\'il est ecrit : Le zele de ta maison me devore.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 18, 'Cependant les Juifs s\'adresserent a lui ; ils lui dirent : Quel miracle nous montres-tu pour agir de la sorte?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 19, 'Jesus leur repondit par ces paroles : Renversez ce Temple et en trois jours je le releverai! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 20, 'Il a fallu, reprirent les Juifs, quarante-six ans pour batir ce Temple et toi tu le releverais en trois jours!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 21, 'Mais lui, il parlait du Temple de son corps ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 22, 'et lorsqu\'il fut ressuscite d\'entre les morts, ses disciples se souvinrent de ce langage, et ils crurent alors a l\'Ecriture et a la parole dite par Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 23, 'Pendant qu\'il etait a Jerusalem, a la fete de Paque, un grand nombre, a la vue des miracles qu\'il faisait, crurent en son nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 24, 'Mais lui, Jesus, ne se fiait pas a eux, parce qu\'il les connaissait tous. Il n\'avait pas besoin en effet que tel ou tel temoignage vint le renseigner sur qui que ce soit ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 2, 25, 'il savait par-lui-meme ce qu\'il y avait en chacun.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 1, 'Parmi les Pharisiens se trouvait un homme du nom de Nicodeme. C\'etait l\'un des chefs des Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 2, 'Il vint a Jesus pendant la nuit. Rabbi, lui dit-il, nous savons que tu es venu de la part de Dieu comme docteur, car nul ne peut faire les miracles que tu fais, si Dieu n\'est avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 3, 'Jesus alors lui adressa ces paroles : En verite, en verite, je te le dis, personne, s\'il ne nait de nouveau, ne peut voir le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 4, 'Nicodeme lui dit : Comment un homme qui est vieux peut-il naitre! peut-il retourner dans le sein de sa mere et naitre une seconde fois?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 5, 'Jesus repondit : En verite, en verite, je te le dis, personne, ne nait d\'eau et d\'Esprit, ne peut entrer dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 6, 'Ce qui est ne de la chair est chair ; ce qui est ne de l\'Esprit est Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 7, 'Ne t\'etonne pas de ce que je t\'aie dit: il faut que vous naissiez de nouveau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 8, 'Le vent souffle ou il veut ; tu en entends le bruit, mais tu ne sais ni d\'ou il vient, ni ou il va. Ainsi en est-il de tout homme ne de l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 9, 'Nicodeme l\'interrogea : Comment, dit-il, cela se peut-il faire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 10, 'Jesus alors lui adressa ces paroles : Tu es le docteur d\'Israel  et tu ne sais pas ces choses!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 11, 'En verite, en verite, je te le dis, ce que nous savons, nous le disons ; ce que nous avons vu, nous l\'attestons ; et vous n\'acceptez pas notre temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 12, 'Si, quand je vous ai parle des choses de la terre, vous ne croyez pas, comment croiriez-vous si je vous parlais des choses du ciel?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 13, 'Nul n\'est monte au ciel, sinon celui qui est descendu du ciel, le Fils de l\'homme qui est au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 14, 'Et de meme que, dans le desert, Moise a eleve le serpent, de meme il faut que le Fils de l\'homme soit eleve,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 15, 'afin que quiconque croit en lui ait la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 16, 'Dieu, en effet, a tant aime le monde qu\'il a donne son Fils unique afin que quiconque croit en lui ne perisse point, mais qu\'il ait la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 17, 'Car Dieu n\'a pas envoye son Fils dans le monde pour juger le monde, mais pour que le monde soit sauve par lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 18, 'Qui croit en lui n\'est point juge ; qui ne croit point est deja juge parce qu\'il n\'a pas cru au nom du Fils unique de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 19, 'Voici quel est le jugement : la lumiere est venue dans le monde et les hommes ont mieux aime les tenebres que la lumiere, parce que leurs oeuvres etaient mauvaises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 20, 'Quiconque, en effet, commet le mal hait la lumiere et ne vient pas vers la lumiere, de crainte que ses oeuvres ne soient decouvertes et condamnees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 21, 'Quant a celui qui pratique la verite, il vient vers la lumiere, afin que ses oeuvres deviennent manifestes, parce que c\'est en Dieu qu\'elles sont accomplies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 22, 'Apres cela, Jesus et ses disciples se rendirent en Judee. Il y demeurait avec eux et il baptisait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 23, 'Jean baptisait egalement a Ainon, pres de Salim, parce qu\'il y avait la beaucoup d\'eau. On y venait alors recevoir le bapteme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 24, 'En effet, Jean n\'avait pas encore ete mis en prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 25, 'Or, a la suite d\'une discussion des disciples de Jean et d\'un Juif a propos de la purification,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 26, 'ceux-ci vinrent a Jean et lui dirent: Rabbi, celui qui etait avec toi au dela du Jourdain, celui dont tu as ete le temoin, voici qu\'il baptise et que tout le monde vient a lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 27, 'Jean repondit par ces paroles : Un homme ne peut rien prendre qui ne lui soit donne du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 28, 'Vous-memes vous me rendez le temoignage que j\'ai dit : Je ne suis pas, moi, le Christ ; mais je suis envoye devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 29, 'L\'epoux c\'est celui qui a l\'epouse ; l\'ami de l\'epoux qui se tient pres de lui et l\'ecoute, est rejoui d\'une grande joie par la voix de l\'epoux. Cette joie-la est pleinement mienne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 30, 'Lui, il faut qu\'il croisse ; moi, que je diminue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 31, 'Celui qui vient d\'en haut est au-dessus de tous. Celui qui vient de la terre est de la terre et parle de la terre ; celui qui vient du ciel est au-dessus de tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 32, 'il atteste ce qu\'il a vu et entendu et personne n\'accepte son temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 33, 'Celui qui accepte son temoignage confirme que Dieu est veridique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 34, 'Car ce sont les paroles de Dieu que prononce celui que Dieu a envoye, parce que ce n\'est pas avec mesure que Dieu donne l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 35, 'Le Pere aime le Fils, et a tout remis entre ses mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 3, 36, 'Qui croit au Fils a la vie eternelle ; qui refuse de croire au Fils ne verra pas la vie ; mais la colere de Dieu demeure sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 1, 'Ayant su que les Pharisiens avaient oui dire qu\'il faisait plus de disciples et baptisait plus que Jean');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 2, '(quoiqu\'il ne baptisat pas lui-meme ; c\'etaient ses disciples qui baptisaient),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 3, 'le Seigneur quitta la Judee et repartit pour la Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 4, 'Il lui fallait alors traverser la Samarie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 5, 'Il arriva donc a une ville de ce pays nomme Sychar, pres de la terre que Jacob avait donnee a son fils Joseph.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 6, 'La se trouvait le puits de Jacob. Fatigue par le voyage, Jesus s\'assit sur le puits ; il etait environ la sixieme heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 7, 'Survint pour puiser de l\'eau une femme de la Samarie.Donne-moi a boire, lui dit Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 8, '(Ses disciples etaient alles a la ville pour acheter des vivres.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 9, 'La femme samaritaine lui repondit : Comment me demandes-tu a boire, toi, qui es Juif, a moi qui suis Samaritaine? (Les Juifs, en effet, n\'ont point de rapports avec les Samaritains.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 10, 'Jesus alors lui adressa ces paroles : Si tu savais le don de Dieu, et quel est celui qui te dit : Donne-moi a boire, tu lui ferais toi-meme une telle demande, et il te donnerait de l\'eau vive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 11, 'Seigneur, lui dit la femme, tu n\'as rien pour puiser, et le puits est profond. D\'ou as-tu cette eau vive?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 12, 'Es-tu plus grand que notre pere Jacob, lequel nous a donne ce puits, et qui y a bu lui-meme ainsi que ses fils et ses troupeaux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 13, 'Jesus lui repondit par ces paroles : Quiconque boit de l\'eau que voici aura soif de nouveau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 14, 'mais celui qui boira de l\'eau que je lui donnerai n\'aura plus jamais soif ; au contraire, l\'eau que je lui donnerai deviendra en lui une source d\'eau jaillissant en vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 15, 'Seigneur, reprit la femme, donne-moi cette eau-la, pour que je n\'aie plus soif, et que je ne vienne plus ici pour puiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 16, 'Va appeler ton mari, lui dit Jesus, et reviens ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 17, 'La femme repondit ainsi: Je n\'ai pas de mari. Jesus repartit: Tu as raison de dire : je n\'ai pas de mari ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 18, 'en effet, tu as eu cinq maris et celui que tu as maintenant n\'est pas ton mari ; en cela, tu as dit vrai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 19, 'Seigneur, lui dit la femme, je vois que tu es prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 20, 'Nos peres ont adore sur cette montagne-ci ; et vous dites, vous, qu\'a Jerusalem est le lieu ou il faut adorer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 21, 'Femme, crois-moi, lui dit Jesus, il vient une heure ou ni sur cette montagne-ci, ni a Jerusalem vous n\'adorerez le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 22, 'Vous adorez, vous, ce que vous ne connaissez pas ; nous, nous adorons ce que nous connaissons, parce que le salut vient des Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 23, 'Mais il vient une heure, et elle est deja venue, ou les vrais adorateurs adoreront le Pere en esprit et en verite ; car ce sont de tels adorateurs que demande le Pere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 24, 'Dieu est esprit, et il faut que ceux qui l\'adorent, l\'adorent en esprit et en verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 25, 'Je sais, repartit la femme, qu\'il va venir un Messie  (ce mot signifie Christ).  Quand il viendra, il nous apprendra tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 26, 'Jesus lui dit : Je le suis, moi qui te parle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 27, 'En ce moment, arriverent ses disciples ; ils furent surpris de ce qu\'il parlait a une femme. Aucun d\'eux pourtant ne lui dit : Que lui demandes-tu? ou : Pourquoi t\'entretiens-tu avec elle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 28, 'Quant a celle-ci, laissant la sa cruche, elle partit pour la ville et dit aux uns et aux autres :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 29, 'Venez voir un homme qui m\'a dit tout ce que j\'ai fait ; ne serait-ce pas le Christ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 30, 'Ils sortirent de la ville, et ils allaient vers Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 31, 'Dans l\'intervalle, les disciples insistaient aupres de lui et lui disaient : Rabbi, mange.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 32, 'J\'ai pour me nourrir, leur repondit-il, un aliment que vous ne connaissez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 33, 'Les disciples s\'interrogeaient l\'un l\'autre : Quelqu\'un lui a-t-il apporte a manger ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 34, 'Ma nourriture, reprit Jesus, c\'est de faire la volonte de Celui qui m\'a envoye, c\'est d\'accomplir son oeuvre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 35, 'Vous dites, n\'est-ce pas, qu\'il y a encore quatre mois avant la moisson. Eh bien! moi, je vous dis : levez les yeux et regardez ces campagnes, elles sont blanches pour la moisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 36, 'Deja le moissonneur recoit son salaire, et ramasse du fruit pour la vie eternelle, afin que le semeur se rejouisse en meme temps que le moissonneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 37, 'Ici se verifie cette parole : autre est le semeur, autre le moissonneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 38, 'Je vous ai envoye moissonner la ou vous n\'aviez point travaille. D\'autres ont travaille et vous, vous etes entres dans leur travails.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 39, 'Beaucoup de Samaritains de cette ville crurent en lui d\'apres le temoignage porte par cette femme : Il m\'a dit tout ce que j\'ai fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 40, 'Aussi ces Samaritains vinrent-ils le prier de s\'arreter chez eux. Jesus y passa deux jours ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 41, 'et un bien plus grand nombre encore crurent en lui, apres avoir ecoute sa parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 42, 'Ce n\'est plus sur ton recit que nous croyons, disaient-ils a la femme ; car nous l\'avons entendu nous-memes, et nous savons qu\'il est vraiment le Sauveur du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 43, 'Apres ces deux jours, Jesus partit de la et s\'en alla en Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 44, 'Il est vrai qu\'il avait lui-meme declare que dans sa propre patrie un prophete est sans honneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 45, 'Cependant quand il y arriva, les Galileens l\'accueillirent a cause de tout ce qu\'ils lui avaient vu faire a Jerusalem pendant la fete a laquelle eux aussi avaient assiste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 46, 'Jesus se rendit aussi de nouveau a Cana de Galilee, ou il avait change l\'eau en vin. Or, il y avait a Capharnaum un officier royal dont le fils etait malade.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 47, 'Cet officier ayant appris que Jesus, venant de Judee, etait arrive dans le pays, alla le trouver et le pria de descendre a Capharnaum pour guerir son fils, qui etait mourant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 48, 'Jesus lui dit: A moins d\'avoir sous vos yeux des miracles et des prodiges vous ne croyez pas! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 49, 'Viens, Seigneur, reprit cet officier du roi, avant que mon enfant meure! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 50, 'Jesus lui repondit : Va, ton fils vit. Cet homme crut a la parole que lui avait dite Jesus et s\'en alla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 51, 'Il etait deja en route lorsqu\'il rencontra ses serviteurs qui lui annoncerent que son enfant etait vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 52, 'Il leur demanda a quelle heure l\'enfant s\'etait senti mieux. Hier, repondirent-ils, a la septieme heure la fievre l\'a quitte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 53, 'Le pere constata que c\'etait a cette heure meme que Jesus lui avait dit : Ton fils vit. Des lors il devint croyant, ainsi que tous ceux de sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 4, 54, 'C\'etait un nouveau miracle, le second que fit Jesus a son retour de Judee en Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 1, 'Apres cela, vint une fete des Juifs et Jesus monta a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 2, 'Or, il s\'y trouve pres de la Porte-des-Brebis une piscine (le mot hebreu est Bethzatha) ; elle a cinq portiques');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 3, 'sous lesquels etaient etendus une multitude de malades : aveugles, estropies, perclus. (Ils attendaient l\'agitation de l\'eau ;)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 4, '(a certains moments, en effet, un ange descendait dans la piscine et mettait l\'eau en mouvement, et le premier qui entrait dans la piscine apres ce bouillonnement de l\'eau etait gueri, quelle que fut sa maladie.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 5, 'Il y avait la un homme dont la maladie remontait a trente-huit ans.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 6, 'Le voyant couche et sachant qu\'il etait la depuis longtemps, Jesus lui dit : Veux-tu etre gueri? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 7, 'Seigneur, lui repondit le malade, je n\'ai personne pour me jeter dans la piscine lorsque l\'eau vient a bouillonner. Pendant que j\'y vais, un autre y descend avant moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 8, 'Jesus lui dit : Leve-toi, prends ton grabat et marche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 9, 'Cet homme fut immediatement gueri ; il prit son grabat ; il marchait. Or ceci se passait en un jour de sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 10, 'Alors les Juifs s\'adresserent a l\'homme gueri : C\'est le jour du sabbat ; il ne t\'est point permis d\'emporter ce grabat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 11, 'Il leur repliqua : Celui-la meme qui m\'a gueri m\'a dit : Prends ton grabat et marche. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 12, 'Quel est cet homme, lui demanderent-ils, qui t\'a dit de le prendre et de marcher?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 13, 'Mais l\'homme gueri ne savait pas qui c\'etait ; Jesus, d\'ailleurs, s\'etait retire parce qu\'il y avait foule en ce lieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 14, 'Quelque temps apres Jesus rencontra cet homme dans le Temple et lui dit : Te voila gueri ; ne peche plus de peur qu\'il ne t\'arrive quelque chose de pire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 15, 'Cet homme s\'en alla dire aux Juifs que c\'etait Jesus qui l\'avait gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 16, 'Et c\'est parce que Jesus agissait de la sorte le jour du sabbat, c\'est pour cela meme que les Juifs le poursuivaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 17, 'Mais lui, il leur dit : Mon Pere jusqu\'a present agit, et moi aussi j\'agis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 18, 'La-dessus, les Juifs cherchaient d\'autant plus a le faire mourir ; non seulement parce qu\'il annulait le sabbat, mais parce qu\'en outre il disait que Dieu etait son propre Pere, se faisant l\'egal de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 19, 'Jesus alors leur adressa ces paroles : En verite, en verite, je vous le dis, le Fils ne peut rien faire de lui-meme ; il ne fait que ce qu\'il voit faire au Pere. En effet, tout ce que le Pere fait, le Fils aussi le fait egalement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 20, 'car le Pere aime le Fils et lui montre tout ce qu\'il fait ; et il lui montrera des oeuvres plus grandes que celles-ci, pour que vous soyez remplis d\'admiration.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 21, 'De meme, en effet, que le Pere ressuscite les morts et vivifie, de, meme le Fils vivifie qui il veut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 22, 'Car le Pere ne juge qui que ce soit ; mais il a remis au Fils tout le jugement');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 23, 'afin que tous honorent le Fils comme ils honorent le Pere. Celui qui n\'honore pas le Fils n\'honore pas le Pere qui l\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 24, 'En verite, en verite, je vous le dis, celui qui ecoute ma parole et croit a Celui qui m\'a envoye, possede une vie eternelle et ne comparait point en jugement : de la mort il est passe a la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 25, 'En verite, en verite, je vous le dis, il vient une heure, et elle est deja venue, ou les morts entendront la voix du Fils de Dieu, et ceux qui l\'auront entendue vivront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 26, 'Car, comme le Pere a la vie en soi, ainsi il a donne au Fils d\'avoir la vie en soi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 27, 'et il lui a donne la puissance d\'exercer un jugement, parce qu\'il est fils d\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 28, 'Ne vous etonnez pas de cela ; car il vient une heure ou tous ceux qui sont dans les sepulcres entendront sa voix,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 29, 'et en sortiront ; ceux qui auront fait le bien ressusciteront pour vivre, et ceux qui auront fait le mal ressusciteront pour etre juges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 30, 'Je ne puis, moi, rien faire de moi-meme ; comme j\'entends, je juge, et mon jugement est juste, parce que je ne cherche pas ma volonte, mais la volonte de Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 31, 'Si c\'est moi qui me rends temoignage a moi-meme, mon temoignage n\'est pas veridique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 32, 'Il en est un autre qui me rend temoignage, et le temoignage qu\'il me rend est, je le sais, veridique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 33, 'Vous vous etes adresses a Jean, et il a rendu temoignage a la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 34, 'Quant a moi, ce n\'est pas le temoignage qui vient d\'un homme que j\'accepte, mais j\'en parle afin que vous soyez sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 35, 'Il etait lui le flambeau qui brule et qui brille et, a sa clarte, vous vous etes plu un moment a vous rejouir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 36, 'Quant a moi, j\'ai le temoignage qui est plus grand que celui de Jean ; car j\'ai les oeuvres que le Pere m\'a donne d\'accomplir ; ce sont ces oeuvres memes, celles que je fais, qui rendent temoignage de moi et attestent que le Pere m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 37, 'De plus, le Pere qui m\'a envoye a, lui aussi, rendu temoignage de moi. Vous n\'avez jamais ni ecoute sa voix ni contemple sa face,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 38, 'et sa parole vous ne l\'avez pas demeurant en vous, puisque vous ne croyez pas a celui qu\'Il a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 39, 'Vous scrutez les Ecritures, parce que vous pensez y trouver la vie eternelle, et elles aussi, elles me rendent temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 40, 'Et vous ne voulez pas venir a moi pour avoir la vie !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 41, 'La gloire qui vient des hommes, je ne l\'accepte pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 42, 'mais je vous connais pour n\'avoir pas l\'amour de Dieu en vous :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 43, 'moi, qui suis venu au nom de mon Pere, vous ne me recevez pas! Qu\'un autre se presente en son propre nom, et vous le recevrez!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 44, 'Comment pourriez-vous croire, vous qui recevez votre gloire les uns des autres et ne recherchez pas la gloire qui vient de Dieu seul?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 45, 'Ne pensez pas que, devant le Pere, ce soit moi qui vous accuserai. Votre accusateur c\'est Moise en qui vous mettez votre espoir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 46, 'Car si vous croyiez a Moise, vous croiriez a moi-meme ; en effet, c\'est de moi que parlent ses ecrits ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 5, 47, 'mais si vous ne croyez pas a ce qu\'il a ecrit, comment croirez-vous a mes paroles ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 1, 'Ensuite Jesus passa de l\'autre cote de la mer de Galilee (ou de Tiberiade).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 2, 'Il etait suivi d\'une foule immense, attiree par les miracles qu\'il operait sur les malades.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 3, 'Il gravit la montagne et y fit sa demeure ainsi que ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 4, 'C\'etait aux approches de la fete juive de la Paque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 5, 'Ayant leve les yeux, Jesus apercut l\'immense foule qui venait a lui, et dit a Philippe : Ou acheterons-nous des pains pour leur donner a manger?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 6, '(Il lui parlait ainsi pour le mettre a l\'epreuve ; car, lui, il savait ce qu\'il allait faire.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 7, 'Pour que chacun en eut tant soit peu, lui repondit Philippe, deux cents deniers de pains seraient insuffisants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 8, 'L\'un des disciples (c\'etait. Andre, le, frere de Simon Pierre) dit a Jesus :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 9, 'Il y a ici un petit garcon qui a cinq pains d\'orge et deux poissons ; mais pour une pareille foule qu\'est-ce que cela? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 10, 'Faites asseoir ces gens, dit Jesus. En ce lieu-la, l\'herbe etait abondante. Les hommes s\'assirent au nombre d\'environ cinq mille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 11, 'Jesus prit alors les pains, prononca l\'action de graces et les distribua a ceux qui etaient assis ; il fit de meme avec les poissons ; ils en eurent autant qu\'ils en voulaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 12, 'Quand tous furent rassasies, il dit a ses disciples : Ramassez, pour que rien ne se perde, les morceaux qui sont restes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 13, 'Ils les ramasserent donc ; et, avec ces morceaux des cinq pains d\'orge, avec ce superflu du repas, ils remplirent douze paniers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 14, 'Tous ces gens, s\'apercevant qu\'il avait fait un miracle, disaient : C\'est vraiment la le prophete qui doit venir dans le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 15, 'Sachant alors qu\'ils allaient l\'enlever de force pour le faire roi, Jesus se retiras de nouveau, lui seul, dans la montagne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 16, 'Quand vint le soir, ses disciples redescendirent vers la mer ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 17, 'ils monterent dans une barque et entreprirent la traversee dans la direction de Capharnaum. Il faisait deja nuit, Jesus ne les avait pas encore rejoints,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 18, 'et la mer s\'enflait au souffle d\'un grand vent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 19, 'Les disciples avaient parcouru environ vingt-cinq ou trente stades, quand ils apercurent Jesus, marchant sur la mer et s\'approchant de la barque. L\'epouvante les saisit,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 20, 'mais Jesus leur dit : C\'est moi, soyez sans crainte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 21, 'Ils voulurent alors le recevoir dans la barque, et tout aussitot elle se trouva a terre, la ou ils allaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 22, 'Le lendemain, les multitudes, restees de l\'autre cote de la mer, remarquerent que Jesus n\'etait pas entre avec ses disciples dans la seule et unique barque qui fut la, et que ceux-ci etaient partis seuls.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 23, 'D\'autres bateaux cependant arriverent de Tiberiade pres de l\'endroit ou, a la suite de l\'action de graces du Seigneur, tous avaient ete nourris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 24, 'Les multitudes, qui finirent par se convaincre que, de meme que ses disciples, Jesus n\'etait plus la, monterent dans ces embarcations pour aller le chercher a Capharnaum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 25, 'Et l\'ayant en effet trouve sur l\'autre rive, elles lui demanderent : Rabbi, quand es-tu venu ici?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 26, 'Jesus leur repondit par ces paroles: En verite, en verite, je vous le dis, vous me cherchez non pas a cause des miracles que vous avez vus, mais a cause des pains dont vous avez ete nourris et rassasies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 27, 'Travaillez a acquerir, non l\'aliment qui perit, mais l\'aliment qui subsiste en vie eternelle, et que le Fils de l\'homme vous donnera, le Fils de l\'homme que le Pere, que Dieu a marque de son sceau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 28, 'Que devons-nous faire, lui dirent-ils, pour travailler aux oeuvres de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 29, 'Jesus leur repondit par ces paroles : L\'oeuvre de Dieu est d\'avoir foi en celui qu\'Il a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 30, 'Alors ils lui demanderent: Quel est le miracle que tu fais afin que nous le voyions et que nous ayons foi en toi? Quelle est l\'oeuvre que tu accomplis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 31, 'Nos peres ont mange la manne dans le desert comme cela est ecrit: Il leur donna a manger un pain qui vient du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 32, 'Le pain qui vient du ciel, leur repondit Jesus, en verite, en verite, je vous le dis, Moise ne vous l\'a pas donne ; mais c\'est mon Pere qui vous donne le pain qui vient du ciel, le pain veritable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 33, 'Le pain de Dieu est, en effet, celui qui descend du ciel et qui donne la vie au monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 34, 'Seigneur, lui demanderent-ils, donne-nous toujours ce pain-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 35, 'Jesus leur dit alors : C\'est moi qui suis le pain de la vie. Celui qui vient a moi n\'aura jamais faim ; celui qui croit en moi n\'aura jamais soif.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 36, 'Mais je vous l\'ai dit: Bien que vous m\'ayez vu, vous ne croyez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 37, 'A moi viendra tout ce que le Pere me donne, et celui qui vient a moi je ne le repousserai point au dehors ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 38, 'car je suis descendu du ciel, non pour faire ma volonte, mais la volonte de Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 39, 'Or, la volonte de Celui qui m\'a envoye, c\'est que je ne perde rien de ce qu\'il m\'a donne, mais que je ressuscite tout au dernier jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 40, 'Oui, la volonte de mon Pere c\'est que quiconque contemple le Fils et croit en lui ait la vie eternelle, et moi-meme je le ressusciterai au dernier jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 41, 'Cependant les Juifs murmuraient contre cette parole de lui : C\'est moi qui suis le pain descendu du ciel. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 42, 'Est-ce que ce n\'est pas la Jesus, le fils de Joseph, disaient-ils, Jesus dont nous connaissons le pere et la mere? Comment cet homme peut-il dire: je suis descendu du ciel?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 43, 'Jesus leur repondit par ces paroles: Ne murmurez pas entre vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 44, 'nul ne peut venir a moi, sans etre attire par le Pere qui m\'a envoye, et c\'est moi qui ressusciterai celui-la au dernier jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 45, 'Il est ecrit dans les prophetes : Ils seront tous enseignes de Dieu. Quiconque a entendu le Pere et a appris, vient a moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 46, 'Non que quelqu\'un ait vu le Pere, sauf celui qui est de la part de Dieu, lui, il a vu le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 47, 'En verite, en verite, je vous le dis, celui qui croit a la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 48, 'Je suis, moi, le pain de la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 49, 'Vos peres ont mange la manne dans le desert ; puis ils sont morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 50, 'Voici le pain descendant du ciel, afin qu\'on en mange et qu\'on ne meure points.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 51, 'Je suis, moi, le pain vivant descendu du ciel. Si quelqu\'un mange de ce pain, il vivra eternellement. Et le pain que je donnerai pour la vie du monde, c\'est ma chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 52, 'Il y eut alors un debat entre les Juifs ; ils disaient : Comment cet homme peut-il nous donner a manger sa chair?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 53, 'En verite, en verite, je vous le dis, reprit Jesus, si vous ne mangez la chair du Fils de l\'homme, et si vous ne buvez son sang, vous n\'avez pas la vie en vous-memes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 54, 'Qui mange ma chair et boit mon sang a la vie eternelle, et je le ressusciterai au dernier jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 55, 'Car ma chair est une vraie nourriture, et mon sang est un vrai breuvage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 56, 'Celui qui mange ma chair et boit mon sang demeure en moi et moi en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 57, 'De meme que Celui qui est vivant, le Pere, m\'a envoye et que, moi, je vis par le Pere, de meme aussi celui qui me mange vivra par moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 58, 'Tel est le pain descendu du ciel. Il n\'en est pas de lui comme de la manne dont se nourrirent vos peres, lesquels sont morts ensuite. Celui qui mange ce pain-ci vivra eternellement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 59, 'Telles furent les paroles de Jesus enseignant dans la synagogue a Capharnaum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 60, 'Apres les avoir entendues, plusieurs de ses disciples dirent: C\'est dur a accepter ce qu\'il dit la! Qui peut ecouter ces paroles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 61, 'Sachant en lui-meme les murmures de ses disciples a ce sujet, Jesus leur dit : Cela vous scandalise!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 62, 'Et si vous voyiez le Fils de l\'homme montant la ou il etait auparavant!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 63, 'C\'est l\'Esprit qui vivifie ; la chair ne sert de rien. Les paroles que je vous ai dites sont esprit et sont vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 64, 'Mais il en est parmi vous quelques-uns qui, ne croient pas! (Des le commencement, en effet, Jesus savait quels etaient ceux qui ne croyaient point, et quel etait l\'homme qui le livrerait.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 65, 'Voila pourquoi, ajoutait-il, je vous ai dit que nul ne peut venir a moi, a moins que cela ne lui soit donne par le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 66, 'Ce fut alors qu\'un grand nombre de ses disciples se retirerent ; ils ne furent plus de sa suite ; ils ne marchaient plus avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 67, 'Et vous, voulez-vous aussi vous en aller? dit Jesus aux douze.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 68, 'Simon Pierre lui repondit : Seigneur, a qui irions-nous? tu as des paroles de vie eternelle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 69, 'et nous, nous avons cru et nous avons reconnu que tu es le Saint de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 70, 'Jesus reprit : N\'est-ce pas moi qui vous ai choisis tous les douze? eh bien, l\'un de vous est un demon !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 6, 71, 'Il parlait de Judas, fils de Simon, l\'Iskariote. C\'etait lui, en effet, qui devait le livrer, lui, l\'un des douze.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 1, 'Jesus, apres ces choses, parcourait la Galilee, ne voulant pas, voyager en Judee, ou les Juifs cherchaient a le faire perir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 2, 'La fete juive dite des Tabernacles approchait cependant,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 3, 'et ses freres lui dirent : Pars de ce pays-ci, transporte-toi en Judee, afin que les disciples que tu y comptes voient, eux aussi, les oeuvres que tu accomplis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 4, 'On n\'agit pas en cachette quand on veut se faire connaitre ; si reellement tu fais ces choses, manifeste-toi toi-meme au monde!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 5, '(En effet, ses freres non plus ne croyaient pas en lui.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 6, 'Jesus leur repondit : Mon temps, a moi, n\'est pas encore venu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 7, 'pour vous, que le monde ne peut hair, le moment est toujours opportun ; mais le monde me hait, moi, parce que je rends de lui le temoignage que ses oeuvres sont mauvaises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 8, 'Rendez vous a la fete ; quant a moi, je ne vais pas a cette fete, parce que mon temps n\'est pas encore accompli.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 9, 'Cela dit, il demeura en Galilee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 10, 'Puis, lorsque ses freres furent partis pour la fete, lui-meme s\'y rendit aussi, mais ce n\'etait pas d\'une facon ostensible, c\'etait comme en cachettes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 11, 'Pendant la fete, cependant, les Juifs s\'informaient de lui: Ou donc est-il, demandait-on.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 12, 'Dans la foule, une quantite de bruits couraient sur lui : C\'est un homme de bien, disaient ceux-ci. Nullement, c\'est tout le contraire, disaient ceux-la, il egare la foule ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 13, 'mais personne, par crainte des Juifs, n\'osait s\'exprimer tout haut sur son compte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 14, 'On etait deja au milieu de la fete, lorsque Jesus monta au Temple ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 15, 'il se mit a enseigner au grand etonnement des Juifs. Comment, disaient-ils, cet homme est-il si instruit, lui qui n\'a pas etudie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 16, 'Jesus leur repondit par ces paroles : Mon enseignement n\'est pas de moi s, mais de Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 17, 'Si quelqu\'un veut faire sa volonte, il saura si mon enseignement est de Dieu s ou si je parle de mon chef.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 18, 'L\'homme qui parle de son chef poursuit sa gloire propre ; mais l\'homme qui poursuit la gloire de celui qui l\'a envoye, est veridique, lui, et il n\'y a en lui aucun manque de droiture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 19, 'N\'est-ce pas Moise qui vous a donne la Loi? Et aucun de vous ne la met en pratique! Pourquoi cherchez-vous a me tuer?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 20, 'La foule repondit: Tu es possede d\'un demon ; qui est-ce qui, cherche a te tuer ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 21, 'Jesus repliqua par ces paroles : Une seule oeuvre accomplie par moi, vous a tous surpris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 22, 'Moise vous a donne la circoncision (elle n\'a pas ete instituee par Moise, elle vient des patriarches), cette circoncision vous la pratiquez le jour du sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 23, 'Eh bien, si on peut, pour ne pas violer la loi de Moise, pratiquer la circoncision le jour du sabbat, pourquoi vous irriter contre moi pour avoir, ce meme jour de sabbat, rendu un homme entierement sain?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 24, 'Ne jugez pas sur l\'apparence ; jugez au contraire suivant la justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 25, 'Parmi les habitants de Jerusalem quelques-uns disaient : N\'est-ce point cet homme que l\'on cherche a faire perir?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 26, 'Le voici qui parle en toute liberte, et on ne lui dit rien! Les autorites auraient-elles vraiment reconnu qu\'il est le Christ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 27, 'Nous savons pourtant d\'ou est celui-ci ; or quand le Christ vient, personne ne sait d\'ou il est.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 28, 'Alors Jesus, qui enseignait dans le Temple, s\'ecria : Vous me connaissez! vous savez d\'ou je suis! mais je ne suis pas venu de moi-meme. Il y en a un qui reellement m\'a envoye, et Celui-la vous ne le connaissez pas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 29, 'Moi je le connais, parce que je viens de lui et que c\'est lui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 30, 'La-dessus, ils chercherent a l\'arreter ; personne toutefois ne porta la main sur lui, parce que son heure n\'etait pas encore venue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 31, 'Dans la foule un grand nombre crurent en lui. Lorsque le Christ viendra, disaient-ils, fera-t-il plus de miracles que n\'en a fait cet homme-la?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 32, 'Les Pharisiens entendirent ces rumeurs de la foule ; aussi, de concert avec les chefs des pretres, envoyerent-ils des agents pour l\'arreter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 33, 'Jesus dit alors : Je suis encore avec vous pour un peu de temps ; apres quoi je retourne a Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 34, 'Vous me chercherez et vous ne me trouverez pas et, au lieu ou je suis, vous ne pouvez venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 35, 'Les Juifs se demandaient entre eux : Ou donc va-t-il aller, que nous ne le trouverons pas? Va-t-il aller chez les dissemines, parmi les paiens et instruire les paiens?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 36, 'Qu\'est-ce que ce mot qu\'il a prononce : vous me chercherez et vous ne me trouverez pas ; et au lieu ou je suis vous ne pouvez venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 37, 'Durant le dernier jour de la fete, qui en est le plus solennel, Jesus, se tenant debout, s\'ecria : Si quelqu\'un a soif, qu\'il vienne a moi et qu\'il boive!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 38, 'Comme dit l\'Ecriture: Du sein de celui qui croit en moi jailliront des fleuves d\'eau vives. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 39, 'Il disait cela de l\'Esprit que devaient recevoir ceux qui avaient cru en lui. En effet, il n\'y avait pas encore d\'Esprit, parce que Jesus n\'etait pas encore glorifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 40, 'Dans la multitude, ceux qui avaient entendu ces paroles disaient: C\'est vraiment le prophete!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 41, 'd\'autres : C\'est le Christ ! d\'autres encore :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 42, 'Est-ce que le Christ doit sortir de la Gaulee? Est-ce que l\'Ecriture ne dit pas que le Christ sortira de la famille de David et du village de Bethleem d\'ou etait David?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 43, 'On etait donc dans le peuple fort divise a son sujet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 44, 'Un certain nombre d\'entre eux voulaient l\'arreter ; mais aucun ne porta la main sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 45, 'Les agents retournerent aupres des chefs des pretres et des Pharisiens. Pourquoi ne l\'avez-vous pas amene! demanderent ceux-ci.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 46, 'A quoi les agents repondirent : Jamais homme n\'a parle comme parle cet homme. -');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 47, 'Vous etes-vous aussi laisse seduire? leur dirent les Pharisiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 48, 'Est-ce qu\'il y a un seul personnage ayant autorite qui ait cru en lui? Est-ce qu\'il y a un seul Pharisien?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 49, 'Il n\'y a que cette maudite populace qui n\'entend rien a la Loi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 50, 'Nicodeme, un des leurs, celui qui etait venu a Jesus tout d\'abord, leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 51, 'Est-ce que notre Loi juge un homme sans qu\'il soit entendu et sans qu\'on se soit rendu compte de ses actes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 52, 'Ils lui repondirent ainsi : Serais-tu, toi aussi, de la Galilee? informe-toi, et tu verras que de la Galilee ne surgit pas de prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 7, 53, '(Apres quoi, chacun deux retourna a sa maison.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 1, '(Quant a Jesus, il alla au mont des Oliviers.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 2, 'Au point du jour, il revint au Temple. Tout le peuple accourait a lui. Jesus s\'etait assis et enseignait,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 3, 'lorsque les Scribes et les Pharisiens lui amenerent une femme qu\'on avait surprise en adultere et la placerent au milieu de tout le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 4, 'Maitre, dirent-ils a Jesus, cette femme a ete saisie en flagrant delit d\'adultere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 5, 'Or, dans la Loi, Moise [nous] a ordonne de lapider ces femmes-la. Et toi, quel est ton avis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 6, 'Ils parlaient ainsi pour le mettre a l\'epreuve et avoir un pretexte a accusation.] Quant a Jesus, il s\'etait baisse et il ecrivait sur le sol avec son doigt.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 7, 'Ceux qui l\'interrogeaient ayant persiste, il se releva et [leur] dit : Que celui d\'entre vous qui est sans peche lui jette la pierre le premier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 8, 'Puis se penchant de nouveau, il continua d\'ecrire sur le sol.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 9, 'Mais, en entendant cette reponse, ils sortirent un a un, a commencer par les plus ages. Et [Jesus] resta seul avec la femme, toujours la, au milieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 10, 'Alors il se leva et lui dit : Femme, ou sont-ils [tes accusateurs]? Est-ce qu\'aucun ne t\'a condamnee?  Aucun. Seigneur, repondit-elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 11, 'Jesus lui dit: Moi non plus je ne te condamne pas. Va et [desormais] ne peche plus !)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 12, 'Jesus leur parla de nouveau disant : Je suis la lumiere du, monde. Qui me suit ne marchera pas dans les tenebres ; mais il aura la lumiere de la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 13, 'Alors les Pharisiens lui dirent : C\'est toi-meme qui temoignes de toi-meme, ton temoignage n\'est pas valable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 14, 'Jesus leur repondit par ces paroles : Bien que je sois a moi-meme mon propre temoin, mon temoignage est valable, parce que je sais d\'ou je suis venu et ou je vais. Vous, vous ne savez ni d\'ou je viens, ni ou je vais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 15, 'Vous, vous jugez selon la chair, moi, je ne juge personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 16, 'Et s\'il m\'arrive de juger, mon jugement est veritable, parce que je ne suis pas seul, mais avec moi est le Pere qui m\'a envoye,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 17, 'et il est ecrit dans votre Loi que le temoignage de deux personnes est valable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 18, 'Or il y a moi qui me rends temoignage a moi-meme, et il y a le Pere qui m\'a envoye, et qui, lui aussi, me rend temoignage. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 19, 'Ton pere, lui disaient-ils alors, ou est-il?  Vous ne connaissez ni moi, ni mon Pere, repondit Jesus ; si vous me connaissiez, vous connaitriez egalement mon Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 20, 'Il prononca ces paroles dans le Tresor, lorsqu\'il enseignait au Temple. Personne ne l\'arreta, parce que son heure n\'etait pas encore venue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 21, 'Il leur dit encore : Je m\'en vais ; vous me chercherez et vous, mourrez dans votre peche. Au lieu ou je vais, vous ne pouvez venir. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 22, 'Est-ce qu\'il se donnera la mort ; se demandaient les Juifs, puisqu\'il dit : Au lieu ou je vais, vous ne pouvez venir!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 23, 'Jesus reprit : Vous, vous etes d\'en bas ; moi, je suis d\'en haut ; Vous, vous etes de ce monde ; moi, je ne suis pas de ce monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 24, 'Aussi vous ai-je dit que vous mourrez dans vos peches ; car si vous ne croyez pas que c\'est moi vous mourrez dans vos peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 25, 'Qui donc es-tu? demanderent-ils. Jesus leur repondit : Avant tout, je suis ce que je vous dis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 26, 'J\'ai, sur vous, beaucoup a dire, j\'ai beaucoup a juger, mais Celui qui m\'a envoye est veridique, et ce que j\'ai entendu aupres de lui, je le proclame dans le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 27, 'Comme ils ne comprenaient pas qu\'il leur parlait du Pere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 28, 'il ajouta : Quand vous aurez eleve le Fils de l\'homme, alors vous reconnaitrez que c\'est moi vous reconnaitrez que je ne fais rien de moi-meme, mais que je parle d\'apres les enseignements du Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 29, 'Celui qui m\'a envoye est avec moi ; il ne m\'a pas laisse seul, parce que je fais toujours ce qui lui est agreable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 30, 'Pendant qu\'il parlait ainsi, plusieurs crurent en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 31, 'Or, a ces Juifs qui avaient eu foi en lui, Jesus disait : Si vous demeurez dans ma parole, vous etes veritablement mes disciples ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 32, 'vous connaitrez la verite et la verite vous rendra libres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 33, 'Ils lui repliquerent: Nous sommes du sang d\'Abraham et nous n\'avons jamais ete les esclaves de qui que ce soit. Comment dis-tu : Vous deviendrez libres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 34, 'Jesus leur repondit : En verite, en verite, je vous le dis, quiconque commet le peche est l\'esclave du peche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 35, 'or ce n\'est pas pour toujours que l\'esclave demeure dans la maison, le Fils y demeure toujours ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 36, 'si donc le Fils vous affranchit, vous serez veritablement libres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 37, 'Je sais que vous etes de la descendance d\'Abraham ; mais, parce que ma parole n\'a pas prise sur vous, vous cherchez a me faire perir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 38, 'Ce que j\'ai vu aupres du Pere, moi, je le dis ; ce que vous avez appris de votre pere, vous, vous le faites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 39, 'Ils repondirent par ces paroles : Notre pere a nous, c\'est Abraham. Si vous etiez enfants d\'Abraham, leur dit Jesus, vous feriez les oeuvres d\'Abraham ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 40, 'au lieu de cela, vous cherchez a me mettre a mort, moi, un homme qui vous ai dit la verite, que j\'ai entendue aupres de Dieu. Cela, Abraham ne l\'a pas fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 41, 'Vous, vous faites les oeuvres de votre pere. Nous ne sommes pas le produit de quelque fornication, repliquerent-ils, nous avons un seul Pere : Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 42, 'Si Dieu etait votre Pere, reprit Jesus, vous m\'aimeriez! car de Dieu je suis sorti et je viens ; ce n\'est pas, en effet, de moi-meme que je suis venu ; mais c\'est lui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 43, 'Pourquoi ne reconnaissez-vous pas mon langage? parce que vous ne pouvez entendre s ma parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 44, 'Vous avez le diable pour pere, et c\'est aux convoitises de votre pere que vous voulez obeir. Des le commencement, celui-la fut homicide, et il ne se tient pas dans la verite, parce qu\'il n\'y a point de verite en lui. Quand il profere le mensonge, il parle de son propre fond, etant menteur et pere du mensonge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 45, 'Quant a moi, c\'est parce que je dis la verite que vous ne me croyez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 46, 'Qui de vous me convaincra de peche? Si je dis la verite, pourquoi ne me croyez-vous pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 47, 'Celui qui est de Dieu, entend les paroles de Dieu. Voici pourquoi vous, vous n\'entendez pas: c\'est que vous n\'etes pas de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 48, 'Les Juifs lui repondirent par ces paroles : N\'avons-nous pas raison de dire que tu es un Samaritain et un possede du demon?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 49, 'Je ne suis pas un possede du demon, repliqua Jesus, mais j\'honore mon Pere, et vous, vous me deshonorez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 50, 'Pour moi, je ne cherche pas ma gloire ; il est quelqu\'un qui la cherche et qui juge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 51, 'En verite, en verite, je vous le dis, si quelqu\'un garde ma parole, il ne verra jamais la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 52, 'Nous sommes maintenant convaincus, lui dirent les Juifs, que tu es possede du demon. Abraham est mort ainsi que les prophetes, et toi, tu dis : Si quelqu\'un garde ma parole, il ne goutera jamais la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 53, 'Es-tu plus grand que notre pere Abraham qui est mort? que les prophetes, qui sont morts? Qui pretends-tu etre ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 54, 'Jesus repondit : Si je me glorifie moi-meme, ma gloire n\'est rien. C\'est mon Pere qui me glorifie, mon Pere dont vous dites qu\'il est votre Dieu');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 55, 'et que vous ne connaissez pas ; pour moi, je le connais entierement. Et si je disais que je ne le connais pas entierement, je serais comme vous, un menteur ; mais je le connais entierement et je garde sa parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 56, 'Abraham votre pere a tressailli de joie dans l\'espoir de voir mon jour ; il l\'a vu et il a ete dans la joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 57, 'Tu n\'as pas encore cinquante ans, repartirent les Juifs, et tu as vu Abraham !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 58, 'Jesus leur dit : En verite, en verite, je vous le dis, avant qu\'Abraham fut, moi, je suis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 8, 59, 'Alors ils prirent des pierres pour les lui jeter ; mais Jesus se cacha et sortit du Temple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 1, 'Il vit, en passant, un aveugle de naissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 2, 'Ses disciples l\'interrogerent : Rabbi, lui dirent-ils, est-ce le peche de cet homme, ou celui de ses parents, qui l\'a fait naitre aveugle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 3, 'Jesus repondit : Ni lui, ni ses parents n\'ont peche, mais il est aveugle pour que les oeuvres de Dieu soient manifestees en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 4, 'Il faut, pendant qu\'il est jour, que j\'accomplisse les oeuvres de Celui qui m\'a envoye. La nuit arrive, ou personne ne peut travailler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 5, 'Pendant que je suis dans le monde, je suis la lumiere du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 6, 'Ayant ainsi parle, il cracha a terre ; et, formant de la boue avec sa salive, il etendit cette boue sur les yeux de l\'aveugle ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 7, 'puis il lui dit : Va te laver a la piscine de Siloam (mot qui signifie : Envoye). L\'aveugle alla se laver et revint voyant clair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 8, 'Les voisins et les gens qui auparavant l\'avaient remarque, quand il demandait l\'aumone, disaient : N\'est-ce pas la l\'homme qui etait assis et mendiait? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 9, 'C\'est bien lui, repondaient les uns.  Nullement! affirmaient les autres, c\'est quelqu\'un qui lui ressemble. Lui, il disait : C\'est bien moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 10, 'On lui demanda il alors: Comment tes yeux se sont-ils donc ouverts?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 11, 'Il repondit: Celui que l\'on nomme Jesus a fait de la boue ; il l\'a etendue sur mes yeux ; puis il m\'a dit : Va te laver a Siloam. J\'y suis alle ; je me suis lave et j\'y vois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 12, 'Ils lui demanderent : Et lui, ou est-il?  Je n\'en sais rien, repondit-il.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 13, 'On conduisit aux Pharisiens cet aveugle gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 14, 'Or c\'etait un jour de sabbat que Jesus avait fait de la boue et lui avait ouvert les yeux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 15, 'Les Pharisiens lui demanderent a leur tour comment il avait recouvre la vue. Il m\'a mis de la boue sur les yeux, leur repondit-il ; je me suis lave et j\'y vois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 16, 'La-dessus quelques-uns de ces Pharisiens dirent : Cet homme, n\'est pas de Dieu, puisqu\'il n\'observe pas le sabbat. Mais d\'autres : Comment un pecheur peut-il faire de tels miracles? Ils etaient en desaccord.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 17, 'Alors ils s\'adresserent de nouveau a l\'aveugle: Et toi, que dis-tu de lui, puisqu\'il t\'a ouvert les yeux?  C\'est un prophete, repondit-il.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 18, 'Ne croyant pas que cet homme avait ete aveugle et qu\'il avait recouvre la vue, les Juifs manderent son pere et sa mere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 19, 'et ils leur poserent ces questions : Est-ce bien la votre fils que vous dites etre aveugle de naissance? Comment se fait-il qu\'il y voie maintenant?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 20, 'Ses parents repondirent par ces paroles : Nous savons que c\'est la notre fils ; nous savons qu\'il est ne aveugle ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 21, 'mais comment il se fait qu\'il y voie maintenant, nous ne le savons pas ; quel est celui qui lui a ouvert les yeux, nous ne le savons pas. Interrogez-le ; il est d\'age a s\'expliquer lui-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 22, 'Les parents parlerent ainsi par crainte des Juifs. Ceux-ci, en effet, avaient deja decide que quiconque reconnaitrait que Jesus etait le Christ, serait chasse de la synagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 23, 'C\'est pour cela que les parents dirent :  Il est d\'age a repondre, interrogez-le.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 24, 'Alors les Pharisiens appelerent pour la seconde fois l\'homme qui avait ete aveugle. Rends gloire a Dieu! lui dirent-ils, nous savons que cet homme est un pecheur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 25, 'Et lui, il repondit : S\'il est un pecheur, je ne sais ; je sais une chose : j\'etais aveugle et maintenant je vois ! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 26, 'Que t\'a-t-il fait? reprirent-ils ; comment t\'a-t-il ouvert les yeux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 27, 'Il repondit : Je vous l\'ai deja dit, et vous n\'avez pas ecoute. Pourquoi voulez-vous l\'entendre encore? Voulez-vous, vous aussi, devenir ses disciples?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 28, 'Alors ils l\'accablerent d\'injures, disant C\'est toi qui es son disciple ; nous sommes, nous, disciples de Moise ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 29, 'nous savons que Dieu a parle a Moise ; quant a cet homme, nous ne savons d\'ou il est. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 30, 'Voici qui est fort etonnant, reprit laveugle gueri, vous ne savez d\'ou il est, alors qu\'il m\'a ouvert les yeux!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 31, 'Nous savons que Dieu n\'ecoute pas les pecheurs ; si, au contraire, quelqu\'un craint Dieu et fait sa volonte, c\'est celui-la que Dieu ecoute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 32, 'Jamais on n\'a entendu dire que quelqu\'un ait ouvert les yeux d\'un aveugle-ne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 33, 'Si cet homme n\'etait pas de Dieu, il ne pourrait rien faire. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 34, 'Tu es ne dans le peche des pieds a la tete, lui repliquerent-ils, et tu nous fais la lecon ! Et ils le jeterent dehors.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 35, 'Jesus apprit qu\'ils l\'avaient ainsi chasse, et l\'ayant rencontre, il lui dit : Crois-tu au Fils de l\'homme ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 36, 'Il lui repondit par ces paroles : Qui est-il, Seigneur, pour que je croie en lui?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 37, 'Jesus lui dit : Tu l\'as vu ; celui qui te parle, c\'est lui. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 38, 'Je crois, Seigneur, dit alors cet homme en se prosternant devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 39, 'Et Jesus dit : C\'est pour un jugement que je suis venu dans ce monde ; c\'est pour que les aveugles voient et que ceux qui voient deviennent aveugles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 40, 'Ceux des Pharisiens qui etaient presents entendirent ce mot : Et nous, demanderent-ils, sommes nous aussi des aveugles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 9, 41, 'Jesus leur repondit : Si vous etiez aveugles, vous ne seriez point coupables ; mais comme vous dites a l\'heure presente : nous voyons, votre culpabilite reste entiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 1, 'En verite, en verite, je vous le dis, celui qui n\'entre pas par la porte dans le bercail des brebis, mais y monte de quelque autre cote, est un voleur et un brigand.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 2, 'Celui qui entre par la porte est berger des brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 3, 'A celui-ci le portier ouvre, et les brebis entendent sa voix. Il appelle par leur nom les brebis qui lui appartiennent et les fait sortir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 4, 'Quand il a fait sortir toutes les siennes, il marche devant elles et ses brebis le suivent parce qu\'elles connaissent sa voix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 5, 'Elles ne suivront point un etranger ; au contraire, elles fuiront loin de lui, car elles ne connaissent point la voix des etrangers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 6, 'Telle est la parabole que leur dit Jesus ; mais eux, ils ne comprirent pas de quoi il leur parlait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 7, 'Jesus reprit donc : En verite, en verite, je vous le dis, je suis la porte des brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 8, 'Tous ceux qui sont venus avant moi sont des voleurs et des brigands, mais les brebis ne les ont pas ecoutes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 9, 'C\'est moi qui suis la porte ; si quelqu\'un entre par moi, il sera sauve. Il entrera, il sortira, il trouvera sa pature.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 10, 'Le voleur ne vient que pour derober, egorger et detruire. Moi, je suis venu afin que les brebis aient la vis et qu\'elles aient l\'abondance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 11, 'Je suis le bon berger. Le bon berger donne sa vie pour les brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 12, 'Quant au mercenaire, quant a celui qui n\'est pas berger, a qui les brebis n\'appartiennent pas, s\'il voit venir le loup, il abandonne les brebis et prend la fuite ; alors le loup les ravit et les disperse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 13, 'Il agit ainsi parce qu\'il est mercenaire et qu\'il n\'a nul soin des brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 14, 'C\'est moi qui suis le bon berger. Je connais mes brebis et mes brebis me connaissent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 15, 'de la meme facon que le Pere me connait et que, moi, je connais le Pere ; et je donne ma vie pour les brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 16, 'J\'ai d\'autres brebis, qui ne sont pas de ce bercail. Celles-la aussi il faut que je les amene. Elles entendront ma voix, et il y aura un seul troupeau, un seul berger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 17, 'Voici pourquoi le Pere m\'aime : il m\'aime parce que je donne ma vie pour la reprendre de nouveau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 18, 'Personne ne me l\'ote, mais c\'est moi qui la donne de moi-meme ; j\'ai le pouvoir de la donner ; j\'ai le pouvoir de la reprendre ; tel est l\'ordre que j\'ai recu de mon Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 19, 'Ces paroles furent l\'occasion de nouvelles divisions entre les Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 20, 'C\'est un possede, disaient-ils pour la plupart, c\'est un fou ; pourquoi l\'ecoutez-vous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 21, 'D\'autres disaient : Ce ne sont pas les paroles d\'un possede ; est-ce qu\'un demon peut rendre ta vue aux aveugles?.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 22, 'C\'etait l\'hiver et l\'on celebrait a Jerusalem la fete de la Dedicace!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 23, 'Dans le Temple, sous le portique de Salomon, Jesus se promenait!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 24, 'Les Juifs firent cercle autour de lui et lui dirent: Jusqu\'a quand nous tiendras-tu l\'esprit en suspens? Si tu es le Christ, dis-le-nous franchement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 25, 'Je vous l\'ai dit, et vous ne croyez pas, repondit Jesus. Les oeuvres que je fais au nom de mon Pere, rendent temoignage de moi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 26, 'mais vous ne croyez pas parce que vous n\'etes pas de mes brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 27, 'Mes brebis a moi entendent ma voix ; je les connais, et elles me suivent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 28, 'Je leur donne la vie eternelle ; elles ne periront jamais, et nul ne les ravira de nia main.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 29, 'Mon Pere qui me les a donnees est plus grand que tous les hommes, et nul ne peut ravir ce qui est dans la main du Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 30, 'Moi et le Pere nous sommes uns.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 31, 'De nouveau les Juifs apporterent des pierres pour le lapider.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 32, 'Jesus leur dit: J\'ai fait devant vous, de par le Pere, beaucoup d\'oeuvres excellentes ; pour laquelle de ces oeuvres me lapidez vous? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 33, 'Ce n\'est pour aucune oeuvre excellente que nous te lapidons, repliquerent les Juifs, mais parce que tu blasphemes, et que, toi qui n\'es qu\'un homme, tu te fais Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 34, 'Jesus leur repondit : N\'est-il pas ecrit dans votre Loi : Moi, j\'ai dit : vous etes dieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 35, 'Eh bien, votre Loi a appele dieux ceux auxquels s\'adressait la parole de Dieu (et l\'Ecriture est indiscutable),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 36, 'et moi que le Pere a consacre et a envoye dans le monde, vous m\'accusez de blaspheme, parce que j\'ai dit : je suis Fils de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 37, 'Si je ne fais pas les oeuvres de mon Pere, ne me croyez point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 38, 'Mais si je les fais (quand meme vous n\'auriez aucune foi en mes paroles), croyez du moins a mes oeuvres ; qu\'elles vous fassent savoir, qu\'elles vous fassent reconnaitre que le Pere est en moi, et que moi je suis dans le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 39, '(Encore une fois ils s\'efforcerent de mettre la main sur lui, mais il leur echappa');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 40, 'et retourna sur l\'autre rive du Jourdain, a l\'endroit ou Jean avait baptise tout d\'abord, et il y sejourna.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 41, 'Des multitudes vinrent a lui et l\'on disait : Jean, il est vrai, n\'a fait aucun miracle ; mais tout ce qu\'il a dit de celui-ci etait vrai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 10, 42, 'La, un grand nombre eurent foi en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 1, 'Il y avait un malade, nomme Lazare, qui etait de Bethanie, le village de Marie et de Marthe, sa soeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 2, 'Ce fut cette Marie qui repandit un parfum sur le Seigneur, et qui lui essuya les pieds avec ses cheveux. Lazare, qui etait malade, etait son frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 3, 'Les soeurs envoyerent dire a Jesus : Seigneur, voila celui que tu aimes qui est malade. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 4, 'Cette maladie n\'est pas a la mort, a dit Jesus en l\'apprenant, mais elle est pour la gloire de Dieu, et afin que le Fils de Dieu soit glorifie par elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 5, 'Or, Jesus aimait Marthe et sa soeur et Lazare.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 6, 'Lors donc qu\'il eut appris la maladie de celui-ci, il demeura deux jours encore dans le lieu ou il se trouvait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 7, 'Apres quoi, il dit aux disciples : Retournons en Judee. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 8, 'Rabbi, lui repondirent-ils, les Juifs te cherchaient tout dernierement pour te lapider, et tu veux aller de nouveau vers eux! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 9, 'N\'y a-t-il pas douze heures a la journee, repliqua Jesus ; si quelqu\'un marche pendant le jour, il ne trebuche pas, parce qu\'il voit la lumiere de ce monde ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 10, 'mais s\'il marche pendant la nuit, il trebuche, parce que la lumiere n\'est pas en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 11, 'Il parla ainsi ; puis il ajouta : Notre ami Lazare s\'est endormi, mais je pars pour le reveiller. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 12, 'S\'il dort, Seigneur, il sera sauve, dirent les disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 13, 'Or Jesus avait parle de sa mort ; mais ils avaient cru, eux, qu\'il parlait du sommeil ordinaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 14, 'Il leur dit alors nettement: Lazare est mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 15, 'Et je me rejouis, a cause de vous, et afin que vous croyiez, de ce que je n\'etais pas la. Mais allons aupres de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 16, 'Sur ce mot, Thomas (celui qu\'on appelle Didyme), s\'adressant a ses condisciples : Et nous aussi, dit-il, allons-y afin de perir avec lui!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 17, 'Lorsque Jesus arriva, Lazare, depuis quatre jours, etait enseveli dans le tombeau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 18, 'Comme Bethanie n\'etait eloignee de Jerusalem que d\'environ quinze stades,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 19, 'beaucoup de Juifs s\'etaient rendus aupres de Marthe et de Marie pour les consoler de la mort de leur frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 20, 'Marthe, cependant, des qu\'elle eut appris que Jesus approchait, t alla au-devant de lui. Marie resta assise a la maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 21, 'Seigneur, dit Marthe a Jesus, si tu avais ete ici, mon frere ne serait pas mort ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 22, 'et, je sais, encore en ce moment, que tout ce que tu demanderas a Dieu, Dieu te le donnera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 23, 'Jesus lui repondit : Ton frere ressuscitera. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 24, 'Je le sais, lui dit Marthe, il ressuscitera a la resurrection, au dernier jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 25, 'Jesus lui dit : Je suis, moi, la resurrection et la vie. Qui croit en moi, fut-il mort, vivra,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 26, 'et quiconque vit et croit en moi ne mourra jamais. Crois-tu cela? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 27, 'Oui, Seigneur, repondit-elle, je crois que tu es le Christ, le Fils de Dieu, celui qui doit venir au monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 28, 'Apres ces paroles, elle s\'eloigna et alla appeler sa soeur. Le maitre est la et il t\'appelle, lui dit-elle en secret.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 29, 'A ces mots, Marie se leve a la hate et se rend vers Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 30, 'Celui-ci n\'etait pas encore entre dans le bourg ; il etait a l\'endroit ou Marthe l\'avait rencontre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 31, 'Voyant Marie se lever si vite et partir, les Juifs qui etaient ave celle dans la maison et cherchaient a la consoler, la suivirent. Ils croyaient qu\'elle allait au tombeau pour y pleurer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 32, 'Parvenue au lieu ou etait Jesus, Marie, en l\'apercevant, se jeta a ses pieds : Seigneur, lui dit-elle, si tu avais ete ici, mon frere ne serait pas mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 33, 'La voyant pleurer, et voyant pleurer les Juifs qui l\'accompagnaient, Jesus fremit en lui-meme et se troubla.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 34, 'Ou l\'avez-vous depose? demanda-t-il. On lui repondit : Seigneur, viens et vois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 35, 'Jesus pleura.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 36, 'Voyez comme il l\'aimait! dirent alors les Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 37, 'Il y en avait cependant quelques-uns qui disaient: Ne pouvait-il pas empecher qu\'il mourut, lui, qui a ouvert les yeux de l\'aveugle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 38, 'Fremissant de nouveau en lui-meme, Jesus s\'approche du sepulcre. C\'etait une caverne, sur l\'entree de laquelle une pierre etait placee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 39, 'Otez la pierre, dit Jesus.  Seigneur, dit Marthe, la soeur du mort, il sent deja mauvais! c\'est le quatrieme jour!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 40, 'Jesus lui repond : Ne t\'ai-je pas dit que si tu crois, tu verras la gloire de Dieu!,Donc, on enleva la pierre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 41, 'Alors Jesus, tournant son regard vers le ciel, dit : Pere! je te rends graces de ce que tu m\'as ecoute!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 42, 'Pour moi, je savais que tu m\'ecoutes toujours ; mais je parle ainsi a cause de ce peuple qui m\'environne, pour qu\'il croie que c\'est toi qui m\'as envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 43, 'Et quand il eut dit ces mots, il cria d\'une voix forte : Lazare! sors et viens !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 44, 'Le mort sortit, les pieds et les mains lies par des bandelettes, et le visage enveloppe d\'un suaire. Deliez-le, dit Jesus, et laissez-le aller.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 45, 'A la vue de ce qu\'il avait fait, plusieurs des Juifs qui etaient venus voir Marie, crurent en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 46, 'Mais quelques autres s\'en allerent trouver les Pharisiens et leur raconterent ce qu\'avait fait Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 47, 'Les chefs des pretres et les Pharisiens convoquerent alors une assemblee. Que faire? disaient-ils, cet homme opere de nombreux miracles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 48, 'si nous le laissons continuer, tout le monde croira en lui ; puis viendront les Romains qui en finiront et avec notre vit le et avec notre nation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 49, 'L\'un d\'entre eux, Kaiphe qui, en cette annee, etait le grand-pretre, leur dit : Vous n\'y entendez rien ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 50, 'vous ne reflechissez pas qu\'il vaut mieux pour vous qu\'un seul homme meure pour le peuple et que toute la nation ne perisse pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 51, 'Il parlait de la sorte, non de lui-meme, mais parce que, grand-pretre cette annee-la, il prophetisait que Jesus allait mourir pour la nation');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 52, 'et non seulement pour la nation, mais afin de rassembler dans l\'unite les enfants de Dieu disperses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 53, 'Donc, des ce jour, ils arreterent la resolution de mettre Jesus a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 54, 'Aussi celui-ci ne se montrait-il plus en public parmi les Juifs. Il s\'en alla meme dans le pays voisin du desert, a une ville nommee Ephraim ; et il y sejourna avec les disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 55, 'Cependant la Paque des Juifs approchait et, dans la contree, un grand nombre montaient, avant la fete, a Jerusalem, pour y proceder a des purifications.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 56, 'Aussi cherchaient-ils Jesus et, se rencontrant au Temple, ils se demandaient les uns aux autres : Pensez-vous qu\'il s\'abstiendra de venir a la fete?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 11, 57, 'Or les chefs des pretres et les Pharisiens avaient donne cet ordre : Que si quelqu\'un savait ou etait Jesus, il le declarat, afin qu\'on le fit arreter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 1, 'Six jours avant la Paque, Jesus vint a Bethanie, ou etait Lazare, qu\'il avait ressuscite des morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 2, 'On lui fit la un festin et Marthe servait ; Lazare etait un de ceux qui etaient a table avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 3, 'Quant a Marie, elle prit une livre d\'un parfum de nard pur, de tres haut prix, et elle en oignit les pieds de Jesus, les essuyant avec ses cheveux. La maison fut remplie de l\'odeur de ce parfum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 4, 'L\'un des disciples, Judas l\'Iskariote, celui qui devait le livrer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 5, 'dit: Pourquoi ne pas avoir vendu ce parfum? Il vaut trois cents deniers qu\'on aurait donnes a des pauvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 6, 'Il ne parlait pas ainsi, parce qu\'il se souciait des pauvres, mais parce qu\'il etait un voleur, et que, tenant la bourse commune, il se chargeait de ce qu\'on y mettait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 7, 'Jesus repondit : Laisse-la, afin qu\'elle reserve ce parfum pour le jour de ma sepulture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 8, 'Les pauvres, vous les avez toujours avec vous ; tandis que moi vous ne m\'avez pas pour toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 9, 'Une foule enorme de Juifs avaient appris la presence de Jesus, et ils arriverent non seulement a cause de lui, mais aussi pour voir Lazare qu\'il avait ressuscite des morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 10, 'Aussi les chefs des pretres songerent-ils a faire perir Lazare lui-meme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 11, 'parce qu\'une quantite de Juifs les abandonnaient a cause de cet homme, et croyaient en Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 12, 'Le lendemain une multitude immense, venue pour la fete, ayant appris que Jesus venait a Jerusalem');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 13, 'prit en main des branches de palmier et sortit au-devant de lui. Hosanna ! criait-on, Beni soit celui qui vient au nom du Seigneur Beni soit le roi d\'Israel!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 14, 'Jesus trouva un anon et s\'assit dessus, accomplissant cette parole de l\'Ecriture :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 15, 'Sois sans crainte, fille de Sion, Voici ton Roi qui t\'arrive Assis sur le petit d\'une anesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 16, 'Ses disciples, tout d\'abord, ne s\'en rendirent point compte. Mais quand Jesus eut ete glorifie, ils se souvinrent que ces choses avaient ete ecrites de lui, et qu\'ils les lui avaient faites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 17, 'La multitude qui etait avec lui attestait donc qu\'il avait appele Lazare du tombeau et l\'avait ressuscite des morts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 18, 'et c\'etait la rumeur d\'un tel miracle, opere par lui, qui faisait aussi accourir la foule a sa rencontre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 19, 'Quant aux Pharisiens, ils se disaient entre eux : Vous le voyez! Vous n\'y gagnez rien! Voila que tout le monde court apres lui !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 20, 'Parmi ceux qui etaient venus pour adorer Dieu pendant la fete, se trouvaient quelques Grecs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 21, 'Ils aborderent Philippe qui etait de Bethsaide en Galilee, et lui firent cette demande : Seigneur, nous desirons voir Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 22, 'Philippe alla en parler a Andre ; et tous deux ensemble allerent le dire a Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 23, 'Celui-ci leur adressa alors ces paroles : L\'heure est venue ou le Fils de l\'homme doit etre glorifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 24, 'En verite, en verite, je vous le dis, si le grain de froment tombant en terre ne passe par la mort, il demeure seul ; mais qu\'il vienne a mourir, il porte beaucoup de fruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 25, 'Qui aime sa vie la perdra ; et qui hait sa vie en ce monde, la conservera en vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 26, 'Qu\'il me suive, celui qui veut me servir! et la ou je suis mon serviteur sera aussi. Si quelqu\'un me sert, mon Pere l\'honorera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 27, 'Maintenant mon ame est troublee ; et que dirai-je? Dirai-je : Pere, preserve-moi de cette heure? mais, c\'est pour cela que je suis arrive a cette heure!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 28, 'Pere, glorifie ton nom! Du ciel il vint alors une voix : Je l\'ai glorifie et je le glorifierai encore! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 29, 'C\'est le tonnerre, dit la foule qui etait presente et qui entendait. Il y en avait qui disaient : C\'est un ange qui lui a parle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 30, 'Jesus reprit ainsi : Ce n\'est pas pour moi, mais pour vous que cette voix a retenti.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 31, 'Maintenant a lieu le jugement de ce monde ; maintenant le prince de ce monde va etre chasse dehors ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 32, 'et moi, quand j\'aurai ete eleve de terre, j\'attirerai tous les hommes a moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 33, 'Il disait cela pour indiquer de quelle mort il allait mourir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 34, 'La Loi nous apprend que le Christ demeure eternellement, lui repondit la foule ; comment donc dis-tu : Il faut que le Fils de l\'homme soit eleve de terre? Quel est ce Fils de l\'homme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 35, 'Jesus leur repondit : Pour un peu de temps encore la lumiere est avec vous. Marchez pendant que vous avez la lumiere, de peur que les tenebres ne vous surprennent. Celui qui marche dans les tenebres ne sait ou il va.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 36, 'Pendant que vous avez la lumiere, ayez foi en la lumiere, afin de devenir des fils de lumiere. Ainsi parla Jesus, puis il s\'eloigna et se cacha d\'eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 37, 'Ils ne croyaient pas en lui qui avait fait tant de miracles en leur presence ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 38, 'c\'etait afin que fut accomplie la parole du prophete Esaie qui a dit : Seigneur, qui a cru a ce que nous faisons entendre? Et a qui le bras du Seigneur a-t-il ete revele?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 39, 'Aussi bien, croire leur etait impossible, puisque Esaie a dit encore :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 40, 'Il a aveugle leurs yeux Et il a endurci leur coeur, Afin qu\'ils ne voient pas de leurs yeux ; Et qu\'ils ne comprennent pas de leur coeur ; Qu\'ils ne se convertissent pas Et que je ne les guerisse pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 41, 'Voila ce qu\'a dit Esaie quand il a vu sa gloire et qu\'il a parle de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 42, 'Et pourtant, meme parmi ceux qui exercaient l\'autorite, beaucoup avaient foi en lui ; mais, a cause des Pharisiens, ils n\'en convenaient pas, pour ne pas etre chasses de la synagogue,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 43, 'preferant en cela la gloire humaine a la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 44, 'Jesus eleva la voix : Qui croit en moi, s\'ecria-t-il, ne croit pas en moi, mais en Celui qui m\'a envoye,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 45, 'et qui me voit, voit Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 46, 'Moi, qui suis lumiere, je suis venu dans le monde afin que quiconque croit en moi ne demeure point dans les tenebres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 47, 'Quant a celui qui entend mes paroles et ne les garde point, ce n\'est pas moi qui le juge ; car je ne suis pas venu pour juger le monde ; mais pour sauver le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 48, 'Qui me rejette et ne recoit pas mes paroles a deja son juge ; ce juge, c\'est la parole meme que j\'ai dite, elle le jugera au dernier jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 49, 'Ce n\'est pas, en effet, de moi-meme que j\'ai parle ; mais c\'est le Pere, dont je suis l\'envoye, qui m\'a lui-meme commande de parler et qui m\'a prescrit ce que j\'avais a dire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 12, 50, 'Et je sais que son commandement c\'est la vie eternelle. Les choses donc que je dis, je les dis comme le Pere me les a dites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 1, 'Avant la fete de Paque, sachant que pour lui l\'heure etait venue, de passer de ce monde au Pere, Jesus, apres avoir aime ceux qui etaient siens dans le monde, mit le comble a son amour pour eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 2, 'Pendant un repas, alors que le diable avait deja jete dans le coeur de Judas, fils de Simon, l\'Iskariote, de le livrer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 3, 'sachant que le Pere avait tout remis entre ses mains, sachant qu\'il etait issu de Dieu et qu\'il retournait a Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 4, 'Jesus se leva de table, ota ses vetements, prit un linge et se ceignit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 5, 'Apres quoi, versant de l\'eau dans le bassin, il se mit a laver les pieds de ses disciples et a les essuyer avec le linge dont il etait ceint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 6, 'Il vint donc a Simon Pierre qui lui dit : Toi, Seigneur, a moi? me laver les pieds?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 7, 'Jesus lui repondit par ces paroles : Ce que je fais tu ne le sais pas maintenant, tu le sauras plus tard. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 8, 'Jamais, lui dit Pierre, non, jamais, tu ne me laveras les pieds !  Si je ne te lave, repliqua Jesus, tu n\'as point de part avec moi. -');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 9, 'Simon Pierre dit alors : Seigneur! non seulement tes pieds, mais aussi les mains et la tete ! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 10, 'Celui qui s\'est baigne, ajouta Jesus, n\'a plus besoin que de se laver les pieds ; dans tout le reste il est entierement pur ; et vous, vous etes purs ; non pas tous cependant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 11, 'Il connaissait, en effet, celui qui allait le livrer ; voila pourquoi il dit : Vous n\'etes pas tous purs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 12, 'Apres leur avoir lave les pieds il reprit ses vetements, se remit a table et leur dit : Comprenez-vous ce que je vous ai fait?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 13, 'Vous m\'appelez Maitre, Seigneur, et vous dites bien, car je le suis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 14, 'Si donc je vous ai lave les pieds, moi, le Seigneur, moi, le Maitre, vous devez, vous aussi, vous laver les pieds les uns aux autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 15, 'Car je vous ai donne un exemple pour que, vous aussi, vous fassiez comme je vous ai fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 16, 'En verite, en verite, je vous le dis, un serviteur n\'est pas plus grand que son Seigneur, ni un apotre plus grand que celui qui l\'envoie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 17, 'Si vous savez ces choses, vous etes heureux, pourvu que vous les mettiez en pratique. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 18, '(Je ne parle pas de vous tous. Ceux que j\'ai choisis, je les connais ; mais il faut que cette parole de l\'Ecriture s\'accomplisse : Celui qui mangeait a ma table a leve le talon contre moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 19, 'Je vous le dis des aujourd\'hui et avant l\'evenement, afin, quand il aura lieu, que vous croyiez que c\'est moi...) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 20, 'En verite, en verite, je vous le dis, recevoir quelqu\'un que j\'aurai envoye, c\'est me recevoir ; et me recevoir, c\'est recevoir Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 21, 'Apres qu\'il eut dit ces paroles, Jesus fut trouble en son esprit et il fit cette declaration : En verite, en verite, je vous le dis, l\'un d\'entre vous me livrera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 22, 'Les disciples se regardaient les uns les autres, ne sachant duquel il parlait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 23, 'L\'un d\'eux etait couche sur le sein de Jesus  c\'etait celui qu\'aimait Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 24, 'Simon Pierre lui fait signe et lui dit : Demande-lui de qui il parle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 25, 'L\'autre, se penchant aussitot sur la poitrine de Jesus, lui demande : Seigneur, lequel est-ce?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 26, 'Jesus repond : Celui pour lequel je vais tremper ce morceau de pain et auquel je le donnerai. Et Jesus, trempant un morceau de pain, le donne a Judas, fils de Simon, l\'Iskariote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 27, 'Quand cet homme eut pris ce morceau, Satan entra en lui, Jesus lui dit alors : Ce que tu fais, fais-le au plus tot.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 28, 'Aucun de ceux qui etaient a table ne comprit pour quel objet il lui parlait ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 29, 'Comme Judas etait porteur de la bourse commune, quelques-uns penserent que Jesus lui disait soit d\'acheter ce qu\'il fallait pour la fete, soit de donner telle ou telle chose aux pauvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 30, 'Donc, a peine Judas eut-il pris ce morceau de pain, qu\'il sortit brusquement. Il faisait nuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 31, 'Quand il fut parti, Jesus dit : C\'est maintenant que le Fils de l\'homme est glorifie et que Dieu est glorifie en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 32, 'Dieu le glorifiera lui aussi, en lui-meme, et c\'est bientot qu\'il le glorifiera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 33, 'Mes enfants, je ne suis plus que pour peu de temps avec vous ! vous me chercherez ; et ce que j\'ai dit aux Juifs, je vous le dis aussi a vous-memes a l\'heure presente : ou je vais vous ne pouvez venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 34, 'Je vous donne un commandement nouveau: vous aimer les uns les autres. Oui, aimez-vous les uns les autres, comme je vous ai aimes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 35, 'Tous reconnaitront que vous etes mes disciples a l\'amour que vous aurez les uns pour les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 36, 'Seigneur, lui demanda Simon Pierre, ou vas-tu ?  Au lieu ou je vais, repondit Jesus, tu ne peux me suivre presentement ; tu me suivras plus tard. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 37, 'Et pourquoi donc, Seigneur, lui dit Pierre, ne puis-je te suivre des maintenant. Je donnerai ma vie pour toi! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 13, 38, 'Tu donneras ta vie pour moi! repliqua Jesus, en verite, en verite, je te le dis, un coq ne chantera pas avant que trois fois tu ne m\'aies renie !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 1, 'Que votre coeur ne se trouble point ; ayez foi en Dieu, ayez, aussi foi en moi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 2, 'il y a plusieurs demeures dans la maison de mon Pere, sinon je vous l\'aurais dit ; je vais vous preparer une place ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 3, 'et quand je m\'en serai alle et vous aurai prepare une place, je reviendrai et vous prendrai avec moi, afin que la ou je suis, vous y soyez aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 4, 'Et vous savez le chemin de l\'endroit ou je vais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 5, 'Thomas lui dit : Seigneur, nous ne savons ou tu vas, comment saurions-nous le chemin?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 6, 'Jesus lui dit : Je suis, moi, le chemin, la verite et la vie. Personne ne vient au Pere que par moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 7, 'Si vous m\'avez connu, vous connaitrez aussi mon Pere et des a present vous le connaissez et vous l\'avez vu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 8, 'Philippe lui dit : Seigneur, montre-nous le Pere, cela nous suffit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 9, 'Jesus lui dit : Il y a si longtemps que je suis avec vous et tu ne m\'as pas connu, Philippe! Qui m\'a vu a vu le Pere. Comment peux-tu dire: Montre-nous le Pere?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 10, 'Ne crois-tu pas que je suis, moi, dans le Pere, et que le Pere est en moi. Les paroles que je vous dis, ce n\'est pas de moi-meme que je les prononce. C\'est le Pere, demeurant en moi, qui fait ces oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 11, 'Croyez-moi, croyez parce que je suis, moi, dans le Pere et que le Pere est en moi ; sinon, croyez a cause de ces oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 12, 'En verite, en verite, je vous le dis, les oeuvres que je fais, qui croit en moi les fera, lui aussi, et en fera de plus grandes, parce que je m\'en vais aupres du Pere');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 13, 'et que tout ce que vous demanderez en mon nom, je le ferai, afin que le Pere soit glorifie dans le Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 14, 'Si vous me demandez quelque chose en mon nom, moi, je le ferai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 15, 'Si vous m\'aimez, gardez mes commandements ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 16, 'et moi, je prierai le Pere et il vous donnera un autre Conseiller, afin qu\'il soit pour toujours avec vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 17, 'l\'Esprit de verite, que le monde ne peut recevoir parce qu\'il ne le voit ni ne le connait ; mais vous, vous le connaissez parce qu\'il demeure avec vous, et il sera en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 18, 'Je ne vous laisserai pas orphelins ; je reviens a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 19, 'Encore un peu de temps, et le monde ne me verra plus ; mais vous, vous me verrez, car je vis, moi, et vous aussi, vous vivrez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 20, 'En ce jour-la, vous reconnaitrez que je suis en mon Pere, que vous etes en moi, et que je suis en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 21, 'Qui retient mes commandements et les garde, celui-la est celui qui m\'aime. Et celui qui m\'aime sera aime de mon Pere, et moi je l\'aimerai et je me manifesterai a lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 22, 'Judas (qu\'il ne faut pas confondre avec l\'Iskariote) lui dit : Seigneur, qu\'est-il arrive pour que tu veuilles te manifester a nous et non pas au monde?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 23, 'Jesus lui repondit par ces paroles : Si quelqu\'un m\'aime, il gardera ma parole et mon Pere l\'aimera et nous viendrons a lui et nous ferons chez lui notre demeure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 24, 'Celui qui ne m\'aime pas ne garde pas mes paroles ; et la parole que vous entendez n\'est pas mienne, mais de Celui qui m\'a envoye, du Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 25, 'Je vous ai parle de ces choses pendant que je demeurais avec vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 26, 'mais le Conseiller, lEsprit saint que le Pere enverra en mon nom, vous enseignera toutes choses et vous rappellera tout ce que je vous ai dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 27, 'Je vous laisse la paix, je vous donne ma paix ; ce n\'est pas comme le monde la donne, que, moi, je vous la donne ; que votre coeur ne se trouble pas, qu\'il ne faiblisse pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 28, 'Vous avez entendu que je vous ai dit : Je m\'en vais et je reviens a vous. Si vous m\'aimiez, vous vous rejouiriez de ce que je vais aupres du Pere, parce que le Pere est plus grand que moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 29, 'Et je vous ai parle maintenant, avant l\'evenement, afin que vous croyiez lorsqu\'il arrivera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 30, 'Je ne vous parlerai plus guere, car le Prince du monde vient, et en moi il n\'a rien ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 14, 31, 'mais il vient afin que le monde sache que j\'aime le Pere et que j\'agis suivant les ordres du Pere. Levez-vous, sortons d\'ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 1, 'Moi, je suis le veritable cep de vigne et. mon Pere est le vigneron.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 2, 'Tout sarment qui ne porte pas de fruit en moi, il le retranche et tout sarment qui en porte il l\'emonde, afin qu\'il en porte davantage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 3, 'Vous, vous etes deja emondes, a cause de la parole que je vous ai annoncee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 4, 'Demeurez-en moi et moi je demeurerai en vous. De meme que le sarment ne peut porter de fruit de lui-meme, et qu\'il lui faut demeurer uni au cep de vigne, de meme vous n\'en pourrez porter si vous ne demeurez pas en moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 5, 'Moi, je suis le cep de vigne ; vous, vous etes les sarments. Qui demeure en moi, et moi en lui, porte, lui, beaucoup de fruit, parce que, sans moi, vous ne pouvez rien faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 6, 'Si quelqu\'un ne demeure pas en moi, il est jete dehors comme le sarment, et il se desseche, et on ramasse ce sarment, et on le jette au feu et il brule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 7, 'Si vous demeurez-en moi, et que mes paroles demeurent en vous, ce que vous voudrez, demandez-le, et vous l\'aurez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 8, 'En ceci mon Pere est glorifie que vous portiez beaucoup de fruits, et alors vous deviendrez mes disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 9, 'De meme que le Pere m\'a aime, de meme je vous ai aimes. Demeurez dans mon amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 10, 'Si vous gardez mes commandements, vous demeurerez dans mon amour, comme moi j\'ai garde les commandements de mon Pere, et je demeure dans son amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 11, 'Je vous ai parle ainsi afin que ma joie soit la votre, et que il votre joie soit dans sa plenitude.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 12, 'Voici mon commandement : Aimez-vous les uns les autres, comme je vous ai aimes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 13, 'Nul amour n\'est plus grand que l\'amour de celui qui donne sa vie pour ses amis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 14, 'Vous etes mes amis si vous faites ce que je vous commande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 15, 'Je ne vous appelle plus serviteurs, parce que le serviteur ne sait pas ce que fait son maitre ; je vous ai dit : mes amis, parce que tout ce que j\'ai appris de mon Pere, je vous l\'ai fait connaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 16, 'Ce n\'est pas vous qui m\'avez choisi ; c\'est moi qui vous ai choisis et vous ai mis a votre place afin que vous alliez porter du fruit, et que votre fruit demeure, et afin que tout ce que vous demanderez au Pere en mon nom, il vous le donne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 17, 'Voila ce que je vous commande, et c\'est pour que vous vous aimiez les uns les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 18, 'Si le monde vous hait, sachez qu\'il m\'a hai avant vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 19, 'Si vous etiez du monde, le monde aimerait ce qui lui appartient ; comme vous n\'etes pas du monde, mais que je vous ai choisis du monde, a cause de cela le monde vous hait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 20, 'Souvenez-vous de la parole que je vous ai dite : Le serviteur n\'est pas plus grand que son maitre. S\'ils m\'ont persecute, ils vous persecuteront aussi. S\'ils ont garde ma parole, ils garderont aussi la votre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 21, 'Mais toutes ces choses ils vous les feront a cause de mon nom, parce qu\'ils ne connaissent pas Celui qui m\'a envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 22, 'Si je n\'etais point venu et ne leur eusse point parle, ils ne seraient point coupables ; mais maintenant leur culpabilite est sans excuse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 23, 'Qui me hait, hait aussi mon Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 24, 'Si je n\'avais pas fait au milieu d\'eux des oeuvres que nul autre n\'a faites, ils ne seraient pas coupables ; mais maintenant leurs yeux ont vu et neanmoins ils ont hai et moi et mon Pere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 25, 'mais c\'est afin que s\'accomplisse la parole ecrite dans leur Loi : Ils me hairent sans sujet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 26, 'Quand sera venu le Conseiller que je vous enverrai de la part du Pere, l\'Esprit de verite qui procede du Pere, lui, rendra temoignage de moi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 15, 27, 'et vous aussi, vous me rendez temoignage, parce que, depuis le commencement, vous etes avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 1, 'Je vous parle ainsi pour vous preserver de toute chute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 2, 'On vous expulsera des synagogues ; et meme elle approche, l\'heure ou tous ceux qui vous mettront a mort croiront rendre un culte a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 3, 'Ils feront cela parce qu\'ils n\'auront connu ni le Pere, ni moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 4, 'Je vous parle ainsi pour que, leur moment venu, vous vous souveniez que je vous ai dit ces choses ; je ne vous les ai pas dites des le commencement parce que j\'etais avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 5, 'Maintenant je m\'en vais aupres de Celui qui m\'a envoye et aucun de vous ne me demande : Ou vas-tu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 6, 'mais parce que je vous ai ainsi parle votre coeur s\'est rempli de tristesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 7, 'Cependant, c\'est la verite que je dis, il est bon pour vous que je m\'en aille ; car, si je ne m\'en vais, le Conseiller ne viendra pas vers vous. Si je m\'en vais, je vous l\'enverrai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 8, 'Et quand il sera venu, il convaincra le monde en ce qui concerne le peche, la justice et le jugement :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 9, 'le peche en tant qu\'ils ne croient pas en moi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 10, 'la justice en tant que je m\'en vais aupres du Pere, et que vous ne me verrez plus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 11, 'le jugement en tant que le Prince de ce monde est il juge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 12, 'J\'ai encore beaucoup de choses a vous dire ; mais vous ne les pouvez pas encore porter ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 13, 'quand il sera venu, lui, l\'Esprit de verite, il vous dirigera dans toute la verite, car il ne parlera pas de son propre fonds, mais tout ce qu\'il aura entendu il le dira et il vous annoncera l\'avenir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 14, 'C\'est lui qui me glorifiera, parce qu\'il prendra de ce qui est a moi et il vous l\'annoncera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 15, 'Tout ce qu\'a le Pere est aussi a moi ; voila pourquoi je vous dis : Il prend de ce qui est a moi, et il vous l\'annoncera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 16, 'Encore un peu de temps, et vous ne me verrez plus, puis encore un peu de temps, et vous me verrez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 17, 'Alors quelques-uns de ses disciples se demanderent l\'un a l\'autre : Qu\'est-ce qu\'il nous dit : Encore un peu de temps, et vous ne me verrez pas, puis encore un peu de temps, et vous me verrez ; et : je m\'en vais aupres du Pere. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 18, 'Que signifient, disaient-ils, ces mots : Un peu de temps. Nous ne savons de quoi il parle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 19, 'Jesus comprit qu\'ils desiraient l\'interroger, et il leur dit : Vous vous demandez l\'un a l\'autre ce que j\'ai voulu dire par ces mots : Encore un peu de temps, et vous ne me verrez pas, puis encore un peu de temps, et vous me verrez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 20, 'En verite, en verite, je vous le dis, vous, vous pleurerez et vous gemirez ; quant au monde, il se rejouira. Vous, vous serez dans la douleur ; mais votre douleur sera changee en joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 21, 'La femme passe par la douleur pendant qu\'elle enfante, parce que, pour elle, l\'heure est venue. Mais quand l\'enfant est ne, elle oublie son angoisse, tant est grande sa joie d\'avoir mis un homme au monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 22, 'Vous, de meme, vous passez maintenant par la douleur ; mais je vous reverrai, et votre coeur se rejouira, et nul ne vous ravira votre joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 23, 'Et, en ce jour-la, vous ne m\'interrogerez plus sur rien. En verite, en verite, je vous le dis, si vous demandez quelque chose au Pere en mon nom, il vous le donnera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 24, 'Jusqu\'ici vous n\'avez rien demande en mon nom. Demandez et vous recevrez afin que votre joie soit dans sa plenitude.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 25, 'Je vous ai parle de ces choses en termes figures ; il vient une heure ou je ne vous parlerai plus en termes figures, mais ou je vous communiquerai clairement ce qui concerne le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 26, 'En ce jour-la vous demanderez en mon nom, et je ne vous dis pas que je prierai le Pere pour vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 27, 'car le Pere lui-meme vous aime, parce que vous m\'avez aime, et que vous avez cru que je suis issu du Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 28, 'Je suis issu du Pere, et je suis venu dans le monde ; maintenant je quitte le monde, et je vais aupres du Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 29, 'C\'est maintenant, dirent les disciples, que tu parles clairement, que tu n\'emploies pas de termes figures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 30, 'C\'est maintenant que nous voyons que tu sais toutes choses, et que tu n\'as besoin d\'etre interroge par personne. Voila pourquoi nous croyons que tu es issu de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 31, 'Vous croyez pour le moment, leur repondit Jesus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 32, 'mais il vient une heure, elle est meme deja venue, ou vous vous disperserez chacun de son cote, et ou vous me laisserez seul. Mais je ne suis pas seul parce que le Pere est avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 16, 33, 'Je vous ai ainsi parle afin que vous ayez la paix en moi ; dans le monde, vous passez par l\'angoisse ; mais prenez courage, j\'ai vaincu le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 1, 'Ainsi parla Jesus. Puis, levant les yeux au ciel, il dit : Pere, l\'heure est venue ; glorifie ton Fils, afin que le Fils te glorifie en donnant,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 2, 'selon le pouvoir que tu lui as donne sur toute chair, la vie eternelle, a tous ceux que tu lui as donnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 3, 'Or voici l\'eternelle vie : Te connaitre, toi, le seul vrai Dieu et celui que tu as envoye, Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 4, 'Moi, je t\'ai glorifie sur la terre ; j\'ai accompli l\'oeuvre que tu m\'avais donnee a faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 5, 'Et maintenant, glorifie-moi, toi, Pere, aupres de toi-meme, de la gloire que j\'avais, avant que le monde fut, aupres de toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 6, 'J\'ai revele ton nom aux hommes que tu m\'as donnes du monde. Ils etaient a toi, et tu me les as donnes ; et ils ont garde ta parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 7, 'Maintenant ils ont reconnu que tout ce que tu m\'as donne vient de toi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 8, 'parce que les paroles que tu m\'as donnees, je les leur ai donnees, et ils les ont recues, et ils ont reconnu veritablement que je suis issu de toi, et ils ont cru que c\'est toi qui m\'as envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 9, 'C\'est pour eux que je prie ; ce n\'est pas pour le monde que je prie, mais pour ceux que tu m\'as donnes parce qu\'ils sont a toi ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 10, '(or tout ce qui est a moi est a toi, et tout ce qui est a toi est a moi)  et que j\'ai ete glorifie en eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 11, 'Je ne suis plus dans le monde, mais eux sont dans le monde, et moi je viens a toi. Pere saint! garde-les en ton nom, le nom que tu m\'as donne, afin qu\'ils soient uns comme nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 12, 'Quand j\'etais avec eux, c\'est moi qui les gardais en ton nom, le nom que tu m\'as donne, et je les ai preserves et aucun d\'eux ne s\'est perdu, excepte le fils de la perdition, afin que l\'Ecriture fut accomplie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 13, 'Maintenant je viens a toi, et je parle ainsi, etant encore dans le monde, afin qu\'ils aient en eux la plenitude de ma joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 14, 'Je leur ai donne ta parole, et le monde les a pris en haine, parce qu\'ils ne sont pas du monde, comme moi je ne suis pas du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 15, 'Je ne demande pas que tu les retires du monde, mais que tu les preserves du mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 16, 'Ils ne sont pas du monde, comme moi je ne suis pas du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 17, 'Consacre-les dans la verite. Ta parole est la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 18, 'De meme que tu m\'as envoye dans le monde, je les ai, moi aussi, envoyes dans le monde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 19, 'et je me consacre moi-meme pour eux afin qu\'ils soient, eux aussi, consacres en verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 20, 'Ce n\'est pas seulement pour eux que je prie, mais aussi pour ceux qui croient en moi par leur parole,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 21, 'afin que tous ils soient un, comme toi, Pere, es en moi, et moi en toi, qu\'eux aussi soient en nous, pour que le monde croie que c\'est toi qui m\'as envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 22, 'Et moi, la gloire que tu m\'as donnee, je la leur ai donnee, afin qu\'ils soient un comme nous sommes un,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 23, 'moi en eux et toi en moi, que cette unite soit parfaite, et que le monde reconnaisse que c\'est toi qui m\'as envoye, et que tu les as aimes comme tu m\'as aime.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 24, 'Pere! ceux que tu m\'as donnes, je veux que la ou je suis, moi, ils y soient avec moi, afin qu\'ils voient la gloire que tu m\'as donnee, parce que tu m\'as aime avant la creation du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 25, 'Pere juste! le monde ne t\'a pas connu ; mais moi je t\'ai connu, et ceux-ci ont reconnu que c\'est toi qui m\'as envoye.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 17, 26, 'Et je leur ai fait connaitre ton nom ; et je le leur ferai connaitre, afin que l\'amour dont tu m\'as aime soit en eux, et que je sois moi aussi en eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 1, 'Apres avoir ainsi parle, Jesus sortit avec ses disciples et traversa le torrent de Kedron. La se trouvait un jardin dans lequel il entra, et eux avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 2, 'Judas, celui qui le livrait, connaissait cet \' endroit, parce que Jesus y etait souvent venu avec ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 3, 'Ayant pris une escouade de soldats et accompagne d\'agents fournis par les chefs des pretres et les Pharisiens, Judas arriva donc en cet endroit avec des lanternes, des torches et des armes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 4, 'Jesus, sachant tout ce qui allait lui arriver, s\'avanca et leur dit : Qui cherchez-vous? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 5, 'Jesus de Nazareth, lui repondirent ils.  Jesus leur dit : C\'est moi. Au milieu d\'eux se tenait Judas, celui qui le livrait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 6, 'Mais a peine Jesus avait-il dit : C\'est moi, qu\'ils firent un mouvement en arriere et tomberent sur le sol.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 7, 'Il leur demanda une seconde fois: Qui cherchez-vous?  Jesus de Nazareth, dirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 8, ' Jesus reprit : Je vous ai dit que c\'est moi ; si donc c\'est moi que vous cherchez, laissez ceux-ci s\'en aller.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 9, '(C\'etait afin que fut accomplie la parole qu\'il avait dite : De ceux que tu m\'as donnes, je n\'en ai perdu aucun.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 10, 'Simon Pierre, cependant, avait une epee, et la tira. Il en frappa le serviteur du grand-pretre et lui coupa l\'oreille droite.  Ce serviteur s se nommait Malchus. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 11, 'Remets ton epee dans le fourreau, dit alors Jesus a Pierre ; la coupe que m\'a donnee le Pere, ne la boirai-je pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 12, 'Cependant la cohorte, le tribun et les agents des Juifs se saisirent de Jesus et le garrotterent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 13, 'Ils commencerent par le mener chez Hanne, beau-pere de Kaiphe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 14, '(Kaiphe etait le grand-pretre de l\'annee, et c\'etait lui qui avait ouvert aux Juifs cet avis : Il vaut mieux qu\'un seul homme meure pour le peuple.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 15, 'Simon Pierre et un autre disciple avaient suivi Jesus. Cet autre disciple, etant de la connaissance du grand-pretre, entra en meme temps que Jesus dans la cour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 16, 'Quant a Pierre, il etait reste dehors contre la porte. L\'autre disciple, qui etait de la connaissance du grand-pretre, sortit pour parler a la portiere et le fit entrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 17, 'Cette servante, qui gardait la porte, dit alors a Pierre : N\'es-tu pas toi aussi des disciples de cet homme? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 18, 'Non ; pas moi, repondit-il. Il faisait froid ; les esclaves et les hommes de service, groupes autour d\'un brasier allume par eux, se chauffaient ; et Pierre, se tenant au milieu d\'eux, se chauffait aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 19, 'Le grand-pretre cependant interrogea Jesus sur ses disciples et sur son enseignement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 20, 'Jesus lui repondit : C\'est ouvertement que j\'ai parle au monde ; j\'ai toujours enseigne dans la synagogue et dans le Temple, la ou se reunissent tous les Juifs, et je n\'ai rien dit en cachette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 21, 'Pourquoi m\'interroges-tu? demande a mes auditeurs de quoi je leur ai parle. Eux savent bien ce que j\'ai dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 22, 'A ces mots, un des hommes de service qui se tenait a cote de Jesus, lui donna un coup de baton en lui disant : Est-ce de la sorte que tu reponds au grand-pretre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 23, 'Jesus s\'adressa a cet homme : Si j\'ai mal parle, prouve ce que j\'ai dit de mal ; si j\'ai bien parle, pourquoi me frappes-tu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 24, 'Hanne l\'envoya, tout garrotte, a Kaiphe, le grand-pretre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 25, 'Cependant Simon Pierre se tenait la et se chauffait. N\'es-tu pas, toi aussi, de ses disciples? lui dit-on. Il le nia. Moi? dit-il, non pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 26, 'Un des serviteurs du grand-pretre, parent de celui a qui Pierre avait coupe l\'oreille, lui dit : Est-ce que, moi-meme, je ne t\'ai pas vu, avec lui, dans le jardin?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 27, 'Encore une fois Pierre nia, et a ce moment un coq chanta.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 28, 'De chez Kaiphe, ils menerent Jesus au pretoire ; le jour se levait. Eux-memes n\'entrerent pas dans le pretoire afin de ne pas contracter de souillure et de pouvoir manger la Paque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 29, 'Pilate vint donc a eux dehors. Quelle accusation, dit-il, portez-vous contre cet homme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 30, 'Ils lui repondirent par ces paroles : Si ce n\'etait pas un malfaiteur, nous ne te l\'aurions pas livre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 31, 'Pilate leur repliqua : Prenez-le, et jugez-le vous-memes, suivant votre Loi.  Nous n\'avons pas le droit, repondirent les Juifs, de mettre personne a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 32, '(C\'etait afin que fut accomplie la parole que Jesus avait dite pour indiquer de quelle mort il allait mourir.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 33, 'Pilate rentra donc dans le pretoire, fit venir Jesus et lui dit : Tu es le Roi des Juifs? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 34, 'Est-ce de toi-meme que tu dis cela, repondit Jesus, ou d\'autres te l\'ont-ils dit de moi? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 35, 'Est-ce que je suis Juif, moi ! repliqua Pilate, c\'est ta nation, ce sont les chefs des pretres qui t\'ont livre a moi ; qu\'as-tu fait?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 36, 'Jesus repondit : Ma Royaute n\'est pas de ce monde. Si ma Royaute etait de ce monde, mes sujets combattraient pour moi, pour que je ne fusse pas livre aux Juifs ; mais ma Royaute n\'est pas d\'ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 37, 'Alors Pilate lui dit : Ainsi donc tu es Roi! Jesus repondit : Tu le dis, je suis Roi. Voici pourquoi je suis ne, voici pourquoi je suis venu dans le monde : pour rendre temoignage a la verite. Quiconque est de la verite ecoute ma voix. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 38, 'Qu\'est-ce que la verite? lui dit Pilate. Sur ce mot, il revint dehors vers les Juifs et leur dit : Moi, je ne trouve en lui aucune matiere a condamnation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 39, 'Il est d\'usage que je vous delivre quelqu\'un a la fete de Paque, voulez-vous que je vous delivre le Roi des Juifs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 18, 40, 'Ils recommencerent a crier : Pas celui-la, disaient-ils, mais Bar-Abbas. Ce Bar-Abbas etait un brigand.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 1, 'Pilate ordonna alors de saisir Jesus et de le flageller.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 2, 'Les soldats, ayant tresse une couronne avec des epines, la lui poserent sur la tete et l\'affublerent d\'un manteau couleur pourpre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 3, 'puis ils s\'avancaient vers lui et disaient : Salut! le Roi des Juifs! Et il lui donnaient des coups de baton.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 4, 'Encore une fois, Pilate revint dehors. Je vais vous l\'amener, leur dit-il, pour que vous sachiez que je ne trouve aucun motif de le condamner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 5, 'Et Jesus parut dehors, portant la couronne d\'epines et le manteau couleur pourpre. Voila l\'homme, leur dit Pilate.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 6, 'Des qu\'ils le virent, les chefs des pretres et leurs gens se mirent a crier : Crucifie-le ! crucifie-le!  Prenez-le vous-memes, leur dit Pilate, et crucifiez-le! Quant a moi je ne trouve en lui nulle matiere a condamnation. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 7, 'Nous avons une Loi, repliquerent les Juifs, et de par cette Loi, il faut qu\'il meure pour s\'etre fait Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 8, 'Pilate, entendant ces paroles, fut de plus en plus effraye,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 9, 'Rentrant encore une fois dans le pretoire, il dit a Jesus : D\'ou es-tu? Mais Jesus ne lui fit point de reponse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 10, 'Tu ne me parles pas ? reprit Pilate, ignores-tu que j\'ai le pouvoir de te delivrer et le pouvoir de te crucifier. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 11, 'Tu n\'aurais contre moi aucun pouvoir, repondit Jesus, s\'il ne t\'etait donne d\'en haut. Voila pourquoi celui qui me livre a toi est coupable d\'un plus grand peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 12, 'La-dessus, Pilate cherchait a le delivrer ; mais les Juifs lui criaient ces paroles : Si tu delivres cet homme, tu n\'es pas partisan de Cesar, car quiconque se fait Roi se pose en adversaire de Cesar.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 13, 'Pilate, entendant un tel langage, fit venir Jesus au dehors, et prit siege au tribunal, a l\'endroit appele Pave en mosaique (en hebreu Gabbatha).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 14, 'C\'etait le jour de la Preparation de la Paque, et vers la sixieme heure. Pilate dit aux Juifs : Voila votre Roi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 15, 'Ceux-ci se mirent a crier : A mort! a mort! crucifie-le!  Votre Roi! leur dit Pilate, je le crucifierais!  Nous n\'avons d\'autre Roi que Cesar, repondirent les chefs des pretres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 16, 'Ce fut alors que Pilate le leur abandonna pour etre crucifie. Ils s\'emparerent de Jesus');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 17, 'et lui, portant lui-meme la croix, vint au lieu qu\'on appelle Crane (en hebreu Golgotha).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 18, 'C\'est la qu\'ils le crucifierent et avec lui deux autres, un de chaque cote, et au milieu, Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 19, 'Pilate redigea aussi une inscription et la fit placer sur la croix ; il y etait ecrit : JESUS DE NAZARETH, LE ROI DES JUIFS.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 20, 'Cette inscription, beaucoup de Juifs la lurent parce que le lieu ou Jesus fut crucifie etait pres de la ville. Elle etait redigee en hebreu, en latin et en grec.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 21, 'Alors les chefs des pretres Juifs dirent a Pilate : N\'inscris pas : LE ROI DES JUIFS ; mais : CET HOMME A DIT : JE SUIS LE ROI DES JUIFS. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 22, 'Ce que j\'ai ecrit, je l\'ai ecrit, repondit Pilate.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 23, 'Apres avoir mis Jesus en croix, les soldats s\'emparerent de ses vetements dont ils firent quatre parts ; une pour chaque soldat. Restait la tonique, une tunique sans couture, tout entiere d\'un seul tissu depuis le haut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 24, 'Ne la dechirons pas, se dirent-ils, mais tirons au sort a qui l\'aura. (C\'etait afin que fut accomplie l\'Ecriture : Ils ont partage mes vetements entre eux Et sur ma robe ils ont jete le sort.) Donc, les soldats firent ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 25, 'Pres de la croix de Jesus se tenaient sa mere, la soeur de sa mere, Marie, femme de Clopas, et Marie Magdeleine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 26, 'Jesus, voyant sa mere et, pres d\'elle, le disciple qu\'il aimait, dit a sa mere : Femme, voila ton fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 27, 'Puis il dit au disciple : Voila ta mere. A partir de ce moment le disciple la prit chez lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 28, 'Apres cela, Jesus sachant que desormais tout etait acheve d\'accomplir, dit, afin de parfaire cet accomplissement de l\'Ecriture : J\'ai soif.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 29, 'Il y avait la un vase plein de vinaigre. On y trempa une eponge qui fut fixee a une tige d\'hysope, et on l\'approcha de sa bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 30, 'Quand il eut pris le vinaigre, il dit: Tout est accompli et, ayant baisse la tete, il rendit l\'esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 31, 'C\'etait le jour de la Preparation et pour que les corps ne restassent pas en croix pendant le sabbat,  un sabbat qui devait etre tres solennel,  les Juifs demanderent a Pilate de faire briser les jambes des supplicies et de les faire enlever.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 32, 'Les soldats vinrent donc et briserent les jambes du premier crucifie, puis du second.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 33, 'Arrivant a Jesus, ils s\'apercurent qu\'il etait deja mort, et alors ils ne lui briserent pas les jambes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 34, 'Seulement un des soldats lui perca le cote d\'un coup de lance, et il en sortit immediatement du sang et de l\'eau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 35, 'Celui qui l\'a vu l\'a atteste et son attestation est veridique. Lui, il sait qu\'il dit vrai, afin que, vous aussi, vous croyiez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 36, '(Car cela arriva afin que fut accomplie l\'Ecriture : Aucun de ses os ne sera brise');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 37, 'et un autre passage dit encore : Ils regarderont celui qu\'ils ont perce.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 38, 'Apres cela, Joseph (celui qui est d\'Arimathee),  il etait disciple de Jesus, mais en secret par crainte des Juifs,  demanda a Pilate la permission d\'enlever le corps de Jesus. Pilate la lui donna. Il vint donc enlever le corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 39, 'Avec lui se trouvait aussi Nicodeme (celui qui etait alle trouver Jesus pendant la nuit au commencement) ; il apportait une composition de myrrhe et d\'aloes, une centaine de livres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 40, 'Ils prirent donc le corps de Jesus et l\'envelopperent dans des bandes de linge avec les parfums, suivant le mode d\'ensevelissement usite chez les Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 41, 'Il y avait dans le lieu ou il avait ete crucifie un jardin et, dans ce jardin, un sepulcre neuf, ou personne n\'avait encore ete place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 19, 42, 'Ce fut donc la, a cause de la Preparation des Juifs, et parce que ce sepulcre etait tout proche, qu\'ils deposerent Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 1, 'Le premier jour de la semaine, Marie Magdeleine se rend de grand matin, avant le jour, au sepulcre et voit la pierre enlevee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 2, 'Alors elle court trouver Simon Pierre et l\'autre disciple (celui que Jesus aimait) et leur dit : On a enleve le Seigneur de son tombeau, et nous ne savons ou on l\'a place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 3, 'Pierre sort alors avec l\'autre disciple,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 4, 'et tous deux ensemble se mettent a courir pour aller au sepulcre ; mais l\'autre disciple courait devant et plus vite que Pierre. Il arrive le premier ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 5, 'il se penche, voit les linges poses a terre (toutefois il n\'entra point).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 6, 'Arrive alors Simon Pierre qui le suivait : lui, il entre dans le tombeau. Il regarde ; les linges etaient la ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 7, 'le suaire, qui avait ete sur la tete, n\'etait pas place avec les linges, mais roule a part a un autre endroit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 8, 'Alors l\'autre disciple, celui qui etait arrive le premier au tombeau, y entra aussi ; et il vit, et il crut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 9, 'Jusque-la, en effet, ils ne comprenaient pas l\'Ecriture ou il est dit : Qu\'il faut qu\'il ressuscite d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 10, 'Les disciples retournerent chez eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 11, 'Mais Marie etait restee pres du tombeau, a l\'entree, et pleurait. Tout en pleurant, elle se pencha et regarda dans le sepulcre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 12, 'Elle y voit deux anges en vetements blancs, s\'asseyant a la place ou etait le corps de Jesus, l\'un du cote de la tete, l\'autre du cote des pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 13, 'Ils lui disent : Femme, pourquoi pleures-tu? Et elle leur repond : Parce qu\'on a enleve mon Seigneur, et que je ne sais ou on l\'a place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 14, 'Ayant dit cela, elle se retourne et, derriere elle, elle voit Jesus qui etait la, debout. (Elle ne savait pas que c\'etait Jesus.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 15, 'Jesus lui dit : Femme, pourquoi pleures-tu? Qui cherches-tu? Elle, le prenant pour le jardinier, lui repond : Seigneur, si c\'est toi qui l\'as emporte, dis-moi ou tu l\'as place, et j\'irai le chercher.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 16, 'Jesus lui dit : Marie! Elle se retourne et lui dit en hebreu : Rabbouni ! (ce qui veut dire : Maitre!).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 17, 'Jesus lui dit : Ne me touche point! car je ne suis pas encore monte vers le Pere, mais va vers mes freres et dis-leur: Je monte vers mon Pere et votre Pere, vers mon Dieu et votre Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 18, 'Marie Madeleine va porter la nouvelle aux disciples : J\'ai vu le Seigneur, et voila ce qu\'il m\'a dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 19, 'Le soir de ce meme jour, le premier de la semaine, les portes du lieu ou les disciples etaient rassembles etant fermees, par crainte des Juifs, Jesus vint et se tint au milieu d\'eux, et leur dit : La paix soit avec vous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 20, 'et, en disant cela, il leur montra ses mains et son cote. Les disciples se rejouirent, voyant le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 21, 'Il leur dit encore une fois : La paix soit avec vous! Comme le Pere m\'a envoye, moi aussi, je vous envoie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 22, 'En disant cela, il souffla sur eux et leur dit : Recevez l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 23, 'A ceux auxquels vous remettrez les peches, ils seront remis ; a ceux auxquels vous les retiendrez, ils seront retenus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 24, 'Un des douze, Thomas, appele Didyme, n\'etait pas avec eux lorsque vint Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 25, 'Les autres disciples lui dirent : Nous avons vu le Seigneur. Il leur repondit : Si je ne vois pas dans ses mains la marque des clous, si je ne mets pas le doigt a la place des clous, si je ne mets pas la main dans son cote, je n\'en croirai rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 26, 'Huit jours apres, les disciples etaient reunis dans la meme chambre, Thomas avec eux ; Jesus vint, les portes etant fermees. Il se tint au milieu d\'eux et dit : La paix soit avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 27, 'Ensuite il dit a Thomas: Porte ici ton doigt et regarde mes mains ; avance aussi ta main, mets-la dans mon cote, et ne deviens pas incredule, mais croyant!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 28, 'Thomas lui repondit par ces paroles : Mon Seigneur et mon Dieu! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 29, 'Parce que tu m\'as vu, tu as cru! lui dit Jesus ; Heureux ceux qui, sans avoir vu, sont croyants!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 30, 'Jesus a fait encore, en presence des disciples, beaucoup d\'autres miracles qui ne sont pas consignes dans ce livre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 20, 31, 'Ceux-ci le sont, afin que vous croyiez que Jesus est le Christ, le Fils de Dieu, et que, le croyant, vous ayez la vie en son nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 1, 'Apres cela, sur le bord de la mer de Tiberiade, Jesus se manifesta encore aux disciples. Voici comment :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 2, 'Simon Pierre, Thomas appele Didyme, Nathanael de Cana en Galilee, les fils de Zebedee et deux autres disciples etaient ensemble.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 3, 'Je vais pecher, leur dit Simon Pierre. Nous y allons avec toi repondirent les autres. Ils partirent et monterent dans la barque. Cette nuit-la ils ne prirent rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 4, 'Le matin venu, Jesus etait la debout sur le rivage. Les disciples cependant ne savaient pas que c\'etait Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 5, 'Enfants, leur dit-il, n\'avez-vous rien a manger?  Ils lui repondirent : Non. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 6, 'Jetez le filet, dit Jesus, a droite de la barque, vous trouverez. Ils le jeterent et ils n\'avaient plus la force de le retirer tellement il etait plein de poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 7, 'Alors le disciple que Jesus aimait dit a Pierre : C\'est le Seigneur ! A cette parole : C\'est le Seigneur, Simon Pierre qui n\'etait pas vetu remit sa tunique et se jeta a l\'eau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 8, 'Quant aux autres disciples ils vinrent avec la barque, et comme ils etaient peu eloignes de terre,  environ de deux cents coudees,  ils trainaient le filet plein de poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 9, 'Une fois descendus a terre, ils voient du charbon prepare, du, poisson dessus et du pain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 10, 'Jesus leur dit : Apportez quelques-uns des poissons que vous venez de prendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 11, 'Simon Pierre monta alors dans la barque et tira a terre le filet. Il etait plein de gros poissons ; il y en avait cent cinquante-trois et malgre ce grand nombre le filet ne se dechira pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 12, 'Venez manger, dit Jesus aux disciples. Aucun d\'eux n\'osait lui poser cette question : Qui es-tu? Ils savaient que c\'etait le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 13, 'Jesus s\'approche ; prend le pain et le leur donne, ainsi que le poisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 14, 'C\'etait la troisieme fois, depuis sa resurrection d\'entre les morts, que Jesus se manifestait a ses disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 15, 'Quand ils eurent mange, Jesus dit a Simon Pierre : Simon, fils de Jean, m\'aimes-tu plus que ceux-ci? Il lui repondit : Oui, Seigneur, tu sais que je t\'aime. Jesus lui dit : Fais paitre mes agneaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 16, 'Il lui dit une seconde fois : Simon, fils de Jean, m\'aimes-tu? Il lui repondit : Oui, Seigneur, tu sais que je t\'aime. Jesus lui dit : Sois le pasteur de mes brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 17, 'Il lui dit pour la troisieme fois : Simon, fils de Jean, M\'aimes-tu? Pierre fut attriste de ce qu\'il lui disait pour la troisieme fois: M\'aimes-tu? et lui repondit : Seigneur, tu sais tout ; tu sais que je t\'aime. Jesus lui dit: Fais paitre mes brebis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 18, 'En verite, en verite, je te le dis, lorsque tu etais plus jeune, tu te ceignais toi-meme et tu allais ou tu voulais ; mais quand tu seras vieux, tu etendras les mains, un autre te ceindra, et te menera ou tu ne voudras pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 19, '(Il dit cela pour indiquer par quel genre de mort Pierre devait glorifier Dieu) ; et apres avoir ainsi parle, il ajouta : Suis-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 20, 'Pierre, se retournant, voit le suivre le disciple que Jesus aimait, celui qui, pendant le repas, s\'etait penche sur sa poitrine et lui avait dit : Seigneur qui est celui qui te livrera?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 21, 'Le voyant, Pierre dit a Jesus : Seigneur, et celui-ci, que lui arrivera-t-il ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 22, 'Jesus lui repond : Si je veux qu\'il attende jusqu\'a ce que je vienne, que t\'importe? Toi, suis-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 23, 'Alors le bruit se repandit parmi les freres que ce disciple-la ne mourrait pas. Cependant Jesus n\'a pas dit a Pierre : Il ne mourra pas ; mais : Si je veux qu\'il attende jusqu\'a ce que je vienne, que t\'importe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 24, 'C\'est ce disciple-la meme qui atteste ces choses et qui les a ecrites, et nous savons que son temoignage est veridique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (43, 21, 25, 'Il y a encore beaucoup d\'autres choses que Jesus a faites. Si on les ecrivait l\'une apres l\'autre, je crois que le monde entier ne pourrait contenir les livres qu\'on ecrirait. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 1, 'Dans mon premier recit, Theophile, j\'ai raconte depuis le commencement tout ce que Jesus a fait et enseigne');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 2, 'jusqu\'au jour ou, apres avoir donne, par l\'Esprit saint, ses ordres aux apotres qu\'il avait choisis, il fut enleve au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 3, 'Deja, apres sa passion, il s\'etait montre a eux vivant, leur avait donne de nombreuses preuves de sa resurrection, leur etait apparu pendant quarante jours, leur avait parle des choses du Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 4, 'Un jour qu\'ils etaient reunis, il leur ordonna de ne pas quitter Jerusalem, mais d\'y attendre ce que le Pere avait promis... Ce dont vous m\'avez entendu parler, leur dit-il ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 5, 'car Jean baptisait d\'eau, mais vous, vous serez baptises d\'Esprit saint dans peu de jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 6, 'Les apotres, tout en l\'accompagnant, lui posaient des questions : Seigneur, lui demanderent-ils, est-ce maintenant que tu vas retablir la royaute en Israel ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 7, 'Il leur repondit : Vous n\'avez pas a savoir les epoques ou les moments que le Pere a fixes de sa propre autorite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 8, 'mais vous recevrez une grande force quand le saint Esprit descendra sur vous, et vous serez mes temoins a Jerusalem, dans toute la Judee, en Samarie et jusqu\'au bout du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 9, 'Pendant qu\'il prononcait ces paroles et pendant qu\'ils le regardaient, il fut enleve et une nuee vint le derober a leurs yeux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 10, 'Comme ils consideraient attentivement le ciel pendant qu\'il s\'en allait, deux hommes en vetements blancs se presenterent a eux :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 11, 'Galileens, leur dirent-ils, pourquoi restez-vous la a regarder au ciel ? Ce Jesus qui vient d\'etre enleve au ciel du milieu de vous, en reviendra de la meme maniere que vous l\'avez vu y monter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 12, 'Ils retournerent alors de la montagne appelee Bois d\'Oliviers a Jerusalem, qui est tout pres, a la distance d\'un chemin de sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 13, 'Rentres dans la ville, ils monterent a la chambre haute, ou ils se tenaient d\'ordinaire. C\'etaient : Pierre, Jean, Jacques et Andre,  Philippe et Thomas,  Barthelemy et Matthieu,  Jacques, fils d\'Alphee, Simon le Zelote et Judas, fils de Jacques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 14, 'Eux tous, avec perseverance et d\'un seul coeur, s\'y adonnaient a la priere avec les femmes, avec Marie, mere de Jesus, et avec les freres de celui-ci.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 15, 'Un de ces jours-la, Pierre se leva au milieu des freres (cent vingt personnes environ etaient ensemble reunies) et voici ce qu\'il dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 16, 'Mes freres, il fallait que s\'accomplit l\'Ecriture, la prediction de l\'Esprit saint faite par la bouche de David sur Judas (celui qui s\'est charge de conduire ceux qui ont arrete Jesus),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 17, 'parce qu\'il etait au nombre des notres et qu\'il avait recu sa part de notre ministere');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 18, ' (cet homme, apres avoir achete un champ avec le salaire de son crime, s\'est jete, la tete la premiere ; son corps s\'est brise par le milieu ; ses entrailles se sont repandues ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 19, 'ce fait est si bien, connu de tous les habitants de Jerusalem, que dans leur langue ils appellent ce champ Acheldamach, c\'est-a-dire champ du sang)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 20, 'en effet, il est ecrit au livre des Psaumes : Que sa demeure devienne un desert! Que personne n\'y habite ! et : Qu\'un autre obtienne sa charge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 21, 'Donc, il faut qu\'un des hommes qui nous ont accompagnes pendant tout le temps, depuis que le Seigneur Jesus a commence jusqu\'a ce qu\'il ait cesse d\'etre avec nous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 22, 'en prenant depuis le bapteme de Jean jusqu\'au jour ou il nous a ete enleve, devienne avec nous temoin de sa resurrection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 23, 'Ils en proposerent deux : Joseph dit Bar-Sabbas, surnomme Justus, et Matthias ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 24, 'puis ils firent cette priere : Toi, Seigneur, qui connais tous les coeurs, montre-nous lequel de ces deux freres tu as choisi');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 25, 'pour qu\'il occupe la place de ministre et d\'apotre que Judas a quittee pour aller a la place qui est la sienne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 1, 26, 'Ensuite on tira au sort. Le sort tomba sur Matthias, qui fut alors associe aux onze apotres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 1, 'Quand arriva le jour de la Pentecote, ils etaient tous ensemble, reunis,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 2, 'et tout a coup il se produisit, venant du ciel, un bruit semblable a un violent coup de vent, bruit qui remplit toute la maison ou ils se trouvaient ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 3, 'il leur apparut alors, separees les unes des autres, des langues ressemblant a des flammes ; il s\'en posa une sur chacun d\'eux ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 4, 'et ils furent tous remplis d\'Esprit saint et commencerent a parler en langues etrangeres, selon que l\'Esprit leur donnait de s\'exprimer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 5, 'A Jerusalem se trouvaient alors en sejour des Juifs, des hommes pieux venus de tous les pays qui sont sous le ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 6, 'Quand ce bruit se fit entendre, ils accoururent en foule et chacun fut stupefait d\'entendre parler sa propre langue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 7, 'Ils etaient tous confondus d\'etonnement et disaient : Tous ces hommes qui sont la et qui parlent, ne sont-ils pas Galileens?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 8, 'Comment donc chacun de nous les entend-il s\'exprimer dans sa langue maternelle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 9, 'Parthes, Medes, Elamites, habitants de la Mesopotamie, de la Judee et de la Cappadoce, du Pont et de l\'Asie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 10, 'de la Phrygie et de la Pamphylie, de l\'Egypte et du territoire de la Lybie cyrenaique, etrangers de passage, venus de Rome! tant Juifs que proselytes!...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 11, 'Cretois!... Arabes!... nous les entendons parler dans nos langues des grandes choses que Dieu a faites !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 12, 'Ils etaient tous confondus, ne savaient que penser ; les uns se demandaient : Qu\'en adviendra-t-il?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 13, 'D\'autres se moquaient, en disant qu\'ils etaient pris de vin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 14, 'Pierre alors s\'avanca avec les onze, eleva la voix et s\'exprima ainsi : Habitants de Judee et vous qui etes en sejour a Jerusalem, Sachez bien ceci, pretez l\'oreille a mes paroles :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 15, 'ces hommes ne sont point ivres, comme vous le supposez, car il n\'est que la troisieme heure du jour,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 16, 'mais il arrive ce dont a parle le prophete Joel :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 17, 'Il arrivera pendant les derniers jours, dit Dieu, Que je repandrai de mon Esprit sur toute chair ; Et vos fils et vos filles prophetiseront ; Vos jeunes gens auront des visions Et vos vieillards auront des songes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 18, 'Oui, sur mes serviteurs et sur mes servantes, en ces jours-la, Je repandrai de mon Esprit et ils prophetiseront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 19, 'Je ferai paraitre des prodiges la-haut dans le ciel, Et des miracles ici-bas sur la terre, Du sang, du feu et des tourbillons de fumee,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 20, 'Le soleil se changera en tenebres - Et la lune en sang, Lorsque approchera le grand et glorieux jour du Seigneur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 21, 'Et quiconque invoquera le nom du Seigneur sera sauve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 22, 'Hommes d\'Israel, ecoutez ces paroles : Jesus de Nazareth, cet homme dont Dieu a legitime la mission devant vous, en faisant par ses mains et sous vos yeux des miracles, des prodiges et des signes, comme vous le savez parfaitement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 23, 'cet homme qui a ete trahi  (et c\'etait un dessein arrete par Dieu, une determination prise d\'avance par lui)  que des mains impies ont crucifie, que vous avez mis a mort,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 24, 'Dieu l\'a ressuscite en aneantissant les horreurs de la mort, puisqu\'il ne pouvait pas rester en sa puissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 25, 'Et, en effet, David dit de lui : Je voyais toujours le Seigneur devant mois, Car il est a ma droite pour que je ne sois pas ebranle ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 26, 'Voila pourquoi mon coeur a ete dans la joie Et ma langue a chante mon bonheur ; Aussi mon corps reposera-t-il plein d\'esperance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 27, 'Car tu ne laisseras pas mon ame dans la Demeure-des-morts ; Et tu ne permettras pas que ton Saint voie la corruption ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 28, 'Tu m\'as fait connaitre les sentiers de la vie, Tu me rempliras de joie par ta presence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 29, 'Mes freres, permettez-moi de vous dire en toute liberte que, lui, le patriarche David, est mort, qu\'il a ete enseveli, que son tombeau est encore aujourd\'hui au milieu de nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 30, 'Mais il etait prophete, il savait que Dieu lui avait jure, sous la foi du serment, qu\'un de ses descendants occuperait son trone ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 31, 'c\'est donc la resurrection du Christ qu\'il a prevue et annoncee, disant: Il n\'a pas ete laisse au sejour des morts Et son corps n\'a pas vu la corruption.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 32, 'Eh bien, ce Christ, c\'est le Jesus que Dieu a ressuscite et nous en sommes tous temoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 33, 'Puis il a ete eleve a la droite de Dieu, il a recu du Pere la promesse de l\'Esprit saint, et alors il l\'a repandu comme vous le voyez et l\'entendez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 34, 'David, lui, n\'est pas monte au ciel, il dit meme : Le Seigneur a dit a mon Seigneur Assieds-toi a ma droite');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 35, 'Jusqu\'a ce que je te donne tes ennemis pour marchepied. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 36, 'Sois donc bien convaincue, maison d\'Israel tout entiere, que Dieu a fait Seigneur et Christ ce Jesus que vous, vous avez crucifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 37, 'Touches jusqu\'au fond du coeur par ces paroles, ils dirent a Pierre et aux autres apotres : Freres, que devons-nous faire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 38, 'Et alors Pierre : Repentez-vous ; que chacun de vous se fasse baptiser au nom de Jesus-Christ pour la remission de ses peches, et vous recevrez le don du saint Esprit,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 39, 'car la promesse est pour vous, pour vos enfants, pour tous ceux qui sont au loin en aussi grand nombre que le Seigneur notre Dieu les appellera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 40, 'Il les conjurait par beaucoup d\'autres paroles encore, et il les exhortait en disant : Echappez a cette generation perverse!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 41, 'Ceux donc qui accueillirent sa predication recurent le bapteme, et, ce jour-la, trois mille ames environ furent admises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 42, 'Ils etaient assidus a suivre l\'enseignement des apotres et a vivre en communion fraternelle quand ils rompaient le pain et quand ils priaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 43, 'Ils etaient respectes de tout le monde ; les apotres faisaient en grand nombre des prodiges et des miracles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 44, 'et tous les croyants vivaient ensemble. Ils mettaient tout en commun ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 45, 'ils vendaient leurs proprietes et leurs biens, et en partageaient le produit entre tous, en proportion des besoins de chacun.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 46, 'Tous ensemble, chaque jour, etaient assidus au Temple et, dans leur maison, ils rompaient le pain, ils prenaient leurs repas d\'un coeur pur et joyeux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 2, 47, 'louant Dieu et se faisant aimer de tout le peuple. Le Seigneur ajoutait chaque jour a l\'eglise ceux qui etaient sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 1, 'Pierre et Jean montaient un jour au Temple pour la priere de la neuvieme heure ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 2, 'or, il y avait un homme estropie de naissance que, tous les jours, on apportait et on deposait a la porte du Temple dite La Belle, pour demander l\'aumone a ceux qui entraient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 3, 'Voyant Pierre et Jean qui allaient penetrer dans le Temple, il s\'adressa a eux pour recevoir une aumone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 4, 'Pierre arreta sur lui son regard, ainsi que Jean : Regarde-nous, lui dit-il.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 5, 'Lui les fixait attentivement, s\'attendant a recevoir quelque chose.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 6, 'Mais Pierre continua : Je n\'ai ni argent ni or, mais ce que j\'ai, je te le donne ; au nom de Jesus-Christ de Nazareth, marche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 7, 'En meme temps il lui prenait la main droite et le levait ; immediatement ses pieds et ses chevilles devinrent fermes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 8, 'd\'un saut il se mit debout ; il marchait! et il entra avec eux dans le Temple, marchant, sautant et louant Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 9, 'Tout le peuple le vit marcher et louer Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 10, 'chacun le reconnaissait ; c\'etait bien lui, l\'homme qui demandait l\'aumone assis a La Belle Porte du Temple ; complete etait la stupefaction comme l\'enthousiasme de ce qui lui etait arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 11, 'Cet homme ne quittant pas Pierre et Jean, c\'est vers eux, au portique dit de Salomon, qu\'accourut tout le peuple, confondu de surprise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 12, 'Voyant cela, Pierre s\'adressa ainsi a la foule : Hommes d\'Israel, Pourquoi vous etonnez-vous de ce qui vient de se passer? Pourquoi nous regardez-vous avec cette insistance, comme si c\'etait nous, par nos propres forces ou par notre piete, qui avions fait, marcher cet homme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 13, 'Le Dieu d\'Abraham, d\'Isaac et de Jacob, le Dieu de nos peres a glorifie son serviteur Jesus que vous, vous avez livre et que vous avez renie devant Pilate, et cela, quand lui, il voulait le relacher!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 14, 'Oui, vous avez renie le Saint et le Juste, et vous avez demande la grace d\'un meurtrier,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 15, 'vous avez tue l\'auteur de la vie, que Dieu a ressuscite d\'entre les morts, et nous, nous sommes ses temoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 16, 'C\'est par la foi en son nom que ce nom a gueri l\'homme que vous voyez et connaissez ; la foi qui agit par lui a donne a cet homme, et en votre presence a tous, une guerison complete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 17, 'Je sais d\'ailleurs, freres, que vous avez fait tout cela dans votre ignorance et vos magistrats aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 18, 'Dieu a accompli de cette maniere ce qu\'il avait predit d\'avance par la bouche de tous les prophetes, savoir que son Christ devait souffrir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 19, 'Repentez-vous donc et convertissez-vous pour que vos peches soient effaces et alors pourront venir de la part du Seigneur des temps de rafraichissement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 20, 'et il vous enverra celui qu\'il vous a destine, Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 21, 'que le ciel doit recevoir jusqu\'a l\'epoque du retablissement universel dont Dieu a parle autrefois par la bouche de ses saints prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 22, 'D\'une part, Moise a dit : Le Seigneur notre Dieu vous suscitera d\'entre vos freres un prophete semblable a moi, vous l\'ecouterez dans tout ce qu\'il vous dira,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 23, 'et quiconque n\'ecoutera pas ce prophete, sera extermine du milieu du peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 24, 'D\'autre part, tous les prophetes, depuis Samuel et ses successeurs, ont parle et ont annonce ces jours-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 25, 'Vous etes les fils des prophetes et les heritiers de l\'alliance que Dieu, a conclue avec nos peres, disant a Abraham : Toutes les familles de la terre seront benies en ta posterite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 3, 26, 'Pour vous, les premiers, Dieu a suscite son serviteur et l\'a envoye vous benir en detournant chacun de vous de ses iniquites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 1, 'Pendant qu\'ils parlaient au peuple, survinrent les pretres, le capitaine du Temple et les Sadduceens,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 2, 'fort ennuyes de ce qu\'ils enseignaient le peuple et lui annoncaient la resurrection d\'entre les morts en lui parlant de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 3, 'Ils les firent arreter et jeter en prison, et comme il etait deja tard, ils les y laisserent jusqu\'au lendemain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 4, 'Cependant plusieurs de ceux qui avaient entendu le discours de Pierre devinrent croyants, et le nombre des freres fut porte a cinq mille environ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 5, 'Le lendemain se reunirent, a Jerusalem, les magistrats, les Anciens, les Scribes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 6, 'le grand-pretre Anne, Kaiphe, Jean, Alexandre, tous ceux qui etaient des grandes familles sacerdotales.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 7, 'Ils firent comparaitre Pierre et Jean et leur poserent cette question : Par quelle autorite ou au nom de qui avez-vous agi ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 8, 'Alors Pierre, plein d\'Esprit saint, leur dit : Magistrats du peuple et Anciens,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 9, 'Puisque nous sommes interroges aujourd\'hui a propos du bien que nous avons fait a un homme malade, puisque vous voulez savoir comment il a ete gueri,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 10, 'sachez vous tous, et que le peuple d\'Israel tout entier sache aussi, que c\'est au nom de Jesus-Christ de Nazareth, crucifie par vous, ressuscite d\'entre les morts par Dieu ; oui, c\'est par lui que cet homme est la devant vous en bonne sante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 11, 'Ce Jesus est La pierre rejetee par vous, les constructeurs, Et devenue la pierre angulaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 12, 'Il n\'y a de salut en aucun autre ; car, sous le ciel, il n\'a pas ete donne d\'autre nom aux hommes par lequel nous devions etre sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 13, 'Quand ils virent l\'assurance de Pierre et de Jean, qu\'ils savaient etre des gens du peuple, sans instruction aucune, ils furent tres surpris. Ils les reconnaissaient pour avoir ete avec Jesus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 14, 'et voyant debout a cote d\'eux l\'homme qu\'ils avaient gueri, ils n\'avaient rien a repliquer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 15, 'Alors ils leur ordonnerent de sortir du Sanhedrin, puis ils delibererent :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 16, 'Que faire a ces gens? se disaient-ils, car pour tous les habitants de Jerusalem il est evident qu\'ils ont fait un miracle remarquable et il nous est impossible de le nier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 17, 'Cependant, pour que l\'affaire ne se repande pas davantage dans le peuple, interdisons-leur avec menaces de jamais parler a homme quelconque en ce nom-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 18, 'Ils les firent rappeler et leur defendirent absolument de parler et d\'enseigner au nom de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 19, 'Mais Pierre et Jean leur repondirent ainsi : S\'il est juste devant Dieu de vous ecouter plus que Dieu, jugez-le ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 20, 'quant a nous, nous ne pouvons pas ne pas parler de ce que nous avons vu et entendu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 21, 'Ils les relacherent neanmoins, apres avoir reitere leurs menaces, ne trouvant aucun moyen de les punir, a cause du peuple, car tout le monde glorifiait Dieu de ce qui etait arrive :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 22, 'en effet, l\'homme qui avait ete gueri par ce miracle etait age de plus de quarante ans.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 23, 'Lorsqu\'ils furent libres, ils se rendirent aupres des leurs et leur raconterent tout ce que les chefs des pretres et les Anciens leur avaient dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 24, 'Quand ils l\'eurent appris, ils eleverent unanimes leurs voix a Dieu : O Maitre, dirent-ils, Toi qui as fait le ciel, la terre, la mer et tout ce qui s\'y trouve!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 25, 'Toi qui as dit par l\'Esprit saint, par la bouche de notre pere, ton serviteur David : Pourquoi les nations ont-elles tremble de colere, Et les peuples ont-ils forme des desseins inutiles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 26, 'Les rois de la terre se sont souleves Et les magistrats se sont ligues ensemble Contre le Seigneur et contre son Oint,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 27, ' (en effet, Herode et Ponce-Pilate avec les paiens et le peuple d\'Israel se sont veritablement ligues dans cette ville meme contre ton saint serviteur Jesus, que tu as oint');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 28, 'pour faire tout ce que ta main et ta volonte avaient decide d\'avance),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 29, ' a present, Seigneur, aie l\'oeil ouvert sur leurs menaces et donne a tes serviteurs d\'annoncer ta Parole en toute assurance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 30, 'lorsque tu etendras ta main pour operer des guerisons, des miracles et des prodiges par le nom de ton saint serviteur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 31, 'Quand ils eurent prie, le lieu ou ils etaient rassembles trembla, tous furent remplis du saint Esprit et annoncaient avec assurance la Parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 32, 'La multitude des croyants n\'avaient qu\'un coeur et qu\'une ame ; personne ne disait rien posseder qui lui fut propre, mais ils mettaient tout en commun.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 33, 'Les apotres rendaient avec une grande energie leur temoignage de la resurrection du Seigneur Jesus-Christ, et une grande grace reposait sur eux tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 34, 'car ils n\'avaient pas un seul indigent : tous ceux qui etaient proprietaires de terres ou de maisons les vendaient, apportaient le prix de leur vente et le deposaient aux pieds des apotres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 35, 'On le distribuait ensuite a chacun selon ses besoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 36, 'C\'est ainsi qu\'un levite, Chypriote de naissance, Joseph, surnomme par les apotres Bar-Nabas (ce qu\'on peut traduire fils de la predication), possedait un champ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 4, 37, 'qu\'il vendit ; il en apporta l\'argent et le deposa aux pieds des apotres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 1, 'Mais un homme nomme Ananias, d\'accord avec sa femme Sapphira, vendit une propriete,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 2, 'mit de cote une partie du prix de vente, sa femme le sachant, et apportant le reste, le deposa aux pieds des apotres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 3, 'Ananias, lui dit Pierre, pourquoi Satan s\'est-il empare de ton coeur, jusqu\'a te faire mentir a l\'Esprit saint, et te faire mettre de cote une partie du prix de ton champ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 4, 'Si tu ne l\'avais pas vendu, ne te serait-il pas reste? et, si tu le vendais, ne pouvais-tu pas a ton gre disposer de sa valeur? comment as-tu pu former un pareil projet dans ton coeur? ce n\'est pas a des hommes que tu as menti, c\'est a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 5, 'A l\'ouie de ces paroles, Ananias tomba et expira. Tous les auditeurs furent frappes d\'epouvante ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 6, 'cependant les plus jeunes, s\'etant leves, l\'arrangerent pour l\'ensevelir, puis ils l\'emporterent et l\'enterrerent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 7, 'Il s\'etait ecoule trois heures environ, lorsque sa femme entra, ne sachant pas ce qui etait arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 8, 'Pierre s\'adressa a elle : Dis-moi si c\'est a ce prix-la que vous avez vendu votre champ? Elle repondit : C\'est a ce prix-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 9, 'Mais Pierre: Eh quoi! vous vous etes concertes pour tenter l\'Esprit du Seigneur ! Voici les pas de ceux qui ont enseveli ton mari s\'approchent de cette porte ; ils vont t\'emporter!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 10, 'A l\'instant meme elle tomba a ses pieds et expira ; les jeunes gens qui rentraient la trouverent morte, ils l\'emporterent et l\'enterrerent a cote de son mari.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 11, 'L\'Eglise tout entiere et tous ceux qui apprirent ces faits furent frappes d\'epouvante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 12, 'Cependant il se faisait, dans le peuple, par les mains des apotres, des miracles et des prodiges en grand nombre. Ceux-ci se tenaient d\'habitude tous ensemble sous le portique de Salomon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 13, 'Aucun des autres n\'osait entrer en rapport avec eux, mais le peuple leur montrait une grande admiration ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 14, 'des croyants toujours plus nombreux, des hommes et des femmes en foule s\'attachaient au Seigneur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 15, 'c\'etait au point qu\'on portait les malades jusque dans les rues, qu\'on les deposait sur de petits lits et sur des grabats afin que, Pierre venant a passer, son ombre au moins couvrit quelqu\'un d\'eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 16, 'Meme des villes voisines, la foule accourait a Jerusalem, amenant des malades et des personnes tourmentees par des Esprits impurs : tous etaient gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 17, 'Cependant le grand-pretre et tous ceux de son parti (le parti des Sadduceens) se mirent en mouvement. Pousses par la jalousie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 18, 'ils firent arreter les apotres et les jeterent dans la prison publique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 19, 'Mais un ange du Seigneur ouvrit, pendant la nuit, les portes de la prison, les fit sortir et leur dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 20, 'Allez vous montrer au Temple et annoncez au peuple toutes ces paroles de vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 21, 'A ces mots, ils entrerent au Temple des le point du jour et reprirent leur enseignement. Cependant le grand-pretre et ses partisans convoquerent le Sanhedrin et tout le senat des Israelites, puis ils envoyerent chercher les apotres a la prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 22, 'Leurs agents s\'y rendirent donc et ne les y trouverent pas ; ils revinrent et rapporterent le fait en ces termes :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 23, 'Nous avons trouve la prison fermee avec grand soin et les sentinelles se tenant aux portes ; nous avons ouvert et, dedans, nous n\'avons trouve personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 24, 'A l\'ouie de ces paroles, le capitaine du Temple et les chefs des pretres se demanderent avec inquietude ou etaient les apotres et ce que tout cela deviendrait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 25, 'La-dessus, quelqu\'un vint leur dire : Les hommes que vous avez fait mettre en prison sont installes au Temple et enseignent le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 26, 'Le capitaine du Temple partit alors avec ses agents et les amena, mais sans employer la force, car ils avaient peur du peuple qui aurait pu les lapider.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 27, 'Ils les amenerent donc et les introduisirent dans le Sanhedrin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 28, 'Le grand-pretre les interrogea Ne vous avons-nous pas expressement interdit, leur dit-il, d\'enseigner en ce nom-la, et voila que vous avez rempli Jerusalem de votre enseignement et que vous voulez faire retomber sur nous le sang de cet homme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 29, 'Mais Pierre et les apotres repondirent par ces paroles : Il faut obeir a Dieu plutot qu\'aux hommes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 30, 'le Dieu de nos peres a ressuscite Jesus que vous, vous avez fait mourir en le pendant a une croix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 31, 'Dieu, par sa droite, l\'a eleve a la dignite de Chef et de Sauveur, pour donner a Israel la repentance et le pardon des peches,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 32, 'et nous, nous sommes temoins de ces choses, ainsi que le saint Esprit donne par Dieu a ceux qui lui obeissent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 33, 'Ce langage les exaspera ; ils voulaient les faire mettre a mort ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 34, 'mais un des membres du Sanhedrin, un des Pharisiens, appele Gamaliel, un legiste qui etait estime de tout le peuple, se leva et demanda qu\'on fit sortir un instant les apotres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 35, 'puis il dit : Israelites, prenez bien garde a ce que vous allez faire a ces hommes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 36, 'il y a quelque temps s\'est leve Theudas qui pretendait aussi etre quelque chose, et quatre cents hommes environ se joignirent a lui ; il fut tue et tous ceux qui s\'etaient rallies a lui ont ete mis en deroute et reduits a rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 37, 'Apres lui, a l\'epoque du recensement, s\'est leve Judas le Galileen, et il a attire bien des gens a lui ; celui-la aussi a peri, et tous ceux qu\'il avait rallies a lui ont ete disperses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 38, 'Eh bien, je vous dis ceci : Cessez vos poursuites et laissez aller ces hommes ; car si cette entreprise ou cette oeuvre vient des hommes, elle tombera d\'elle-meme ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 39, 'si, au contraire, elle vient de Dieu, vous ne pourrez l\'arreter et vous risquez de vous trouver avoir combattu contre Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 40, 'Ils se rangerent a son avis et, ayant rappele les apotres, ils leur firent donner la bastonnade, puis leur defendirent de precher au nom de Jesus et les relacherent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 41, 'Les apotres se retirerent de devant le Sanhedrin pleins de joie d\'avoir ete juges dignes de subir des outrages pour le nom de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 5, 42, 'et tous les jours, dans le Temple et dans leur maison, ils ne cessaient d\'enseigner et d\'annoncer l\'Evangile de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 1, 'En ce temps-la, le nombre des disciples augmentant toujours, les Hellenistes se plaignirent de ce que leurs veuves etaient moins bien traitees dans les distributions quotidiennes que celles des Hebreux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 2, 'Les douze convoquerent alors une reunion de tous les disciples et leur dirent : Il n\'est pas convenable que nous negligions la predication de la parole de Dieu pour faire le service des tables.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 3, 'Choisissez donc parmi vous, freres, sept hommes, pleins d\'Esprit saint, sages et consideres, que nous chargerons de ce service');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 4, 'et, quant a nous, nous nous occuperons exclusivement de la priere et du ministere de la parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 5, 'Cette proposition plut a tout le monde et les elus furent Etienne, homme plein de foi et d\'Esprit saint, Philippe, Procore, Nicanor, Timon, Parmenas et Nicolas d\'Antioche, un proselyte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 6, 'On les presenta aux apotres qui prierent en leur imposant les mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 7, 'La parole de Dieu se repandait de plus en plus, le nombre des disciples augmentait considerablement a Jerusalem ; une foule de pretres, entre autres, embrasserent la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 8, 'Etienne, plein de grace et plein de force, faisait dans le peuple des prodiges et de grands miracles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 9, 'Cependant quelques membres de la synagogue dite des Affranchis, des gens de Cyrene et d\'Alexandrie, et d\'autres venus de Cilicie et d\'Asie s\'emurent et commencerent a discuter avec lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 10, 'mais sans pouvoir resister a sa sagesse et a l\'Esprit par lequel il parlait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 11, 'Alors ils subornerent des individus qui dirent : Nous l\'avons entendu prononcer des paroles blasphematoires contre Moise et contre Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 12, 'Ils souleverent le peuple, les Anciens et les Scribes, et, survenant a l\'improviste, ils s\'emparerent de lui et l\'amenerent au Sanhedrin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 13, 'La ils produisirent de faux temoins qui dirent: Cet homme ne cesse de tenir des discours contre le Lieu saint et contre la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 14, 'Nous l\'avons entendu dire que Jesus, cet homme de Nazareth, detruira ce lieu et changera les institutions que nous tenons de Moise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 6, 15, 'Tous ceux qui siegeaient au Sanhedrin avaient les yeux fixes sur Etienne, et, quand ils le regardaient ainsi, il leur semblait voir un ange.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 1, 'Tout cela est-il vrai? lui demanda le grand-pretre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 2, 'Etienne dit alors : Mes freres et mes peres, ecoutez ! Le Dieu de gloire apparut a notre pere Abraham, en Mesopotamie, avant son etablissement a Charran,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 3, 'et lui dit : Sors de ton pays et de ta famille et va dans le pays que je te montrerai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 4, 'Il sortit alors du pays des Chaldeens et s\'etablit a Charran. De la, apres la mort de son pere, Dieu le fit passer dans ce pays-ci que vous habitez maintenant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 5, 'Il ne lui donna aucune propriete dans ce pays, pas meme de quoi poser le pied, mais il promit de le mettre, lui et ses descendants, en sa possession, quoiqu\'il n\'eut pas d\'enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 6, 'Voici comment Dieu parla : Ses descendants demeureront dans un pays etranger, on les fera esclaves et on les persecutera pendant quatre cents ans');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 7, 'et la nation qui les fera esclaves c\'est moi qui la jugerai, dit Dieu, et ensuite ils partiront et me rendront un culte dans ce lieu-ci.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 8, 'Puis Dieu fit avec Abraham l\'alliance de la circoncision. C\'est ainsi qu\'ayant engendre Isaac, Abraham le circoncit le huitieme jour, de meme Isaac Jacob, et Jacob les douze patriarches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 9, 'Les patriarches, jaloux de Joseph, le vendirent pour le faire emmener en Egypte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 10, 'Mais Dieu etait avec lui, il le tira de toutes ses epreuves, il lui donna de la sagesse et lui fit trouver grace devant Pharaon, roi d\'Egypte, qui l\'etablit gouverneur d\'Egypte et de toute sa maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 11, 'Il survint une famine dans toute l\'Egypte et en Chanaan, la detresse etait grande, et nos peres ne trouvaient pas de quoi se nourrir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 12, 'Mais Jacob apprit qu\'il y avait du ble en Egypte et il y envoya nos peres une premiere fois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 13, 'Au second voyage, Joseph se fit reconnaitre par ses freres et Pharaon apprit quelle etait l\'origine de Joseph.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 14, 'Alors Joseph envoya chercher son pere Jacob et toute sa famille, en tout soixante-quinze personnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 15, 'Jacob descendit en Egypte et y mourut ; nos peres aussi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 16, 'on les transporta a Sichem et on les deposa dans le tombeau qu\'Abraham, a prix d\'argent, y avait achete des fils d\'Emmor.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 17, 'Lorsque approcha le moment ou devait s\'accomplir la promesse solennellement faite par Dieu a Abraham, le peuple s\'accrut et se multiplia en Egypte');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 18, 'jusqu\'au regne d\'un autre roi qui n\'avait pas connu Joseph.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 19, 'Celui-ci traita notre peuple par la ruse et persecuta nos peres jusqu\'a leur faire exposer leurs enfants pour leur oter la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 20, 'C\'est alors que naquit Moise ; il etait d\'une divine beaute ; trois mois il fut eleve dans la maison de son pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 21, 'Quand il fut expose, la fille de Pharaon le recueillit et l\'eleva comme son propre fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 22, 'Moise fut instruit dans toute la sagesse des Egyptiens ; il etait puissant en paroles et en oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 23, 'Quand il eut quarante ans accomplis, il lui monta au coeur le desir de visiter ses freres les enfants d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 24, 'Il en vit maltraiter un et, prenant son parti, il vengea le tort qui lui etait fait en frappant l\'Egyptien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 25, 'Il pensait faire comprendre a ses freres que Dieu, par ses mains, voulait les delivrer ; mais ils ne comprirent pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 26, 'Le lendemain il se presenta a eux pendant qu\'ils se querellaient et comme il les exhortait a se reconcilier en leur disant : Vous etes freres, pourquoi vous maltraitez-vous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 27, 'celui qui faisait tort a son prochain le repoussa par ces mots : Qui donc t\'a nomme notre chef et notre juge?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 28, 'Voudrais-tu me tuer, moi aussi, comme l\'Egyptien que tu as tue hier ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 29, 'A cette parole, Moise prit la fuite et alla vivre en etranger dans le pays de Madian, ou il engendra deux fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 30, 'Quarante ans plus tard, un ange lui apparut au desert du mont Sinai dans la flamme d\'un buisson en feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 31, 'Moise le vit et, tout surpris de cette apparition, il s\'approchait pour la considerer de plus pres, lorsque la voix du Seigneur se fit entendre :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 32, 'Je suis le Dieu de tes peres, le Dieu d\'Abraham et d\'Isaac et de Jacob.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 33, 'Tremblant de peur, Moise n\'osa pas regarder davantage. Alors le Seigneur lui dit : Ote de tes pieds tes chaussures, car le lieu ou tu te trouves est une terre sainte ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 34, 'j\'ai regarde et j\'ai vu la misere de mon peuple en Egypte, et j\'ai entendu ses gemissements et je suis descendu pour le delivrer. Viens maintenant pour que je t\'envoie en Egypte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 35, 'C\'est ce Moise qu\'ils avaient renie en disant : Qui donc t\'a nomme notre chef et notre juges? que Dieu a envoye comme chef et comme liberateur avec l\'aide de l\'ange qui lui etait apparu dans le buisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 36, 'C\'est lui qui les a delivres en faisant des prodiges et des miracles en Egypte, sur la mer Rouge et au desert pendant quarante ans.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 37, 'C\'est ce Moise qui a dit aux enfants d\'Israel : Dieu vous suscitera d\'entre vos freres un prophete semblable a moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 38, 'C\'est lui qui etait au milieu de l\'assemblee au desert avec l\'ange qui lui parlait sur le mont Sinai et avec nos peres. C\'est lui qui recut, pour vous les transmettre, des oracles de vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 39, 'C\'est a lui que nos peres n\'ont pas voulu obeir. C\'est lui qu\'ils repousserent pour tourner leurs coeurs vers l\'Egypte,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 40, 'disant a Aaron : Fais-nous des dieux qui nous conduisent ; car ce Moise, qui nous a fait sortir du pays d\'Egypte, nous ne savons ce qui lui est arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 41, 'En ces jours-la, ils fabriquerent un veau et ils offrirent un sacrifice a cette idole et celebrerent une joyeuse fete a ces oeuvres de leurs mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 42, 'Alors Dieu les abandonna et les livra au culte de l\'armee du ciel, comme il est ecrit dans le livre des Prophetes : M\'avez-vous offert des victimes et des sacrifices Au desert pendant quarante ans, o maison d\' Israel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 43, 'Quand vous transportiez le tabernacle de Moloch et l\'etoile de votre dieu Romphan, Ces idoles que vous avez fabriquees pour vous prosterner devant elles ? Eh bien, je vous deporterai au dela de Babylone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 44, 'Nos peres avaient au desert le tabernacle du temoignage, comme l\'avait ordonne celui qui avait dit a Moise de le faire sur le modele qu\'il avait vu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 45, 'Nos peres, l\'ayant recu a leur tour, l\'introduisirent avec Josue dans le pays conquis sur les peuples chasses par Dieu devant eux. Il en fut ainsi jusqu\'au jour de David');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 46, 'qui trouva grace devant Dieu et demanda a elever une demeure a la maison de Jacob.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 47, 'Ce fut Salomon qui lui batit une maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 48, 'Mais le Tres Haut n\'habite point dans ce qui est fait de mains d\'hommes, comme le dit le prophete :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 49, 'Le ciel est mon trone, La terre est mon marchepied ; Quelle maison m\'edifierez-vous, dit le Seigneur, Ou quel sera mon lieu de repos?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 50, 'N\'est-ce pas ma main qui a fait toutes ces choses ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 51, 'Tetes dures! coeurs et oreilles incirconcis! vous resistez toujours a l\'Esprit saint, comme l\'ont fait vos peres!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 52, 'Lequel des prophetes vos peres n\'ont-ils pas persecute! Ils ont tue ceux qui annoncaient la venue du Juste, que vous avez trahi et dont vous avez ete les meurtriers!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 53, 'Cette Loi que vous aviez recue sur les ordres des anges, vous ne l\'avez pas gardee...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 54, 'Ces paroles leur mirent la rage au coeur et ils grincerent des dents contre Etienne ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 55, 'mais lui, plein d\'Esprit saint, les yeux fixes au ciel, vit la gloire de Dieu et Jesus debout a la droite de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 56, 'et il dit : Voila que je vois les cieux ouverts et le Fils de l\'homme debout a la droite de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 57, 'Alors ils pousserent de grands cris, se boucherent les oreilles, se precipiterent tous ensemble sur lui');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 58, 'et l\'entrainerent hors de la ville, ou ils le lapiderent. Les temoins deposerent leurs vetements aux pieds d\'un jeune homme nomme Saul');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 59, 'et, pendant qu\'ils le lapidaient, Etienne priait et disait: Seigneur Jesus, recois mon esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 7, 60, 'Puis il se mit a genoux et s\'ecria a haute voix : Seigneur ne leur demande pas compte de ce peche! En prononcant les paroles, il s\'endormit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 1, 'Saul avait approuve ce meurtre. Ce meme jour, une cruelle persecution sevit sur l\'Eglise de Jerusalem ; tous les freres, sauf les apotres, se repandirent dans toutes les parties de la Judee et de la Samarie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 2, 'Cependant quelques pieux proselytes ensevelirent Etienne et celebrerent ses funerailles avec eclat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 3, 'Quant a Saul il ravageait l\'Eglise, il penetrait dans les maisons, il en arrachait hommes et femmes et les faisait jeter en prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 4, 'Ceux qui avaient ete disperses allerent ca et la annoncer la Bonne Nouvelle de la parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 5, 'Ainsi Philippe, qui s\'etait rendu dans la ville de Samarie, y precha le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 6, 'La population fut unanime a subir l\'influence des paroles de Philippe, lorsqu\'elle l\'entendit et qu\'elle vit les miracles qu\'il faisait :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 7, 'des Esprits impurs sortirent du corps d\'un grand nombre de possedes, en jetant de grands cris ; plusieurs paralytiques et plusieurs estropies furent gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 8, 'Ce fut une grande joie dans cette ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 9, 'Or, il y avait auparavant dans cette ville un homme nomme Simon, magicien de profession, qui en imposait au peuple de Samarie et se faisait passer pour un grand personnage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 10, 'Tous, petits et grands, s\'etaient attaches a lui et disaient: C\'est lui qui est la Puissance de Dieu, la Grande, comme on l\'appelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 11, 'S\'ils s\'etaient ainsi attaches a lui, c\'est que depuis longtemps ses sorcelleries leur en imposaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 12, 'Mais quand ils eurent ete amenes a la foi par Philippe, qui leur annoncait l\'Evangile du Royaume de Dieu et de la personne de Jesus-Christ, tous, hommes et femmes, se firent baptiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 13, 'Simon, lui-meme, devint croyant, et, quand il fut baptise, il ne quitta plus Philippe ; le spectacle des miracles et des prodiges etonnants qu\'il operait le remplissait d\'admiration.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 14, 'A Jerusalem, les apotres apprirent que la Samarie avait accueilli la Parole de Dieu, et Pierre et Jean y furent delegues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 15, 'S\'y etant rendus, ils prierent pour les croyants, afin qu\'ils recussent l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 16, '(en effet, il n\'etait encore descendu sur aucun d\'eux, ils n\'avaient ete que baptises au nom du Seigneur Jesus) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 17, 'alors Pierre et Jean leur imposerent les mains, et ils recurent l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 18, 'Quand Simon vit que l\'imposition des mains des apotres conferait l\'Esprit, il leur offrit de l\'argent :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 19, 'Donnez-moi ce pouvoir, a moi aussi, dit-il, afin que ceux a qui j\'imposerai les mains recoivent l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 20, ' Perisse ton argent avec toi, lui repliqua Pierre, puisque tu as cru que le don de Dieu s\'achete!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 21, 'Tu n\'as ni part ni heritage dans tout ceci, car ton coeur n\'est pas droit devant Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 22, 'Repens-toi donc de cette mauvaise pensee, et prie le Seigneur de, te pardonner ce dessein de ton coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 23, 'Car, je le vois, tu es tout rempli de venin, tu es esclave de l\'iniquite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 24, ' Priez vous-memes le Seigneur pour moi, repondit Simon, afin qu\'il ne m\'arrive rien de ce que vous avez dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 25, 'Pierre et Jean, apres avoir ainsi rendu leur temoignage et annonce la Parole du Seigneur, regagnerent Jerusalem, en evangelisant plusieurs villages du pays des Samaritains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 26, 'Cependant un ange du Seigneur parla a Philippe : Leve-toi, lui dit-il, et rends-toi vers le sud sur la route qui descend de Jerusalem a Gaza ; cette route est deserte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 27, 'Il se leva et il partit. Or, un Ethiopien, un eunuque, officier de la candace, reine d\'Ethiopie, et gardien de tous ses tresors, venu adorer a Jerusalem,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 28, 's\'en retournait, et, assis sur son char, il lisait le prophete Esaie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 29, 'L\'Esprit dit a Philippe : Approche-toi de ce char ; rejoins-le.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 30, 'Philippe s\'empressa de le faire, et entendant l\'Ethiopien lire le prophete Esaie, il lui dit: Comprends-tu ce que tu lis? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 31, 'Comment le pourrai-je, repondit celui-ci, si quelqu\'un ne me dirige? Puis il invita Philippe a monter et a s\'asseoir a cote de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 32, 'Or voici quel etait le passage de l\'Ecriture qu\'il lisait : Il a ete conduit comme une brebis a la mort ; Comme un agneau muet devant celui qui le tond, Il n\'a pas ouvert la bouche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 33, 'Dans son humiliation son jugement a ete leve ; Mais qui racontera sa generation? Car sa vie a ete retranchee de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 34, 'L\'eunuque, s\'adressant a Philippe : Je te prie de me dire, lui demanda-t-il, qui le prophete designe ici ? Est-ce lui-meme? Est-ce quelqu\'un d\'autre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 35, 'Philippe prit alors la parole et, commencant par ce passage, il lui annonca l\'Evangile de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 36, 'Chemin faisant, ils rencontrerent de l\'eau. Voila de l\'eau, dit l\'eunuque, qui empeche que je ne sois baptise?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 37, '(Philippe dit: Si tu crois de tout ton coeur, cela est possible. L\'eunuque repondit : je crois que Jesus-Christ est le Fils de Dieu.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 38, 'Il fit arreter le char: Philippe et l\'eunuque descendirent tous deux dans l\'eau et celui-ci fut baptise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 39, 'Lorsqu\'ils furent sortis de l\'eau, l\'Esprit du Seigneur enleva Philippe, l\'eunuque ne le vit plus, et poursuivit son chemin plein de joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 8, 40, 'Philippe, en effet, se trouvait etre a Azot, d\'ou il partit, evangelisant toutes les villes qu\'il rencontra jusqu\'a son arrivee a Cesaree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 1, 'Saul, ne respirant toujours que menaces et carnage contre les disciples du Seigneur, s\'adressa au grand-pretre');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 2, 'et lui demanda des lettres pour les synagogues de Damas, afin de pouvoir, s\'il y trouvait des membres de la secte, hommes et femmes, les amener garrottes a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 3, 'Pendant ce voyage, comme il approchait de Damas, tout a coup, autour de lui, brilla une lumiere venue du ciel ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 4, 'il tomba par terre et entendit une voix, lui disant: Saul! Saul! pourquoi me persecutes-tu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 5, ' Qui es-tu, Seigneur? repondit-il, et lui: Je suis Jesus que tu persecutes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 6, 'mais releve-toi, entre dans la ville, et on te dira ce que tu dois faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 7, 'Ses compagnons de voyage restaient la, stupefaits, entendant bien la voir, mais ne voyant personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 8, 'Saul se releva de terre, rouvrit les yeux, mais ne vit rien. On dut le tenir par la main pour le mener a Damas,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 9, 'et, pendant trois jours, il resta aveugle et ne prit aucune nourriture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 10, 'Or il y avait a Damas un disciple du nom d\'Hananias, a qui le Seigneur dit en vision : Hananias!  Me voici, Seigneur, repondit-il.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 11, ' Leve-toi, lui dit le Seigneur, va dans la rue dite la rue Droite, et cherche dans la maison de Juda un nomme Saul de Tarse, car il est en prieres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 12, 'et il a vu un homme du nom d\'Hananias entrer aupres de lui et lui imposer les mains pour lui rendre la vue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 13, 'Hananias repondit: Seigneur, j\'ai entendu dire a bien des personnes quel mal cet homme a fait a tes fideles a Jerusalem,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 14, 'et il vient ici avec pleins pouvoirs, recus des chefs des pretres, d\'enchainer tous ceux qui invoquent ton nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 15, 'Mais le Seigneur lui dit : Va, car cet homme est un instrument que je me suis choisi pour porter mon nom devant les paiens, les rois et les enfants d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 16, 'Je lui ferai connaitre tout ce qu\'il devra souffrir pour mon nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 17, 'Alors Hananias alla ; il entra dans la maison et, imposant les mains a Saul, il lui dit : Saul, mon frere, le Seigneur m\'a envoye, Jesus, qui t\'est apparu sur le chemin par lequel tu es venu, pour que tu recouvres la vue et que tu sois rempli d\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 18, 'Aussitot il tomba de ses yeux des sortes d\'ecailles ; il recouvra la vue,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 19, 'se leva et fut baptise, puis il mangea, et il reprit des forces. Il passa quelques jours avec les disciples de Damas,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 20, 'et il precha immediatement dans les synagogues, disant que Jesus est le Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 21, 'Tous ses auditeurs etaient confondus. N\'est-ce pas, disaient-ils, l\'homme qui persecutait a Jerusalem ceux qui invoquaient ce nom et qui est venu ici pour les conduire garrottes aux chefs des pretres ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 22, 'Quant a Saul, il mettait chaque jour plus de force a confondre les Juifs residant a Damas, et a prouver que Jesus est le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 23, 'Il se passa plusieurs jours, au bout desquels les Juifs s\'entendirent entre eux pour le mettre a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 24, 'Saul eut connaissance de ce complot ; on gardait les portes jour et nuit pour parvenir a le tuer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 25, 'Alors les disciples, le prenant une nuit, le descendirent par-dessus le mur dans un panier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 26, 'Arrive a Jerusalem, il essaya d\'entrer en rapport avec les disciples ; mais tous le craignaient, ne croyant pas qu\'il fut lui-meme un disciple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 27, 'Ce fut Barnabas qui le prit avec lui, le mena aux apotres et leur raconta comment, dans le chemin, il avait vu le Seigneur, que celui-ci lui avait parle, et enfin comment, a Damas, il avait franchement preche au nom de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 28, 'Des lors il allait et venait avec eux dans Jerusalem');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 29, 'et parlait avec assurance au nom du Seigneur. Il avait aussi des entretiens et des discussions avec les Hellenistes ; mais ceux-ci cherchaient a lui oter la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 30, 'Les freres, l\'apprenant, le conduisirent a Cesaree et le firent partir pour Tarse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 31, 'L\'Eglise, dans toute la Judee, toute la Galilee, toute la Samarie, etait en paix, s\'edifiait, marchait dans la crainte du Seigneur et, par l\'assistance du saint Esprit, le nombre de ses membres augmentait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 32, 'Il arriva que Pierre, dans une visite a tous les fideles, se rendit aussi aupres de ceux qui habitaient Lydda.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 33, 'Il y trouva un homme nomme Enee, qui depuis huit ans n\'avait pas quitte son lit ; il etait paralyse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 34, 'Enee, lui dit Pierre, Jesus-Christ te guerit ; leve-toi et arrange ton lit toi-meme, et immediatement il se leva.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 35, 'Tous les habitants de Lydda et de la plaine de Saron le virent, et ils se convertirent au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 36, 'A Joppe, il y avait parmi les disciples une femme nommee Tabitha (mot qui signifie Dorcas). Elle se consacrait aux bonnes oeuvres et faisait beaucoup d\'aumones ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 37, 'et il arriva, ces jours-la, qu\'elle tomba malade et mourut. On fit sur elle les ablutions d\'usage, puis on la deposa dans la chambre haute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 38, 'Les disciples, ayant appris que Pierre etait a Lydda, Lydda est dans le voisinage de Joppe,  lui envoyerent deux hommes pour le prier de se rendre aupres d\'eux sans tarder.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 39, 'Pierre se mit en route et partit avec eux. Quand il arriva, on le conduisit a la chambre haute ; la toutes les veuves l\'entourerent et lui montrerent en pleurant des vetements et des manteaux que faisait Dorcas quand elle etait encore avec elles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 40, 'Pierre fit sortir tout le monde, se mit a genoux et pria ; puis, se tournant vers le corps, il dit : Tabitha, leve-toi! Celle-ci ouvrit les yeux et, voyant Pierre, elle s\'assit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 41, 'Il lui tendit alors la main et la fit lever ; puis il appela les fideles et les veuves et la leur rendit vivante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 42, 'Ce fait fut connu dans toute la ville de Joppe et plusieurs personnes crurent au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 9, 43, 'Quant a Pierre, il resta plusieurs jours a Joppe chez un tanneur nomme Simon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 1, 'Il y avait a Cesaree un homme nomme Cornelius, centurion d\'une cohorte appelee Italica ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 2, 'il etait pieux, vivait dans la crainte de Dieu, ainsi que tous les siens, faisait beaucoup d\'aumones au peuple et priait Dieu sans cesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 3, 'Cet homme eut une vision ; il vit distinctement vers la neuvieme heure du jour un ange de Dieu qui entrait chez lui et lui disait : Cornelius !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 4, 'Il le regarda tout effraye. Qu\'y a-t-il, Seigneur? lui repondit-il. Tes prieres et tes aumones, lui dit l\'ange, sont montees jusqu\'a Dieu, et il en a ete fait mention devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 5, 'Et maintenant envoie des hommes a Joppe, et fais venir ici un certain Simon, surnomme Pierre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 6, 'il demeure chez Simon le tanneur, dont la maison est pres de la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 7, 'Quand l\'ange qui lui avait parle fut parti, Cornelius appela deux de ses domestiques et un soldat pieux de ceux qui etaient attaches a sa personne ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 8, 'il leur raconta tout et les envoya a Joppe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 9, 'Le lendemain, comme ils etaient en route et s\'approchaient de la ville, Pierre monta sur la terrasse de la maison vers la sixieme heure, pour y prier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 10, 'Il eut faim et demanda a manger. Pendant qu\'on lui preparait quelque chose, il eut une extase,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 11, 'il, vit le ciel ouvert et un objet ressemblant a une grande nappe nouee aux quatre coins en descendre et s\'abaisser vers la terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 12, 'a l\'interieur de la nappe il y avait des quadrupedes de toute espece, des reptiles de la terre et des oiseaux du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 13, 'Leve-toi, Pierre, lui dit une voix, tue et mange.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 14, 'Mais Pierre repondit: Nullement, Seigneur, car jamais je n\'ai rien mange de souille ni d\'impur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 15, 'Alors une voix s\'adressa a lui une seconde fois: N\'appelle pas souille ce que Dieu a purifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 16, 'Cela se repeta par trois fois et aussitot apres l\'objet remonta au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 17, 'Pendant que Pierre meditait, incertain du sens de la vision qu\'il venait d\'avoir, les hommes envoyes par Cornelius et qui s\'etaient informes de la maison de Simon se presenterent a la porte d\'entree');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 18, 'et appelerent pour savoir si : Simon, surnomme Pierre, demeurait la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 19, 'A ce moment meme l\'Esprit dit a Pierre, qui etait a reflechir sur la vision : Voici trois hommes qui te demandent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 20, 'leve-toi, descends, et pars avec eux sans scrupule, car c\'est moi qui les ai envoyes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 21, 'Alors Pierre descendit : Me voici, dit-il a ces hommes, je suis la personne que vous demandez ; quel motif vous amene?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 22, 'Ils repondirent : Cornelius, centurion, homme juste et craignant Dieu, auquel toute la population juive rend un bon temoignage, a recu d\'un saint ange l\'ordre divin de te faire venir dans sa maison et d\'ecouter tes paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 23, 'Alors Pierre les fit entrer et leur donna l\'hospitalite. Le lendemain, il se mit en route et partit avec eux, et quelques-uns des freres de Joppe l\'accompagnerent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 24, 'Le jour suivant, ils arriverent a Cesaree. Cornelius les attendait et avait reuni ses parents et ses amis intimes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 25, 'Au moment ou Pierre entra, Cornelius, qui etait alle a sa rencontre, tomba a ses pieds et se prosterna.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 26, 'Mais Pierre le releva. Leve-toi, lui dit-il, comme toi, je ne suis qu\'un homme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 27, 'et il entra en s\'entretenant avec lui. Trouvant un grand nombre de personnes reunies,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 28, 'il leur dit : Vous savez, qu\'il est defendu a un Juif de se mettre en rapport personnel avec un homme d\'une autre nation ou d\'entrer chez lui ; mais, pour ce qui me concerne, Dieu m\'a appris a ne regarder aucun homme comme souille ou impur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 29, 'Voila pourquoi, etant appele, je suis venu ici sans objection ; je demande donc pour quel motif vous m\'avez fait appeler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 30, 'Cornelius repondit : il y a quatre jours, j\'avais jeune jusqu\'a cette heure-ci, et, a la neuvieme heure, j\'etais chez moi a prier, quand tout a coup un homme portant un vetement eblouissant se presenta a moi');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 31, 'et me dit: Cornelius, ta priere a ete exaucee et il a ete fait mention devant Dieu de tes aumones.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 32, 'Envoie donc a Joppe et fais venir Simon surnomme Pierre, il demeure dans la maison de Simon le tanneur, pres de la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 33, 'Immediatement j\'ai envoye vers toi ; tu as eu la bonte de venir, et maintenant nous sommes tous en presence de Dieu, prets a entendre tout ce que le Seigneur t\'a ordonne de nous dire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 34, 'Pierre prit alors la parole : En verite, dit-il, je reconnais que Dieu ne fait point acception de personnes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 35, 'mais que, chez tous les peuples, celui qui le craint et qui pratique la justice lui est agreable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 36, 'Il a envoye sa Parole aux enfants d\'Israel, en leur faisant precher l\'Evangile de paix par Jesus-Christ, le Seigneur de tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 37, 'vous savez ce qui s\'est passe dans toute la Judee, ce qui avait d\'abord commence en Galilee a la suite du bapteme preche par Jean ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 38, 'vous savez comment Jesus de Nazareth, oint par Dieu d\'Esprit saint et de puissance, allait de lieu en lieu en faisant le bien, en guerissant tous ceux qui etaient sous l\'empire du diable, parce que Dieu etait avec lui');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 39, '(nous avons ete temoins de tout ce, qu\'il a fait dans le pays des Juifs et a Jerusalem) ; ceux-ci l\'ont mis a mort, ils l\'ont pendu a une croix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 40, 'Mais Dieu l\'a ressuscite le troisieme jour et a permis quil se montrat');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 41, 'non a tout le peuple, mais aux temoins qu\'il avait choisis d\'avance, c\'est-a-dire a nous qui avons mange et bu avec lui apres sa resurrection d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 42, 'Et il nous a ordonne de precher au peuple et d\'attester que c\'est lui qui a ete designe par Dieu pour juger les vivants et les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 43, 'C\'est de lui que tous les prophetes rendent ce temoignage : le pardon des peches sera accorde par son nom a quiconque croit en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 44, 'Pendant que Pierre parlait encore de ces choses, l\'Esprit saint descendit sur tous ceux qui ecoutaient la Parole,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 45, 'et les croyants circoncis qui avaient accompagne Pierre etaient confondus de voir que le don du saint Esprit s\'etendait aussi aux paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 46, 'En effet, ils les entendaient parler en langues etrangeres et glorifier Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 47, 'Pierre dit alors : Peut-on interdire l\'eau du bapteme a ceux qui ont recu l\'Esprit saint aussi bien que nous?...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 10, 48, 'et il ordonna qu\'ils fussent baptises au nom de Jesus-Christ. Ceux-ci le prierent de rester quelques jours avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 1, 'Les apotres et les freres de Judee apprirent que les paiens aussi avaient accepte la Parole de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 2, 'et Pierre, a son retour a Jerusalem, fut blame par les fideles circoncis :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 3, 'Tu es entre chez des incirconcis, lui dirent-ils, et tu as mange avec eux !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 4, 'Mais Pierre leur raconta, en suivant l\'ordre des faits, tout ce qui s\'etait passe:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 5, 'J\'etais, leur dit-il, dans la ville de Joppe, et pendant que je priais j\'eus une extase et une vision m\'apparut ; c\'etait un objet descendant du ciel, ressemblant a une grande nappe nouee aux quatre coins et qui vint jusqu\'a moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 6, 'Ayant regarde a l\'interieur, l\'ayant bien examinee, j\'y vis les quadrupedes de la terre, les animaux sauvages, les reptiles et les oiseaux du ciel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 7, 'et j\'entendis une voix qui me disait : Leve-toi, Pierre, tue et mange ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 8, 'je repondis : Nullement, Seigneur, parce que jamais rien de souilles ni d\'impur n\'est entre dans ma bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 9, 'Une voix, venant du ciel, s\'adressa a moi pour la seconde fois: N\'appelle pas souilles ce que Dieu a purifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 10, 'Cela se repeta par trois fois, puis tout remonta au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 11, 'Et voila qu\'a ce moment meme trois hommes se presenterent a la porte de la maison ou j\'etais ; ils m\'etaient envoyes de Cesaree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 12, 'L\'Esprit me dit d\'aller avec eux sans aucun scrupule. Les six freres que voici m\'accompagnerent et nous entrames dans la maison de Cornelius.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 13, 'Celui-ci nous raconta comment il avait vu dans sa maison l\'ange se tenant devant lui et lui disant : Envoie a Joppe et fais venir Simon, surnomme Pierre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 14, 'il te dira des choses par lesquelles tu obtiendras le salut, toi et tous les tiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 15, 'Lorsque je commencai a leur parler, l\'Esprit saint descendit sur eux, comme sur nous a l\'origine ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 16, 'et je me souvins de ce mot du Seigneur, quand il disait : Jean baptisait d\'eau, mais vous, vous serez baptises d\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 17, 'Si donc Dieu leur a accorde le meme don qu\'a nous, pour avoir cru au Seigneur Jesus-Christ, qui etais-je moi pour oser m\'opposer a Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 18, 'A l\'ouie de ces paroles, ils se calmerent, et ils rendirent gloire a Dieu : Ainsi, disaient-ils, Dieu a aussi accorde aux paiens la repentance pour qu\'ils aient la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 19, 'Ceux qui avaient ete disperses par la persecution, a la suite de la mort d\'Etienne, allerent jusqu\'en Phenicie et atteignirent Chypre et Antioche, n\'annoncant la parole a personne qu\'aux Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 20, 'Mais quelques-uns d\'eux qui etaient Chypriotes et Cyreneens, arrives a Antioche, parlerent aussi aux Hellenistes et leur annoncerent l\'Evangile du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 21, 'La main du Seigneur fut avec eux et grand fut le nombre de ceux qui crurent et se convertirent au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 22, 'Le bruit en parvint aux oreilles des membres de l\'Eglise de Jerusalem et ils envoyerent Barnabas jusqu\'a Antioche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 23, 'Lorsque, a son arrivee, il vit la grace de Dieu, il s\'en rejouit et il les exhorta tous a rester, d\'un coeur convaincu, fideles au Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 24, 'parce qu\'il etait homme de bien, plein d\'Esprit saint et de foi ; et un grand nombre de personnes s\'unirent au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 25, 'Barnabas se rendit ensuite a Tarse, y chercha Saul, le trouva et l\'amena a Antioche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 26, 'Durant une annee entiere ils resterent ensemble dans l\'Eglise et instruisirent un grand nombre de personnes. Ce fut a Antioche qu\'on donna pour la premiere fois aux disciples le nom de chretiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 27, 'En ce temps-la, des prophetes se rendirent de Jerusalem a Antioche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 28, 'L\'un d\'eux, nomme Agabus, se leva et annonca par l\'Esprit qu\'une grande famine regnerait sur toute la terre ; elle arriva, en effet, sous Claude.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 29, 'Les disciples deciderent d\'envoyer, chacun selon son pouvoir, un secours aux freres demeurant en Judee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 11, 30, 'ce qu\'ils firent en le remettant aux Anciens par l\'entremise de Barnabas et de Saul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 1, 'Vers la meme epoque, le roi Herode e se mit a persecuter quelques-uns des membres de l\'Eglise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 2, 'Il fit trancher la tete a Jacques, frere de Jean.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 3, 'Voyant le bon effet qu\'il produisait ainsi sur les Juifs, il ordonna aussi d\'enfermer Pierre. On etait a la semaine des Azymes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 4, 'Il le fit arreter et mettre en prison, et chargea quatre escouades, de quatre soldats chacune, de le surveiller. Son intention etait de le juger apres la Paque et en presence de tout le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 5, 'Pierre etait donc garde dans la prison et l\'Eglise adressait a Dieu de ferventes prieres pour lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 6, 'Or, la nuit qui preceda le moment fixe par Herode pour sa comparution, Pierre dormait entre deux soldats, retenu par une double chaine et, devant la porte, des sentinelles gardaient la prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 7, 'Tout a coup apparut un ange du Seigneur ; le cachot devint resplendissant de lumiere. Cet ange, touchant Pierre au cote, le reveilla et lui dit : Leve-toi vite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 8, 'et les chaines tomberent de ses mains. Mets ta ceinture, lui dit ensuite l\'ange, et chausse tes sandales. Pierre le fit. Il ajouta : Enveloppe-toi de ton manteau et suis-moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 9, 'Pierre sortit et le suivit sans croire a la realite de ce que l\'ange lui faisait faire ; il s\'imaginait avoir une vision.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 10, 'Cependant ils passerent la premiere garde, puis la seconde et arriverent a la porte de fer qui donne sur la ville. Cette porte s\'ouvrit d\'elle-meme devant eux ; ils sortirent ; ils marcherent dans une rue et, a cet instant, l\'ange s\'eloigna de Pierre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 11, 'qui revint a lui et se dit : Je vois maintenant que le Seigneur m\'a reellement envoye son ange, qu\'il m\'a arrache aux mains d\'Herode et a l\'avide impatience du peuple juif.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 12, 'Apres reflexion, il se rendit a la maison de Marie, mere de Jean surnomme Marc, ou plusieurs personnes etaient assemblees et priaient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 13, 'Il frappa a la porte d\'entree ; une servante, nommee Rhode, alla ecouter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 14, 'Elle reconnut la voix de Pierre ; transportee de joie, au lieu d\'ouvrir, elle rentra en courant et annonca que Pierre etait la, devant la porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 15, 'On la traita de folle. Elle affirma qu\'elle disait vrai. C\'est son ange, dit-on alors.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 16, 'Pierre cependant continuait a frapper ; on ouvrit enfin ; on le vit ; la joie fut immense.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 17, 'Il leur fit de la main signe de se taire et leur raconta comment le Seigneur l\'avait tire de prison, puis il ajouta : Annoncez ces choses a Jacques et aux autres freres. Ensuite il sortit et s\'en alla dans un autre endroit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 18, 'Le jour venu, l\'inquietude fut grande parmi les soldats ; ils ne savaient ce que Pierre etait devenu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 19, 'Herode le fit rechercher et, ne le trouvant pas, il mit les gardes a la question et ordonna leur execution ; puis il quitta la Judee et alla demeurer a Cesaree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 20, 'Les gens de Tyr et de Sidon avaient des difficultes avec lui ; ils vinrent ensemble le trouver et, ayant gagne a leur cause Blastus, son chambellan, ils solliciterent un arrangement parce que leur pays tirait sa subsistance de celui du roi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 21, 'Un jour fut fixe et Herode, revetu d\'une tunique royale, siegea sur son trone et harangua le peuple avec une grande solennite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 22, 'Le peuple s\'ecria : C\'est un dieu qui parle et non un homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 23, 'A l\'instant meme un ange du Seigneur frappa Herode parce qu\'il n\'avait pas donne gloire a Dieu ; et, ronge par les vers, il expira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 24, 'Cependant la parole de Dieu se repandait de plus en plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 12, 25, 'Barnabas et Saul, leur mission remplie, revinrent de Jerusalem, ramenant avec eux Jean surnomme Marc.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 1, 'Il y avait alors a Antioche, dans l\'eglise de cette ville, comme prophetes et docteurs, Barnabas, Symeon dit Niger, Lucius de Cyrene, Menahem, frere de lait du tetrarque Herode, et Saul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 2, 'Un jour qu\'ils celebraient le culte du Seigneur et qu\'ils jeunaient l\'Esprit saint leur dit: Mettez-moi a part Barnabas et Saul pour l\'oeuvre a laquelle je les ai appeles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 3, 'Alors, apres avoir jeune et prie, ils leur imposerent les mains et les laisserent partir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 4, 'Paul et Barnabas, ainsi envoyes par le saint Esprit, descendirent a Seleucie, ou ils s\'embarquerent pour l\'ile de Chypre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 5, 'Arrives a Salamis, ils annoncerent la parole de Dieu dans les synagogues des Juifs. Ils avaient Jean pour les aider.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 6, 'Ayant traverse toute lile jusqu\'a Paphos, ils rencontrerent un certain personnage, un Juif, magicien et faux prophete dont le nom etait Bar-Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 7, 'Il etait aupres du proconsul Sergius Paulus qui, lui, etait un homme sense. Celui-ci fit appeler Barnabas et Saul et demanda a entendre la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 8, 'Mais le magicien Elymas (c\'est ainsi que se traduit son nom), leur fit opposition et chercha a detourner le proconsul de la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 9, 'Alors Saul, nomme aussi Paul, plein d\'Esprit saint, le regarda en face et lui dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 10, 'Etre tout rempli d\'astuce et de fraude, fils du diable, ennemi de toute justice, ne cesseras-tu pas de pervertir les droites voies du Seigneur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 11, 'La main du Seigneur va maintenant te frapper ; tu seras aveugle ; pendant quelque temps tu ne verras pas le soleil. Immediatement il se trouva enveloppe d\'epaisses tenebres et, marchant ca et la, il cherchait quelqu\'un pour le conduire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 12, 'Alors le proconsul, voyant ce qui etait arrive, devint croyant ; il etait tres frappe de la doctrine du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 13, 'Embarques a Paphos, Paul et ses compagnons arriverent a Perge en Pamphylie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 14, 'la, Jean se separa d\'eux et revint a Jerusalem. Mais eux traverserent le pays depuis Perge et arriverent a Antioche de Pisidie. Ils se rendirent a la synagogue le jour du sabbat et y prirent place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 15, 'Apres la lecture de la Loi et des Prophetes, les presidents envoyerent leur dire : Freres, si vous avez quelques paroles d\'exhortation a adresser au peuple, parlez !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 16, 'Alors Paul se leva, fit un signe de la main et dit : Hommes d\'Israel, et vous qui craignez Dieu! Ecoutez :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 17, 'Le Dieu de notre nation a elu nos peres, il a releve le peuple pendant son sejour a l\'etranger en Egypte ; il l\'en a fait sortir a bras leve ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 18, 'pendant environ quarante ans, il s\'est accommode a eux dans le desert,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 19, 'il a aneanti sept peuples dans le pays de Chanaan, et a mis leur territoire en sa possession.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 20, 'Ensuite, pendant quatre cent cinquante ans environ, il leur a donne des Juges jusqu\'au prophete Samuel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 21, 'C\'est alors qu\'ils ont demande un roi et Dieu leur a donne pour quarante ans Saul, fils de Kis, de la tribu de Benjamin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 22, 'L\'ayant detrone, il a eleve a la royaute David, dont il a fait l\'eloge en disant : J\'ai trouve David, fils de Jesse, il est selon mon coeur, et il fera toutes mes volontes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 23, 'C\'est de sa famille que Dieu, comme il l\'avait promis, a fait sortir Jesus, un Sauveur pour Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 24, 'Jean avait preche d\'avance, avant sa venue, un bapteme de repentance a tout le peuple d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 25, 'Jean disait pendant qu\'il remplissait sa carriere : Je ne suis pas, moi, celui pour lequel vous me prenez. Mais, voici, il vient apres moi celui dont je ne suis pas digne de detacher les sandales.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 26, 'Mes freres, vous qui descendez de la famille d\'Abraham, et vous qui etes ici et qui craignez Dieu, c\'est a nous qu\'un message de salut a ete adresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 27, 'Car les habitants de Jerusalem et leurs magistrats, ayant meconnu ce Jesus, ont accompli par la meme, en le jugeant, les paroles des prophetes lues chaque sabbat ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 28, 'ils ne trouvaient aucun motif de le faire mourir, et ils ont demande a Pilate de le mettre a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 29, 'Quand ils eurent accompli tout ce qui avait ete ecrit sur lui, ils le descendirent de la croix et le mirent dans un tombeau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 30, 'Mais Dieu l\'a ressuscite d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 31, 'Il est apparu pendant plusieurs jours a ceux qui etaient montes avec lui de la Galilee a Jerusalem et qui sont maintenant ses temoins aupres du peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 32, 'Voici donc la Bonne Nouvelle que nous vous annoncons: La promesse faite a nos peres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 33, 'Dieu l\'a tenue pour nous, leurs enfants, en ressuscitant Jesus. Voila pourquoi il est ecrit dans le Psaume second : Tu es mon fils Et moi, je t\'ai engendre aujourd\'hui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 34, 'Et sur sa resurrection d\'entre les morts, sur ce fait qu\'il ne tombera pas en corruption, voici ce qui est dit : Je vous donnerai les graces saintes et certaines promises a David.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 35, 'Voila encore pourquoi il est dit ailleurs : Tu ne permettras point que ton Saint voie la corruption.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 36, 'Eh bien, David, apres avoir servi a son epoque aux desseins de Dieu, est mort, a ete reuni a ses peres et a vu la corruption.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 37, 'C\'est celui que Dieu a ressuscite qui n\'a pas vit la corruption.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 38, 'Sachez donc, mes freres, que, par lui, la remission des peches vous est annoncee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 39, 'Tout croyant trouve en lui la justification de tout ce dont la Loi de Moise n\'a pu vous justifier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 40, 'Prenez donc garde qu\'il ne vous arrive ce qui est dit dans les prophetes :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 41, 'Regardez, contempteurs, soyez etonnes et disparaissez, Parce que je ferai une oeuvre, de vos jours, Une oeuvre que vous ne croiriez pas si on vous la racontait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 42, 'Lorsqu\'ils sortirent, on les pria de recommencer leur predication le sabbat suivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 43, 'A l\'issue de la reunion, une grande foule de Juifs et de proselytes pieux suivirent Paul et Barnabas qui s\'entretinrent avec eux et les exhorterent a rester attaches a la grace de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 44, 'Le sabbat suivant presque toute la ville se reunit pour entendre la parole du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 45, 'Mais les Juifs, voyant cette foule, furent pleins de jalousie ; ils s\'opposerent a tout ce que disait Paul et se mirent a le contredire et a l\'injurier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 46, 'Paul et Barnabas leur dirent alors en toute franchise : Nous devions commencer par vous precher la parole de Dieu, mais puisque vous la repoussez et que vous vous jugez vous-memes indignes de la vie eternelle, nous allons nous tourner vers les paiens,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 47, 'car voici ce que le Seigneur nous a commande : Je t\'ai etabli pour etre la lumiere des paiens, Pour repandre le salut jusqu\'aux extremites de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 48, 'Les paiens furent dans la joie de les entendre parler ainsi ; ils glorifierent la parole du Seigneur, et tous ceux qui etaient destines a la vie eternelle devinrent croyants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 49, 'La parole du Seigneur se repandit dans tout le pays ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 50, 'mais les Juifs exciterent les dames les plus considerables de la ville rattachees au Judaisme et les principaux habitants ; ils provoquerent une persecution contre Paul et Barnabas, et ils les bannirent de leur territoire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 51, 'Ceux-ci secouerent sur eux la poussiere de leurs pieds et se rendirent a Iconium ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 13, 52, 'quant aux disciples, ils etaient pleins de joie et d\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 1, 'Il arriva qu\'a Iconium, ils se rendirent ensemble a la synagogue, juive et y parlerent de telle sorte que Juifs et Grecs devinrent croyants en grand nombre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 2, 'Mais les Juifs restes incredules exciterent et indisposerent la population paienne contre les freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 3, 'Ceux-ci prolongerent alors leur sejour, pleins de courage et se confiant au Seigneur qui rendait temoignage a la predication de sa grace en leur donnant de faire des miracles et des prodiges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 4, 'Cependant la population de la ville se divisa, les uns furent pour les Juifs, les autres pour les apotres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 5, 'et comme les Juifs et les paiens, d\'accord avec les autorites, parlaient de les maltraiter et de les lapider,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 6, 'les apotres, l\'ayant appris, se refugierent dans les villes de Lycaonie, a Lystre, a Derbe et aux environs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 7, 'et ils y annoncerent l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 8, 'Or, il y avait a Lystre un homme paralyse des jambes, estropie de naissance ; toujours assis, il n\'avait jamais marche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 9, 'Cet homme ecoutait Paul parler et lui, arretant sur lui son regard, vit qu\'il avait la foi pour etre gueri. Il lui dit alors a haute voix :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 10, 'Leve-toi droit sur tes pieds ; il se leva d\'un saut ; il marchait!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 11, 'Le peuple, voyant ce que Paul avait fait, se mit a pousser des acclamations, et a s\'ecrier en langue lycaonienne : Les dieux sous forme humaine sont descendus vers nous !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 12, 'Barnabas fut appele Zeus, et Paul Hermes, parce que c\'etait lui qui portait la parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 13, 'Le pretre de Zeus, qui avait un temple a l\'entree de la ville, amena des taureaux et apporta des guirlandes devant les portes, et, d\'accord avec le peuple, il allait offrir un sacrifice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 14, 'Mais les apotres Barnabas et Paul, apprenant cela, dechirerent leurs vetements, et se jeterent au milieu de la foule en s\'ecriant :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 15, 'Lycaoniens Pourquoi faites-vous cela ? Nous sommes de meme nature que vous, nous ne sommes que des hommes, nous vous prechons l\'Evangile qui vous dit de renoncer a ces idoles inutiles, de vous tourner vers le Dieu vivant, createur du ciel, de la terre, de la mer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 16, 'et de tout ce qu\'ils renferment. Il a laisse, dans les siecles passes, toutes les nations paiennes suivre leurs propres voies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 17, 'et cependant il s\'est fait connaitre a vous par ses bienfaits ; c\'est lui qui vous donne la pluie du ciel, les saisons avec leurs fruits, la nourriture et toutes les joies du coeur dont vous etes combles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 18, 'Malgre ces paroles, ce ne fut pas sans peine qu\'ils empecherent le peuple de leur offrir un sacrifice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 19, 'Cependant, survinrent d\'Antioche et d\'Iconium des Juifs qui gagnerent le peuple et Paul fut lapide, traine hors de la ville et laisse pour mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 20, 'Quand les disciples vinrent l\'entourer, il se releva et rentra dans la ville. Le lendemain, il partit avec Barnabas pour Derbe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 21, 'Ils y annoncerent l\'Evangile et y firent de nombreux disciples, puis retournerent a Lystre, a Iconium et a Antioche,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 22, 'affermissant l\'esprit des disciples, les exhortant a perseverer dans la foi et leur apprenant que c\'est par beaucoup de tribulations qu\'on entre dans le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 23, 'Ils firent nommer des Anciens dans chaque Eglise, puis ils les recommanderent par des jeunes et des prieres au Seigneur en qui ils avaient cru.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 24, 'Ils traverserent ensuite la Pisidie, vinrent en Pamphylie');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 25, 'et, apres avoir annonce la parole a Perge, ils descendirent a Attalie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 26, 'De la ils firent voile pour Antioche, ou ils avaient ete livres a la grace de Dieu pour l\'oeuvre qu\'ils venaient d\'accomplir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 27, 'A leur arrivee, ils convoquerent l\'Eglise et raconterent les choses que Dieu avait faites par eux. Il a ouvert aux paiens, dirent-ils, la porte de la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 14, 28, 'Ils faisaient un assez long sejour aupres des disciples');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 1, 'lorsqu\'arriverent de Judee des gens qui donnaient aux freres cet enseignement : Vous ne pouvez etre sauves, si vous ne vous faites pas circoncire selon les rites du Mosaisme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 2, 'Il s\'ensuivit entre Paul et Barnabas d\'une part, et ces gens de l\'autre, une discussion, une altercation tres vive. Il fut decide que Paul et Barnabas iraient a Jerusalem avec quelques-uns des leurs s\'entendre avec les apotres et les Anciens sur ce sujet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 3, 'Delegues par l\'Eglise, ils traverserent la Phenicie et la Samarie, racontant la conversion des paiens, et causant ainsi une grande joie a tous les freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 4, 'A leur arrivee a Jerusalem, ils furent accueillis par l\'Eglise, les apotres et les Anciens et leur raconterent les choses que Dieu avait faites par eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 5, 'Mais quelques membres du parti des Pharisiens, devenus croyants, protesterent et dirent qu\'il fallait faire circoncire les paiens et leur enjoindre d\'observer la loi de Moise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 6, 'Les apotres et les Anciens se reunirent pour etudier la question.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 7, 'La discussion fut longue ; Pierre enfin se leva et dit ceci : Mes freres, vous savez que, depuis longtemps deja, Dieu m\'a choisi parmi vous pour que les paiens entendissent de ma bouche la predication de l\'Evangile et devinssent croyants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 8, 'Ce Dieu qui lit dans les coeurs leur a rendu temoignage et leur a donne l\'Esprit saint comme a nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 9, 'Il n\'a fait aucune difference entre eux et nous, il a purifie leurs coeurs par la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 10, 'Pourquoi donc maintenant tentez-vous Dieu, pourquoi mettez-vous sur les epaules des disciples un joug que nos peres ni nous-memes n\'avons pu porter?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 11, 'C\'est par la grace du Seigneur Jesus que nous croyons etre sauves et c\'est exactement ce qu\'ils croient aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 12, 'Alors tout le monde se tut et on ecouta Barnabas et Paul faisant le recit des miracles et des prodiges que Dieu avait faits par eux aupres des paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 13, 'Quand ils eurent cesse de parler, Jacques s\'exprima ainsi : Mes freres, ecoutez-moi :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 14, 'Simeon vous a raconte comment Dieu, une premiere fois, a pris soin de choisir parmi les paiens un peuple qui portat son nom ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 15, 'et cela s\'accorde avec les paroles des prophetes, car il est ecrit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 16, 'Apres cela je reviendrai, et je dresserai de nouveau la tente de David qui est tombee ; Oui, je la redresserai et je reparerai ses ruines,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 17, 'Pour que le reste des hommes cherchent le Seigneur, Ainsi que tous les paiens sur lesquels mon nom est invoque, Dit le Seigneur qui fait ces choses,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 18, 'et elles lui sont connues de toute eternite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 19, 'Mon avis est donc de ne pas creer de difficultes aux paiens qui se convertissent a Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 20, 'mais ecrivons-leur de s\'abstenir des souillures des idoles, de la fornications, des animaux morts par suffocation et du sang.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 21, 'Car Moise a, de temps immemorial, ses predicateurs dans chaque ville, et il est lu dans les synagogues tous les jours du sabbat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 22, 'Alors les apotres et les Anciens, d\'accord avec toute l\'Eglise, deciderent de choisir quelques-uns d\'entre eux et de les envoyer a Antioche avec Paul et Barnabas ce furent Judas surnomme BarSabbas et Silas, tous deux fort estimes de leurs freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 23, 'Voici la lettre qu\'ils leur remirent : Les apotres et les freres les anciens aux freres d\'Antioche de Syrie et de Cilicie, autrefois paiens. salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 24, 'Ayant appris que certains des notres, sans aucun mandat de notre part, vous ont troubles par leurs paroles et vous ont bouleverse l\'esprit,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 25, 'nous avons decide a l\'unanimite de vous envoyer des delegues qui accompagneront nos bien-aimes Barnabas et Paul,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 26, 'ces hommes qui ont risque leurs vies pour le nom de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 27, 'Nous vous avons donc envoye Judas et Silas qui vous diront la meme chose de vive voix ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 28, 'savoir qu\'il a semble bon a l\'Esprit saint et a nous, de ne pas vous imposer d\'autres charges que celles qui sont indispensables,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 29, 'c\'est-a-dire vous abstenir des viandes offertes en sacrifice aux dieux, du sang, des animaux morts par suffocation et de la fornication, toutes choses dont vous ferez bien de vous garder. Adieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 30, 'Les delegues prirent donc conge de l\'Eglise et se rendirent a Antioche. Ils rassemblerent tous les freres et leur remirent la lettre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 31, 'Sa lecture les remplit de joie et de courage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 32, 'Judas et Silas, prophetes eux-memes, encouragerent les freres dans de longs discours et les affermirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 33, 'Quelque temps apres, ils retournerent avec les voeux des freres aupres de ceux qui les avaient envoyes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 34, 'Cependant Paul et Barnabas restaient a Antioche,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 35, 'prechant l\'Evangile et enseignant avec plusieurs autres la Parole du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 36, 'Quelque temps plus tard, Paul dit a Barnabas : Retournons visiter les freres dans toutes les villes ou nous avons annonce la Parole du Seigneur pour voir en quel etat ils se trouvent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 37, 'Barnabas proposa d\'emmener aussi Jean, surnomme Marc.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 38, 'Mais Paul n\'etait pas d\'avis d\'emmener celui qui les avait quittes en Pamphylie et ne les avait pas accompagnes dans leur oeuvre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 39, 'Ce dissentiment fut si grave qu\'il se separerent, et Barnabas, emmenant Marc, s\'embarqua pour l\'ile de Chypre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 40, 'Quant a Paul, il choisit Silas et partit, recommande par les freres a la grace du Seigneur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 15, 41, 'il parcourut la Syrie et la Cilicie, en fortifiant les Eglises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 1, 'Il arriva aussi a Derbe et a Lystres et y trouva un disciple appele Timothee, fils d\'une Juive devenue croyante, mais dont le pere etait Grec.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 2, 'Les freres de Lystres et d\'Iconium avaient bonne opinion de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 3, 'Paul voulut l\'emmener avec lui ; il le prit donc et le circoncit, a cause des Juifs de l\'endroit ; car tout le monde savait que son pere etait Grec.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 4, 'Dans toutes les villes ou ils passaient, ils faisaient connaitre et recommandaient d\'observer les decisions prises par les apotres et les Anciens de Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 5, 'C\'est ainsi que la foi des Eglises s\'affermissait et que leur nombre augmentait tous les jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 6, 'Ils traverserent la Phrygie et le pays des Galates (le saint Esprit les avait empeches de precher la Parole en Asie)..');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 7, 'Arrives du cote de la Mysie, ils se proposaient d\'entrer en Bithynie, mais l\'Esprit de Jesus ne le leur permit pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 8, 'Ils traverserent alors rapidement la Mysie et descendirent a Troas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 9, 'Paul y eut une vision pendant la nuit ; un Macedonien se presentait a lui et lui disait en le suppliant : Passe en Macedoine et viens a notre aide.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 10, 'Immediatement apres que cette vision lui eut apparu, nous cherchames a partir pour la Macedoine ; nous comprenions que Dieu nous appelait a y precher l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 11, 'Embarques a Troas, nous marchames, vent arriere, sur l\'ile de Samothrace ; le lendemain sur Neapolis');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 12, 'et, de la, nous nous rendimes a Philippes, la premiere ville de la province de Macedoine et une colonie. Nous restames quelques jours dans cette ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 13, 'Le jour du sabbat nous sortimes des portes et nous nous rendimes au bord de la riviere, ou nous supposions qu\'on se reunissait pour la Priere. Nous primes notre place, et nous nous entretinmes avec les femmes qui y etaient rassemblees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 14, 'Il y en avait une appelee Lydie, marchande d\'etoffes de pourpre de Thyatire, une proselyte, qui nous ecouta. Le Seigneur lui toucha le coeur, de sorte qu\'elle fit attention a ce que disait Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 15, 'Quand elle eut ete baptisee avec sa famille, elle nous invita en disant : Si vous me croyez fidele au Seigneur, entrez dans ma maison ; demeurez-y ; et elle nous en pria instamment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 16, 'Il arriva, comme nous allions a la Priere, qu\'une esclave, possedee d\'un Esprit devin, se presenta a nous. Elle faisait gagner beaucoup d\'argent a ses maitres en annoncant l\'avenir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 17, 'Elle se mit a nous suivre, Paul et nous. Ces hommes-la, criait-elle, sont des serviteurs du Dieu supreme, ils vous annoncent le chemin du salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 18, 'Elle fit cela plusieurs jours ; enfin Paul, impatiente, se retourna et dit a l\'Esprit : Je t\'ordonne, au nom de Jesus-Christ, de sortir de cette femme. A l\'instant meme, il sortit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 19, 'Cependant les maitres de cette esclave, se voyant enlever le gain sur lequel ils comptaient, se saisirent de Paul et de Silas, les trainerent sur la place publique devant les magistrats');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 20, 'et les amenerent aux preteurs. Ces hommes-la, dirent-ils, troublent notre ville ; ce sont des Juifs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 21, 'et ils prechent des coutumes qu\'il ne nous est permis ni d\'accepter ni de suivre, puisque nous sommes Romains!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 22, 'La populace se souleva aussi contre eux, et les preteurs leur firent arracher leurs vetements et les condamnerent a recevoir la bastonnade.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 23, 'On les frappa cruellement, puis on les jeta en prison, et il fut recommande au geolier de les garder en lieu sur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 24, 'Celui-ci, pour obeir a cet ordre, les mit dans le cachot le plus recule et engagea leurs pieds dans les ceps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 25, 'Vers le milieu de la nuit, Paul et Silas priaient, chantaient les louanges de Dieu, et les prisonniers les ecoutaient,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 26, 'quand tout a coup se produisit un tremblement de terre si violent que la prison fut ebranlee jusque dans ses fondements ; toutes les portes s\'ouvrirent instantanement, et les liens de tous les prisonniers se relacherent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 27, 'Le geolier, reveille en sursaut et voyant ouvertes les portes de la prison, tira son epee et voulut se donner la mort, croyant que les prisonniers s\'etaient echappes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 28, 'Mais Paul lui parla: Ne te fais point de mal, lui cria-t-il, nous sommes tous ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 29, 'Alors le geolier demanda de la lumiere, entra precipitamment dans le cachot et se jeta tout tremblant aux pieds de Paul et de Silas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 30, 'puis il les fit sortir et leur dit : Seigneurs, que faut-il que je fasse pour etre sauve?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 31, 'Ils lui repondirent : Crois au Seigneur Jesus et tu seras sauve, toi et ta maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 32, 'Et ils lui annoncerent la Parole de Dieu, a lui et a tous ceux de sa famille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 33, 'Le geolier les prenant avec lui a l\'heure meme, pendant la nuit, lava leurs plaies et se fit immediatement baptiser, lui et tous les siens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 34, 'Puis il les conduisit dans son logement et leur donna a manger ; il etait transporte de joie, et tous les siens l\'etaient avec lui, d\'avoir cru en Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 35, 'Le jour venu, les preteurs envoyerent les licteurs dire au geolier : Relache ces hommes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 36, 'Le geolier rapporta ces paroles a Paul : Les preteurs me font dire de vous relacher ; partez donc et allez en paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 37, 'Mais Paul repondit aux licteurs : Ils nous ont fait frapper en public et sans jugement, nous, des citoyens romains, puis ils nous ont jetes en prison, et maintenant ils nous font sortir en cachette! Non, non, il faut qu\'ils viennent eux-memes et nous raccompagnent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 38, 'Les licteurs repeterent ces paroles aux preteurs. Ceux-ci, apprenant qu\'ils etaient romains, prirent peur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 39, 'ils vinrent les apaiser, les raccompagnerent et les prierent de quitter la ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 16, 40, 'Quant a eux, a leur sortie de prison, ils se rendirent chez Lydie, ils y virent les freres, leur adresserent quelques exhortations et partirent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 1, 'Ils traverserent Amphipolis et Apollonie et arriverent a Thessalonique, ou les Juifs avaient une synagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 2, 'Selon sa coutume, Paul s\'y rendit et, durant trois sabbats, il discuta avec eux, expliquant et demontrant, d\'apres les Ecritures,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 3, 'que le Christ devait souffrir et ressusciter d\'entre les morts, et ce Christ, disait-il, c\'est le Jesus que je vous annonce.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 4, 'Paul et Silas gagnerent quelques Juifs qui se rallierent a eux, un grand nombre de Grecs craignant Dieu et beaucoup de femmes de la meilleure societe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 5, 'Mais les Juifs, pleins de jalousie, recruterent une bande de vagabonds, de ces vauriens toujours prets a faire du bruit, et mirent le trouble dans la ville. Ils assaillirent la maison de Jasons et y chercherent Paul et Silas pour les mener a l\'assemblee populaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 6, 'Ne les trouvant pas, ils trainerent Jason et quelques freres devant les politarques, en criant : Ceux qui ont bouleverse le monde entier sont dans la ville,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 7, 'Jason les a recus: tous ces gens-la sont en revolte contre les edits de l\'Empereur ; ils ont un autre roi qu\'ils appellent Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 8, 'La foule et les politarques qui entendaient ces paroles en furent fort emus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 9, 'et ils ne laisserent aller Jason et les autres qu\'apres avoir exige d\'eux une caution.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 10, 'Sans tarder, la nuit suivante, les freres firent partir Paul et Silas pour Beree ; en y arrivant, ils se rendirent a la synagogue des Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 11, 'Ceux-ci etaient mieux eleves que ceux de Thessalonique ; ils accueillirent tres volontiers la predication de la Parole, et tous les jours ils etudiaient les Ecritures pour y verifier ce qu\'on leur disait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 12, 'Beaucoup devinrent croyants, ainsi que des femmes grecques du meilleur monde et un grand nombre d\'hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 13, 'Mais quand les Juifs de Thessalonique apprirent que Paul avait annonce la Parole de Dieu a Beree, ils y vinrent pour mettre le trouble dans le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 14, 'Alors les freres firent immediatement partir Paul dans la direction de la mer: Silas et Timothee resterent a Beree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 15, 'Ceux qui accompagnerent Paul le menerent jusqu\'a Athenes, et ils s\'en retournerent, charges par lui de mander a Silas et a Timothee de venir le rejoindre le plus tot possible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 16, 'Paul, qui les attendait a Athenes, se sentait indigne jusqu\'au fond de l\'ame de voir la ville remplie d\'idoles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 17, 'Cependant il s\'entretenait dans la synagogue avec les Juifs et les proselytes, et sur la place publique, tous les jours, avec les premiers venus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 18, 'Entre autres, il fut entrepris par quelques philosophes epicuriens et stoiciens. Que veut donc dire ce radoteur? disaient les uns. C\'est, parait-il, un precheur de nouveaux dieux, disaient les autres, parce que Paul leur annoncait l\'Evangile de Jesus et la resurrection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 19, 'Ils le prirent avec eux et le conduisirent a l\'Areopage, en lui disant : Pouvons-nous savoir quelle est cette nouvelle doctrine que tu enseignes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 20, 'car tu nous racontes la des choses etranges ; nous desirons bien savoir ce que tout cela signifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 21, '(Tous les Atheniens et les etrangers domicilies dans la ville ne s\'occupaient qu\'a dire ou a apprendre des nouvelles).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 22, 'Alors Paul, debout au milieu de l\'Areopage, s\'exprima ainsi : Atheniens, En tout, je vous trouve le plus religieux des peuples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 23, 'Comme, en effet, je regardais, en passant, vos objets sacres, j\'ai trouve un autel sur lequel etait ecrit : A UN DIEU INCONNU. Ce que vous honorez sans le connaitre, moi, je viens vous le reveler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 24, 'Le Dieu qui a fait le monde et tout ce qu\'il renferme, etant maitre du ciel et de la terre, n\'habite point dans des temples faits de main d\'homme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 25, 'et il ne saurait etre servi par des mains humaines comme s\'il avait besoin de rien, lui qui donne a tous la vie, le souffle et toutes choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 26, 'C\'est lui qui a fait naitre d\'un seul homme toutes les nations, et les a fait habiter sur la surface de la terre, fixant a chacune la duree de son existence et les limites de son domaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 27, 'C\'est lui qui leur a donne de le chercher pour voir si elles parviendraient a le trouver et a le toucher quoiqu\'il ne soit pas loin de chacun de nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 28, 'Car c\'est en lui que nous vivons, que nous nous mouvons, que nous existons, comme l\'ont dit quelques-uns de vos poetes : De sa race nous sommes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 29, 'Etant de la race de Dieu nous ne devons pas croire que la Divinite ressemble a l\'or, a l\'argent, a la pierre sculptes par l\'art et le genie de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 30, 'Oubliant donc des siecles d\'ignorance, Dieu maintenant invite partout tous les hommes a se repentir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 31, 'car il a fixe un jour ou il doit juger le monde avec justice, par un homme qu\'il a designe pour cela, et qu\'il a accredite aupres de tous en le ressuscitant d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 32, 'Quand ils entendirent parler de resurrection des morts, les uns se mirent a plaisanter, les autres dirent : Nous t\'ecouterons la-dessus une autre fois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 33, 'C\'est ainsi que Paul sortit de leur assemblee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 17, 34, 'Quelques hommes cependant se joignirent a lui et devinrent croyants, entre autres Denys, membre de l\'Areopage ; il y eut aussi une femme du nom de Damaris et d\'autres avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 1, 'Apres cela, Paul partit d\'Athenes et vint a Corinthe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 2, 'Il y trouva un Juif nomme Aquilas, originaire du Pont, et recemment arrive d\'Italie avec sa femme Priscille, chasse de Rome comme tous les Juifs par un edit de Claude. Il se reunit a eux');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 3, 'et comme ils etaient du meme metier que lui, il demeura chez eux pour y travailler. Ils etaient, de leur etat, fabricants de tentes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 4, 'Chaque sabbat il parlait a la synagogue, et il gagnait des Juifs et des Grecs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 5, 'Quand Silas et Timothee arriverent de Macedoine, Paul se consacrait uniquement a la predication, affirmant aux Juifs que Jesus etait le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 6, 'Mais ceux-ci s\'opposaient a lui et l\'insultaient ; alors, secouant sur eux la poussiere de ses vetements, il leur dit : Que votre sang retombe sur votre tete! J\'en suis innocent et desormais je m\'adresserai aux paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 7, 'Il sortit de la salle et se rendit chez un certain Titius Justus, homme craignant Dieu, dont la maison etait contigue a la synagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 8, 'Crispus, le chef de la synagogue, crut au Seigneur avec toute sa maison, et plusieurs des Corinthiens qui entendaient Paul devenaient croyants et se faisaient baptiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 9, 'Une nuit, le Seigneur dit a Paul dans une vision : Sois sans crainte, parle au contraire, ne cesse de parler, car je suis avec toi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 10, 'personne ne mettra la main sur toi pour te faire du mal, parce que j\'ai un grand peuple dans cette ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 11, 'Paul alors s\'etablit a Corinthe pendant un an et demi, et il y precha la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 12, 'Pendant le proconsulat de Gallion en Achaie, les Juifs, tous d\'accord, se souleverent contre Paul et l\'amenerent devant le tribunal :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 13, 'Cet homme, dirent-ils, excite les gens a rendre a Dieu un culte contraire a la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 14, 'Paul ouvrait la bouche pour repondre, quand Gallion, s\'adressant aux Juifs: S\'il s\'agissait de quelque crime ou de quelque mefait, leur dit-il, j\'accueillerais vos plaintes comme il convient, o Juifs!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 15, 'mais s\'il s\'agit de vos discussions sur une doctrine, sur des noms propres, sur votre Loi particuliere, regardez-y vous-memes ; je ne veux pas juger ces questions-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 16, 'Et il les renvoya du tribunal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 17, 'Tout le monde alors tomba sur Sosthenes, le chef de la synagogue, et l\'on se mit a le battre devant le tribunal. Gallion ne s\'en soucia nullement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 18, 'Paul resta encore longtemps a Corinthe ; puis il prit conge des freres et s\'embarqua pour la Syrie avec Priscille et Aquilas, apres s\'etre fait raser la tete a Kenchrees ; car il avait fait un voeu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 19, 'On debarqua a Ephese ; Paul y laissa ses compagnons et se rendit a la synagogue, ou il s\'entretint avec les Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 20, 'Mais quand ils lui demanderent de prolonger son sejour, il n\'y consentit pas');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 21, 'et leur dit en prenant conge d\'eux : Je reviendrai une autre fois chez vous, s\'il plait a Dieu. Il partit d\'Ephese,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 22, 'debarqua a Cesaree et, apres etre monte a Jerusalem pour saluer l\'Eglise, il descendit a Antioche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 23, 'Il en repartit apres un court sejour et traversa successivement le pays des Galates et la Phrygie pour fortifier tous les disciples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 24, 'Cependant un certain Juif, du nom d\'Apollos, ne a Alexandrie, un homme eloquent, arriva a Ephese ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 25, 'il connaissait a fond les Ecritures. On l\'avait instruit dans la doctrine du Seigneur ; c\'etait une ame ardente qui prechait et enseignait avec soin ce qui concerne Jesus, bien qu\'il ne connut que le bapteme de Jean.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 26, 'Quand il commenca a parler librement dans la synagogue, Priscilla et Aquilas qui l\'entendirent l\'emmenerent avec eux et lui exposerent plus exactement la doctrine de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 27, 'Lorsqu\'il se proposa de passer en Achaie, les freres l\'y encouragerent et ecrivirent aux disciples de lui faire bon accueil. Une fois arrive, il se rendit, par la grace de Dieu, tres utile aux croyants ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 18, 28, 'car il refutait vigoureusement les Juifs en public, leur demontrant par les Ecritures que Jesus est le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 1, 'Pendant qu\'Apollos etait a Corinthe, Paul, qui venait de parcourir les parties les plus elevees du pays, descendit a Ephese et y trouva quelques disciples. Il leur demanda : Avez-vous recu l\'Esprit saint, quand vous etes devenus croyants?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 2, ' Nous n\'avons pas entendu parler d\'un Esprit saint, lui repondirent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 3, 'Il reprit alors : Quel bapteme avez-vous donc recu?  Le bapteme de Jean, repliquerent-ils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 4, 'Mais Paul : Jean a baptise ; c\'etait un bapteme de repentance, et il disait au peuple de croire en celui qui devait venir apres lui, c\'est-a-dire en Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 5, 'Apprenant cela, ils furent baptises au nom du Seigneur Jesus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 6, 'Paul leur imposa les mains, et l\'Esprit saint descendit sur eux ; ils se mirent a parler en langues etrangeres et a prophetiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 7, 'Ils etaient douze en tout environ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 8, 'Paul se rendit a la synagogue, et il y precha en toute franchise sur le Royaume de Dieu. Pendant trois mois il s\'efforca, par ses discours, de convaincre les Juifs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 9, 'Mais quelques-uns resterent endurcis, incredules, et se mirent a decrier l\'enseignement de Paul devant la foule ; alors il se separa d\'eux, et tous les jours il reunit a part ses disciples dans un endroit qu\'on appelait : Ecole de Tyrannus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 10, 'Cela dura deux ans, de sorte que tous les habitants de l\'Asie, Juifs et Grecs, entendirent la parole du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 11, 'Dieu faisait par les mains de Paul des miracles extraordinaires ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 12, 'au point qu\'on mettait sur les malades le linge ou les vetements qu\'il avait portes, et ils etaient gueris de leurs maladies et delivres des mauvais Esprits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 13, 'Alors quelques exorcistes juifs ambulants essayerent d\'invoquer le nom du Seigneur Jesus sur ceux qui etaient au pouvoir des mauvais Esprits. Ils disaient : Je vous adjure par ce Jesus que preche Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 14, '(C\'etaient les sept fils d\'un archipretre juif, Skevas, qui faisaient cela.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 15, 'Mais le mauvais Esprit leur repondit : Je connais Jesus, et je sais qui est Paul ; et vous, qui etes-vous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 16, 'Et puis, sautant sur eux, l\'homme qui etait possede du mauvais Esprit en terrassa deux et eut si bien le dessus qu\'ils durent s\'enfuir de la maison sans vetements et tout blesses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 17, 'Le fait fut connu de tous les Juifs et de tous les Grecs qui habitaient Ephese, et tous furent frappes de stupeur ; on magnifiait le nom du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 18, 'Plusieurs de ceux qui etaient devenus croyants venaient confesser et reconnaitre leurs pratiques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 19, 'Ceux qui s\'etaient livres a la magie vinrent en foule, apporterent leurs livres et les brulerent publiquement. On en estima la valeur ; elle se trouva etre de cinquante mille pieces d\'argent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 20, 'C\'etait ainsi que la parole du Seigneur gagnait en force et en autorite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 21, 'Apres ces evenements, Paul forma le projet de se rendre a Jerusalem en traversant la Macedoine et l\'Achaie.  Quand j\'aurai fait cela, disait-il, il faudra aussi que je voie Rome.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 22, 'Puis il envoya en Macedoine deux de ses aides, Timothee et Eraste, mais lui-meme resta encore quelque temps en Asie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 23, 'Ce fut vers cette epoque que la diffusion de l\'Evangile fut l\'occasion de troubles assez serieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 24, 'Un orfevre, du nom de Demetrius, fabriquait des reductions en argent du Temple d\'Artemis. Ses ouvriers trouvaient a ce travail un profit considerable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 25, 'Il les reunit un jour, avec tous les ouvriers du meme etat, et leur dit : Ephesiens, Vous savez que cette industrie fait notre fortune,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 26, 'et vous voyez et entendez dire que non seulement a Ephese, mais dans presque toute l\'Asie, ce Paul a gagne et entraine une foule de gens, en disant que les dieux faits de main d\'homme ne sont pas de vrais dieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 27, 'Ce ne sont pas seulement nos interets qui vont etre en souffrance, c\'est le Temple meme de notre grande deesse Artemis qui ne compte plus pour rien, et bientot il ne sera plus question de la majeste de celle que toute l\'Asie, que le monde entier adore.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 28, 'Ces paroles les rendirent furieux et ils se mirent a crier: Vive la grande Artemis d\'Ephese!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 29, 'L\'agitation gagna la ville entiere. Toute la foule se porta au theatre en y entrainant Caius et Aristarque, les Macedoniens qui accompagnaient Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 30, 'Lui-meme voulait se montrer au peuple, mais les disciples le retinrent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 31, 'Il y eut meme des Asiarques de ses amis qui le firent prier de ne pas se presenter au theatre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 32, 'Chacun criait de son cote ; aussi l\'assemblee etait-elle tres confuse et le plus grand nombre ne savait pas meme pourquoi on s\'etait reuni.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 33, 'Enfin, on fit sortir de la foule un certain Alexandre que les Juifs voulaient mettre en avant. Cet Alexandre, ayant fait signe de la main, se preparait a prendre devant le peuple la defense des siens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 34, 'Mais la foule s\'apercut qu\'il etait juif, et alors elle se mit a crier d\'une seule voix pendant pres de deux heures : Vive la grande Artemis d\'Ephese !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 35, 'Le chancelier de la ville parvint enfin a l\'apaiser, et prononca ces paroles : Habitants d\'Ephese, Quel homme au monde ignore que votre ville d\'Ephese ne soit la gardienne du Temple de la grande Artemis et de sa statue tombee du ciel?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 36, 'Cela est incontestable ; il faut donc que vous restiez calmes et ne fassiez rien avec precipitation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 37, 'Vous avez amene la ces hommes, mais ils ne sont coupables ni de sacrilege ni de blaspheme envers votre deesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 38, 'Si Demetrius et ses ouvriers ont a se plaindre de quelqu\'un, il y a des tribunaux, il y a des proconsuls, qu\'on se fasse assigner!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 39, 'S\'il s\'agit d\'autres choses, on peut les examiner dans une assemblee legalement convoquee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 40, 'Autrement nous risquons d\'etre accuses de sedition pour ce qui s\'est passe aujourd\'hui, car nous n\'avons aucune bonne raison a donner pour expliquer notre rassemblement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 19, 41, 'La-dessus, il congedia l\'assemblee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 1, 'Quand la tranquillite fut retablie, Paul convoqua les disciples, leur fit ses adieux et partit pour la Macedoine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 2, 'Il parcourut cette province et, apres avoir longuement exhorte les fideles, il passa en Grece et s\'y arreta trois mois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 3, 'Les Juifs lui tendirent un guet-apens au moment ou il allait s\'embarquer pour la Syrie ; il se decida alors a retourner par la Macedoine,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 4, 'et il fut accompagne jusqu\'en Asie par Sopater, fils de Pyrrhus de Beree, par les Thessaloniciens Aristarque et Secundus, par Caius de Derbe et Timothee et par les Asiates Tychique et Trophime.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 5, 'Ces freres prirent les devants et nous attendirent a Troas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 6, 'Nous-memes nous partimes de Philippes apres les fetes de Paques et, apres une traversee de cinq jours, nous les rejoignimes a Troas, ou nous passames sept jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 7, 'Le premier jour de la semaine, comme nous etions reunis pour rompre ensemble le pain, Paul, qui voulait partir le lendemain, s\'entretint avec l\'assemblee et prolongea son discours jusqu\'a pres de minuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 8, 'Il y avait plusieurs lampes dans la chambre haute ou nous etions reunis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 9, 'Un jeune homme, nomme Eutychus, assis sur le bord de la fenetre, s\'endormit profondement pendant le long discours de Paul, et, emporte par le sommeil, il se laissa tomber du haut du troisieme etage en bas. Quand on le releva, il etait mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 10, 'Mais Paul descendit, se pencha sur lui, le prit dans ses bras : N\'ayez aucune inquietude, dit-il, son ame est encore en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 11, 'Ensuite il remonta, il rompit le pain et il mangea, puis il reprit longuement la parole jusqu\'au point du jour, ou il partit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 12, 'Quant au jeune garcon, on le ramena plein de vie ; ce fut pour tous une immense joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 13, 'Quant a nous, nous avions pris les devants ; nous nous etions embarques pour Assos, ou nous devions reprendre Paul ; c\'est lui qui l\'avait ainsi decide ; il preferait faire la route a pied.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 14, 'Quand il nous eut rejoints a Assos, nous le primes a notre bord, et nous nous rendimes a Mitylene.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 15, 'Partis de la, toujours par mer, nous passions le lendemain en vue de Chios, le surlendemain nous touchions a Samos, et enfin, le jour suivant, nous abordions a Milet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 16, 'Car Paul avait resolu de depasser Ephese sans s\'y arreter, afin de ne pas perdre de temps en Asie. Il se hatait pour etre a Jerusalem, si possible, le jour de la Pentecote.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 17, 'Cependant, de Milet il envoya a Ephese et fit appeler les Anciens de l\'Eglise ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 18, 'quand ils furent reunis aupres de lui, il leur dit : Vous savez, depuis le premier jour de mon arrivee en Asie, ce que j\'ai sans cesse ete pour vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 19, 'vous m\'avez vu servir le Seigneur avec une grande humilite, dans les larmes, et au milieu des epreuves qui me venaient des Juifs et de leurs embuches ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 20, 'vous savez que je ne vous ai rien cache de ce qui pouvait vous etre utile, que je vous ai preche, que je vous ai instruits en public et en particulier,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 21, 'conjurant Juifs et Grecs de se convertir a Dieu et de croire en notre Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 22, 'Et maintenant voila que, lie par l\'Esprit, je vais a Jerusalem ; je ne sais ce qui m\'y arrivera ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 23, 'je sais seulement que, de ville en ville, l\'Esprit saint m\'avertit et me declare que des chaines et des tribulations m\'y attendent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 24, 'Mais peu m\'importe ; je fais volontiers le sacrifice de ma vie, pourvu que j\'acheve ma course, que je termine le ministere que j\'ai recu du Seigneur Jesus : rendre temoignage a l\'Evangile de la grace de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 25, 'O vous tous, au milieu de qui j\'ai vecu, annoncant le Royaume, je sais que vous ne verrez plus mon visage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 26, 'Je proteste donc aujourd\'hui que je suis innocent de la perte de ceux qui periront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 27, 'Car je n\'ai rien neglige pour vous faire connaitre tout le conseil de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 28, 'Veillez sur vous-memes et sur tout le troupeau, au sein duquel l\'Esprit saint vous a places comme Surveillants ; paissez l\'Eglise de Dieu quil a acquise par son sang.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 29, 'Je sais qu\'apres mon depart des loups rapaces se glisseront parmi vous et n\'epargneront pas le troupeau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 30, 'Du milieu de vous se leveront des hommes prononcant des discours pervers pour entrainer les disciples a leur suite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 31, 'Veillez donc, vous souvenant que pendant trois annees, nuit et jour, je n\'ai cesse d\'exhorter chacun avec larmes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 32, 'Et maintenant je vous recommande a Dieu et a la parole de sa grace, a lui qui peut vous edifier et vous assurer un heritage parmi tous ceux qui lui sont consacres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 33, 'Je n\'ai desire ni l\'argent, ni l\'or, ni le vetement de personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 34, 'Vous savez que ces mains ont pourvu a mes besoins et a ceux de mes compagnons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 35, 'Je vous ai toujours montre comment il faut, par le travail, avoir encore de quoi secourir les indigents, et se souvenir des paroles du Seigneur Jesus quand il a dit : Il y a plus de bonheur a donner qu\'a recevoir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 36, 'Il parla ainsi, puis il se mit a genoux, et il pria avec eux tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 37, 'On n\'entendait qu\'un long sanglot.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 20, 38, 'Ils se jetaient a son cou et l\'embrassaient. C\'etait la parole de Paul : Vous ne verrez plus mon visage, qui les avait le plus affliges. Ils le conduisirent au bateau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 1, 'Nous etant enfin separes, nous reprimes la mer et marchant, vent arriere, nous arrivions bientot a Cos ; le lendemain nous etions a Rhodes, le troisieme jour a Patare.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 2, 'La nous trouvions un navire qui faisait voile pour la Phenicie et qui nous prit a son bord.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 3, 'Bientot l\'ile de Chypre etait en vue, mais, la laissant sur notre gauche, nous marchions sur la Syrie et nous arrivions a Tyr. C\'etait la que le navire devait laisser son chargement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 4, 'Nous allames voir les disciples et restames sept jours avec eux. Diriges par l\'Esprit, ils disaient a Paul de ne pas monter a Jerusalem ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 5, 'mais, notre sejour acheve, nous nous mimes en route pour le depart. Tous, avec leurs femmes et leurs enfants, nous conduisirent hors de la ville ; et la, agenouilles sur le sable du rivage, nous priames ensemble');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 6, 'et nous nous dimes adieu. Nous montames ensuite a bord et les freres retournerent chez eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 7, 'Notre traversee se termina par le trajet de Tyr a Ptolemaide ; nous allames y saluer les freres et restames un jour avec eux ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 8, 'le lendemain nous partions pour Cesaree. A notre arrivee, nous nous rendimes chez l\'evangeliste Philippe, qui etait l\'un des sept diacres. C\'est chez lui que nous demeurames.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 9, 'Il avait quatre filles non mariees, qui etaient prophetesses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 10, 'Nous y etions depuis plusieurs jours lorsque arriva de Judee un prophete du nom d\'Agabus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 11, 'Il vint a nous, prit la ceinture de Paul et, s\'attachant les pieds et les mains, il dit : Voici ce que declare l\'Esprit saint : L\'homme a qui appartient cette ceinture sera ainsi lie a Jerusalem par les Juifs et livre par eux aux mains des paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 12, 'A ces paroles les gens de l\'endroit et nous, suppliames Paul de ne pas monter a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 13, 'Mais il nous repondit : Que faites-vous la a pleurer et a me fendre le coeur? Je suis pret, quant a moi, non seulement a me laisser enchainer, mais a mourir a Jerusalem pour le nom du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 14, 'Comme nous ne pouvions le persuader, nous n\'insistames pas davantage et nous dimes : Que la volonte du Seigneur se fasse!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 15, 'Ces quelques jours passes, nous fimes nos preparatifs de depart, et nous montames a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 16, 'Quelques-uns des disciples de Cesaree nous y accompagnerent et nous conduisirent chez un ancien disciple, un Chypriote, nomme Mnason, qui devait nous donner l\'hospitalite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 17, 'Les freres, a notre arrivee a Jerusalem, nous firent un fort bon accueil.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 18, 'Le lendemain, Paul se rendit avec nous chez Jacques ; tous les Anciens s\'y etaient rassembles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 19, 'On se donna le salut de paix, et Paul raconta en detail ce que Dieu avait fait dans le monde paien par son ministere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 20, 'Les Anciens, l\'entendant, rendaient gloire a Dieu. Cependant ils lui dirent : Tu vois, frere, que les Juifs devenus croyants se comptent par milliers, et tous sont d\'ardents zelateurs de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 21, 'Or, on leur a raconte que tu preches l\'apostasie de la Loi de Moise a tous les Juifs dissemines parmi les paiens, les detournant de circoncire leurs enfants et de marcher selon les rites d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 22, 'Qu\'y a-t-il donc a faire? Il est de toute evidence qu\'une foule enorme se rassemblera, car on apprendra ton arrivee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 23, 'Fais ce que nous allons te dire : Nous avons ici quatre hommes qui ont contracte un voeu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 24, 'Prends-les, purifie-toi avec eux, supporte les frais pour qu\'ils puissent se faire raser la tete ; chacun saura alors que ce qu\'on a raconte de toi n\'est rien et que toi aussi tu pratiques la Loi et l\'observes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 25, 'Quant aux paiens devenus croyants, nous avons decide (et nous le leur avons fait dire) qu\'ils devaient s\'abstenir des viandes offertes en sacrifice aux dieux, du sang, des animaux morts par suffocation, et de la fornication.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 26, 'Paul alors se mit en la compagnie de ces hommes et des le lendemain il se purifia avec eux, puis il entra dans le Temple pour indiquer d\'avance le jour ou les rites de purification seraient acheves et ou chacun d\'eux ferait offrir le sacrifice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 27, 'Les sept jours allaient expirer, quand les Juifs d\'Asie l\'apercurent dans le Temple, ameuterent toute la foule et mirent la main sur lui :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 28, 'Au secours! enfants d\'Israel! crierent-ils, le voila, l\'homme qui declame partout, devant tout le monde, contre le peuple, contre la Loi, contre ce saint Lieu! Il vient de plus d\'introduire des paiens dans le Temple ; c\'est un profanateur du sanctuaire!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 29, ' On avait vu, en effet, Trophime d\'Ephese dans la ville avec lui, et on croyait que Paul l\'avait introduit dans le Temple. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 30, 'Toute la ville fut bientot en emoi ; le peuple accourut de tous, cotes, on s\'empara de Paul, on l\'entraina hors du Temple, dont les portes furent immediatement fermees,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 31, 'et on se mettait en devoir de le tuer, lorsque le tribun de la cohorte fut informe que Jerusalem tout entiere etait en emeute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 32, 'Il prit immediatement quelques soldats et centurions et descendit en courant vers les Juifs, qui, voyant le tribun et les soldats, cesserent de frapper Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 33, 'Le tribun s\'approcha, le fit saisir, donna ordre de le lier de deux chaines et demanda qui il etait et ce qu\'il faisait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 34, 'Mais les bruits les plus divers se croisaient dans la foule et le tumulte l\'empechant de rien savoir de positif, il donna ordre de mener Paul a la Forteresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 35, 'Quand ils furent sur l\'escalier, la presse etait telle que les soldats furent obliges de le porter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 36, 'Une foule enorme le suivait en criant : A mort !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 37, 'Au moment d\'entrer dans la Forteresse, Paul dit au tribun : M\'est-il permis de te parler?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 38, ' Tu sais le grec? lui repondit-il, tu n\'es donc pas le Juif d\'Egypte qui a dernierement souleve et entraine avec lui au desert quatre mille sicaires?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 39, 'Paul repartit : Je suis un Juif de Tarse, je suis citoyen d\'une ville importante de Cilicie, permets-moi, je te prie, de parler au peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 21, 40, 'Le tribun le lui permit. Paul alors, debout sur les marches de l\'escalier, fit signe au peuple de la main. Un grand silence s\'etablit et il prononca en langue hebraique les paroles suivantes:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 1, 'Mes freres et mes peres,, Ecoutez ce que j\'ai maintenant a vous dire pour me justifier');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 2, ' (quand on l\'entendit parler en hebreu, on redoubla d\'attention) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 3, 'il continua : Je suis un Juif, ne a Tarse, en Cilicie, mais j\'ai ete eleve dans cette ville-ci, j\'ai fait mes etudes aux pieds de Gamaliel, dans toute la rigueur de la Loi de nos peres ; j\'ai ete plein de zele pour la cause de Dieu, comme vous l\'etes tous aujourd\'hui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 4, 'Cette secte, je l\'ai persecutee a mort ; j\'ai charge de chaines, j\'ai jete en prison hommes et femmes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 5, 'comme peuvent l\'attester le grand-pretre et le Sanhedrin tout entier. Car c\'est d\'eux que j\'ai recu des lettres pour nos freres de Damas, et j\'y allai pour amener prisonniers a Jerusalem ceux que j\'y trouverais et les faire punir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 6, 'J\'etais en chemin et j\'approchais de Damas, quand tout a coup, vers midi, une vive lumiere venue du ciel brilla autour de moi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 7, 'je tombai par terre et j\'entendis une voix qui me disait : Saul, Saul, pourquoi me persecutes-tu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 8, ' Je repondis : Qui es-tu, Seigneur?  La voix me dit : Je suis Jesus de Nazareth que tu persecutes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 9, ' Ceux qui etaient avec moi avaient vu la lumiere, mais n\'avaient pas entendu la voix de celui qui me parlait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 10, 'Je dis alors : Que dois-je faire, Seigneur? et le Seigneur me repondit : Releve-toi, rends-toi a Damas, et la on te dira tout ce que tu as l\'ordre de faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 11, ' Comme je n\'y voyais pas (la lumiere m\'avait ebloui), mes compagnons me prirent par la main et me menerent a Damas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 12, 'Un certain Hananias, homme pieux et fidele a la Loi, estime de tous les residents juifs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 13, 'vint se presenter a moi et me dit : Saul, mon frere, regarde,  et immediatement je le vis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 14, 'Puis il me dit : Le Dieu de nos peres t\'a predestine a connaitre sa volonte, a voir le Juste et a entendre les paroles de sa bouche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 15, 'car tu seras son temoin aupres de tous les hommes, temoin de ce que tu as vu et entendu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 16, 'Et maintenant pourquoi tarderais-tu? Leve-toi, recois le bapteme et purifie-toi de tes peches en invoquant son nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 17, 'De retour a Jerusalem, comme j\'etais en priere au Temple, je tombai en extase,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 18, 'et je le vis qui me disait : Hate-toi de partir, sors au plus vite de Jerusalem, car ils n\'accepteront pas le temoignage que tu me rends.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 19, 'Et je lui dis : Seigneur, il savent eux-memes que j\'ai fait emprisonner et flageller dans les synagogues ceux qui croient en toi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 20, 'et quand ils ont verse le sang, d\'Etienne, ton temoin, j\'etais la, present, j\'applaudissais, je gardais les vetements des bourreaux ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 21, 'et il me dit : Va, car je t\'enverrai au loin vers les paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 22, 'On l\'avait ecoute jusque-la, mais, a ce mot, les clameurs recommencerent: A mort, cet homme! il ne faut pas lui laisser la vie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 23, 'Ils vociferaient, otaient leurs vetements, faisaient voler la poussiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 24, 'Le tribun commanda alors de le faire entrer dans la Forteresse et de le mettre a la question pour savoir la vraie cause de tous ces cris contre lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 25, 'Comme on l\'avait deja attache au poteau pour recevoir les coups de lanieres, Paul dit au centurion qui commandait: Vous est-il permis de flageller un citoyen romain qui n\'est pas meme condamne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 26, 'A ces paroles, le centurion alla en referer au tribun : Que vas-tu faire? lui dit-il, cet homme est citoyen romain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 27, 'Le tribun vint a Paul et lui dit: Reponds-moi, es-tu citoyen romain?  Oui, lui dit Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 28, 'Le tribun repartit : Moi, j\'ai depense une somme considerable pour avoir ce titre.  Et moi, lui repliqua Paul, je l\'ai par ma naissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 29, 'Les executeurs s\'ecarterent immediatement, et le tribun commenca a avoir peur, voyant qu\'il avait fait mettre aux fers un citoyen romain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 22, 30, 'Le lendemain, voulant savoir positivement quels griefs articulaient les Juifs, il lui fit oter les fers et donna ordre de convoquer le haut sacerdoce et le Sanhedrin en entier ; puis il amena Paul et le fit comparaitre devant eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 1, 'Paul, regardant en face le Sanhedrin, dit: Freres, C\'est en toute bonne conscience que je me suis conduit devant Dieu jusqu\'a aujourd\'hui...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 2, 'Le grand-pretre Hananias ordonna alors aux assistants de souffleter Paul sur la bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 3, 'Dieu te frappera a ton tour, muraille blanchie! repondit Paul en se tournant vers lui, tu sieges pour me juger selon la Loi, et c\'est au mepris de la Loi que tu ordonnes de me frapper!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 4, ' Quoi! tu injuries le grand-pretre de Dieu! dirent les assistants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 5, 'Mais Paul : Je ne savais pas, freres, que ce fut le grand-pretre, car il est ecrit : Tu n\'insulteras pas le chef de ton peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 6, 'Paul, sachant que l\'assemblee etait composee en partie de Sadduceens, en partie de Pharisiens, s\'ecria en plein Sanhedrin : Freres, je suis Pharisien, fils de Pharisiens, c\'est pour mon esperance, c\'est pour la resurrection des morts qu\'on me met en jugement !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 7, 'Ces paroles provoquerent une scission entre les Pharisiens et les Sadduceens et l\'assemblee se divisa.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 8, 'En effet, les Sadduceens nient la resurrection, l\'existence des anges et des Esprits ; les Pharisiens admettent tout cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 9, 'Il s\'ensuivit une discussion tumultueuse ; certains Scribes du parti pharisien se leverent, prirent la defense de Paul et dirent : Nous ne trouvons rien a reprocher a cet homme... Si un Esprit lui a parle! un ange, peut-etre !...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 10, 'La dispute s\'envenimait, et le tribun craignait que Paul ne fut mis en pieces ; il donna ordre a une escouade de soldats de descendre dans la salle, d\'arracher Paul de leurs mains et de le reconduire a la Forteresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 11, 'La nuit suivante, le Seigneur lui apparut et lui dit : Courage! comme tu as ete mon temoin a Jerusalem, il faut aussi que tu sois mon temoin a Rome!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 12, 'Le lendemain matin, les Juifs formerent entre eux un complot et s\'obligerent par voeu a ne manger ni boire tant qu\'ils n\'auraient pas tue Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 13, 'Ils etaient plus de quarante a avoir fait cette conjuration.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 14, 'Ils allerent trouver les chefs des pretres et les Anciens et leur dirent : Nous nous sommes obliges par voeu, sous les plus terribles anathemes, a ne rien manger que nous n\'ayons tue Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 15, 'Intervenez donc, avec le Sanhedrin, aupres du tribun pour obtenir une nouvelle comparution, comme si vous vouliez etudier plus a fond son affaire. Quant a nous, nous sommes prets ; nous le tuerons dans le trajet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 16, 'Mais le fils de la soeur de Paul entendit parler de ce guet-apens, se rendit a la Forteresse, y entra et revela tout a Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 17, 'Celui-ci fit alors appeler un des centurions : Mene ce jeune homme au tribun, lui dit-il, il a quelque chose a lui communiquer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 18, 'Le centurion l\'emmena donc chez le tribun et lui dit : Le prisonnier Paul m\'a fait appeler et m\'a prie de t\'amener ce jeune homme, qui a quelque chose a te dire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 19, 'Le tribun prit le jeune homme par la main, le conduisit a l\'ecart et lui demanda : Qu\'as-tu a me communiquer?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 20, ' Les Juifs, repondit-il, sont convenus de te prier de faire comparaitre Paul demain devant le Sanhedrin, comme s\'ils voulaient etudier plus a fond son affaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 21, 'Garde-toi de le faire ; c\'est un guet-apens ; ils sont plus de quarante qui se sont obliges par voeu a ne manger ni boire avant de l\'avoir tue. Ils sont tout prets ; ils n\'attendent que ton consentement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 22, 'Le tribun renvoya le jeune homme en lui defendant de parler a personne de ce qu\'il venait de lui apprendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 23, 'Puis il fit appeler deux centurions : Tenez prets, leur dit-il, a partir de la troisieme heure de la nuit, deux cents soldats, soixante-dix cavaliers et deux cents hommes du train pour aller jusqu\'a Cesaree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 24, 'Il commanda aussi de se pourvoir de montures pour Paul, afin de le mener sain et sauf au procurateur Felix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 25, 'En meme temps il ecrivit a celui-ci une lettre, dont voici la teneur :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 26, 'Claudius Lysias a son excellence le procurateur Felix. Salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 27, 'Cet homme a ete arrete par les Juifs, et ils allaient le tuer, quand j\'arrivai avec la troupe et le leur enlevai ; j\'avais appris qu\'il etait citoyen romain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 28, 'Voulant connaitre le motif de leurs accusations, je le conduisis au Sanhedrin ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 29, 'voici ce que je trouvai : des poursuites sur des questions relatives a leur Loi et aucun grief qui meritat la mort ou la prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 30, 'Mais on m\'a denonce un complot contre cet homme ; je te l\'ai donc envoye sans retard et j\'ai prevenu les accusateurs qu\'ils eussent aussi a prendre la parole contre lui devant toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 31, 'Les soldats executerent ces ordres ; ils prirent Paul et le conduisirent de nuit jusqu\'a Antipatris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 32, 'Le lendemain, ils laisserent les cavaliers continuer le chemin avec lui et rentrerent a la Forteresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 33, 'Ceux-la, arrives a Cesaree, remirent la lettre au procurateur et lui presenterent Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 34, 'Il la lut et s\'informa de quel pays il etait. Apprenant qu\'il etait de Cilicie :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 23, 35, 'Je t\'entendrai, dit-il, quand tes accusateurs seront arrives. Puis il ordonna de le garder dans le pretoire d\'Herode.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 1, 'Cinq jours apres arriva le grand-pretre Hananias, accompagne de quelques Anciens et d\'un avocat, un certain Tertullus ; ils vinrent denoncer Paul au procurateur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 2, 'On le fit appeler, et Tertullus commenca son accusation en ces termes:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 3, 'Excellent Felix, Nous jouissons d\'une paix profonde, grace a toi, grace aussi, aux reformes faites au sein de ce peuple, et dues a ta sollicitude en tout et partout ; nous en eprouvons une bien vive reconnaissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 4, 'Mais je ne veux pas te retenir trop longtemps ; je te prie seulement de nous ecouter un instant avec ta bonte habituelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 5, 'Cet homme est une peste ; il excite des seditions parmi les Juifs dans le monde entier ; il est chef de l\'heresie des Nazareens ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 6, 'nous l\'avons trouve essayant de profaner le Temple ; alors nous l\'avons arrete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 7, '(Nous avons voulu le juger selon notre propre Loi : mais le tribun Lysias, etant survenu, l\'a arrache de nos mains avec une grande violence, en ordonnant a ses accusateurs de venir devant toi.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 8, 'Tu peux, en l\'interrogeant sur ces faits, apprendre de sa bouche tout ce dont nous l\'accusons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 9, 'Les Juifs s\'associerent a ces paroles et declarerent qu\'elles etaient exactes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 10, 'Sur un signe du procurateur, Paul prit ensuite la parole : Voila plusieurs annees que tu administres ce peuple, je le sais ; aussi est-ce avec une grande confiance que je viens me defendre moi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 11, 'Tu peux constater:  qu\'il n\'y a pas plus de douze jours que je suis arrive a Jerusalem pour y adorer ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 12, '- que personne ne m\'a vu discuter avec quelqu\'un dans le Temple ;  que je n\'y ai point fait d\'attroupement ;  pas davantage dans les synagogues ou dans la ville ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 13, ' et qu\'ils ne peuvent pas prouver ce dont ils m\'accusent maintenant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 14, 'Mais voici ce que je t\'avoue : Il y a une doctrine qu\'ils appellent une heresie, et c\'est en la suivant :  que j\'adore le Dieu de mes peres ;  que je crois a tout ce qui est ecrit dans da Loi et les Prophetes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 15, ' que j\'ai en Dieu l\'esperance (et eux aussi ils la partagent) qu\'il y aura une resurrection des justes et des injustes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 16, 'Voila pourquoi je m\'efforce moi-meme d\'avoir en tout temps une conscience irreprochable devant Dieu et devant les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 17, 'Apres une absence de plusieurs annees, je suis venu faire a mon peuple des aumones, lui porter des offrandes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 18, 'C\'est alors que certains Juifs d\'Asie m\'ont trouve dans le Temple, occupe a des purifications, sans le moindre attroupement, sans le moindre bruit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 19, 'Ils auraient du se presenter devant toi et m\'accuser, s\'ils avaient un reproche a me faire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 20, 'ou bien, que ceux ici presents disent de quel mefait ils m\'ont trouve coupable quand j\'ai comparu devant le Sanhedrin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 21, 'Serait-ce de leur avoir dit ces mots, les seuls que je leur ai adresses : C\'est la question de la resurrection des morts qui me fait juger aujourd\'hui par vous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 22, 'Felix, qui savait assez exactement de quelle doctrine il s\'agissait, ajourna la cause, en disant : Quand Lysias, le tribun, sera ici, j\'etudierai votre affaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 23, 'En attendant, il ordonna au centurion de garder Paul, mais de le traiter avec douceur et de n\'empecher aucun des siens de lui rendre des services.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 24, 'Quelques jours apres, Felix vint avec sa femme Drusille, qui etait juive. Il fit appeler Paul et l\'entendit parler de la foi au Christ Jesus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 25, 'et comme il traitait de la justice, de la temperance, du jugement a venir, Felix eut peur : En voila assez pour le moment, dit-il, je te ferai rappeler a la premiere occasion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 26, 'En outre, il esperait que Paul lui donnerait de l\'argent, et, dans ce but, il le faisait venir assez souvent et s\'entretenait avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 24, 27, 'Deux annees se passerent ainsi ; Felix eut pour successeur Porcius Festus, et comme il voulait faire plaisir aux Juifs, il laissa Paul en prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 1, 'Trois jours apres son arrivee dans sa province, Festus monta de Cesaree a Jerusalem.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 2, 'La, le haut sacerdoce et les autorites juives porterent plainte contre Paul ; ils lui demanderent avec instance et comme une faveur (mais dans un but hostile) de le faire revenir a Jerusalem ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 3, 'ils auraient dresse une embuscade pour le tuer dans le trajet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 4, 'Festus repondit que Paul resterait prisonnier a Cesaree et que lui-meme y retournerait bientot.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 5, 'Que les plus notables d\'entre vous, ajouta-t-il, qui voudraient charger cet homme descendent avec moi et formulent leur accusation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 6, 'Il ne resta avec eux que peu de jours, huit ou dix, puis retourna a Cesaree. Des le lendemain, il siegea sur son tribunal et fit comparaitre Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 7, 'A son entree, les Juifs, descendus de Jerusalem, l\'entourerent, en formulant de nombreuses et graves accusations qu\'ils ne pouvaient prouver ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 8, 'tandis que Paul se defendait, en declarant qu\'il n\'avait commis aucune faute ni contre la Loi juive, ni contre le Temple, ni contre l\'Empereur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 9, 'La-dessus, Festus, qui voulait faire plaisir aux Juifs, dit a Paul : Veux-tu monter a Jerusalem et y etre juge sur tout cela en ma presence?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 10, 'Paul lui repondit: C\'est devant le tribunal de l\'Empereur que je comparais ; c\'est la que je dois etre juge ; je n\'ai fait aucun tort aux Juifs, et tu le sais fort bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 11, 'Si je suis coupable, si j\'ai merite la mort, je ne demande pas de grace ; si, au contraire, je n\'ai rien fait de ce dont ils m\'accusent, personne n\'a le droit de me livrer a eux par complaisance. J\'en appelle a l\'Empereur !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 12, 'Festus, alors, s\'entretint un moment avec ses assesseurs et repondit : Tu en as appele a l\'Empereur, tu iras a l\'Empereur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 13, 'Quelques jours plus tard, le roi Agrippa et Berenice vinrent a Cesaree saluer Festus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 14, 'Ils y resterent plusieurs jours et Festus exposa au roi l\'affaire de Paul. Il y a ici un homme, lui dit-il, que Felix a laisse prisonnier ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 15, 'pendant mon sejour a Jerusalem, le haut sacerdoce et les autorites juives ont porte plainte contre lui et m\'ont demande sa condamnation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 16, 'Je leur ai repondu que la coutume romaine ne permet pas de livrer un homme par complaisance, sans que l\'accuse ait ete confronte avec ses accusateurs et ait eu toute facilite de se justifier de l\'accusation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 17, 'Ils sont alors venus ici et, sans perdre un moment, des le lendemain, je siegeai sur mon tribunal et je fis comparaitre cet homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 18, 'Les accusateurs qui se sont presentes n\'ont releve contre lui aucun des crimes que je m\'attendais a voir etablir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 19, 'il ne s\'est agi entre eux que de subtilites relatives a leurs croyances particulieres et d\'un certain Jesus qui est mort et que Paul dit etre vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 20, 'Ne sachant quel parti prendre dans une discussion de ce genre, j\'ai demande a Paul s\'il voulait aller a Jerusalem et y etre juge sur tout cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 21, 'Mais il en a appele pour que sa cause fut reservee au jugement de Sa Majeste ; j\'ai alors ordonne de le garder en prison jusqu\'a ce que je l\'envoie a l\'Empereur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 22, 'Agrippa dit a Festus : Je voulais justement entendre cet homme.  Tu l\'entendras demain, repondit Festus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 23, 'Le lendemain, en effet, Agrippa et Berenice vinrent avec une suite brillante et se rendirent a la salle d\'audience, accompagnes des officiers de l\'armee et des principaux personnages de la ville. Sur l\'ordre de Festus, Paul fut introduit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 24, 'Festus prit la parole : Roi Agrippa, et vous tous ici presents, Vous voyez cet homme a propos duquel tous les Juifs, en masse, et a Jerusalem et ici, sont venus m\'importuner ; ils me crient que je ne dois pas lui laisser la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 25, 'Moi j\'ai compris qu\'il n\'avait rien fait qui meritat la mort ; lui-meme, d\'ailleurs, en a appele a l\'Empereur et j\'ai decide de le lui envoyer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 26, 'Mais je n\'ai rien de bien positif a ecrire a son sujet au Maitre ; voila pourquoi je l\'ai fait paraitre devant vous, et surtout devant toi, Roi Agrippa, et, apres cet interrogatoire, j\'aurai quelque chose a ecrire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 25, 27, 'En effet, je trouve absurde d\'envoyer un prisonnier sans indiquer les charges qui pesent sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 1, ' Je te donne la parole pour ta defense, dit Agrippa a Paul. Celui-ci fit alors un geste de la main et se defendit en ces termes :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 2, 'Roi Agrippa, Je m\'estime heureux d\'avoir aujourd\'hui a me disculper devant toi de tout ce dont m\'accusent les Juifs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 3, 'car tu connais parfaitement tous les usages de ce peuple et les questions qui le preoccupent. Je te prie donc de m\'ecouter patiemment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 4, 'Ce qu\'a ete des l\'origine ma conduite, pendant ma jeunesse, au milieu de mon peuple, a Jerusalem, tous les Juifs le savent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 5, 'ils me connaissent d\'ancienne date, pour avoir appartenu, s\'ils veulent bien en convenir, a la tendance la plus stricte de notre religion ; je veux dire que j\'etais Pharisien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 6, 'Et maintenant me voila mis en cause pour avoir espere en la promesse faite par Dieu a nos peres');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 7, 'et que nos douze tribus, priant avec instance, nuit et jour, esperent voir s\'accomplir. Et c\'est pour cette esperance-la, o Roi, que des Juifs m\'accusent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 8, 'Est-ce que vous trouvez incroyable que Dieu ressuscite des morts?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 9, 'Pour moi, j\'avais cru devoir m\'opposer de toutes mes forces au nom de Jesus de Nazareth ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 10, 'et c\'est ce que j\'ai fait a Jerusalem. J\'ai fait jeter en prison un grand nombre de fideles, m\'y etant fait autoriser par les chefs des pretres, et quand on les mettait a mort, j\'y souscrivais entierement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 11, 'Il m\'arrivait dans toutes les synagogues de sevir contre eux et de les forcer a blasphemer. J\'etais tellement furieux contre eux que je les persecutais jusque dans les villes du dehors.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 12, 'Dans ce but, je partis pour Damas avec de pleins pouvoirs et une autorisation delivree par les chefs des pretres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 13, 'lorsque, en plein jour, sur la route, je vis, Roi, une lumiere venant du ciel, plus brillante que le soleil et nous enveloppant de son eclat, mes compagnons de voyage et moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 14, 'Tous nous tombames par terre et j\'entendis une voix qui me disait en hebreu : Saul, Saul, pourquoi me persecutes-tu? Il t\'est difficile de regimber contre l\'aiguillon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 15, 'Je repondis : Qui es-tu, Seigneur? et le Seigneur dit : Je suis Jesus que tu persecutes ; mais releve-toi, tiens-toi debout ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 16, 'voici pourquoi je te suis apparu : pour t\'elire serviteur et temoin de ce que tu viens de voir et de ce que tu verras encore ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 17, 'je te protegerai contre ce peuple et contre les paiens auxquels je t\'envoie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 18, 'pour leur ouvrir les yeux, pour les convertir des tenebres a la lumiere, de la puissance de Satan a Dieu, pour leur obtenir, par la foi en moi, le pardon des peches et une part de l\'heritage promis a ceux qui lui sont consacres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 19, 'Et alors, Roi Agrippa, je n\'ai pas voulu desobeir a cette vision celeste ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 20, 'mais a ceux de Damas d\'abord, ensuite a ceux de Jerusalem, puis a tous les habitants de la Judee et aux paiens, j\'ai preche de se repentir, de se convertir a Dieu, de faire des oeuvres dignes de la repentance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 21, 'Voila pourquoi les Juifs se sont empares de moi dans le Temple et ont essaye de me tuer sur place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 22, 'Mais, grace a la protection de Dieu, j\'ai vecu jusqu\'a aujourd\'hui, rendant temoignage aux petits et aux grands, sans rien dire en dehors de ce que Moise et les Prophetes ont predit devoir arriver ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 23, 'savoir que le Christ devait souffrir, et qu\'etant le premier des ressuscites, il devait annoncer la lumiere au peuple et aux paiens');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 24, 'Paul en etait la de sa defense quand Festus dit a haute voix :, Tu es fou, Paul ; tes longues etudes t\'ont fait perdre l\'esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 25, 'Mais Paul : Je ne suis pas fou, Excellent Festus, ce sont des paroles vraies et sensees que je prononce.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 26, 'Le roi connait ces faits, aussi je m\'adresse a lui avec confiance, car je suis persuade qu\'il n\'en ignore aucun ; tout cela ne s\'est pas fait en cachette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 27, 'Crois-tu aux prophetes, Roi Agrippa? Je sais que tu y crois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 28, 'Agrippa dit alors a Paul : Tu vas me persuader bientot de me faire chretien !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 29, 'Et Paul: Dieu veuille que tot ou tard non seulement toi, mais tous ceux qui m\'ecoutent aujourd\'hui en viennent a me ressembler... excepte par ces chaines!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 30, 'Tous se leverent, le roi, le procurateur, Berenice, l\'assemblee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 31, 'En se retirant, ils s\'entretenaient ensemble et se disaient que cet homme ne faisait rien qui meritat la mort ou la prison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 26, 32, 'On aurait pu relacher cet homme, dit Agrippa a Festus, s\'il n\'en avait pas appele a l\'Empereur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 1, 'Lorsqu\'il fut decide que nous partirions pour l\'Italie, on remit Paul et quelques autres prisonniers a un centurion de la cohorte Augusta, nomme Julius.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 2, 'Montes a bord d\'un navire d\'Adramyttium qui devait faire escale dans les ports de la province d\'Asie, nous primes la mer ; un Macedonien de Thessalonique, Aristarque, etait avec nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 3, 'Le second jour nous arrivions a Sidon, et Julius, qui traitait Paul avec beaucoup de douceur, lui permit d\'aller visiter ses amis et de recevoir leurs soins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 4, 'Partis de la, nous suivimes les cotes de l\'ile de Chypre, parce que les vents etaient contraires,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 5, 'et, apres avoir traverse la mer qui baigne la Cilicie et la Pamphylie, nous abordames a Myrra en Lycie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 6, 'La le centurion, ayant trouve un navire alexandrin qui faisait voile pour l\'Italie, nous fit monter a son bord.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 7, 'Apres plusieurs jours d\'une navigation tres lente, nous arrivions a grand\'peine a la hauteur de Cnide. Le vent ne nous permettant pas d\'aborder, nous suivimes les cotes de l\'ile de Crete dans la direction du cap Salmone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 8, 'Apres l\'avoir double, non sans difficulte, nous arrivames a un endroit appele Les Bons-Ports, pres de la ville de Lasa.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 9, 'Il s\'etait ecoule un temps considerable, et la navigation devenait dangereuse, l\'epoque du Grand Jeune etant deja passee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 10, 'Paul alors donna son avis. Je prevois, dit-il, de serieuses avaries et de grands dangers, non seulement pour la cargaison et pour le bateau, mais pour nos personnes memes, si nous continuons notre voyage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 11, 'Mais le centurion avait plus de confiance en ce que disaient le capitaine et le pilote qu\'en ce que disait Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 12, 'D\'ailleurs le port n\'etait pas bon pour hiverner, et l\'avis general fut d\'en repartir et de tacher de gagner, pour y passer l\'hiver, Phenix, port de Crete, expose au sud-ouest et au nord-ouest.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 13, 'Comme il soufflait une brise du sud, on crut le moment favorable, on leva l\'ancre et on se mit a longer de pres les cotes de Crete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 14, 'Mais, tout a coup, un des ouragans, appeles Euraquilon, vint s\'abattre sur l\'ile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 15, 'Le navire entraine fut hors d\'etat de tenir tete au vent ; on ceda ; on fut emporte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 16, 'Comme nous passions sous une petite ile appelee Clande, nous parvinmes, mais non sans peine, a manoeuvrer la chaloupe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 17, 'On s\'en servit pour prendre quelques precautions et entourer le navire de cables. Puis, comme on craignait d\'etre jete sur les Syrtes, on plia les vergues et on s\'abandonna au vent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 18, 'Le second jour, la tempete etait toujours aussi forte, et on, jeta a la mer tout le chargement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 19, 'le troisieme, nous nous debarrassions nous-memes du mobilier du navire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 20, 'On ne vit pas le soleil, on n\'apercut pas une seule etoile pendant plusieurs jours, et la tempete restant toujours aussi affreuse, tout espoir de salut nous fut des lors interdit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 21, 'Comme depuis longtemps personne n\'avait pris de nourriture, Paul parut au milieu de tous et dit : Il aurait fallu ecouter mon conseil et ne pas partir de l\'ile de Crete ; vous auriez evite ce desastre et cette perte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 22, 'Mais maintenant je vous invite a prendre courage ; aucun de vous ne perira ; le navire seul sera perdu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 23, 'Car cette nuit m\'est apparu un ange du Dieu auquel j\'appartiens et que j\'adore,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 24, 'et il m\'a dit : Ne crains rien, Paul! tu dois comparaitre devant l\'Empereur, et Dieu t\'accorde la vie de tous ceux qui naviguent avec toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 25, 'Ayez donc bon courage ; car j\'ai cette foi en Dieu, qu\'il en sera comme il m\'a ete dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 26, 'Il faut que nous soyons jetes sur une ile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 27, 'La quatorzieme nuit que nous etions ainsi ballottes sur l\'Adriatique, vers le milieu de cette nuit, les matelots crurent au voisinage de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 28, 'Ils jeterent la sonde, et trouverent vingt brasses ; un peu apres ils la jeterent encore, et trouverent quinze brasses :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 29, 'ils craignirent d\'aller donner sur des recifs ; quatre ancres furent alors jetees de la poupe et chacun attendit le jour avec anxiete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 30, 'Comme les matelots cherchaient a s\'echapper du navire et mettaient la chaloupe a la mer sous pretexte de jeter les ancres de l\'avant,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 31, 'Paul dit au centurion et aux soldats : Si ces hommes ne restent pas a bord vous ne pouvez etre sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 32, 'Les soldats couperent alors les cordes de la chaloupe et la laisserent tomber.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 33, 'Paul, en attendant le jour, conseilla a tous de prendre de la nourriture. C\'est aujourd\'hui le quatorzieme jour, dit-il, que vous passez dans l\'attente, a jeun, sans rien prendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 34, 'Je vous conseille donc de manger ; cela est necessaire a votre salut ; aucun de vous ne perdra un cheveu de sa tete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 35, 'En disant cela, il prit du pain, le rompit, en rendant graces a Dieu devant tous, et se mit a manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 36, 'Tous reprirent alors courage et mangerent aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 37, 'Nous etions en tout deux cent soixante-seize a bord');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 38, 'Quand on eut fini, on allegea encore le navire en jetant tout le ble a la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 39, 'Lorsque le jour parut, personne ne reconnut la terre, mais on entrevoyait une baie avec une plage et on resolut d\'essayer d\'y mettre le navire a l\'abri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 40, 'On coupa donc les cables des ancres qu\'on laissa se perdre dans la mer, on lacha les amarres des gouvernails, on hissa la voile d\'artimon qu\'on offrit au vent et on gouverna vers la plage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 41, 'On tomba sur une langue de terre battue des deux cotes par la mer ; la, le navire echoua. La proue, qui s\'etait enfoncee dans le sable, resta immobile ; la poupe, au contraire, se disloquait a chaque coup de mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 42, 'Les soldats proposerent alors de tuer les prisonniers de peur qu\'ils ne s\'echappassent a la nage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 43, 'Mais le centurion, qui voulait sauver Paul, les empecha de le faire ; il ordonna a ceux qui savaient nager de se jeter les premiers a l\'eau et de gagner la terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 27, 44, 'aux autres de se mettre sur des planches, sur des epaves de toutes sortes ; et c\'est ainsi que tous reussirent a se sauver a terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 1, 'Une fois hors de danger, nous apprimes que cette ile s\'appelait Malte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 2, 'Les indigenes nous accueillirent avec une humanite peu ordinaire ; ils allumerent un grand feu et nous y reunirent tous, car la pluie tombait, et il faisait froid.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 3, 'Comme Paul prenait une poignee de bois sec et la jetait dans le brasier, la chaleur en fit sortir une vipere qui s\'attacha a sa main.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 4, 'Quand les indigenes virent l\'animal qui pendait a sa main, ils se dirent entre eux : Certainement c\'est un meurtrier que cet homme ; a peine echappe-t-il a la mer et la Justice divine ne veut pas lui laisser la vie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 5, 'Mais lui secoua l\'animal dans le feu et ne ressentit aucun mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 6, 'Eux s\'attendaient a le voir enfler ou tomber mort tout a coup. Pendant longtemps ils attendirent, et voyant qu\'il ne lui arrivait rien de facheux, ils changerent d\'avis et declarerent qu\'il etait un dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 7, 'Il y avait, pres de cet endroit-la, des terres appartenant au principal personnage de l\'ile, un certain Publius ; il nous recueillit et nous traita pendant trois jours avec beaucoup d\'hospitalite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 8, 'Or le pere de ce Publius etait retenu au lit par des acces de fievre et la dysenterie. Paul entra chez lui, fit la priere, lui imposa les mains et le guerit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 9, 'La-dessus, tous les autres habitants malades de l\'ile vinrent aussi et furent gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 10, 'Aussi nous rendirent-ils toutes sortes d\'honneur et, a notre depart, ils nous fournirent ce dont nous avions besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 11, 'Trois mois plus tard, nous prenions passage sur un navire alexandrin, a l\'enseigne des Dioscures, qui avait hiverne dans le port de l\'ile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 12, 'Puis nous gagnions Syracuse et nous y passions trois jours ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 13, 'de la, longeant toujours la cote, nous touchions a Reggio. Le lendemain le vent du sud se leva, et, en deux jours, nous etions a Pouzzoles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 14, 'Nous y trouvames des freres qui nous engagerent a rester une semaine avec eux. Ensuite nous nous mimes en route pour Rome.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 15, 'Les freres de cette ville, qui avaient eu de nos nouvelles, vinrent a notre rencontre jusqu\'a Forum d\'Appius et jusqu\'a Les Trois Tavernes. A leur vue, Paul rendit graces a Dieu et prit courage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 16, 'Quand nous fumes arrives a Rome, Paul eut la permission de rester seul avec le soldat qui le gardait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 17, 'Trois jours apres, cependant, il convoqua les Juifs de Rome les plus consideres, et quand ils furent reunis, il leur dit : Mes freres, je n\'avais rien fait de contraire a notre peuple et aux institutions de nos peres, lorsqu\'on m\'a arrete a Jerusalem et livre entre les mains des Romains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 18, 'Ceux-ci, apres avoir examine ma cause, voulaient me relacher, parce que rien ne pouvait motiver ma condamnation a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 19, 'Mais les Juifs, s\'y etant opposes, je me vis force d\'en appeler a l\'Empereur, sans cependant vouloir me plaindre de mes compatriotes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 20, 'Voila pourquoi je vous ai demandes ; je voulais vous voir et vous parler, car c\'est a cause de l\'esperance d\'Israel que je porte cette chaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 21, 'Ils lui repondirent : Nous n\'avons pas recu de lettres de Judee a ton sujet, et aucun de nos freres n\'est venu nous faire de rapport ni nous dire du mal de toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 22, 'Cependant nous voudrions bien apprendre de toi ce que tu penses ; car, a notre connaissance, ce parti-la rencontre partout de l\'opposition.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 23, 'Alors ils prirent jour ensemble et un plus grand nombre encore vint le trouver chez lui. Du matin au soir il leur exposait ce que c\'etait que le Royaume de Dieu et les suppliait en leur demontrant par la Loi et les Prophetes tout ce qui concerne Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 24, 'Les uns se laisserent convaincre par ses paroles, les autres resterent incredules.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 25, 'Comme ils se separaient sans avoir pu se mettre d\'accord, Paul leur dit ce simple mot : L\'Esprit saint a admirablement parle quand il a dit a vos peres par le prophete Esaie :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 26, 'Va vers ce peuple et dis-lui : Vos oreilles entendront et vous ne comprendrez pas ; Vos yeux regarderont et vous ne verrez pas,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 27, 'Car le coeur de ce peuple s\'est endurci ; Ils ont fait la sourde oreille ; ils ont ferme les yeux, Pour que leurs yeux ne voient pas, que leurs oreilles n\'entendent pas, Que leurs coeurs ne comprennent pas Pour se convertir et que je puisse les guerir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 28, 'Sachez donc que ce salut de Dieu a ete envoye aux paiens ; ceux-la l\'ecouteront!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 29, '(Lorsqu\'il eut dit cela, les Juifs s\'en allerent, discutant vivement entre eux.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 30, 'Paul demeura deux ans entiers dans un appartement qu\'il avait loue a ses frais ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (44, 28, 31, 'il pouvait y recevoir tous ceux qui venaient le voir ; il prechait le Royaume de Dieu ; il enseignait ce qui concerne le Seigneur Jesus-Christ sans difficultes et en toute liberte. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 1, 'Paul, serviteur de Jesus-Christ, elu apotre choisi pour precher l\'evangile de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 2, '(Evangile promis d\'avance par ses prophetes dans les saintes Ecritures');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 3, 'et concernant son Fils ne, quant a la chair, de la race de David,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 4, 'et, quant a l\'Esprit de saintete, declare avec puissance Fils de Dieu, par sa resurrection d\'entre les morts, Jesus-Christ notre Seigneur) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 5, '(Nous avons recu de sa grace la mission d\'amener en son nom a lobeissance de la foi tous les paiens),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 6, '(dont vous faites aussi partie, vous elus de Jesus-Christ).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 7, 'A tous les biens-aimes de Dieu, fideles elus qui sont a Rome. Grace et paix vous soient accordees par Dieu notre Pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 8, 'Je commence en rendant graces pour vous tous a mon Dieu par Jesus-Christ de ce que votre foi est renommee dans le monde entier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 9, 'Le Dieu, que je sers de toute mon ame en prechant l\'Evangile de son Fils, m\'est temoin que sans cesse je fais mention de vous dans mes prieres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 10, 'lui demandant toujours que je puisse une fois enfin reussir, par sa volonte, a venir chez vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 11, 'J\'ai, en effet, le plus grand desir de vous voir, pour vous communiquer quelque don spirituel propre a vous affermir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 12, 'je veux dire, pour que, chez vous, nous nous encouragions mutuellement, vous et moi, par la foi qui nous est commune.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 13, 'Je ne veux pas vous laisser ignorer, mes freres, que souvent j\'ai forme le projet d\'aller chez vous (j\'en ai ete empeche jusqu\'a present), afin de recueillir aussi quelques fruits parmi vous comme parmi les autres paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 14, 'Je me dois aux Grecs et aux etrangers, aux savants et aux ignorants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 15, 'J\'ai donc, pour ma part, grande envie de vous precher l\'Evangile a vous aussi, habitants de Rome !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 16, 'Car je n\'ai pas honte de l\'Evangile ; il est une puissance de Dieu donnant le salut a tout croyant, au Juif d\'abord, puis au paien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 17, 'Il s\'y revele, en effet, une justice de Dieu, qui vient de la foi, qui est accordee a la foi, comme il est ecrit : Le juste vivra par la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 18, 'Il y a une colere de Dieu qui se revele du haut du ciel contre toute impiete et toute iniquite d\'hommes qui mechamment etouffent la verite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 19, 'car ce qu\'on peut savoir sur Dieu est a leur portee, Dieu le leur a fait connaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 20, 'En effet, les perfections invisibles de Dieu, son eternelle puissance et sa divinite, eclatent aux yeux depuis la creation du monde, pour quiconque sait regarder ses oeuvres. De la sorte ils sont inexcusables ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 21, 'car enfin, ayant conscience de Dieu, ils ne lui ont ni rendu gloire, ni rendu graces comme a leur Dieu ; au contraire, ils se sont perdus dans des raisonnements sans valeur ; leur coeur inintelligent a ete plonge dans les tenebres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 22, 'Ils se sont dits sages et ils ont ete insenses,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 23, 'ils ont substitue, a la gloire du Dieu immortel des images representant l\'homme mortel, les oiseaux, les quadrupedes, les reptiles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 24, 'Voila pourquoi Dieu les a livres a toutes les passions de leurs coeurs et a une impurete telle qu\'eux-memes ils deshonorent leurs corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 25, 'Oui, c\'est pour avoir substitue a la verite de Dieu le mensonge, pour avoir servi, avoir adore la creature au lieu du Createur,  (Beni soit-il a jamais ! Amen!) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 26, 'c\'est pour cela que Dieu les a livres a des passions infames : Les femmes ont remplace les relations naturelles par des actes contre nature ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 27, 'les hommes de meme ont abandonne leurs relations naturelles avec la femme, se sont pris de passions furieuses les uns pour les autres, ont commis entre hommes des infamies et ont recu en leurs personnes le salaire que merite leur egarement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 28, 'et comme ils ne se sont pas soucies de savoir qui etait Dieu, Dieu les a livres a un esprit d\'aveuglement, et ils font ce qu\'il ne faut pas faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 29, 'Ils ont tous les vices, toutes les mechancetes, toutes les malices, toutes les rapacites ; ne respirant qu\'envie, meurtre, discorde, ruse, mauvaise foi ; delateurs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 30, 'calomniateurs, impies, insolents, orgueilleux, presomptueux, ingenieux au mal, enfants rebelles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 31, 'inintelligents, parjures, durs, impitoyables.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 1, 32, 'Ces gens-la connaissent le decret de Dieu qui condamne a mort ceux qui commettent de tels actes, et non seulement ils les commettent, mais ils approuvent ceux qui s\'y livrent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 1, 'Donc, tu es sans excuse, o homme, qui que tu sois, de juger autrui ; car, en le jugeant, tu te condamnes toi-meme, et, en effet, tout en le jugeant, tu te conduis comme lui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 2, 'et la sentence de Dieu sur ceux qui se conduisent ainsi est, nous le savons, conforme a la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 3, 'Penses-tu, toi qui juges ceux qui se conduisent ainsi et qui en fais autant, penses-tu echapper a la sentence de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 4, 'Et son inepuisable bonte, et sa tolerance, et sa patience les meprises-tu? Ignores-tu que la bonte de Dieu t\'invite au repentir ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 5, 'Par ton endurcissement et par l\'impenitence de ton coeur, tu t\'amasses un tresor de colere pour le jour terrible ou se revelera le juste jugement de Dieu');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 6, 'Qui rendra a chacun selon ses oeuvres');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 7, 'Aux uns qui, en perseverant dans l\'oeuvre bonne, cherchent la gloire, l\'honneur et l\'immortalite : la vie eternelle !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 8, 'Aux autres qui, par esprit de parti, s\'opposent a la verite et se laissent aller a l\'iniquite : la colere et l\'indignation!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 9, 'Malheur, desespoir sur toute ame d\'homme qui fait le mal, sur celle du Juif d\'abord, sur celle du paien ensuite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 10, 'Gloire, honneur et paix a quiconque fait le bien, au Juif d\'abord, au paien ensuite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 11, 'Car Dieu n\'a pas egard aux personnes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 12, 'tous ceux qui auront peche sans avoir eu de Loi, periront sans avoir eu de Loi, et ceux qui auront peche en ayant eu une Loi, seront juges par cette Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 13, 'En effet, ce ne sont pas ceux qui entendent lire une Loi qui sont justes aux yeux de Dieu ; mais ceux qui la pratiquent seront declares justes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 14, 'Et quand les paiens qui n\'ont pas de Loi font naturellement ce que la Loi commande, tout en n\'ayant pas de Loi, ils sont eux-memes leur propre Loi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 15, 'ils montrent que l\'oeuvre commandee par la Loi est ecrite dans leur coeur, c\'est leur conscience qui l\'atteste et ce sont leurs pensees qui tantot les accusent, tantot meme les absolvent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 16, 'On le verra le jour ou Dieu, selon l\'Evangile que je preche, jugera par Jesus-Christ les actions secretes des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 17, 'Toi tu te nommes un Juif, tu te reposes sur la Loi, tu es fier de ton Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 18, 'tu connais sa volonte, instruit par la Loi tu sais, peser le pour et le contre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 19, 'tu te sens capable d\'etre un guide pour les aveugles, une lumiere pour ceux qui sont dans les tenebres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 20, 'un directeur pour les ignorants, un maitre pour ceux qui sont encore enfants, car tu as dans la Loi l\'expression de la sagesse et de la verite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 21, 'et quand tu instruis ainsi les autres tu ne t\'instruis pas toi-meme! tu preches de ne pas voler, et tu voles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 22, 'tu dis de ne pas commettre d\'adulteres, et tu en commets! tu as les idoles en abomination, et tu commets des sacrileges!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 23, 'tu es fier d\'avoir une Loi, et tu outrages Dieu en la violant !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 24, 'Le nom de Dieu, comme dit l\'Ecriture, est deshonore par les paiens a cause de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 25, 'La circoncision est utile, sans doute, tant que tu pratiques la Loi ; si tu la violes, tu es comme si tu n\'etais pas circoncis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 26, 'Si l\'incirconcis observe les ordonnances de la Loi, tout incirconcis qu\'il est, ne sera-t-il pas considere comme circoncis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 27, 'Et l\'incirconcis de naissance qui observe la Loi ne te jugera-t-il pas, toi, qui la violes, possesseur de la lettre de la Loi et de la circoncision ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 28, 'On n\'est pas Juif par l\'exterieur ; la vraie circoncision n\'est pas celle qui se voit sur le corps ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 2, 29, 'mais le vrai Juif c\'est celui qui l\'est interieurement, le vrai circoncis l\'est dans son coeur, il ne l\'est pas a la lettre, il lest en esprit ; voila le Juif qui est approuve sinon des hommes, du moins de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 1, 'Quelle est donc la prerogative du Juif, ou quelle est l\'utilite de la circoncision?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 2, 'Elle est grande a tous les egards : Il y a d\'abord que Dieu a confie aux Juifs ses oracles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 3, 'Comment donc? s\'il en est qui ont manque de foi, leur infidelite annulera-t-elle la fidelite de Dieu ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 4, 'C\'est impossible, il faut que Dieu soit veridique, Tous les hommes dussent-ils etre des menteurs, comme il est ecrit : Tes paroles doivent etre reconnues justes Et tu dois gagner ta cause quand on te juge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 5, 'Mais de ce que notre injustice met en relief la justice de Dieu, que conclurons-nous? que Dieu sera injuste en laissant agir sa colere? (je parle la comme font les hommes)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 6, 'c\'est impossible ; autrement comment Dieu jugerait-il le monde?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 7, 'Et si mon manque de foi a fait apparaitre dans toute sa gloire la veracite de Dieu, pourquoi donc serai-je juge comme pecheur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 8, 'et pourquoi (comme nos calomniateurs le repandent et nous accusent de le dire) ne ferions-nous pas le mal, pour qu\'il en sorte du bien? Je condamne ceux qui parlent ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 9, 'Que dire alors? qu\'avons-nous a mettre en avant? absolument rien. Nous avons deja montre que les Juifs comme les Grecs, tous sont sous l\'empire du peche,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 10, 'comme il est ecrit : Il n\'y a pas de juste, pas un seul ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 11, 'Personne n\'est intelligent, Personne ne cherche Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 12, 'Tous se sont fourvoyes et corrompus ensemble ; Il n\'y en a pas un qui fasse le bien, pas un seul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 13, 'Leur gosier est un sepulcre beant ; Leur langue leur sert a tromper ; Le venin de l\'aspic est sur leurs levres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 14, 'Leur bouche est pleine de malediction et d\'amertume ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 15, 'Ils ont le pied leger pour repandre le sang,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 16, 'La desolation et le malheur sont sur leur chemin ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 17, 'La voie de la paix, ils l\'ignorent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 18, 'La crainte de Dieu, ils ne l\'ont pas devant les yeux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 19, 'Or nous savons que tout ce que dit la Loi, elle le dit a ceux qui sont soumis a la Loi, pour que toute bouche soit fermee, et que le monde entier soit sous le coup du jugement de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 20, 'Car les oeuvres de la Loi Ne justifient devant lui aucune creature ; la Loi ne donne que la connaissance du peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 21, 'Mais maintenant il a ete revele une justice de Dieu, independante de la Loi ; la Loi et les Prophetes en parlent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 22, 'Cette justice qui vient de Dieu par la foi en Jesus-Christ s\'adresse a tous les croyants ; et cela sans distinction ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 23, 'car tous les hommes ont peche et sont prives de la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 24, 'Dans sa grace, ils sont gratuitement justifies, au moyen de la Redemption faite par Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 25, 'Dieu l\'avait destine a etre, par sa mort sanglante, une victime propitiatoire pour ceux qui croiraient,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 26, 'et il a ainsi montre sa justice. (Dans son indulgence il n\'a pas tenu compte des peches anterieurs). Il a, dis-je, montre de nos jours sa justice, pour qu\'on sut qu\'il est juste et qu\'il justifie celui qui a la foi en Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 27, 'Ou est, apres cela, le motif de s\'enorgueillir? il est exclu. Par quelle institution? celle des oeuvres ; non, mais par celle de la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 28, 'Nous pensons donc que l\'homme est justifie par la foi independamment des oeuvres de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 29, 'Dieu ne serait-il que le Dieu des Juifs? n\'est-il pas aussi le Dieu des paiens?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 30, 'Oui, des paiens aussi, puisqu\'il n\'y a qu\'un Dieu qui justifiera les circoncis par la foi et les incirconcis par la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 3, 31, 'Est-ce que nous annulons donc la Loi par la foi? Nullement. nous la confirmons au contraire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 1, 'Quel avantage pouvons-nous dire que notre premier pere Abraham a obtenu par lui-meme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 2, 'Si ce sont ses oeuvres qui l\'ont justifie, il peut etre orgueilleux ; mais il ne le peut pas devant Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 3, 'Que dit l\'Ecriture : Abraham eut foi en Dieu, et cela lui fut compte pour justice');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 4, 'Or le salaire de celui qui a fait de bonnes oeuvres n\'est pas considere comme une grace, mais comme une dette.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 5, 'Quant a celui qui n\'a pas fait de bonnes oeuvres, mais qui a foi en Celui qui justifie le pecheur, sa foi lui est comptee pour justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 6, 'David aussi parle du bonheur de celui que Dieu considere comme juste independamment de ses oeuvres :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 7, 'Heureux ceux dont les fautes sont pardonnees Et dont les peches sont effaces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 8, 'Heureux celui dont le Seigneur ne porte pas en compte le peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 9, 'Cette beatitude ne s\'adresse-t-elle qu\'aux circoncis? ne s\'applique-t-elle pas aussi aux incirconcis? Quand nous disons pour Abraham que sa foi lui fut comptee pour justice,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 10, 'comment lui fut-elle comptee? avant ou apres sa circoncision? Ce n\'est pas apres, c\'est avant sa circoncision!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 11, 'Et il a recu le signe de la circoncision comme le sceau de la justice que sa foi lui avait obtenue quand il n\'etait pas circoncis ; c\'etait pour qu\'il fut le pere de tous les incirconcis qui auraient la foi, et qu\'elle leur fut comptee pour justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 12, 'Il devait etre aussi le pere des circoncis qui non seulement le seraient, mais qui marcheraient sur ses traces, en ayant la foi qu\'il avait, lui, notre pere Abraham, avant sa circoncision.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 13, 'En effet, ce n\'est pas une Loi qui a assure a Abraham et a ses descendants la possession du monde, c\'est la justice que procure la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 14, 'Si c\'est une Loi qui assure cette possession, alors la foi est inutile et la promesse est annulee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 15, '(La Loi ne produit que la colere, et la ou il n\'y a pas de Loi, il n\'y a pas non plus de violation.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 16, 'Voici pourquoi la possession vient par la foi : c\'est pour qu\'elle soit l\'effet d\'une grace, et que la promesse soit assuree a tous les descendants, non pas seulement a ceux qui ont recu la Loi, mais a ceux qui ont eu la foi d\'Abraham ; il est notre pere a tous');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 17, '(ainsi que cela est ecrit : Je t\'ai rendu pere de plusieurs peuples), il l\'est devant le Dieu en qui il a cru, qui rend la vie aux morts et qui appelle a l\'existence ce qui n\'existe pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 18, 'Contre toute esperance, il a ete plein d\'esperance, il a cru et il est devenu le pere de plusieurs peuples (selon cette parole : Telle sera ta posterite).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 19, 'Sa foi n\'a pas faibli ; il n\'a pas fait attention a son extreme vieillesse (il etait presque centenaire) ni a l\'age trop avance de Sarra.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 20, 'Il ne douta pas de la promesse de Dieu ; il ne fut pas incredule ; au contraire, fortifie par sa foi, il donna gloire a Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 21, 'convaincu que Celui qui a promis est assez puissant pour remplir sa promesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 22, 'Voila pourquoi elle lui fut comptee pour justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 23, 'Or ce n\'est pas pour lui seul que ce mot est ecrit : cela lui fut compte,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 24, 'c\'est aussi pour nous ; cela doit nous etre compte egalement a nous qui croyons en celui qui a ressuscite des morts notre Seigneur Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 4, 25, 'lui qui a ete livre a cause de nos peches et a ete ressuscite a cause de notre justification.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 1, 'Etant ainsi justifies par la foi, soyons en paix avec Dieu par notre Seigneur Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 2, 'qui, de plus, nous a donne, par notre foi, acces a cette grace que nous possedons ; aussi mettons-nous notre orgueil a esperer la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 3, 'Bien plus, nous mettons encore notre orgueil dans les afflictions sachant que l\'affliction produit la patience,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 4, 'que par la patience nous supportons l\'epreuve, que de l\'epreuve nait l\'esperance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 5, 'Or l\'esperance ne trompe pas, parce que l\'Esprit saint, recu par nous, fait abonder l\'amour de Dieu dans nos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 6, 'En effet, nous etions encore faibles, et Christ, au temps marque, est mort pour des impies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 7, 'On donnerait difficilement sa vie pour un juste (oui, il pourrait arriver que l\'on consentit a mourir pour un homme de bien),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 8, 'mais la preuve de son amour pour nous Dieu l\'a donnee, en ce que Christ est mort pour nous quand nous etions encore pecheurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 9, 'A plus forte raison, serons-nous sauves de la colere par celui dans le sang duquel nous avons ete justifies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 10, 'En effet, si, quand nous etions ennemis, Dieu nous a reconcilies par la mort de son Fils, a plus forte raison, etant reconcilies, serons-nous sauves par sa vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 11, 'Bien plus, nous avons encore sujet de mettre notre orgueil en Dieu par notre Seigneur Jesus-Christ, qui nous a maintenant obtenu la reconciliation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 12, 'Par consequent, de meme que par un seul homme le peche est entre dans le monde et par le peche la mort, et que la mort s\'est ainsi etendue a tous les hommes, parce que tous ont peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 13, 'Avant la Loi, le peche etait dans le monde ; or, en l\'absence de Loi, le peche n\'est pas impute');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 14, 'et cependant la mort a exerce son regne depuis Adam jusqu\'a Moise, meme sur ceux qui n\'avaient point peche dans les memes conditions qu\'Adam. Adam etait l\'image de celui qui devait venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 15, 'Toutefois, il n\'en est pas de meme de la faute d\'une part et du don de la grace de l\'autre. Car si la faute d\'un seul homme a entraine la mort de beaucoup, a plus forte raison la grace de Dieu et le don de cette grace, le don d\'un seul homme, Jesus-Christ, en ont enrichi beaucoup.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 16, 'Il n\'en est pas de meme non plus des consequences du premier peche d\'une part et du don de l\'autre ; car la sentence qui suivit une faute unique a entraine une condamnation, et le don de la grace qui suivit des fautes nombreuses a entraine un acquittement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 17, 'Si, en effet, a la suite d\'une seule faute et par le fait d\'un seul homme, la mort a exerce son regne, a bien plus forte raison ceux qui recoivent la grace immense de Dieu et le don de la justice participeront-ils au Royaume et a la vie par le fait d\'un seul homme aussi, Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 18, 'Ainsi donc, de meme qu\'une seule faute a entraine la condamnation de tous les hommes, de meme un seul acte de justice a entraine pour tous les hommes la justification qui donne la vie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 19, 'et de meme que la desobeissance d\'un seul homme en a rendu pecheurs un grand nombre, de meme l\'obeissance d\'un seul en rendra justes un grand nombre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 20, 'Quant a la Loi, elle est intervenue pour multiplier les fautes, mais la ou le peche a abonde, la grace a surabonde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 5, 21, 'et alors, comme le peche a regne par la mort, de meme aussi la grace regnera par la justice pour la vie eternelle par Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 1, 'Et maintenant comment conclure? En disant que nous persisterons a pecher pour faire abonder la grace?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 2, 'Non, certes. Nous qui sommes morts au peche, comment y vivrions-nous encore?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 3, 'Ne savez-vous pas que nous tous qui avons ete baptises en Jesus-Christ, c\'est en sa mort que nous l\'avons ete? Nous avons ete ensevelis avec lui par le bapteme en sa mort,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 4, 'et de meme que Christ a ete ressuscite des morts par la gloire du Pere, de meme nous devons vivre d\'une vie nouvelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 5, 'Si nous sommes etroitement unis a lui par une mort semblable a la sienne, nous le serons aussi par une meme resurrection ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 6, 'nous savons que notre vieil homme a ete crucifie avec lui, afin que ce corps de peche soit detruit, et que nous ne soyons plus esclaves du peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 7, 'Celui qui est mort est declare juste et est delivre du peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 8, 'Si nous sommes morts avec le Christ, nous croyons que nous vivrons aussi avec lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 9, 'car nous savons que Christ ressuscite des morts ne meurt plus ; la mort n\'a plus de pouvoir sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 10, 'Il est mort, en effet, et c\'est a cause du peche qu\'il est mort une fois pour toutes ; puis il est revenu a la vie, et c\'est pour Dieu qu\'il vit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 11, 'De meme considerez-vous aussi comme morts au peche et comme vivants pour Dieu en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 12, 'Que le peche ne regne donc pas dans votre corps mortel, n\'obeissez pas a ses passions ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 13, 'ne livrez pas vos membres au peche ; n\'en faites pas les instruments du vice, mais donnez-vous a Dieu, vous qui etiez morts et qui vivez, consacrez vos membres a Dieu, faites-en des instruments de justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 14, 'Le peche n\'aura plus de pouvoir sur vous, car vous n\'etes pas sous la Loi, mais sous la grace.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 15, 'Mais quoi? Devons-nous pecher parce que nous ne sommes pas sous la Loi, mais sous la grace? Oh ! nullement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 16, 'Ne savez-vous pas que si vous vous asservissez a quelqu\'un et lui obeissez, vous devenez ses esclaves? soit du peche qui mene a la mort, soit de la soumission qui mene a la justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 17, 'Graces soient rendues a Dieu, de ce qu\'autrefois, esclaves du peche, vous vous etes soumis de coeur au type de doctrine qui vous a ete offert!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 18, 'Mais du moment que vous avez ete delivres du peche, vous etes devenus esclaves de la justice (j\'emploie cette expression usuelle : esclaves, a cause de votre faiblesse naturelle) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 19, 'de meme, veux-je dire, que vous avez mis vos membres au service de l\'impurete et de l\'iniquite pour violer la Loi, de meme vous devez mettre maintenant vos membres au service de la justice pour vous sanctifier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 20, 'Lorsque vous etiez esclaves du peche, vous etiez libres a l\'egard de la justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 21, 'Quel avantage en retiriez-vous? Un avantage qui vous fait honte aujourd\'hui ; cela vous menait a la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 22, 'Mais maintenant, delivres du peche et esclaves de Dieu, l\'avantage que vous retirez, c\'est la sanctification qui mene a la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 6, 23, 'Le salaire du peche, c\'est la mort ; mais le don de la grace de Dieu, c\'est la vie eternelle en Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 1, 'Ignorez-vous, freres (je parle a des personnes Connaissant la Loi), que celle-ci tient l\'homme en son pouvoir aussi longtemps qu\'il est en vie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 2, 'La femme mariee, par exemple, est liee par une loi a son mari s\'il est vivant ; si son mari vient a mourir, le lien legal qui l\'attachait a lui est rompu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 3, 'Ainsi elle meritera le nom d\'adultere si, du vivant de son mari, elle se donne a un autre ; mais, apres la mort de son mari, elle est affranchie de toute obligation legale, il n\'y a plus d\'adultere si elle se donne a un autre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 4, 'Il en est de meme de nous, mes freres ; nous avons ete mis a mort relativement a la Loi par le moyen du corps du Christ pour appartenir a un autre, a celui qui est ressuscite des morts, afin que nous portions des fruits pour Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 5, 'Car lorsque nous vivions encore selon la chair, les passions coupables, excitees par la Loi, exercaient leur activite dans les membres de notre corps, et nous portions des fruits pour la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 6, 'Mais maintenant le lien legal a ete rompu ; nous sommes morts a cette Loi qui nous retenait captifs, pour que nous servions Dieu dans la nouveaute de l\'Esprit et non dans la vetuste de la lettre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 7, 'Eh bien, comment conclure? en disant que la Loi c\'est le peche? Non certes, mais que par la Loi seule je connais le peche. Par exemple, je n\'aurais rien su de la convoitise, si la Loi n\'avait dit: Tu ne convoiteras pas !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 8, 'et c\'est le peche qui, saisissant l\'occasion de ce commandement, a fait naitre en moi toutes sortes de convoitises, car sans Loi le peche n\'existe pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 9, 'Je vivais sans Loi autrefois, mais le commandement me fut donne, le peche vint a naitre, et moi je mourus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 10, 'Et il est arrive ceci : le commandement qui devait me mener a la vie, ce commandement-la meme m\'a conduit a la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 11, 'Oui, le peche a saisi l\'occasion du commandement, m\'a seduit et m\'a tue par ce commandement meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 12, 'Quant a la Loi, elle est sainte, et le commandement lui-meme est saint, juste et bon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 13, 'Mais alors c\'est une bonne chose qui est cause de ma mort? Nullement, c\'est le peche ; il s\'est servi d\'une bonne chose pour m\'apporter la mort, et alors il a montre ce qu\'il etait, il est apparu on ne peut plus coupable, ce peche qui se sert ainsi du commandement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 14, 'Oui, nous savons que la Loi elle-meme est spirituelle ; mais c\'est moi qui suis charnel, vendu et asservi au peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 15, 'Je ne sais pas meme ce que je fais : car je ne fais pas ce que je veux ; au contraire, ce que je deteste, voila ce que je fais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 16, 'Et alors si je fais ce que je ne veux pas, je rends temoignage a la Loi, je la reconnais bonne,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 17, 'et, dans ce cas, ce n\'est plus moi qui agis, c\'est le peche qui habite en moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 18, 'Je sais, en effet, qu\'en moi, je veux dire en ma chair, il n\'habite rien de bon : vouloir le bien est, il est vrai, a ma portee, mais l\'accomplir, non.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 19, 'Car je ne fais pas le bien que je veux, mais le mal que je ne veux pas, voila ce que je fais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 20, 'Eh bien, si je fais ce que je ne veux pas, ce n\'est plus moi qui agis, mais le peche qui habite en moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 21, 'Voici donc la situation ou je me trouve : quand, ma volonte est de faire le bien, c\'est le mal qui est la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 22, 'Mon etre interieure adhere avec joie a la Loi de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 23, 'mais je decouvre dans mes membres une autre loi en guerre avec la loi de ma raison et qui m\'asservit a la loi du peche qui est dans mes membres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 24, 'Malheureux que je suis ! qui me delivrera de ce corps de mort !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 7, 25, 'Graces soient a Dieu par Jesus-Christ notre Seigneur! Ainsi je me soumets, moi-meme, par la raison, a la Loi de Dieu, mais, par la chair, je suis soumis a la loi du peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 1, 'Il n\'y a donc plus maintenant de condamnation pour ceux qui sont en Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 2, 'parce que la Loi de l\'Esprit de vie nous a affranchis, en Jesus-Christ, de la loi du peche et de la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 3, 'Ce qui etait impossible a la Loi, la chair lui otant toute force, Dieu l\'a fait en envoyant son propre Fils, a cause du peche, prendre une chair de peche semblable a la notre ; il a condamne le peche dans la chair meme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 4, 'afin que le decret de la Loi s\'accomplit en nous, en ceux qui ne marchent pas selon la chair, mais selon l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 5, 'Ceux qui sont selon la chair s\'attachent a ce qui est de la chair ; ceux qui sont selon l\'Esprit, a ce qui est de l\'Esprit ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 6, 'or l\'attachement pour la chair mene a la mort, l\'attachement pour l\'Esprit, a la vie et au salut,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 7, 'parce que l\'attachement pour la chair est une inimitie contre Dieu, puisqu\'il ne se soumet pas a la Loi de Dieu (et meme il ne le peut pas).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 8, 'Ceux qui sont dans la chair ne peuvent plaire a Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 9, 'mais vous, vous n\'etes pas dans la chair, mais dans l\'Esprit, si toutefois l\'Esprit de Dieu habite en vous.  Si quelqu\'un n\'a pas l\'Esprit de Christ, il ne lui appartient pas. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 10, 'Si Christ est en vous, votre corps est, il est vrai, soumis a la mort a cause du peche, mais votre esprit l\'est a la vie a cause de la justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 11, 'Si l\'Esprit de Celui qui a ressuscite Jesus des morts habite en vous, lui qui a ressuscite Jesus des morts donnera aussi la vie a vos corps mortels au moyen de son Esprit qui demeure en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 12, 'Ainsi, mes freres, nous sommes obliges non envers la chair pour vivre selon la chair...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 13, 'Si vous vivez selon la chair, vous devez vous attendre a mourir. Si, au contraire, vous tuez par l\'Esprit l\'activite du corps, vous vivrez,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 14, 'parce que tous ceux qui se laissent conduire par l\'Esprit de Dieu, sont fils de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 15, 'et que vous n\'avez point recu un esprit de servitude pour craindre encore, mais que vous avez recu un Esprit d\'adoption, par lequel nous nous ecrions : Abba! Pere !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 16, 'Cet Esprit atteste lui-meme a notre esprit que nous sommes enfants de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 17, 'et si nous sommes enfants, nous sommes aussi heritiers ; heritiers de Dieu, coheritiers de Christ, si nous participons a ses souffrances pour participer aussi a sa gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 18, 'J\'estime, en effet, que les souffrances d\'a present ne sont rien en comparaison de la gloire qui doit un jour se reveler pour nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 19, 'Car la creation attend avec impatience cette revelation des fils de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 20, 'parce que ce n\'est pas de son propre gre qu\'elle a ete assujettie a la fragilite ; elle l\'a ete au gre de celui qui l\'a assujettie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 21, 'et elle espere etre affranchie, elle aussi, de la servitude de la corruption et passer a la glorieuse liberte des enfants de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 22, 'Nous savons, en effet, que jusqu\'a present la creation tout entiere gemit, elle est dans les angoisses de l\'enfantement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 23, 'et ce n\'est pas elle seulement, c\'est nous aussi qui avons recu comme premices l\'Esprit et qui gemissons en nous-memes, attendant l\'adoption, c\'est-a-dire la delivrance de notre corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 24, 'Car c\'est en esperance que nous sommes sauves ; or une esperance dont on voit l\'objet n\'est plus une esperance ; ce qu\'on voit, pourquoi l\'esperer encore?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 25, 'Mais si nous esperons ce que nous ne voyons pas, c\'est que nous attendons avec perseverance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 26, 'De plus, l\'Esprit supplee aussi a notre faiblesse ; nous ne savons pas, en effet, ce qu\'il faut demander dans nos prieres, mais l\'Esprit lui-meme intercede pour nous par des soupirs et sans paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 27, 'Or, Celui qui scrute les coeurs connait les desirs de l\'Esprit, parce que celui-ci intercede pour les fideles selon les vues de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 28, 'et nous savons que tout contribue au bien de ceux qui aiment Dieu, de ceux qui sont elus d\'apres son decret ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 29, 'nous savons que ceux qu\'il a connus d\'avance, ont ete aussi predestines par lui a reproduire l\'image de son Fils qui sera ainsi l\'aine de plusieurs freres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 30, 'et ceux qu\'il a predestines, il les a aussi appeles ; ceux qu\'il a appeles, il les a aussi justifies ; ceux qu\'il a justifies il les a aussi glorifies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 31, 'Que dire de plus? Si Dieu est pour nous, qui sera contre nous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 32, 'Lui qui n\'a pas epargne son propre Fils, mais l\'a livre a la mort pour nous tous, comment ne nous donnerait-il pas toutes choses avec lui?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 33, 'Qui osera accuser les elus de Dieu? Serait-ce Dieu qui les justifie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 34, 'Qui les condamnera? Serait-ce Jesus-Christ, qui est mort? plus encore, qui est ressuscite, qui est assis a la droite de Dieu, qui intercede pour nous ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 35, 'Qui nous arrachera a l\'amour du Christ? Sera-ce la tribulation? ou l\'angoisse? ou la persecution? ou la faim? ou le denuement ? ou les perils? ou le glaive?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 36, 'A cause de toi, comme dit l\'Ecriture, nous sommes mis a mort tout le long du jour ; On nous considere comme des brebis pour la boucherie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 37, 'Mais, dans tous ces combats, nous restons plus que vainqueurs, grace a Celui qui nous a aimes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 38, 'Oui, je suis certain que ni la mort ni la vie, ni les anges ni les dominations, ni le present ni l\'avenir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 8, 39, 'ni les puissances, ni les forces d\'en haut ni les forces d\'en bas, ni une creation quelconque, ne pourra nous arracher a l\'amour de Dieu en Jesus-Christ notre Seigneur !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 1, 'Je parle en verite, devant Christ, je ne mens point (ma conscience eclairee par l\'Esprit saint me l\'atteste)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 2, 'en disant que j\'eprouve une grande tristesse, une continuelle peine de coeur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 3, 'et que je voudrais etre anatheme et separe du Christ pour mes freres, pour ceux de ma race et de mon sang,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 4, 'je parle des Israelites qui ont eu le privilege de l\'adoption, de la gloire promise, des alliances, de la legislature, du vrai culte, des promesses,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 5, 'qui ont eu les patriarches et dont est sorti, selon la chair, le Christ qui est au-dessus de tous! (Dieu en soit eternellement beni! Amen.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 6, 'Je ne veux cependant pas dire que Dieu ait manque a ses promesses. Pour etre issu d\'Israel, on n\'est pas toujours vrai Israelite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 7, 'pour etre de la race d\'Abraham, on n\'est pas toujours de ses vrais enfants, mais : C\'est d\'apres Isaac que ta race sera nommee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 8, 'Cela signifie que les enfants par le sang ne sont pas necessairement les enfants de Dieu, mais que ce sont les enfants par la promesse qui comptent comme la vraie race ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 9, 'car c\'est d\'une promesse qu\'il est question dans ce passage : Vers ce temps-la je viendrai et Sarra aura un fils ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 10, 'et ce n\'est pas tout, il y a aussi Rebecca qui fut enceinte des oeuvres d\'un seul homme, Isaac, notre pere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 11, 'eh bien, avant que ses jumeaux fussent nes, avant il, qu\'ils eussent fait ni bien, ni mal,  pour que fut bien etabli le decret de Dieu base sur un choix qui ne depend pas des oeuvres, mais uniquement de Celui qui appelle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 12, ' Rebecca apprit que l\'aine servirait le plus jeune,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 13, 'car il est ecrit: J\'ai aime Jacob Et j\'ai hai Esau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 14, 'Que faut-il en conclure? qu\'il y a de l\'injustice en Dieu? c\'est impossible,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 15, 'puisqu\'il dit a Moise : J\'aurai pitie de celui dont il me plaira d\'avoir pitie et j\'aurai compassion de celui dont il me plaira d\'avoir compassion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 16, 'Ainsi rien ne sert de vouloir et de s\'agiter, c\'est Dieu qui fait misericorde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 17, 'En effet, l\'Ecriture dit a Pharaon : Voici pourquoi je t\'ai suscite : Pour montrer en toi ma puissance et proclamer mon nom sur toute la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 18, 'Ainsi, il a pitie de qui il veut, il endurcit qui il veut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 19, 'Tu me diras alors :  pourquoi donc se plaint-il encore? qui donc peut resister a sa volonte? ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 20, 'Mais toi, o homme, qui es-tu donc pour discuter avec Dieu? Est-ce que le vase d\'argile dit au potier : Pourquoi m\'as-tu fait ainsi ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 21, 'Est-ce que le potier n\'a pas le droit avec la meme masse de terre de faire deux vases l\'un pour un noble usage, l\'autre pour un usage vulgaire?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 22, 'Et s\'il plait a Dieu de montrer sa colere et de faire connaitre sa puissance! Et s\'il a eu la grande patience de supporter des vases de colere fabriques pour la perdition!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 23, 'Et si cela fait ressortir les richesses de sa gloire a l\'egard des vases de misericorde prepares d\'avance pour la gloire!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 24, 'Je parle ici de nous qu\'il a appeles et parmi les Juifs et meme parmi les paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 25, 'C\'est ce qu\'il dit dans Osee : J\'appellerai mon peuple celui qui n\'etait pas mon peuple, Et bien aimee celle qui n\'etait pas bien aimee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 26, 'Et la ou on leur disait : Vous n\'etes pas mon peuple, On les appellera fils du Dieu vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 27, 'Esaie s\'ecrie aussi a propos d\'Israel : Quand le nombre des fils d\'Israel serait comme le sable de la mer, Un petit reste seulement sera sauve ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 28, 'Car le Seigneur achevera promptement d\'executer sa sentence sur la terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 29, 'Et comme l\'avait deja dit Esaie : Si le Seigneur des armees ne nous avait laisse une posterite, Nous serions devenus comme Sodome, Nous aurions ete semblables a Gomorrhe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 30, 'Que faut-il en conclure? que les paiens qui ne cherchaient pas la justice ont obtenu la justice, celle qui vient de la foi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 31, 'et les Israelites qui cherchaient dans la Loi un principe de justice n\'y sont pas arrives ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 32, 'pourquoi donc? parce qu\'ils ne le cherchaient pas par la foi, mais comme devant venir des oeuvres de la Loi ; ils se sont heurtes a la pierre d\'achoppement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 9, 33, 'suivant ce mot de l\'Ecriture : Voici je place en Sion une pierre d\'achoppement, Une pierre qui fait tomber ; Mais celui qui aura confiance en elle ne sera pas decu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 1, 'Freres, le desir de mon coeur et la priere que j\'adresse a Dieu pour eux, c\'est qu\'ils soient sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 2, 'Je leur rends le temoignage qu\'ils ont du zele pour Dieu, mais c\'est un zele ignorant,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 3, 'car c\'est parce qu\'ils ont ignore la justice de Dieu et qu\'ils ont voulu etablir leur propre justice qu\'ils ne se sont pas soumis a la justice de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 4, 'En effet, la fin de la Loi c\'est Christ donnant la justice a, quiconque a foi en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 5, 'Voici comment Moise deliait la justice obtenue par la Loi : L\'homme qui fera ces choses aura la vie par elles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 6, 'Mais la justice obtenue par la foi parle ainsi : Ne dis pas en ton coeur : Qui montera au ciel (le dire serait vouloir en faire descendre Christ),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 7, 'ou : Qui descendra dans l\'abime? (le dire serait vouloir faire remonter Christ de chez les morts ) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 8, 'au contraire, que dit-elle : Tout pres de toi est la parole, dans ta bouche et dans ton coeur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 9, '(il s\'agit ici de la parole de la foi que nous prechons) ; et alors si tu confesses de ta bouche le Seigneur Jesus et si tu crois dans ton coeur que Dieu l\'a ressuscite des morts, tu seras sauve ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 10, 'parce que la foi du coeur mene a la justice, la confession de la bouche mene au salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 11, 'L\'Ecriture dit en effet : Quiconque aura foi en lui ne sera pas decu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 12, 'Ainsi, point de difference entre le Juif et le Grec : tous ont un seul et meme Seigneur, riche pour tous ceux qui l\'invoquent, car :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 13, 'Quiconque invoquera le nom du Seigneur sera sauve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 14, 'Mais comment l\'invoqueront-ils s\'ils n\'ont pas cru en lui? comment croiront-ils s\'ils n\'en ont pas entendu parler? Comment en entendront-ils parler si personne ne le leur preche?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 15, 'comment ira-t-on le leur precher, s\'il n\'y a point d\'envoyes? Aussi est-il ecrit : Qu\'ils sont beaux... Les pieds de ceux qui annoncent de bonnes nouvelles !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 16, 'Mais ils n\'ont pas tous voulu ecouter la Bonne Nouvelle ; aussi Esaie dit-il :Seigneur, qui a ajoute foi a ce que nous avons fait entendre?.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 17, 'Donc, la foi se produit apres qu\'on a entendu, et l\'on entend quand la parole de Christ est prechee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 18, 'Mais je me dis : Peut-etre n\'ont-ils pas entendu precher? eh bien, c\'est tout le contraire: Dans la terre entiere a retenti leur voix, Et jusqu\'au bout du monde leurs paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 19, 'Je me dis encore : Peut-etre Israel n\'a-t-il pas compris? Eh bien ! Moise a dit le premier : Je vous rendrai jaloux d\'un peuple de rien ; Contre un peuple inintelligent je vous mettrai en colere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 20, 'Esaie va plus loin encore et il dit : Ils m\'ont trouve ceux qui ne me cherchaient pas ; Ils m\'ont decouvert ceux qui ne s\'informaient pas de moi ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 10, 21, 'tandis qu\'en parlant d\'Israel il dit : Tout le long du jour j\'ai tendu les bras A un peuple desobeissant et recalcitrant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 1, 'Je demande maintenant si Dieu a rejete son peuple ? Non certainement ; moi-meme suis un Israelite, descendant d\'Abraham, de la tribu de Benjamin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 2, 'Dieu n\'a pas rejete le peuple quil a autrefois prefere. Ne savez-vous pas ce que dit l\'Ecriture dans l\'histoire d\'Elie, et comme celui-ci se plaint a Dieu d\'Israel :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 3, 'Seigneur, ils ont tue tes prophetes, renverse tes autels, moi seul ai survecu et ils en veulent a ma vie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 4, 'Mais que lui dit l\'oracle : Je me suis reserve sept mille hommes qui n\'ont pas flechi le genou devant Baal');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 5, 'Eh bien! il y a de meme aujourd\'hui une reserve par suite de l\'election qui est une grace.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 6, 'Mais, si elle est une grace, elle n\'est donc pas le fruit des oeuvres, autrement la grace ne serait plus une grace.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 7, 'Qu\'est-ce a dire? Israel n\'a pas obtenu ce qu\'il cherche, les elus l\'ont obtenu ; et le reste s\'est endurci');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 8, 'selon qu\'il est ecrit : Dieu leur a donne un esprit d\'engourdissement, Des yeux pour ne pas voir, Des oreilles pour ne pas entendre jusqu\'a aujourd\'hui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 9, 'et David dit : Que leur table leur soit un filet, un piege, Une trappe, un chatiment ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 10, 'Que leurs yeux soient obscurcis pour ne pas voir, Que leur dos soit a jamais courbe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 11, 'Je demande alors si c\'est pour tomber qu\'ils ont ainsi bronche? Non certainement, mais leur faute a eu pour resultat l\'annonce du salut aux paiens de maniere a provoquer leur emulation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 12, 'Si, par leur faute, ils ont enrichi le monde, si, par un moment de retard, ils ont enrichi les paiens, que sera leur entree en masse dans l\'Eglise?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 13, 'C\'est a vous, paiens, que je parle : tout en etant apotre des paiens je couvre mon ministere de gloire');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 14, 'en m\'efforcant de provoquer l\'emulation de ceux de ma race et d\'en sauver quelques-uns.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 15, 'Car, si leur rejet a eu pour resultat la reconciliation du monde, que sera leur admission ? ce sera vraiment une resurrection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 16, 'Si les premices sont saintes, toute la masse l\'est aussi ; si la racine est sainte, les rameaux le sont aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 17, 'Si quelques rameaux ont ete retranches, si toi, olivier sauvage, as ete greffe a leur place, si tu as ete mis en communication avec la racine et la seve de l\'olivier,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 18, 'garde-toi bien de t\'enorgueillir aux depens des branches coupees. Si tu t\'enorgueillis, souviens-toi que ce n\'est pas toi qui portes la racine, c\'est la racine qui te porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 19, ' Mais les rameaux, diras-tu, ont ete coupes pour que je sois greffe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 20, ' D\'accord, ils ont ete coupes faute de foi ; toi tu restes-la par ta foi ; garde-toi de te vanter, tremble au contraire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 21, 'si Dieu n\'a pas epargne les rameaux naturels, il pourrait ne pas t\'epargner non plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 22, 'Remarque donc et la bonte et la severite de Dieu : severite pour ceux qui sont tombes, bonte pour toi, si toutefois tu te maintiens dans cette bonte, autrement toi aussi tu seras retranche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 23, 'et quant a eux, s\'ils ne persistent pas dans leur incredulite, ils seront greffes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 24, 'Dieu a bien le pouvoir de les greffer de nouveau, car si toi, tu as ete coupe sur un olivier sauvage de sa nature et as ete greffe, contrairement a ta nature, sur l\'olivier franc, a plus forte raison, eux, qui appartiennent a l\'olivier franc de sa nature, pourront etre greffes sur leur propre tronc.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 25, 'Pour que vous ne preniez pas de vous-memes une trop haute opinion, freres, je ne veux pas vous laisser ignorer une chose restee jusqu\'ici secrete : l\'aveuglement d\'une partie d\'Israel durera jusqu\'a ce que la totalite des paiens soit entree dans l\'Eglise');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 26, 'et, apres cela, tout Israel sera sauve, comme il est ecrit : De Sion viendra le Liberateur Et il eloignera de Jacob l\'impiete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 27, 'Telle est l\'alliance que je conclurai avec eux, Quand j\'effarerai leurs peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 28, 'A regarder a l\'Evangile, ils sont ennemis de Dieu a cause de vous, a regarder a l\'election ils sont des bien-aimes a cause de leurs peres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 29, 'Dieu ne se repent jamais ni des graces qu\'il accorde ni de l\'appel qu\'il adresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 30, 'De meme que vous autrefois vous desobeissiez a Dieu et que maintenant vous avez obtenu misericorde par suite de leur desobeissance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 31, 'eux de meme ont maintenant desobei a cause de la misericorde qui vous est accordee et afin de l\'obtenir maintenant a leur tour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 32, 'Car Dieu a enveloppe tous les hommes dans la desobeissance pour leur faire a tous misericorde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 33, 'O profondeur de la richesse, de la sagesse et de la science de Dieu! Que ses jugements sont insondables! que ses voies sont impenetrables!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 34, 'car : Qui a connu la pensee du Seigneur ; De qui a-t-il pris conseil?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 35, 'et encore : De qui est-il l\'oblige? Et qui doit recevoir de lui en retour?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 11, 36, 'Tout vient de lui ! Tout est par lui ! Tout est pour lui ! Gloire a lui dans l\'eternite ! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 1, 'Je vous exhorte donc, freres, au nom du Dieu des compassions, a lui offrir vos personnes en sacrifice vivant, saint, acceptable ; ce serait de votre part un culte raisonnable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 2, 'Ne suivez pas les errements de notre siecle ; au contraire, que votre esprit se transforme en se renouvelant, de maniere a bien vous penetrer de ce qu\'est la volonte de Dieu : volonte qui est bonne, acceptable, parfaite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 3, 'Ainsi je vous dis a tous, m\'autorisant de la grace qui m\'a ete accordee, de ne pas vous exagerer votre valeur, mais de rester dans les limites d\'une juste appreciation, chacun selon la mesure de foi que Dieu lui a departie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 4, 'car de meme que notre corps, qui est un, a plusieurs membres et que tous les membres n\'ont pas la meme fonction,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 5, 'de meme, tous, tant que nous sommes, nous ne faisons qu\'un seul corps en Christ, et nous sommes tous membres les uns des autres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 6, 'possedant des dons divers chacun selon la grace qui lui a ete accordee ; l\'un a le don de prophetie, en proportion de sa foi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 7, 'l\'autre a, dans son ministere, le don du diaconat ; ici c\'est un docteur qui a le don de l\'enseignement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 8, 'la un predicateur qui a le don de la parole ; le bienfaiteur doit donner avec generosite ; le superieur, presider avec zele ; celui qui est charge des oeuvres de misericorde, remplir ses fonctions avec joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 9, 'Que votre amour soit sans hypocrisie. Ayez horreur du mal ; soyez fermement attaches au bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 10, 'Comme des freres, soyez pleins d\'affection les uns pour les autres ; estimez-vous ; ayez des prevenances reciproques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 11, 'Ne ralentissez pas votre zele, ayez de l\'ardeur d\'ame, mettez-vous au service du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 12, 'Soyez joyeux dans l\'esperance, patients dans l\'affliction, perseverants dans la priere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 13, 'Subvenez aux besoins des fideles. Exercez l\'hospitalite avec empressement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 14, 'Benissez vos persecuteurs, oui, benissez, ne maudissez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 15, 'Soyez heureux avec les heureux, pleurez avec ceux qui pleurent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 16, 'Vivez ensemble en bonne intelligence. Gardez-vous de l\'orgueil ; marchez avec ceux qui sont humbles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 17, 'Ne vous croyez pas meilleurs que vous ne l\'etes. Ne rendez a personne le mal pour le mal. Interessez-vous a ce qui est bien aux yeux de tous les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 18, 'Autant que possible et, dans la mesure ou cela depend de vous, vivez en paix avec tout le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 19, 'Ne vous vengez pas vous-memes, mes bien aimes ; laissez faire la colere dont il est ecrit : La vengeance est a moi, c\'est moi qui retribuerai, dit le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 20, 'Ainsi donc : Si ton ennemi a faim donne-lui a manger, S\'il a soif donne-lui a boire ; En faisant cela tu entasseras sur sa tete des charbons ardents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 12, 21, 'Ne te laisse pas vaincre par le mal, mais sache vaincre le mal par le bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 1, 'Que tout le monde se soumette aux puissances regnantes ; car il n\'y a pas de puissance qui ne vienne de Dieu ; les puissances qui existent sont instituees par Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 2, 'et, de la sorte, celui qui fait de l\'opposition aux puissances resiste a l\'ordre etabli par Dieu, et ceux qui resistent ainsi s\'attirent un jugement severe.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 3, 'En effet, ceux qui gouvernent ne sont pas a redouter pour les bonnes actions, mais pour les mauvaises. Veux-tu ne pas avoir a redouter l\'autorite? Fais le bien et tu recevras ses eloges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 4, 'Car elle est au service de Dieu pour toi, pour ton bien ; mais si tu fais le mal, tremble, car ce n\'est pas pour rien qu\'elle porte l\'epee ; elle est au service de Dieu pour le venger et montrer sa colere a ceux qui font le mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 5, 'Il faut donc se soumettre, non seulement par crainte de cette colere, mais par devoir de conscience.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 6, 'Voila encore pourquoi vous avez des impots a payer ; en effet, ceux qui gouvernent sont des fonctionnaires de Dieu employes particulierement a remplir l\'office qu\'il leur a confie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 7, 'Donnez donc a chacun ce qui lui est du : a qui l\'impot, l\'impot ; a qui la redevance, la redevance ; a qui le respect, le respect ; a qui l\'honneur, l\'honneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 8, 'Ne contractez de dettes envers personne, sauf la dette de l\'amour mutuel. En effet, celui qui aime les autres a accompli la Loi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 9, 'car les commandements : Tu ne seras point adultere, tu ne seras point meurtrier, tu ne seras point voleur, tu ne convoiteras point ou n\'importe quel autre, se resument d\'un seul mot, celui-ci : Tu aimeras ton prochain comme toi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 10, 'L\'amour ne fait point de mal au prochain ; l\'amour est donc l\'accomplissement de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 11, 'Je dis cela, parce que vous savez dans quel temps nous vivons! vous savez que l\'heure est venue de nous reveiller du sommeil! car le salut est maintenant plus proche de nous que lorsque nous sommes devenus croyants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 12, 'La nuit s\'avance ; le jour approche ; laissons donc la les oeuvres de tenebres et revetons les armes de la lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 13, 'Marchons honnetement comme on marche en plein jour ; point d\'orgies, point d\'exces, point d\'impuretes, point de debauches, point de disputes, point de jalousies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 13, 14, 'revetez, au contraire, le Seigneur Jesus-Christ, et ne vous preoccupez pas de la chair pour ne pas en reveiller les passions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 1, 'Accueillez celui dont les convictions sont mal affermies, sans discuter ses opinions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 2, 'Celui-ci a des convictions qui lui permettent de manger de tout ; celui-la, mal affermi, ne mange que des legumes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 3, 'Eh bien, celui qui mange de tout ne doit pas mepriser celui qui ne mange pas de tout, et celui qui ne mange pas de tout juger celui qui mange de tout, car Dieu l\'a aussi accueilli.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 4, 'Qui es-tu toi, pour juger le serviteur d\'autrui? s\'il marche droit, s\'il bronche, c\'est l\'affaire de son maitre ; mais il ne bronchera pas car son maitre est puissant pour le soutenir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 5, 'Celui-ci considere, en effet, un jour comme plus sacre qu\'un autre, celui-la considere tous les jours comme egaux : que chacun ait ici une conviction bien entiere et personnelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 6, 'Celui qui fait attention au jour le fait a cause du Seigneur ; celui qui mange de tout le fait a cause du Seigneur, puisqu\'il rend graces a Dieu, et celui qui ne mange pas de tout le fait a cause du Seigneur et rend, lui aussi, graces a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 7, 'En effet, aucun de nous ne vit pour lui-meme, et aucun ne meurt pour lui-meme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 8, 'car si nous vivons, nous vivons pour le Seigneur ; si nous mourons, nous mourons pour le Seigneur. Soit donc que nous vivions, soit que nous mourions, nous sommes au Seigneur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 9, 'et c\'est pour cela que Christ est mort et est ressuscite, c\'est pour etre le Seigneur des vivants et des morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 10, 'Pourquoi donc, toi, juges-tu ton frere et pourquoi donc, toi, meprises-tu ton frere, quand nous devons tous comparaitre devant le tribunal de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 11, 'En effet, il est ecrit : Aussi vrai que je vis, dit le Seigneur, Devant moi flechira tout genou Et toute langue louera Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 12, 'Par consequent, chacun de nous rendra compte a Dieu pour soi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 13, 'Ne nous jugeons donc plus les uns les autres. mais decidez plutot que vous ne placerez jamais devant votre frere une pierre d\'achoppement, que vous ne lui donnerez jamais une occasion de tomber.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 14, '(Je sais, je suis convaincu en Jesus mon Seigneur que rien n\'est impur en soi, sauf cependant si quelqu\'un pense que telle ou telle chose est impure, alors pour lui elle est impure).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 15, 'Si prenant telle nourriture tu attristes ton frere, tu n\'agis plus avec charite. Ne va pas, a cause de ta nourriture, perdre celui pour lequel Christ est mort ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 16, 'ne faites pas calomnier ce que vous avez de bon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 17, 'On ne realise pas le Royaume de Dieu par ce qu\'on mange et par ce qu\'on boit, mais par la justice, par la paix, par la joie dans l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 18, 'Celui qui sert le Christ de cette maniere est agreable a Dieu et estime des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 19, 'Recherchons donc tout ce qui peut contribuer a la paix et a l\'edification mutuelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 20, 'Ne va pas, pour une question de nourriture, ruiner l\'oeuvre de Dieu. Tout est pur, c\'est vrai ; mais un homme fait le mal quand, par la nourriture qu\'il prend, il devient une cause d\'achoppement ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 21, 'et il fait le bien quand il ne mange pas de viande, quand il ne boit pas de vin, quand il ne fait rien qui risque de faire tomber son frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 22, 'Toi tu as ta conviction, garde-la pour toi seul sous le regard de Dieu. Heureux celui qui ne prononce pas son propre jugement par ce qu\'il approuve!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 14, 23, 'Celui qui, en ayant des doutes, mange de tout, est condamne, parce qu\'il agit sans conviction ; tout ce qu\'on ne fait pas avec conviction est un peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 1, 'Nous devons, nous les forts, supporter les faiblesses de ceux, qui sont moins forts que nous, et nous ne devons pas n\'avoir de complaisance que pour nous-memes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 2, 'Que chacun de nous tache de plaire a son prochain pour lui faire du bien et pour l\'edifier,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 3, 'car le Christ n\'a pas cherche ce qui lui plaisait a lui, mais, comme il est ecrit : Les outrages de ceux qui t\'outragent sont tombes sur moi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 4, 'Or tout ce qui a ete ecrit d\'avance l\'a ete pour notre instruction, afin que la patience et la consolation que nous puisons dans les Ecritures conservent en nous l\'esperance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 5, 'Que le Dieu de patience et de consolation vous donne d\'etre d\'accord entre vous comme le veut Jesus-Christ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 6, 'afin que, d\'un seul coeur et d\'une seule voix, vous donniez gloire a Dieu le Pere de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 7, 'Accueillez-vous donc les uns les autres, comme le Christ vous a accueillis, pour la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 8, 'J\'affirme, en effet, que Christ est devenu le serviteur de ceux de la circoncision pour montrer la veracite de Dieu, pour confirmer les promesses faites a leurs peres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 9, 'et quant aux paiens, ils glorifient Dieu de sa misericorde, comme il est ecrit : C\'est pourquoi je te celebrerai parmi les paiens et je louerai ton nom ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 10, 'et ailleurs il est dit : Rejouissez-vous, paiens, avec son peuple,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 11, 'et encore : Louez le Seigneur, vous toutes les nations ; Celebrez-le, vous tous les peuples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 12, 'Esaie dit aussi : paraitra le rejeton de Jesse, Celui qui s\'elevera pour dominer sur les paiens ; Les paiens espereront en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 13, 'Que le Dieu de l\'esperance vous comble de toute la joie et de toute la paix que donne la foi pour que vous soyez riches d\'esperance par la puissance de l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 14, 'Je suis convaincu, mes freres, je le suis tout le premier, que, vous etes pleins de bonte, doues de beaucoup d\'intelligence, capables de vous avertir les uns les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 15, 'Cependant je vous ai parle severement dans quelques passages et comme pour raviver vos souvenirs : je l\'ai fait en vertu de la grace que Dieu m\'a accordee');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 16, 'd\'etre le ministre de Jesus-Christ aupres des paiens, officiant pour l\'Evangile de Dieu, afin que les paiens lui soient une offrande agreable, etant sanctifiee par l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 17, 'Je puis donc me glorifier en Jesus-Christ de tout ce qui regarde la cause de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 18, 'Car je n\'oserais jamais parler de choses que Christ n\'aurait pas faites par mon ministere pour la soumission des paiens,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 19, 'soit par mes paroles soit par mes actes, par la puissance des miracles et des prodiges, par la puissance de l\'Esprit de Dieu ; c\'est au point que j\'ai rempli de l\'Evangile du Christ le cercle immense qui va depuis Jerusalem jusqu\'a l\'Illyrie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 20, 'et je me suis fait un devoir d\'honneur de ne pas precher l\'Evangile la ou le nom de Christ avait deja ete prononce pour ne pas batir sur un fondement pose par autrui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 21, 'Mais, comme il est ecrit : Ceux auxquels il n\'avait pas ete annonce le verront, Et ceux qui n\'en avaient pas entendu parler le comprendront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 22, 'C\'est pour cela que j\'ai ete si souvent empeche de venir chez vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 23, 'mais aujourd\'hui je n\'ai plus rien a faire dans ces contrees-ci, et comme j\'ai depuis plusieurs annees un tres grand desir d\'aller vous voir et que je dois aller en Espagne,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 24, 'j\'espere vous faire une visite en passant, puis vous me fournirez les moyens d\'aller la-bas apres que j\'aurai satisfait, en partie du moins, le besoin que j\'ai de vous voir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 25, 'Pour le moment je vais a Jerusalem porter un secours aux fideles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 26, 'car la Macedoine et l\'Achaie ont bien voulu organiser une collecte en faveur des fideles de Jerusalem qui sont dans la pauvrete ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 27, 'elles l\'ont bien voulu, dis-je ; mais aussi bien, le leur doivent-elles ; car si les paiens ont eu leur part de leurs biens spirituels, elles doivent a leur tour les assister leurs biens materiels.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 28, 'Quand j\'aurai termine cette affaire et que je leur aurai soigneusement remis la somme, je me rendrai en Espagne en passant par chez vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 29, 'et je sais que je viendrai chez vous accompagne des plus riches benedictions de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 30, 'Mais je vous demande, freres, au nom de notre Seigneur Jesus-Christ et de l\'amour qui unit nos esprits, de lutter avec moi et pour moi dans vos prieres a Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 31, 'afin quil me delivre des incredules de Judee et que la mission que je vais remplir a Jerusalem, soit favorablement accueillie des fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 32, 'Et alors je pourrai aller chez vous dans la joie et, s\'il plait a Dieu, me reposer avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 15, 33, 'Le Dieu de paix soit avec vous tous. Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 1, 'Je vous recommande notre soeur Phoebe, diaconesse de l\'eglise de Kenchrees,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 2, 'pour que l\'accueil que vous lui ferez en notre Seigneur soit digne des fideles, et que vous l\'assistiez dans toute affaire ou elle pourrait avoir besoin de vous. Elle a rendu service a plusieurs personnes, a moi en particulier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 3, 'Saluez Prisca et Aquilas, mes collaborateurs en Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 4, 'qui, pour me sauver la vie, ont expose la leur, et je ne suis pas le seul a les en remercier, mais encore toutes les Eglises des paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 5, 'Saluez aussi l\'Eglise qui s\'assemble dans leur maison. Saluez mon cher Epenete, le premier-ne de l\'Asie en Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 6, 'Saluez Marie, elle s\'est donnee beaucoup de peine pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 7, 'Saluez Andronicus et Junias, mes parents et mes compagnons de captivite ; ils sont distingues entre les apotres et meme ont ete en Christ avant moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 8, 'Saluez Amplias ; il m\'est cher dans le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 9, 'Saluez Urbanus, notre collaborateur en Christ, ainsi que mon cher Stachys.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 10, 'Saluez Apelles, qui est un honnete homme en Christ. Saluez les gens de la maison d\'Aristobule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 11, 'Saluez Herodion, mon parent. Saluez les gens de la maison de Narcisse qui sont au Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 12, 'Saluez Triphaina et Tryphosa, ouvrieres dans l\'oeuvre du Seigneur. Saluez ma chere Persis, elle a bien travaille pour le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 13, 'Saluez Rufus, homme distingue dans le Seigneur, et sa mere que j\'appelle ma mere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 14, 'Saluez Asyncrytus, Phlegon, Hermes, Patrobas, Hermas et les freres qui sont avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 15, 'Saluez Philologus et Julie, Neree et sa soeur, Olympas et tous les fideles qui sont avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 16, 'Saluez-vous les uns les autres d\'un saint baiser. Toutes les Eglises du Christ vous saluent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 17, 'Je vous invite, freres, a prendre garde a ceux qui sement des divisions et des scandales contre la doctrine que vous avez apprise. Evitez-les ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 18, 'car ces gens-la ne servent pas notre Seigneur Christ, mais leur ventre, et, par leurs flatteries et leurs beaux discours, ils seduisent les coeurs des simples.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 19, 'Votre docilite est maintenant partout connue, je me rejouis donc de vous, mais je veux que vous soyez sages pour le bien et innocents devant le mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 20, 'Le Dieu de paix ecrasera bientot Satan sous vos pieds. La grace de notre Seigneur Jesus soit avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 21, 'Timothee, mon collaborateur, vous salue, ainsi que Lucius, Jason, Sosipater, mes parents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 22, 'Moi, Tertius, qui ai ecrit cette lettre, je vous salue aussi en notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 23, 'Gaius, qui me donne l\'hospitalite, ainsi qu\'a toute l\'Eglise, vous salue. Eraste, le receveur de la ville, et le frere Quartus vous saluent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 24, '(Que la grace de notre Seigneur Jesus-Christ soit avec vous tous, amen.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 25, 'A Celui qui peut vous fortifier dans la foi en mon Evangile et en l\'enseignement de Jesus-Christ, conformement a la revelation du mystere cache durant des siecles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 26, 'mais aujourd\'hui devoile et publie dans des ecrits prophetiques par l\'ordre du Dieu eternel, pour amener tous les paiens a obeir a la foi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (45, 16, 27, 'au Dieu qui seul est sage soit gloire par Jesus-Christ a jamais ! Amen. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 1, 'Paul, elu, par la volonte de Dieu, apotre de Jesus-Christ, et le frere Sosthenes, a l\'eglise de Dieu qui est a Corinthe,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 2, 'aux sanctifies en Jesus-Christ, aux fideles elus, et a tous ceux qui invoquent le nom de notre Seigneur Jesus-Christ dans tout ce pays, qui est le notre comme le leur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 3, 'Grace et paix vous soient accordees par Dieu notre Pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 4, 'Je ne cesse de remercier Dieu de la grace qu\'il vous a accordee en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 5, 'Vous avez ete, par lui, combles de richesses ; vous avez recu beaucoup d\'instruction et d\'intelligence,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 6, 'et le temoignage du Christ a ete bien solidement etabli au milieu de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 7, 'Aussi ne vous manque-t-il aucun don de la grace, a vous qui attendez l\'apparition de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 8, 'Dieu, de son cote, vous fortifiera jusqu\'a la fin, et vous serez irreprochables le jour de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 9, 'Ah ! il est fidele, le Dieu qui vous a appeles a la communion avec son fils Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 10, 'Je vous conseille cependant, mes freres, au nom de notre Seigneur Jesus-Christ, d\'etre tous d\'accord dans vos paroles ; qu\'il n\'y ait pas de scission entre vous ; soyez tous bien unis d\'esprit et de sentiment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 11, 'Qu\'ai-je appris, en effet, mes freres, par les fils de Chloe? qu\'il y a de la discorde parmi vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 12, 'Je m\'explique : Voici ce qu\'on vous entend dire: Moi, je suis du parti de Paul!  Moi, de celui d\'Apollos!  Moi, de celui de Kephas !  Moi, de celui de Christ!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 13, ' Est-ce que le Christ est divise ? Est-ce que Paul a ete crucifie pour vous? Est-ce que vous avez ete baptises au nom de Paul?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 14, 'Je rends graces de n\'avoir baptise aucun de vous - (sauf toutefois Crispus et Caius) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 15, 'pour que personne ne puisse dire que vous avez ete baptises en mon nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 16, 'J\'ai bien baptise aussi la maison de Stephanas ; a cela pres, personne d\'autre que je sache.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 17, 'Christ ne m\'a pas fait apotre pour baptiser, mais pour precher l\'Evangile, pour le precher sans aucune des habiletes de la philosophie, afin de ne pas rendre inutile la croix du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 18, 'La predication de la croix, en effet, est folie pour ceux qui vont perir ; pour nous, les sauves, elle est une puissance de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 19, 'car il est ecrit : Je perdrai la sagesse des sages, Et la prudence des prudents je la rendrai vaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 20, 'Ou est le philosophe? ou est le docteur de la Loi? ou est le raisonneur mondain?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 21, 'Dieu n\'a-t-il pas change en folie la sagesse de ce monde ? Puisque Dieu a permis, dans sa sagesse, que le monde ne parvint pas avec sa philosophie a le connaitre, il a bien voulu par une predication appelee folie, sauver les croyants :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 22, 'les Juifs exigent des miracles, les Grecs veulent de la philosophie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 23, 'nous, nous prechons Christ crucifie ; il est pour les Juifs un scandale ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 24, 'il est pour les paiens une folie ; mais pour les appeles, soit Juifs, soit Grecs, il est Christ, puissance de Dieu, sagesse de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 25, 'Ah! cette folie de Dieu, elle est plus sage que ce qui vient des hommes ; cette faiblesse de Dieu, elle est plus forte que ce qui vient des hommes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 26, 'Voyez, mes freres, comment vous avez ete appeles : il n\'y a pas parmi vous beaucoup de philosophes, comme on les appelle, ni beaucoup d\'hommes influents ou de haute naissance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 27, 'mais Dieu a choisi ce qui est insense selon le monde pour confondre les philosophes, il a choisi ce qui est faible selon le monde pour confondre les forts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 28, 'oui, ceux d\'une condition inferieure, les meprises, Dieu les a choisis ; ceux qui n\'etaient rien');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 29, 'pour reduire au neant ceux qui etaient quelque chose, afin qu\'aucun etre mortel ne puisse s\'enorgueillir en sa presence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 30, 'Par lui, vous appartenez a Jesus-Christ, qui, grace a Dieu, est devenu notre philosophie, je veux dire notre justice, notre sanctification et notre redemption,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 1, 31, 'afin, comme dit l\'Ecriture, Que celui qui veut se glorifier se glorifie...au sujet du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 1, 'Et moi, freres, quand je vins a vous, je ne vins pas vous annoncer le temoignage de Dieu avec le prestige de l\'eloquence ou de la philosophie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 2, 'Parmi vous, je n\'ai juge savoir qu\'une seule chose: Jesus-Christ et Jesus-Christ crucifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 3, 'Aussi ai-je passe tout ce temps dans la faiblesse, la crainte, la plus grande humilite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 4, 'Mes discours, mes predications ne tiraient pas leur valeur des arguments de la philosophie, mais ils la tiraient des demonstrations de l\'Esprit et de la puissance divine,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 5, 'afin que votre foi ne reposat pas sur la philosophie des hommes, mais sur la puissance de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 6, 'Nous avons bien notre philosophie, mais nous ne la prechons qu\'aux hommes d\'age mur. C\'est une philosophie qui n\'est pas celle de ce siecle, ni celle des princes de ce siecle qui vont passer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 7, 'Nous prechons une philosophie divine, jusqu\'ici mysterieuse et cachee, et que Dieu avait decretee avant les siecles et cela pour notre gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 8, 'Aucun des princes de ce siecle ne l\'a connue (s\'ils l\'eussent connue, ils n\'auraient pas crucifie le Seigneur de gloire),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 9, 'mais, comme dit l\'Ecriture : ...Ce qu\'aucun oeil n\'a vu, ce qu\'aucune oreille n\'a entendu, Ce qui n\'est monte au coeur d\'aucun homme, Ce que Dieu a prepare pour ceux qui l\'aiment...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 10, 'C\'est a nous que Dieu l\'a revelee par son Esprit ; car l\'Esprit sonde tout, meme les profondeurs de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 11, 'Quel homme sait ce qui se passe dans un homme? Ce n\'est que l\'esprit de cet homme qui est en lui-meme. De meme ce qui est en Dieu, personne ne le sait que l\'Esprit de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 12, 'Nous, nous n\'avons pas recu l\'esprit du monde, mais l\'Esprit qui vient de Dieu (pour que nous sachions les graces que Dieu nous a faites).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 13, 'Ce qu\'il nous revele, nous ne l\'exprimons pas dans les termes qu\'enseigne la philosophie humaine, mais dans ceux qu\'enseigne l\'Esprit ; nous sommes attentifs a exprimer ce qui est spirituel en style spirituel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 14, 'L\'homme qui n\'a que ses facultes naturelles n\'accepte pas les choses de l\'Esprit de Dieu. Il les taxe de folie, il ne peut les connaitre, parce qu\'elles demandent a etre jugees avec l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 15, 'L\'homme spirituel, au contraire, juge de tout et n\'est juge par personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 2, 16, 'Qui connait, en effet, la pensee du Seigneur Pour l\'instruire?  Nous, nous avons la pensee de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 1, 'Quant a moi, freres, je n\'ai pu vous parler jusqu\'a present comme a des hommes spirituels, mais je vous ai parle comme a des hommes charnels, comme a de petits enfants en Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 2, 'Je vous ai donne du lait, et non une nourriture solide ; vous n\'eussiez pu la supporter. Vous ne le pourriez pas encore ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 3, 'vous etes toujours charnels. Il y a entre vous de la jalousie et des querelles ; n\'est-ce pas la preuve que vous etes charnels et que vous agissez dans des vues purement humaines?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 4, 'Quand vous dites, l\'un : Moi, je suis pour Paul ; lautre : Moi, je suis pour Apollos, n\'est-ce pas a la maniere des hommes que vous agissez ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 5, 'Qui est donc Apollos ? qui est Paul? Ce sont des serviteurs par lesquels le Seigneur vous a amenes a la foi, chacun selon la tache qui lui a ete confiee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 6, 'Moi j\'ai plante, Apollos a arrose, mais c\'est Dieu qui a donne la croissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 7, 'Celui qui plante n\'est rien, celui qui arrose n\'est rien, Dieu qui donne la croissance est tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 8, 'Celui qui plante et celui qui arrose sont sur la meme ligne, et chacun recevra sa propre recompense suivant son propre travail.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 9, 'Nous sommes les collaborateurs de Dieu ; vous etes le champ auquel Dieu travaille ; la maison qu\'il construit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 10, 'En architecte prudent et dans la mesure de grace que Dieu m\'a accordee, j\'en ai pose le fondement ; un autre continue la construction. Chacun doit prendre garde a la maniere dont il construit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 11, 'Quant au fondement, personne ne peut en poser un autre que celui qui est deja en place, c\'est-a-dire Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 12, 'Sur ce fondement, que chacun eleve sa construction, en or, en argent, en pierres precieuses, en bois, en foin, en paille,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 13, 'son oeuvre sera mise en lumiere ; on la connaitra, le jour du Seigneur ; le feu la revelera ; oui, le feu prouvera ce que vaut l\'oeuvre de chacun.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 14, 'Celui dont l\'oeuvre, dont la construction restera debout, recevra son salaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 15, 'Celui dont l\'oeuvre sera consumee, la perdra ; lui-meme sera sauve, mais comme on l\'est a travers le feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 16, 'Ne savez-vous pas que vous etes un sanctuaire de Dieu et que l\'Esprit de Dieu demeure en vous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 17, 'Si quelqu\'un detruit le sanctuaire de Dieu, Dieu le detruira lui aussi ; car le sanctuaire de Dieu est sacre et vous l\'etes, vous aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 18, 'Que personne ne se fasse d\'illusion ; si l\'un d\'entre vous croit etre ce que notre siecle appelle sage, qu\'il commence par etre fou pour devenir sage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 19, 'Car la sagesse de ce monde est une folie devant Dieu ; comme il est ecrit: Celui qui surprend les sages dans leurs ruses...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 20, 'et encore : Le Seigneur connait les raisonnements des sages ; Il sait qu\'ils sont vains,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 21, 'Ainsi, que personne ne mette son orgueil dans des hommes, car tout est a vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 22, 'Paul, Apollos ou Kephas, le monde, la vie ou la mort, le present ou l\'avenir, tout est a vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 3, 23, 'vous a Christ, Christ a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 1, 'On doit donc nous considerer comme ministres de Christ et comme administrateurs des desseins caches de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 2, 'D\'ailleurs, ce qu\'on demande a des administrateurs, c\'est de les trouver fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 3, 'Pour moi, il m\'importe peu d\'etre juge par vous ou par un tribunal humain. Je m\'interdis aussi de me juger moi-meme ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 4, 'ma conscience, certes, ne me reproche rien ; mais ce n\'est pas la ce qui me justifie, mon vrai juge est le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 5, 'Ne portez donc pas de jugement premature ; attendez que le Seigneur vienne ; il jettera la lumiere sur les choses cachees dans l\'ombre ; il mettra en plein jour les volontes des coeurs ; alors chacun recevra de Dieu l\'eloge qu\'il merite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 6, 'Si j\'ai fait l\'application de ces principes a Apollos et a moi-meme, freres, c\'est a cause de vous ; pour que vous appreniez, par notre exemple, a ne pas depasser de justes limites et que vous cessiez de vous enfler l\'un au-dessus de l\'autre et aux depens d\'un tiers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 7, 'Qui donc t\'accorde un privilege? que possedes-tu que tu n\'aies recu? Si tu as recu, pourquoi te vanter comme si tu n\'avais pas recu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 8, 'Vous etes donc deja rassasies! vous etes donc deja assez riches! vous etes donc entres sans nous dans le Royaume ! Ah ! que je voudrais que vous y fussiez entres pour qu\'il nous fut possible d\'y entrer avec vous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 9, 'Je pense, en effet, que Dieu a fait de nous autres apotres les derniers des hommes et, pour ainsi dire, de malheureux condamnes a mort, puisque nous sommes donnes en spectacle, comme a l\'amphitheatre, au monde, aux anges et aux hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 10, 'Nous sommes des fous a cause de Christ, vous, des sages en Christ! nous sommes faibles, vous etes forts! vous etes honores, nous sommes obscurs!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 11, 'Jusqu\'a cette heure, nous souffrons la faim, la soif, le denument, nous sommes insultes, nous menons une vie errante, nous travaillons sans relache de nos mains ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 12, 'on nous maudit, et nous benissons ; on nous persecute, et nous supportons ; on nous injurie, et nous exhortons ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 13, 'nous sommes, pour ainsi dire, les balayures du monde, le rebut de tous jusqu\'a present !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 14, 'Je ne vous ecris pas cela pour vous faire honte, mais je vous avertis comme mes enfants bien-aimes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 15, 'Car eussiez-vous des milliers de maitres en Christ, vous n\'avez pas plusieurs peres : c\'est moi qui suis votre pere en Jesus-Christ par la predication de l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 16, 'Je vous en conjure donc, suivez mon exemple ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 17, 'c\'est dans ce but que je vous envoie Timothee, qui est mon fidele et bien-aime fils dans le Seigneur ; il vous rappellera quelle route je suis en Jesus-Christ, et comment j\'enseigne partout dans toutes les Eglises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 18, 'Certaines gens ont eu l\'orgueil de pretendre que je ne viendrai plus vous voir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 19, 'Je viendrai bientot, au contraire, s\'il plait au Seigneur, et je connaitrai les orgueilleux, non a ce qu\'ils disent, mais a ce qu\'ils font.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 20, 'Car le Royaume de Dieu ne se fonde pas avec des paroles, mais avec des actes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 4, 21, 'Que preferez-vous? que je vienne chez vous avec une verge ou avec amour et dans un esprit de douceur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 1, 'On dit partout qu\'il y a chez vous un cas de fornication, et de fornication telle qu\'on n\'en voit pas parmi les paiens : Quelqu\'un vivrait avec la femme de son pere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 2, 'Et vous vous laissez enfler d\'orgueil! et vous ne portez pas plutot le deuil! et vous n\'avez pas chasse de votre sein celui qui a commis une telle action!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 3, 'Eh bien, moi, absent de corps, mais present en esprit, j\'ai deja juge comme si j\'etais present celui qui a peche de la sorte :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 4, 'Au nom de notre Seigneur-Jesus, vous et mon esprit etant reunis, avec le pouvoir de notre Seigneur Jesus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 5, 'je livre un tel homme a Satan pour la mortification de sa chair, afin que son esprit soit sauve au jour du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 6, 'Il n\'est pas beau, votre sujet d\'orgueil! Ne savez-vous pas qu\'un peu de levain fait lever toute la pate?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 7, 'Jetez donc le vieux levain pour etre une pate nouvelle ; aussi bien devez-vous etre sans levain, puisque notre agneau pascal, Christ, a ete immole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 8, 'Celebrons donc la fete, non avec du vieux levain, du levain de mechancete et de perversite, mais avec les pains azymes de la purete et de la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 9, 'Je vous ai ecrit dans ma lettre de ne point avoir de relations avec les impurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 10, 'Je ne parlais pas absolument des impurs qui vivent dans le monde, ou des rapaces, avares ou idolatres, car pour cela il vous faudrait sortir du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 11, 'Mais je vous ai ecrit de ne point avoir de relations avec celui qui, se nommant votre frere, serait ou impur, ou rapace, ou idolatre, ou calomniateur, ou ivrogne, ou avare, de ne pas meme vous mettre a table avec un homme pareil!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 12, 'Est-ce a moi de juger les gens du dehors? Ce sont ceux du dedans, n\'est-ce pas, que vous avez a juger?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 5, 13, 'Ceux du dehors Dieu les jugera. Retranchez le coupable du milieu de vous !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 1, 'Osez-vous bien, quand vous avez une affaire entre vous, en appeler au jugement des paiens et non a celui des fideles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 2, 'Ne savez-vous pas que les fideles jugeront le monde? Et si le monde doit etre juge par vous, seriez-vous incapables de rendre des jugements de moindre importance?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 3, 'Ne savez-vous pas que nous jugerons des anges? Et nous ne jugerions pas des questions d\'interet !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 4, 'Quand vous avez entre vous des questions d\'interet, vous vous adressez a ceux que l\'Eglise meprise ; et vous les constituez Juges !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 5, 'Ainsi, je le dis a votre honte, il n\'y a pas parmi vous un seul homme intelligent qui soit capable d\'etre juge entre ses freres!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 6, 'Mais un frere est en proces avec un frere et cela devant des infideles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 7, 'C\'est deja un bien grand dommage que vous ayez des proces entre vous. Pourquoi ne souffrez-vous pas plutot l\'injustice? Pourquoi ne vous laissez-vous pas plutot depouiller?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 8, 'mais c\'est vous-memes qui etes injustes, spoliateurs, et cela envers des freres!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 9, 'Ne savez-vous donc pas que les injustes ne seront point heritiers du Royaume de Dieu? Ne vous y trompez pas : ni impurs, ni idolatres, ni adulteres, ni debauches effemines et infames,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 10, 'ni voleurs, ni rapaces, ni ivrognes, ni calomniateurs, ni avares ne seront heritiers du Royaume de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 11, 'Et cela vous l\'etiez, quelques-uns, mais vous. avez ete purifies ; mais vous avez ete sanctifies ; mais vous avez ete justifies, au nom du Seigneur Jesus-Christ et par l\'Esprit de notre Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 12, 'Tout m\'est permis,  mais tout n\'est pas opportun. Tout m\'est permis,  mais, moi, je ne me laisserai dominer par rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 13, 'Les aliments sont pour l\'estomac et l\'estomac pour les aliments, mais Dieu mettra fin a l\'un comme aux autres. Le corps n\'est pas pour l\'impurete, mais pour le Seigneur et le Seigneur pour le corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 14, 'Dieu a ressuscite le Seigneur et il nous ressuscitera aussi par sa puissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 15, 'Ne savez-vous pas que vos corps sont membres de Christ? Arracherai-je donc les membres du Christ, pour en faire les membres d\'une prostituee?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 16, 'A Dieu ne plaise! Ne savez-vous pas que celui qui s\'unit a une prostituee est un meme corps avec elle? car il est dit : Ils seront deux dans une chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 17, 'Celui qui s\'unit au Seigneur est un meme esprit avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 18, 'Fuyez l\'impurete! Tout autre peche que l\'homme commette, se fait hors du corps ; celui qui se livre a l\'impurete peche contre son propre corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 19, 'Ne savez-vous pas que vos corps sont le temple du saint Esprit qui est en vous, et que vous tenez de Dieu, et que vous n\'etes plus a vous-memes ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 6, 20, 'Car vous avez ete achetes a un grand prix. Glorifiez donc Dieu dans vos corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 1, 'Voici ma reponse aux divers sujets de votre lettre : L\'homme fait bien de renoncer au mariage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 2, 'Cependant, pour eviter tout dereglement, que chaque homme ait sa femme, que chaque femme ait son mari ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 3, 'que le mari remplisse ses devoirs envers sa femme, et la femme envers son mari.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 4, 'Le corps de la femme ne lui appartient pas, il est a son mari. De meme le corps du mari ne lui appartient pas, il est a sa femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 5, 'Ne refusez pas d\'etre l\'un a l\'autre, sauf d\'un commun accord et momentanement pour vaquer a la priere ; puis retournez ensemble, de peur que Satan ne se serve de votre incontinence pour vous tenter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 6, 'En parlant ainsi, je vous fais une concession, je ne vous donne pas d\'ordre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 7, 'Je voudrais que tous les hommes fussent comme moi : mais chacun recoit de Dieu son don particulier, l\'un celui-ci, l\'autre celui-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 8, 'Je dis aux hommes non maries et aux veuves : il est bon que vous restiez ainsi, comme moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 9, 'Mais si vous ne pouvez vous astreindre a la continence, mariez-vous. Car il vaut mieux se marier que de bruler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 10, 'A ceux qui sont maries, j\'ordonne ceci (non pas moi, mais le Seigneur) : que la femme ne se separe point de son mari');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 11, '(si elle s\'en trouvait separee, qu\'elle reste sans se remarier ou qu\'elle se reconcilie avec lui) ; que le mari ne repudie point sa femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 12, 'Aux autres je dis (moi, non le Seigneur) : si un frere a pour femme une paienne et qu\'elle consente a vivre avec lui, qu\'il ne divorce pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 13, 'Et si une femme a pour mari un paien et qu\'il consente a vivre avec elle, qu\'elle ne divorce pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 14, 'Car la femme a rapproche des fideles son mari paien ; le frere a rapproche des fideles sa femme paienne ; si cela n\'etait pas, vos enfants seraient hors de l\'Eglise, tandis qu\'ils sont au nombre des fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 15, 'Si la partie paienne veut se separer, qu\'elle se separe! dans ce cas, le frere ou la soeur ne sent pas lies, quoique Dieu vous ait appeles a vivre en paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 16, 'Sais-tu, en effet, femme, si tu ne sauveras pas ton mari? Sais-tu, mari, si tu ne sauveras pas ta femme?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 17, 'En general, chacun doit garder la part que le Seigneur lui a faite, rester la ou il etait quand Dieu l\'a appele. C\'est la ce que j\'ordonne dans toutes les Eglises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 18, 'As-tu ete appele circoncis? ne dissimule pas ta circoncision ; as-tu ete appele incirconcis? ne te fais pas circoncire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 19, 'La circoncision n\'est rien, l\'incirconcision n\'est rien, mais l\'observation des commandements de Dieu...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 20, 'Chacun doit rester la ou il etait quand il a ete appele.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 21, 'As-tu ete appele esclave? ne t\'en soucie pas, et alors meme que tu pourrais te liberer, reste plutot esclave,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 22, 'car l\'esclave appele au Seigneur est l\'affranchi du Seigneur, et de meme, l\'homme libre appele est l\'esclave de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 23, 'Vous avez ete achetes un grand prix ; ne devenez pas esclaves des hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 24, 'et que chacun, mes freres, reste devant Dieu la ou il etait quand il a ete appele.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 25, 'Pour ceux ou celles qui sont vierges, je n\'ai point recu d\'ordre du Seigneur ; mais je donne mon opinion comme etant, par la misericorde du Seigneur, digne de confiance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 26, 'Je crois qu\'il vaut mieux, puisque la crise finale est imminente, oui, je crois qu\'il vaut mieux pour l\'homme rester comme il est.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 27, 'Es-tu engage avec une femme? ne cherche pas a rompre ; n\'as-tu aucun engagement? ne cherche point de femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 28, 'Si cependant tu te maries, ce n\'est pas un peche ; et si une vierge se marie, ce n\'est pas un peche. Mais ceux qui font cela auront des souffrances dans cette vie, et moi je voudrais les leur epargner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 29, 'Je vous assure, freres, que le temps est court ; voici ce qui reste a faire : que ceux qui ont des femmes soient comme n\'en ayant pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 30, 'ceux qui pleurent comme ne pleurant pas ; ceux qui se rejouissent comme ne se rejouissant pas ; ceux qui achetent comme ne possedant pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 31, 'ceux qui usent de ce monde, comme n\'en usant absolument pas ; car la figure de ce monde passe!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 32, 'Je voudrais que vous n\'eussiez pas de soucis mondains : or, l\'homme non marie a le souci des affaires du Seigneur ; il cherche a plaire au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 33, 'L\'homme marie a le souci des affaires de ce monde ; il cherche a plaire a sa femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 34, 'Il y a la meme difference entre la femme mariee et la vierge : la vierge a le souci des affaires du Seigneur, afin d\'etre sainte de corps et d\'esprit ; mais la femme mariee a le souci des affaires de ce monde ; elle cherche a plaire a son mari.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 35, 'Je vous dis cela dans votre interet, non pour vous tendre un piege ; je vous le dis en vue des convenances et de ce qui est le plus propre a vous attacher sans distraction au culte du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 36, 'Si cependant quelqu\'un pense qu\'il y a des inconvenients pour sa fille a passer l\'age du mariage, et s\'il croit devoir agir en consequence, qu\'il fasse ce qu\'il voudra! Ce n\'est pas un peche, qu\'on se marie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 37, 'Mais celui qui a pris dans son coeur une resolution inebranlable sans subir aucune contrainte, maitre de faire ce qu\'il veut, oui, qui a decide dans le fond de son coeur de garder sa fille vierge, fait bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 38, 'Ainsi celui qui marie sa fille fait bien, celui qui ne la marie pas fait mieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 39, 'Une femme est liee aussi longtemps que son mari est vivant. Si le mari vient a mourir, elle est libre de se remarier avec qui elle veut, seulement que ce soit dans le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 7, 40, 'Cependant elle est plus heureuse si elle reste comme elle est ; c\'est mon opinion ; et je crois, moi aussi, avoir l\'Esprit de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 1, 'Sur la question des viandes offertes en sacrifice aux idoles, tous, nous le savons, nous sommes eclaires... (Etre eclaire rend orgueilleux, c\'est l\'amour qui edifie ) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 2, ' si quelqu\'un s\'imagine etre eclaire sur quelque chose, il n\'est pas encore eclaire comme il faudrait l\'etre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 3, 'mais si quelqu\'un a de l\'amour pour Dieu, Dieu est eclaire sur lui ...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 4, 'Ainsi donc sur la question : Peut-on manger des viandes offertes en sacrifice aux idoles ? nous savons qu\'il n\'y a pas d\'idoles au monde, et qu\'il n\'y a qu\'un seul Dieu, qu\'il n\'y en a pas d\'autre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 5, 'Il y a bien de pretendus dieux qui seraient les uns au ciel, les autres sur la terre, et il y aurait ainsi plusieurs dieux et plusieurs seigneurs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 6, 'mais nous n\'avons qu\'un seul Dieu, le Pere, d\'ou tout vient et pour lequel nous sommes ; nous n\'avons qu\'un seul Seigneur Jesus-Christ, par lequel tout existe et, nous aussi, nous sommes par lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 7, 'Mais tous ne sont pas eclaires ; quelques personnes ont garde leurs anciennes idees sur l\'idole ; elles croient manger d\'un sacrifice fait a une idole reelle, et, par suite de cette faiblesse, leur conscience perd sa purete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 8, 'Or, ce n\'est pas la maniere dont nous nous nourrissons qui nous rendra agreables a Dieu ; que nous mangions de ceci, que nous ne mangions pas de cela, nous n\'y perdons, nous n\'y gagnons rien ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 9, 'mais prenez garde que votre force meme ne soit la pierre d\'achoppement des faibles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 10, 'si l\'on te voit, toi qui es eclaire, a table dans un temple paien, celui dont la conscience est faible ne sera-t-il pas porte a manger de la viande sacrifiee aux idoles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 11, 'et il se perd alors, parce que toi tu es eclaire, cet homme faible, ce frere pour lequel Christ est mort!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 12, 'En etant ainsi coupables envers des freres, en blessant leur conscience qui est faible, vous vous rendez coupables envers Christ lui-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 8, 13, 'Voila pourquoi, si la viande que je mange scandalise mon frere, je renoncerai pour toujours a manger de la viande, afin de ne pas scandaliser mon frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 1, 'Ne suis-je pas libre? ne suis pas apotre? n\'ai-je pas vu Jesus notre Seigneur? N\'etes-vous pas mon oeuvre dans le Seigneur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 2, 'Si pour d\'autres je ne suis pas apotre, au moins le suis-je pour vous. Car le sceau de mon apostolat c\'est vous qui l\'etes dans le Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 3, 'et mon apologie aupres de ceux qui me mettent en cause, la voila.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 4, 'N\'aurions-nous pas le droit de manger et boire a vos frais?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 5, 'N\'aurions-nous pas le droit de mener partout avec nous une femme soeur comme les autres apotres, comme les freres du Seigneur, comme Kephas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 6, 'Barnabas et moi, sommes-nous seuls a ne pas avoir le droit de nous dispenser de travailler?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 7, 'Qui a jamais fait son service militaire a ses propres frais? Plante-t-on une vigne pour n\'en pas manger le fruit? Fait-on paitre un troupeau sans se nourrir de son lait?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 8, 'Est-ce que je ne vous indique la que les usages du monde? La Loi meme ne dit-elle pas la meme chose?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 9, 'Dans la Loi de Moise il est ecrit : Tu ne muselleras pas le boeuf qui foule le ble. Est-ce que Dieu s\'occupe des boeufs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 10, 'Ne dit-il pas cela exclusivement pour nous? Oui, c\'est pour nous que cette parole a ete ecrite. Elle signifie que celui qui laboure la terre et celui qui bat le ble doivent le faire avec l\'esperance d\'en avoir leur part.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 11, 'Nous avons seme chez vous la semence spirituelle, serait-ce il beaucoup de cueillir quelque chose de votre temporel?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 12, 'Si d\'autres se sont donne ce droit sur vous, ne l\'aurions-nous pas a plus forte raison? Eh bien, nous n\'avons pas use de ce droit ; au contraire, nous supportons tout pour ne creer aucun obstacle a l\'Evangile du Christ!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 13, 'Ignorez-vous que ceux qui font le service sacre sont nourris par le Temple? que ceux qui sont occupes a l\'autel sont les commensaux de l\'autel?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 14, 'De meme le Seigneur a ordonne a ceux qui annoncent l\'Evangile de vivre de l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 15, 'Eh bien, moi, je n\'ai use d\'aucun de ces droits. Je n\'ecris pas cela pour que vous m\'en fassiez jouir desormais, car j\'aimerais mieux mourir que de me laisser enlever ce sujet de gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 16, 'En effet, ce n\'est pas precher l\'Evangile qui est mon sujet de gloire. C\'est une obligation qui m\'est imposee et malheur a moi si je ne preche pas l\'Evangile!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 17, 'Ah! si je le prechais de mon propre mouvement, j\'aurais un merite, mais si je le preche par ordre, c\'est un ministere qui m\'est confie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 18, 'Quel est alors mon merite? c\'est qu\'en evangelisant je preche l\'Evangile gratis, de sorte que je n\'use pas des droits que j\'ai au nom de l\'Evangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 19, 'Etant libre de tous, je me suis fait esclave de tous, pour en gagner un plus grand nombre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 20, 'Je me suis fait Juif pour les Juifs, afin de gagner les Juifs ; a ceux qui sont sous l\'autorite de la Loi, je me suis presente comme etant sous l\'autorite de la Loi (quoique je ne fusse pas sous cette autorite), afin de gagner ceux qui sont, sous l\'autorite de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 21, 'Avec ceux qui ne sont pas sous l\'autorite de la Loi, j\'ai ete sans Loi (quoique je ne fusse pas hors de la vraie Loi de Dieu, etant dans la Loi de Christ), afin de gagner ceux qui ne sont pas sous l\'autorite de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 22, 'Pour les faibles, j\'ai ete faible afin de gagner les faibles. J\'ai ete tout a tous pour en sauver un certain nombre de toutes les manieres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 23, 'Et tout cela je le fais a cause de l\'Evangile, afin d\'en avoir ma part.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 24, 'Ne savez-vous pas que dans les courses du stade tous courent, mais qu\'un seul remporte le prix? Courez de maniere a le remporter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 25, 'Ceux qui doivent concourir aux jeux s\'imposent de rigoureuses abstinences ; et c\'est pour recevoir une couronne perissable ; nous, faisons de meme pour une couronne imperissable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 26, 'Pour moi, voici comment je cours : je ne vais pas a l\'aventure ; voici comment je me bats : je ne frappe pas dans le vide ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 9, 27, 'mais je meurtris mon corps et je le rends esclave, de peur qu\'apres avoir fait le heraut pour les autres je ne sorte de la lice rejete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 1, 'Je ne veux pas vous laisser ignorer, freres, que nos peres ont tous ete sous la nuee, qu\'ils ont tous passe a travers la mer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 2, 'que tous, en Moise, ont recu le bapteme dans la nuee et dans la mer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 3, 'que tous ont mange du meme aliment spirituel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 4, 'que tous ont bu du meme breuvage spirituel (en effet ils buvaient a un rocher spirituel qui les suivait, ce rocher c\'etait le Christ).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 5, 'Cependant la plupart d\'entre eux ne furent point agreables a Dieu, puisque le desert etait jonche de leurs corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 6, 'Ces evenements sont des figures prophetiques pour nous afin que nous ne convoitions point le mal, comme eux l\'ont convoite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 7, 'Ne devenez pas idolatres comme quelques-uns d\'entre eux, ainsi qu\'il est ecrit : Le peuple s\'assit pour manger et boire et ils se leverent pour danser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 8, 'Ne vous adonnez pas a l\'impurete, comme quelques-uns d\'entre eux s\'y sont adonnes, et, en un seul jour, il en tomba vingt-trois mille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 9, 'Ne tentez pas le Seigneur, comme quelques-uns d\'entre eux l\'ont tente, et les serpents les firent perir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 10, 'ne murmurez pas comme quelques-uns d\'entre eux, ont murmure et l\'exterminateur les fit perir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 11, 'Ces evenements sont des figures prophetiques, ils ont ete ecrits pour notre instruction a nous qui vivons a la fin du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 12, 'Que celui donc qui croit etre debout, prenne garde de tomber!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 13, 'Vous n\'avez ete exposes a aucune epreuve surhumaine, et Dieu est fidele, il ne permettra pas que vous soyez eprouves au dela de vos forces, mais, avec l\'epreuve, il vous enverra le moyen d\'en sortir et de pouvoir la supporter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 14, 'Pour ces raisons, mes biens- aimes, fuyez l\'idolatrie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 15, 'Je vous parle comme a des personnes raisonnables, jugez vous-memes de ce que je dis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 16, 'La coupe de benediction que nous benissons n\'est-elle pas une communion avec le sang du Christ? Le pain que nous rompons n\'est-il pas une communion avec le corps du Christ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 17, 'Il n\'y a qu\'un seul pain ; de meme, si nombreux que nous soyons, nous ne formons qu\'un seul corps ; car tous nous avons notre part de ce seul pain.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 18, 'Voyez chez le peuple d\'Israel ; ceux qui mangent des victimes sacrifiees sont, n\'est-ce pas, en communion avec l\'autel?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 19, 'Que veux-je dire? que la viande sacrifiee aux idoles ait quelque valeur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 20, 'que l\'idole ait quelque valeur? Certes non, mais les sacrifices des paiens Sont faits aux demons et non a Dieu, et je ne veux pas que vous soyez en communion avec les demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 21, 'Vous ne pouvez pas boire a la fois a la coupe du Seigneur et a la coupe des demons ; vous ne pouvez prendre part a la table du Seigneur et a la table des demons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 22, 'Voudrions-nous irriter le Seigneur? Sommes-nous plus forts que lui?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 23, 'Tout est permis, mais tout n\'est pas opportun ; tout est permis, mais tout n\'edifie pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 24, 'Que personne ne cherche son propre interet, mais celui d\'autrui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 25, 'Tout ce qu\'on vend a la boucherie, mangez-le sans faire d\'enquete par motif de conscience,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 26, 'car La terre et tout ce qu\'elle contient sont au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 27, 'Si un des paiens vous invite et que vous vouliez y aller, tout ce qui vous est offert, mangez-le sans faire d\'enquete par motif de conscience.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 28, 'Mais si quelqu\'un vous dit : Ceci a ete offert en sacrifice , alors n\'en mangez pas a cause de celui qui vous avertit, et aussi a cause de la conscience :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 29, 'la conscience, dis-je, non la votre, mais celle d\'autrui.  Mais pourquoi ma liberte serait-elle critiquee par une autre conscience que par la mienne?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 30, 'Si je mange avec actions de graces, pourquoi serais-je blame pour une chose dont je rends graces?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 31, ' Soit donc que vous mangiez, que vous buviez, que vous fassiez quoi que ce soit, faites tout a la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 32, 'Ne scandalisez ni les Juifs, ni les Grecs, ni l\'Eglise de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 10, 33, 'comme moi qui, en tout, m\'accommode a tout le monde, ne cherchant pas ce qui m\'est agreable a moi, mais ce qui l\'est au plus grand nombre, afin qu\'ils soient sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 1, 'Imitez mon exemple, comme moi celui de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 2, 'Je vous loue de ce que vous vous souvenez de moi en toutes choses, et de ce que vous observez les instructions que je vous ai transmises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 3, 'Cependant je veux que vous sachiez ceci : Le chef de tout homme, c\'est le Christ ; le chef de la femme, c\'est l\'homme ; le chef du Christ, c\'est Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 4, 'Tout homme qui prie ou prophetise la tete couverte, deshonore son chef,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 5, 'et toute femme qui prie ou prophetise la tete sans voile, deshonore son chef ; c\'est exactement comme si elle etait rasee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 6, 'Si une femme ne met pas de voile, alors qu\'elle se coupe aussi les cheveux ; et s\'il est honteux pour une femme de se couper les cheveux ou de se raser la tete, qu\'elle mette donc un voile!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 7, 'L\'homme ne doit pas se voiler la tete, parce qu\'il est l\'image et la gloire de Dieu ; mais la femme est la gloire de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 8, 'L\'homme n\'a pas ete tire de la femme, mais la femme de l\'homme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 9, 'et l\'homme n\'a pas ete cree a cause de la femme, mais la femme a cause de l\'homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 10, 'Voici pourquoi la femme doit porter sur la tete la marque de sa dependance : a cause des anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 11, 'Neanmoins, dans le Seigneur, la femme n\'existe pas sans lhomme, ni l\'homme sans la femme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 12, 'Car si la femme a ete tiree de l\'homme, l\'homme nait de la femme, et tout vient de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 13, 'Jugez-en vous-memes ; est-il convenable qu\'une femme prie Dieu sans etre voilee?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 14, 'Un sentiment naturel ne vous enseigne-t-il pas qu\'il est honteux a l\'homme de laisser croitre ses cheveux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 15, 'tandis que si la femme les laisse croitre c\'est un honneur, car la chevelure lui a ete donnee en guise de voile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 16, 'Si quelqu\'un aime ces sortes de discussions, nous n\'avons pas cette habitude, et les Eglises de Dieu non plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 17, 'En vous faisant cette recommandation, je ne vous loue pas non plus de ce que vos reunions, loin de vous faire du bien, vous font du mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 18, 'D\'abord, on me dit que lorsque vous vous reunissez en assemblee, il y a des divisions parmi vous. Je le comprends jusqu\'a un certain point ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 19, 'il faut bien qu\'il y ait divers partis parmi vous, pour qu\'on reconnaisse ceux d\'entre vous qui tiennent bon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 20, 'Mais quand vous vous reunissez, ce n\'est pas le vrai repas du Seigneur que vous prenez ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 21, 'quand vous vous mettez a table, chacun commence par prendre la nourriture qui lui appartient, et alors l\'un souffre de la faim et l\'autre mange avec exces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 22, 'N\'avez-vous pas vos maisons, s\'il ne s\'agit que de manger et de boire? ou bien meprisez-vous l\'Eglise de Dieu? voulez-vous insulter les indigents? que dois-je vous dire? faut-il vous louer? sur ce point-la je ne vous loue pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 23, 'Pour moi, j\'ai recu, de la part du Seigneur, ce que je vous ai transmis, savoir que le Seigneur Jesus, dans la nuit ou il fut livre, prit du pain');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 24, 'et, qu\'apres avoir rendu graces, il le rompit et dit: Ceci est mon corps qui est pour vous, faites ceci en memoire de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 25, 'Apres le repas il prit, de meme, la coupe, en disant : Cette coupe est la nouvelle alliance en mon sang, faites ceci, toutes les fois que vous boirez, en memoire de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 26, 'Car toutes les fois que vous mangez ce pain et que vous buvez cette coupe, vous annoncez la mort du Seigneur jusqu\'a ce qu\'il vienne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 27, 'C\'est pourquoi celui qui mangerait le pain ou boirait la coupe du Seigneur indignement, se rendrait coupable envers le corps et le sang du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 28, 'Que chacun s\'examine donc soi-meme et qu\'ensuite il mange du pain et boive de la coupe ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 29, 'car celui qui mange et qui boit, mange et boit son propre jugement s\'il ne reconnait pas le corps du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 30, 'Voila pourquoi il y a parmi vous beaucoup de gens debiles et malades, et que plusieurs personnes meurent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 31, 'Si nous nous jugions nous-memes, nous ne serions pas juges ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 32, 'mais les jugements du Seigneur sont des chatiments qui nous preservent d\'etre condamnes avec le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 33, 'Ainsi donc, mes freres, quand vous vous reunissez pour un repas, attendez-vous les uns les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 11, 34, 'Si quelqu\'un a faim, qu\'il mange chez lui, afin que vos reunions ne tournent pas a votre condamnation. Je reglerai le reste a mon arrivee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 1, 'Sur la question de l\'inspiration, je ne veux pas, freres, vous laisser dans l\'ignorance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 2, 'Vous savez que, lorsque vous etiez paiens, vous vous laissiez entrainer, comme au hasard, vers les idoles muettes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 3, 'Je vous apprends donc que personne, dirige par l\'Esprit de Dieu, ne dit : Maudit Jesus, et personne ne peut dire : Seigneur Jesus que par l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 4, 'Il y a differents dons de la grace, mais l\'Esprit est le meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 5, 'Il y a differentes fonctions, mais le meme Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 6, 'Il y a differentes manieres d\'agir, mais c\'est le meme Dieu qui opere tout en tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 7, 'En chacun l\'Esprit se manifeste par le don le plus utile a tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 8, 'En effet, a lun l\'Esprit donne une parole de sagesse ; a l\'autre le meme Esprit donne une parole de science ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 9, 'le meme Esprit donne a un autre la foi ; a un autre le meme Esprit accorde le don de guerison ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 10, 'un autre opere des miracles ; un autre prophetise ; un autre distingue les esprits ; l\'un parle certaines langues ; l\'autre interprete ces langues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 11, 'Tout cela est l\'ouvrage d\'un seul et meme Esprit, distribuant a chacun ses dons comme il lui plait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 12, 'Il y a un seul corps et plusieurs membres, et tous les membres, malgre leur nombre, ne forment qu\'un corps ; eh bien, il en est de meme pour le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 13, 'Car nous avons tous ete baptises d\'un seul et meme Esprit pour ne former qu\'un corps. Juifs ou Grecs, esclaves ou libres, c\'est un seul et meme Esprit qui a etanche notre soif.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 14, 'Or le corps n\'est pas forme d\'un seul membre, mais de plusieurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 15, 'Si le pied disait : comme je ne suis pas une main, je ne fais pas partie du corps, en ferait-il moins partie pour cela?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 16, 'Et si l\'oreille disait : comme je ne suis pas un oeil, je ne fais pas partie du corps, en ferait-elle moins partie pour cela?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 17, 'Si le corps entier etait l\'oeil, ou serait l\'ouie? s\'il etait l\'ouie, ou serait l\'odorat?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 18, 'Mais Dieu a dispose les membres dans le corps, chacun comme il a voulu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 19, 'Si le tout ne formait qu\'un membre, y aurait-il un corps?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 20, 'Il y a donc plusieurs membres et un seul corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 21, 'L\'oeil ne peut dire a la main : je n\'ai pas besoin de toi ; ni la tete aux pieds: je n\'ai pas besoin de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 22, 'Il y a plus : les membres que l\'on croit les plus faibles sont tres necessaires.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 23, 'Ceux que l\'on croit les moins respectables, sont au fond ceux que nous respectons le plus ; ceux qui ne sont pas decents, sont le plus decemment traites ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 24, 'ceux qui sont decents par eux-memes, n\'en ont pas besoin. Dieu a ainsi arrange le corps, faisant respecter davantage ce qui n\'est pas respectable en soi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 25, 'pour eviter les divisions entre les membres et leur donner un souci reciproque les uns des autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 26, 'Si un membre souffre, tous les membres souffrent avec lui ; si un membre recoit des honneurs, tous les membres en ont de la joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 27, 'Vous, vous etes le corps de Christ et, chacun pour sa part, un de ses membres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 28, 'et ceux que Dieu a places dans l\'Eglise sont premierement les apotres, secondement les prophetes, troisiemement les docteurs, ensuite ceux qui font des miracles, puis ceux qui ont le don de guerison, le don des oeuvres de charite, des soins administratifs, ou le don de parler en langues de toutes sortes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 29, 'Tous sont-ils apotres? tous prophetes? tous docteurs? tous font-ils des miracles?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 30, 'tous ont-ils le don de guerison? tous parlent-ils en langues etrangeres tous savent-ils les interpreter?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 12, 31, 'Ambitionnez les plus grands de tous les dons! Je vais, cependant, vous montrer un chemin qu\'il faut suivre de preference.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 1, 'Quand je parlerais toutes les langues des hommes, quand je parlerais meme toutes celles des anges, si je n\'ai point l\'amour, je ne suis qu\'un airain sonnant ou une cymbale retentissante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 2, 'Quand j\'aurais le don de prophetie, quand je connaitrais tous les mysteres, quand je possederais toute science, quand j\'aurais la foi parfaite pour transporter les montagnes, si je n\'ai pas l\'amour, je ne suis rien!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 3, 'Je distribuerais tous mes biens, je livrerais mon corps aux flammes, si je n\'ai pas l\'amour, cela ne me sert de rien!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 4, 'L\'amour est patient ; l\'amour est bienveillant ; l\'amour ne connait ni l\'envie, ni la presomption, ni l\'enflure ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 5, 'il n\'est pas malhonnete ; il n\'est pas egoiste ; il ne s\'irrite pas ; il ne pense pas a mal ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 6, 'il ne se plait pas a l\'injustice ; il se plait au contraire a la verite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 7, 'il souffre tout ; il croit tout ; il espere tout ; il supporte tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 8, 'L\'amour n\'a pas de decadence. Les propheties? elles disparaitront ; les langues? elles cesseront ; la science? elle disparaitra ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 9, 'car notre science est limitee et notre don de prophetie est limite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 10, 'et quand le parfait viendra, ce qui est limite disparaitra.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 11, 'Lorsque j\'etais enfant, je parlais comme un enfant, je pensais comme un enfant, je raisonnais comme un enfant ; quand je suis devenu homme, j\'ai laisse la les facons de l\'enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 12, 'Aujourd\'hui, nous voyons au moyen d\'un miroir, confusement ; alors, nous verrons face a face. Aujourd\'hui, ma science est limitee ; alors, je connaitrai comme j\'aurai ete connu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 13, 13, 'En somme, il reste la foi, l\'esperance, l\'amour, trois grandes choses: mais la plus grande des trois est l\'amour ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 1, 'proposez-vous l\'amour!, Ambitionnez aussi les dons spirituels, surtout le don de prophetie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 2, 'Celui qui parle en langue etrangere ne parle pas aux hommes, mais a Dieu, car personne ne le comprend ; par l\'inspiration de l\'Esprit, il dit des mysteres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 3, 'tandis que celui qui parle comme prophete s\'adresse aux hommes, il les edifie, les exhorte et les console.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 4, 'Celui qui parle en langue etrangere n\'edifie que lui-meme, tandis que celui qui parle comme prophete edifie l\'Eglise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 5, 'Je veux bien que vous parliez tous en langues etrangeres, mais je prefere que vous parliez comme prophetes. Le prophete est plus grand que celui qui parle en langues etrangeres, sauf le cas ou celui-ci s\'interprete lui-meme, pour que l\'Eglise soit edifiee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 6, 'En quoi vous serai-je utile si, par exemple, je viens chez vous, freres, et que je vous parle en langues etrangeres sans vous parler d\'apres une revelation comme prophete ou selon mon savoir, comme docteur?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 7, 'Il en est comme de ces instruments de musique inanimes, la flute, la cithare ; s\'ils ne rendent pas un son distinct, comment reconnaitre ce qui est joue sur la flute de ce qui est joue sur la cithare?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 8, 'Et si la trompette rend un son confus, qui se preparera au combat?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 9, 'De meme, si votre langue ne donne pas de sons nettement articules, comment savoir ce que vous dites? vous ne ferez que frapper l\'air !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 10, 'Il y a, on le dit du moins, tant de langues differentes dans le monde, et pourtant chacune a sa signification ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 11, 'mais si je ne sais pas le sens de tel idiome, je serai un etranger pour celui qui le parle, et celui qui le parle sera un etranger pour moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 12, 'Il en est de meme de vous, puisque vous ambitionnez le don d\'inspiration, cherchez a y exceller pour l\'edification de l\'Eglise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 13, 'Celui donc qui parle en langue etrangere doit prier pour qu\'il lui soit donne de s\'interpreter ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 14, 'car si je prie en langue etrangere, je prie par une inspiration de l\'Esprit, mais mon bon sens reste sterile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 15, 'Que ferai-je alors? Je prierai par l\'inspiration de l\'Esprit, je prierai aussi avec mon bon sens ; je chanterai par l\'inspiration de l\'Esprit, je chanterai aussi avec mon bon sens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 16, 'Si tu rends gloire a Dieu par l\'inspiration de l\'Esprit, comment veux-tu que celui qui occupe la place du simple auditeur reponde Amen a ton action de graces, puisqu\'il ne sait pas ce que tu dis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 17, 'Ton action de graces est sans doute excellente, mais les autres n\'en sont pas edifies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 18, 'Je parle plus de langues etrangeres que vous tous, et j\'en benis Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 19, 'mais, dans l\'Eglise, j\'aime mieux dire cinq paroles avec mon bon sens pour instruire les autres que dix mille paroles en langues etrangeres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 20, 'Mes freres, ne soyez pas enfants dans vos jugements, ne restez enfants que s\'il s\'agit du mal ; dans vos jugements, soyez virils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 21, 'Il est ecrit dans la Loi : C\'est dans d\'autres langues, C\'est par la bouche d\'etrangers Que je parlerai a ce peuple, Et, meme ainsi, ils ne m\'ecouteront pas, dit le Seigneur!.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 22, 'Il s\'ensuit que les langues etrangeres ne sont pas un signe pour les croyants, mais pour les incredules, et que la prophetie n\'en est pas un pour les incredules, mais pour les croyants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 23, 'Si, par exemple, l\'Eglise est tout entiere reunie dans le meme endroit, et que tous parlent en langues etrangeres, les simples curieux ou les incredules qui entreront ne vous croiront-ils pas tous fous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 24, 'Si, au contraire, tous prononcent des propheties, l\'incredule ou le simple curieux qui entre se voit repris par tous, juge par tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 25, 'les secrets de son coeur sont devoiles ; il se jettera la face contre terre, il adorera Dieu, et il avouera que Dieu est certainement au milieu de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 26, 'Que faire alors, mes freres? Quand vous etes reunis, vous avez l\'un un cantique, l\'autre une lecon, l\'autre une revelation, l\'autre une langue etrangere, l\'autre une interpretation ; eh bien que tout se fasse pour l\'edification.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 27, 'S\'agit-il d\'une langue etrangere? que deux freres, trois au plus, prennent la parole et l\'un apres l\'autre, ensuite qu\'un seul interprete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 28, 'S\'il n\'y a pas la d\'interprete, que les freres se taisent dans l\'assemblee, qu\'ils se parlent a eux-memes et a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 29, 'De meme pour les propheties : que deux ou trois prennent la parole et que les autres jugent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 30, 'Cependant si un frere, assis a sa place, recoit une revelation, celui qui parle doit se taire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 31, 'Vous pouvez tous prophetiser, mais chacun a son tour, pour que tout le monde soit instruit et tout le monde edifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 32, 'Le prophete est maitre de l\'esprit prophetique qui l\'anime ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 33, 'Dieu n\'est pas le Dieu du desordre, mais le Dieu de la paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 34, 'Que vos femmes gardent le silence en public, comme cela se fait dans toutes les communautes des fideles ; elles n\'ont pas la permission de parler ; elles doivent rester soumises, comme dit la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 35, 'Si elles desirent s\'instruire sur quelque detail, qu\'elles interrogent leurs maris chez elles ; il n\'est pas convenable qu\'une femme parle en public.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 36, 'Est-ce de chez vous que la parole de Dieu est partie, ou bien n\'est-elle parvenue qu\'a vous seuls?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 37, 'Si quelqu\'un croit etre un prophete ou un homme inspire, il doit reconnaitre que ce que je vous ecris la est un commandement du Seigneur. Si quelqu\'un veut l\'ignorer, qu\'il l\'ignore!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 38, 'En resume, mes freres, ambitionnez le don de prophetie');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 39, 'et n\'empechez pas qu\'on parle en langues etrangeres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 14, 40, 'mais que tout se passe convenablement et avec ordre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 1, 'Je vous rappelle, mes freres, l\'Evangile que je vous ai preche, que vous avez aussi accepte, dans lequel vous avez aussi persevere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 2, 'et qui aussi vous sauvera si vous le retenez dans les termes memes ou je vous l\'ai preche ; autrement votre foi aurait ete vaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 3, 'Car je vous ai transmis avant tout ce que j\'ai recu, savoir que Christ est mort pour nos peches conformement aux Ecritures,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 4, 'qu\'il a ete enseveli, qu\'il est ressuscite le troisieme jour, conformement aux Ecritures,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 5, 'qu\'il est apparu a Kephas, ensuite aux douze.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 6, 'Ensuite il est apparu a plus de cinq cents freres a la fois, dont la plupart vivent encore aujourd\'hui, et quelques-uns sont morts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 7, 'ensuite il est apparu a Jacques ; ensuite a tous les apotres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 8, 'enfin apres eux tous, il m\'est apparu a moi comme a l\'avorton !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 9, 'Car, moi, je suis le moindre des apotres, je ne suis pas digne d\'etre appele apotre, parce que j\'ai persecute l\'Eglise de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 10, 'Mais, par la grace de Dieu, je suis ce que je suis, et la grace qu\'il m\'a faite n\'a pas ete sterile ; au contraire, j\'ai plus travaille qu\'eux tous ; non pas moi cependant, mais la grace de Dieu qui est avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 11, 'Ainsi donc, que ce soit moi, que ce soit eux, voila ce que, nous prechons, et voila ce que vous avez cru.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 12, 'Eh bien, si l\'on preche que Christ est ressuscite des morts, comment certains d\'entre vous disent-ils qu\'il n\'y a pas de resurrection des morts?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 13, 'S\'il n\'y a pas de resurrection des morts, Christ non plus n\'est pas ressuscite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 14, 'et si Christ n\'est pas ressuscite, vaine est notre predication, vaine votre foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 15, 'Il se trouve meme que nous sommes de faux temoins a l\'egard de Dieu, car nous avons temoigne contre lui en disant qu\'il a ressuscite le Christ quil n\'a pas ressuscite puisque, en effet, les morts ne ressuscitent pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 16, 'Si les morts ne ressuscitent pas, Christ non plus n\'est pas ressuscite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 17, 'Si Christ n\'est pas ressuscite, votre foi est illusoire, vous etes encore dans vos peches');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 18, 'et, par suite, ceux qui se sont endormis en Christ ont peri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 19, 'Si notre espoir dans le Christ ne depasse pas la vie presente, nous sommes les plus a plaindre de tous les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 20, 'Mais maintenant, Christ est ressuscite des morts, le premier de ceux qui se sont endormis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 21, 'En effet, par un homme est venue la mort, par un homme aussi la resurrection des morts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 22, 'car de meme qu\'en Adam tous sont mortels, de meme dans le Christ tous recouvreront la vie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 23, 'mais chacun a son rang : le premier, le Christ, puis ceux du Christ a son apparition ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 24, 'ensuite il y aura la fin, quand il remettra la royaute a Dieu le Pere, apres avoir aneanti toute domination, toute autorite, toute puissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 25, 'Il doit regner, en effet, Jusqu\'a ce qu\'il ait mis tous les ennemis sous ses pieds.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 26, 'Le dernier ennemi aneanti sera la mort,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 27, 'car Il a tout mis sous ses pieds. Mais quand il dira que tout est soumis, il faudra evidemment excepter Celui qui lui aura tout soumis ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 28, 'et quand tout lui sera ainsi soumis, alors le Fils lui-meme se soumettra a Celui qui lui a tout soumis, pour que Dieu soit tout en tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 29, 'Autrement que feraient ceux qui se font baptiser pour les morts ? Si, en tout cas, les morts ne ressuscitent pas, pourquoi se font-ils baptiser pour eux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 30, 'et pourquoi nous, nous exposons-nous a tout moment?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 31, 'Tous les jours je vois la mort en face, aussi vrai, mes freres, que vous etes mon sujet de gloire en Jesus-Christ notre Seigneur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 32, 'Si j\'ai lutte contre des especes de betes fauves a Ephese, a quoi cela me sert-il? Si les morts ne ressuscitent pas, Mangeons et buvons, car demain nous mourrons!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 33, 'Ne vous y trompez pas : Les mauvaises compagnies corrompent les bonnes moeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 34, 'Revenez a votre bon sens, serieusement, et ne pechez pas. Car certains d\'entre vous sont dans l\'ignorance sur Dieu, je le dis a votre honte!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 35, '- Mais, dira quelqu\'un, comment les morts ressusciteront-ils? Avec quel corps reviendront-ils ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 36, ' Insense, ce que tu semes n\'est vivifie qu\'apres avoir traverse la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 37, 'Ce que tu semes n\'est pas le corps qui sera un jour, mais un simple grain, par exemple de froment ou de n\'importe quelle plante.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 38, 'Puis Dieu lui donne le corps qu\'il lui a plu de lui donner, et a chaque semence un corps particulier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 39, 'Toute chair n\'est pas une meme chair ; autre est la chair des hommes, autre la chair des betes, autre la chair des oiseaux, autre des poissons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 40, 'Il y a des corps celestes et des corps terrestres ; autre est la gloire des celestes, autre des terrestres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 41, 'autre est la gloire du soleil, autre la gloire de la lune ; autre la gloire des etoiles ; une etoile meme differe en gloire d\'une autre etoile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 42, 'Il en est de meme de la resurrection des morts : seme corruptible, le corps ressuscite incorruptible ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 43, 'seme humble, il ressuscite glorieux ; seme faible, il ressuscite fort ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 44, 'seme corps materiel, il ressuscite corps spirituel ; s\'il y a un corps materiel, il y a aussi un corps spirituel ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 45, 'c\'est ainsi qu\'il est ecrit : Le premier homme Adam devint une ame vivante, le dernier Adam est devenu un esprit vivifiant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 46, 'Mais ce n\'est pas le corps spirituel qui existe d\'abord, c\'est le corps materiel ; ensuite le spirituel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 47, 'Le premier homme etait de terre, de poussiere le second homme est du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 48, 'Tel celui qui est de poussiere, tels ceux qui sont de poussiere ; et tel celui qui est du ciel, tels seront ceux du ciel ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 49, 'et comme nous avons porte l\'image de celui qui est de poussiere, nous porterons aussi l\'image de celui qui est du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 50, 'Voici ce que j\'affirme, mes freres : la chair et le sang ne peuvent avoir en heritage le Royaume de Dieu, la corruption n\'a pas en heritage l\'incorruptibilite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 51, 'Je vais vous dire une chose restee jusqu\'a present secrete : Tous nous ne mourrons pas, mais tous nous serons transformes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 52, 'en un instant indivisible, en un clin d\'oeil, au son de la derniere trompette. Car la trompette sonnera, les morts ressusciteront incorruptibles, et nous, nous serons transformes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 53, 'Il faut, en effet, que ce corps corruptible revete l\'incorruptibilite et que ce corps mortel revete l\'immortalite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 54, 'et quand ce corps corruptible aura revetu l\'incorruptibilite et que ce corps mortel aura revetu l\'immortalite, alors s\'accomplira ce qui est ecrit : La mort a ete absorbee dans la victoire !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 55, 'Ou est, o mort, ta victoire? Ou est, o mort, ton aiguillon ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 56, '(l\'aiguillon de la mort c\'est le peche, et la puissance du peche, la loi).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 57, 'Rendons grace a Dieu qui nous a donne la victoire par notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 15, 58, 'Ainsi, mes freres bien-aimes, soyez fermes, inebranlables, faites sans cesse des progres dans l\'oeuvre du Seigneur, sachant que votre peine n\'est pas vaine dans le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 1, 'Pour la collecte en faveur des fideles, vous suivrez, vous aussi, les indications que j\'ai donnees aux Eglises de Galatie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 2, 'le premier jour de chaque semaine, chacun de vous mettra quelque chose de cote chez lui, et reunira ainsi ce qu\'il peut donner pour que la collecte ne se fasse pas seulement a mon arrivee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 3, 'Quand je serai la, j\'enverrai au moyen d\'une lettre ceux que vous aurez choisis porter vos offrandes a Jerusalem ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 4, 's\'il convient que j\'y aille moi-meme, ils feront le voyage avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 5, 'Je viendrai chez vous en passant par la Macedoine ; je ne ferai, en effet, que la traverser,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 6, 'mais chez vous il est possible que je m\'arrete et meme que je passe l\'hiver pour que vous me conduisiez la ou j\'irai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 7, 'Je ne veux pas, cette fois, vous voir seulement en passant, et j\'espere rester quelque temps avec vous, s\'il plait au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 8, 'Mais je resterai a Ephese jusqu\'a la Pentecote,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 9, 'car une porte d\'activite s\'y est ouverte pour moi toute grande, et les opposants sont nombreux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 10, 'Si Timothee vient, ayez soin qu\'il n\'ait rien a craindre au milieu de vous, car il travaille a l\'oeuvre du Seigneur, tout comme moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 11, 'Que personne donc ne le meprise, et reconduisez-le en paix pour qu\'il vienne aupres de moi, car je l\'attends avec les freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 12, 'Quant a notre frere Apollos, je l\'ai vivement engage a aller chez vous avec les autres freres ; mais il ne veut decidement pas aller maintenant ; il ira quand il en aura le loisir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 13, 'Veillez, soyez fermes dans la foi, soyez virils, soyez forts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 14, 'Faites, tout dans un esprit d\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 15, 'Encore une priere, mes freres! Vous connaissez la famille de Stephanas ; elle est la premiere de lAchaie qui ait cru, elle s\'est devouee au service des fideles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 16, 'eh bien, ayez de la deference pour des personnes semblables et pour quiconque agit et travaille avec elles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 17, 'Je suis heureux de la visite de Stephanas, de Fortunatus et d\'Achaicus ; ils ont supplee a votre absence ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 18, 'car ils ont tranquillise mon esprit et le votre. Sachez apprecier de tels hommes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 19, 'Les Eglises d\'Asie vous saluent. Aquilas et Prisca vous envoient bien des salutations dans le Seigneur ainsi que l\'Eglise qui s\'assemble dans leur maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 20, 'Tous les freres vous saluent. Saluez vous les uns les autres d\'un saint baiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 21, 'Je vous salue en ecrivant moi-meme : Paul.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 22, 'Si quelqu\'un n\'aime pas le Seigneur, qu\'il soit anatheme ! Maranatha.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 23, 'La grace du Seigneur Jesus soit avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (46, 16, 24, 'Je vous aime tous en Jesus-Christ. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 1, 'Paul, apotre de Jesus-Christ par la volonte de Dieu, et le frere Timothee, a l\'eglise de Dieu qui est a Corinthe et a tous les fideles qui sont dans l\'Achaie entiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 2, 'grace et paix vous soient accordees par Dieu notre Pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 3, 'Beni soit le Dieu et Pere de notre Seigneur Jesus-Christ, Pere des misericordes et Dieu de toute consolation,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 4, 'qui nous console dans toutes nos afflictions, pour que nous puissions, a notre tour, consoler les autres dans toutes leurs afflictions, les consolant comme Dieu nous a consoles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 5, 'Car si les souffrances du Christ debordent sur nous, par le Christ aussi deborde notre consolation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 6, 'Sommes-nous affliges? c\'est pour votre consolation et votre salut ; sommes-nous consoles ? c\'est aussi pour votre consolation que vous trouvez en supportant patiemment des souffrances semblables a nos souffrances,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 7, 'et notre esperance a votre egard est inebranlable. Nous savons qu\'ayant votre part de souffrances, vous aurez aussi votre part de consolations.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 8, 'Nous ne vous laisserons pas ignorer, freres, que nous avons ete persecutes en Asie ; nous avons ete absolument accables ; nos forces etaient a bout, au point que nous desesperions meme de conserver la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 9, 'Nous en avions fait a part nous le sacrifice pour apprendre a ne pas nous confier en nous-memes, mais en Dieu qui ressuscite les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 10, 'C\'est lui qui nous a arraches a ce grand danger de mort ; il nous y arrachera encore, oui, nous l\'esperons, il nous y arrachera a l\'avenir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 11, 'Vous aiderez a cette delivrance par vos prieres ; ainsi, plusieurs personnes nous auront obtenu ce bienfait, et plusieurs personnes en feront l\'objet de leurs actions de graces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 12, 'Notre gloire a nous c\'est le temoignage que nous rend notre conscience de nous etre conduits dans le monde, et surtout a votre egard, avec la saintete et la sincerite que Dieu demande, nullement par la sagesse mondaine, mais par la grace de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 13, 'Nous ne vous ecrivons pas autre chose que ce que vous lisez et connaissez ; et j\'espere que vous reconnaitrez jusqu\'a la fin');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 14, '(une partie d\'entre vous l\'ont deja reconnu) que nous faisons votre orgueil et que vous ferez le notre le jour du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 15, 'Dans cette conviction, je voulais aller d\'abord chez vous pour vous faire une double faveur :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 16, 'je me serais rendu de chez vous en Macedoine ; puis de Macedoine je serais revenu chez vous, et vous m\'auriez aide pour le voyage de Judee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 17, 'Ai-je pris cette resolution avec legerete? est-ce que, dans mes projets, je n\'ecoute que mes caprices? est-ce que je dis tantot oui, tantot non?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 18, 'Ah ! je le declare, par la fidelite de Dieu, la parole que nous vous avons adressee n\'a pas ete tantot oui, tantot non ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 19, 'le Fils de Dieu, Jesus-Christ, que nous vous avons preche, Sylvanus, Timothee et moi, n\'a pas ete tantot oui, tantot non ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 20, 'mais il a ete oui. Toutes les promesses de Dieu sont oui en Jesus-Christ et nous prononcons par lui le mot Amen a la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 21, 'Celui qui nous rend ainsi, vous et nous, fermes pour le Christ, et qui nous a oints, c\'est Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 22, 'Il nous a aussi marques de son sceau, et il a mis, a titre d\'arrhes, son Esprit dans nos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 23, 'Mais si je ne suis pas revenu a Corinthe, j\'en prends Dieu a temoin, sur mon ame, c\'est pour vous epargner ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 1, 24, 'non que nous dominions sur votre foi ; mais nous voulons contribuer a votre joie ; quant a la foi, vous y restez fermes!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 1, 'Je me suis donc decide a ne pas revenir une seconde fois chez vous pour vous attrister');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 2, '(car si je vous attristais, que deviendrais-je, n\'ayant pour m\'egayer que celui que j\'aurais attriste?) ; et je vous ai ecrit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 3, 'Je vous ai ecrit pour qu\'a mon arrivee ceux qui devaient me donner de la joie ne me fissent pas de chagrin ; j\'etais convaincu, et cela de vous tous, que ma joie est aussi la votre a tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 4, 'J\'etais profondement afflige, j\'avais le coeur serre, je pleurais en vous ecrivant, non que je voulusse vous faire de la peine, mais vous montrer l\'amour sans bornes que j\'ai pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 5, 'Si l\'un de vous en particulier a ete une cause de tristesse, ce n\'est pas moi personnellement, non je ne veux pas l\'accabler, c\'est vous tous, en un sens, qu\'il a attristes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 6, 'Ce blame general est pour lui un chatiment suffisant ; aussi vous faut-il au contraire lui accorder sa grace et le consoler de peur qu\'il ne succombe a un trop grand chagrin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 7, 'Prenez donc, je vous prie, une decision charitable a son egard ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 8, 'car j\'avais un but en vous ecrivant, je voulais vous mettre a l\'epreuve et voir si vous m\'obeissiez en toutes choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 9, 'Si vous lui accordez sa grace, il a aussi la mienne ; et, quant a moi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 10, 'si je l\'accorde, c\'est a cause de vous, c\'est en presence du Christ que je l\'accorde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 11, 'et pour que nous ne fassions pas les affaires de Satan dont les desseins nous sont bien connus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 12, 'J\'etais alle a Troas pour annoncer l\'Evangile du Christ, le Seigneur m\'y ouvrait une porte,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 13, 'et cependant je n\'y ai pas eu l\'esprit en repos parce que je n\'y ai pas rencontre mon frere Tite, aussi ai-je pris conge des freres, et suis-je parti pour la Macedoine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 14, 'Graces soient a Dieu qui nous fait sans cesse triompher en Christ et qui, en tout lieu, repand par nous sa connaissance comme on repand un parfum.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 15, 'Car le parfum de Christ c\'est nous qui le sommes pour Dieu et parmi les sauves et parmi ceux qui vont perir ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 16, 'pour ceux-ci cette odeur vient de la mort et va a la mort, pour ceux-la elle vient de la vie et va a la vie ;  (a cette tache qui suffira?) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 2, 17, 'nous ne sommes pas, en effet, comme le grand nombre, nous ne frelatons pas la parole de Dieu, mais c\'est avec sincerite, mais c\'est telle qu\'elle vient de Dieu, c\'est devant Dieu, c\'est en Christ que nous la prechons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 1, 'Nous recommencons, n\'est-ce pas, a faire nous-memes notre eloge? Aurions-nous peut-etre besoin, comme certains individus, de lettres de recommandation pour vous ou de votre part?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 2, 'notre lettre, c\'est vous ; ecrite dans nos coeurs, reconnaissable et lisible pour tout le monde ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 3, 'on sait bien que vous etes une lettre de Christ redigee par nous, ecrite non a l\'encre, mais par l\'Esprit du Dieu vivant, non sur des tables de pierre, mais sur des tables de chair, sur vos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 4, 'Une pareille assurance en Dieu, c\'est du Christ que nous la tenons ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 5, 'par nous-memes nous sommes incapables de rien concevoir, de rien tirer de notre propre fonds,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 6, 'toute notre capacite vient de Dieu, qui nous a rendus capables d\'etre ministres d\'une Nouvelle Alliance, qui n\'est pas de lettre, mais d\'esprit ; car la lettre tue, et l\'esprit vivifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 7, 'Si le ministere de la mort, grave en lettres sur la pierre, a ete si entoure de gloire que les enfants d\'Israel ne pouvaient fixer le visage de Moise, tellement il resplendissait d\'un eclat maintenant aneanti,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 8, 'combien le ministere de l\'Esprit ne sera-t-il pas plus glorieux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 9, 'Si le ministere de la condamnation a ete glorieux, le ministere de la justice sera beaucoup plus glorieux encore ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 10, 'on peut meme dire, dans un sens, que ce qui a ete glorifie autrefois ne l\'a pas ete, en comparaison d\'une gloire superieure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 11, 'Si, en effet, ce qui est aneanti a ete glorieux, a plus forte raison ce qui est eternel sera glorieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 12, 'Cette esperance que nous avons nous remplit de hardiesse');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 13, 'et nous ne faisons pas comme Moise qui se voilait la face pour cacher aux regards des enfants d\'Israel la fin d\'un eclat perissable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 14, 'Leur intelligence s\'obscurcit, et aujourd\'hui encore ce voile est la quand on leur lit l\'Ancienne Alliance ; ils ne voient pas avec evidence que le Christ seul l\'abolit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 15, 'Oui, aujourd\'hui encore, quand on leur lit Moise, il y a un voile sur leur coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 16, 'Lorsqu\'ils se convertissent au Seigneur, ce voile est enleve .');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 17, 'C\'est le Seigneur qui est l\'Esprit ; la ou est l\'Esprit du Seigneur, la est la liberte ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 3, 18, 'et nous tous qui, le visage decouvert, contemplons dans un miroir la gloire du Seigneur, nous nous transformons a son image ; sa gloire devient la notre comme cela doit se faire par l\'Esprit du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 1, 'Voila pourquoi, charges par la misericorde de Dieu d\'un pareil ministere, nous ne faiblissons pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 2, 'au contraire nous repoussons bien loin tout procede secret et honteux, nous ne recourons pas a la ruse, nous ne falsifions pas la parole de Dieu, mais nous publions ouvertement la verite, et ainsi nous nous recommandons nous-memes a toute conscience d\'homme, et cela a la face de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 3, '(S\'il reste encore un voile sur notre Evangile, c\'est pour ceux qui vont perir que ce voile existe,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 4, 'pour les incredules dont le dieu de ce monde a aveugle les pensees, afin qu\'ils ne soient point illumines des splendeurs du glorieux Evangile du Christ, qui est l\'image de Dieu.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 5, 'Car nous ne nous prechons pas nous-memes, mais Jesus-Christ le Seigneur ; et nous-memes nous sommes vos serviteurs a cause de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 6, 'En effet, le Dieu qui a dit : Que des tenebres jaillisse la lumiere, a fait aussi jaillir la lumiere dans nos coeurs, pour que nous fassions resplendir la connaissance de la gloire de Dieu sur la face de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 7, 'Nous portons ce tresor dans des vases d\'argile pour prouver que la puissance debordante de sa vertu vient de Dieu et non pas de nous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 8, 'nous sommes toujours surcharges, jamais ecrases ; tourmentes, jamais desesperes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 9, 'persecutes, jamais vaincus ; jetes a terre, jamais morts ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 10, 'nous portons partout dans notre corps le martyre de Jesus, pour que, dans notre corps, se montre aussi sa vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 11, 'Nous sommes toujours livres vivants a la mort a cause de Jesus pour que, dans la mort de notre chair, se montre aussi sa vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 12, 'Ainsi la mort exerce son pouvoir sur nous et la vie agit en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 13, 'Nous avons le meme Esprit de foi que dans ce passage de l\'Ecriture : J\'ai cru, c\'est pourquoi j\'ai parle. Nous croyons, nous aussi, voila pourquoi nous parlons,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 14, 'sachant que celui qui a ressuscite le Seigneur Jesus nous ressuscitera avec Jesus et nous fera paraitre avec vous devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 15, 'Tout cela a cause de vous et afin que la grace de plus en plus abondante multiplie les actions de graces d\'un plus grand nombre d\'entre vous pour la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 16, 'Aussi nous ne faiblissons pas ; au contraire, si notre etre exterieur tombe en ruines, notre etre interieur se renouvelle de jour en jour ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 17, 'car un moment de legeres afflictions nous vaut une somme immense, incalculable de gloire eternelle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 4, 18, 'a nous qui ne regardons pas aux choses visibles, mais aux invisibles, parce que les choses visibles ne durent qu\'un temps, les invisibles sont eternelles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 1, 'Nous savons, en effet, que si notre maison sur la terre, si notre tente vient a etre detruite, nous avons un edifice fait par Dieu, une maison que la main de l\'homme n\'a point construite, qui est eternelle, qui est dans les cieux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 2, 'Et alors nous soupirons dans cette demeure-ci, ayant un immense desir de revetir celle qui vient du ciel, par-dessus l\'autre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 3, 'si toutefois nous avons deja un vetement, si ce n\'est pas dans la nudite que nous sommes trouves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 4, 'Oui, nous qui vivons dans une tente, nous soupirons, nous sommes accables, parce que nous ne voulons pas etre depouilles de notre vetement, mais mettre l\'autre par-dessus, afin que tout ce qui est mortel soit absorbe par la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 5, 'Celui qui nous a formes a cet avenir c\'est Dieu, et il nous a donne, a titre d\'arrhes, l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 6, 'Nous sommes donc, en tout temps, pleins de courage (nous savons bien que demeurant dans le corps, nous demeurons loin du Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 7, 'car nous marchons par la foi et non par la vue) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 8, 'nous sommes, dis-je, pleins de courage, et nous aimerions mieux sortir du corps et aller demeurer pres du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 9, 'Voila encore pourquoi nous faisons tous nos efforts, que nous en sortions, que nous y restions, pour lui etre agreables.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 10, 'Nous tous, en effet, devons comparaitre devant le tribunal du Christ pour y recevoir, chacun ce qu\'il a merite, d\'apres ce qu\'il a fait,  pendant qu\'il avait un corps,  soit en bien, soit en mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 11, 'Sachant ainsi qu\'il faut craindre le Seigneur, nous cherchons a en persuader les hommes ; quant a Dieu, il nous connait bien, et j\'espere que, au fond de vos consciences, vous nous connaissez bien aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 12, 'Nous ne voulons pas vous recommencer notre eloge, mais vous fournir l\'occasion de faire valoir notre merite et d\'avoir a repondre a ceux pour lesquels l\'apparence est tout et la realite n\'est rien ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 13, '(si, en effet, nous sommes des exaltes, c\'est pour glorifier Dieu ; si nous sommes pleins de bon sens, c\'est pour votre bien ;)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 14, 'ce qui nous retient, c\'est l\'amour du Christ ; etant d\'avis que si un seul est mort pour tous, alors tous sont morts,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 15, 'et lui il est mort pour tous afin que les vivants ne vivent plus pour eux-memes, mais pour celui qui est mort et ressuscite pour eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 16, 'Aussi ne connaissons-nous plus personne selon la chair ; si nous avons connu un Christ selon la chair, nous ne le connaissons plus aujourd\'hui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 17, 'de plus, si quelqu\'un est en Christ, il est une nouvelle creature ; les vieilles choses sont passees ; voyez, tout est devenu nouveau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 18, 'et tout cela vient de Dieu qui nous a reconcilies avec lui par Christ et nous a confie le ministere de la reconciliation ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 19, 'et, en effet, Dieu a reconcilie le monde avec lui en Christ, en ne tenant plus compte des peches des hommes et en nous chargeant de precher la reconciliation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 20, 'C\'est donc pour Christ que nous remplissons les fonctions d\'ambassadeurs, comme si Dieu lui-meme vous exhortait par nous ; c\'est pour Christ que nous vous prions : reconciliez-vous avec Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 5, 21, 'Celui qui n\'a pas connu le peche, il l\'a fait peche pour nous, afin que nous devenions en lui justice de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 1, 'Nous sommes ses collaborateurs ; aussi vous exhortons-nous a ne pas recevoir inutilement cette grace de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 2, '(car il dit : Au temps favorable, je t\'ai exauce, Au jour du salut, je t\'ai secouru ; eh bien, le voici ce temps particulierement favorable, le voici ce jour du salut.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 3, 'Nous tachons de ne jamais scandaliser personne et de ne donner aucune prise a la critique dans notre ministere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 4, 'au contraire nous nous faisons respecter comme ministres de Dieu par une grande patience dans les afflictions, dans les detresses, dans les angoisses,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 5, 'sous les coups, dans les cachots, dans les emeutes, dans les fatigues, dans les veilles, dans les jeunes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 6, 'par la purete, la science, la bienveillance, la douceur, un esprit saint, un amour sincere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 7, 'une predication veridique, une puissance de Dieu! nous servant des armes offensives et defensives de la justice ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 8, 'tantot honores, tantot vilipendes ; tantot diffames, tantot consideres ; tenus pour imposteurs bien que veridiques ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 9, 'pour obscurs quoique bien connus ; pour mourants et voila que nous vivons ; pour gens que Dieu chatie et pourtant nous ne mourons pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 10, 'pour tristes, nous qui sommes toujours dans la joie ; pour pauvres, nous qui enrichissons les autres ; pour denues de tout, nous qui possedons tout!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 11, 'Notre bouche s\'est ouverte pour vous, Corinthiens, notre coeur s\'est elargi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 12, 'vous tenez une grande place dans ce coeur, et vous ne nous en faites aucune dans le votre!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 13, 'Ah! rendez-nous la pareille (je vous parle comme a mes enfants), faites-nous une large place!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 14, '(Ne marchez pas de concert avec les incredules ; la justice peut-elle s\'accorder avec l\'iniquite? la lumiere s\'entendre avec les tenebres?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 15, 'le Christ peut-il s\'unir a Belial? le croyant partager avec l\'incredule?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 16, 'un temple de Dieu peut-il frayer avec des idoles? car nous sommes, nous, un temple du Dieu vivant, selon cette parole de Dieu : J\'habiterai au milieu d\'eux, et j\'y marcherai, je serai leur Dieu, et ils seront mon peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 17, 'C\'est pourquoi Sortez du milieu d\'eux et separez-vous, dit le Seigneur ; Ne touchez pas a ce qui est impur Et moi je vous recevrai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 6, 18, 'Je serai donc pour vous un Pere Et vous serez pour moi des fils et des filles, Dit le Seigneur tout-puissant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 1, 'Ayant de pareilles promesses, bien-aimes, purifions-nous de toute souillure de la chair et de l\'esprit, achevant notre sanctification dans la crainte de Dieu.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 2, 'Accueillez-nous ; nous n\'avons fait tort a personne ; nous n\'avons nui a personne ; nous n\'avons exploite personne ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 3, 'je ne dis pas cela pour vous faire un reproche, car, je l\'ai deja dit, vous etes dans nos coeurs a la vie et a la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 4, 'J\'ai une grande confiance en vous ; je suis tout fier de vous ; je suis extremement console, je nage dans la joie, si grandes que soient nos afflictions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 5, 'En effet, a notre arrivee en Macedoine, nous n\'avons pas eu un moment de repos, nous etions eprouves de toutes manieres: combats au dehors, inquietudes au dedans.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 6, 'Mais le Dieu qui releve ceux qui sont abattus nous a releves par l\'arrivee de Tite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 7, 'par son arrivee et surtout par le bien qu\'il avait recu de vous ; car il nous a parle de votre ardeur, de votre chagrin, de votre zele pour moi ; et j\'en ai eu une immense joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 8, 'Si ma lettre vous a fait de la peine, je ne le regrette pas ; je l\'avais d\'abord regrette (je vois bien que ma lettre vous a fait de la peine, du moins momentanement) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 9, 'aujourd\'hui je suis heureux, non certes de votre chagrin, mais du repentir quil a produit. Vous avez ete attristes selon Dieu, pour que nous ne vous causions aucun tort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 10, 'La tristesse selon Dieu produit une repentance qui conduit au salut et qu\'on ne regrette jamais ; la tristesse selon le monde amene la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 11, 'Voyez plutot : Votre tristesse selon Dieu, quel empressement elle a provoque en vous ; bien plus, quelles excuses, quelle indignation, quelle crainte, quelle ardeur, quel devouement, quelle severite! De toutes facons, vous avez tenu a vous montrer vous-memes innocents dans cette affaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 12, 'Du reste, si je vous ai ecrit a ce sujet, ce n\'etait ni a cause de l\'offenseur, ni a cause de l\'offense, c\'etait afin que votre devouement pour nous eclatat parmi vous a la face de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 13, 'Voila ce qui nous a consoles. A cette consolation s\'est ajoutee une immense joie : la vue de la joie de Tite ; vous lui avez tous mis l\'esprit en repos.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 14, 'Si je vous avais un peu vantes devant lui, je n\'ai pas eu a le regretter ; vous savez que nous vous avons toujours dit la verite ; eh bien, l\'eloge de vous que j\'avais fait a Tite s\'est trouve de meme etre la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 15, 'Son affection pour vous est d\'autant plus grande qu\'il se souvient de votre deference a tous et de la modestie respectueuse de votre accueil.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 7, 16, 'Je suis heureux de l\'entiere confiance que je puis avoir en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 1, 'Nous voulons vous informer, freres, de la grace que Dieu a accordee aux Eglises de Macedoine :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 2, 'tres eprouvees et affligees, d\'une extreme pauvrete, elles ont, avec une profonde joie, repandu a profusion les richesses de leur liberalite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 3, 'Selon leurs moyens, j\'en suis temoin, meme au dela de leurs moyens, spontanement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 4, 'allant jusqu\'aux supplications pour recevoir de nous la faveur de les laisser participer a ce service destine aux fideles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 5, 'et, depassant notre attente, elles se sont donnees elles- memes au Seigneur d\'abord, puis a nous par la volonte de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 6, 'Aussi avons-nous engage Tite a aller achever chez vous, comme il l\'avait commencee, cette oeuvre de charite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 7, 'D\'ailleurs, vous etes partout les premiers, par la foi, par l\'eloquence, par la science, par l\'ardeur du zele, par l\'amour que vous nous temoignez ; soyez aussi les premiers dans cette oeuvre de charite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 8, 'Ce n\'est pas un ordre que je vous donne, je vous cite seulement le zele des autres pour eprouver la sincerite de votre amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 9, ' (Car vous connaissez la grace de notre Seigneur Jesus-Christ qui, etant riche, s\'est fait pauvre pour vous, afin de vous rendre riches par sa pauvrete.) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 10, 'C\'est donc un simple avis que je vous donne, et vous n\'avez pas besoin d\'autre chose, vous qui avez non seulement commence l\'oeuvre les premiers, mais qui, les premiers, l\'avez decidee, et cela des l\'annee derniere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 11, 'Maintenant donc, achevez votre oeuvre, et apportez a la terminer, selon vos moyens, l\'ardeur que vous avez mise a la vouloir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 12, 'Quand on donne de bon coeur, on est bien venu d\'apres ce qu\'on a, non d\'apres ce qu\'on n\'a pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 13, 'car il n\'est pas question de se mettre dans la gene pour mettre les autres a l\'aise ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 14, 'la regle c\'est l\'egalite. Aujourd\'hui votre superflu servira a leurs besoins ; et un jour leur superflu servira a son tour a vos besoins, ainsi s\'etablira l\'egalite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 15, 'comme il est ecrit : Celui qui avait beaucoup ne surabondait pas, et celui qui avait peu ne manquait de rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 16, 'Graces soient rendues a Dieu d\'avoir mis au coeur de Tite le meme empressement pour vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 17, 'car il a accueilli ma priere. Son zele, du reste, est plus ardent encore, et il part de lui-meme pour aller vous voir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 18, 'Avec lui, nous envoyons un frere, loue par toutes les Eglises de ce qu\'il a fait pour l\'Evangile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 19, 'bien plus, delegue par elles pour nous accompagner dans notre voyage avec le produit de la collecte que nous avons faite pour la gloire du Seigneur lui-meme et aussi de bon coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 20, 'Nous tenons a eviter qu\'on puisse dire du mal de nous a propos de l\'abondante collecte que nous venons de faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 21, 'Nous voulons etre irreprochables, non seulement devant le Seigneur, mais aussi devant les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 22, 'Avec eux, nous envoyons aussi un autre de nos freres, dont nous avons eprouve le zele plusieurs fois, en diverses occasions, et qui en a d\'autant plus aujourd\'hui qu\'il est plein de confiance en vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 23, 'Quant a Tite, il est mon collegue et mon collaborateur aupres de vous ; quant a nos freres, ils sont les envoyes des Eglises, la gloire de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 8, 24, 'Prouvez donc et votre charite et l\'eloge que j\'ai fait de vous ; prouvez-les a la face des Eglises!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 1, 'Quant au secours destine aux fideles, il est bien superflu que je vous en ecrive ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 2, 'car je sais votre bonne volonte, et je m\'en fais gloire aupres des Macedoniens : L\'Achaie, leur dis-je, est prete depuis l\'annee derniere, et votre zele a ete un stimulant pour la plupart.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 3, 'Et maintenant je vous envoie les freres pour que l\'eloge que j\'ai fait de vous ne recoive pas en cela un dementi, et que vous soyez prets comme je l\'ai dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 4, 'Car enfin, si les Macedoniens arrivent avec moi et ne vous trouvent pas prets, quelle honte pour nous (et permettez-moi d\'ajouter, pour vous) qu\'une telle confiance!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 5, 'J\'ai donc juge necessaire de prier les freres de prendre les devants aupres de vous, pour vous faire organiser d\'avance la liberalite annoncee, qu\'elle soit prete, qu\'elle soit une vraie liberalite et non une lesinerie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 6, 'Ecoutez ceci : Celui qui seme chichement, recoltera chichement et celui qui seme liberalement, recoltera aussi liberalement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 7, 'Que chacun donne ce qu\'il a decide en son coeur de donner, sans chagrin, sans contrainte ; Dieu aime celui qui donne joyeusement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 8, 'Dieu peut vous combler de toutes ses graces, de sorte qu\'ayant toujours et en tout votre suffisance, vous ayez encore du superflu pour toutes les bonnes oeuvres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 9, 'selon qu\'il est ecrit : Il a repandu, il a donne aux pauvres, Sa justice demeure a toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 10, 'Celui qui fournit La semence au semeur, Et le pain dont il se nourrit vous fournira, vous multipliera la semence et augmentera les fruits de votre justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 11, 'Vous serez ainsi combles de richesses pour de grandes liberalites qui nous feront rendre, a nous, des actions de graces a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 12, 'Car l\'accomplissement de cette oeuvre sainte n\'aura pas seulement pour effet de subvenir aux besoins des fideles, mais encore il y aura un excedent qui consistera en ferventes actions de graces rendues a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 13, 'Ils le glorifieront parce que ce secours leur aura prouve votre soumission, votre adhesion a l\'Evangile du Christ, votre liberalite, votre communion avec eux et avec tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 14, 'Ils prieront pour vous, ils eprouveront de l\'affection pour vous en voyant les graces dont Dieu vous a combles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 9, 15, 'Ah! graces a Dieu pour son ineffable don !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 1, 'C\'est moi Paul qui vous demande au nom de la douceur et de la bonte du Christ,  oui, moi qui ai, a chez vous, des dehors Si modestes et qui, de loin, suis si hardi avec vous');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 2, '- c\'est moi qui vous prie de ne pas m\'obliger, a mon arrivee, de vous traiter avec la ferme hardiesse dont je me propose d\'user envers certaines gens qui pensent que nous nous conduisons d\'apres des considerations charnelles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 3, 'Sans doute nous vivons dans une chair mortelle, mais notre combat n\'est pas charnel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 4, 'nos armes de guerre ne sont pas charnelles ; au contraire elles sont divinement puissantes pour detruire des forteresses,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 5, 'pour renverser des raisonnements, pour abattre toute hauteur qui s\'eleve contre la science de Dieu, pour assujettir toute pensee au joug du Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 6, 'et nous sommes prets a punir toute desobeissance, aussitot que votre obeissance a vous sera parfaite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 7, 'Ah! ce sont les dehors que vous regardez? Eh bien, si quelqu\'un croit etre du parti de Christ, qu\'il soit bien persuade d\'une chose : s\'il est du parti de Christ, nous en sommes nous aussi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 8, 'Et quand j\'aurais ete un peu trop fier du pouvoir que nous tenons du Seigneur (pour vous edifier, non pour vous ruiner), je n\'aurais pas a en rougir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 9, 'pour ne pas avoir l\'air de vouloir vous effrayer par mes lettres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 10, 'Ses lettres, dit-on, sont severes, sont energiques, mais, quand il est la, c\'est un homme faible, et sa parole est bien peu de chose.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 11, ' Que celui qui parle ainsi soit bien convaincu de ceci : le langage que nous tenons de loin dans nos lettres, nous le tiendrons de pres dans nos actes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 12, 'Nous ne sommes pas assez oses pour nous confondre ou nous comparer avec certaines gens qui font leur propre eloge. Ceux-la se mesurent a leur propre mesure, ils se comparent a eux-memes ; ils sont peu intelligents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 13, 'Nous, nous ne nous vanterons pas sans mesure ; nous resterons dans le ressort, dans le champ d\'action dont Dieu nous a trace la limite et qui nous a permis, entre autres, de parvenir jusqu\'a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 14, 'Nous n\'etendons pas trop notre domaine, comme nous le ferions si nous n\'etions pas arrives jusqu\'a vous, car c\'est jusque chez vous que nous sommes alles precher l\'Evangile du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 15, 'Nous ne nous vantons pas sans mesure en nous parant des travaux d\'autrui ; nous esperons, au contraire, que votre foi se developpera assez pour que nous puissions agrandir considerablement notre domaine en partant de chez vous');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 16, 'et en suivant toujours nos limites ; et alors nous irons evangeliser au dela de chez vous, sans jamais nous vanter du champ d\'action d\'un autre et des travaux qu\'il a termines.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 17, 'Celui qui veut se glorifier doit se glorifier dans le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 10, 18, 'Ce n\'est pas celui qui se loue lui-meme, qui aura bien soutenu l\'epreuve, mais celui que le Seigneur loue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 1, 'Ah! que je voudrais vous voir supporter un moment mes folies ; mais, oui, vous me supportez,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 2, 'parce que je vous aime avec jalousie, avec une sainte jalousie ; et je vous ai fiances a un seul epoux, au Christ, pour vous presenter a lui comme une vierge pure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 3, 'Mais j\'ai bien peur que, semblables a Eve seduite par les ruses du serpent, vos pensees ne soient corrompues et n\'aient perdu leur candeur pour Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 4, 'Si, en effet, on vient vous precher un autre Jesus que le notre, si on vous fait accepter un Esprit different de celui que vous avez recu, ou un Evangile different de celui que vous avez accueilli, vous supportez cela parfaitement!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 5, 'Je crois, cependant, que je ne suis nullement au-dessous de ces archi apotres !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 6, 'Si je parle comme un homme du peuple, je ne suis pas sans instruction ; et je vous l\'ai bien montre en tout et pour tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 7, 'Ai-je fait une faute en m\'humiliant moi-meme pour vous elever, je veux dire en vous annoncant l\'Evangile de Dieu gratis?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 8, 'J\'ai depouille d\'autres Eglises, j\'ai accepte d\'elles un salaire pour pouvoir vous servir');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 9, 'et quand j\'etais chez vous, m\'etant trouve dans la gene, je ne vous en ai pas importunes ; ce sont les freres venus de Macedoine qui m\'ont donne ce qui me manquait. Je me suis garde jusqu\'au bout de vous etre a charge, et je m\'en garderai a l\'avenir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 10, 'Je prends Christ a temoin que cette gloire-la ne me sera pas enlevee dans les pays d\'Achaie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 11, 'Pourquoi? parce que je ne vous aime pas? Ah! Dieu le sait!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 12, 'Mais j\'agis et j\'agirai toujours ainsi pour oter tout pretexte a ceux qui ne veulent qu\'un pretexte pour se dire mes egaux dans ce detail auquel ils tiennent tant!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 13, 'Ces hommes-la ce sont de faux apotres, des ouvriers trompeurs, ils se deguisent en apotres de Christ ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 14, 'ce n\'est pas etonnant ; Satan se deguise bien en ange de lumiere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 15, 'il n\'est donc pas surprenant que ses ministres se deguisent en ministres de justice ; leur fin sera digne de leurs oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 16, 'Je le repete, personne ne doit me croire fou ; ou bien, convenons-en, je suis un fou, alors laissez-moi chanter un peu ma gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 17, 'Ce que je vais dire, je ne le dirai pas selon le Seigneur, je parlerai comme a un fou ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 18, 'j\'ai la pretention de chanter ma gloire ; il y en a tant qui chantent la leur, chantons aussi la notre!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 19, 'Vous qui etes si sages, vous savez etre indulgents pour les fous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 20, 'vous avez une etonnante patience avec ces gens qui vous asservissent, qui vous mangent, qui vous pillent, qui vous regardent de haut en bas, qui vous frappent au visage !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 21, 'Je suis honteux de le dire, mais nous nous sommes montres bien faibles. Si ces gens-la se mettent en avant, moi, dans ma folie, je le fais comme eux ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 22, 'ils sont Hebreux? moi aussi je le suis ; ils sont Israelites? moi aussi je le suis ; ils sont de la race d\'Abraham? moi aussi j\'en suis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 23, 'Ils sont ministres de Christ? (ici ma folie depasse toute mesure), je suis bien plus ; oui, bien plus qu\'eux par mes immenses travaux, par les innombrables coups que j\'ai recus, par mes emprisonnements multiplies, par les mille morts que j\'ai souffertes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 24, 'Cinq fois les Juifs m\'ont applique leurs quarante coups moins un ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 25, 'trois fois j\'ai ete batonne, une fois j\'ai ete lapide, trois fois j\'ai fait naufrage ; j\'ai passe toute une nuit et un jour dans l\'abime ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 26, 'et les voyages sans nombre, et les dangers en passant les fleuves, et les dangers du cote des voleurs, et les dangers du cote des Juifs, et les dangers du cote des paiens, et les dangers dans les villes, et les dangers dans la solitude, et les dangers sur mer, et les dangers chez les faux freres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 27, 'et les labeurs, et les fatigues, et les veilles repetees, et la faim, et la soif, et les jeunes prolonges, et le froid, et le denument!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 28, 'Et, sans parler du reste, mes preoccupations quotidiennes! le souci de toutes les Eglises!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 29, 'Qui vient a faiblir que je n\'en souffre? Qui vient a tomber sans que j\'en aie la fievre !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 30, 'S\'il faut me vanter, c\'est de mes souffrances que je me vanterai :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 31, 'Le Dieu et Pere du Seigneur Jesus (Beni soit-il a jamais! ) sait que je ne mens pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 32, 'A Damas, le gouverneur du roi Aretas faisait garder la ville pour m\'arreter,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 11, 33, 'et c\'est par une fenetre, dans un panier, qu\'on me fit descendre le long des murailles, et c\'est ainsi que je parvins a m\'echapper.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 1, 'Me vanter! ah! j\'ai tort ; cependant j\'en viens aux visions et aux revelations du Seigneur:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 2, 'Je connais un homme en Christ qui, il y a de cela quatorze ans (est-ce avec son corps, est-ce hors de son corps, je n\'en sais rien, Dieu le sait), fut enleve jusqu\'au troisieme ciel ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 3, 'et je sais que cet homme (avec son corps ou sans son corps, je n\'en sais rien, Dieu le sait) fut enleve jusqu\'au paradis');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 4, 'et entendit des choses ineffables, qu\'un homme ne doit pas repeter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 5, 'Je me vanterai de cet homme-la ; mais de moi-meme je ne me vanterai pas, sauf toutefois de mes souffrances ; et cependant si je voulais me vanter, je ne serais nullement fou, je dirais la verite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 6, 'mais je m\'en abstiens pour que personne ne me pense superieur a ce qu\'il voit et entend quand je suis present.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 7, 'D\'ailleurs, pour que l\'extraordinaire grandeur de ces revelations ne me jetat pas dans l\'orgueil, il m\'a ete donne une echarde enfoncee en ma chair, c\'est un ange de Satan, occupe a me souffleter, pour m\'empecher de m\'enorgueillir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 8, 'Trois fois j\'ai prie le Seigneur de l\'eloigner de moi, et il m\'a dit : Ma grace te suffit, car la grandeur de ma puissance se montre surtout dans l\'infirmite. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 9, 'Je me vanterai donc bien plus volontiers de mes souffrances afin que la puissance du Christ repose sur moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 10, 'C\'est pourquoi je me complais dans les souffrances, dans les insultes, dans les necessites, dans les persecutions, dans les angoisses pour Christ, car lorsque je suis faible, c\'est alors que je suis fort !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 11, 'Je viens de faire le fou ; vous m\'y avez force ; c\'est vous qui auriez du faire mon eloge. En rien je n\'ai ete inferieur aux archiapotres, et cependant je ne suis rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 12, 'Les signes de l\'apotre, miracles, prodiges, actes de puissance surnaturelle, je les ai operes devant vous, avec une patience qui ne s\'est jamais lassee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 13, 'En quoi avez-vous ete moins favorises que les autres Eglises, si ce n\'est que je ne vous ai pas importunes de mes besoins? Pardonnez-moi cette injustice-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 14, 'Voici la troisieme fois que je suis sur le point de venir chez vous, et cette fois-ci encore je ne vous importunerai pas. Ce que je recherche, ce ne sont pas vos biens, c\'est vous-memes. Ce ne sont pas les enfants qui doivent thesauriser pour les parents, ce sont les parents pour les enfants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 15, 'Quant a moi, je depenserai volontiers tout ce que j\'ai, et je me depenserai moi-meme pour le bien de vos ames, quand meme vous m\'aimeriez d\'autant moins que je vous aime davantage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 16, 'D\'accord, dit-on, je ne vous ai pas ete a directement a charge, mais comme je suis un fripon,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 17, 'je vous ai pris de l\'argent par ruse.  Est-ce que je vous ai fait exploiter par aucun de ceux que je vous ai adresses?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 18, 'J\'ai prie Tite d\'aller chez vous, et j\'ai envoye avec lui le frere que vous savez ; est-ce que Tite vous aurait exploites? n\'avons-nous pas marche selon le meme esprit et dans les memes traces?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 19, 'Vous croyez toujours que nous voulons vous faire notre eloge ? c\'est devant Dieu, c\'est en Christ que nous parlons ; et tout cela, mes bien-aimes, pour votre edification.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 20, 'Ah! je crains bien qu\'a mon arrivee je ne vous trouve pas tels que je voudrais, et que vous, vous ne me trouviez tel que vous ne voudriez pas. Je crains qu\'il n\'y ait parmi vous des disputes, des jalousies, des coleres, des rivalites, des calomnies, des bavardages,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 12, 21, 'de l\'insolence, du desordre ; je crains qu\'a mon arrivee, mon Dieu ne m\'humilie encore a votre sujet, et que je n\'aie a pleurer sur plusieurs de vous qui auront ete pecheurs et qui ne se seront pas repentis de l\'impurete, de la corruption, des debauches auxquelles ils se seront livres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 1, 'Pour la troisieme fois j\'arrive chez vous. Sur la deposition de deux ou de trois temoins se decidera toute affaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 2, 'A mon second sejour j\'ai declare, et aujourd\'hui, de loin, je declare de nouveau a ceux qui ont peche et a tous que si je viens encore a vous, je serai sans pitie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 3, 'Puisqu\'il vous plait de tenter si c\'est vraiment Christ qui parle en moi, vous verrez que, pour vous, il n\'est pas faible, et qu\'au contraire il fait eclater sa puissance parmi vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 4, 'Sans doute il a ete crucifie dans sa faiblesse, mais il est vivant par la puissance de Dieu, et nous, de meme, nous sommes faibles comme lui, mais nous vivrons avec lui et pour vous par la puissance de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 5, 'Examinez-vous vous-memes pour voir si vous etes dans la foi, mettez-vous vous-memes a l\'epreuve ; peut-etre ne reconnaissez-vous pas que Jesus-Christ est en vous? Mais alors l\'epreuve tourne contre vous !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 6, 'J\'espere en tout cas vous amener a reconnaitre qu\'elle ne tourne pas contre nous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 7, 'Cependant nous demandons instamment a Dieu que vous ne fassiez rien de mal, car nous ne tenons pas a montrer que l\'epreuve tourne pour nous ; nous desirons seulement que vous fassiez le bien, dut-on nous considerer comme manquant a notre devoir !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 8, 'Car nous n\'avons aucun pouvoir contre la verite, nous n\'en avons que pour la verite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 9, 'et nous sommes joyeux de passer pour faibles, si vous, vous etes forts. Aussi demandons-nous ceci a Dieu : que vous arriviez a la perfection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 10, 'Si je vous ecris tout cela de loin, c\'est parce que je ne veux pas avoir, de pres, a sevir et a user du pouvoir que je tiens du Seigneur (pour vous edifier et non pour vous ruiner).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 11, 'Enfin, freres, soyez joyeux, tendez a la perfection, acceptez les exhortations, soyez tous d\'accord, vivez en paix, et le Dieu d\'amour et de paix sera avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 12, 'Saluez-vous les uns les autres d\'un saint baiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 13, 'Tous les fideles vous saluent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (47, 13, 14, 'Que la grace du Seigneur Jesus-Christ, l\'amour de Dieu et la communion du Saint-Esprit soient avec vous tous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 1, 'Paul, apotre (non par la grace des hommes ou par une institution humaine, mais par la grace de Jesus-Christ et de Dieu le Pere qui a ressuscite Jesus d\'entre les morts)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 2, 'et tous les freres qui sont avec moi, aux eglises de Galatie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 3, 'Grace et paix vous soient accordees par Dieu le Pere et par notre Seigneur Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 4, 'qui s\'est donne lui-meme pour nos peches afin de nous delivrer de ce monde pervers ou nous vivons, conformement a la volonte de notre Dieu et Pere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 5, 'auquel soit la gloire aux siecles des siecles ! amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 6, 'J\'admire que si vite vous vous detourniez de celui qui vous a appeles par la grace de Christ, pour adopter un autre Evangile!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 7, 'Un autre! il n\'y en a point ; il n\'y a que certains brouillons qui veulent pervertir l\'Evangile du Christ!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 8, 'Eh bien, ecoutez : Si jamais quelqu\'un, fut-ce moi, fut-ce un ange du ciel, venait vous evangeliser autrement que je ne l\'ai fait, qu\'il soit anatheme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 9, 'Je viens de le dire, et je vous le repete : Si quelqu\'un preche autre chose que ce que vous avez appris, qu\'il soit anatheme!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 10, 'Est-ce la faveur des hommes que je cherche ou celle de Dieu? Est-ce aux hommes que je veux plaire? Ah! si j\'en etais encore a plaire aux hommes, je ne serais pas serviteur de Christ!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 11, 'Je vous declare, mes freres, que l\'Evangile qui vous a ete preche par moi n\'est pas d\'origine humaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 12, 'Ce n\'est pas meme des hommes que je l\'ai recu ou appris, c\'est d\'une revelation de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 13, 'Vous avez entendu parler de ma vie passee, quand j\'etais dans le Judaisme. Vous savez que je persecutais a outrance l\'Eglise de Dieu, que je la ravageais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 14, 'Vous savez que je surpassais dans mon Judaisme bon nombre de ceux de mon age et de ma race ; j\'etais plus que fanatique de mes traditions nationales.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 15, 'Mais quand Celui qui m\'a choisi des le sein de ma mere et qui m\'a appele par sa grace,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 16, 'jugea bon de me reveler interieurement son Fils, pour que je fusse son evangeliste parmi les paiens, sur-le-champ, sans prendre conseil de personne');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 17, 'sans aller a Jerusalem, aupres de ceux qui etaient apotres avant moi, je me retirai en Arabie, puis je revins a Damas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 18, 'Ensuite, trois annees plus tard, j\'allai, il est vrai, a Jerusalem, pour faire la connaissance de Kephas ; et je passai quinze jours aupres de lui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 19, 'mais je ne vis aucun des autres apotres, si ce n\'est Jacques, le frere du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 20, 'Ce que je vous ecris, j\'affirme en presence de Dieu que ce n\'est pas un mensonge!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 21, 'Je me rendis ensuite dans les provinces de Syrie et de Cilicie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 22, 'mais j\'etais personnellement inconnu des Eglises de Christ qui sont en Judee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 23, 'elles avaient seulement entendu dire ceci : Notre ancien persecuteur preche maintenant la foi que naguere il voulait detruire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 1, 24, 'et on rendait gloire a Dieu a mon sujet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 1, 'Ensuite, quatorze annees plus tard, je montai de nouveau a Jerusalem, avec Barnabas ; je pris aussi Tite avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 2, 'J\'y montai sur une revelation ; je leur communiquai l\'Evangile tel que je le preche aux paiens. J\'eus des entretiens particuliers avec les plus consideres d\'entre eux, de peur que mes courses passees ou presentes ne fussent inutiles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 3, 'Eh bien, on n\'exigea pas meme que Tite qui, m\'accompagnait, et qui etait Grec, se fit circoncire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 4, 'Cependant, par egard pour les intrus, les faux freres qui s\'etaient glisses parmi nous pour espionner la liberte dont nous jouissons grace a Jesus-Christ et qui voudraient nous remettre en esclavage...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 5, 'Nous ne leur avons pas cede, nous ne nous sommes pas soumis un seul instant, afin que la verite de l\'Evangile fut maintenue pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 6, 'Quant a ceux que l\'on considerait comme des personnages (ce qu\'ils etaient jadis peu m\'importe, Dieu ne fait pas acception de personnes)... ceux, dis-je, qui etaient consideres comme des personnages ne m\'apprirent rien de plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 7, 'Au contraire, voyant que l\'evangelisation des non-circoncis m\'etait confiee, comme l\'etait a Pierre celle des circoncis');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 8, '(car Celui qui a donne a Pierre la force d\'etre apotre des circoncis m\'a donne aussi a moi la force d\'etre apotre des paiens),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 9, 'connaissant, dis-je, la grace qui m\'avait ete accordee, Jacques, Kephas et Jean, qui passaient pour les colonnes de l\'Eglise, nous donnerent la main, a Barnabas et a moi, en signe d\'union, afin que nous fussions, pour les paiens, ce qu\'ils etaient pour les circoncis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 10, 'Ils nous prierent seulement de nous souvenir des pauvres ; aussi me suis-je empresse de le faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 11, 'Quand Kephas vint a Antioche, je lui resistai en face, parce quil avait evidemment tort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 12, 'En effet, avant l\'arrivee de certains individus, envoyes par Jacques, il mangeait avec les paiens ; apres leur arrivee, il se tint a l\'ecart, il s\'isola, parce qu\'il avait peur des partisans de la circoncision. Les autres Juifs firent les hypocrites avec lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 13, 'si bien que Barnabas lui-meme fut entraine dans cette hypocrisie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 14, 'Alors, quand je vis qu\'ils ne marchaient pas dans le droit chemin de la verite de l\'Evangile, je dis a Kephas devant tout le monde : Si toi qui es Juif, tu agis comme les paiens et non comme les Juifs, comment peux-tu obliger les paiens a faire comme les Juifs?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 15, 'Nous autres, nous sommes Juifs de naissance ; nous ne sommes pas des pecheurs comme tous ces paiens...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 16, 'Sachant cependant que l\'homme n\'est pas justifie par les oeuvres de la Loi, mais qu\'il l\'est uniquement par la foi en Jesus-Christ. nous avons cru en Jesus-Christ pour etre justifies par cette foi. Ce n\'est pas par les oeuvres de la Loi, parce que les oeuvres de la Loi &lt;&lt; Ne justifient aucune creatures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 17, 'S\'il se trouve alors que nous, qui cherchons notre justification en Christ, nous sommes aussi des pecheurs, Christ aura ete (ce qu\'a Dieu ne plaise) un ministre de peche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 18, 'Si je rebatis ce que j\'avais abattu, je me constitue moi-meme prevaricateur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 19, 'Pour moi, c\'est par egard pour la Loi que je suis mort a la Loi, afin de vivre a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 20, 'J\'ai ete crucifie avec Christ ; je ne vis plus, c\'est Christ qui vit en moi. Le reste de vie que je traine en la chair, je le vis dans la foi au Fils de Dieu a qui m\'a aime et s\'est livre pour moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 2, 21, 'Je ne veux pas aneantir la grace de Dieu, et si la justice s\'obtient par la Loi, alors Christ est mort pour rien!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 1, 'O Galates insenses! qui est-ce donc qui vous a ensorceles, vous sous les yeux desquels on a fait passer l\'image de Jesus-Christ crucifie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 2, 'Je ne vous poserai qu\'une question : Est-ce pour avoir observe la Loi que vous avez recu l\'Esprit, ou est-ce pour avoir entendu precher la foi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 3, 'Que vous etes donc fous! Vous avez commence par l\'Esprit, et puis vous finissez par la chair!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 4, 'Tant d\'experiences pour rien! Et encore si c\'etait pour rien!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 5, 'Celui qui vous confere l\'Esprit et qui fait des miracles parmi vous, agit-il ainsi parce que vous observez la Loi ou parce que vous avez entendu precher la foi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 6, 'Souvenez-vous qu\'Abraham eut foi en Dieu et cela lui fut compte pour justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 7, 'Sachez donc que ceux qui ont la foi pour principe sont fils d\'Abraham.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 8, 'L\'Ecriture a prevu que Dieu justifierait les paiens par la foi, et elle a d\'avance annonce cette bonne nouvelle a Abraham :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 9, 'Toutes les nations seront benies en toi. Ainsi ceux qui ont la foi sont benis avec Abraham le croyant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 10, 'Ceux qui en restent aux oeuvres de la Loi sont sous le poids d\'une malediction, car il est ecrit : Maudit est quiconque n\'observe pas tout ce qui est ecrit dans le Livre de la Loi de maniere a le pratiquer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 11, 'De plus, il est bien evident que personne n\'est justifie devant Dieu par la Loi, puisque : Le juste vivra par la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 12, 'Or la Loi ne se base pas sur la foi, mais : Celui qui pratiquera ces commandements aura la vie par eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 13, 'Christ nous a rachetes de la malediction de la Loi, en devenant malediction pour nous, (puisqu\'il est ecrit: Maudit est quiconque est pendu au bois),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 14, 'afin que la benediction accordee a Abraham passat aux paiens par Jesus-Christ, et que nous obtenions par la foi l\'Esprit promis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 15, 'Mes freres, quand un contrat est passe en bonne forme (je parle des usages du monde), personne ne peut l\'annuler ou le modifier par addition.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 16, 'Or, c\'est a Abraham que les promesses ont ete faites et a sa posterite. Il n\'est pas dit : et a ses posterites, comme s\'il s\'agissait de plusieurs personnes, mais, comme pour parler d\'une seule : et a ta posterite. Il s\'agit de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 17, 'Eh bien, je dis ceci : Dieu ayant passe un contrat en bonne forme, la Loi venue quatre cent trente ans plus tard, ne peut le casser ; elle ne peut annuler la promesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 18, 'Si on obtient l\'heritage par une Loi, il ne vient plus d\'une promesse. Or ce qu\'Abraham a recu de Dieu, il l\'a recu par une promesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 19, 'Alors, pourquoi la Loi? elle a ete ajoutee a la promesse a cause des transgressions, jusqu\'a la venue de la posterite au sujet de laquelle les promesses avaient ete faites. Elle a ete promulguee par des anges et par le ministere d\'un mediateur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 20, 'Or, un mediateur ne depend pas d\'un seul parti. Dieu au contraire est un!.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 21, 'Y aurait-il alors une contradiction entre la Loi et les promesses? Pas le moins du monde. Ah! sans doute, s\'il avait ete donne une Loi capable de produire la vie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 22, 'alors la justice viendrait de la Loi. L\'Ecriture a au contraire tout enferme sous le peche, afin que la promesse soit realisee, pour les croyants, par la foi en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 23, 'Avant la venue de la foi, la Loi nous tenait prisonniers et nous gardait ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 24, 'nous attendions la foi qui devait etre revelee. La Loi a ete pour nous ce qu\'est un pedagogue ; elle nous a conduits au Christ, pour que nous fussions justifies par la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 25, 'Maintenant que la foi est venue, nous ne sommes plus soumis au pedagogue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 26, 'Car vous etes tous fils de Dieu par la foi en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 27, 'Oui, vous qui avez tous ete baptises pour etre a Christ, vous etes revetus de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 28, 'Il n\'y a plus la ni Juif, ni paien ; ni esclave, ni libre ; ni homme, ni femme ; vous etes tous un en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 3, 29, 'Et si vous etes a Christ, vous etes par consequent de la famille d\'Abraham, vous heritez en vertu de la promesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 1, 'Eh bien, voici ce que j\'affirme : tant que l\'heritier est mineur, il ne differe en rien de l\'esclave. Il possede tout l\'heritage,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 2, 'et cependant il est sous des tuteurs et des administrateurs jusqu\'au temps marque par le pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 3, 'De meme nous, tant que nous etions mineurs, nous etions esclaves des prescriptions du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 4, 'Quand le temps fut accompli, Dieu envoya son Fils, ne d\'une femme, ne sous la Loi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 5, 'pour racheter ceux qui etaient sous la Loi, et pour que nous jouissions des privileges de fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 6, 'Et c\'est parce que vous etes fils, que Dieu a envoye en nos coeurs l\'Esprit de son Fils, s\'ecriant : Abba! c\'est-a-dire Pere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 7, 'Vous n\'etes donc plus esclaves, vous etes fils, et si vous etes fils, vous etes aussi heritiers, grace a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 8, 'Autrefois, ne connaissant pas Dieu, vous serviez des etres qui ne sont pas dieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 9, 'mais maintenant que vous connaissez Dieu, bien mieux! que vous etes connus de Dieu, comment retournez-vous a ces pauvres et miserables commencements, dont vous voulez de nouveau devenir esclaves?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 10, 'Vous observez certains jours, et les lunaisons, et les epoques des fetes, et certaines annees !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 11, 'ah! j\'ai bien peur pour vous de n\'avoir travaille chez vous en pure perte!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 12, 'Faites comme moi ; car moi, j\'ai fait comme vous ; freres, je vous en supplie, jusqu\'ici vous ne m\'aviez fait aucun tort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 13, 'Vous savez comme j\'etais malade la premiere fois que je vous ai annonce l\'Evangile ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 14, 'et vous ne m\'avez pas meprise, vous ne m\'avez pas repousse, quand ma chair passait ainsi par l\'epreuve ; au contraire, vous m\'avez recu comme un ange de Dieu, comme Jesus-Christ !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 15, 'Qu\'est devenue votre joie d\'alors? Je vous rends temoignage que, Si vous l\'aviez pu, vous vous seriez arrache les yeux pour me les donner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 16, 'Et me voila devenu votre ennemi, parce que je vous ai dit la verite !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 17, 'Il y a des gens pleins de zele pour vous, mais non en vue du bien ; ce qu\'ils veulent c\'est vous detacher de moi, pour que vous les aimiez seuls.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 18, 'S\'aimer en vue du bien, c\'est une belle chose ; mais il faut que cela dure, et ne pas seulement m\'aimer quand je suis la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 19, 'Oh ! mes enfants, vous que j\'enfante de nouveau dans la douleur jusqu\'a ce que Christ soit forme en vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 20, 'que je voudrais etre pres de vous dans ce moment, et changer de langage, car je suis bien inquiet de vous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 21, 'Dites-moi, vous qui voulez etre sous la Loi, n\'entendez-vous pas la lecture de la Loi?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 22, 'Il y est ecrit qu\'Abraham avait deux fils, l\'un de sa servante, l\'autre de sa femme libre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 23, 'Celui de la servante etait ne selon l\'ordre de la nature, celui de la femme libre a la suite de la promesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 24, 'Tout cela est allegorique : ces femmes sont deux alliances, l\'une, celle du mont Sinai, a enfante pour la servitude ; c\'est Agar.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 25, '(Le mot Agar designe, en effet, le mont Sinai en Arabie.) Elle correspond a la Jerusalem actuelle');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 26, 'qui est en servitude avec ses enfants. Mais la Jerusalem d\'en haut est libre, c\'est notre mere a nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 27, 'Il est ecrit, en effet : Rejouis-toi, femme sterile, qui n\'as point enfante! Pousse des cris de joie, eleve la voix, toi qui n\'as pas senti les douleurs maternelles, Car les enfants de la femme abandonnee sont plus nombreux Que ceux de la femme mariee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 28, 'Et nous, mes freres, nous sommes les vrais descendants d\'Isaac, les enfants de la promesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 29, 'Mais comme alors le fils ne selon l\'ordre de la nature persecutait celui qui etait ne selon l\'Esprit, il en est encore ainsi aujourd\'hui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 30, 'Mais que dit lEcriture : Chasse la servante et son fils, car le fils de la servante ne doit pas heriter avec le fils de la femme libre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 4, 31, 'Ainsi, mes freres, nous ne sommes pas enfants de la servante, mais de la femme libre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 1, 'Christ nous a liberes ; restez fermes dans cette liberte et ne reprenez pas le joug de l\'esclavage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 2, 'C\'est moi, Paul, qui vous le dis : Si vous vous faites circoncire, Christ ne vous servira de rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 3, 'Je declare encore une fois a tout homme qui se fait circoncire que cet acte l\'oblige a observer toute la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 4, 'Vous etes absolument separes de Christ, vous tous qui cherchez la justification dans la Loi ; vous etes dechus de la grace.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 5, 'Nous, par l\'Esprit, nous attendons de la foi l\'esperance de la justification.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 6, 'Car, en Jesus-Christ, peu importe circoncision ou non-circoncision ; ce qui importe c\'est la foi devenant active par l\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 7, 'Vous couriez si bien! Qui donc vous a arretes, detournes de la verite?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 8, 'Ah! ce conseil ne vient pas de Celui qui vous appelle!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 9, 'Un peu de levain fait lever la pate tout entiere...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 10, 'J\'ai la confiance  dans le Seigneur  que vous reviendrez a penser comme moi... Quant a celui qui vous trouble, il portera la peine de tout ceci, quel qu\'il soit');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 11, 'Et moi, mes freres, je n\'ai qu\'a precher la circoncision, on ne me persecutera plus!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 12, 'mais alors la croix et son scandale sont aneantis Ah ! Je les voudrais plus que circoncis, ceux qui vous troublent');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 13, 'Vous, mes freres, c\'est a la liberte que vous avez ete appeles ; mais que cette liberte ne vous entraine pas vers la chair. Vous devez, en vous aimant, vous servir les uns les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 14, 'Toute la Loi se resume d\'un seul mot : Tu aimeras ton prochain comme toi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 15, 'Et si, les uns les autres, vous vous mordez et vous devorez, prenez garde, les uns par les autres vous vous perdrez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 16, 'Marchez, vous dis-je, sous la direction de l\'Esprit et resistez aux passions de la chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 17, 'La chair, en effet, conspire contre l\'Esprit, et l\'Esprit contre la chair ; tous deux sont en lutte pour que vous ne fassiez pas ce que vous voudriez faire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 18, 'Mais si l\'Esprit vous conduit, vous n\'etes plus sous le joug de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 19, 'Les oeuvres de la chair sont faciles a reconnaitre ; c\'est la fornication, l\'impurete, la debauche,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 20, 'l\'idolatrie, la sorcellerie, les haines, les disputes, l\'envie, la colere, les querelles, les factions, l\'esprit de parti,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 21, 'les jalousies, l\'ivresse, les orgies et autres choses semblables, et, je vous le declare d\'avance (je vous en ai deja prevenus), ceux qui agissent ainsi n\'auront pas en heritage le Royaume de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 22, 'Le fruit de l\'Esprit, au contraire, est l\'amour, la joie, la paix, la patience, l\'indulgence, la bonte, la fidelite, la douceur, la temperance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 23, 'Pour ceux qui vivent ainsi, il n\'y a pas de Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 24, 'Ceux qui sont a Jesus-Christ ont crucifie leur chair avec ses passions et ses desirs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 25, 'Si nous vivons par l\'Esprit, marchons aussi avec l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 5, 26, 'Ne devenons pas vains et vaniteux en nous provoquant les uns les autres, et en nous portant mutuellement envie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 1, 'Mes freres, lors meme que vous surprenez un homme en faute, vous qui etes diriges par l\'Esprit, corrigez-le avec douceur ; veille sur toi-meme, que tu ne sois pas tente, toi aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 2, 'Supportez mutuellement le poids de vos fautes et accomplissez ainsi la loi du Christ ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 3, 'car s\'imaginer valoir quelque chose, quand on n\'est rien, c\'est se faire illusion a soi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 4, 'Que chacun examine ses propres actes, et alors il gardera ses motifs de vanite pour lui seul, et il ne les gardera plus pour autrui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 5, 'Chacun, en effet, aura son propre fardeau a porter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 6, 'Celui a qui l\'on enseigne la Parole de Dieu, doit faire part de tous ses biens a celui qui enseigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 7, 'Ne vous y trompez pas, on ne se moque pas de Dieu ; ce que l\'homme seme, il le recolte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 8, 'Celui qui seme dans sa chair, recoltera ce que produit la chair: la mort ; celui qui seme dans l\'Esprit, recoltera ce que produit l\'Esprit: la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 9, 'Ne nous, lassons pas de faire le bien et, le moment venu, nous recolterons, si nous avons tenu bon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 10, 'Maintenant que nous en avons l\'occasion, faisons du bien a tout le monde, et surtout a nos freres dans la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 11, 'Remarquez ces grands caracteres, ils sont de ma main.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 12, 'Des gens qui veulent plaire par la chair, voila ceux qui vous forcent a vous faire circoncire, et seulement pour n\'etre pas persecutes au nom de la croix du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 13, 'Car ces circoncis n\'observent pas la Loi, mais ils veulent vous faire circoncire pour tirer gloire de votre chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 14, 'Pour moi, Dieu me garde de me glorifier, sauf de la croix de notre Seigneur Jesus-Christ. Par lui le monde est crucifie pour moi, comme je le suis pour le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 15, 'Il ne s\'agit ni d\'etre circoncis, ni de ne pas l\'etre, mais d\'etre cree a nouveau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 16, 'Sur tous ceux qui observeront cette regle, paix et misericorde, ainsi que sur l\'Israel de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 17, 'Qu\'a l\'avenir personne ne me fasse de la peine, car je porte sur mon corps les stigmates de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (48, 6, 18, 'La grace de notre Seigneur Jesus-Christ soit avec votre esprit, freres. Amen ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 1, 'Paul, apotre de Jesus-Christ par la volonte de Dieu, aux fideles et aux croyants en Jesus-Christ qui sont a Ephese.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 2, 'Grace et paix vous soient accordees par Dieu notre Pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 3, 'Beni soit le Dieu et Pere de notre Seigneur Jesus-Christ, car il nous a combles par Christ et dans les cieux de toutes sortes de benedictions spirituelles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 4, 'il nous a elus en lui avant la creation du monde, pour nous sanctifier et nous rendre irreprochables devant lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 5, 'Dans son amour, il a decide d\'avance que nous serions ses enfants par Jesus-Christ ; tel a ete son bon plaisir, telle sa volonte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 6, 'Honneur en soit a la grace magnifique qu\'il nous a faite en son bien-aime !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 7, 'C\'est en lui, par son sang, que nous avons la Redemption, la remission des peches, tant est riche sa grace,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 8, 'celle qu\'il a repandue sur nous, en nous donnant toute sagesse et toute intelligence ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 9, 'il nous a devoile le secret de sa volonte, ce dessein qu\'il avait forme a part lui');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 10, '(et qu\'il devait faire eclater le moment venu) de tout reunir en Christ, seul chef, ce qui est sur la terre, comme ce qui est dans le ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 11, 'C\'est aussi par le Christ que nous appartenons a Dieu, designes a cela d\'avance, predestines par Celui qui fait tout a son gre et a sa volonte,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 12, 'pour que nous servions a le louer et a le glorifier, nous qui les premiers avons espere en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 13, 'C\'est aussi par le Christ que vous avez entendu la parole de verite, l\'Evangile qui vous sauve ; enfin c\'est par lui que vous avez cru et que vous avez ete marques du sceau de l\'Esprit saint promis,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 14, 'gage de notre heritage futur et de la Redemption de ceux qui appartiennent a Dieu. Honneur en soit a sa gloire!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 15, 'Voila pourquoi, moi, qui ai entendu parler de votre foi au Seigneur Jesus et de votre amour pour tous les fideles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 16, 'je ne cesse de rendre graces pour vous et de vous nommer dans mes prieres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 17, 'Je demande au Dieu de notre Seigneur Jesus-Christ, au Pere de gloire, de vous donner un esprit de sagesse et de revelation ; que vous appreniez a le connaitre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 18, 'qu\'il eclaire les yeux de vos coeurs ; que vous sachiez a quelle esperance il vous appelle ; quelle est la richesse et la gloire de l\'heritage offert par lui aux fideles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 19, 'quelle est la grandeur, l\'immensite de la puissance qu\'il exerce en nous les croyants, quand il fait agir la vertu de sa force.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 20, 'Il a fait agir cette force en Christ, en le ressuscitant des morts, en le placant a sa droite dans les cieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 21, 'au-dessus de toute autorite, puissance, pouvoir, souverainete, ou de tout autre titre a donner, non seulement dans ce monde, mais aussi dans l\'autre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 22, 'En mettant tout a ses pieds, en le donnant comme chef supreme a l\'Eglise,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 1, 23, 'qui est son corps, qui est le tout, qu\'il remplit partout et de toutes manieres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 1, 'Vous aussi, vous etiez morts par les fautes, par les peches');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 2, 'auxquels vous vous etes autrefois livres sous l\'influence de notre siecle mondain, sous l\'influence de celui qui commande aux puissances qui sont dans l\'air, je parle de cet Esprit qui agit aujourd\'hui dans les hommes rebelles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 3, 'Oui, nous tous, nous cedions autrefois a nos passions charnelles, nous obeissions a tous les caprices de la chair et des sens, et nous etions naturellement enfants de colere comme les autres hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 4, 'Mais Dieu, dans la richesse de sa misericorde et au nom du grand amour dont il nous aimait,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 5, 'nous a fait revivre avec le Christ, nous que nos fautes avaient deja fait mourir (c\'est par grace que vous etes sauves) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 6, 'il nous a ressuscites, et nous a assigne une place dans les cieux par Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 7, 'Ainsi montre-t-il a tous les ages a venir l\'extraordinaire richesse de sa grace, par la bonte qu\'il a eue pour nous en Jesus-Christ ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 8, 'car c\'est par la grace que vous etes sauves, au moyen de la foi ; et cela ne vient pas de vous, c\'est un don de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 9, 'Ce n\'est pas l\'effet de vos oeuvres, et personne n\'a a s\'en glorifier ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 10, 'car nous sommes son ouvrage ; nous avons ete crees en Jesus-Christ pour faire de bonnes oeuvres, et Dieu nous a rendus d\'avance capables de les pratiquer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 11, 'Souvenez-vous donc qu\'autrefois, vous, paiens de naissance, ceux qui s\'appelaient les circoncis, parce qu\'ils l\'etaient de main d\'homme dans leur corps, vous appelaient les incirconcis ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 12, 'souvenez-vous, dis-je, que vous etiez alors sans Christ, prives du droit de cite en Israel, etrangers aux alliances et aux promesses, n\'ayant ni esperance ni Dieu dans le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 13, 'Maintenant, au contraire, etant en Jesus-Christ, vous qui etiez autrefois eloignes, vous vous etes rapproches par le sang du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 14, 'Car c\'est lui qui est notre paix ; il a reuni les deux peuples, il a abattu le mur de separation, il a fait disparaitre leur inimitie');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 15, 'en aneantissant par sa mort, la Loi, les ordonnances, les commandements ; de la sorte il a forme en lui-meme, avec les deux hommes, un seul homme nouveau, il a fait, la paix,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 16, 'et, tous deux, reunis en un seul corps, il les a reconcilies avec Dieu par la croix, sur laquelle il fit mourir en sa personne leur inimitie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 17, 'Il est venu vous annoncer La paix a vous qui etiez au loin comme a ceux qui etaient pres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 18, 'Car, grace a lui, nous pouvons avoir, tous les deux, acces aupres du Pere dans un meme Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 19, 'Vous n\'etes donc plus ni etrangers, ni intrus, vous etes concitoyens des saints et membres de la famille de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 20, 'vous avez ete edifies sur le fondement pose par les apotres qui sont aussi prophetes, fondement dont la pierre angulaire est Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 21, 'L\'edifice entier, solidement eleve sur elle, deviendra un saint temple dans le Seigneur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 2, 22, 'et c\'est sur elle que vous aussi avez ete eleves pour servir a Dieu d\'habitation spirituelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 1, 'Voila pourquoi, moi Paul, prisonnier de Jesus-Christ, pour vous paiens...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 2, 'Vous savez quelle charge Dieu, dans sa grace, m\'a conferee aupres de vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 3, 'et comment c\'est une revelation qui m\'a initie au mystere ; je viens de vous en dire quelques mots.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 4, 'Vous pouvez comprendre, en les lisant, l\'intelligence que j\'ai de ce mystere du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 5, 'Dans les autres ages, les hommes ne l\'ont pas connu, tel que l\'Esprit vient de le reveler aux saints apotres du Christ, qui sont aussi prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 6, 'Le voici: les paiens heritent avec nous, ils font partie avec nous du meme corps ; ils ont leur part de la meme promesse faite en Jesus-Christ et par l\'Evangile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 7, 'et j\'en ai ete fait ministre ; c\'est un don de la grace de Dieu qui m\'a ete accorde par sa puissante intervention.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 8, 'Oui, cette grace m\'a ete faite, a moi le moindre des fideles, d\'annoncer aux paiens l\'insondable richesse du Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 9, 'et d\'eclairer tout le monde sur la vraie nature du mystere cache de toute eternite en Dieu, createur de l\'univers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 10, 'Il faut que maintenant les puissances et les pouvoirs qui le regnent dans les cieux apprennent de l\'Eglise a connaitre l\'infinie sagesse de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 11, 'C\'est la l\'eternel dessein que Dieu avait forme en Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 12, 'C\'est en lui, c\'est par la foi en lui que nous nous approchons de Dieu avec courage et confiance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 13, 'Voila pourquoi je vous prie de ne pas perdre courage a cause des tribulations que j\'endure pour votre bien. Elles sont plutot pour vous un sujet de gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 14, 'Voila pourquoi aussi je me mets a genoux devant le Pere');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 15, '(qui donne son nom a tout ce qui est famille, soit dans le ciel, soit sur la terre ),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 16, 'pour qu\'il vous accorde, dans sa glorieuse richesse, d\'etre puissamment fortifie par son Esprit dans votre etre interieur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 17, 'qu\'il fasse que le Christ habite dans vos coeurs, par la foi ; que vous preniez racine dans l\'amour ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 18, 'qu\'en lui, vous ayez votre fondement ; que vous arriviez a comprendre, avec tous les fideles, quelle en est la largeur, la longueur, la profondeur, la hauteur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 19, 'que vous connaissiez l\'amour du Christ qui depasse toute connaissance, et qu\'enfin vous soyez remplis de toute la plenitude de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 20, 'A celui qui peut faire, pour nous, beaucoup plus, infiniment plus que tout ce que nous demandons et comprenons, grace a sa puissance qui agit en nous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 3, 21, 'a lui soit gloire dans l\'Eglise et en Jesus-Christ de generation en generation et aux siecles des siecles. Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 1, 'Je vous exhorte donc, moi qui suis prisonnier dans le Seigneur,, a vivre d\'une maniere digne de l\'appel qui vous a ete adresse,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 2, 'avec une entiere humilite, avec douceur, avec patience ; supportez-vous avec amour les uns les autres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 3, 'efforcez-vous de conserver l\'unite de l\'esprit par le lien de la paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 4, 'Il n\'y a qu\'un corps, qu\'un Esprit, comme il n\'y a qu\'une esperance, celle de l\'appel qui vous a ete adresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 5, 'Il n\'y a qu\'un Seigneur, qu\'une foi, qu\'un bapteme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 6, 'qu\'un Dieu, Pere de tous, au-dessus de tous, qui agit par tous, qui est en tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 7, 'Mais a chacun de nous a ete donnee la grace selon la mesure de la liberalite du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 8, 'Voila pourquoi il dit : Il est monte dans les hauteurs, Il a emmene des captifs, Il a fait des presents aux hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 9, 'Que suppose ce mot : il est monte? Que d\'abord il etait descendu dans des regions inferieures, sur la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 10, 'Celui qui est descendu est le meme que celui qui est monte au-dessus de tous les cieux, afin de tout remplir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 11, 'C\'est lui aussi qui a donne aux uns d\'etre apotres ; a d\'autres, d\'etre prophetes ; a d\'autres, d\'etre evangelistes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 12, 'a d\'autres, d\'etre pasteurs et docteurs, et leur ministere doit servir aux progres des fideles, a l\'edification du corps du Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 13, 'jusqu\'a ce que nous soyons tous arrives a l\'unite de la foi et de la connaissance du Fils de Dieu, a une virile maturite, a la hauteur de la parfaite stature du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 14, 'Il veut que nous ne soyons plus des enfants, entraines par tous les courants, emportes par tout vent de doctrine, joues par les hommes, trompes par leurs ruses qui egarent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 15, 'Il veut que fideles, avec amour, a la verite, nous grandissions a tous egards, pour atteindre celui qui est la tete, le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 16, 'C\'est de lui que le corps entier bien proportionne, bien lie, solidement pourvu de nombreuses jointures, tire son developpement, chaque membre selon ses forces, pour s\'edifier lui-meme et croitre dans l\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 17, 'Voici donc ce que je vous demande, ce dont je vous conjure au nom du Seigneur : ne vivez plus comme vivent les paiens dont la raison est egaree,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 18, 'dont l\'intelligence est obscurcie, qui sont etrangers a la vie de Dieu, par suite de l\'ignorance ou ils sont, et de l\'endurcissement de leur coeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 19, 'Ils ont perdu tout sens moral, ils se sont livres a la debauche, a la pratique de toute sorte d\'impurete et a l\'avarice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 20, 'Mais vous, ce n\'est pas ainsi qu\'on vous a enseigne, le Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 21, 'puisque vous en avez entendu parler et qu\'on vous a appris a son ecole');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 22, '(et c\'est la verite en Jesus) a vous defaire, comme l\'exige votre conduite passee, du vieil homme corrompu par de trompeuses passions,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 23, 'a renouveler votre esprit, votre intelligence,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 24, 'a revetir le nouvel homme cree a l\'image de Dieu dans une justice et une saintete inseparables de la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 25, 'Ainsi donc renoncez au mensonge ; Que chacun dise la verite a son prochain, car nous sommes membres les uns des autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 26, 'Dans la colere ne pechez pas ; que le soleil ne se couche pas sur votre colere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 27, 'n\'offrez aucune prise au Diable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 28, 'Que le voleur ne commette plus de vol, qu\'il occupe plutot ses mains a quelque bon travail pour qu\'il puisse donner a celui qui est dans le besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 29, 'Que votre bouche ne prononce jamais de mauvaises paroles, mais, a l\'occasion, dites quelques bonnes paroles edifiantes qui fassent du bien a ceux qui les entendent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 30, 'N\'attristez pas le saint Esprit de Dieu, dont vous avez recu le sceau pour le jour de la delivrance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 31, 'Que tout ce qui est amertume, violence, colere, crierie, medisance, soit banni d\'entre vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 4, 32, 'et aussi tout ce qui est mechancete. Soyez bons les uns pour les autres, pleins de tendresse, pardonnez-vous mutuellement, comme Dieu vous a pardonne en Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 1, 'Devenez donc imitateurs de Dieu, comme des enfants bien-aimes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 2, 'et appliquez-vous a l\'amour a l\'exemple du Christ qui vous a aimes et qui s\'est lui-meme livre pour nous a offrande et sacrifice, parfum d\'une odeur agreable a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 3, 'Que ni la debauche, ni une impurete quelconque, ni l\'avarice ne soient meme nommees parmi vous, comme il convient a des fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 4, 'Point de paroles indecentes, point de niaiseries, point de plaisanteries, ce n\'est pas convenable. Prononcez plutot des actions de graces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 5, 'Vous savez, en effet, vous reconnaissez que ni debauche, ni impur, ni avare (l\'avare est un idolatre), ne possedent d\'heritage dans le Royaume du Christ et de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 6, 'Que personne ne vous trompe par de vaines paroles : c\'est bien a cause de ces vices que la colere de Dieu frappe les hommes rebelles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 7, 'N\'ayez donc rien de commun avec eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 8, 'Car autrefois vous etiez tenebres, aujourd\'hui vous etes lumiere dans le Seigneur. Vivez comme des enfants de lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 9, '(Le fruit de la lumiere c\'est tout ce qui est bonte, justice, verite.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 10, 'Examinez ce qui est agreable au Seigneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 11, 'ne vous associez pas aux oeuvres steriles des tenebres ; faites mieux : condamnez-les ouvertement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 12, 'Ce que les paiens font en secret, on a honte meme de le dire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 13, 'Mais tout ce que l\'on condamne ouvertement est devoile, mis en pleine lumiere, car tout ce qu\'on devoile, on le met en pleine lumiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 14, 'C\'est pour cela qu\'il dit: Reveille-toi, toi qui dors, releve-toi d\'entre les morts, et le Christ t\'eclairera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 15, 'Veillez donc avec le plus grand soin sur votre conduite ; n\'agissez pas en insenses, mais en hommes sages,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 16, 'profitant des occasions, car les temps sont mauvais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 17, 'Ainsi ne devenez pas deraisonnables, et comprenez ce que veut le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 18, 'Ne vous enivrez... pas le vin mene a l\'intemperance ; soyez au contraire remplis de l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 19, 'Parlez-vous en psaumes, en hymnes, en chants spirituels, chantez et psalmodiez du fond du coeur au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 20, 'Rendez toujours graces de tout au nom de notre Seigneur Jesus-Christ a Dieu notre Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 21, 'Soumettez-vous les uns aux autres dans la crainte de Christ.,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 22, 'Que les femmes soient soumises, chacune a son mari, comme au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 23, 'Car le mari est le chef de la femme, comme le Christ est le chef de l\'Eglise, lui, le Sauveur du corps ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 24, 'de meme que l\'Eglise est soumise au Christ, de meme les femmes doivent se soumettre en tout a leurs maris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 25, 'Vous, maris, aimez vos femmes, comme le Christ a aime l\'Eglise et s\'est lui-meme livre pour elle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 26, 'pour la sanctifier par sa parole apres l\'avoir purifiee et lavee dans l\'eau,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 27, 'afin de faire paraitre a ses yeux cette Eglise, brillante de beaute, sans tache, ni ride, ni rien de semblable, mais sainte et sans defaut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 28, 'De meme aussi, les maris doivent aimer leurs femmes, car elles font partie d\'eux-memes, et celui qui aime sa femme s\'aime lui-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 29, 'Personne ne se met a hair son corps ; on le nourrit au contraire ; on en prend soin, et c\'est ainsi que le Christ en use envers l\'Eglise,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 30, 'envers nous qui sommes les membres de son corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 31, 'A cause de cela l\'homme laissera son pere et sa mere et s\'attachera a sa femme, et ils seront deux dans une chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 32, 'Ce passage est bien mysterieux ; moi, je l\'explique en l\'appliquant a Christ et a l\'Eglise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 5, 33, 'En tout cas, chacun parmi vous doit aimer sa femme comme soi-meme, et la femme doit respecter son mari.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 1, 'Vous, enfants, obeissez a vos parents selon le Seigneur ; c\'est justice :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 2, 'Respecte ton pere et ta mere. Voila un commandement capital a cause de cette promesse :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 3, 'afin d\'etre heureux et de vivre longtemps sur la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 4, 'Et vous, qui etes peres, n\'irritez pas vos enfants, mais elevez-les en les instruisant et les avertissant selon le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 5, 'Vous, qui etes esclaves, obeissez a ceux qui sont vos maitres sur la terre, dans la crainte, dans l\'humilite, dans la droiture de votre coeur, comme au Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 6, 'et non pas seulement quand ils ont l\'oeil sur vous, comme si vous n\'aviez a satisfaire que des hommes, mais, esclaves de Christ, faites la volonte de Dieu de toute votre ame.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 7, 'Servez-les avec affection, comme s\'il s\'agissait du Seigneur et non pas d\'hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 8, 'et sachez, que chacun sera recompense par le Seigneur selon le bien qu\'il aura fait, qu\'il soit esclave ou qu\'il soit libre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 9, 'Et vous, maitres, agissez de meme envers eux, laissez la les menaces, car vous savez que leur maitre et le votre est dans les cieux, et qu\'il n\'a pas egard a la condition des personnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 10, 'Enfin, cherchez votre force dans le Seigneur, dans sa vertu, dans sa puissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 11, 'Revetez l\'armure de Dieu afin de pouvoir resister aux manoeuvres du Diable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 12, 'Car nous avons a combattre, non contre des adversaires de chair et de sang, mais contre les chefs, les souverains, les dominateurs de ce monde de tenebres, contre les mauvais Esprits des regions celestes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 13, 'Prenez donc l\'armure de Dieu afin de pouvoir resister dans les mauvais jours et rester debout apres avoir triomphe de tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 14, 'Tenez ferme ; Que la verite soit la ceinture de vos reins. Que la justice soit votre cuirasse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 15, 'Que vos pieds aient pour chaussures la preparation a suivre Evangile de paix. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 16, 'Par-dessus tout, prenez le bouclier de la foi ; avec lui vous pourrez eteindre tous les traits enflammes du Malin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 17, 'Saisissez aussi le casque du salut et l\'epee de l\'Esprit, c\'est-a-dire la Parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 18, 'Priez Dieu ardemment, suppliez-le en toute occasion et par l\'Esprit. Montrez la de la vigilance, beaucoup de perseverance ; priez pour tous les fideles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 19, 'ainsi que pour moi ; qu\'il me soit donne,, quand j\'ouvre la bouche, de parler courageusement, de faire connaitre le mystere de l\'Evangile');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 20, '(moi, son ambassadeur, et qui suis aux fers) ; oui, que j\'en parle avec assurance, comme je le dois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 21, 'Pour que vous sachiez, vous aussi, ou j\'en suis et ce que je fais, le bien-aime frere Tychique, un fidele ministre du Seigneur, vous dira tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 22, 'Je vous l\'envoie dans ce but ; vous saurez par lui ou nous en sommes, et il consolera vos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 23, 'La paix soit avec les freres, et l\'amour, uni a la foi, accorde par Dieu notre Pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (49, 6, 24, 'La grace soit avec tous ceux qui aiment notre Seigneur Jesus-Christ d\'une maniere inalterable. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 1, 'Paul et Timothee, serviteurs de Jesus-Christ, a tous les fideles en Jesus-Christ qui sont a Philippes ainsi qu\'aux conducteurs de l\'eglise et aux diacres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 2, 'grace et paix vous soient accordees par Dieu notre pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 3, 'Je rends graces a mon Dieu chaque fois que je me souviens de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 4, 'Sans cesse, dans toutes mes prieres, je le prie pour vous tous avec joie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 5, 'parce que vous etes restes fideles a l\'Evangile depuis le premier jour jusqu\'a maintenant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 6, 'Je suis persuade que celui qui a commence cette bonne oeuvre en vous la menera a bonne fin jusqu\'au jour de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 7, 'En pensant ainsi de vous tous, je ne fais que vous rendre justice, car je vous porte dans mon coeur. Que je sois en prison, que je defende et preche l\'Evangile, vous prenez tous une si vive part a la grace qui m\'est faite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 8, 'Dieu m\'est temoin que j\'eprouve pour vous tous la tendresse de Jesus-Christ !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 9, 'Et je demande ceci : que votre amour aille toujours en grandissant, et qu\'il vous donne de l\'intelligence');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 10, 'et toute sorte de discernement, que vous appreciiez toutes choses a leur juste valeur, que vous arriviez purs et irreprochables a la journee de Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 11, 'riches des fruits de la justice qui viennent de Jesus-Christ, a la gloire et a la louange de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 12, 'Je veux que vous sachiez, mes freres, que ma position a ete plutot un avantage pour lEvangile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 13, 'En effet, on a parfaitement su dans tout le pretoire et dans le public que j\'etais prisonnier pour la cause de Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 14, 'et la plupart de nos freres rassures, grace au Seigneur, sur ma detention, ont plus de hardiesse, moins de crainte pour annoncer la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 15, 'Il en est, sans doute, qui prechent le Christ par jalousie et pour faire de la polemique, mais d\'autres le font dans des dispositions bienveillantes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 16, 'Ceux-ci sont pleins d\'affection, parce qu\'ils savent que je suis charge de defendre l\'Evangile ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 17, 'ceux-la ont un esprit de parti, ils annoncent le Christ sans loyaute ; ils pensent aggraver ma situation de prisonnier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 18, 'Qu\'importe apres tout? Avec ou sans arriere-pensee, Christ est de toutes manieres annonce, et je m\'en rejouis et m\'en rejouirai toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 19, 'Car je sais que Cela tournera a mon salut, grace a vos prieres et au secours de l\'Esprit de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 20, 'J\'ai la conviction, j\'ai le ferme espoir de n\'etre en rien confondu ; au contraire, je serai plein de hardiesse, et, maintenant comme toujours, mon corps, que je vive ou que je meure, servira a la gloire de Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 21, 'Pour moi, Christ est ma vie et mourir est un avantage!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 22, 'Si, d\'un autre cote, ma vie se prolonge, je verrai fructifier mon oeuvre ; aussi ne sais-je lequel preferer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 23, 'Je suis arrete entre deux desirs contraires : d\'un cote celui de partir et d\'etre avec Christ, c\'est de beaucoup ce que je prefererais ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 24, 'de l\'autre, celui de rester dans cette vie, et a cause de vous c\'est necessaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 25, 'Aussi ai-je la confiance, la certitude que je resterai, que je resterai avec vous tous pour vos progres dans la joie et dans la foi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 26, 'et ainsi mon retour aupres de vous vous fournira l\'occasion de rendre gloire a mon sujet a Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 27, 'Seulement que votre conduite soit digne de l\'Evangile du Christ ;, que je vienne vous voir ou que je reste absent, il me faut apprendre que vous restez fermes, tous dans le meme esprit, luttant d\'un commun accord pour la foi de l\'Evangile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 28, 'ne vous laissant intimider en rien par les adversaires ; cette fermete sera un signe evident de ruine pour eux, de salut pour vous, et cela de la part de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 29, 'Car il vous aura fait la grace, a propos de Christ, non seulement de croire en lui, mais aussi de souffrir pour lui');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 1, 30, 'et de soutenir le meme combat que vous m\'avez vu soutenir et que, vous le savez, je soutiens encore.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 1, 'S\'il y a donc quelque consolation en Christ, s\'il y a quelque, encouragement a s\'aimer, si c\'est quelque chose que la communion d\'esprit, quelque chose que la tendresse et la compassion,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 2, 'mettez le comble a ma joie, en vivant en bonne intelligence, en etant animes du meme amour, en ne faisant qu\'un coeur et qu\'une ame.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 3, 'N\'agissez jamais par esprit de parti ou par vaine gloire, mais que l\'un, en toute humilite, regarde l\'autre comme lui etant superieur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 4, 'et que chacun, au lieu de songer a son propre interet, songe a celui des autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 5, 'Ayez les sentiments qui animaient Jesus-Christ :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 6, 'il etait dans la meme condition que Dieu et il n\'a pas considere cette egalite avec Dieu comme un butin a garder pour lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 7, 'mais il s\'est depouille lui-meme, il a pris la condition de l\'esclave ; il est devenu semblable aux hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 8, 'il s\'est montre homme dans toute son apparition ; il s\'est humilie lui-meme, il s\'est soumis jusqu\'a mourir, mourir meme sur une croix !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 9, 'Aussi Dieu l\'a-t-il eleve bien haut et lui a-t-il donne un nom qui l\'emporte sur tous les noms,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 10, 'afin qu\'au nom de Jesus Flechissent tous les genoux dans le ciel, sur la terre, sous la terre');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 11, 'et que Toute langue confesse que Jesus-Christ est le Seigneur pour la gloire de Dieu le Pere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 12, 'Mes bien-aimes, vous avez toujours ete soumis ; travaillez donc a votre salut avec crainte et en tremblant, et non seulement comme lorsque j\'etais au milieu de vous, mais plus encore aujourd\'hui que je suis absent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 13, 'Car c\'est Dieu qui fait naitre en vous et la volonte et l\'action, suivant son bon plaisir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 14, 'Faites tout sans murmurer et sans hesiter,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 15, 'pour devenir irreprochables et purs, des ...enfants de Dieu parfaits... au milieu ... d\'un siecle deprave et corrompu... ; vous y apparaissez comme les astres dans le monde,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 16, 'retenant la parole de vie, et je serai fier a la journee de Christ, car je n\'aurai pas inutilement parcouru ma carriere, et je n\'aurai pas ...travaille inutilement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 17, 'Si meme je dois verser mon sang et etre offert en sacrifice pour le service de votre foi, je dis : Quelle joie! quelle joie! avec vous tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 18, 'vous aussi dites : Quelle joie! quelle joie! avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 19, 'J\'espere cependant, avec l\'aide du Seigneur Jesus, vous envoyer bientot Timothee, pour avoir de vos nouvelles et etre, moi aussi, un peu tranquillise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 20, 'Je n\'ai personne ici qui pense comme moi et qui puisse s\'interesser serieusement a ce qui vous concerne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 21, 'Tous songent a leurs propres interets et non a ceux de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 22, 'Quant a lui, vous savez qu\'il a ete mis a l\'epreuve et qu\'il s\'est consacre avec moi au service de l\'Evangile, comme un fils avec son pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 23, 'J\'espere vous l\'envoyer aussitot que je verrai quelle tournure prennent mes affaires.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 24, 'J\'ai de plus la confiance qu\'avec l\'aide du Seigneur j\'arriverai moi-meme incessamment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 25, 'En attendant, j\'ai cru necessaire de vous envoyer Epaphrodite, mon frere, mon collaborateur, mon compagnon de luttes qui est venu, depute par vous, pourvoir a mes besoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 26, 'D\'ailleurs il desirait ardemment vous revoir tous, et il etait desole que vous eussiez appris sa maladie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 27, 'Il a ete malade, en effet, et, a vu la mort de pres. Mais Dieu a eu pitie de lui, et non seulement de lui, mais aussi de moi, pour que je n\'eusse pas tristesse sur tristesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 28, 'Je vous l\'envoie donc avec d\'autant plus d\'empressement que vous aurez la joie de le revoir et que moi, j\'aurai un chagrin de moins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 29, 'Ainsi, accueillez-le dans le Seigneur avec une joie entiere, et rendez honneur a des hommes');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 2, 30, 'comme lui, car c\'est pour l\'oeuvre de Christ qu\'il a ete a la mort ; il a expose sa vie pour prendre votre place dans les services que vous ne pouviez me rendre, etant absents.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 1, 'Enfin, mes freres, soyez joyeux dans le Seigneur ; cela ne m\'ennuie pas de vous repeter la meme chose, et pour vous c\'est plus sur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 2, 'Attention aux chiens ! attention aux mauvais ouvriers! attention a tous ces mutiles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 3, 'c\'est nous qui sommes les vrais circoncis, nous qui adorons selon l\'Esprit de Dieu, qui tirons notre gloire de Jesus-Christ et ne mettons pas notre confiance dans des distinctions charnelles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 4, 'et cependant je pourrais le faire si je le voulais! Des distinctions charnelles! j\'en ai plus que personne,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 5, 'moi, circoncis le huitieme jour, de la race d\'Israel, de la tribu de Benjamin, Hebreu fils d\'Hebreux, pratiquant la Loi jusqu\'a etre Pharisien,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 6, 'acharne jusqu\'a persecuter l\'Eglise, irreprochable observateur des justices legales!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 7, 'C\'etaient la pour moi des titres ; mais je suis arrive, a cause du Christ, a les considerer comme rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 8, 'Oui, tout cela n\'est plus rien pour moi, quand je songe a toute l\'importance qu\'il y a a connaitre Jesus-Christ, mon Seigneur. A cause de lui, j\'ai tout perdu ; pour gagner Christ, j\'ai tenu tous mes avantages pour des ordures ; pour m\'unir a lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 9, 'j\'ai renonce a ma justice legale ; j\'ai la justice que donne la foi en Christ, justice selon Dieu fondee sur la foi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 10, 'mon but est de le connaitre lui et toute la valeur de sa resurrection ; mon but est de participer a ses souffrances ; j\'ai pris sur moi l\'image de sa mort,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 11, 'esperant atteindre, moi aussi, a la resurrection d\'entre les morts !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 12, 'Ce n\'est pas que je me sois encore empare du prix, que je sois deja arrive a la perfection ; mais je le poursuis, esperant aussi m\'en emparer, car Jesus-Christ s\'est empare de moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 13, 'Non, mes freres, je ne pretends pas, moi, m\'etre empare du prix ; seulement, j\'oublie ce qui est en arriere, je m\'elance vers ce qui est devant moi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 14, 'je cours au but, pour remporter le prix auquel Dieu m\'a appele d\'en haut en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 15, 'Tels doivent etre les sentiments des plus avances ; et si sur quelque detail vous en etiez d\'un autre avis, Dieu vous l\'expliquera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 16, 'Mais du point ou nous sommes arrives, allons toujours en avant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 17, 'Imitez-moi, mes freres, et regardez ceux qui marchent en me prenant pour modele.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 18, 'Il y en a plusieurs dont je vous ai souvent parle,  et je vous en reparle les larmes aux yeux,  qui marchent en ennemis de la croix du Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 19, 'Ils finiront par la perdition ; leur Dieu c\'est leur ventre ; ils se vantent de leurs infamies ; ils ne pensent qu\'aux choses de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 20, 'Pour nous, notre patrie est dans les cieux, d\'ou nous attendons, pour Sauveur, notre Seigneur Jesus-Christ ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 3, 21, 'il transformera notre miserable corps et le rendra semblable a son corps glorieux, car il possede le pouvoir de se rendre maitre de tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 1, 'Ainsi donc, mes freres, mes bien-aimes que je cheris, vous, ma joie et ma couronne, restez fermement attaches au Seigneur, mes bien-aimes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 2, 'Je demande a Evodia et a Syntyche de vivre en bonne intelligence sous le regard du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 3, 'Et quant a toi, mon vrai collegue, je te supplie de les supporter. Elles ont lutte pour l\'Evangile avec moi, et aussi avec Clement et mes autres collaborateurs, dont les noms sont dans le livre de vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 4, 'Soyez toujours joyeux dans le Seigneur, je le repete: soyez, joyeux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 5, 'Que tout le monde connaisse votre douceur. Le Seigneur est proche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 6, 'ne vous inquietez de rien, mais, pour tout, priez ; priez en rendant graces et en exposant vos besoins a Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 7, 'et la paix de Dieu, qui surpasse toute comprehension, gardera vos coeurs et vos pensees en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 8, 'Enfin, mes freres, tout ce qui est vrai, tout ce qui est respectable, tout ce qui est juste, tout ce qui est pur, tout ce qui est aimable, tout ce qui est honorable, tout ce qui est une vertu, tout ce qui est un eloge doit etre l\'objet de vos pensees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 9, 'Ce que vous avez appris et recu, ce que vous m\'avez entendu dire, ce que vous m\'avez vu faire, mettez-le en pratique, et le Dieu de paix sera avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 10, 'J\'ai ete extremement heureux dans le Seigneur de cette refloraison tardive de votre amitie et de ce qu\'enfin vous avez pu penser a moi. Vous y songiez bien, mais vous n\'aviez pas d\'occasion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 11, 'Si je dis cela, ce n\'est pas pour parler de ma pauvrete ; j\'ai appris a me contenter de ce que j\'ai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 12, 'Je sais etre dans le besoin ; je sais aussi avoir du superflu. Je suis initie a tout et partout ; a etre rassasie comme a avoir faim, a avoir le superflu comme a manquer du necessaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 13, 'Je puis tout en Celui qui me fortifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 14, 'Vous n\'en avez pas moins bien fait de prendre votre part de ma detresse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 15, 'D\'ailleurs vous savez, vous, les Philippiens, qu\'au commencement de l\'evangelisation, quand j\'ai quitte la Macedoine, il n\'y eut aucune Eglise, sauf la votre, qui se mit, en rapport avec moi et m\'ouvrit un compte courant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 16, 'Ainsi, a Thessalonique, vous m\'avez envoye une fois, deux fois meme, ce dont j\'avais besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 17, 'Ce n\'est pas que je recherche les presents ; mais je recherche le profit qui en resulte pour vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 18, 'J\'ai tout ce qu\'il me faut, je surabonde meme, je suis riche depuis que j\'ai recu ce que vous m\'avez envoye par Epaphrodite, delicieux parfum, sacrifice accepte et approuve de Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 19, 'Mon Dieu, selon sa richesse, pourvoira glorieusement et en Jesus-Christ a tous vos besoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 20, 'Qu\'a notre Dieu et Pere soit la gloire aux siecles des siecles! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 21, 'Saluez en Jesus-Christ tous les fideles. Les freres qui sont avec moi vous saluent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 22, 'Tous les fideles vous saluent, particulierement ceux de la maison de l\'Empereur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (50, 4, 23, 'La grace du Seigneur Jesus-Christ soit avec votre esprit. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 1, 'Paul, apotre de Jesus-Christ, par la volonte de Dieu, et, le frere Timothee');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 2, 'aux freres en Christ, fideles et croyants, qui sont a Colosses. Grace et paix vous soient accordees par Dieu notre Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 3, 'Nous rendons sans cesse des actions de graces pour vous au Dieu et Pere de notre Seigneur Jesus-Christ dans nos prieres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 4, 'car on nous a raconte votre foi en Jesus-Christ et l\'amour que vous eprouvez pour tous les fideles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 5, 'votre esperance enfin qui sera recompensee dans le ciel. On vous a parle autrefois de ces choses quand on vous a annonce la verite evangelique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 6, 'L\'Evangile est au milieu de vous, comme, du reste, dans le monde entier. Il y porte des fruits, il y gagne du terrain comme il l\'a fait chez vous depuis le jour ou vous avez appris a connaitre la verite, c\'est-a-dire la grace de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 7, 'C\'est Epaphras, notre bien-aime compagnon de service, qui vous a instruits ; ce fidele ministre du Christ aupres de nous');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 8, 'nous a fait connaitre les sentiments d\'amour que l\'Esprit vous inspire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 9, 'Et alors nous, depuis le jour ou nous avons appris tout cela, nous ne cessons de prier pour vous ; nous demandons a Dieu que vous appreniez a connaitre tout a fait sa volonte, avec cette entiere sagesse et cette intelligence que donne l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 10, 'Vous vivrez ainsi d\'une maniere digne du Seigneur, vous le satisferez entierement. Vous porterez toute espece de bons fruits, vous ferez des progres dans la connaissance de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 11, 'Vous serez admirablement fortifies par sa glorieuse puissance ; vous serez remplis de patience, de perseverance, de joie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 12, 'Vous rendrez des actions de graces a notre Pere qui vous a mis a meme d\'avoir un jour votre part de l\'heritage des fideles dans la lumiere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 13, 'qui nous a arraches au pouvoir des tenebres, qui nous a transportes dans le Royaume de son Fils bien-aime ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 14, 'car par lui nous sommes rachetes, delivres de nos peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 15, 'Il est une image du Dieu invisible, il est ne le premier de tous les etres crees ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 16, 'c\'est en lui, en effet, que tout a ete cree, tout ce qui est dans le ciel, tout ce qui est sur la terre, ce qui est visible et ce qui est invisible, les trones, les seigneuries, les autorites, les puissances, tout a ete cree par lui et pour lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 17, 'Il est avant toutes choses et toutes choses subsistent en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 18, 'Il est la tete du corps, c\'est-a-dire le chef de l\'Eglise ; il est le principe ; il est le premier-ne d\'entre les morts, afin d\'etre partout a la premiere place.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 19, 'En effet, c\'est en lui que Dieu a voulu que sa plenitude entiere residat ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 20, 'c\'est par lui qu\'il a voulu tout reconcilier avec lui-meme, et ce qui est sur la terre et ce qui est dans le ciel ; par lui il a retabli la paix au moyen du sang verse sur la croix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 21, 'Autrefois vous lui etiez etrangers, vous etiez ses ennemis par vos pensees et vos mauvaises oeuvres ; aujourd\'hui le Christ vous a reconcilies');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 22, 'par la mort de son corps, de sa chair, pour vous faire comparaitre devant lui, saints, sans tache et sans reproche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 23, 'si du moins vous restez attaches a la foi, fermes, inebranlables, sans vous ecarter de l\'esperance apportee par l\'Evangile que vous avez entendu, Evangile preche a toute creature sous le ciel, et dont moi, Paul, je suis devenu le ministre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 24, 'Je suis maintenant dans la joie en souffrant pour vous ; et ce, qui manquait encore aux souffrances du Christ, je le complete moi-meme pour le bien de son corps, je veux dire de l\'Eglise');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 25, 'dont je suis devenu le ministre. C\'est Dieu qui m\'a confie cette charge ; je dois vous annoncer le plein accomplissement de la parole de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 26, 'le mystere cache de tout temps et a tous les peuples, mais qu\'il a aujourd\'hui revele a ses fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 27, 'Dieu a voulu leur faire connaitre toute la glorieuse richesse de ce mystere a l\'egard des paiens, c\'est-a-dire : Christ en vous, l\'esperance de la gloire!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 28, 'Nous le prechons ; nous exhortons, nous instruisons chaque homme avec une entiere sagesse, pour presenter a Dieu chaque homme devenu parfait en Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 1, 29, 'C\'est a cela que je travaille, pour cela que je lutte, grace a sa force qui agit puissamment en moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 1, 'Je tiens a ce que vous sachiez toute la grandeur de ce combat que je livre pour vous, pour les Laodiceens, pour tous ceux qui ne me connaissent pas de vue,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 2, 'j\'y tiens pour reconforter leurs coeurs, pour que l\'amour les unisse, pour qu\'ils aient une intelligence riche et complete, qu\'ils connaissent le mystere de Dieu, de Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 3, 'ou sont ... caches tous les tresors de la sagesse et de la science.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 4, 'Je vous dis cela pour que personne ne vous trompe par de specieux raisonnements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 5, 'Si je suis absent de corps, je suis cependant avec vous en esprit, heureux de voir votre bon ordre et la fermete de votre foi en Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 6, 'Comme vous avez appris a connaitre le Christ, Jesus, le Seigneur, vivez ainsi en lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 7, 'greffes, edifies sur lui, affermis dans la foi, telle qu\'on vous l\'a enseignee, et montrez beaucoup de reconnaissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 8, 'Faites attention a celui qui voudrait vous accaparer avec de la philosophie, trompeuse illusion, enseignement purement humain, tout elementaire et mondain, qui n\'est pas selon Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 9, 'Car c\'est en lui que reside reellement toute la plenitude de la divinite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 10, 'Et vous, vous etes remplis de lui, le chef de toute autorite et de tout pouvoir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 11, 'En lui aussi vous avez ete circoncis ; je ne parle pas de la circoncision que font les hommes, mais de celle du Christ, du depouillement de votre corps charnel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 12, 'Vous avez ete ensevelis avec lui au moyen du bapteme, ou vous etes aussi ressuscites avec lui, par la foi en la puissance de Dieu qui l\'a ressuscite d\'entre les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 13, 'Vous etiez morts par vos peches, et par votre incirconcision charnelle, et il vous a fait revivre avec lui ; il nous a pardonne tous nos peches,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 14, 'il a efface l\'acte qui etait contre nous, dont les clauses nous etaient contraires, il l\'a mis de cote en le clouant sur la croix,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 15, 'il a depouille les autorites et les puissances, il les a victorieusement donnees en spectacle, il a triomphe d\'elles par le Christ !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 16, 'Que personne ne vienne donc vous critiquer pour un mets, une boisson, un jour de fete, de nouvelle lune ou pour des sabbats,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 17, 'tout cela n\'est que l\'ombre de ce qui devait venir ; la realite est en Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 18, 'Ne vous laissez pas enlever votre prix par ceux qui font les humbles, rendent un culte aux anges, speculent sur des reveries, que leurs idees charnelles remplissent d\'un vain orgueil,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 19, 'et qui ne s\'attachent pas au chef, dont le corps entier pourvu de nerfs et de muscles, et soutenu par eux, prend le developpement que Dieu lui donne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 20, 'Si vous etes morts avec Christ aux enseignements elementaires et mondains, pourquoi, comme si vous viviez encore dans le monde, vous faites-vous des reglements:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 21, 'Ne touche pas ceci, ne goute pas cela ; n\'approche pas de ceci ; tout cela te souillera, si tu en fais usage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 22, 'Ce sont les hommes qui enseignent et commandent ces choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 2, 23, 'Elles ont sans doute un certain air de sagesse, c\'est un culte volontaire, plein d\'humilite et de durete pour le corps, mais elles n\'ont aucune valeur et ne servent qu\'a satisfaire les sens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 1, 'Si vous etes ressuscites avec le Christ, recherchez les choses, qui sont en haut, ou le Christ est Assis a la droite de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 2, 'pensez aux choses qui sont en haut et non a celles qui sont sur la terre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 3, 'car vous etes morts et votre vie est cachee avec le Christ en Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 4, 'et quand, un jour, le Christ qui est votre vie aura paru, vous, vous apparaitrez avec lui dans la gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 5, 'Mortifiez donc vos membres, ceux qui sont de la terre, la debauche, l\'impurete, la luxure, les passions coupables et l\'avarice (elle est une idolatrie).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 6, 'Voila ce qui attire la colere de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 7, 'et voila ce que, vous aussi, vous avez fait autrefois ; oui, vous avez vecu dans ces desordres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 8, 'Mais aujourd\'hui vous devez vous defaire de tout ce qui est colere, emportement, mechancete, medisance ; ne prononcez plus de mots inconvenants ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 9, 'ne vous dites pas de mensonge l\'un a l\'autre ; debarrassez-vous du vieil homme et de ses oeuvres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 10, 'et revetez-vous du nouvel homme, qui se renouvelle a l\'image de son Createur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 11, 'et arrive a comprendre l\'etat ou il n\'y a ni Grec ni Juif, ni circoncis ni incirconcis, ni etranger ni barbare, ni esclave ni homme libre, mais ou Christ est tout et en tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 12, 'Vous, les elus de Dieu, les fideles, les bien-aimes, montrez des coeurs pleins de pitie, de bonte, d\'humilite, de douceur, de patience.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 13, 'Supportez-vous les uns les autres ; pardonnez-vous mutuellement quand l\'un de vous a a se plaindre de son prochain ; faites comme, le Seigneur qui vous a pardonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 14, 'Par-dessus tout, montrez de l\'amour ; c\'est lui qui reunit toutes les perfections.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 15, 'Que dans vos coeurs regne la paix du Christ ; vous y avez ete appeles pour ne former qu\'un seul et meme corps. Soyez reconnaissants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 16, 'Que la parole du Christ demeure en vous dans toute sa richesse. Instruisez-vous, avertissez-vous les uns les autres avec une grande sagesse. Que des psaumes, des hymnes, des chants spirituels montent a Dieu de vos coeurs reconnaissants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 17, 'Quoi que vous fassiez, que vous parliez, que vous agissiez, faites tout au nom du Seigneur Jesus, en rendant graces par lui a Dieu le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 18, 'Vous, femmes, soyez soumises a vos maris, comme il convient quand on est au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 19, 'Vous, maris, aimez vos femmes et ne vous irritez pas contre elles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 20, 'Vous, enfants, obeissez en tout a vos parents, car cela est agreable au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 21, 'Vous, peres, ne tourmentez pas vos enfants, de peur qu\'ils ne se decouragent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 22, 'Vous, esclaves, obeissez en tout a ceux qui sont vos maitres sur la terre ; ne les servez pas seulement quand ils vous voient, comme si vous n\'aviez a plaire qu\'a des hommes ; mais servez-les en toute simplicite de coeur et en craignant le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 23, 'Dans tout ce que vous faites, travaillez de toute votre ame, comme s\'il s\'agissait du Seigneur et non des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 24, 'Vous savez que vous recevrez du Seigneur en recompense l\'heritage celeste. Soyez les esclaves de Christ le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 3, 25, 'Celui qui aura fait le mal sera puni pour ce mal ; il n\'y aura point d\'acception de personnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 1, 'Vous, maitres, fournissez a vos esclaves ce qui est juste, equitable ; vous savez que vous avez, vous aussi, un maitre au ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 2, 'Dans vos prieres montrez de la perseverance, de la vigilance, de la reconnaissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 3, 'En meme temps, priez pour nous, que Dieu nous ouvre une porte, que nous parlions, que nous prechions le mystere du Christ. C\'est pour lui que je porte la chaine ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 4, 'demandez que je le fasse connaitre comme je dois l\'annoncer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 5, 'Conduisez-vous avec prudence envers ceux du dehors, et profitez bien des moments opportuns.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 6, 'Que vos paroles soient toujours aimables, pleines de saveur, de maniere a savoir repondre a chacun comme il faut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 7, 'Vous apprendrez tout ce qui me concerne personnellement par Tychique, mon bien-aime frere, mon aide fidele, mon collegue dans l\'oeuvre du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 8, 'Je vous l\'envoie tout expres pour cela, pour que vous sachiez ou en sont nos affaires, et qu\'il console vos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 9, 'Avec lui est notre fidele et bien-aime frere, Onesime, l\'un de vos compatriotes. Ils vous informeront de tout ce qui se passe ici.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 10, 'Vous avez les salutations d\'Aristarque, mon compagnon de prison, et celles de Marc, le cousin de Barnabas (vous avez recu des avis a son sujet ; quand il viendra vous voir, faites-lui bon accueil),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 11, 'celles de Jesus dit Justus. Ce sont les seuls circoncis qui aient travaille avec moi pour le Royaume de Dieu ; ils ont ete pour moi une veritable force.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 12, 'Vous avez aussi les salutations de votre compatriote Epaphras ; c\'est un serviteur de Jesus-Christ ; il lutte sans cesse pour vous dans ses prieres, pour que vous persistiez dans votre parfaite et entiere soumission a toute la volonte de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 13, 'Je lui rends le temoignage qu\'il est plein de sollicitude pour vous ainsi que pour les freres de Laodicee et de Hierapolis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 14, 'Vous avez les salutations de mon bien-aime Luc, le medecin, et celles de Demas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 15, 'Nous saluons nos freres de Laodicee ainsi que Nymphas et l\'Eglise qui s\'assemble dans leur maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 16, 'Quand vous aurez lu cette lettre, ayez soin qu\'elle soit lue aussi de l\'Eglise de Laodicee et lisez a votre tour celle qu\'on vous enverra de Laodicee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 17, 'Dites a Archippe : Prends garde de bien remplir le ministere que tu as recu de la part du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (51, 4, 18, 'Je vous salue en ecrivant moi-meme : Paul. N\'oubliez pas que je suis en prison. La grace soit avec vous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 1, 'Paul, Silvanus et Timothee a l\'eglise des Thessaloniciens en Dieu le Pere et en Jesus-Christ le Seigneur. Grace et paix vous soient accordees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 2, 'Nous rendons constamment graces a Dieu pour vous tous ; nous vous nommons dans nos prieres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 3, 'et nous rappelons sans cesse, en presence de Dieu notre Pere, les oeuvres de votre foi, les travaux de votre charite et la perseverance de votre espoir en notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 4, 'Nous savons, freres bien-aimes de Dieu, que vous avez ete elus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 5, 'car nous ne vous avons pas evangelises seulement avec des paroles, mais avec une vraie puissance, avec l\'Esprit saint, avec une forte conviction. Vous savez bien ce que nous avons ete au milieu de vous et pour votre bien,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 6, 'et vous etes devenus nos imitateurs et ceux du Seigneur, en accueillant sa parole dans un moment de grande affliction avec la joie que donne l\'Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 7, 'Aussi etes-vous devenus un modele pour tous les croyants de la Macedoine et de l\'Achaie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 8, 'Car c\'est de chez vous que la parole du Seigneur s\'est fait entendre ; et ce n\'est pas seulement en Macedoine et en Achaie, c\'est partout que votre foi en Dieu a ete connue ; aussi n\'avons-nous pas besoin d\'en parler.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 9, 'Chacun raconte en parlant de nous comment vous nous avez recus a notre arrivee, comment vous vous etes convertis a Dieu, abandonnant les idoles pour le servir, lui, le Dieu vivant et vrai,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 1, 10, 'et pour attendre du haut des cieux son Fils qu\'il a ressuscite des morts, Jesus, celui qui nous delivre de la colere a venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 1, 'Vous savez bien vous-memes, freres, que notre arrivee chez vous n\'a pas ete sterile :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 2, 'apres avoir ete persecutes, apres avoir ete insultes a Philippes, vous le savez, nous avons eu une grande confiance en notre Dieu, et nous vous avons preche son Evangile au milieu de bien des luttes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 3, 'C\'est que notre predication ne reposait ni sur l\'erreur, ni sur des motifs impurs, ni sur la fraude ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 4, 'mais Dieu nous ayant juges dignes de nous confier l\'Evangile, nous parlons non pour plaire aux hommes, mais a Dieu qui sonde nos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 5, 'Jamais, en effet, nous n\'avons employe la flatterie en vous parlant, vous le savez ; jamais non plus nos motifs n\'ont ete interesses, Dieu nous en est temoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 6, 'Nous n\'avons pas recherche non plus les honneurs des hommes, ni chez vous, ni chez d\'autres, et cependant, etant apotres de Christ, nous aurions pu nous imposer a vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 7, 'Eh bien, au contraire, nous avons ete pleins de douceur au milieu de vous. Comme une mere qui soigne tendrement ses enfants,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 8, 'nous etions disposes, dans notre affection pour vous, a vous donner non seulement l\'Evangile de Dieu, mais nos vies elles-memes, tellement vous nous etiez devenus chers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 9, 'Vous vous rappelez, freres, nos efforts et nos peines ; c\'est en travaillant nuit et jour pour n\'etre a charge a aucun de vous que nous vous avons preche l\'Evangile de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 10, 'Vous etes temoins, et Dieu aussi, de la piete, de l\'equite et de l\'integrite avec lesquelles nous avons agi envers vous, croyants ; comme un pere le ferait pour ses enfants,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 11, 'nous vous avons, vous le savez, exhortes, encourages, conjures de vivre');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 12, 'd\'une maniere digne de Dieu qui vous appelle a son Royaume et a sa gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 13, 'Voila aussi pourquoi nous rendons sans cesse graces a Dieu de ce que, recevant de nous, de notre bouche, la parole de Dieu, vous lavez acceptee, non comme une parole d\'hommes, mais, ce qu\'elle est reellement, comme une parole de Dieu ; et elle se montre efficace en vous, les croyants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 14, 'En effet, vous etes devenus, freres, les imitateurs des Eglises de Dieu qui, en Judee, sont a Jesus-Christ ; car vous avez eu a souffrir de la part de vos compatriotes ce qu\'elles ont eu a souffrir de la part des Juifs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 15, 'de ceux qui ont aussi mis a mort le Seigneur Jesus et les prophetes, qui nous ont persecutes, qui deplaisent a Dieu, sont ennemis de tous les hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 16, 'qui nous empechent de parler aux paiens pour les sauver, et qui mettent sans cesse le comble a leurs peches ; mais la colere de Dieu les a atteints, et definitivement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 17, 'Quant a nous, freres, prives de votre presence pendant quelque temps, mais pres de vous par le coeur, nous avons ardemment, immensement desire revoir vos visages.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 18, 'Nous avons voulu une ou deux fois revenir chez vous (je parle de moi Paul), et Satan s\'y est oppose.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 19, ' (Qui, en effet, est notre esperance, qui, notre joie, qui, la couronne dont un jour nous serons fiers? n\'est-ce pas vous ? vous aussi, ne serez-vous pas tout cela en presence de notre Seigneur Jesus, lors de son apparition?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 2, 20, 'Oui, c\'est vous qui etes notre gloire et notre joie! ) ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 1, 'Aussi nous n\'y tenions plus ; nous nous sommes alors decides a rester seuls a Athenes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 2, 'et a vous envoyer Timothee, notre frere, qui est ministre de Dieu dans l\'Evangile du Christ, pour vous fortifier, pour vous engager, dans l\'interet de votre foi,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 3, 'a ne pas vous laisser ebranler par les epreuves de ce temps-ci. Vous savez vous-memes que nous y sommes destines.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 4, 'Quand nous etions chez vous, nous vous avons, en effet, predit qu\'il nous fallait etre eprouves et, vous le savez, c\'est ce qui est arrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 5, 'Voila pourquoi, n\'y tenant plus, j\'ai envoye savoir des nouvelles de votre fidelite ; je craignais que le Tentateur ne vous eut tentes, et que notre peine n\'eut ete perdue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 6, 'Mais Timothee vient d\'arriver de chez vous, et il nous a apporte de bonnes nouvelles de votre foi et de votre amour ; il nous a dit le bon souvenir que vous conservez de nous et votre desir de nous revoir, desir qui est aussi le notre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 7, 'Aussi votre foi nous a-t-elle fortifies, freres, au milieu de nos afflictions et de nos epreuves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 8, 'Dans ce moment, nous ne vivons que parce que vous etes attaches au Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 9, 'Comment pouvons-nous assez remercier Dieu de toute la joie que vous nous faites eprouver devant lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 10, 'nous qui lui demandons instamment, jour et nuit, de faire que nous vous revoyions et que nous allions completer ce qui manque encore a votre foi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 11, 'Veuille Dieu notre Pere et le Seigneur Jesus aplanir le chemin qui mene chez vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 12, 'et veuille le Seigneur augmenter et faire abonder votre amour les uns pour les autres et pour tous les hommes (comme aussi le notre pour vous),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 3, 13, 'pour que vos coeurs se fortifient et soient d\'une irreprochable saintete, en presence de Dieu notre Pere, a l\'apparition de notre Seigneur Jesus au milieu de tous ses saints!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 1, 'Enfin, freres, nous vous demandons, nous vous prions, au nom du Seigneur Jesus, de faire toujours plus de progres (vous en faites deja), car vous avez appris de nous comment on doit se conduire pour plaire a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 2, 'Vous savez quels preceptes nous vous avons donnes de la part du Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 3, 'Ce que Dieu veut, c\'est votre sanctification ; que vous vous absteniez de toute impurete ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 4, 'que chacun de vous sache avoir sa femme a lui, purement et honnetement');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 5, 'et sans les passions desordonnees des paiens qui ne connaissent pas Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 6, 'En affaires, ne faites aucun tort, ne causez aucun prejudice a votre frere ; car le Seigneur fait justice de tout cela, comme nous vous l\'avons dit et certifie autrefois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 7, 'Dieu ne nous a pas appeles a l\'impurete, mais a la sanctification.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 8, 'Celui donc qui repousse ces paroles ne repousse pas un homme, mais Dieu qui vous a donne a vous son Esprit saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 9, 'Quant a l\'amour fraternel, vous n\'avez pas besoin qu\'on vous en ecrive, car vous-memes vous avez appris de Dieu a vous aimer les uns les autres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 10, 'et vraiment vous le faites, pour tous les freres, dans la Macedoine entiere. Nous vous exhortons, freres, a le faire de plus en plus,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 11, 'a tenir a honneur de vivre en paix, a vous occuper de vos propres affaires, a travailler de vos mains, comme nous vous l\'avons recommande,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 12, 'pour que votre conduite soit respectee par ceux du dehors, et que vous n\'ayez besoin de personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 13, 'Nous ne voulons pas, freres, vous laisser dans l\'ignorance touchant ceux qui se sont endormis, afin que vous ne soyez pas tristes comme les autres qui n\'ont pas d\'esperance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 14, 'Si nous croyons que Jesus est mort et qu\'il est ressuscite, de meme nous devons croire que Dieu ramenera les morts par Jesus et avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 15, 'Nous vous disons ceci d\'apres une parole du Seigneur : Nous qui vivons, qui sommes reserves pour voir l\'apparition du Seigneur, nous ne devancerons pas ceux qui se sont endormis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 16, 'Car le Seigneur lui-meme, a un signal donne, a la voix d\'un archange et au son de la trompette de Dieu, descendra du ciel ; alors ceux qui sont morts en Christ ressusciteront d\'abord.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 17, 'Puis, nous, les vivants, les reserves, nous serons enleves en meme temps qu\'eux, vers les nues, pour aller a la rencontre du Seigneur dans l\'air ; et ainsi nous serons toujours avec le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 4, 18, 'Fortifiez-vous donc les uns les autres dans ces pensees-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 1, 'Quant a l\'epoque, a la date, vous n\'avez pas besoin, freres, qu\'on vous en ecrive.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 2, 'Vous savez parfaitement vous-memes que le jour du Seigneur doit arriver comme un voleur qui vient la nuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 3, 'C\'est au moment ou les gens diront : paix! securite! qu\'une ruine subite les surprendra, comme les douleurs de l\'enfantement surprennent une femme enceinte, et ils n\'echapperont pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 4, 'Mais vous, freres, vous n\'etes pas dans les tenebres, et ce jour ne vous surprendra pas comme nous surprend un voleur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 5, 'Vous etes tous des fils de lumiere, des fils du jour ; nous ne le sommes ni de la nuit ni des tenebres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 6, 'Ne dormons donc pas comme font les autres, mais soyons vigilants et sobres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 7, 'Ceux qui dorment, dorment pendant la nuit ; ceux qui s\'enivrent, s\'enivrent pendant la nuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 8, 'Quant a nous, fils du jour, vivons sobrement, mettons la cuirasse de la foi et de l\'amour a et le casque de l\'esperance du salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 9, 'Dieu ne nous a pas destines a la colere, mais a l\'acquisition du salut par notre Seigneur Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 10, 'mort pour nous, afin que, veillant ou dormant, nous vivions toujours avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 11, 'Fortifiez-vous donc les uns les autres ; edifiez-vous reciproquement, et d\'ailleurs vous le faites deja.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 12, 'Nous vous demandons encore, freres, d\'avoir des egards pour ceux qui travaillent parmi vous, qui vous dirigent dans ce qui est du Seigneur et qui vous avertissent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 13, 'Montrez-leur une affection toute particuliere a cause de leurs fonctions. Vivez en paix entre vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 14, 'Nous vous engageons, freres, a avertir ceux qui se laissent aller au desordre, a relever les decourages, a soutenir les faibles, a etre patients envers tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 15, 'Prenez garde a ce que personne ne rende le mal pour le mal, mais appliquez-vous toujours a ce qui est bien, soit entre vous, soit avec tout le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 16, 'Soyez toujours dans la joie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 17, 'priez sans cesse ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 18, 'soyez reconnaissants de tout : voila ce que Dieu veut de vous en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 19, 'N\'eteignez pas l\'Esprit ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 20, 'ne dedaignez pas les inspirations prophetiques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 21, 'Examinez tout ; retenez ce qui est bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 22, 'Abstenez-vous de toute espece de mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 23, 'Que le Dieu de paix lui-meme vous sanctifie parfaitement ; que votre esprit, votre ame, votre corps, tout votre etre se conserve irreprochable pour l\'apparition de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 24, 'Il est fidele, Celui qui vous appelle ; il fera cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 25, 'Freres, priez pour nous.,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 26, 'Saluez tous les freres d\'un saint baiser.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 27, 'Je vous conjure, au nom du Seigneur, de faire lire cette lettre a tous les freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (52, 5, 28, 'La Grace de Notre Seigneur Jesus-Christ soit avec vous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 1, 'Paul, Silvanus et Timothee a l\'eglise des Thessaloniciens en Dieu notre Pere et en Jesus-Christ le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 2, 'Grace et paix vous soient accordees par Dieu notre Pere et par le Seigneur Jesus-Christ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 3, 'Nous devons sans cesse rendre graces a Dieu a votre sujet ; freres, ce n\'est que juste, car votre foi s\'accroit beaucoup, et votre amour les uns pour les autres augmente tellement que');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 4, 'nous nous felicitons de vous aupres des Eglises de Dieu ; nous leur parlons de votre patience et de votre foi dans toutes les persecutions et les afflictions que vous avez eues a supporter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 5, 'Il y a la comme un presage du juste jugement de Dieu ; un jour vous serez juges dignes de son Royaume pour lequel vous souffrez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 6, 'Car enfin il est juste devant Dieu qu\'il fasse souffrir a leur tour ceux qui vous font souffrir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 7, 'et qu\'il vous donne du repos avec nous a vous qui souffrez. Cela sera quand le Seigneur Jesus descendra du ciel avec les anges de sa puissance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 8, 'Entoure de flammes de feu, il fera justice de ceux qui meconnaissent Dieu et qui n\'obeissent pas a l\'Evangile de notre Seigneur Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 9, 'Leur chatiment sera la destruction eternelle, Loin de la face du Seigneur Et de son eclatante puissance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 10, 'quand il viendra pour etre, en ce jour-la, glorifie au milieu de ses fideles et admire par tous ceux qui auront ete croyants ; car vous avez cru a notre temoignage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 11, 'Voila aussi pourquoi nous prions sans cesse pour vous ; nous demandons que notre Dieu vous trouve dignes de son appel ; qu\'il vous remplisse, dans sa puissance, de bonnes dispositions et d\'oeuvres de foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 1, 12, 'C\'est ainsi que le nom de notre Seigneur Jesus-Christ sera glorifie en vous, et vous en lui, par la grace de notre Dieu et du Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 1, 'Quant a l\'apparition de notre Seigneur Jesus-Christ et a notre reunion a lui, freres, nous vous en supplions,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 2, 'n\'allez pas vite vous monter l\'imagination, ne vous laissez pas troubler par de fausses inspirations, ou par des paroles ou par de pretendues lettres de nous, vous annoncant que nous sommes arrives au jour du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 3, 'Ne vous laissez tromper par rien ni par personne : il n\'arrivera rien avant qu\'ait eu lieu la grande apostasie et que se soit revele l\'homme de l\'iniquite, le fils de la perdition,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 4, 'le grand adversaire s\'elevant au-dessus de tout ce qu\'on appelle Dieu et qu\'on adore, jusqu\'a s\'installer dans le temple de Dieu et se presenter comme etant Dieu lui-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 5, 'Ne vous souvenez-vous pas que je vous disais cela quand j\'etais encore aupres de vous?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 6, 'Vous savez bien ce qui l\'empeche de paraitre et pourquoi il ne se revelera que lorsque son heure sera venue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 7, 'L\'iniquite exerce deja sa mysterieuse action ; elle eclatera quand celui qui l\'arrete encore aura disparu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 8, 'Alors se revelera l\'impie que le Seigneur Jesus Tuera du souffle de sa bouche et aneantira par l\'eclat de son apparition.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 9, 'Quant a l\'apparition de l\'impie, elle se fera par la puissance de Satan, avec toutes sortes de miracles, de signes, de prodiges menteurs');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 10, 'et une foule de seductions coupables pour les hommes qui vont perir, qui n\'ont pas voulu accepter et aimer la verite qui les eut sauves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 11, 'Voila pourquoi Dieu leur envoie un formidable penchant pour l\'erreur qui les fait croire au mensonge,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 12, 'afin que tous ceux qui n\'auront pas cru a la verite et se seront complus dans le mal tombent sous son jugement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 13, 'Quant a nous, freres cheris du Seigneur, nous devons rendre a Dieu, pour vous, de continuelles actions de graces de ce qu\'il vous a choisis depuis le commencement pour vous sauver par la sanctification de l\'Esprit et par la foi en la verite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 14, 'il vous a appeles a cet etat par notre predication de l\'Evangile, afin que vous prissiez part a la gloire de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 15, 'Restez donc fermes, freres, et retenez les enseignements que nous vous avons donnes soit de vive voix, soit dans notre lettre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 16, 'Que notre Seigneur Jesus-Christ lui-meme et que Dieu notre Pere, qui nous a aimes et nous a donne dans sa grace une consolation eternelle et une douce esperance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 2, 17, 'consolent vos coeurs et vous affermissent en toute bonne oeuvre et en toute bonne parole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 1, 'Enfin, priez pour nous, freres, afin que la parole du Seigneur se repande, qu\'elle soit partout glorifiee comme chez vous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 2, 'et que nous soyons delivres des hommes intraitables et mechants ; car tous n\'ont pas la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 3, 'Le Seigneur est fidele ; il vous fortifiera et vous preservera du Malin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 4, 'Nous avons la confiance en vous dans le Seigneur, que vous faites et que vous ferez ce que nous vous recommandons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 5, 'Veuille le Seigneur former vos coeurs a l\'amour de Dieu et a la patiente attente du Christ!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 6, 'Nous vous recommandons, freres, au nom de notre Seigneur Jesus-Christ de vous eloigner de tout frere qui se conduit d\'une maniere dereglee et ne suit pas l\'enseignement que vous avez recu de nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 7, 'Vous savez vous-memes ce qu\'il faut faire pour nous imiter, car nous n\'avons pas vecu parmi vous d\'une maniere dereglee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 8, 'nous n\'avons mange gratis le pain de personne, mais nous avons jour et nuit peniblement et laborieusement travaille pour n\'etre a charge a aucun de vous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 9, 'et ce n\'etait pas parce que nous n\'en avions pas le droit, mais c\'etait pour vous donner un exemple a imiter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 10, 'Quand nous etions au milieu de vous, nous vous donnions cette regle : celui qui refuse de travailler ne doit pas non il plus manger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 11, 'Nous apprenons, en effet, qu\'il y a parmi vous des personnes qui se conduisent d\'une maniere dereglee, qui ne travaillent point et perdent leur temps a des futilites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 12, 'Nous invitons et nous exhortons ces personnes-la par notre Seigneur Jesus-Christ a travailler paisiblement et a manger un pain qui soit le leur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 13, 'Pour vous, freres, ne vous lassez pas de faire le bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 14, 'Si quelqu\'un ne se soumet pas a ce que nous vous disons dans cette lettre, notez-le, n\'ayez point de rapport avec lui pour qu\'il soit couvert de honte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 15, 'Cependant ne le traitez pas comme un ennemi, mais avertissez-le comme un frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 16, 'Que le Seigneur de paix vous donne lui-meme la paix, toujours, de toutes manieres. Que le Seigneur soit avec vous tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 17, 'Je vous salue en ecrivant moi-meme, PAUL. Je signe ainsi toutes mes lettres ; c\'est mon ecriture');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (53, 3, 18, 'La grace de notre Seigneur Jesus-Christ soit avec vous tous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 1, 'Paul, apotre de Jesus-Christ, par ordre de Dieu notre sauveur et de Jesus-Christ notre esperance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 2, 'a Timothee, mon veritable enfant dans la foi. Grace, misericorde et paix te soient accordees par Dieu notre Pere et par Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 3, 'Comme je t\'ai recommande, a mon depart pour la Macedoine, de rester a Ephese afin d\'ordonner a certaines gens de ne pas enseigner de fausses doctrines,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 4, 'de ne pas s\'occuper de contes et d\'interminables genealogies qui provoquent les discussions au lieu de favoriser le plan de Dieu base sur la foi...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 5, 'Le but de cet ordre c\'est l\'amour venant d\'un coeur pur, d\'une bonne conscience, d\'une foi sincere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 6, 'Certaines gens s\'en sont detournes et se sont perdus dans ce qui n\'est que du bavardage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 7, 'Ils veulent etre docteurs de la Loi, sans comprendre ni ce qu\'ils disent ni ce qu\'ils affirment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 8, 'Nous ne nions pas que la Loi ne soit bonne, mais pour celui qui sait en faire usage,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 9, 'qui est convaincu qu\'elle n\'est pas faite pour le juste, mais pour ceux qui n\'en veulent pas, qui la rejettent, pour ceux qui sont impies et pecheurs, irreligieux et profanes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 10, 'parricides, meurtriers, impurs, debauches infames, qui commettent des rapts ; menteurs, parjures, ou qui violent d\'une maniere quelconque la saine instruction.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 11, 'Voila ce qu\'enseigne le glorieux Evangile du Dieu bienheureux, Evangile que je suis charge de precher.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 12, 'Je remercie celui qui m\'a donne ma force, Jesus-Christ notre Seigneur, de ce qu\'il m\'a juge digne de confiance, de ce qu\'il m\'a appele au ministere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 13, 'moi l\'ancien blasphemateur, l\'ancien persecuteur, l\'ancien insulteur. Mais il a eu pitie de moi parce que j\'etais un ignorant, j\'avais agi dans l\'incredulite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 14, 'et la grace de notre Seigneur a surabonde ainsi que la foi et l\'amour pour Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 15, 'Il est un fait certain et absolument digne de foi, c\'est que Jesus-Christ est venu au monde pour sauver les pecheurs, dont je suis, moi, le premier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 16, 'Mais voici pourquoi il a eu pitie de moi: Jesus-Christ voulait montrer en moi tout le premier sa grande patience, il voulait que je servisse d\'exemple a ceux qui croiront en lui et obtiendront la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 17, 'Au Roi de l\'univers, immortel, invisible, au Dieu unique, soient honneur et gloire aux siecles des siecles ! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 18, 'Voici la recommandation que je t\'adresse, Timothee, mon enfant, m\'autorisant des propheties dont tu as ete l\'objet : accomplis-les en faisant en bon soldat cette belle campagne, ayant confiance et bonne conscience.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 19, 'Certains l\'ont perdue et leur foi a fait naufrage.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 1, 20, 'Parmi eux se trouvent Hymenee et Alexandre, que j\'ai livres a Satan, pour leur apprendre a ne pas blasphemer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 1, 'Je t\'exhorte, avant tout, a faire faire des prieres, supplications, intercessions, actions de graces pour tous les hommes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 2, 'pour les rois, pour tous ceux qui sont au pouvoir, afin que nous puissions vivre paisiblement, tranquillement, en toute piete et honnetete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 3, 'C\'est une bonne chose et qui est agreable a Dieu, notre Sauveur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 4, 'qui veut que tous les hommes soient sauves et parviennent a la connaissance de la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 5, 'Il n\'y a, en effet, qu\'un seul Dieu ; il n\'y a aussi qu\'un seul mediateur entre Dieu et les hommes, un homme, Jesus-Christ, qui s\'est donne en rancon pour tous ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 6, 'ce fait s\'est passe au temps, marque.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 7, 'Quant a moi, j\'ai ete designe pour en etre le heraut et l\'apotre (je dis la verite, je ne mens pas) et pour enseigner aux paiens la foi et la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 8, 'Je veux donc que, partout, les hommes prient, qu\'ils levent au ciel des mains pures, sans colere, sans arriere-pensees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 9, 'Les femmes, de meme ; et qu\'elles aient une tenue convenable ; qu\'elles se fassent une parure de leur pudeur et de leur modestie ; ni tresses, ni or, ni perles, ni vetements couteux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 10, 'mais seulement de bonnes oeuvres ; voila ce qui convient a des femmes qui font profession de piete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 11, 'La femme doit se laisser instruire en silence et avec une entiere soumission.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 12, 'Je ne permets a la femme ni d\'enseigner, ni de s\'emanciper de l\'autorite de l\'homme ; qu\'elle garde le silence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 13, 'Car c\'est Adam qui fut cree le premier, puis ce fut le tour d\'Eve ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 14, 'et ce n\'est pas Adam qui a ete seduit, c\'est la femme qui s\'est laisse seduire et a commis la faute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 2, 15, 'Cependant elle sera sauvee par la maternite, si elle persiste sagement dans la foi, dans la charite, et dans la sanctification.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 1, 'Il est un fait certain : si quelqu\'un aspire a la charge de conducteur d\'une eglise, il convoite une belle tache.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 2, 'Eh bien, il faut, que le conducteur d\'une eglise soit irreprochable ; qu\'il ne soit le mari que d\'une femme ; qu\'il soit sobre, modere, modeste, hospitalier, capable d\'enseigner ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 3, 'qu\'il n\'aime ni le vin, ni les querelles ; au contraire, qu\'il soit doux et pacifique ; qu\'il ne tienne pas a, l\'argent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 4, 'quil dirige bien sa propre maison ; que ses enfants soient obeissants et parfaitement eleves');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 5, '(si on ne sait pas diriger sa propre maison, comment se charger du soin de l\'eglise de Dieu!) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 6, 'qu\'il ne soit pas nouvellement converti, de peur que l\'orgueil ne le fasse tomber au pouvoir du Diable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 7, 'Il faut encore qu\'il jouisse de la consideration de ceux du dehors, de peur qu\'il ne donne prise aux reproches, et ne tombe dans le piege du Diable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 8, 'Les diacres de meme doivent etre respectables ; qu\'ils ne soient point de mauvaise foi ; qu\'ils ne se livrent pas a des exces de boisson ; qu\'ils ne soient pas bassement interesses ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 9, 'qu\'ils gardent dans une conscience pure le mystere de la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 10, 'Qu\'on commence par les prendre a l\'essai ; ensuite qu\'on les admette comme diacres, s\'il ne s\'est eleve aucune plainte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 11, 'Les femmes, de meme, doivent etre respectables, non medisantes, sobres, fideles en toutes choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 12, 'Les diacres ne doivent etre maris que d\'une femme ; bien diriger leurs enfants et leurs propres maisons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 13, 'Ceux qui ont ete de bons diacres finissent par arriver a un rang honorable et par acquerir beaucoup d\'assurance dans la foi en Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 14, 'Je t\'ecris tout cela  et cependant j\'espere aller bientot te trouver ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 15, 'parce que, si je tardais, je veux que tu saches la conduite que tu dois tenir dans la maison de Dieu, qui est l\'eglise du Dieu vivant, la colonne et le fondement de la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 3, 16, 'Il est grand, sans contredit, le mystere de la piete : ...Celui qui a ete manifeste en chair, A ete legitime par l\'Esprit, A ete vu des anges, A ete preche aux paiens, A ete cru dans le monde, A ete eleve dans la gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 1, 'L\'Esprit dit clairement que, dans les derniers temps, un certain nombre abandonneront la foi pour s\'attacher a des Esprits seducteurs et a des doctrines de demons,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 2, 'd\'hypocrites menteurs, qui portent sur leur conscience meme la marque de la fletrissure,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 3, 'qui proscrivent le mariage, qui ordonnent de s\'abstenir d\'aliments crees par Dieu pour etre pris avec actions de graces par les croyants qui ont reconnu la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 4, 'En effet, tout ce que Dieu a cree est bon ; on ne doit renoncer a aucun aliment, pourvu qu\'on le prenne avec actions de graces,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 5, 'car alors la parole de Dieu et la priere le sanctifient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 6, 'C\'est en exposant ces choses aux freres que tu seras un bon ministre de Jesus-Christ, nourri des paroles de la foi et de la bonne doctrine que tu as suivie jusqu\'a present.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 7, 'Quant aux contes profanes et aux radotages, repousse-les. Exerce-toi a la piete ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 8, 'les exercices du corps ont peu d\'utilite, mais la piete est utile a tout, puisqu\'elle a la promesse de la vie presente et celle de la vie a venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 9, 'C\'est la un fait certain et absolument digne de foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 10, 'Si, en effet, nous nous donnons tant de peine et si nous livrons un si rude combat c\'est que nous avons place notre esperance en un Dieu vivant qui est le Sauveur de tous les hommes, surtout des croyants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 11, 'Voila ce que tu dois prescrire et enseigner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 12, 'Que personne ne te meprise a cause de ta jeunesse ; au contraire, sois un modele pour les croyants par tes paroles, par ta conduite, par ta charite, par ta foi, par ta chastete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 13, 'En attendant que j\'arrive, occupe-toi de la lecture, de la predication, de l\'enseignement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 14, 'Ne neglige pas le don de la grace que tu possedes, que tu as recu quand les Anciens prophetisaient et t\'imposaient les mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 15, 'Occupe-toi de ces choses, donne-toi a ces choses, afin que tout le monde soit frappe de tes progres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 4, 16, 'Veille sur toi-meme et sur ton enseignement. Mets-y de la perseverance, car en agissant ainsi tu te sauveras toi-meme et tu sauveras ceux qui t\'ecoutent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 1, 'Ne te montre pas dur pour l\'homme age ; au contraire, exhorte-le comme un pere, les jeunes gens comme des freres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 2, 'les femmes agees comme des meres, les jeunes femmes comme des soeurs, avec une parfaite purete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 3, 'Aie des egards pour les veuves ; je parle de celles qui sont reellement veuves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 4, 'Mais quant a une veuve qui a des enfants ou des petits-enfants, c\'est a ceux-ci a apprendre les premiers s\'acquitter envers leur famille de leurs devoirs de piete filiale et a payer leurs parents de retour ; car cela est agreable a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 5, 'La femme qui, au contraire, est reellement veuve, absolument seule au monde, a appris a mettre son esperance en Dieu et ne cesse ni la nuit ni le jour ses prieres et ses supplications,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 6, 'tandis que celle qui court apres les plaisirs est une vivante qui est morte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 7, 'Rappelle-leur ces choses pour qu\'elles soient a l\'abri de tout reproche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 8, 'Si quelqu\'un neglige les siens, et, en particulier, les membres de sa famille, il a renie la foi et il est pire qu\'un incredule.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 9, 'Une veuve, pour etre inscrite sur le registre, doit etre agee de soixante ans au moins, n\'avoir eu qu\'un mari,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 10, 'se recommander par quelques oeuvres excellentes, comme d\'avoir eleve des enfants, exerce l\'hospitalite, lave les pieds des fideles, secouru les malheureux, pratique toutes sortes de bonnes actions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 11, 'Quant aux veuves encore jeunes, ne les admets pas, car lorsque l\'attrait du plaisir les detache du Christ, elles veulent se remarier,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 12, 'et elles s\'exposent au reproche de manquer a leur premier engagement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 13, 'En meme temps elles prennent l\'habitude de ne rien faire, de courir les maisons et non seulement de ne rien faire, mais de jaser, de s\'occuper de tout, de dire ce qu\'il ne faut pas dire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 14, 'Je veux donc que les jeunes veuves se remarient, qu\'elles aient, des enfants, une maison a diriger, et ne donnent aucune prise aux medisances de l\'adversaire,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 15, 'car deja quelques-unes ont deserte pour suivre Satan.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 16, 'Si donc, parmi les croyants, un homme, une femme a dans sa famille des veuves, il doit les secourir ; celles-ci ne doivent pas etre a la charge de l\'Eglise, qui doit se consacrer aux veuves absolument seules au monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 17, 'Juge dignes d\'un double honneur les Anciens qui ont bien rempli leurs devoirs, en particulier ceux qui se chargent de la predication et de l\'enseignement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 18, 'L\'Ecriture dit en effet : Tu ne muselleras point le boeuf qui foule le ble,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 19, 'et l\'ouvrier merite son salaire. N\'accueille point d\'accusation contre un Ancien, sauf dans le cas ou il y aurait deux ou trois temoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 20, 'Reprends devant tout le monde ceux qui sont en faute, pour inspirer aussi de la crainte aux autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 21, 'Je te conjure devant Dieu, devant Jesus-Christ et devant les anges elus de suivre ces conseils sans prejuge, sans rien faire par esprit de parti.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 22, 'Ne te decide pas trop vite a imposer les mains a quelqu\'un, et ne te rends pas solidaire des torts d\'autrui ; toi-meme reste pur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 23, 'Renonce a ne boire que de l\'eau ; prends au contraire un peu de vin a cause de ton estomac et de tes frequentes indispositions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 24, 'Certaines personnes sont si evidemment en faute qu\'on les juge tout de suite ; d\'autres ne se devoilent que plus tard.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 5, 25, 'Il en est de meme des bonnes oeuvres ; elles sont en evidence, et celles qui ne sont pas telles tout de suite ne peuvent pas rester cachees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 1, 'Ceux qui portent le joug de la servitude doivent considerer leurs maitres comme dignes de tout respect afin qu\'on ne calomnie ni le nom de Dieu, ni notre enseignement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 2, 'Ceux qui ont des maitres croyants ne doivent pas les mepriser sous pretexte qu\'ils sont leurs freres, mais les servir d\'autant mieux que ceux qui recoivent leurs bons soins sont des croyants, sont des bien-aimes. Tels seront tes enseignements et tes exhortations.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 3, 'Si quelqu\'un enseigne d\'une autre maniere et s\'ecarte des saines doctrines de notre Seigneur Jesus-Christ et de l\'enseignement qui favorise la piete,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 4, 'c\'est un orgueilleux, c\'est un ignorant, il a la maladie des discussions oiseuses et des disputes de mots, d\'ou naissent les jalousies, les querelles, les calomnies, les soupcons injustes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 5, 'les interminables discussions de gens a l\'esprit faux, qui sont toujours a cote de la verite, et considerent la piete comme une affaire d\'argent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 6, 'Oui, sans doute, la piete est une affaire et une excellente affaire pour celui qui est content de son sort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 7, 'Car nous n\'avons rien apporte dans le monde, et nous n\'en pouvons rien emporter.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 8, 'Sachons nous contenter d\'avoir de quoi nous nourrir et nous couvrir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 9, 'Ceux qui veulent devenir riches se laissent tenter, tombent dans le piege et ont une quantite de desirs deraisonnables et pernicieux qui entrainent les hommes a leur ruine et a leur perte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 10, 'Car la racine de tous les maux c\'est l\'amour de l\'argent ; quelques-uns s\'y sont abandonnes, se sont detournes de la foi et se sont cree a eux-memes de cruels remords.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 11, 'Pour toi, homme de Dieu, fuis ces choses, et recherche la justice, la piete, la foi, l\'amour, la patience, la douceur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 12, 'Combats le bon combat de la foi, empare-toi de la vie eternelle en vue de laquelle tu as recu ta vocation, et tu as fait ta belle declaration devant de nombreux temoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 13, 'Je te recommande, en presence du Dieu qui donne la vie a toutes choses et de Jesus-Christ, qui a rendu temoignage devant Ponce-Pilate dans une si belle declaration,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 14, 'd\'observer ces commandements sans tache et sans reproche jusqu\'a la venue de notre Seigneur Jesus-Christ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 15, 'que nous montrera, au temps marque, le bienheureux et unique Souverain, le Roi des rois, le Seigneur des seigneurs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 16, 'le seul Etre immortel, Celui qui habite une lumiere inaccessible, que personne n\'a vu ni ne peut voir ; a lui soit a jamais honneur et puissance. Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 17, 'Aux riches de ce monde tu recommanderas de ne pas etre orgueilleux, de ne pas compter sur des richesses instables, mais sur Dieu qui nous fournit largement tout ce dont nous avons besoin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 18, 'Recommande-leur de faire du bien, de s\'enrichir de bonnes oeuvres, d\'etre genereux, charitables,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 19, 'economisant ainsi un beau fonds pour l\'avenir afin d\'acquerir la veritable vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 20, 'Timothee, garde bien ton depot, evite les discussions inutiles et profanes, les objections de ce qu\'on appelle a tort science.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (54, 6, 21, 'Certaines gens, en affectant de s\'y adonner, se sont eloignes de la foi. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 1, 'Paul, apotre de Jesus-Christ par la volonte de Dieu, pour annoncer la promesse de la vie qui est en Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 2, 'a Timothee son enfant bien-aime. Grace, misericorde, paix te soient accordees par Dieu notre Pere et par Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 3, 'Je rends graces au Dieu que je sers, comme mes ancetres, avec une conscience pure (car je te nomme sans cesse, nuit et jour, dans mes prieres');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 4, 'et, quand je me souviens de tes larmes, j\'ai un ardent desir de te revoir, ce serait pour moi le comble de la joie),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 5, 'me rappelant combien ta foi est sincere. Cette foi animait autrefois ta grand\'mere Lois, puis ta mere Eunice ; je suis convaincu qu\'elle t\'anime aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 6, 'Voila le motif pour lequel je te recommande d\'entretenir le don que Dieu t\'a fait dans sa grace, quand je t\'ai impose les mains ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 7, 'car Dieu ne nous a pas donne un esprit de timidite, mais de force, d\'amour, de sagesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 8, 'N\'aie donc pas honte de rendre temoignage a notre Seigneur, ni de moi, prisonnier pour lui ; sache souffrir avec moi pour l\'Evangile par la puissance que Dieu te donne ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 9, 'lui qui nous a sauves et nous a adresse un saint appel. Ce n\'est pas a cause de nos oeuvres ; c\'est une decision de sa liberte, une grace qu\'il nous a accordee en Jesus-Christ de toute eternite');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 10, 'et qui s\'est montree de nos jours par l\'apparition de notre Sauveur Jesus-Christ, qui a aneanti la mort et mis en pleine lumiere la vie et mortalite par l\'Evangile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 11, 'dont j\'ai ete fait le heraut, l\'apotre et le docteur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 12, 'Voila encore le motif pour lequel je souffre ce que je souffre, mais sans en avoir honte ; car je sais en qui j\'ai cru et je suis persuade que Celui-la a le pouvoir de garder mon depot jusqu\'au grand jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 13, 'Conserve dans l\'esprit de foi et d\'amour qui s\'inspire de Jesus-Christ le modele des saines instructions que tu m\'as entendu te donner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 14, 'Garde le precieux depot avec l\'aide de l\'Esprit saint qui demeure en nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 15, 'Tu sais que tous ceux d\'Asie, entre autres Phygelles et Hermogene, m\'ont abandonne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 16, 'Que le Seigneur fasse descendre sa grace sur la famille d\'Onesiphore, car il m\'a souvent fortifie et n\'a pas eu honte de la chaine que je porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 17, 'Au contraire, des son arrivee a Rome, il s\'est mis avec empressement a ma recherche et a fini par me trouver.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 1, 18, 'Que le Seigneur lui donne de rencontrer sa grace au grand jour! Et tous les services qu\'il a rendus a Ephese, tu les connais mieux que personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 1, 'Pour toi, mon enfant, cherche la force dans la grace qui est en Jesus-Christ ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 2, 'et ce que tu m\'as entendu dire en presence de plusieurs temoins, transmets-le a des croyants, a des hommes qui soient capables a leur tour d\'en enseigner d\'autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 3, 'Prends ta part de souffrances comme un bon soldat de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 4, 'Personne, partant pour la guerre, ne va s\'embarrasser des affaires de la vie civile, s\'il veut plaire a celui qui l\'a enrole ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 5, 'et jamais athlete ne remporte la couronne, qui n\'a pas lutte suivant les regles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 6, 'Le laboureur doit commencer par travailler pour un, jour faire la moisson.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 7, 'Pense bien a ce que je dis la, et c\'est le Seigneur qui te donnera de tout comprendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 8, 'Souviens-toi de Jesus-Christ, ressuscite des morts, descendant de David, d\'apres mon Evangile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 9, 'Evangile pour lequel j\'endure de telles souffrances que je suis enchaine comme un malfaiteur ; mais la parole de Dieu n\'est pas enchainee!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 10, 'Voici pourquoi je supporte tout: c\'est a cause des elus, c\'est pour qu\'ils obtiennent le salut qui est en Jesus-Christ, avec une gloire eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 11, 'Un fait est certain : si nous mourons avec lui, nous vivrons aussi avec lui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 12, 'si nous perseverons, nous serons dans son Royaume avec lui ; si nous le renions, lui aussi nous reniera ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 13, 'si nous sommes infideles, lui reste fidele, car il ne peut se renier lui-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 14, 'Voila ce que tu dois rappeler en conjurant, en presence de Dieu, de renoncer aux disputes de mots, qui ne servent a rien et sont la ruine de ceux qui les entendent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 15, 'Fais des efforts pour te montrer a Dieu ouvrier eprouve, qui n\'a point a rougir, qui suit fidelement la parole de la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 16, 'Evite les conversations vaines et profanes ; car, avec elles, on fera d\'enormes progres dans l\'impiete,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 17, 'et la parole deviendra une vraie gangrene qui etendra partout ses ravages. Voila ce qui est arrive a Hymenee et a Philete:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 18, 'ils se sont detournes de la verite, ils disent que la resurrection a deja eu lieu, et ils detruisent la foi d\'un certain nombre de personnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 19, 'Cependant l\'edifice dont Dieu a pose l\'inebranlable fondement se dresse portant cette inscription : Le Seigneur connait les siens ; et: Quiconque invoque le nom du Seigneur doit se detourner de l\'iniquite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 20, 'Dans une grande maison, il n\'y a pas seulement des vases d\'or et d\'argent, il y en a aussi de bois et de terre ; les uns ont un noble usage, les autres en ont un vulgaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 21, 'Eh bien, celui qui veille sur sa purete, qui s\'eloigne de ces gens-la, sera un vase destine a un noble usage, sanctifie, utile a son maitre, propre a toute bonne oeuvre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 22, 'Fuis les passions de la jeunesse, recherche la justice, la foi, la charite, la paix avec ceux qui invoquent le Seigneur d\'un coeur pur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 23, 'Repousse les questions absurdes et deraisonnables, tu sais qu\'elles engendrent des querelles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 24, 'Or, le serviteur du Seigneur ne doit pas etre querelleur, mais au contraire doux envers tout le monde, savoir enseigner, savoir supporter le mal,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 25, 'savoir ramener par la douceur ceux qui le contredisent, dans l\'espoir que Dieu leur donnera de changer d\'avis, de reconnaitre la verite, de revenir a leur bon sens,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 2, 26, 'd\'echapper aux pieges du Diable qui les tient esclaves et leur fait faire tout ce qu\'il veut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 1, 'Sache bien que, pendant les derniers jours, il y aura des moments tres difficiles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 2, 'Les hommes seront egoistes, avares, pretentieux, arrogants, medisants, rebelles a leurs parents, ingrats, irreligieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 3, 'durs, deloyaux, calomniateurs, intemperants, impitoyables, indifferents,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 4, 'traitres, emportes, vaniteux, aimant moins Dieu que leurs plaisirs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 5, 'se donnant des airs de piete et reniant tout ce qui fait la force de la piete. Evite ces gens-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 6, 'Il y a, entre autres, ceux qui se glissent dans les familles, qui s\'emparent de miserables femmes, chargees de peches, travaillees par toutes sortes de passions,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 7, 'voulant toujours apprendre et ne pouvant jamais arriver a connaitre la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 8, 'De meme que Jannes et Jambres s\'opposerent a Moise, de meme ces hommes s\'opposent a la verite, ils ont l\'esprit faux, et leur foi ne resiste pas a l\'epreuve.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 9, 'Mais ils n\'iront pas plus loin, car leur folie sera bientot evidente pour tout le monde, comme l\'a ete celle de ces deux imposteurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 10, 'Toi, tu m\'es attache par l\'enseignement, la conduite, les projets, la foi, la patience, l\'amour, le support,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 11, 'les persecutions, les souffrances, telles que je les ai endurees a Antioche, a Iconium, a Lystres! Que de persecutions j\'ai eu a subir! et chaque fois le Seigneur m\'a delivre!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 12, 'Tous ceux qui voudront mener une vie pieuse en Jesus-Christ, seront persecutes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 13, 'Quant aux mechants et aux charlatans, ils tomberont toujours plus bas, aussi bien les seducteurs que les egares.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 14, 'Toi, persevere dans ce que tu as appris et reconnu certain, n\'oublie pas qui t\'a instruit');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 15, 'et que, depuis ton enfance, tu connais les saintes Ecritures qui peuvent, par la foi en Jesus-Christ, te faire comprendre le salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 16, 'Toute Ecriture est inspiree de Dieu et utile pour a enseigner, pour avertir, pour corriger, pour former a la justice,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 3, 17, 'afin que l\'homme de Dieu soit accompli et propre a toute bonne oeuvre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 1, 'Je te conjure, en presence de Dieu et de Jesus-Christ qui doit, juger les vivants et les morts, et au nom de son avenement et de son Royaume,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 2, 'de precher la parole, d\'insister a propos et hors de propos, de reprendre, d\'exhorter, de censurer, toujours avec patience et toujours en instruisant ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 3, 'car il viendra un temps ou on ne supportera plus une saine instruction, mais chacun suivra son gout particulier, ou s\'entourera en quantite de ces docteurs qui savent chatouiller agreablement les oreilles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 4, 'et alors on se detournera de la verite et on s\'attachera a des fables.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 5, 'Pour toi, sois sobre de toutes ces choses, apprends a souffrir, remplis tes devoirs d\'evangeliste, donne-toi completement a ton ministere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 6, 'Quant a moi, je suis bien pres du sacrifice, je touche au moment du depart ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 7, 'j\'ai combattu le bon combat, j\'ai acheve la course, j\'ai garde la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 8, 'Il ne me reste plus qu\'a recevoir la couronne de la justice ; le Seigneur, le juste juge, me la donnera au grand jour, et non seulement a moi, mais aussi a tous ceux qui ont desire son avenement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 9, 'Tache de me rejoindre bientot,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 10, 'car Demas m\'a abandonne par a amour pour le monde ; il est alle a Thessalonique, Crescens en Galatie, Tite en Dalmatie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 11, 'Luc seul est avec moi. Prends Marc et amene-le avec toi, car il m\'est utile dans mon ministere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 12, 'Quant a Tychique, je l\'ai envoye a Ephese.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 13, 'Lorsque tu viendras, apporte-moi le manteau que j\'ai laisse a Troas chez Carpus ; les livres aussi ; surtout les parchemins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 14, 'Alexandre, le forgeron, m\'a temoigne une tres grande malveillance ; que le Seigneur Lui rende selon ses oeuvres!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 15, 'Garde-toi aussi de lui ; il s\'est montre tres oppose a mes paroles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 16, 'Lors de mon premier plaidoyer personne n\'est venu a mon aide ; ils m\'ont tous abandonne (qu\'il ne leur en soit pas tenu compte!) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 17, 'mais le Seigneur m\'a soutenu et m\'a donne des forces ; j\'ai pu precher d\'une maniere tres complete ; les paiens m\'ont tous entendu, et j\'ai ete delivre de la gueule du lion.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 18, 'Le Seigneur me delivrera de toute facheuse affaire et me sauvera dans son celeste Royaume ; a lui soit la gloire aux siecles des siecles. Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 19, 'Salue Prisca, Aquilas et la famille d\'Onesiphore.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 20, 'Eraste est reste a Corinthe ; j\'ai laisse Trophime malade a Milet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 21, 'Tache de venir avant l\'hiver. Eubulus te salue, ainsi que Pudens, Linus, Claudie, et tous les freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (55, 4, 22, 'Le Seigneur soit avec ton esprit. La grace soit avec vous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 1, 'Paul, serviteur de Dieu, apotre de Jesus-Christ, pour annoncer la foi des elus de Dieu, pour faire connaitre la verite conforme a la piete');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 2, 'et donner l\'esperance de la vie eternelle, promise depuis un temps immemorial par le Dieu veridique,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 3, 'revelee au temps marque par sa parole dont il a donne l\'ordre de me confier la predication, lui, notre Dieu sauveur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 4, 'a Tite, mon veritable enfant dans la foi qui nous est commune. Grace et paix te soient donnees par Dieu notre Pere et par Jesus-Christ notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 5, 'Voici pourquoi je t\'ai laisse en Crete : pour que tu regles ce qui reste a regler et que, dans chaque ville, tu installes des Anciens aux conditions que je t\'ai indiquees :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 6, 'etre sans reproche, mari d\'une seule femme, avoir des enfants croyants, qui ne soient ni indisciplines, ni accuses de vivre dans le desordre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 7, 'Oui, le conducteur d\'une Eglise doit etre sans reproche, etant l\'intendant de Dieu ; il ne doit etre ni insolent, ni colere ; n\'aimer ni le vin ni les querelles ; ne pas etre bassement interesse ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 8, 'mais etre hospitalier, aimer a faire le bien ; etre modere, juste, pieux, sobre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 9, 'attache a la parole authentique, conformement a la doctrine recue, pour etre capable, lui aussi, d\'exhorter par un sain enseignement et de refuter les contradicteurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 10, 'Car ils sont nombreux, surtout parmi les circoncis, les indisciplines, les bavards, les trompeurs');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 11, 'auxquels il faut fermer la bouche. Ces gens-la mettent le desordre dans des familles entieres, enseignant ce qu\'il ne faut pas enseigner et cela par honteux interet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 12, 'Un des leurs a ete leur propre prophete quand il a dit : ...Cretois, toujours menteurs, vilaines betes, ventres paresseux...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 13, 'Voila un jugement merite ; voila le motif de les reprendre severement, pour qu\'ils aient une foi saine,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 14, 'qu\'ils ne s\'occupent pas de contes judaiques et de commandements d\'hommes abandonnant la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 15, 'Tout est pur pour ceux qui sont purs ; quant aux corrompus et aux incredules, rien ne leur est pur ; leurs pensees et leurs consciences sont corrompues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 1, 16, 'Ils pretendent connaitre Dieu, et leurs oeuvres le renient ; ils sont abominables, rebelles, incapables de rien faire de bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 1, 'Mais, toi, preche comme le demande un sain enseignement :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 2, 'Que les hommes d\'un age mur doivent etre sobres, mesures, serieux, sains dans leur foi, leur charite, leur patience.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 3, 'Que les femmes agees doivent aussi avoir un exterieur respectable, n\'etre ni medisantes, ni adonnees a la boisson ; qu\'elles doivent etre bonnes conseilleres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 4, 'qu\'elles apprennent aux jeunes femmes a aimer leurs maris et leurs enfants,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 5, 'a etre serieuses, chastes, bonnes menageres, soumises, chacune a son mari, pour qu\'on ne dise point de mal de la parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 6, 'Exhorte aussi les jeunes gens a etre serieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 7, 'et donne toi-meme a tous egards le modele de toutes les vertus, de la purete dans l\'enseignement, de la dignite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 8, 'd\'une predication saine et irreprochable, et l\'adversaire aura la confusion de ne pouvoir dire aucun mal de nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 9, 'Aux esclaves tu diras d\'etre soumis a leurs maitres, qu\'en tout ils cherchent a leur plaire, qu\'ils ne les contredisent pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 10, 'qu\'ils ne detournent rien, mais qu\'ils se montrent en tout parfaitement fideles, et ainsi ils feront honneur en tout a la doctrine de Dieu notre Sauveur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 11, 'Car la grace de Dieu qui sauve tous les hommes a ete revelee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 12, 'elle nous apprend a renoncer a l\'impiete et aux plaisirs du monde, a vivre sagement, justement et pieusement dans le monde present,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 13, 'et a attendre la realisation de notre bienheureuse esperance, l\'apparition de la gloire de notre grand Dieu et Sauveur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 14, 'Il s\'est lui-meme donne pour nous afin de nous racheter de tout peche et de se creer un peuple particulier et zele pour les bonnes oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 2, 15, 'Voila ce que tu dois dire, enseigner, recommander avec une pleine autorite ; que personne ne te meprise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 1, 'Rappelle aux fideles qu\'ils ont a se soumettre aux autorites, aux, puissances, a leur obeir, a etre prets a faire le bien ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 2, 'a ne dire de mal de personne, a etre paisibles, indulgents, a se montrer doux envers tout le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 3, 'Nous aussi etions autrefois insenses, desobeissants, egares, esclaves de toutes sortes de plaisirs et de passions, vivant dans la mechancete et la jalousie, ne meritant que la haine et nous haissant les uns les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 4, 'Mais quand a paru la bonte, l\'amour pour les hommes de Dieu notre Sauveur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 5, 'nous avons ete sauves, non parce que nous avions accompli des oeuvres de justice, mais parce qu\'il est misericordieux ; il nous a purifies, regeneres, renouveles par l\'Esprit saint');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 6, 'qu\'il a liberalement repandu sur nous par Jesus-Christ notre Sauveur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 7, 'C\'est ainsi que justifies par sa grace, nous devenons, en esperance, heritiers de la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 8, 'Le fait est certain, et je veux que tu les en persuades, afin que ceux qui ont cru en Dieu se mettent a faire de bonnes oeuvres. Voila ce qui est bon et utile aux hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 9, 'Quant aux sottes questions, aux genealogies, aux querelles et aux discussions sur la Loi, ecarte-les ; tout cela est frivole et inutile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 10, 'Tu rompras apres deux avertissements avec celui qui fomente des divisions ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 11, 'ces gens-la, sache-le, sont coupables, pervertis et se condamnent eux-memes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 12, 'Des que je t\'aurai envoye soit Artemas, soit Tychique, tache de me rejoindre a Nicopolis, car je me suis decide a y passer l\'hiver.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 13, 'Tu auras bien soin de Zenas, le docteur de la Loi, et d\'Apollon, quand ils partiront ; qu\'il ne leur manque rien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 14, 'Les notres aussi doivent apprendre, dans les cas urgents, a pratiquer de bonnes oeuvres et ainsi a ne pas rester steriles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (56, 3, 15, 'Tous ceux qui sont avec moi te saluent. Salue les croyants qui nous aiment. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 1, 'Paul, prisonnier de Jesus-Christ, et le frere Timothee a Philemon, notre bien-aime et notre collaborateur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 2, 'a notre soeur Appia, a Archippe, notre compagnon d\'armes, et a l\'eglise qui s\'assemble dans ta maison.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 3, 'Grace et paix vous soient accordees par Dieu notre Pere et par le Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 4, 'Je rends sans cesse grace a mon Dieu quand ton souvenir se presente a moi dans mes prieres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 5, 'J\'apprends, en effet, ta foi au Seigneur Jesus et ton amour pour tous les fideles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 6, 'et je demande que ta foi se communique et porte des fruits et que tu saches ce qui pour nous est le bien, en vue de Christ !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 7, 'Ton amour, en effet, nous a cause beaucoup de joie et de consolation ; car les coeurs des fideles ont ete tranquillises par toi, frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 8, 'Aussi aurais-je le droit en Christ de te prescrire en toute confiance ce que tu dois faire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 9, 'mais je prefere m\'adresser a toi au nom de cet amour, et en mon nom, au nom de Paul, un vieillard, et, dans ce moment, un prisonnier de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 10, 'Je t\'adresse donc une priere pour mon fils, que j\'ai engendre dans les fers, pour Onesime.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 11, 'Il s\'appelle Utile, et il ne te servait a rien autrefois. A l\'avenir il nous servira beaucoup a tous les deux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 12, 'Je te le renvoie, lui, c\'est-a-dire la meilleure partie de moi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 13, 'Je voulais le retenir aupres de moi, pour qu\'au lieu de te servir, il me servit pendant que je suis prisonnier pour l\'Evangile ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 14, 'mais je n\'ai rien voulu faire sans ton avis, afin de ne pas t\'obliger a lui faire du bien, et de te laisser entierement libre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 15, 'Peut-etre n\'a-t-il ete separe de toi un moment qu\'afin que tu le retrouves a jamais');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 16, 'non plus comme esclave, mais comme frere bien-aime au lieu d\'esclave ; oui, bien-aime de moi d\'abord et plus encore de toi et selon la nature et selon le Seigneur!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 17, 'Si donc tu me consideres comme ton ami, recois-le comme moi-meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 18, 'S\'il t\'a fait quelque tort, s\'il te doit quelque chose, passe-le a mon compte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 19, 'Moi, Paul,  j\'ecris ceci de ma main  je te rembourserai, sans te rappeler que tu es mon debiteur et que tu l\'es de ta propre personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 20, 'Allons, frere, fais-moi ce plaisir dans le Seigneur! Tranquillise mon coeur en Christ!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 21, 'Je t\'ecris convaincu de ton obeissance, certain meme que tu iras au dela de ce que je te demande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 22, 'En meme temps, prepare-toi a me recevoir ; car j\'espere vous etre rendu, grace a vos prieres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 23, 'Epaphras, mon compagnon de chaines en Jesus-Christ, te salue');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 24, 'ainsi que Marc, Aristarque, Demas, Luc, mes collaborateurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (57, 1, 25, 'La grace de notre Seigneur Jesus-Christ soit avec votre esprit. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 1, 'C\'est a plusieurs reprises et de plusieurs manieres que Dieu a autrefois parle a nos peres par les prophetes ; et, de nos jours, qui sont les derniers,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 2, 'il nous a parle, a nous, par un Fils, qu\'il a fait heritier de toutes choses et par lequel il a aussi cree le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 3, 'Etant un reflet de sa gloire et une empreinte de son essence, soutenant toutes choses par sa puissante parole, ce Fils a accompli l\'oeuvre de la purification des peches et ...s\'est assis a la droite... de la majeste divine dans les regions celestes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 4, 'devenu d\'autant superieur aux anges que le nom dont il a herite est plus eminent que le leur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 5, 'Auquel des anges, en effet, Dieu a-t-il jamais dit : Tu es mon Fils ; Je t\'ai engendre aujourd\'hui, et ailleurs : Je serai son Pere, et il sera mon Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 6, 'Et lorsque, dans un autre passage, il introduit le premier-ne dans le monde, il dit : Que tous les anges de Dieu l\'adorent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 7, 'Quant aux anges, il en dit ceci ...Celui qui fait de ses anges des esprits Et de ses serviteurs une flamme de feu...');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 8, 'Il dit, au contraire, du Fils : Ton trone, Dieu, est eternel. Le sceptre de ta royaute est un sceptre de droiture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 9, 'Tu as aime la justice et tu as hai l\'iniquite. Voila pourquoi, Dieu, ton Dieu t\'a oint D\'une huile de rejouissance, de preference a tes egaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 10, 'Et ensuite : C\'est toi, Seigneur, qui, au commencement, as fonde la terre Et les cieux sont l\'oeuvre de tes mains.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 11, 'Ils periront, toi tu restes. Ils vieilliront tous comme un manteau,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 12, 'Tu les plieras comme une couverture, comme un manteau, et ils se transformeront, Mais toi tu restes le meme Et tes annees ne finiront pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 13, 'Auquel des anges Dieu a-t-il jamais dit : Assieds-toi a ma droite Jusqu\'a ce que j\'aie mis tes ennemis sous tes pieds comme un marchepied?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 1, 14, 'Les anges ne sont-ils pas tous des esprits subalternes, employes au service de Dieu, pour ceux qui doivent heriter du salut?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 1, 'Voila pourquoi nous devons apporter une attention toute speciale a ce que nous avons entendu, pour ne pas le perdre par notre negligence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 2, 'Si, en effet, la parole apportee par des anges a recu sa sanction, si toute transgression et toute desobeissance ont ete punies comme elles le meritaient,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 3, 'comment echapperions-nous si nous negligions un si grand salut, salut qui d\'abord fut annonce par le Seigneur, et qui nous fut ensuite confirme par ceux qui l\'avaient entendu de lui,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 4, 'et dont Dieu appuyait le temoignage par des signes, des prodiges, toutes sortes de miracles et par son Esprit saint qu\'il repartissait selon sa volonte?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 5, 'car ce n\'est pas aux anges qu\'il a soumis le monde a venir dont nous parlons.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 6, 'On lit quelque part cette declaration : Qu\'est-ce que l\'Homme que tu te souviennes de lui? Ou le Fils de l\'homme que tu t\'en occupes?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 7, 'Tu l\'as mis pour quelque temps au-dessous des anges, Tu l\'as couronne de gloire et d\'honneur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 8, 'Tu as tout mis sous ses pieds ! Ce terme : tu lui as tout soumis indique que Dieu n\'a rien laisse en dehors de son pouvoir. Cependant nous ne voyons pas encore que tout lui soit soumis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 9, 'Nous voyons seulement que Jesus mis pour quelque temps au-dessous des anges a ete, a cause de ses souffrances et de sa mort, couronne de gloire et d\'honneur. C\'etait par une grace de Dieu, afin de subir la mort pour tous les hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 10, 'Il etait digne, en effet, de Celui par lequel et a cause duquel tout a ete cree, de Celui qui voulait conduire a la gloire un grand nombre de fils, d\'elever par des souffrances jusqu\'a la perfection l\'auteur de leur salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 11, 'Tous, et celui qui sanctifie et ceux qui sont sanctifies, ont un meme Pere ; voila pourquoi Jesus ne dedaigne pas d\'appeler ceux-ci ses freres');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 12, 'quand il dit : Jannoncerai ton nom a mes freres ; Au milieu de l\'assemblee, je te louerai.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 13, 'Et ailleurs : Je mettrai ma confiance en lui. Ailleurs encore : Me voici, moi et les enfants que Dieu m\'a donnes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 14, 'Puisque les enfants ont tous en partage la chair et le sang, lui en a eu aussi sa part pour detruire par sa mort celui qui est le maitre de la mort, je veux dire le Diable,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 15, 'et pour delivrer ceux que la crainte de la mort rendait esclaves toute leur vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 16, 'Car assurement ce n\'est pas aux anges qu\'il porte secours, c\'est a la race d\'Abraham.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 17, 'Par suite, il le fallait entierement semblable a ses freres, afin d\'etre, pour leurs rapports avec Dieu, un grand-pretre compatissant et digne de foi, pour faire l\'expiation des peches du peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 2, 18, 'C\'est parce qu\'il a souffert, c\'est parce qu\'il a ete eprouve qu\'il peut secourir ceux qui sont eprouves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 1, 'En consequence, mes saints freres, vous qui etes appeles a aller au ciel, fixez les yeux sur l\'apotre et le grand-pretre de la foi que nous professons,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 2, 'sur Jesus, qui etait fidele a celui qui l\'avait institue, comme Moise l\'etait dans toute sa maison .');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 3, '- Mais Jesus a ete juge digne de surpasser Moise en eclat de toute la distance qui separe une maison de son constructeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 4, '(Toute maison a un architecte ; Celui qui a tout construit c\'est Dieu.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 5, 'Moise, lui, a ete fidele DANS toute sa maison, comme un serviteurs, par le temoignage qu\'il a rendu a ce qui devait etre revele plus tard ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 6, 'Christ l\'etait comme un fils, SUR sa maison ; et sa maison a lui c\'est nous, si toutefois nous persistons fermement, jusqu\'a la fin dans l\'assurance et l\'esperance qui font notre gloire.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 7, ' Ainsi, comme le dit l\'Esprit saint : Si vous entendez aujourd\'hui sa voix,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 8, 'N\'endurcissez pas vos coeurs, comme au jour de la revolte Et de la provocation dans le desert,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 9, 'Quand vos peres m\'ont provoque et m\'ont mis a l\'epreuve, Eux qui avaient vu mes oeuvres pendant quarante ans!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 10, 'Voila pourquoi je me suis irrite contre cette generation Et j\'ai dit : leurs coeurs s\'egarent toujours. Ils n\'ont point reconnu mes voies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 11, 'Et voici le serment que j\'ai fait dans ma colere : Jamais ils n\'entreront dans mon repos !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 12, 'Que personne de vous, mes freres, prenez-y garde, n\'ait un mauvais coeur, ne soit incredule, ne se separe du Dieu vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 13, 'Mais exhortez-vous les uns les autres, tous les jours, tant que l\'on peut dire : aujourd\'hui, et qu\'aucun de vous ne se laisse seduire et endurcir par le peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 14, 'Car nous n\'avons notre part du Christ que si nous gardons notre assurance premiere, fermement, jusqu\'a la fin,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 15, 'aussi longtemps qu\'on peut dire : Si vous entendez aujourd\'hui sa voix N\'endurcissez pas vos coeurs, comme au jour de la revolte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 16, 'Lesquels, apres avoir entendu, se sont revoltes? N\'est-ce pas tous ceux que Moise avait conduits hors d\'Egypte?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 17, 'Et contre qui Dieu s\'irrita-t-il pendant quarante ans? N\'est-ce pas contre ceux qui pechaient et dont les corps jonchaient le desert?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 18, 'Et a qui jura-t-il qu\'ils n\'entreraient pas dans son repos? N\'est-ce pas a ceux qui desobeissaient?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 3, 19, 'Nous voyons, en effet, que leur incredulite les empecha d\'y entrer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 1, 'Prenons donc garde : elle dure encore, la promesse d\'entrer dans son repos, et alors, qu\'aucun de vous ne vienne a en perdre le benefice ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 2, 'oui, la Bonne Nouvelle nous en a ete annoncee a nous comme aux autres ; mais, a eux il n\'a servi de rien d\'entendre la parole, parce que, tout en l\'entendant, ils ne se la sont pas appropriee par la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 3, 'Et nous, c\'est si nous avons eu la foi que nous entrerons dans le repos, dont il a dit : Voici le serment que j\'ai fait dans ma colere, Jamais ils n\'entreront dans mon repos!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 4, 'Il a parle ainsi et cependant ses oeuvres etaient achevees depuis la creation du monde (en effet, il a dit quelque part a propos du septieme jour : Dieu se reposa de toutes ses oeuvres le septieme jour ;)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 5, 'et puis, dans le passage ci-dessus il dit de nouveau : Jamais ils n\'entreront dans mon repos!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 6, 'C\'est parce que l\'entree dans le repos est reservee a un certain nombre, et que ceux qui en avaient les premiers recu la Bonne Nouvelle n\'y sont pas entres a cause de leur desobeissance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 7, 'c\'est pour cela qu\'il fixe de nouveau un jour : aujourd\'hui et il le fait dans un passage de David, bien longtemps apres, nous l\'avons cite plus haut : Si vous entendez aujourd\'hui sa voix, N\'endurcissez pas vos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 8, 'Si, en effet, Josue leur avait donne le repos, Dieu ne parlerait pas plus tard d\'un autre jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 9, 'Donc, il reste un repos, un sabbat pour le peuple de Dieu');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 10, 'et celui qui entrera dans son repos se reposera de ses oeuvres comme Dieu des siennes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 11, 'Empressons-nous donc d\'entrer dans ce repos pour qu\'aucun de nous ne tombe en donnant, comme les autres, l\'exemple de la desobeissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 12, 'Car elle est vivante, la parole de Dieu, et puissante, et plus aceree qu\'aucune epee a deux tranchants, elle penetre jusqu\'a diviser et l\'ame et l\'esprit et les jointures et les moelles, elle juge des sentiments et des intentions du coeur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 13, 'aucune creature ne se derobe devant elle ; tout est a nu, tout est a decouvert aux yeux de Celui a qui nous avons affaire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 14, 'Eh bien, puisque nous avons un tel grand-pretre, qui a penetre jusqu\'au fond des cieux, qui s\'appelle Jesus, le Fils de Dieu, soyons fermes a confesser notre foi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 15, 'Et, en effet, nous n\'avons pas un grand-pretre qui ne puisse compatir a nos faiblesses ; au contraire, il a ete tente en toutes choses exactement comme nous, et il est reste sans peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 4, 16, 'Approchons-nous donc avec assurance du trone de la grace afin d\'obtenir misericorde et de trouver grace en un temps bien opportun pour etre secouru.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 1, 'Tout grand-pretre, pris parmi les hommes, est etabli pour le bien des hommes, pour regler leurs relations avec Dieu ; dans ce but, il offre des oblations et des sacrifices pour les peches ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 2, 'il peut etre indulgent pour les ignorants et les egares, puisque lui aussi est sujet a la faiblesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 3, 'Aussi est-il tenu a des sacrifices expiatoires aussi bien pour lui-meme que pour le peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 4, 'De plus, on ne s\'arroge pas soi-meme cette dignite ; c\'est Dieu qui vous y appelle, comme il fit pour Aaron.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 5, 'Ainsi le Christ : il ne s\'est pas arroge lui-meme la gloire d\'etre grand-pretre, mais Dieu lui a dit : Tu es mon Fils, Je t\'ai engendre aujourd\'hui!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 6, 'et dans un autre passage : Tu es pretre a tout jamais Selon l\'ordre de Melchisedek.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 7, 'Lui qui, pendant sa vie sur la terre, a offert, a celui qui pouvait le delivrer de la mort, des prieres et des supplications en jetant de grands cris et en versant des larmes, lui qui a ete exauce a cause de sa piete');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 8, '(il etait Fils, et cependant par ses souffrances il a appris l\'obeissance),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 9, 'arrive alors au plus haut point de la perfection, il est devenu pour tous ceux qui lui obeissent l\'auteur d\'un salut, eternel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 10, 'ayant ete proclame par Dieu grand-pretre selon l\'ordre de Melchisedek.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 11, 'C\'est un sujet sur lequel nous avons beaucoup a dire et des choses difficiles a expliquer, parce que vous etes lents a comprendre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 12, 'Il y a longtemps que vous devriez etre des maitres, et vous avez encore besoin qu\'on vous apprenne les premiers elements des revelations de Dieu ; vous en etes a avoir besoin de lait au lieu d\'une nourriture solide.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 13, 'Celui qui en est encore au lait ne comprend pas un enseignement complet ; il n\'est qu\'un enfant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 5, 14, 'La nourriture solide est pour les hommes faits, pour ceux qui ont acquis par la pratique un sens exerce a discerner le vrai du faux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 1, 'Laissons donc les commencements de ce qu\'il y a a dire sur le Christ, mettons-nous a ce qu\'il y a de plus acheve, ne recommencons pas a poser les principes fondamentaux: renoncement aux oeuvres mortes, foi en Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 2, 'doctrine des baptemes, imposition des mains, resurrection des morts, jugement eternel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 3, 'Tout cela nous le ferons, si Dieu le permet.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 4, 'Car il est impossible que ceux qui ont ete une fois eclaires, qui ont goute le don celeste, qui ont eu part a l\'Esprit saint,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 5, 'qui ont goute l\'excellente parole de Dieu et les richesses du monde a venir');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 6, 'et qui retombent, soient ramenes a la repentance par une seconde regeneration, eux qui, pour leur propre compte, remettent en croix le Fils de Dieu et le vouent aux outrages!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 7, 'En effet, une terre, arrosee par des pluies frequentes et produisant des plantes utiles a ceux pour lesquels elle est cultivee, a part a la benediction de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 8, 'Mais si elle ne rapporte que des epines et des chardons, on l\'abandonne, on la declare presque maudite, on finit par y mettre le feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 9, 'Cependant, bien-aimes, malgre ce langage, nous croyons pour vous a tout ce qu\'il y a de mieux, a ce qui mene au salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 10, 'Dieu est trop juste pour oublier votre activite, l\'amour que vous avez montre pour son nom, les services passes et presents rendus par vous aux fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 11, 'Nous desirons bien que chacun de vous montre jusqu\'au bout le meme zele a affermir de plus en plus son esperance');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 12, 'de maniere a ne pas vous relacher, mais a imiter ceux que leur foi et leur perseverance ont mis en possession de l\'heritage promis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 13, 'Quand Dieu fit sa promesse a Abraham (comme il ne pouvait preter serment au nom d\'un plus grand que lui, il preta serment sur son propre nom),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 14, 'il dit: Certes je te comblerai de benedictions ; certes je multiplierai beaucoup tes descendants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 15, 'Et alors, sa perseverance lui obtint ce qui lui avait ete promis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 16, 'Les hommes, en effet, pretent serment sur un plus grand qu\'eux, et leur serment est une garantie, il met fin a toutes leurs discussions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 17, 'Eh bien, Dieu, voulant expressement montrer a ceux qui devaient recevoir l\'heritage promis que sa volonte est immuable, intervint par un serment,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 18, 'et il l\'a fait pour que, par deux choses immuables, par lesquelles il etait impossible que Dieu mentit, nous ayons un puissant encouragement qui soit notre refuge, nous saisissions l\'esperance qui s\'offre a nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 19, 'Oui, cette esperance nous la tenons comme l\'ancre de notre ame, ferme et solide, elle penetre dans le sanctuaire, derriere le voile,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 6, 20, 'la ou Jesus notre precurseur est entre pour nous quand il est devenu grand-pretre a tout jamais selon l\'ordre de Melchisedek.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 1, 'En effet, ce Melchisedek, roi de Salem, pretre du Dieu Tres-Haut, qui alla au-devant d\'Abraham, revenant de vaincre les rois, et le benit,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 2, 'qui recut d\'Abraham la dime de tout, dont le nom signifie d\'abord roi de justice (roi de Salem, qui vient ensuite, veut dire roi de paix),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 3, 'qui est sans pere, sans mere, sans genealogie, dont les jours et la vie n\'ont ni commencement, ni fin, qui est absolument assimile au Fils de Dieu, reste pretre a perpetuite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 4, 'Voyez maintenant comme il doit etre grand, celui auquel Abraham lui-meme, le patriarche, a donne une dime prise sur les premices de son butin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 5, 'Or, la Loi ordonne a ceux des enfants des Levites qui recoivent la pretrise de lever la dime sur le peuple, c\'est-a-dire sur leurs freres, bien qu\'ils soient egalement issus d\'Abraham,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 6, 'et voila que celui qui n\'est pas de leur famille a leve la dime sur Abraham et a beni celui qui avait recu les promesses!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 7, 'Mais, de l\'aveu de tous, c\'est l\'inferieur qui est beni par le superieur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 8, 'de plus, ce sont ici des hommes mortels qui percoivent la dime, la c\'est quelqu\'un dont il est atteste qu\'il possede la vie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 9, 'et Levi lui-meme, pour ainsi dire, lui qui doit percevoir la dime, l\'a payee dans la personne d\'Abraham,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 10, 'car il etait encore dans les reins de son pere quand Melchisedek alla au-devant de ce patriarche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 11, 'Si l\'on avait pu realiser la perfection par le sacerdoce levitique (car la legislation donnee au peuple a pour base ce sacerdoce), pourquoi fallait-il qu\'il parut un autre pretre selon l\'ordre de Melchisedek pourquoi pas selon l\'ordre d\'Aaron?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 12, 'Au changement de l\'ordre du sacerdoce correspond necessairement un changement de Loi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 13, 'et celui auquel s\'appliquent ces paroles, appartient a une, autre tribu dont aucun membre n\'a servi a l\'autel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 14, 'Il est notoire, en effet, que notre Seigneur est issu de Juda, et Moise n\'a jamais parle de pretres de cette tribu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 15, 'Et tout cela devient plus evident encore quand c\'est a l\'instar de Melchisedek que parait un autre pretre');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 16, 'nomme en vertu de sa vie imperissable et nullement par une loi ou un commandement charnel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 17, 'Voici, en effet, le temoignage qui lui est rendu : Tu es pretre a tout jamais Selon l\'ordre de Melchisedek');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 18, 'La Loi ancienne a ete abrogee parce qu\'elle etait impuissante et inutile.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 19, 'Elle n\'a rien amene a la perfection et, a la place, est introduite une meilleure esperance par laquelle nous nous approchons de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 20, 'Et il y a eu un serment de prete ; les pretres etaient consacres sans que personne pretat serment,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 21, 'mais lui a ete consacre avec le serment de Celui qui a dit : Le Seigneur a jure et il ne s\'en repentira pas ; Tu es pretre a tout jamais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 22, 'Par la meme, Jesus est devenu le garant d\'une meilleure alliance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 23, 'De plus, il y a eu une succession de pretres, parce que la mort les empechait de continuer leurs fonctions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 24, 'Mais lui, qui est nomme a tout jamais, possede le sacerdoce intransmissible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 25, 'Et voila pourquoi il peut sauver parfaitement ceux qui, par lui, s\'approchent de Dieu, puisqu\'il vit toujours pour interceder en leur faveur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 26, 'Qu\'il etait bien le grand-pretre qu\'il nous fallait, saint, innocent, sans tache, separe des pecheurs et eleve au-dessus des cieux!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 27, 'qui n\'est pas tous les jours oblige comme les autres grands-pretres d\'offrir des sacrifices pour ses propres peches d\'abord, pour ceux du peuple ensuite  (cela il l\'a fait une fois pour toutes en s\'offrant lui-meme), ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 7, 28, 'car la Loi institue grands-pretres des hommes pleins de faiblesse ; or la parole du serment prete apres la Loi institue un Fils arrive pour jamais a la perfection !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 1, 'Voici maintenant, apres tout ce que nous venons de dire, le point capital : Ce grand-pretre que nous avons est tel qu\'il s\'est assis a la droite! du trone de la majeste divine, dans les cieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 2, 'comme ministre du sanctuaire et du vrai tabernacle, dresse par le Seigneur et non par un homme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 3, 'Tout grand-pretre est institue pour offrir des dons et des sacrifices ; il faut donc que celui-la ait aussi quelque chose a offrir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 4, 'S\'il etait sur la terre, il ne serait pas meme pretre  (puisqu\'il s\'y trouve ceux qui offrent les dons selon la Loi');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 5, 'et qui servent ce qui n\'est que la copie, l\'ombre des realites celestes, d\'apres l\'avis donne a Moise lorsqu\'il allait dresser le tabernacle : Aie soin, lui fut-il dit, de tout faire d\'apres le modele qui t\'a ete montre sur la montagne),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 6, 'mais il a maintenant obtenu un ministere d\'autant superieur qu\'il est mediateur d\'une plus excellente Alliance dont la promulgation repose sur de meilleures promesses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 7, 'En effet, si la premiere Alliance avait ete irreprochable, il n\'aurait pas ete question de la remplacer par une seconde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 8, 'Or c\'est bien un reproche qu\'il fait quand il dit: Voici qu\'il vient des jours, dit le Seigneur, Ou je contracterai avec la maison d\'Israel et avec la maison de Juda, Une nouvelle Alliance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 9, 'Elle ne ressemblera pas a l\'Alliance que je fis avec leurs peres Le jour ou je les pris par la main Pour les faire sortir du pays d\'Egypte ; Comme ils ne sont pas restes fideles a mon Alliance, Moi, de mon cote, je les ai negliges, dit le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 10, 'Voici l\'Alliance que je contracterai avec la maison d\'Israel : Apres ces jours-ci Je mettrai mes lois dans leur esprit Et je les graverai dans leur coeur ; Je serai leur Dieu Et ils seront mon peuple,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 11, 'Et aucun d\'eux n\'aura a instruire son concitoyen, Ou son frere en lui disant : Sache qui est le Seigneur. Car tous me connaitront Depuis le plus petit jusqu\'au plus grand');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 12, 'Aussi serai-je plein de misericorde pour leurs iniquites Et ne me souviendrai-je plus de leurs peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 8, 13, 'appelant cette Alliance-la nouvelle il a declare la premiere vieillie ; or ce qui a vieilli, ce qui est suranne est pres de disparaitre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 1, 'La premiere Alliance avait-elle aussi des regles pour le culte et, un sanctuaire terrestre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 2, 'On avait, en effet, construit un tabernacle ; dans sa premiere partie se trouvaient le candelabre, la table et les pains de proposition. On l\'appelait Lieu Saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 3, 'Puis, derriere le second voile, la seconde partie appelee: Lieu Tres-Saint.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 4, 'La, se trouvaient l\'autel d\'or pour les parfums et l\'arche de l\'Allince entierement recouverte d\'or ; dans l\'arche, un vase d\'or renfermant la manne, la verge d\'Aaron qui avait fleuri et les tables de l\'Alliance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 5, 'au-dessus, les Cherubins de la gloire couvrant de leur ombre le propitiatoire : ce n\'est pas le moment de parler de tout cela en detail.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 6, 'Tout etant ainsi dispose, les pretres qui celebrent le culte entrent constamment dans la premiere partie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 7, 'mais dans la seconde le grand-pretre seul et une seule fois dans l\'annee, et jamais sans y porter du sang qu\'il offre et pour lui-meme et pour les peches du peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 8, 'L\'Esprit saint montre par la que l\'entree du Lieu Tres-Saint n\'etait pas ouverte tant que subsistait la premiere partie du tabernacle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 9, 'et celle-ci est un symbole pour le temps present ou l\'on fait encore des offrandes et des sacrifices incapables de mener celui qui les fait a la perfection qu\'exige sa conscience.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 10, 'Ce sont des prescriptions purement charnelles et qui ne sont imposees, comme tout ce qui concerne la nourriture, la boisson, les diverses ablutions, que jusqu\'a l\'epoque de la reforme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 11, 'Mais Christ est survenu, grand-pretre des biens a venir et traversant le tabernacle plus grand et plus parfait, que la main de l\'homme n\'a pas construit (je veux dire qui n\'est pas de cette creation-ci) ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 12, 'il est entre une fois pour toutes dans le Lieu Tres-Saint, non avec du sang de boucs et de veaux, mais avec son propre sang, et il a accompli une eternelle redemption.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 13, 'Car si le sang de boucs et de taureaux, si la cendre d\'une genisse dont on asperge ceux qui sont souilles, les sanctifie et leur donne la purete charnelle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 14, 'combien plus le sang du Christ, qui, avec son esprit eternel, s\'est lui-meme offert a Dieu sans defaut, purifiera-t-il votre conscience des oeuvres mortes pour que vous serviez un Dieu vivant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 15, 'Et voila pourquoi il a fait par sa mediation un nouveau Testament ; sa mort est venue racheter les peches commis sous le premier, et, par elle, les elus ont recu l\'eternel heritage qui la leur avait ete promis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 16, 'En effet, quand on parle de testament, il faut que la mort du testateur soit constatee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 17, 'Un testament n\'est valable qu\'a la mort ; il est de nul effet pendant la vie du testateur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 18, 'Aussi le premier contrat a-t-il ete inaugure avec du sang.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 19, 'Quand Moise eut expose a tout le peuple la Loi entiere, tous les commandements, il prit le sang des veaux et des boucs avec de l\'eau, de la laine rouge et une branche d\'hysope, il aspergea le livre et tout le peuple,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 20, 'en disant : Voici le sang de l\'Alliance que Dieu m\'a charge de faire avec vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 21, 'Il aspergea aussi de sang le tabernacle et tous les objets servant au culte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 22, 'En general, on purifie tout avec du sang, d\'apres la Loi ; sans effusion de sang, il n\'y a pas de pardon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 23, 'Eh bien, puisque l\'image des realites celestes est purifiee de cette, maniere-la, il fallait que ces realites memes le fussent par des sacrifices plus importants encore.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 24, 'Aussi Christ n\'est-il pas entre dans un sanctuaire fait de main d\'homme, dans une imitation du vrai sanctuaire, mais dans le ciel meme, afin de se presenter pour nous devant la face de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 25, 'Et il n\'y est pas entre pour se sacrifier plusieurs fois lui-meme, a la maniere du grand-pretre qui, chaque annee, entre dans le sanctuaire avec du sang etranger.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 26, 'S\'il avait fait cela, il aurait du souffrir plusieurs fois depuis le commencement du monde ; non, il ne s\'est montre qu\'une fois, a la fin du monde, pour annuler le peche par son sacrifice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 27, 'De meme qu\'il faut que les hommes meurent une fois, puis vient leur jugement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 9, 28, 'de meme le Christ ne s\'est offert qu\'une fois Pour aneantir une quantite de peches, puis il reviendra, non plus a cause du peche, mais pour donner le salut a ceux qui l\'attendent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 1, 'En effet, la Loi, ne contenant que l\'ombre des biens a venir, et non l\'image reelle des choses, ne peut jamais par ses sacrifices, toujours les memes et offerts sans cesse chaque annee donner la parfaite purete a ceux qui y prennent part.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 2, 'Autrement n\'aurait-on pas cesse de les offrir, car ceux qui les offraient, une fois purifies, n\'auraient plus eu conscience de leurs peches?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 3, 'Mais, au contraire, ces sacrifices eux-memes ravivaient chaque annee le souvenir des peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 4, 'Car il est impossible que du sang de taureaux et de boucs effacent des peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 5, 'Voila pourquoi le Christ dit en entrant dans le monde : Tu n\'as pas voulu de sacrifice, ni d\'offrande, Tu m\'as forme un corps ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 6, 'les holocaustes, ni les offrandes pour le peche ne t\'ont fait plaisir. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 7, 'J\'ai dit alors : Voici je viens (Dans le volume il est question de moi) Pour faire, Dieu, ta volonte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 8, 'Apres avoir commence par dire : Tu n\'as voulu ni sacrifices, ni oblations, ni holocaustes, ni offrandes pour le peche, et tu n\'y as pas pris plaisir, (Tout cela cependant est offert d\'apres la Loi,)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 9, 'il ajoute : Voici je viens pour faire ta volonte. Il supprime le premier point pour etablir le second,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 10, 'et c\'est grace a cette volonte que nous sommes sanctifies une fois pour toutes, nous qui le sommes par le sacrifice du corps de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 11, 'De plus, chaque pretre se presente tous les jours et officie, offrant des sacrifices, souvent les memes et qui ne peuvent pas effacer les peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 12, 'Lui, au contraire, n\'a offert qu\'un seul sacrifice pour les peches, puis S\'est assis pour toujours a la droite de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 13, 'et desormais il attend Que ses ennemis soient renverses, soient sous ses pieds, comme un marchepied.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 14, 'C\'est par une offrande unique qu\'il a donne pour toujours la parfaite purete a ceux qu\'il sanctifie. D\'ailleurs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 15, 'l\'Esprit saint nous l\'atteste, car, apres avoir dit :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 16, 'Voici l\'alliance que je contracterai avec eux Quand le moment sera venu, dit le Seigneur, Je mettrai mes lois dans leur coeur, Je les graverai dans leur esprit... ajoute :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 17, 'Je n\'aurai plus aucun souvenir de leurs peches et de leurs iniquites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 18, 'Or, la ou il y a pardon, il n\'est plus question de sacrifice pour le peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 19, 'Ainsi, mes freres, puisque nous avons la ferme assurance que nous entrerons dans le sanctuaire, grace au sang de Jesus ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 20, '(car il a inaugure pour nous un chemin nouveau et reel a travers le rideau, c\'est-a-dire par son corps)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 21, ' puisque nous avons un grand-pretre prepose a la maison de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 22, 'approchons-nous avec un coeur sincere, avec une foi pleine et entiere, le coeur et la conscience purifies de tout mal, et le corps lave d\'une eau pure ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 23, 'continuons, sans flechir, a confesser notre esperance (car il est, fidele, Celui qui a fait la promesse!).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 24, 'Ayons l\'oeil les uns sur les autres pour nous exciter a l\'amour et aux bonnes oeuvres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 25, 'ne desertons pas nos reunions, comme quelques-uns en ont pris l\'habitude, mais fortifions-nous-y, et cela d\'autant plus que vous voyez approcher le grand jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 26, 'Si, apres avoir fait connaissance avec la verite, nous pechons expres, il ne reste plus de sacrifice pour les peches ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 27, 'il ne reste que la terrible attente du jugement et un feu devorant qui doit consumer les rebelles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 28, 'Celui qui a viole la Loi de Moise meurt sans misericorde sur la deposition de deux ou trois temoins.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 29, 'Combien sera plus terrible le chatiment dont sera juge digne celui qui aura foule aux pieds le Fils de Dieu et regarde comme vil le sang de l\'Alliance,  ce sang qui l\'a sanctifie,  qui aura outrage l\'Esprit de la grace?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 30, 'Nous connaissons celui qui a dit : A moi la vengeance, moi je rendrai a mon tour, et ailleurs : Le Seigneur jugera son peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 31, 'Il est terrible de tomber entre les mains du Dieu vivant!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 32, 'Souvenez-vous des premiers temps ou, apres avoir ete eclaires, vous avez du soutenir de nombreuses et douloureuses luttes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 33, 'ici, exposes publiquement aux insultes et aux afflictions, la, sympathisant avec ceux que l\'on traitait ainsi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 34, 'Vous avez eu compassion des prisonniers, vous vous etes resignes avec joie a la confiscation de vos biens, sachant que vous possedez ailleurs une fortune qui vaut mieux et vous restera toujours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 35, 'Ne perdez donc rien de votre confiance ; une recompense immense y est attachee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 36, 'Ce qu\'il vous faut, c\'est de la patience, pour qu\'apres avoir fait la volonte de Dieu, vous remportiez ce qui vous est promis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 37, 'Encore Un bien, un bien petit espace de temps. Celui qui doit venir arrivera et ne tardera point,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 38, 'Et mon juste vivra par la foi ; Mais s\'il recule, mon ame ne trouve pas en lui de plaisirs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 10, 39, 'Quant a nous, nous ne sommes pas de ceux qui reculent pour se perdre, mais de ceux qui ont la foi pour sauver leurs ames!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 1, 'Or, la foi est la ferme conviction de choses qu\'on espere, l\'absolue certitude de faits qu\'on ne voit pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 2, 'C\'est parce que les hommes d\'autrefois ont eu cette foi la que leur eloge nous a ete fait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 3, 'C\'est par la foi que nous reconnaissons qu\'une parole de Dieu a forme le monde et, de la sorte, ce que nous voyons n\'est pas provenu de choses existantes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 4, 'C\'est par la foi qu\'Abel offrit a Dieu un meilleur sacrifice que Cain, sacrifice qui le fit declarer juste, Dieu approuvant ses offrandes et, par elle, lui qui est mort parle encore.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 5, 'C\'est a cause de sa foi qu\'Enoch fut enleve de maniere a ne pas voir la mort et on ne le trouva plus parce que Dieu l\'avait enleve. En effet, avant son enlevement, on fait de lui cet eloge : il etait agreable a Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 6, 'Or, sans la foi, il est impossible de lui etre agreable, car celui qui s\'approche de Dieu doit croire que Dieu existe et qu\'il est un remunerateur pour ceux qui le cherchent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 7, 'C\'est par la foi que Noe, averti de choses qu\'on ne voyait pas encore, construisit avec une pieuse prevoyance une arche pour sauver sa famille ; par elle, il condamna le monde et devint heritier de la justice que produit la foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 8, 'C\'est par la foi qu\'Abraham, appele a partir pour le pays qu\'il devait recevoir en heritage, obeit, et il partit sans savoir ou il allait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 9, 'C\'est par la foi qu\'il vint s\'etablir dans la terre promise, comme, en pays etranger, vivant sous la tente, avec Isaac et Jacob, heritiers, comme lui, de la meme promesse ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 10, 'car il attendait la cite aux solides fondements dont Dieu est l\'architecte et le constructeur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 11, 'C\'est par la foi que Sarra, elle aussi, recut, malgre son age, la force de donner le jour a un fils, parce qu\'elle avait cru a la veracite de celui qui le lui avait promis ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 12, 'et c\'est aussi pour cela que d\'un seul homme, et d\'un homme au dernier terme de la vieillesse, naquit une descendance semblable pour le nombre aux etoiles du ciel et aux grains de sable qui couvrent le bord de la mer et qui sont innombrables.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 13, 'Dans cette foi ils moururent tous, sans avoir recu les choses promises, les ayant seulement vues et saluees de loin et se reconnaissant etrangers et voyageurs sur la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 14, 'Ceux qui tiennent ce langage montrent bien qu\'ils cherchent une patrie ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 15, 'et s\'ils avaient songe a celle dont ils etaient sortis, ils avaient tout le temps d\'y retourner ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 16, 'mais non, c\'est une meilleure patrie qu\'ils desirent, je veux dire une patrie celeste ; aussi Dieu n\'a point honte d\'eux ; il se fait appeler leur Dieu, car il leur a prepare une cite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 17, 'C\'est par la foi qu\'Abraham, mis a l\'epreuve, offrit Isaac, offrit son fils unique, lui qui avait recu les promesses,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 18, 'lui auquel il avait ete dit : C\'est d\'apres Isaac que ta race sera nommee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 19, 'il pensait que Dieu a le pouvoir meme de ressusciter les morts, aussi le recouvra-t-il de telle maniere qu\'il est un symbole.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 20, 'C\'est par la foi, et en vue de l\'avenir, qu\'Isaac benit Jacob et Esau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 21, 'C\'est par la foi que Jacob benit, en mourant, chacun des fils de Joseph et qu\' il se prosterna devant le sommet de son baton.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 22, 'C\'est par la foi que Joseph, aux approches de la mort, fit mention de l\'exode des enfants d\'Israel et ordonna ce qu\'on ferait de ses restes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 23, 'C\'est par la foi que Moise, a sa naissance, fut cache trois mois par ses parents, parce qu\'ils avaient vu que leur enfant etait beau et qu\'ils ne se laissaient pas effrayer par le decret du roi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 24, 'C\'est par la foi que Moise, devenu grand, renonca au nom de fils de la fille de Pharaon,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 25, 'aimant mieux partager les souffrances du peuple de Dieu que d\'avoir du peche une jouissance momentanee,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 26, 'considerant l\'opprobre du Christ comme une plus grande richesse que tous les tresors de l\'Egypte ; et cela parce qu\'il regardait a la remuneration.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 27, 'C\'est par la foi qu\'il quitta l\'Egypte sans redouter la colere du roi, car il tint ferme comme s\'il voyait Celui qui est invisible.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 28, 'C\'est par la foi qu\'il etablit la Paque et l\'aspersion du sang, pour que l\'exterminateur ne touchat pas les premiers-nes des Israelites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 29, 'C\'est par la foi qu\'ils traverserent la mer Rouge comme on marche sur la terre seche, tandis que les Egyptiens qui essayerent de passer furent engloutis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 30, 'C\'est par la foi que les murs de Jericho tomberent apres qu\'on en eut fait le tour pendant sept jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 31, 'C\'est par la foi que Rahab, la femme de mauvaise vie, ne perit pas avec les incredules, parce qu\'elle avait accueilli les espions avec bonte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 32, 'Que dire encore? le temps me manquera si je parle de Gedeon, de Barak, de Samson, de Jephte, de David, de Samuel, des prophetes');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 33, 'qui, par la foi, ont conquis des royaumes, ont exerce la justice, ont obtenu des promesses, ont ferme la gueule des lions,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 34, 'ont eteint l\'ardeur du feu, ont echappe au tranchant de l\'epee, ont triomphe de la maladie, ont ete vaillants a la guerre, ont repousse les invasions etrangeres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 35, 'Des femmes ont recouvre leurs morts par la resurrection ; les uns ont ete livres a la torture, refusant d\'etre delivres, pour obtenir une resurrection meilleure ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 36, 'les autres ont eu a subir les moqueries, la flagellation, et meme les fers et le cachot ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 37, 'on les a lapides, on les a tortures, on les a scies, on les a tues avec des epees ; ils ont ete ca et la, n\'ayant pour se couvrir que des peaux de brebis ou de chevres, manquant de tout, opprimes, maltraites,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 38, 'eux dont le monde n\'etait pas digne, errants dans les deserts, les montagnes, les cavernes, les antres de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 39, 'Et tous ils ont ete loues pour leur foi, et cependant ils n\'ont point vu les promesses s\'accomplir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 11, 40, 'Dieu avait en vue quelque chose de meilleur pour nous, et ils ne devaient pas arriver sans nous a la perfection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 1, 'Eh bien, nous aussi, puisque nous avons autour de nous une, si grande nuee de temoins, debarrassons-nous de tout fardeau et du peche qui nous circonvient si facilement, courons avec perseverance dans la carriere ouverte devant nous,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 2, 'les yeux fixes sur \' l\'auteur et le consommateur de ta foi, sur Jesus, lequel, regardant a la joie placee devant lui, a endure la croix et, pour en avoir meprise la honte, S\'est assis a la droite du trone de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 3, 'Considerez celui qui a supporte de la part des pecheurs une telle opposition contre lui pour que vos ames ne se decouragent pas, pour que vous ne faiblissiez pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 4, 'Vous n\'avez pas encore eu a resister jusqu\'au sang dans votre lutte contre le peche,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 5, 'et vous auriez oublie l\'exhortation qui vous est adressee comme a des fils : Mon fils, ne meprise pas le chatiment du Seigneur Et ne te decourage pas quand il te reprend,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 6, 'Car le Seigneur chatie celui qu\'il aime Et il frappe de ses verges tout fils qu\'il reconnait comme sien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 7, 'Si vous avez des chatiments a endurer, c\'est que Dieu vous traite comme des fils, car quel est le fils que son pere ne chatie pas?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 8, 'Si vous etes dispenses du chatiment dont tous les fils ont leur part, vous etes illegitimes, vous n\'etes pas de vrais fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 9, 'De plus, nos peres par le sang nous ont chaties, et nous les avons respectes ; ne devrons-nous pas a bien plus forte raison nous, soumettre a notre Pere spirituel pour avoir la vie?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 10, 'Ceux-la nous chatiaient pour quelques jours et comme bon leur semblait ; lui le fait pour notre bien, pour que nous ayons notre part de sa saintete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 11, 'Tout chatiment, il est vrai, ne parait pas etre au premier moment un sujet de joie, mais de tristesse ; mais plus tard il fait recueillir en paix par ceux qui en ont subi l\'epreuve, le fruit qui s\'appelle la justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 12, 'Ainsi donc, Relevez ces mains qui tombent Et ces genoux qui flechissent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 13, 'et : Mettez vos pieds dans les chemins droits pour que ce qui boite ne soit pas renverse, mais plutot gueri.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 14, 'Cherchez a etre en paix avec tout le monde ; cherchez la sanctification, sans laquelle personne ne verra le Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 15, 'Veillez a ce que personne ne se prive de la grace de Dieu, a ce qu\'ail ne pousse aucune. plante veneneuse qui pourrait faire du mal, qui pourrait empoisonner plusieurs d\'entre vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 16, 'Que personne ne soit impur ou profane comme Esau, qui vendit, pour un simple repas, ses droits de premier-ne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 17, 'Et vous savez que lorsqu\'il voulut plus tard recevoir la benediction de son pere, il fut repousse ; il ne put faire accueillir sa repentance, quoiqu\'il l\'eut demandee avec larmes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 18, 'Vous ne vous etes pas approches d\'une montagne qu\'on put toucher, que le feu embrasa, ni de l\'obscurite, des tenebres, de la tempete,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 19, 'du bruit de la trompette, de la voix des commandements, que les auditeurs demanderent instamment a ne pas entendre plus longtemps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 20, 'Car ils ne pouvaient supporter cette injonction : Meme l\'animal qui touchera cette montagne sera lapide.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 21, 'Et ce spectacle etait si effrayant que Moise dit : Je suis epouvante et tremblants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 22, 'Mais vous vous etes approches de la montagne de Sion et de la ville du Dieu vivant, de la Jerusalem celeste, du choeur des myriades d\'anges,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 23, 'de l\'Eglise de vos aines inscrits aux cieux, de Dieu, juge de tous, des esprits des justes arrives au but,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 24, 'de Jesus, mediateur d\'une Alliance nouvelle, et du sang de propitiation qui parle mieux que celui d\'Abel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 25, 'Prenez garde de refuser d\'entendre Celui qui vous parle. Car s\'ils n\'ont pas echappe, ceux qui refusaient d\'entendre Celui qui se revelait sur la terre, a bien plus forte raison n\'echapperons-nous pas si nous repoussons Celui qui se revele du haut des cieux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 26, 'dont la voix faisait autrefois trembler la terre et qui fait aujourd\'hui cette declaration : Je ferai trembler une fois encore non seulement la terre, mais aussi le ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 27, 'Ces mots : une fois encore indiquent la transformation du monde visible qui sera ebranle, tandis que le monde inebranlable est eternel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 28, 'Ainsi, puisque c\'est un Royaume inebranlable que nous avons recu, soyons pleins d\'une reconnaissance telle que nous rendions a Dieu avec piete et avec respect un culte qui lui soit agreable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 12, 29, 'Car notre Dieu est un feu devorant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 1, 'Continuez a vous aimer comme des freres ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 2, 'n\'oubliez pas l\'hospitalite ; car, en la pratiquant, quelques-uns, sans le savoir, ont recu chez eux des anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 3, 'Souvenez-vous des prisonniers comme si vous etiez des leurs, et des malheureux, en songeant que vous aussi vous vivez dans ce corps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 4, 'Respectez tous le mariage ; ne deshonorez pas le lit nuptial ; les debauches et les adulteres, Dieu les jugera!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 5, 'Ne soyez pas de ceux qui tiennent a l\'argent ; contentez-vous de ce que vous avez ; car lui-meme a dit : Je ne te laisserai pas, certainement je ne t\'abandonnerai pas ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 6, 'aussi pouvons-nous dire avec confiance : Le Seigneur me vient en aide ; je ne m\'effrayerai de rien ; Que pourra me faire un homme ?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 7, 'Souvenez-vous de vos directeurs, qui vous ont preche la parole de Dieu ; representez-vous la fin de leur carriere et imitez leur foi!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 8, 'Jesus-Christ est le meme hier, aujourd\'hui et eternellement !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 9, 'Ne vous laissez pas entrainer par toutes sortes de doctrines etrangeres ; car il est bon que la grace donne de la fermete au coeur, et que ce ne soient point des aliments qui n\'ont point profite a ceux qui en usent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 10, 'Nous avons un autel dont ceux qui celebrent le culte dans le tabernacle n\'ont pas le droit de manger la victime.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 11, 'Les corps des animaux, immoles pour le peche, dont le sang est porte dans le sanctuaire par le grand-pretre sont brules hors du camp.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 12, 'Voila pourquoi Jesus aussi, pour sanctifier le peuple par son propre sang, a souffert hors de la porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 13, 'Sortons alors du camp et allons a lui en portant son opprobre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 14, 'Car nous n\'avons pas ici de cite permanente, mais nous cherchons celle qui est a venir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 15, 'Offrons donc sans cesse a Dieu par lui un sacrifice de louanges, je veux dire le fruit de levres confessant son nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 16, 'De plus, n\'oubliez ni la bienfaisance, ni la liberalite, voila les sacrifices auxquels Dieu prend plaisir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 17, 'Obeissez a vos directeurs et acceptez leurs avis ; car ils veillent sur vos ames comme devant en rendre compte ; et il faut qu\'ils le fassent avec joie et sans avoir a se plaindre, car ce serait a votre prejudice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 18, 'Priez pour nous, car nous sommes certains d\'avoir une bonne conscience, etant decides a bien nous conduire en toutes choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 19, 'Je vous demande surtout de le faire pour que je vous sois plus tot rendu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 20, 'Que le Dieu de paix qui a retire des morts notre Seigneur Jesus, devenu par le sang d\'une Alliance eternelle le souverain berger des brebis,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 21, 'vous rende capables de toutes sortes de bonnes oeuvres afin que vous fassiez sa volonte, et qu\'il accomplisse en nous ce qui est agreable a ses yeux par Jesus-Christ, auquel soit la gloire aux siecles des siecles! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 22, 'Veuillez, je vous en prie, freres, faire bon accueil a ces quelques paroles d\'exhortation, car je ne vous ecris qu\'une lettre assez breves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 23, 'Vous savez que notre frere Timothee a ete relache ; s\'il arrive assez tot, je vous verrai avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 24, 'Saluez tous vos directeurs et tous les fideles. Ceux qui sont venus d\'Italie vous saluent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (58, 13, 25, 'La grace soit avec vous tous. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 1, 'Jacques, serviteur de Dieu et du Seigneur Jesus-Christ aux douze tribus de la dispersion, salut.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 2, 'Regardez comme un grand bonheur, mes freres, d\'etre en but a diverses epreuves ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 3, 'car, en mettant votre foi au creuset, elles produisent, vous le savez, la patience ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 4, 'patience qui doit etre parfaitement mise en pratique, pour que vous soyez parfaits, accomplis, ne laissant absolument rien a desirer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 5, 'Si l\'un de vous manque de sagesse, qu\'il s\'adresse a Dieu, qui donne a tous, volontiers et sans reproches, et la sagesse lui sera donnee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 6, 'Mais qu\'il demande avec confiance, sans douter ; celui qui doute ressemble aux vagues de la mer quand le vent les souleve et les agite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 7, 'Qu\'une telle personne ne s\'attende pas a obtenir quoi que ce soit du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 8, 'L\'homme irresolu est inconstant dans toutes ses entreprises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 9, 'Que le frere humble songe avec orgueil a sa noblesse');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 10, 'et le riche a sa bassesse, car il passera comme la fleur de l\'herbe ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 11, 'le soleil se leve avec sa chaleur ardente et Il seche l\'herbe, et sa fleur tombe , et la beaute de son aspect disparait ; ainsi se fletrira le riche au milieu de sa carriere!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 12, 'Heureux l\'homme qui supporte patiemment l\'epreuve ; car lorsqu\'il aura fait ses preuves, il recevra la couronne de vie que Dieu a promise a ceux qui l\'aiment.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 13, 'Que personne ne dise dans une tentation : C\'est Dieu qui me tente, car Dieu, qui ne peut etre tente par le mal, ne tente lui-meme personne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 14, 'Lorsqu\'on est tente, c\'est qu\'on est entraine et seduit par sa propre convoitise ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 15, 'ensuite, la convoitise, qui a concu, donne naissance au peche ; et, enfin, le peche, parvenu a sa plus haute puissance, enfante la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 16, 'Ne vous y trompez pas, mes freres bien-aimes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 17, 'tout don excellent et tout present parfaits viennent d\'en haut, descendent d\'aupres du Pere des lumieres, chez lequel il n\'y a aucun changement, pas l\'ombre d\'une variation.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 18, 'Il nous a enfantes, parce qu\'il l\'a bien voulu, au moyen de la parole de verite, pour que nous fussions, en quelque sorte, les premices de ses creatures.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 19, 'Sachez-le bien, mes freres bien-aimes! Que tout homme soit prompt a ecouter, lent a parler, lent a se mettre en colere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 20, 'car la colere de l\'homme n\'accomplit pas la justice de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 21, 'Enlevez donc toute souillure et tout reste de mechancete, et recevez avec douceur la parole plantee en vous et qui peut sauver vos ames.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 22, 'Seulement mettez cette parole en pratique, et ne vous bornez pas a l\'ecouter ; vous tomberiez dans une grave erreur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 23, 'Effectivement, celui qui se borne a ecouter la parole et ne la met pas en pratique ressemble a cet homme qui, apres avoir vu dans un miroir les traits de son visage');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 24, 'et apres s\'etre regarde,, s\'en est alle et a immediatement oublie comment il est.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 25, 'Celui, au contraire, qui considere attentivement la Loi parfaite, la Loi de la liberte, qui prolonge sa contemplation, qui en arrive ainsi non a ecouter pour oublier, mais a observer et a mettre en pratique, celui-la trouvera le bonheur dans son activite meme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 26, 'Si quelqu\'un croit servir Dieu, qui ne tient pas sa langue en bride et qui se trompe lui-meme, son service de Dieu est illusoire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 1, 27, 'Une maniere de servir notre Dieu et Pere, pure et sans tache a ses yeux, est de veiller sur les orphelins et les veuves dans leur detresse et de se garder soi-meme immacule du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 1, 'Mes freres, point d\'acception de personnes en la foi de notre Seigneur Jesus, le Christ de gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 2, 'Si, par exemple, il entre dans votre synagogue un homme ayant un anneau d\'or au doigt et revetu d\'habits brillants, et qu\'il y entre aussi un pauvre aux vetements sordides,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 3, 'et que vous regardiez celui qui porte de brillants habits et lui disiez  Toi, assieds-toi ici a cette place d\'honneur ;  et que vous disiez au pauvre :  Toi, reste la debout ;  ou bien : Assieds-toi plus bas que mon marchepied ; ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 4, 'n\'est-ce pas la faire des distinctions entre freres et vous etablir juges dans de mauvais sentiments?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 5, 'Ecoutez, mes freres bien-aimes : Dieu n\'a-t-il pas choisi ceux que le monde appelle : les pauvres, pour les faire riches en foi et heritiers du Royaume qu\'il a promis a ceux qu\'il aime?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 6, 'Et puis, vous faites affront au pauvre! Ne sont-ce pas les riches qui, vous tyrannisent et qui vous trainent devant les tribunaux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 7, 'Ne sont-ce pas eux qui blasphement le beau nom qu\'on prononce en vous nommant?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 8, 'Sans doute, si vous accomplissez la Loi royale que nous donne l\'Ecriture: Tu aimeras ton prochain comme toi-meme, vous faites bien,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 9, 'mais si vous faites acception de personnes, vous commettez un peche, et la Loi vous condamne et vous declare transgresseurs!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 10, 'En effet, quelqu\'un qui a observe la Loi tout entiere (sauf un commandement pour lequel il a failli) est coupable comme s\'il les avait tous violes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 11, 'Car celui qui a dit : Tu ne commettras pas d\'adultere a dit aussi : Tu ne commettras pas de meurtre et alors si tu ne commets pas d\'adultere, mais que tu commettes un meurtre, tu deviens transgresseur de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 12, 'Parlez et agissez comme devant etre juges par une Loi de liberte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 13, 'Le jugement est sans misericorde pour celui qui n\'a pas fait misericorde. La misericorde n\'a rien a craindre du jugement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 14, 'Mes freres, a quoi sert-il a quelqu\'un de dire: j\'ai la foi, s\'il n\'a pas les oeuvres. La foi peut-elle le sauver?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 15, 'Je suppose qu\'un frere ou une soeur se trouve dans le denuement et qu\'il leur manque le pain quotidien,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 16, 'et voici l\'un de vous qui leur dit:  Allez en paix, chauffez-vous, rassasiez-vous  et cela sans leur donner ce qu\'il leur faut pour vivre, a quoi cela sert-il?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 17, 'Eh bien, il en est de meme de la foi, si elle n\'a pas d\'oeuvres, elle est morte en soi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 18, 'Cependant on pourrait dire :  Toi, tu as la foi ;  moi, j\'ai aussi des oeuvres ; montre-moi ta foi sans les oeuvres ; quant a moi, c\'est par mes oeuvres que je montrerai ma foi !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 19, 'Tu as la foi en un Dieu unique? tu as raison ; eh bien, les demons ont aussi cette foi-la, et cependant ils tremblent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 20, 'Veux-tu comprendre, o homme creux, que la foi sans les oeuvres est sterile?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 21, 'N\'est-ce pas pour des oeuvres qu\'Abraham notre Pere fut declare juste, lorsqu\'il offrit sur l\'autel son fils Isaac?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 22, 'Tu vois que la foi et les oeuvres agissaient chez lui de concert, et que les oeuvres ont porte la foi a sa perfection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 23, 'C\'est ainsi que s\'est accomplie l\'Ecriture qui dit : Abraham eut foi en Dieu et cela lui fut compte pour justice, et il recut le nom d\'ami de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 24, 'Vous voyez bien qu\'un homme est considere comme juste pour des oeuvres et non pas seulement pour sa foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 25, 'De meme encore est-ce que Rahab, la femme de mauvaise vie, n\'a pas ete consideree comme juste pour des oeuvres, quand elle a donne asile aux messagers et les a fait partir par un autre chemin?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 2, 26, 'De meme que le corps sans esprit est mort, de meme la foi sans oeuvres est morte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 1, 'Ne soyez pas nombreux a vous eriger en docteurs, mes freres, vous savez que nous serons juges d\'autant plus severement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 2, 'Nous bronchons tous et de bien des manieres, et si quelqu\'un ne bronche pas dans ses paroles, c\'est un homme parfait, capable de tenir en bride son corps tout entier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 3, 'Quand nous mettons un mors dans la bouche des chevaux pour nous en faire obeir, nous conduisons en meme temps leur, corps tout entier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 4, 'Voyez aussi les navires : qu\'ils sont grands! combien sont violents les vents qui les agitent! et c\'est avec un tout petit gouvernail que le pilote les mene a volonte dans toutes les directions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 5, 'Il en est de meme de la langue, petit membre qui a de grandes pretentions! Un bien petit feu peut embraser une bien grande foret!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 6, 'La langue aussi est du feu ; elle est le monde de l\'iniquite, la langue, installee parmi nos autres membres, souillant le corps tout entier, enflammant le cours de la vie et enflammee elle-meme par le feu de la gehenne !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 7, 'Toute espece d\'animaux sauvages, d\'oiseaux, de reptiles, de poissons peuvent etre domptes ou ont ete domptes par l\'espece humaine,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 8, 'mais la langue, il n\'est pas d\'homme qui puisse la dompter ; mal impossible a arreter, elle est pleine d\'un venin mortel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 9, 'Avec elle nous benissons le Seigneur notre Pere, et avec elle nous maudissons les hommes crees a l\'image de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 10, 'De la meme bouche sortent benediction et malediction! Il ne doit pas en etre ainsi, mes freres!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 11, 'La source donne-t-elle par la meme ouverture de l\'eau douce et de l\'eau saumatre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 12, 'Un figuier, mes freres, peut-il donner des olives? Une vigne, des figues? Une source d\'eau salee ne peut pas davantage donner de l\'eau douce.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 13, 'L\'un de vous est-il sage et intelligent? Qu\'il le prouve par des faits, par une bonne conduite, par la douceur de sa sagesse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 14, 'Mais si vous avez la passion des disputes et de l\'amertume dans le coeur, n\'allez pas vous glorifier de votre sagesse, vous agiriez contre toute verite, vous mentiriez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 15, 'Cette sagesse-la ne vient pas d\'en haut ; au contraire, elle est terrestre, sensuelle, diabolique ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 16, 'car la ou regnent la passion des disputes, la regnent aussi le desordre et toutes sortes de mauvaises choses.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 17, 'La sagesse qui vient d\'en haut est d\'abord pure, ensuite pacifique, moderee, conciliante, pleine de compassion et de bons procedes, sans duplicite, sans hypocrisie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 3, 18, 'Ce fruit, la justice, est seme au moyen de la paix par ceux qui repandent la paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 1, 'D\'ou viennent vos guerres? d\'ou viennent vos querelles? n\'est-ce pas de vos passions qui combattent dans vos membres?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 2, 'Vous convoitez et vous ne possedez pas ; vous etes meurtriers, vous etes jaloux, et vous ne pouvez pas obtenir ; vous vous querellez et vous vous disputez ; vous ne possedez pas, parce que vous ne savez pas demander :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 3, 'vous demandez et vous ne recevez pas, parce que vous demandez mal et pour fournir un aliment a vos passions.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 4, 'Idolatres! ne savez-vous pas que l\'amour du monde, c\'est l\'inimitie contre Dieu? Celui donc qui veut etre l\'ami du monde est constitue l\'ennemi de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 5, 'Prenez-vous pour des paroles en l\'air les paroles de l\'Ecriture? le Dieu jaloux reclame l\'Esprit qu\'il nous a donne ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 6, 'de plus il nous accorde une grace superieure a celle du monde. Voila pourquoi il est ecrit : Dieu resiste aux orgueilleux Et il fait grace aux humbles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 7, 'Soumettez-vous donc a Dieu ; resistez au diable, et il fuira loin de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 8, 'Approchez-vous de Dieu et il s\'approchera de vous. Purifiez vos mains, vous qui etes pecheurs! Sanctifiez vos coeurs, vous qui etes irresolus!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 9, 'Souffrez de votre misere, soyez tristes, versez des larmes, que votre rire se change en pleurs et votre gaite en chagrin ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 10, 'humiliez-vous devant le Seigneur et il vous elevera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 11, 'Ne medisez pas les uns des autres, freres. Celui qui medit d\'un frere ou qui juge son frere, medit de la Loi et juge la Loi. Or, si tu juges la Loi, tu n\'observes plus la Loi, tu te mets au-dessus d\'elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 12, 'Un seul est legislateur et juge : Celui qui a le pouvoir de sauver et de perdre ; mais qui es-tu, toi, pour juger ton prochain?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 13, 'Et maintenant a vous autres, qui dites :  Aujourd\'hui ou demain nous irons dans telle ville, nous y passerons un an, nous ferons des affaires et nous gagnerons de l\'argent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 14, 'sans savoir ce que sera demain. (Qu\'est-ce, en effet, que votre vie ? Vous n\'etes qu\'une vapeur visible un moment, puis disparaissant.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 15, 'Que vous feriez mieux de dire : Si le Seigneur le veut, nous serons en vie et nous ferons ceci ou cela.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 16, 'Vous vous enorgueillissez dans votre vaine ostentation ; cette sorte d\'orgueil est mauvaise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 4, 17, 'Celui qui sait faire le bien et ne le fait pas, commet un peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 1, 'Et maintenant a vous, les riches: Pleurez! Hurlez sur les malheurs qui vous attendent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 2, 'Vos richesses sont pourries! vos etoffes sont mangees aux vers!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 3, 'Votre argent et votre or sont tout rouilles! Cette rouille sera une preuve contre vous et, comme un feu, elle mangera vos chairs! C\'est pendant les derniers jours que vous avez amasse vos tresors !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 4, 'Eh bien, le salaire des ouvriers qui ont moissonne vos campagnes et dont vous les avez frustres, crie, et les voix des moissonneurs sont venues jusqu\'aux oreilles du Seigneur des armees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 5, 'Vous avez vecu dans les delices sur la terre, vous avez joui, vous vous etes repus et cela le jour meme ou vous allez etre egorges!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 6, 'Vous avez condamne, vous avez assassine le juste qui ne vous resistait pas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 7, 'Patientez donc, freres, jusqu\'a l\'apparition du Seigneur. Voyez l\'agriculteur, il attend le precieux fruit de la terre, il patiente jusqu\'a ce qu\'il ait recu les pluies d\'automne et celles du printemps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 8, 'Patientez, vous aussi ; fortifiez vos coeurs, car l\'apparition du Seigneur est prochaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 9, 'Ne vous plaignez pas les uns des autres, freres, pour ne pas etre juges. Voici le juge ; il se tient a la porte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 10, 'Prenez pour modeles, freres, dans la souffrance et dans la patience les prophetes qui ont parle au nom du Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 11, 'Nous appelons heureux, voyez-vous, ceux qui ont tenu bon. Vous avez entendu parler de la constance de Job, et vous avez vu ce que le Seigneur lui a donne a la fin ; car il est plein de compassion et de misericorde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 12, 'Surtout, mes freres, ne pretez pas de serment, ni par le ciel, ni par la terre, ni sous une forme quelconque. Dites seulement : oui, oui ; ou : non, non ; pour n\'encourir aucun jugement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 13, 'Un de vous est-il dans la peine? qu\'il prie. Un de vous est-il dans la joie? qu\'il chante des psaumes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 14, 'Un de vous est-il malade? qu\'il fasse appeler les Anciens de l\'Eglise ; que ceux-ci prient sur lui, apres lui avoir fait une onction d\'huile au nom du Seigneur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 15, 'et la priere faite avec foi sauvera le malade, le Seigneur le retablira, et, s\'il a commis des peches, ils lui seront pardonnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 16, 'Confessez-vous reciproquement vos peches, et priez les uns pour les autres, afin que vous guerissiez ; car la priere d\'un juste est bien puissante quand elle est fervente.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 17, 'Elie etait un homme aussi faible que nous tous, et il demanda dans une priere qu\'il ne plut pas ; et il ne plut pas sur la terre pendant trois ans et demi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 18, 'Puis, quand il eut prie encore, le ciel donna de la pluie, et la terre fit germer ses fruits.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 19, 'Mes freres, si l\'un de vous s\'est laisse egarer loin de la verite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (59, 5, 20, 'et qu\'un autre le ramene, qu\'il sache que celui qui ramene un pecheur du chemin ou il se perd, sauvera une ame de la mort et couvrira un grand nombre de peches. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 1, 'Pierre, apotre de Jesus-Christ, aux expatries de la dispersion de Pont, de Galatie, de Cappadoce, d\'Asie et de Bithynie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 2, 'que Dieu notre Pere a elus, selon une determination prise d\'avance, qu\'il a sanctifies par l\'esprit, pour obeir a Jesus-Christ et etre purifies par l\'aspersion de son sang. Grace et paix vous soient de plus en plus donnees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 3, 'Beni soit le Dieu et Pere de notre Seigneur Jesus-Christ, de nous avoir regeneres dans sa grande misericorde pour que nous ayons, par la resurrection de Jesus-Christ d\'entre les morts, une vivifiante esperance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 4, 'celle d\'un heritage incorruptible, immacule, inalterable, reserve dans les cieux');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 5, 'a vous que la puissance de Dieu garde par la foi pour le salut qui est pret a paraitre et paraitra au moment supreme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 6, 'Vous en etes transportes de joie, quoique vous soyez, pendant quelque temps encore, attristes de diverses epreuves.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 7, 'S\'il le faut, c\'est afin que l\'epreuve par laquelle passe votre foi, bien plus precieuse que celle de l\'or (et cependant cette matiere perissable est eprouvee au feu), tourne a votre louange, votre gloire, votre honneur, lorsque paraitra Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 8, 'que vous aimez sans l\'avoir vu ; et, croyant en lui sans le voir encore, vous etes transportes d\'une joie ineffable et glorieuse,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 9, 'parce que vous remporterez la recompense de votre foi, le salut de vos ames.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 10, 'Ce salut a ete le sujet des recherches et des investigations des prophetes qui ont predit la grace a vous destinee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 11, 'ils cherchaient quelle epoque et quelles circonstances indiquait l\'Esprit de Christ qui rendait en eux un temoignage prophetique aux souffrances destinees au Christ et a la gloire dont elles seraient suivies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 12, 'Et il leur fut revele que ce n\'etait pas pour eux-memes, mais pour vous qu\'ils etaient dispensateurs de ces choses, que vous ont maintenant annoncees par l\'Esprit saint, envoye du ciel, ceux qui vous ont preche l\'Evangile, et au fond desquelles les anges memes desirent plonger leurs regards.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 13, 'Ainsi donc, ceignez vos reins (je parle au figure), et soyez sobres ; placez votre esperance tout entiere dans la grace qui vous sera apportee lorsque paraitra Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 14, 'En enfants obeissants, ne suivez pas les errements de vos passions premieres, quand vous etiez dans l\'ignorance ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 15, 'mais, comme l\'a dit le Saint qui vous a appeles, soyez saints, vous aussi, dans toute votre conduite,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 16, 'car il est ecrit : Vous serez saints, car moi je suis saint ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 17, 'et si vous invoquez comme Pere celui qui, sans faire acception de personne, juge chacun selon son oeuvre, marchez dans la crainte de Dieu tout le temps de votre pelerinage ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 18, 'car ce n\'est pas, vous le savez, par des choses perissables, par de l\'argent ou par de l\'or que vous avez ete rachetes de cette maniere de vivre si vaine que vous avaient leguee vos peres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 19, 'mais par un sang precieux, semblable a celui d\'un agneau sans tache et sans defaut,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 20, 'le sang de Christ, predestine avant la creation du monde, et manifeste a cause de vous a la fin des siecles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 21, 'Par lui vous avez foi en Dieu qui l\'a ressuscite d\'entre les morts et l\'a glorifie, de sorte que votre foi est aussi une esperance en Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 22, 'Apres avoir sanctifie vos ames par l\'obeissance a la verite et, en vue d\'une sincere affection fraternelle, aimez-vous ardemment les uns les autres et de tout coeur,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 23, 'vous qui etes nes de nouveau non d\'un germe corruptible, mais d\'un germe incorruptible, d\'une parole vivifiante de Dieu, d\'une parole qui ne passera pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 24, 'Car : Toute chair est comme l\'herbe, Et toute sa gloire comme la fleur de l\'herbe ; L\'herbe seche et sa fleur tombe,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 1, 25, 'Mais la parole du Seigneur demeure eternellement. Cette parole est celle dont la Bonne Nouvelle vous a ete annoncee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 1, 'Debarrassez-vous donc de toute mechancete, de toute faussete, dissimulation, jalousie et medisance ; semblables a des enfants nouveau-nes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 2, 'desirez vivement le lait pur (je parle au sens spirituel),afin de croitre par lui pour le salut,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 3, 'si : Vous avez goute que le Seigneur est doux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 4, 'Approchez-vous de lui pierre vivante, rejetee par les hommes, mais aux yeux de Dieu, choisie, precieuse,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 5, 'et alors vous, comme des pierres vivantes, edifiez-vous vous-memes en maison spirituelle, formez un saint clerge, qui offre des sacrifices spirituels, agreables a Dieu par Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 6, 'On lit en effet dans l\'Ecriture : Voici que je place en Sion une pierre, Une pierre d\'angle choisie et precieuse, Et celui qui aura confiance en elle ne sera pas decu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 7, 'Ainsi donc a vous, croyants, l\'honneur ; quant aux incredules: La pierre rejetee par les constructeurs Est devenue la principale pierre d\'angle,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 8, 'et : Une pierre d\'achoppement, Un roc qui fait tomber ; Ils s\'y heurtent pour n\'avoir pas cru a la parole et c\'est a cela qu\'ils sont destines.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 9, 'Mais vous, vous etes une race elue, un clerge royal, une nation sainte, un peuple que Dieu s\'est acquis pour que vous annonciez les merites de Celui qui vous a appeles des tenebres a son admirable lumiere ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 10, 'vous qui, autrefois, ne formiez pas un peuple, vous etes maintenant : un peuple de Dieu, vous qui : n\'aviez pas obtenu misericorde, vous avez maintenant obtenu misericorde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 11, 'Mes bien-aimes, je vous exhorte comme des etrangers et des expatries: Gardez-vous des passions sensuelles qui font la guerre a l\'ame ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 12, 'conduisez-vous bien au milieu des paiens, et alors, voyant que ces choses dont ils vous accusent a tort, vous traitant de malfaiteurs, sont, examinees de pres, de bonnes oeuvres, ils rendront gloire a Dieu au jour de sa visitation');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 13, 'Soumettez-vous, a cause du Seigneur, a toutes les autorites instituees par les hommes ; au roi, comme au souverain ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 14, 'aux gouverneurs, comme delegues par lui pour chatier les malfaiteurs et approuver les gens de bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 15, 'C\'est la volonte de Dieu que, par votre bonne conduite, vous imposiez silence aux insenses qui vous meconnaissent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 16, 'Comportez-vous en hommes libres, non en, hommes dont la liberte n\'est qu\'un voile qui cache le vice, mais en serviteurs de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 17, 'Soyez respectueux pour tout le monde, aimez les freres, craignez Dieu, respectez le roi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 18, 'Domestiques, soyez en toutes choses soumis a vos maitres avec crainte, non seulement a ceux qui sont bons et humains, mais aussi a ceux qui sont mechants.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 19, 'C\'est une grace d\'endurer des peines et de souffrir injustement par motif de conscience et pour Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 20, 'Quel est votre merite, si, apres avoir commis une faute, vous supportez patiemment les soufflets? Mais si, apres avoir fait le bien, vous supportez patiemment la souffrance, voila qui est une grace aux yeux de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 21, 'et c\'est a cela que vous avez ete appeles. Car le Christ aussi a souffert pour vous ; il vous a laisse un exemple pour que vous suiviez ses traces ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 22, 'Lui qui n\'a pas commis de peche Et dans la bouche duquel il ne s\'est trouve aucune fraude.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 23, 'Outrage, il n\'a pas rendu l\'outrage ; maltraite, il n\'a point fait de menaces ; il a remis sa cause a Celui qui juge avec justice,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 24, 'et c\'est lui qui a porte nos peches en son corps sur le bois, afin que, morts au peche, nous vivions pour la justice ; c\'est lui dont les plaies vous ont gueris.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 2, 25, 'Vous alliez ca et la comme des brebis et maintenant vous etes revenus vers le berger et le gardien de vos ames.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 1, 'Et vous, femmes, soyez, de meme, soumises, chacune a votre mari, afin que ceux d\'entre eux qui seraient rebelles a la parole soient gagnes, en dehors de la parole,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 2, 'par la conduite de leurs femmes, considerant que cette conduite est chaste et reservee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 3, 'Cherchez non la parure du dehors, qui consiste a s\'entrelacer les cheveux avec art, a porter des bijoux d\'or, a mettre de riches vetements,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 4, 'mais la beaute cachee du coeur, le charme imperissable d\'un esprit doux et tranquille ; voila la vraie richesse devant Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 5, 'Tel etait l\'ornement des saintes femmes d\'autrefois, esperant en Dieu, soumises chacune a son mari,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 6, 'Sarra, par exemple, qui obeissait a Abraham et l\'appelait son Seigneur, Sarra, dont vous etes devenus les enfants en faisant le bien sans crainte, sans aucune apprehension.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 7, 'Et vous, maris, de votre cote, traitez vos femmes comme un etre plus eclaire doit traiter un etre plus faible ; ayez pour elles des egards comme pour des coheritieres de la grace de vie, afin que vos prieres ne soient pas troublees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 8, 'Enfin, ne soyez tous qu\'un coeur et qu\'une ame, fraternels, compatissants, humbles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 9, 'ne rendant pas le mal pour le mal, l\'outrage pour l\'outrage, au contraire benissant, car, pour heriter d\'une benediction, vous avez ete appeles a benir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 10, 'Et en effet : Que celui qui veut aimer la vie Et voir des jours heureux Garde sa langue du mal Et ses levres du mensonge,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 11, 'Qu\'il se detourne du mal et fasse le bien, Qu\'il aspire a la paix, qu\'il la recherche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 12, 'Parce que le Seigneur a les yeux sur les justes Et ses oreilles sont attentives a leurs prieres, Mais la face du Seigneur regarde ceux qui font le mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 13, 'Et puis, qui peut vous faire du mal, si vous vous appliquez a faire le bien?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 14, 'Si meme il vous arrivait de souffrir pour la justice, soyez-en heureux! Seulement n\'ayez d\'eux aucune espece de crainte Et ne vous laissez pas troubler ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 15, 'mais craignez saintement dans vos coeurs Christ le Seigneur. Ayez une apologie toujours prete pour ceux qui vous demandent compte de vos esperances, mais faites-la avec douceur et modestie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 16, 'forts de votre bonne conscience, afin que ceux qui diffament votre bonne conduite en Christ aient a rougir de leurs calomnies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 17, 'car il vaut mieux souffrir en faisant le bien (si telle est la volonte de Dieu) qu\'en faisant le mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 18, 'Christ aussi a souffert pour les peches une fois pour toutes  un juste pour des injustes!  afin de nous amener a Dieu, ayant ete mis a mort quant a la chair, etant revenu a la vie quant a l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 19, 'Etant Esprit, il est alle precher aux esprits en prison,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 20, 'qui ont ete rebelles autrefois, du temps ou Noe construisait l\'arche lorsque Dieu prolongeait sa patience. Dans cette arche, peu de personnes, huit seulement, furent sauvees et ce fut a travers l\'eau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 21, 'or cette eau vous sauve, vous aussi, sous la forme du bapteme, qui en est l\'image correspondante (je n\'entends pas par bapteme une simple ablution debarrassant le corps de ses souillures, mais l\'alliance qu\'une bonne conscience fait avec Dieu) ; ce bapteme-la vous sauve par la resurrection de Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 3, 22, 'qui est a la droite de Dieu, depuis qu\'il est monte au ciel et qu\'il s\'est soumis anges, puissances et dominations.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 1, 'Ainsi donc, Christ a souffert dans la chair ; faites-vous une, arme de cette pensee-la ; car celui qui a souffert dans sa chair en a fini avec le peche');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 2, 'et ne doit plus, pendant le temps qui lui reste ici-bas, vivre selon les passions des hommes, mais selon la volonte de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 3, 'Assez longtemps vous avez fait la volonte des paiens, en vivant dans la debauche, les mauvais desirs, l\'ivrognerie, les exces de, table, les orgies et le culte impie des idoles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 4, 'Ils trouvent etrange que vous ne vous plongiez plus avec eux dans cette fange et dans ces infamies, et ils vous outragent :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 5, 'ils rendront compte a Celui qui est pret a juger les vivants et les morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 6, 'Et, en effet, en vue de ce jugement, l\'Evangile a ete annonce aussi aux morts, afin qu\'apres avoir ete juges et condamnes a la mort du corps comme pecheurs, ils puissent trouver la vie divine, la vie de l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 7, 'La fin de toutes choses approche. Soyez donc sages et sobres pour pouvoir prier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 8, 'Avant tout, ayez les uns pour les autres un ardent amour, car L\'amour couvre une multitude de peches. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 9, 'Exercez entre vous l\'hospitalite sans vous plaindre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 10, 'Que chacun mette au service des autres le don qu\'il a recu, comme le doivent de bons administrateurs des diverses graces de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 11, 'Que celui qui enseigne, enseigne comme etant l\'oracle de Dieu ; que celui qui exerce les fonctions de diacre, les exerce comme employant une force dispensee par Dieu, afin que, en toutes choses, Dieu soit glorifie par Jesus-Christ, auquel appartiennent la gloire et la puissance aux siecles des siecles! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 12, 'Mes bien-aimes, ne soyez pas surpris de l\'incendie qui s\'allume au milieu de vous pour vous eprouver, comme s\'il vous arrivait quelque chose d\'etrange ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 13, 'mais rejouissez-vous d\'avoir part aux souffrances du Christ, afin de vous rejouir encore, et avec chants de triomphe, au jour de la revelation de sa gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 14, 'Si l\'on vous outrage au nom de Christ, vous etes heureux ; car l\'Esprit de gloire, l\'Esprit de Dieu repose sur vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 15, 'Qu\'aucun de vous ne soit puni comme meurtrier, comme voleur, comme malfaiteur, comme surveillant indiscret de ceux du dehors,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 16, 'mais si quelqu\'un souffre comme chretien, qu\'il n\'en rougisse pas ; qu\'il glorifie Dieu au contraire de porter ce nom-la ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 17, 'car le moment est venu ou le jugement va commencer par la maison de Dieu. S\'il commence par nous, quelle sera la fin de ceux qui n\'obeissent pas a l\'Evangile de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 18, 'et si : Le juste ne sera sauve qu\'a grand\'peine, L\'impie, le pecheur que deviendra-t-il?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 4, 19, 'Que ceux donc qui souffrent selon la volonte de Dieu recommandent leurs ames au fidele Createur, en faisant ce qui est bien.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 1, 'J\'adresse maintenant mes exhortations aux Anciens parmi vous, moi, un Ancien comme eux et un temoin des souffrances du Christ, moi, qui aurai ma part aussi de la gloire qui doit etre revelee :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 2, 'Paissez le troupeau de Dieu qui est sous votre garde ; faites-le non par contrainte, mais volontairement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 3, 'non par vil interet, mais par devouement, non en dominateurs de ceux qui vous sont echus en partage, mais en devenant les modeles du troupeau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 4, 'et quand le souverain pasteur paraitra, vous remporterez la couronne de la gloire qui ne se fletrit jamais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 5, 'Quant a vous, jeunes gens, soyez soumis aux Anciens, et, d\'ailleurs, montrez tous de l\'humilite dans vos rapports mutuels, car : Dieu resiste aux orgueilleux Et il fait grace aux humbles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 6, 'Humilliez-vous donc sous la main puissante de Dieu pour qu\'il vous eleve, quand le temps sera venu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 7, 'Dechargez-vous sur lui de tous vos soucis, car c\'est lui qui prend soin de vous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 8, 'Soyez sobres ; soyez vigilants ; votre adversaire, le Diable, comme un lion rugissant, rode, cherchant quelqu\'un a devorer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 9, 'Resistez-lui, fermes en la foi, sachant que vos freres repandus dans le monde, eprouvent les memes souffrances que vous eprouvez.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 10, 'Le Dieu de toute grace qui vous a appeles en Christ a sa gloire eternelle, apres un peu de souffrances, vous perfectionnera lui-meme, vous affermira, vous fortifiera.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 11, 'A lui soit la force aux siecles des siecles! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 12, 'C\'est par Silvanus, frere, a mon avis, fidele que je vous ecris ces quelques lignes, pour vous exhorter et vous attester que c\'est a la vraie grace de Dieu que vous vous etes attaches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 13, 'L\'elue qui est a Babylone vous salue ainsi que Marc, mon fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (60, 5, 14, 'Saluez-vous les uns les autres du baiser d\'amour. Paix a vous tous qui etes en Christ. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 1, 'Symeon Pierre, serviteur et apotre de Jesus-Christ, a ceux qui ont herite par la justice de notre Dieu et du sauveur Jesus-Christ, d\'une foi aussi precieuse que la notre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 2, 'Grace et paix vous soient de plus en plus donnees par la connaissance de Dieu et de Jesus notre Seigneur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 3, 'Dans sa divine puissance, Jesus-Christ nous a dotes de tout ce qui mene a la vie et a la piete, en nous faisant connaitre Celui qui nous a appeles, par sa gloire et par sa vertu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 4, 'et nous a, par elles, dotes des plus grandes et des plus precieuses promesses, pour vous rendre participants par elles de la nature divine, apres vous avoir arraches au monde, a ses passions et a sa corruption.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 5, ' Faites donc de votre cote tous vos efforts pour produire avec votre foi la vertu ; avec la vertu, la science ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 6, 'avec la science, la temperance ; avec la temperance, la patience ; avec la patience, la piete ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 7, 'avec la piete, la fraternite ; avec la fraternite, l\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 8, 'Posseder et developper en vous ces qualites vous empechera d\'etre oisifs, vous fera faire des progres dans la connaissance de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 9, 'Celui auquel elles manquent est un homme a courte vue, un aveugle, il a oublie qu\'il a ete purifie de ses anciens peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 10, 'C\'est pourquoi, mes freres, faites d\'autant plus d\'efforts pour rendre sures votre vocation et votre election. En agissant ainsi, vous ne broncherez jamais,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 11, 'et, de la sorte, l\'entree dans leternel Royaume de notre Seigneur et Sauveur Jesus-Christ vous sera largement assuree.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 12, 'Voila pourquoi j\'aurai toujours soin de vous remettre ces choses en memoire. Vous les connaissez deja, je le sais, et vous etes pleins de fermete dans votre foi en la verite d\'aujourd\'hui ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 13, 'mais je considererai comme un devoir, aussi longtemps que je vivrai, de vous tenir en eveil par mes avertissements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 14, 'Je sais que je serai tout a coup appele a plier ma tente ; c\'est notre Seigneur Jesus-Christ qui me l\'a declare.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 15, 'Mais je ferai mon possible pour qu\'apres ma mort vous conserviez toujours le souvenir de ce que je vous ai dit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 16, 'Ce ne sont pas des fables adroitement fabriquees que nous vous avons racontees en vous faisant connaitre la puissance de notre Seigneur Jesus-Christ et en vous parlant de son avenement ; non, nous avons ete temoins oculaires de sa majeste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 17, 'En effet, il a recu de Dieu son Pere tout honneur et toute gloire lorsqu\'une voix, echo de la gloire supreme, lui parla ainsi : Voici mon Fils bien-aime, c\'est en lui que je me complais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 18, 'Et cette voix, nous l\'avons entendue comme elle venait du ciel quand nous etions avec lui sur la montagne sainte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 19, 'Et nous tenons pour d\'autant plus certaines les paroles des prophetes ; vous ferez bien d\'y faire attention comme a un flambeau brillant dans un endroit obscur, en attendant que le jour luise et que l\'etoile du matin se leve dans vos coeurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 20, 'Avant tout, sachez ceci : aucune des propheties de l\'Ecriture n\'est affaire d\'interpretation privee ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 1, 21, 'car jamais prophetie n\'a ete inspiree par le caprice d\'un homme, mais c\'est pousses par l\'Esprit saint que des hommes ont parle de la part de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 1, 'Il y a eu cependant aussi de faux prophetes parmi le peuple, et, parmi vous, il y aura de meme de faux docteurs qui introduiront des heresies de perdition, qui renieront le maitre qui les a rachetes, qui s\'attireront une ruine subite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 2, 'Bien des gens les suivront dans leurs orgies et, a cause d\'eux, la voix de la verite sera calomniee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 3, 'Dans leur cupide avidite, ils viendront vous exploiter avec de fallacieux discours, mais il y a bien longtemps que leur sentence ne paresse pas ; et leur perdition ne s\'endort pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 4, 'Car si Dieu n\'a pas epargne les anges qui avaient peche, s\'il les a emprisonnes dans les tenebres, precipites dans l\'abime, gardes pour le jugement,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 5, 's\'il n\'a pas epargne l\'ancien monde, s\'il n\'a preserve que sept personnes avec Noe, ce predicateur de la justice, s\'il a envoye le deluge sur un monde impie,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 6, 's\'il a reduit en cendres les villes de Sodome et de Gomorrhe, s\'il les a aneanties, s\'il les a condamnees, s\'il en a fait un exemple pour les impies de l\'avenir,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 7, 's\'il a sauve le juste Lot, excede de l\'infame conduite de ces abominables');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 8, '(car ce juste demeurait au milieu d\'eux et ce qu\'il voyait et entendait journellement, leurs ignobles actions mettaient son ame droite a la torture),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 9, 'c\'est que le Seigneur sait delivrer de l\'epreuve les hommes pieux et reserver les iniques pour le jour du jugement, tout en les chatiant deja,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 10, 'surtout ceux que leurs passions impures menent aux jouissances charnelles, et qui meprisent l\'autorite. Les audacieux! les insolents! ils insultent les gloires, et n\'en tremblent pas!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 11, 'quand des anges, leurs superieurs en force et en puissance, ne portent pas contre elles, en presence du Seigneur, de jugement insultant!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 12, 'Mais eux, comme ces betes brutes qui naissent a une vie purement physique, pour etre prises et detruites, ils insultent ce qu\'ils ignorent! ce sera, perdus comme elles, qu\'ils periront,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 13, 'et ainsi ils recevront le salaire de leur iniquite, puisqu\'ils trouvent leur plaisir dans des debauches d\'un jour! Etres tares et fletris! ils se delectent de leurs fourberies, quand ils font bonne chere avec vous!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 14, 'Ils ont les yeux bouffis de passions adulteres ; ils sont insatiables de peches ; ils seduisent les ames chancelantes ; ils ont un coeur exerce a la cupidite ; ce sont des enfants de malediction ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 15, 'ils ont abandonne le droit chemin ; ils se sont egares ; ils ont suivi la voie de Balaam, fils de Bosor, qui s\'eprit d\'un inique salaire');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 16, 'et fut dument convaincu de sa sceleratesse: un animal muet se mit a parler d\'une voix humaine et arreta la demence du prophete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 17, 'Ce sont des fontaines sans eau ; des brouillards qu\'emporte un ouragan ; le gouffre des tenebres leur est reserve!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 18, 'Beaux parleurs, aussi vides que pompeux, ils seduisent par les passions de la chair, par la debauche, ceux qui venaient a peine d\'echapper aux gens infectes d\'erreur ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 19, 'ils leur promettent la liberte, quand ils sont eux-memes esclaves de la corruption! (car on est esclave de ce qui vous domine).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 20, 'Si, en effet, apres avoir echappe aux souillures du monde par la connaissance de notre Seigneur et Sauveur Jesus-Christ, ils s\'y laissent reprendre, et que ces souillures les dominent, leur derniere condition est pire que la premiere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 21, 'Il leur aurait mieux valu ne pas avoir connu la voie de la justice que de se detourner, apres l\'avoir connue, du saint commandement qui leur a ete transmis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 2, 22, 'Il leur arrive ce que dit si bien le proverbe : Le chien retourne a ce qu\'il a vomi et celui-ci: Truie lavee va se vautrer dans la fange.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 1, 'Voila deja la seconde lettre que je vous ecris, mes bien-aimes, dans celle-ci, comme dans la premiere, je tache de reveiller vos bons sentiments ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 2, 'je vous rappelle les predictions des saints prophetes, et les preceptes de notre Seigneur et Sauveur transmis par vos apotres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 3, 'Sachez surtout ceci : pendant les derniers jours viendront des moqueurs pleins d\'ironie, qui ne se laisseront conduire que par leurs propres passions,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 4, 'et qui diront : Eh bien! que devient la promesse de son apparition ? Depuis que nos peres sont morts, tout continue a se passer comme depuis le commencement du monde!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 5, 'Ils affectent d\'ignorer qu\'il y a eu autrefois des cieux et une terre sortis de l\'eau, et formes au moyen de l\'eau sur la parole de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 6, 'par suite, le monde d\'alors perit submerge dans l\'eau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 7, 'Quant aux cieux et a la terre actuels, ils sont conserves sur cette meme parole de Dieu, et reserves pour le feu qui s\'allumera le jour du jugement et de la destruction des impies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 8, 'Il est une chose que vous ne devez pas oublier, bien-aimes, c\'est qu\'un jour, devant le Seigneur, est comme mille annees et que Mille annees sont comme un jour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 9, 'Le Seigneur n\'est pas en retard pour l\'execution de sa promesse ; certaines personnes croient qu\'il y a du retard ; mais il use de patience envers vous, voulant que personne ne perisse, mais que tous arrivent a la repentance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 10, 'Le jour du Seigneur viendra comme un voleur ; alors les cieux disparaitront avec fracas ; les elements embrases se fondront, et la terre et tout ce qu\'elle renferme sera consume.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 11, 'Puisque tout doit se fondre ainsi, quelle sainte conduite, quelle, piete ne devez-vous pas montrer,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 12, 'attendant et hatant la venue du jour de Dieu (c\'est a cause de ce jour que les cieux se seront fondus dans le feu, et que les elements embrases se seront dissous).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 13, 'Quant a nous, nous attendons, selon sa promesse, des cieux nouveaux et une terre nouvelle ou la justice habitera!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 14, 'C\'est pourquoi, bien-aimes, pleins de cette attente, faites des efforts pour qu\'il vous trouve en paix, sans tache, sans reproche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 15, 'considerez la patience que montre notre Seigneur comme chose fort propre a notre salut, ainsi que notre bien-aime frere Paul, selon la sagesse qui lui a ete donnee,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 16, 'vous l\'a aussi ecrit ; il le fait d\'ailleurs dans toutes les lettres ou il parle de ces sortes de choses, lettres dans lesquelles certains passages sont difficiles a comprendre, dont les ignorants et les esprits mobiles tordent le sens, comme, du reste, les autres Ecritures, pour leur propre perdition.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 17, 'Vous voila prevenus, bien-aimes ; soyez donc sur vos gardes, de peur que, entraines, vous aussi, par les seductions de ces miserables, vous ne veniez a dechoir, a perdre votre fermete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (61, 3, 18, 'Croissez dans la grace et dans la connaissance de notre Seigneur et Sauveur Jesus-Christ. A lui soit la gloire maintenant et jusqu\'au jour de l\'eternite! ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 1, 'Ce qui existait des le commencement, ce que nous avons, entendu, ce que nous avons vu de nos yeux, ce que nous avons contemple et que nos mains ont touche, quant a la Parole de la vie ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 2, '(car la vie s\'est manifestee, et nous l\'avons vue, nous l\'attestons, et nous vous annoncons cette vie eternelle qui etait aupres du Pere et s\'est manifestee a nous),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 3, 'ce que nous avons vu et entendu, nous vous l\'annoncons pour que, vous aussi, vous soyez en communion avec nous ; et notre communion a nous est avec le Pere et avec son Fils Jesus-Christ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 4, 'et nous vous ecrivons ceci pour que notre joie soit complete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 5, 'Voici le message que nous avons appris de lui et que nous vous annoncons : Dieu est lumiere, et il n\'y a point de tenebres en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 6, 'Si nous nous disons en communion avec lui et que nous marchions dans les tenebres, nous mentons, et nous ne pratiquons pas la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 7, 'Si, au contraire, nous marchons dans la lumiere, comme il est lui-meme dans la lumiere, nous sommes en communion les uns avec les autres, et le sang de Jesus son fils nous purifie de tout peche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 8, 'Si nous nous disons sans peche, nous nous trompons nous-memes, et la verite n\'est pas en nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 9, 'Si nous confessons nos peches, il est fidele et juste pour nous pardonner ces peches et nous purifier de toute iniquite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 1, 10, 'Si nous disons que nous n\'avons pas peche, nous faisons de lui un menteur, et sa parole n\'est pas en nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 1, 'Mes enfants, je vous ecris ceci pour que vous ne pechiez point, et si quelqu\'un a peche, nous avons, aupres du Pere, un intercesseur, Jesus-Christ, le juste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 2, 'Il est, lui, une propitiation pour nos peches, et non seulement pour les notres, mais aussi pour ceux du monde entier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 3, 'Et voici comment nous savons que nous l\'avons connu : c\'est quand nous observons ses commandements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 4, 'Celui qui dit : je l\'ai connu, et qui n\'observe pas ses commandements, est un menteur, et la verite n\'est pas en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 5, 'En celui qui observe sa parole, l\'amour de Dieu est veritablement parfait. Voici comment nous reconnaissons que nous sommes en lui :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 6, 'celui qui pretend demeurer en lui, doit marcher, lui aussi, comme Jesus a marche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 7, 'Mes bien-aimes, je ne vous ecris pas un commandement nouveau, mais un commandement ancien, celui que vous aviez des le commencement. Ce commandement ancien c\'est la parole que vous avez entendue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 8, 'Cependant c\'est aussi un commandement nouveau que je vous ecris ; cela est vrai et pour lui et pour vous, parce que les tenebres se dissipent, et que la vraie lumiere brille deja.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 9, 'Celui qui se dit dans la lumiere et qui hait son frere, continue a etre dans les tenebres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 10, 'Celui qui aime son frere reste dans la lumiere, et il n\'y a en lui aucun motif de tomber.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 11, 'Mais celui qui hait son frere est dans les tenebres, marche dans les tenebres, et ne sait ou il va, parce que les tenebres l\'ont rendu aveugle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 12, 'Je vous ecris, enfants, parce que vos peches vous ont ete pardonnes a cause de son nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 13, 'Je vous ecris, peres, parce que vous avez connu celui qui est des le commencement. Je vous ecris, jeunes gens, parce que vous avez vaincu le Malin. Je vous ai ecrit, enfants, parce que vous avez connu le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 14, 'Je vous ai ecrit, peres, parce que vous avez connu celui qui est des le commencement. Je vous ai ecrit, jeunes gens, parce que vous etes forts, et que la Parole de Dieu demeure en vous, et que vous avez vaincu le Malin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 15, 'N\'aimez pas le monde, ni ce qui est dans le monde. Si quelqu\'un aime le monde, l\'amour du Pere n\'est pas en lui : car tout');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 16, 'ce qui est dans le monde, la convoitise de la chair, la convoitise des yeux et l\'ostentation dans la vie, ne vient pas du Pere, mais vient du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 17, 'Or le monde passe, et sa convoitise aussi ; mais celui qui fait la volonte de Dieu subsiste eternellement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 18, 'Mes enfants, nous sommes a la derniere heure ; vous avez entendu annoncer la venue d\'un Antechrist ; sachez qu\'il y a maintenant plusieurs Antechrists ; et ainsi nous savons que nous sommes a la derniere heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 19, 'Ils sont sortis du milieu de nous, mais ils n\'etaient pas des notres ; car s\'ils avaient ete des notres, ils seraient restes avec nous, mais c\'etait afin de montrer que tous ne sont pas des notres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 20, 'Quant a vous, vous avez une onction venant du Saint, et vous le savez tous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 21, 'Je ne vous ai pas ecrit, parce que vous ne connaissez pas la verite, mais parce que vous la connaissez et parce qu\'aucun mensonge ne vient de la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 22, 'Qui est menteur si ce n\'est celui qui nie que Jesus est le Christ? Voila l\'Antechrist, celui qui renie le Pere et le Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 23, 'Quiconque renie le Fils n\'a pas non plus le Pere ; celui qui confesse le Fils a aussi le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 24, 'Quant a vous, ce que vous avez entendu des le commencement doit demeurer en vous. Si ce que vous avez entendu des le commencement demeure en vous, vous demeurerez, vous aussi, dans le Fils et dans le Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 25, 'Et voici la promesse qu\'il nous a faite lui-meme : la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 26, 'Je vous ai ecrit cela en vue de ceux qui vous egarent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 27, 'Quant a vous, l\'onction que vous avez recue de lui reste en vous, et vous n\'avez pas besoin qu\'on vous instruise ; mais, comme son onction vous instruit de toutes choses et qu\'elle est vraie, qu\'elle n\'est pas un mensonge, vous demeurerez en lui comme elle vous l\'a enseigne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 28, 'Desormais, mes enfants, demeurez en lui, afin que, s\'il vient a paraitre, nous soyons pleins d\'assurance et que, lors de son avenement, nous n\'ayons pas a nous eloigner de lui, couverts de honte.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 2, 29, 'Si vous savez qu\'il est juste, reconnaissez aussi que celui qui pratique la justice est ne de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 1, 'Voyez quel amour le Pere nous a temoigne afin que nous fussions appeles enfants de Dieu ; et nous le sommes. Si le monde ne nous connait pas, c\'est que, lui non plus, il ne l\'a pas connu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 2, 'Mes bien-aimes, nous sommes maintenant enfants de Dieu, et ce que nous serons n\'a pas encore ete manifeste, mais nous savons ceci : quand ce que nous serons sera manifeste, nous serons semblables a lui, parce que nous le verrons tel qu\'il est.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 3, 'Quiconque fonde sur lui une telle esperance, se purifie lui-meme comme lui est pur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 4, 'Quiconque commet le peche, viole la Loi : le peche c\'est la violation de la Loi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 5, 'Vous savez qu\'il est apparu pour enlever les peches, et qu\'il n\'y a point de peche en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 6, 'Quiconque demeure en lui ne peche pas. Quiconque peche ne l\'a pas vu et ne l\'a pas connu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 7, 'Mes enfants, que personne ne vous egare! Celui qui pratique la justice est juste, comme lui est juste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 8, 'Celui qui commet le peche est enfant du Diable, car le Diable peche depuis le commencement. Voici pourquoi le Fils de Dieu est apparu : pour detruire les oeuvres du Diable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 9, 'Quiconque est ne de Dieu ne commet pas de peche, parce que la semence de Dieu demeure en lui, et il ne peut pas pecher, parce qu\'il est ne de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 10, 'Voici comment se font reconnaitre les enfants de Dieu et les enfants du Diable: quiconque ne pratique pas la justice n\'est pas ne de Dieu, et celui qui n\'aime pas son frere non plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 11, 'Car voici le message que vous avez entendu des le commencement : aimons-nous les uns les autres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 12, 'et ne soyons pas comme Cain qui etait fils du Malin et qui egorgea son frere. Pourquoi l\'egorgea-t-il? parce que ses oeuvres etaient mauvaises et celles de son frere justes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 13, 'Ne vous etonnez pas, freres, si le monde vous hait.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 14, 'Nous, nous savons que nous sommes passes de la mort a la vie, parce que nous aimons les freres. Celui qui n\'aime pas demeure dans la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 15, 'Quiconque hait son frere est un homicide, et vous savez qu\'aucun homicide n\'a la vie eternelle demeurant en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 16, 'Voici comment nous avons reconnu son amour : a ce qu\'il a donne sa vie pour nous, et, nous aussi, nous devons donner notre vie pour les freres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 17, 'Si quelqu\'un possede les biens de ce monde et que, voyant son frere dans le besoin, il lui ferme son coeur, comment l\'amour de Dieu peut-il demeurer en lui?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 18, 'Mes enfants, n\'aimons pas par nos paroles et avec notre langue, aimons par nos actes et en verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 19, 'Et voici comment nous reconnaitrons que nous sommes de la verite et comment, en sa presence, nous tranquilliserons notre coeur :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 20, 'c\'est que, si notre coeur nous condamne, Dieu est plus grand que notre coeur et il sait tout.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 21, 'Bien-aimes, si notre coeur ne nous condamne pas, nous sommes pleins d\'assurance pour nous approcher de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 22, 'et tout ce que nous lui demandons, il nous l\'accorde, parce que nous observons ses commandements et que nous faisons ce qui lui est agreable.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 23, 'Et voici son commandement : que nous croyions au nom de son fils Jesus-Christ, et que nous nous aimions les uns les autres, comme il nous l\'a commande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 3, 24, 'Celui qui observe ses commandements demeure en Dieu et Dieu en lui, et nous reconnaissons qu\'il demeure en nous, a l\'Esprit qu\'il nous a donne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 1, 'Mes bien-aimes, ne croyez pas a n\'importe quel Esprit ; mais examinez les Esprits pour savoir s\'ils viennent de Dieu, car un grand nombre de faux prophetes sont apparus dans le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 2, 'Voici comment vous reconnaitrez l\'Esprit de Dieu : Tout Esprit qui reconnait Jesus pour le Christ venu en chair est de Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 3, 'et tout Esprit qui ne reconnait pas Jesus n\'est pas de Dieu, mais il est l\'Esprit de l\'Antechrist dont vous avez entendu annoncer la venue, et il est deja maintenant dans le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 4, 'Pour vous, mes enfants, vous etes de Dieu, et vous avez vaincu ces Esprits-la, parce que celui qui est en vous est plus grand que celui qui est dans le monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 5, 'Quant a eux, ils viennent du monde ; voila pourquoi ils parlent selon le monde, et le monde les ecoute ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 6, 'nous, nous venons de Dieu ; celui qui connait Dieu nous ecoute ; celui qui ne vient pas de Dieu ne nous ecoute pas. C\'est ainsi que nous distinguons l\'Esprit de la verite de l\'Esprit de l\'erreur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 7, 'Mes bien-aimes, aimons-nous les uns les autres, parce que l\'amour vient de Dieu et quiconque aime est ne de Dieu et connait Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 8, 'Celui qui n\'aime pas n\'a pas connu Dieu : car Dieu est amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 9, 'Voici comment Dieu a manifeste son amour pour nous : Dieu a envoye son Fils unique dans le monde pour que nous eussions la vie par lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 10, 'Voici en quoi consiste l\'amour : ce n\'est pas nous qui avons aime Dieu, mais c\'est lui qui nous a aimes et qui a envoye son Fils, comme une propitiation pour nos peches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 11, 'Mes bien-aimes, si Dieu nous a ainsi aimes, nous aussi nous, devons nous aimer les uns les autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 12, 'Personne n\'a jamais vu Dieu ; si nous nous aimons les uns les autres, Dieu demeure en nous et son amour est parfait en nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 13, 'Voici comment nous reconnaissons que nous demeurons en lui et lui en nous: a ce qu\'il nous a donne de son Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 14, 'Et nous, nous avons vu et nous attestons que le Pere a envoye le Fils comme Sauveur du monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 15, 'Celui qui reconnait que Jesus est le Fils de Dieu, Dieu demeure en lui, et lui en Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 16, 'Et nous, nous avons connu l\'amour que Dieu manifeste en nous, et nous y avons cru. Dieu est amour, et celui qui demeure dans l\'amour demeure en Dieu et Dieu demeure en lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 17, 'Voici comment l\'amour devient parfait chez nous, afin que nous soyons pleins d\'assurance au jour du jugement : tel qu\'il est lui, tels nous sommes nous dans ce monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 18, 'La crainte n\'est pas dans l\'amour ; au contraire, l\'amour parfait bannit la crainte, parce que la crainte suppose une punition, et celui qui craint n\'est pas parfait dans l\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 19, 'Nous devons aimer, parce quil nous a aimes le premier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 20, 'Si quelqu\'un dit : J\'aime Dieu, et qu\'il haisse son frere, il est un menteur, car celui qui n\'aime pas son frere quil a vu, ne peut pas aimer Dieu qu\'il n\'a pas vu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 4, 21, 'Et voici le commandement que nous tenons de lui : Que celui qui aime Dieu aime aussi son frere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 1, 'Quiconque croit que Jesus est le Christ, est ne de Dieu, et quiconque aime Celui qui l\'a fait naitre, aime aussi celui qui est ne de lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 2, 'Voici comment nous reconnaissons que nous aimons les enfants de Dieu : quand nous aimons Dieu et que nous observons ses commandements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 3, 'Car voici en quoi consiste l\'amour de Dieu : que nous observions ses commandements. Et ses commandements ne sont pas penibles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 4, 'car tout ce qui est ne de Dieu triomphe du, monde, et voici la victoire qui triomphe du monde: notre foi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 5, 'Qui est-ce qui triomphe du monde, sinon celui qui croit que Jesus est le Fils de Dieu?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 6, 'C\'est lui, Jesus-Christ, qui est venu au moyen de l\'eau et du sang ; non pas avec l\'eau seule, mais avec l\'eau et le sang ; et c\'est l\'Esprit qui en rend temoignage, parce que l\'Esprit est la verite ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 7, 'car ils sont trois a rendre temoignage,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 8, 'l\'Esprit, l\'eau et le sang, et tous trois sont d\'accord.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 9, 'Si nous acceptons le temoignage des hommes, le temoignage de Dieu est superieur ; car voici le temoignage de Dieu : il a rendu temoignage a son Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 10, 'Celui qui croit au Fils de Dieu a ce temoignage en lui-meme ; celui qui ne croit pas Dieu en fait un menteur, parce qu\'il ne croit pas au temoignage que Dieu a rendu a son Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 11, 'Et voici ce temoignage: Dieu nous a donne la vie eternelle et cette vie est dans son Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 12, 'Celui qui a le Fils a la vie ; celui qui n\'a pas le Fils de Dieu n\'a pas la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 13, 'Je vous ai ecrit cela pour que vous sachiez que vous avez la vie eternelle, vous qui croyez au nom du Fils de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 14, 'Voici l\'assurance que nous avons en lui : si nous demandons quelque chose selon sa volonte, il nous ecoute.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 15, 'Si nous savons qu\'il nous ecoute, quelles que soient nos demandes, nous le savons, parce que nous obtenons ce que nous lui avons demande.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 16, 'Si quelqu\'un voit un de ses freres commettre un peche qui ne soit pas un peche mortel, il priera, et Dieu donnera la vie a ce frere ; il la donnera a ceux qui n\'ont point commis de peche mortel. Il y a un peche mortel ; ce n\'est pas pour ce peche que je parle de prier.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 17, 'Toute iniquite est un peche, et il y a un peche qui n\'est pas mortel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 18, 'Nous savons que quiconque est ne de Dieu ne peche pas, mais celui qui est ne de Dieu veille sur lui-meme, et le Malin n\'a aucune prise sur lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 19, 'Nous savons que nous sommes venus de Dieu et que le monde entier est au pouvoir du Malin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 20, 'Mais nous savons aussi que le Fils de Dieu est venu et nous a donne l\'intelligence pour que nous connaissions le veritable ; et nous sommes dans le veritable, dans son Fils Jesus-Christ. C\'est lui qui est le Dieu veritable et la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (62, 5, 21, 'Mes enfants, gardez-vous des idoles. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 1, 'L\'ancien a l\'elue Cyria et a ses enfants (que j\'aime veritablement)  et ce n\'est pas moi seul qui les aime, mais aussi tous ceux qui ont connu la verite ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 2, '(je les aime a cause de la verite qui demeure en nous et qui sera eternellement avec nous).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 3, 'Grace, misericorde et paix, venant de Dieu le Pere et de Jesus-Christ, le Fils du Pere, soient avec nous dans la verite et dans l\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 4, 'J\'ai ete bien heureux de trouver quelques-uns de tes enfants marchant dans la verite, comme nous en avons recu le commandement du Pere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 5, 'Et maintenant, je te demande, Cyria (non comme si je t\'ecrivais un nouveau commandement, mais celui-la meme que nous avons recu des le commencement), de nous aimer les uns les autres. Voici en quoi consiste cet amour : marchons selon ses commandements.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 6, 'Et voici en quoi consiste ce commandement : marcher (comme vous l\'avez appris des le commencement), marcher dans l\'amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 7, 'Il s\'est repandu dans le monde un grand nombre de seducteurs qui ne reconnaissent pas que Jesus-Christ est venu en chair : oui, voila le seducteur! voila l\'Antechrist!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 8, 'Faites attention a vous pour ne pas perdre le fruit de vos efforts, mais pour recevoir une pleine recompense.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 9, 'Celui qui va plus loin, qui ne reste pas attache a la doctrine du Christ, n\'a point Dieu ; celui qui reste attache a cette doctrine, a le Pere et a le Fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 10, 'Si quelqu\'un vient a vous et n\'apporte pas cette doctrine, ne le recevez pas dans votre maison et ne lui donnez pas le salut de paix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 11, 'Car celui qui le lui donne prend part a ses mauvaises oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 12, 'Je n\'ai pas voulu, ayant bien des choses a vous dire, vous les faire savoir avec l\'encre et le papier ; mais comme j\'espere aller vous voir, je vous entretiendrai de vive voix, pour que notre joie soit complete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (63, 1, 13, 'Les enfants de ta soeur l\'elue te saluent. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 1, 'L\'ancien au bien-aime Gaius (que j\'aime veritablement).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 2, 'Mon bien-aime, je fais des voeux pour que tu prosperes en toutes choses et que la sante de ton corps soit aussi bonne que celle de ton ame.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 3, 'J\'ai ete, en effet, bien heureux de voir des freres venir et rendre temoignage de la verite qui est en toi ; je veux dire rendre temoignage que tu marches dans la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 4, 'Je n\'ai pas de plus grande joie que d\'apprendre que mes enfants marchent dans la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 5, 'Mon bien-aime, tu te montres fidele dans ce que tu fais pour les freres, et ce sont des etrangers.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 6, 'Ils ont rendu temoignage a ton amour devant toute l\'Eglise. Tu feras bien de pourvoir a leur voyage d\'une maniere digne de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 7, 'car c\'est pour le nom qu\'ils sont partis sans rien recevoir des paiens.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 8, 'Aussi devons-nous accueillir de tels hommes, afin de nous mettre a l\'oeuvre avec eux pour la verite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 9, 'J\'ai ecrit quelques mots a l\'Eglise, mais Diotrephes, qui affecte d\'etre leur chef, n\'accepte rien de nous.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 10, 'Aussi, quand je viendrai, je signalerai les actes qu\'il commet, comment il se repand contre nous en mauvais propos, et cela ne lui suffit pas, car non seulement il n\'accueille pas les freres lui-meme, mais il defend de les accueillir, et ceux qui seraient desireux de le faire, il les chasse de l\'Eglise.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 11, 'Mon bien-aime, n\'imite pas le mal, mais le bien. Celui qui fait le bien est de Dieu. Celui qui fait le mal n\'a pas vu Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 12, 'Quant a Demetrius, tous, et la verite elle-meme, lui rendent un bon temoignage. Nous aussi nous lui rendons un bon temoignage, et tu sais que notre temoignage est veridique.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 13, 'J\'aurais bien des choses a t\'ecrire ; mais je ne veux pas te les faire savoir avec l\'encre et la plume.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (64, 1, 14, 'J\'espere te voir bientot et nous nous entretiendrons de vive voix. La paix soit avec toi. Les amis te saluent. Salue les amis chacun en particulier. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 1, 'Jude, serviteur de Jesus-Christ et frere de Jacques, a ceux qui sont aimes en Dieu le Pere, qui sont gardes pour Jesus-Christ, qui sont elus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 2, 'Misericorde, paix et amour vous soient de plus en plus donnes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 3, 'Bien-aimes, je mettais beaucoup de soin a vous ecrire concernant notre salut commun, quand je me suis trouve force de vous adresser cette lettre pour vous prier de defendre la foi qui a ete une fois pour toutes transmise aux fideles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 4, 'Car il s\'est faufile parmi nous certains personnages (des impies dont la sentence est deja ecrite et depuis longtemps) qui changent en orgie la grace de notre Dieu, et qui renient notre seul maitre et Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 5, 'Je veux vous rappeler (et c\'est inutile, car vous savez tout): que le Seigneur, ayant delivre le peuple du pays d\'Egypte, fit perir la seconde fois ceux qui furent incredules ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 6, 'que les anges qui n\'ont pas su conserver leur rang et ont deserte leurs propres demeures, ont ete mis par lui en reserve pour le jugement du grand jour, dans les tenebres et retenus par des chaines eternelles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 7, 'que Sodome, Gomorrhe et les villes voisines qui se prostituerent comme ceux dont je parle et coururent aux crimes contre nature, sont devant nous comme un exemple, subissant la peine du feu eternel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 8, 'Malgre cela, ceux dont je parle font de meme ; dans leurs reveries, ils souillent la chair, meprisent l\'autorite, insultent les gloires!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 9, 'Or, meme l\'archange Michel, quand il discutait avec le Diable et lui disputait le corps de Moise, n\'osa pas l\'insulter, en prononcant son jugement, il lui dit seulement : Que le Seigneur te punisse !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 10, 'Eh bien, ceux dont il s\'agit insultent tout ce qu\'ils ne connaissent pas, et ce qu\'ils savent naturellement comme des betes brutes, ils s\'y perdent!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 11, 'Malheur a eux! car ils sont entres dans la voie de Cain! Ils se sont jetes, pour de l\'argent, dans l\'erreur de Balaam! Ils ont peri dans la revolte de Core!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 12, 'Ces gens-la sont un ecueil dans vos agapes ; ils s\'y gorgent sans pudeur ; ils ne pensent qu\'a se repaitre ! Nuages sans eau emportes ca et la par les vents! Arbres de fin d\'automne sans fruits! deux fois morts! deracines!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 13, 'Flots sauvages de la mer, jetant l\'ecume de leurs turpitudes! Astres errants, auxquels le gouffre des tenebres est reserve pour l\'eternite!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 14, 'C\'est d\'eux qu\'a prophetise Henoch, le septieme patriarche depuis Adam, quand il a dit : Voila que le Seigneur vient avec ses saintes myriades,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 15, 'pour prononcer un jugement contre tous, et pour convaincre tous les impies de toutes les oeuvres d\'impiete qu\'ils ont commises et de toutes les dures paroles qu\'ils ont prononcees contre lui, eux, ces pecheurs impies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 16, 'Ce sont des mecontents, toujours a gronder, guides par leurs passions, dont la bouche est pleine d\'emphase, qui flattent les gens par interet !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 17, 'Mais vous, bien-aimes, rappelez-vous les paroles prononcees, par les apotres de notre Seigneur Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 18, 'Ils vous disaient qu\'aux derniers temps paraitraient des railleurs, guides par leurs passions impies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 19, 'Eh bien, il s\'agit de ceux-la, de ces auteurs de schismes, de ces etres sensuels, n\'ayant pas l\'Esprit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 20, 'Mais vous, bien-aimes, edifiez-vous vous-memes sur le fondement de votre tres sainte foi ; priez, diriges par l\'Esprit saint ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 21, 'maintenez-vous vous-memes dans l\'amour de Dieu ; attendez que la misericorde de notre Seigneur Jesus-Christ vous donne la vie eternelle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 22, 'De ceux qui hesitent, reprimandez les uns,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 23, 'sauvez les autres en les arrachant du feu ; ayez des autres une pitie melee de crainte, haissant jusqu\'au vetement souille par la chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 24, 'A celui qui peut vous preserver de toute chute et vous faire comparaitre devant sa gloire irreprochables et joyeux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (65, 1, 25, 'au Dieu unique, notre Sauveur par Jesus-Christ notre Seigneur, appartiennent la gloire, la grandeur, la force et la puissance de toute eternite, maintenant et dans tous les siecles! Amen. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 1, 'Revelation de Jesus-Christ que Dieu lui a accordee pour montrer a ses serviteurs ce qui doit arriver bientot, et que le Christ a transmise en envoyant son ange a son serviteur Jean,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 2, 'qui atteste, comme temoin oculaire, la parole de Dieu et le temoignage de Jesus-Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 3, 'Heureux celui qui lira, heureux ceux qui entendront la parole de cette prophetie et qui observeront ce qui y est ecrit, car le temps est proche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 4, 'Jean aux sept eglises d\'Asie. Grace et paix vous soient donnees par celui qui est, qui etait et qui viendra, et par les sept esprits qui sont devant son trone,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 5, 'et par Jesus-Christ, le temoin fidele, le premier-ne des morts, le prince des rois de la terre. A celui qui nous aime, et nous a laves de nos peches dans son sang,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 6, 'et qui a fait de nous un Royaume, qui a fait de nous des pretres de Dieu son Pere, a lui la gloire et la force aux siecles des siecles! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 7, 'Le voici, il vient sur les nuees et tout oeil le verra ; ceux memes qui l\'ont perce le verront, et toutes les tribus de la terre se lamenteront a cause de lui. ');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 8, 'Oui, amen! Je suis l\'Alpha et l\'Omega, dit le Seigneur Dieu, Celui qui est, qui etait et qui viendra, le Tout-Puissant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 9, 'Moi, Jean, votre frere et votre compagnon dans les tribulations, dans la royaute et la ferme attente de Jesus, je me trouvai dans l\'ile qu\'on appelle Patmos, a cause de la parole de Dieu et du temoignage de Jesus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 10, 'Je tombai en extase, le jour du Seigneur, et j\'entendis derriere moi une voix eclatante comme le son d\'une trompette');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 11, 'qui disait : Ce que tu vas voir, ecris-le dans un livre et envoie-le aux sept Eglises, a Ephese, a Smyrne, a Pergame, a Thyatires, a Sardes, a Philadelphie, a Laodicee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 12, 'Et je me retournai pour chercher la voix qui me parlait et, m\'etant retourne, je vis sept chandeliers d\'or ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 13, 'au milieu etait un etre qui ressemblait a un fils d\'homme, revetu d\'une longue robe, et ceint a la hauteur de la poitrine d\'une ceinture d\'or.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 14, 'Sa tete et ses cheveux etaient blancs comme de la laine blanche, comme de la neige, ses yeux etaient comme la flamme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 15, 'ses pieds comme le cuivre rougi dans une fournaise ardente. Sa voix ressemblait a la voix des grosses eaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 16, 'A la main droite il avait sept etoiles ; de sa bouche sortait un glaive aigu a deux tranchants, et son aspect etait celui du soleil quand il brille dans tout son eclat.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 17, 'A sa vue, je tombai comme mort a ses pieds, et il posa sa main droite sur moi, disant : Ne crains point, je suis le Premier et le Dernier');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 18, 'le vivant ; j\'ai ete mort, et voila que maintenant je vis aux siecles des siecles, et je tiens les clefs de la mort et de la Demeure-des-Morts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 19, 'Ecris donc ce que tu as vu et ce qui est et ce qui doit arriver apres,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 1, 20, 'ecris le mystere des sept etoiles que tu as vues dans ma main et des sept chandeliers d\'or. Les sept etoiles sont les anges des sept Eglises, et les chandeliers sont les sept Eglises.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 1, 'A l\'ange de l\'Eglise d\'Ephese ecris : Voici ce que dit celui qui tient les sept etoiles dans sa main droite et qui marche au milieu des sept chandeliers d\'or :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 2, 'Je sais tes oeuvres, et ton labeur, et ta patience, et que tu ne peux supporter les mechants ; et tu as mis a l\'epreuve ceux qui se disent apotres et qui ne le sont pas, et tu les as trouves menteurs,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 3, 'et tu as de la patience ; et tu as tout supporte pour mon nom, et tu ne t\'es point lasse.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 4, 'Mais j\'ai contre toi que tu t\'es relache de ton premier amour.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 5, 'Souviens-toi donc d\'ou tu es tombe, et repens-toi, et reviens a tes premieres oeuvres ; sinon je viens a toi, et je change ton chandelier de place, si tu ne te repens pas.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 6, 'Pourtant tu as en ta faveur que tu hais les oeuvres des Nicolaites que moi aussi je hais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 7, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises : Au vainqueur je donnerai a manger de l\'arbre de vie qui est dans le Paradis de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 8, 'A l\'ange de l\'Eglise de Smyrne ecris: Voici ce que dit le Premier et le Dernier, qui etait mort et qui est revenu a la vie :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 9, 'Je connais tes tribulations et ta pauvrete (pourtant tu es riche), et les injures de gens qui se disent Juifs sans l\'etre et qui ne sont autre chose qu\'une synagogue de Satan.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 10, 'Ne t\'effraye pas de ce que tu vas souffrir ; voila que le Diable va jeter plusieurs des votres en prison pour que vous soyez eprouves, et vous passerez par une tribulation de dix jours. Sois fidele jusqu\'a la mort, et je te donnerai la couronne de vie!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 11, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises : Le vainqueur n\'aura rien a souffrir de la seconde mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 12, 'A l\'ange de l\'Eglise de Pergame ecris: Voici ce que dit celui qui tient le glaive aigu a deux tranchants :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 13, 'Je sais que la ou tu habites est le trone de Satan ; et tu gardes mon nom, et tu n\'a pas renie ma foi, en ces jours ou Antipas, mon temoin fidele, a ete tue chez vous, la ou Satan habite.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 14, 'Mais j\'ai contre toi quelque chose : tu as la des gens qui professent la doctrine de Balaam, qui enseigna a Balac a jeter le scandale devant les fils d\'Israel, a manger des viandes consacrees aux idoles et a forniquer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 15, 'C\'est ainsi que tu as des tiens qui professent la doctrine des Nicolaites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 16, 'Repens-toi donc ; sinon je viens a toi sans retard, et je combattrai contre eux avec le glaive de ma bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 17, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises : Au vainqueur je donnerai de la manne cachee, et je donnerai une pierre blanche, et sur cette pierre un nom nouveau est ecrit que nul ne connait, sauf celui qui la recoit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 18, 'A l\'ange de l\'Eglise de Thyatires ecris : Voici ce que dit le Fils de Dieu, celui qui a les yeux comme la flamme et les pieds comme le cuivre! :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 19, 'Je sais tes oeuvres, et ton amour, et ta foi, et tes services, et ta patience, et tes dernieres oeuvres plus nombreuses que les premieres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 20, 'Mais j\'ai contre toi que tu laisses faire la femme Jezabel, qui se dit prophetesse et qui seduit mes serviteurs, et leur enseigne a forniquer et a manger des viandes consacrees aux idoles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 21, 'Et je lui ai donne du temps pour qu\'elle se repente, et elle ne veut pas se repentir de sa fornication.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 22, 'Et voila que je la jette sur un lit de souffrances ; et aux complices de ses adulteres j\'envoie une grande tribulation, s\'ils ne se repentent pas de leurs oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 23, 'Et je ferai mourir ses enfants, je les tuerai, et toutes les Eglises apprendront que Je suis, moi, celui qui sonde les reins et les coeurs et Je rendrai a chacun de vous selon ses oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 24, 'Quant a vous autres de Thyatires qui ne professez pas cette doctrine et qui n\'avez pas connu les profondeurs de Satan, comme ils disent, je ne vous impose pas d\'autre fardeau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 25, 'Seulement ce que vous avez, tenez-le bien jusqu\'a ce que je vienne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 26, 'Et le vainqueur, et celui qui garde mes oeuvres jusqu\'a la fin, Je lui donnerai pouvoir sur les nations');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 27, 'Et il les gouvernera avec une verge de fer Il les brisera comme des vases d\'argile. Ainsi que j\'en ai recu moi-meme le pouvoir de mon Pere,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 28, 'et je lui donnerai l\'etoile du matin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 2, 29, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 1, 'A l\'ange de l\'Eglise de Sardes ecris: Voici ce que dit celui qui a les sept Esprits de Dieu et les sept etoiles : Je sais tes oeuvres ; tu passes pour etre vivant, mais tu es mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 2, 'Sois vigilant et fortifie ce qui reste, ce qui est pres de mourir ; car je n\'ai pas trouve tes oeuvres parfaites devant mon Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 3, 'Souviens-toi donc comment tu as recu et entendu la parole, et garde-la, et repens-toi. Si tu n\'es pas vigilant, je viendrai comme un voleur, et tu ne sauras pas a quelle heure je viendrai a toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 4, 'Tu as cependant quelques personnes a Sardes qui n\'ont pas souille leurs vetements ; elles marcheront avec moi en robes blanches, car elles en sont dignes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 5, 'Le vainqueur sera ainsi pare de vetements blancs, et je n\'effacerai pas son nom du livre de vie, et je confesserai son nom devant mon Pere et devant ses anges.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 6, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 7, 'A l\'ange de l\'Eglise de Philadelphie, ecris : Voici ce que dit le Saint, le Vrai, qui a la clef de David, Qui ouvre et personne ne fermera, Qui ferme et personne n\'ouvrira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 8, 'Je sais tes oeuvres ; voici, j\'ai ouvert devant toi une porte que personne ne peut fermer, parce que, malgre ta faiblesse, tu as garde ma parole et n\'as pas renie mon nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 9, 'Voici, je te donne des membres de la synagogue de Satan, qui se disent Juifs et qui ne le sont pas, mais qui mentent. Je ferai qu\'ils viennent et se prosternent a tes pieds, et qu\'ils sachent que je t\'ai aime.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 10, 'Parce que tu as garde ma parole d\'attente, moi aussi je te garderai de l\'heure de l\'epreuve qui va venir sur le monde entier, pour eprouver les habitants de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 11, 'Je viens bientot: Tiens bien ce que tu as, pour que personne ne prenne ta couronne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 12, 'Le vainqueur, je le ferai colonne dans le Temple de mon Dieu, et il n\'en sortira plus, et j\'ecrirai sur cette colonne le nom de mon Dieu et le nom de la ville de mon Dieu, le nom de la nouvelle Jerusalem qui descend du ciel d\'aupres de mon Dieu, ainsi que mon nom, mon nouveau nom.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 13, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 14, 'A l\'ange de l\'Eglise de Laodicee ecris : Voici ce que dit l\'Amen, le temoin fidele et vrai, le principe de la creation de Dieu :');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 15, 'Je sais tes oeuvres ; tu n\'es ni froid, ni chaud. Que je te voudrais ou froid ou chaud!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 16, 'mais, puisque tu es tiede, puisque tu n\'es ni chaud, ni froid, je vais te vomir de ma bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 17, 'Tu te dis : Je suis riche, je me suis enrichi, je n\'ai besoin de rien, et tu ne vois pas que tu es malheureux et miserable, et pauvre, et aveugle, et nu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 18, 'Je te conseille d\'acheter de moi de l\'or passe au feu pour devenir vraiment riche, et des vetements blancs pour te couvrir et cacher la honte de ta nudite, et un collyre pour oindre tes yeux afin que tu voies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 19, 'Ceux que j\'aime, je les reprimande et les chatie. Aie donc du zele, et repens-toi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 20, 'Voici, je me tiens a la porte et je frappe ; si quelqu\'un entend ma voix et m\'ouvre la porte, j\'entrerai chez lui, et je mangerai avec lui et lui avec moi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 21, 'Le vainqueur, je lui donnerai de s\'asseoir avec moi sur mon trone, de meme que, moi aussi, j\'ai vaincu et me suis assis avec mon Pere sur son trone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 3, 22, 'Que celui qui a des oreilles ecoute ce que l\'Esprit dit aux Eglises!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 1, 'Apres cela, je regardai, et voici, une porte etait ouverte dans le ciel, et la premiere voix que j\'avais entendue me parler comme le son d\'une trompette, me dit : Monte ici et je te montrerai ce qui doit suivre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 2, 'Immediatement je tombai en extase : Et voici un trone etait dans le ciel, et sur le trone quelqu\'un assis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 3, 'Et Celui qui y etait assis avait l\'eclat d\'une pierre de jaspe et de cornaline ; et, en cercle autour du trone, un arc-en-ciel brillant comme l\'emeraude ; et, en cercle autour du trone, vingt-quatre trones,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 4, 'et sur ces vingt-quatre trones des vieillards assis, vetus de robes blanches et sur leurs tetes des couronnes d\'or ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 5, 'et du trone sortent des eclairs et des voix, et des coups de tonnerre ; et, devant le trone, brulent sept lampes ardentes qui sont les sept Esprits de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 6, 'et, devant le trone, etait comme une mer transparente semblable a du cristal ; et, au milieu du trone et autour du trone, quatre animaux ayant des yeux partout, devant et derriere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 7, 'Et le premier animal a la forme d\'un lion, et le deuxieme animal, la forme d\'un veau, et le troisieme animal a comme un visage humain, et le quatrieme animal a la forme d\'un aigle aux ailes ouvertes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 8, 'Et les quatre animaux ont chacun six ailes et sont couverts d\'yeux autour du corps et sous le corps, et ils ne cessent de dire jour et nuit : Saint, saint, saint est le Seigneur Dieu Tout-Puissant qui etait, qui est et qui viendra.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 9, 'Et quand les animaux rendent gloire, et honneur, et actions de graces a Celui qui est assis sur le trone et qui vit aux siecles des siecles,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 10, 'les vingt-quatre vieillards se prosternent devant Celui qui est assis sur le trone et adorent Celui qui vit aux siecles des siecles, et ils mettent leurs couronnes au pied du trone,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 4, 11, 'disant : Tu es digne, o notre Seigneur et notre Dieu, de recevoir la gloire et l\'honneur et la puissance, parce que tu as cree toutes choses, et par ta volonte elles sont et ont ete creees!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 1, 'Je vis ensuite, a la droite de Celui qui etait assis sur le trone, un livre ecrit des deux cotes, ferme de sept sceaux ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 2, 'et je vis un ange d\'une force extraordinaire qui s\'ecriait d\'une voix eclatante : Qui est digne d\'ouvrir le livre et d\'en rompre les sceaux?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 3, 'Et personne ne put, ni dans le ciel, ni sur la terre, ni sous la terre, ouvrir le livre ni le regarder.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 4, 'Et je versai bien des larmes, parce que personne n\'etait juge digne d\'ouvrir le livre ni de le regarder.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 5, 'Et l\'un des vieillards me dit : Ne pleure point, voici, le lion de la tribu de Juda, le rejeton de David a reussi a ouvrir le livre et a rompre les sept sceaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 6, 'Et je vis, debout, au milieu du trone et des quatre animaux, et au milieu des vieillards, un agneau qui semblait egorge ; il a sept cornes et sept yeux qui sont les sept Esprits de Dieu envoyes par toute la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 7, 'Et il vint, et il prit le livre de la main droite de Celui qui etait assis sur le trone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 8, 'Et quand il le prit, les quatre animaux et les vingt-quatre vieillards se prosternerent devant l\'agneau, et chacun avait une harpe et des coupes d\'or pleines de parfums (les prieres des saints), et ils chantaient un cantique nouveau et disaient:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 9, 'Toi, tu es digne de prendre le livre et d\'en ouvrir les sceaux ; car tu as ete egorge, et, avec ton sang, tu as achete pour Dieu des elus de toute tribu, de toute langue, de tout peuple, de toute race, et tu en as fait pour notre Dieu un Royaume, et tu en as fait des pretres, et ils regneront sur la terre !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 10, 'Et je regardai, et j\'entendis comme la voix d\'une foule d\'anges reunis autour du trone et des animaux et des vieillards,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 11, 'et il y en avait des myriades de myriades, des milliers de milliers qui disaient d\'une voix eclatante:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 12, 'Digne est l\'agneau egorge de recevoir la puissance, et la richesse, et la sagesse, et la force, et l\'honneur, et la gloire, et la benediction!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 13, 'Et j\'entendis toute creature dans le ciel, sur la terre, sous la terre, sur la mer, j\'entendis tout ce qu\'ils contiennent qui disait : A Celui qui est assis sur le trone et a l\'agneau soient la benediction, et l\'honneur, et la gloire, et la force aux siecles des siecles! Et les quatre animaux dirent: Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 5, 14, 'Et les vieillards se prosternerent et adorerent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 1, 'Et je regardai quand l\'agneau ouvrit le premier des sept sceaux et j\'entendis un des quatre animaux qui disait d\'une voix de tonnerre: Viens ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 2, 'et je regardai et voici un cheval blanc, et celui qui le montait tenait un arc, et il lui fut donne une couronne, et il partit en vainqueur pour remporter la victoire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 3, 'Et quand il ouvrit le deuxieme sceau, j\'entendis le deuxieme animal, disant: Viens ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 4, 'et il parut un autre cheval, il etait roux, et a celui qui le montait il fut donne d\'enlever la paix de la terre et de faire que les hommes s\'egorgent les uns les autres, et il lui fut donne une grande epee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 5, 'Et quand il ouvrit le troisieme sceau, j\'entendis le troisieme animal, disant : Viens ; et je regardai, et voici un cheval noir, et celui qui le montait avait une balance a la main.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 6, 'Et j\'entendis comme une voix au milieu des quatre animaux disant : Un choenix de froment, un denier ; trois choenix d\'orge, un denier ; l\'huile et le vin, n\'y touche pas !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 7, 'Et quand il ouvrit le quatrieme sceau, j\'entendis la voix du quatrieme animal, disant : Viens ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 8, 'et je regardai, et voici un cheval jaune, et celui qui le montait s\'appelait la Mort et la Demeure-des-morts le suivait et il leur fut donne puissance de tuer le quart de la terre par le glaive, par la faim, par la maladie et par les betes feroces.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 9, 'Et quand il ouvrit le cinquieme sceau, je vis sous l\'autel les ames de ceux qui avaient ete egorges pour la parole de Dieu et pour le temoignage qu\'ils avaient rendu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 10, 'Et ils s\'ecriaient et disaient d\'une voix eclatante: Jusques a quand, o Maitre, saint et veridique, ne feras-tu point justice et ne redemanderas-tu point notre sang a ceux qui demeurent sur la terre?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 11, 'Et il leur fut donne a chacun une robe blanche, et il leur fut dit de patienter encore un peu, jusqu\'a ce que leurs compagnons de service et leurs freres qui devaient etre tues comme eux, fussent venus completer leur nombre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 12, 'Et je regardai quand il ouvrit le sixieme sceau, et il y eut un vaste ebranlement ; et le soleil devint noir comme un sac de crin ; et la lune prit une couleur de sang ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 13, 'et les etoiles tomberent du ciel sur la terre, comme les fruits verts d\'un figuier agite par un violent coup de vent ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 14, 'et le ciel se retira comme un livre qu\'on roule ; et toutes les montagnes et toutes les iles furent jetees hors de leur place ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 15, 'et les rois de la terre, et les grands, et les tribuns militaires, et les riches, et les forts, et tous les esclaves, et tous les hommes libres se cacherent dans les cavernes et dans les rochers des montagnes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 16, 'et dirent aux montagnes et aux rochers: Tombez sur nous et cachez-nous du regard de Celui qui est assis sur le trone, et de la colere de l\'agneau,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 6, 17, 'car il est venu, le grand jour de sa colere, et qui peut subsister?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 1, 'Et apres cela, je vis quatre anges places aux quatre angles de la terre, retenant les quatre vents de la terre afin qu\'il ne souffle pas de vent ni sur la terre, ni sur la mer, ni sur aucun arbre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 2, 'Et je vis un autre ange qui s\'elevait de l\'Orient, d\'ou vient le soleil, il tenait le sceau du Dieu vivant, et il s\'ecriait, parlant d\'une voix eclatante aux quatre anges qui avaient recu le pouvoir de sevir sur la terre et la mer:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 3, 'Ne sevissez ni contre la terre, ni contre la mer, ni contre les arbres, avant que nous ayons marque au front les serviteurs de notre Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 4, 'Et j\'entendis le nombre de ceux qui etaient marques. Cent quarante-quatre mille etaient marques, appartenant a toutes les tribus des enfants d\'Israel:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 5, 'de la tribu de Juda, douze mille marques ; de la tribu de Ruben, douze mille ; de la tribu de Gad, douze mille ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 6, 'de la tribu d\'Aser, douze mille ; de la tribu de Nephthali, douze mille ; de la tribu de Manasse, douze mille ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 7, 'de la tribu de Simeon, douze mille ; de la tribu de Levi, douze mille ; de la tribu d\'Issachar, douze mille ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 8, 'de la tribu de Zabulon, douze mille ; de la tribu de Joseph, douze mille ; de la tribu de Benjamin, douze mille marques.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 9, 'Apres cela je regardai et voici une foule immense que personne ne pouvait compter, de toute race, de toute tribu, de tout peuple, de toute langue, se tenant devant le trone et devant l\'agneau, vetus de robes blanches, portant des palmes a la main,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 10, 'et s\'ecriant d\'une voix eclatante: Le salut vient de notre Dieu assis sur le trone, et de l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 11, 'Et tous les anges se tinrent en cercle autour du trone et des vieillards et des quatre animaux, et ils se prosternerent devant le trone la face contre terre et adorerent Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 12, 'disant: Amen. La benediction, et la gloire, et la sagesse, et l\'action de graces, et l\'honneur, et la puissance, et la force a notre Dieu aux siecles des siecles! Amen.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 13, 'Et alors un des vieillards, me parlant, me dit: Ceux qui sont vetus de robes blanches, qui sont-ils et d\'ou sont-ils venus?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 14, 'Et je lui dis : Mon Seigneur, c\'est toi qui le sais. Et il me dit: Ceux-la sont venus de la grande tribulation, et ils ont lave leurs robes et les ont blanchies dans le sang de l\'agneau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 15, 'voila pourquoi ils sont devant le trone de Dieu et ils l\'adorent nuit et jour dans son Temple, et Celui qui est assis sur le trone etablira sa demeure au-dessus d\'eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 16, 'Ils n\'auront plus faim, ils n\'auront plus soif, Ils ne souffriront plus ni du soleil, ni de la chaleur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 7, 17, 'car l\'agneau qui est au milieu du trone les fera paitre et les conduira aux eaux, sources de la vie et Dieu essuiera toutes larmes de leurs yeux ;.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 1, 'Et quand il ouvrit le septieme sceau, il se fit, dans le ciel, un silence d\'environ une demi-heure.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 2, 'Et je vis les sept anges qui se tiennent devant Dieu, et sept trompettes leur furent donnees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 3, 'Et un autre ange s\'avanca et se tint pres de l\'autel et il portait un encensoir d\'or ; et il lui fut donne une quantite de parfums pour les offrir avec les prieres de tous les saints sur l\'autel d\'or, qui est en face du trone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 4, 'Et la fumee des parfums s\'eleva, avec les prieres des saints, de la main de l\'ange devant Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 5, 'Et l\'ange prit l\'encensoir et le remplit des charbons embrases de l\'autel et les jeta sur la terre, et ils produisirent des coups de tonnerre et des voix, et des eclairs, et un grand ebranlement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 6, 'Et les sept anges qui avaient les sept trompettes se preparerent a en sonner.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 7, 'Et le premier sonna : et une grele melee de feu et de sang tomba sur la terre ; et le tiers de la terre fut brule, et le tiers des arbres fut brule, et toute herbe verte fut brulee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 8, 'Et le deuxieme ange sonna : et comme une grande montagne incandescente fut lancee dans la mer, et le tiers de la mer fut change en sang ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 9, 'et le tiers des creatures vivantes qui sont dans la mer mourut ; et le tiers des navires fut detruit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 10, 'Et le troisieme ange sonna : et il tomba du ciel une grande etoile, brulant comme un flambeau, et elle tomba sur le tiers des rivieres et sur les sources d\'eaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 11, 'Et le nom de cette etoile est : Absinthe, et le tiers des eaux se changea en absinthe, et beaucoup d\'hommes moururent, parce que ces eaux etaient empoisonnees.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 12, 'Et le quatrieme ange sonna : et furent frappes le tiers du soleil, et le tiers de la lune, et le tiers des etoiles, afin que le tiers de leur lumiere s\'eteignit, le tiers de la lumiere du jour, et de meme pour la nuit.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 8, 13, 'Et je regardai, et j\'entendis un aigle qui volait au zenith et disait d\'une voix eclatante: Malheur! malheur! malheur aux habitants de la terre, a cause des autres coups de trompette des trois anges qui ont encore a sonner!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 1, 'Et le cinquieme ange sonna : et je vis une etoile tomber du ciel sur la terre, et lui fut donnee la clef du puits de l\'abime.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 2, 'Et elle ouvrit le puits de l\'abime, et il sortit du puits de la fumee comme la fumee d\'une grande fournaise ; et le soleil et l\'air furent assombris par la fumee du puits ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 3, 'et de cette fumee sortirent des sauterelles qui assombrirent la terre ; et il leur fut donne une puissance semblable a la puissance des scorpions de la terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 4, 'et il leur fut dit de ne faire de mal ni a l\'herbe de la terre, ni a la verdure, ni a aucun arbre, mais seulement aux hommes qui ne sont pas marques au front du sceau de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 5, 'et il leur fut donne de ne pas les tuer, mais de les tourmenter pendant cinq mois ; et ce tourment ressemblait au tourment du scorpion quand il s\'attaque a l\'homme ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 6, 'et, en ces jours-la, les hommes chercheront la mort, et ne la trouveront point ; et ils desireront mourir, et la mort les fuira.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 7, 'Et ces sauterelles ressemblaient a des chevaux equipes pour la guerre ; elles avaient sur la tete comme des couronnes qui auraient ete en or ; et leurs visages etaient comme des visages d\'hommes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 8, 'et leurs cheveux comme des cheveux de femme ; et leurs dents comme des dents de lions ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 9, 'et elles avaient des cuirasses comme des cuirasses de fer ; et le bruit de leurs ailes etait comme le bruit de chars atteles de nombreux chevaux s\'elancant au combat ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 10, 'et elles avaient des queues armees de dards comme les scorpions ; et dans ces queues etait leur pouvoir de faire du mal aux hommes pendant cinq mois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 11, 'Elles avaient a leur tete un roi, l\'ange de l\'abime, qui s\'appelle en hebreu Abbadon, et en grec Apollyon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 12, 'Le premier malheur est passe ; voici il vient encore deux malheurs apres celui-la.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 13, 'Et le sixieme ange sonna : et j\'entendis sortir une voix des quatre cornes de l\'autel d\'or qui est devant Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 14, 'elle disait au sixieme ange qui tenait la trompette: Delie les quatre anges enchaines sur le grand fleuve l\'Euphrate,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 15, 'et furent delies les quatre anges qui etaient prets pour l\'heure, et le jour, et le mois, et l\'annee, afin qu\'ils missent a mort le tiers des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 16, 'Et leur armee comptait deux cents millions de cavaliers ; j\'entendis le chiffre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 17, 'Et voici comment les chevaux m\'apparurent, et ceux qui les montaient: ceux-ci avaient des cuirasses couleur de feu, d\'hyacinthe et de soufre ; et les tetes des chevaux etaient comme des tetes de lions, et de leurs bouches sortaient du feu, et de la fumee, et du soufre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 18, 'Par ces trois plaies fut mis a mort le tiers des hommes, par le feu, et par la fumee, et par le soufre qui leur sortaient de la bouche ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 19, 'car la puissance de ces chevaux est dans leurs bouches et dans leurs queues. En effet, leurs queues ressemblent a des serpents: elles ont des tetes et c\'est avec elles qu\'ils font du mal.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 20, 'Et le reste des hommes, qui n\'avaient pas ete mis a mort par ces plaies ne se repentirent point des oeuvres de leurs mains, et ne cesserent pas d\'adorer les demons et les idoles d\'or, et d\'argent, et d\'airain, et de pierre, et de bois, qui ne peuvent ni voir, ni entendre, ni marcher ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 9, 21, 'et ils ne se repentirent ni de leurs meurtres, ni de leurs sorcelleries, ni de leur fornication, ni de leurs rapines.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 1, 'Et je vis un autre ange d\'une force extraordinaire qui descendait du ciel, entoure d\'un nuage, la tete ceinte de l\'arc-en-ciel, le visage comme le soleil, les pieds comme des colonnes de feu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 2, 'il tenait a la main un petit livre ouvert, et il posa le pied droit sur la mer, le gauche sur la terre,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 3, 'et d\'une voix terrible comme le rugissement du lion, il jeta un cri ; et quand il jeta ce cri, les sept tonnerres firent retentir leurs voix.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 4, 'Et quand les sept tonnerres eurent parle je voulus ecrire, mais j\'entendis une voix du ciel me dire : Tiens secret ce qu\'ont dit les sept tonnerres et ne l\'ecris point.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 5, 'Et l\'ange que je voyais debout sur la terre et sur la mer leva la main droite vers le ciel,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 6, 'et jura par Celui qui vit aux siecles des siecles, qui a cree le ciel et ce qu\'il renferme, et la terre et ce qu\'elle renferme, et la mer et ce qu\'elle renferme, qu\'il n\'y aurait plus de delai,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 7, 'mais qu\'au jour ou retentirait la voix du septieme ange sonnant de la trompette, s\'accomplirait le mystere de Dieu, comme il l\'avait annonce a ses serviteurs les prophetes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 8, 'Et la voix que j\'avais entendue venant du ciel me parla de nouveau : Va, dit-elle, prends le petit livre ouvert dans la main de l\'ange qui se tient debout sur la mer et sur la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 9, 'Alors j\'allai vers l\'ange et lui demandai de me donner le petit livre. Prends-le, me dit-il, et le devore ; dans tes entrailles il te sera amer ; mais dans ta bouche tu le trouveras doux comme du miel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 10, 'Et je pris le livre de la main de l\'ange, et je le devorai. Dans ma bouche il fut doux comme le miel, mais quand il fut devore, il quand il fut dans mes entrailles, il me fut amer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 10, 11, 'Et on me dit : Il faut que tu prophetises encore sur beaucoup de peuples, de races, de langues et de rois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 1, 'Et on me donna une mesure de la forme d\'un baton et on me dit:Leve-toi et mesure le Temple de Dieu, et l\'autel, et l\'espace occupe par ceux qui adorent dans le Temple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 2, 'Et la cour exterieure du Temple, n\'en tiens pas compte, et ne la mesure point, car elle a ete donnee aux paiens, et ils fouleront aux pieds la ville sainte pendant quarante-deux mois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 3, 'Et je donnerai une mission a mes deux temoins qui prophetiseront pendant douze cent soixante jours, revetus de sac.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 4, 'Ils sont les deux oliviers et les deux chandeliers debout devant le Seigneur de la terre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 5, 'et si quelqu\'un essaye de leur faire du mal, un feu sort de leur bouche et devore leurs ennemis ; oui, si quelqu\'un essaye de leur faire du mal, c\'est ainsi qu\'il doit perir.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 6, 'Ils ont le pouvoir de fermer le ciel, et d\'empecher la pluie de tomber les jours ou ils prophetisent ; et ils ont le pouvoir de changer l\'eau en sang, et de frapper la terre de telle ou telle plaie et aussi souvent qu\'ils le voudront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 7, 'Et quand ils auront fini de rendre leur temoignage, la bete qui monte de l\'abime leur fera la guerre, les vaincra et les tuera ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 8, 'et leurs corps resteront sur la place de la grande ville qui s\'appelle symboliquement Sodome, et Egypte et ou leur Seigneur a ete crucifie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 9, 'Et des gens de tout peuple, de toute tribu, de toute langue et de toute race verront leurs corps pendant trois jours et demi, et ils ne permettront pas qu\'on donne a ces corps la sepulture.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 10, 'Et les habitants de la terre seront dans la joie de leur mort, ils s\'adresseront des felicitations, et s\'enverront des presents, parce que ces deux prophetes ont fait le tourment des habitants de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 11, 'Mais, au bout de trois jours et demi, l\'Esprit de vie venu de Dieu entra en eux, ils se retrouverent sur leurs pieds, et une grande terreur saisit ceux qui les virent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 12, 'Et j\'entendis une grande voix venant du ciel et leur disant: Montez ici ; et ils monterent au ciel sur les nues, a la vue de leurs ennemis.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 13, 'Un terrible tremblement de terre se fit en ce moment, et le dixieme de la ville tomba, et sept mille hommes perirent dans ce tremblement, et les autres, effrayes, rendirent gloire au Dieu du ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 14, 'Le second malheur est passe ; voici venir bientot le troisieme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 15, 'Et le septieme ange sonna : et il y eut de grandes voix dans le ciel qui disaient: Le Royaume du monde est remis a notre Seigneur et a son Christ, et il regnera aux siecles des siecles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 16, 'Et les vingt-quatre vieillards, assis devant Dieu sur leurs trones, tomberent sur la face et adorerent Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 17, 'disant : Nous te rendons graces Seigneur Dieu Tout-Puissant, qui es et qui as ete, de ce que tu t\'es empare de ton grand pouvoir et as inaugure ta Royaute ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 18, 'et les nations etaient en fureur, mais ta colere est venue ; et il est venu le moment de juger les morts et de donner leur recompense a tes serviteurs ;, les prophetes, et aux saints, et a ceux qui craignent ton nom, aux petits et aux grands, et de perdre ceux qui perdent la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 11, 19, 'Alors s\'ouvrit le Temple de Dieu dans le ciel, et on apercut l\'arche de l\'Alliance dans son Temple, et il y eut des eclairs et des voix, et des coups de tonnerre, et un grand ebranlement, et une forte grele.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 1, 'Et un grand prodige parut dans le ciel : une femme, vetue du soleil, ayant la lune sous les pieds, et autour de la tete une couronne de douze etoiles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 2, 'elle etait enceinte et jetait des cris dans le travail et les douleurs de l\'enfantement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 3, 'Et il parut dans le ciel un autre prodige: c\'etait un enorme dragon rouge, a sept tetes et a dix cornes ; sur les tetes, sept diademes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 4, 'et sa queue entrainait le tiers des etoiles du ciel et les jetait sur la terre. Et ce dragon se dressa devant la femme qui allait enfanter, afin de devorer son enfant quand elle l\'aurait mis au monde.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 5, 'Et elle mit au monde un fils, un enfant male, destine a gouverner toutes les nations avec une verge de fer ; et cet enfant fut enleve vers Dieu et vers son trone.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 6, 'Et la femme s\'enfuit au desert, ou Dieu lui a prepare une retraite, et ou elle sera nourrie pendant douze cent soixante jours.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 7, 'Alors eut lieu dans le ciel un combat: Michael et ses anges combattant le dragon. Et le dragon combattit avec ses anges,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 8, 'et ils ne furent pas les plus forts, et il n\'y eut plus de place pour eux dans le ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 9, 'Et il fut precipite, le grand dragon, le Serpent antique, qui s\'appelle le Diable et Satan, le seducteur du monde entier, il fut precipite sur la terre et ses anges avec lui furent precipites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 10, 'Et j\'entendis une grande voix dans le ciel disant: Maintenant le salut et la puissance et la Royaute sont a notre Dieu et le pouvoir a son Christ, car il a ete precipite de haut en bas, l\'accusateur de nos freres qui ne cessait nuit et jour de les accuser devant notre Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 11, 'Et ils l\'ont vaincu eux-memes, a cause du sang de l\'agneau et a cause de la parole de leur temoignage, et ils ont pousse le sacrifice de leur vie jusqu\'a la mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 12, 'Rejouissez-vous donc, o cieux, et vous leurs habitants! malheur a la terre et a la mer! car le Diable est descendu vers vous, plein de rage, sachant que ses jours sont comptes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 13, 'Et quand le dragon se vit precipite sur la terre, il poursuivit, la femme qui avait mis au monde l\'enfant male.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 14, 'Mais la femme recut les deux ailes du grand aigle pour s\'envoler au desert dans sa retraite, ou elle est nourrie loin de la vue du serpent, aune periode, et des periodes, et une demi-periode.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 15, 'Et le serpent vomit de sa bouche apres la femme de l\'eau, une sorte de fleuve pour la noyer et l\'emporter ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 16, 'mais la terre vint au secours de la femme ; elle s\'entr\'ouvrit et absorba le fleuve que le dragon avait vomi de sa bouche.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 12, 17, 'Et le dragon, furieux contre la femme, s\'en alla combattre le reste de sa race qui observait les commandements de Dieu et etaient fideles au temoignage de Jesus ; et il se tint sur le sable de la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 1, 'Et je vis sortir de la mer une bete qui avait dix cornes et sept tetes, et sur les cornes dix diademes, et sur chacune des tetes un nom blasphematoire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 2, 'Et la bete que je vis ressemblait a un leopard ; ses pieds etaient comme ceux de l\'ours, et sa bouche comme celle du lion ; et le dragon lui donna sa force, et son trone, et sa grande puissance.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 3, 'Et l\'une de ses tetes avait comme recu un coup mortel ; mais cette blessure mortelle avait ete guerie. Et la terre entiere tomba en admiration derriere cette bete,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 4, 'et tous les hommes se mirent a adorer le dragon, parce qu\'il avait donne le pouvoir a la bete ; et ils adoraient la bete disant : Qui est semblable a la bete, et qui peut combattre contre elle?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 5, 'Et il lui fut donne une bouche proferant des discours pleins d\'orgueil et de blaspheme, et il lui fut donne le pouvoir d\'agir ainsi pendant quarante-deux mois.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 6, 'Et elle ouvrit la bouche pour vomir des blasphemes contre Dieu, des blasphemes contre son nom, contre son tabernacle, contre ceux qui demeurent dans le ciel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 7, 'Et il lui fut donne de faire la guerre aux saints et de les vaincre, et la puissance lui fut accordee sur toute tribu, tout peuple, toute langue et toute race.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 8, 'Et tous les habitants de la terre l\'adoreront, tous ceux dont le nom n\'est pas ecrit depuis le commencement du monde dans le livre de vie de l\'agneau qui a ete egorge.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 9, 'Que celui qui a des oreilles entende!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 10, 'Celui qui fait des captifs est captif a son tour ; celui qui, frappe de l\'epee perira par l\'epee. C\'est ici que se montrent la patience et la foi des saints.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 11, 'Et je vis une autre bete sortir de la terre ; elle avait deux cornes semblables a celle d\'un agneau, et elle parlait comme un dragon ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 12, 'et elle exercait toute la puissance de la premiere bete en sa presence ; et elle faisait que la terre et ses habitants adoraient la premiere bete (celle dont la blessure mortelle avait ete guerie).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 13, 'Elle faisait de grands prodiges, jusqu\'a faire descendre le feu du ciel sur la terre sous les yeux des hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 14, 'Et elle seduisait les habitants de la terre par les prodiges qu\'il lui etait donne de faire en presence de la bete. Elle disait aux habitants de la terre de faire une image en l\'honneur de la bete (de cette bete qui a recu un coup d\'epee et qui a repris vie),');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 15, 'Il lui fut donne d\'introduire le souffle de vie dans l\'image de la bete, si bien que cette image parla et fit que tous ceux qui ne l\'adoreraient pas, elle, l\'image de la bete, fussent mis a mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 16, 'Et elle fit que tous les hommes, petits et grands, riches et pauvres, libres et esclaves, portaient un signe sur leur main droite ou sur leur front ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 17, 'que personne ne pouvait acheter ni vendre s\'il ne portait le signe de la bete, soit son nom, soit le chiffre de son nom. Ici est la sagesse!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 13, 18, 'Que celui qui a de l\'intelligence calcule le chiffre de la bete ; car c\'est le chiffre d\'un homme ; voici son chiffre: 666');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 1, 'Et je regardai, et je vis l\'agneau debout sur le mont Sion, et avec lui cent quarante-quatre mille personnes qui avaient son nom et le nom de son Pere ecrits sur leurs fronts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 2, 'Et j\'entendis une voix venant du ciel ; c\'etait comme le bruit des grandes eaux et comme le bruit d\'un fort coup de tonnerre ; et la voix que j\'entendais etait aussi comme le son des harpes quand elles sont jouees par des harpistes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 3, 'Et la voix chantait un cantique nouveau devant le trone et devant les quatre animaux, et devant les vieillards. Et personne ne pouvait apprendre ce cantique, si ce n\'est les cent quarante-quatre mille rachetes de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 4, 'Ceux-la ne se sont point souilles avec des femmes ; car ils sont vierges ; ceux-la suivent l\'agneau partout ou il les conduit ; ceux-la ont ete rachetes d\'entre les hommes comme des premices consacrees');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 5, 'a Dieu et a l\'agneau ; et dans leur bouche il ne s\'est point trouve de mensonge ; car ils sont sans taches.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 6, 'Et je vis un autre ange qui volait au zenith, tenant l\'Evangile eternel pour l\'annoncer a tous ceux qui habitaient la terre, et a toute race, tribu, langue et peuple.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 7, 'Il disait d\'une voix eclatante: Craignez Dieu, et rendez-lui gloire, car l\'heure de son jugement est venue, et adorez Celui qui a fait le ciel, et la terre, et la mer, et les sources d\'eaux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 8, 'Et un autre ange encore, un second, le suivit, disant : Elle est tombee, elle est tombee la grande Babylone qui a enivre toutes les nations du vin de feu de sa fornication.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 9, 'Et un autre ange encore, un troisieme, le suivit, disant d\'une voix eclatante: Celui qui adorera la bete et son image, qui prendra le signe de la bete sur son front ou sur sa main,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 10, 'boira du vin brulant de Dieu, du vin pur apprete dans la coupe de sa colere, et il sera tourmente dans le feu et le soufre devant les saints anges et devant l\'agneau ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 11, 'et la fumee de leur tourment monte aux siecles des siecles, et ils n\'ont de repos ni jour ni nuit, ceux qui adorent la bete et son image, et qui prennent sur eux le signe de son nom!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 12, 'C\'est ici que se montre la patience des saints qui gardent les commandements de Dieu et la foi de Jesus!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 13, 'Et j\'entendis une voix venant du ciel qui disait : Ecris: Heureux des a present les morts qui meurent dans le Seigneur! Oui, dit l\'Esprit, c\'est pour qu\'ils se reposent de leurs travaux ; car leurs oeuvres les suivent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 14, 'Et je regardai et voici, un nuage blanc, et sur ce nuage etait assis comme un fils de l\'homme ayant sur la tete une couronne d\'or et dans la main une faux aigue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 15, 'Et un autre ange sortit du Temple criant d\'une voix eclatante a celui qui etait assis sur le nuage: Lance ta faux et moissonne, car l\'heure est venue de moissonner ; la moisson de la terre est mure!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 16, 'Alors celui qui etait assis sur le nuage lanca sa faux sur la terre, et la terre fut moissonnee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 17, 'Et un autre ange sortit du Temple qui est dans le ciel, tenant lui aussi, une faux aigue.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 18, 'Et un autre ange sortit de l\'autel, il avait pouvoir sur le feu, et il appela d\'une voix eclatante celui qui tenait la faux aigue et lui dit: Lance ta faux aigue et vendange les grappes de la vigne de la terre, car ses raisins sont murs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 19, 'Et l\'ange lanca sa faux sur la terre, et il vendangea la vigne de la terre, et il jeta tout dans la grande cuve de la colere de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 14, 20, 'Et la cuve fut foulee aux pieds hors de la ville, et le sang qui sortit de la cuve monta jusqu\'a la hauteur des freins des chevaux, sur un espace de seize cents stades.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 1, 'Et je vis un autre prodige dans le ciel, un grand, un merveilleux prodige: sept anges ayant en main les sept dernieres plaies par lesquelles doit se consommer la colere de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 2, 'Et je vis comme une mer cristalline, melee de feu, et ceux qui avaient ete vainqueurs de la bete, et de son image, et du chiffre de son nom, places sur cette mer cristalline, tenaient les harpes de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 3, 'Et ils chantaient le cantique de Moise, le serviteurs de Dieu, et le cantique de l\'agneau en disant : Grandes et merveilleuses sont tes oeuvres, Seigneur Dieu Tout-Puissant Justes et vraies sont tes voies ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 4, 'roi des nations, Qui ne craindrait, o Seigneur, qui ne glorifierait ton nom? Car toi seul es saint Et toutes les nations viendront se prosterner devant toi, car tes jugements se sont manifestes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 5, 'Et apres cela je regardai, et le Temple du tabernacle celeste du temoignage s\'ouvrit,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 6, 'et les sept anges ayant en main les sept plaies en sortirent vetus de lin pur et eclatant et ceints sur la poitrine de ceintures d\'or.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 7, 'Et l\'un des quatre animaux donna aux sept anges sept coupes d\'or pleines de la colere du Dieu qui vit aux siecles des siecles ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 15, 8, 'et le Temple se remplit de la fumee de la majeste et de la puissance divine, et personne ne pouvait entrer dans le sanctuaire tant que les sept plaies des sept anges ne s\'etaient pas accomplies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 1, 'Et j\'entendis une grande voix qui sortait du Temple et disait aux sept anges : Allez verser sur la terre les sept coupes de la colere de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 2, 'Et le premier ange partit et versa sa coupe sur la terre, et un ulcere mauvais et pernicieux frappa les hommes qui portaient le signe de la bete et qui adoraient son image.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 3, 'Et le deuxieme versa sa coupe dans la mer, et la mer se changea en sang, comme le sang d\'un mort, et tous les etres qui vivent dans la mer moururent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 4, 'Et le troisieme versa sa coupe sur les fleuves et sur les sources d\'eaux ; et elles se changerent en sang');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 5, 'et j\'entendis l\'ange des eaux qui disait: Tu es juste, Etre saint, qui es et qui etais, d\'avoir ainsi juge,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 6, 'car ils ont verse le sang des saints et des prophetes, et tu leur as donne du sang a boire: ils en sont dignes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 7, 'Et j\'entendis l\'autel qui disait : Oui, Seigneur, Dieu Tout-Puissant, tes jugements sont vrais et justes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 8, 'Et le quatrieme versa sa coupe sur le soleil, et il fut donne au soleil de bruler les hommes comme un feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 9, 'Et la grande chaleur brula les hommes, et ils blasphemerent le nom de Dieu qui a le pouvoir de frapper de telles plaies, et ils ne se repentirent pas pour lui rendre gloire.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 10, 'Et le cinquieme versa sa coupe sur le trone de la bete, et le royaume de la bete fut plonge dans les tenebres, et les hommes se mordaient la langue de douleur');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 11, 'et blasphemaient le Dieu du ciel a cause de leur souffrances et de leurs ulceres, et ils ne se repentirent pas de leurs oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 12, 'Et le sixieme versa sa coupe dans le grand fleuve l\'Euphrate, et ses eaux se dessecherent pour preparer la voie aux rois venant de l\'Orient.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 13, 'Et je vis sortir de la bouche du dragon, et de la bouche de la bete, et de la bouche du faux prophete, trois Esprits impurs semblables a des grenouilles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 14, 'Ce sont des Esprits de demons faisant des miracles, qui vont trouver les rois de toute la terre et les rassemblent pour la bataille du grand jour du Dieu Tout-Puissant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 15, '(Voici, je viens comme un voleur: Heureux celui qui veille et qui garde ses vetements, de peur qu\'il ne soit reduit a courir nu, et qu\'on ne voie sa honte.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 16, 'Ils les rassemblent, dis-je, dans le lieu qui est appele en hebreu Harmagedon.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 17, 'Et le septieme versa sa coupe dans l\'air et une grande voix sortit du Temple pres du trone et dit : C\'en est fait!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 18, 'et il y eut des eclairs, des voix, des coups de tonnerre, et un grand ebranlement comme jamais il n\'y en avait eu depuis que l\'homme est sur la terre ; non ; on ne vit jamais un aussi terrible ebranlement.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 19, 'La grande ville se brisa en trois morceaux, et les villes des nations s\'ecroulerent, et la. grande Babylone revint en memoire devant Dieu pour lui faire boire la coupe du vin brulant de sa colere.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 20, 'Et toutes les iles s\'enfuirent, et les montagnes disparurent,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 16, 21, 'et des grelons enormes du poids d\'un talent tomberent du ciel sur les hommes, et les hommes blasphemerent Dieu, a cause de la plaie de la grele ; car c\'etait une terrible plaie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 1, 'Et l\'un des sept anges qui tenaient les sept coupes s\'approcha de moi et me dit: Viens ; je vais te montrer le jugement de la grande prostituee, qui est assise sur de grandes eaux,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 2, 'avec laquelle ont fornique les rois de la terre, et qui a enivre les habitants de la terre du vin de sa fornication.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 3, 'Et il m\'emmena en esprit dans un desert, et je vis une femme assise sur une bete ecarlate, couverte de noms de blaspheme, ayant sept tetes et dix cornes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 4, 'Et cette femme etait vetue de pourpre et d\'ecarlate, couverte d\'or, de pierres precieuses et de perles ; elle tenait a la main une coupe d\'or pleine des abominations et des impuretes de sa fornication.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 5, 'Et sur son front etait ecrit un nom, un mystere: La grande Babylone!, la mere des prostituees et des abominations de la terre!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 6, 'Et je vis la femme enivree du sang des saints et du sang des martyrs de Jesus ; et je fus frappe, a sa vue, d\'un etonnement extreme.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 7, 'Et l\'ange me dit: Pourquoi t\'etonnes-tu? Je vais t\'expliquer le mystere de la femme et de la bete qui la porte, la bete a sept tetes et a dix cornes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 8, 'La bete que tu as vue a ete et n\'est plus, et elle va remonter de l\'abime, puis aller a la perdition. Et les habitants de la terre, dont le nom n\'est pas ecrit dans le livre de vie depuis la creation du monde, seront frappes d\'etonnement en voyant reparaitre la bete qui a ete et qui n\'est plus.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 9, '(C\'est ici qu\'il faut un esprit intelligent!) Les sept tetes sont sept montagnes sur lesquelles la femme est assise ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 10, 'elles sont aussi, sept rois ; cinq sont tombes ; l\'un vit ; l\'autre n\'est pas encore venu ; et quand il sera venu, il doit durer peu de temps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 11, 'Et la bete qui a ete et qui n\'est plus est elle-meme un huitieme roi, et, en meme temps, elle est du nombre des sept, et elle va a la perdition.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 12, 'Et les dix cornes que tu as vues sont dix rois qui n\'ont pas encore recu la royaute, mais qui recoivent pour une heure une puissance egale a celle des rois, et l\'exercent avec la bete.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 13, 'Ils n\'ont tous qu\'un meme avis et mettent au service de la bete leur puissance et leur force.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 14, 'Ils combattront contre l\'agneau, et l\'agneau les vaincra ; car il est le Seigneur des seigneurs et le Roi des rois ; et les appeles, et les elus, et les fideles qui sont avec lui les vaincront aussi.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 15, 'Et il me dit: Les eaux que tu as vues, sur lesquelles la prostituee est assise, sont des peuples, des nations, des races et des langues.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 16, 'Et les dix cornes que tu as vues, et la bete elle-meme hairont la prostituee et la rendront deserte et nue, et ils mangeront ses chairs, et ils la bruleront.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 17, 'Car Dieu leur a mis au coeur d\'executer sa volonte, d\'executer une volonte unique, de donner leur Royaume a la bete jusqu\'a ce que les paroles de Dieu soient accomplies.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 17, 18, 'Et la femme que tu as vue est la grande ville qui exerce la royaute sur les rois de la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 1, 'Apres cela, je vis descendre du ciel un autre ange qui avait une grande puissance, et la terre etait illuminee de sa gloire,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 2, 'et il cria d\'une voix formidable: Elle est tombee, elle est tombee, la grande Babylone, elle est devenue une demeure de demons, un refuge d\'Esprits impurs, un refuge d\'oiseaux immondes et detestes,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 3, 'parce que toutes les nations ont bu du vin de feu de sa fornication, et que les rois de la terre ont fornique avec elle, et que les marchands de la terre se sont enrichis de son opulence.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 4, 'Et j\'entendis une autre voix qui venait du ciel et disait: Sortez d\'elle, o mon peuple, pour ne pas vous rendre complices de ses peches et pour n\'etre pas atteints par les plaies qui vont la frapper.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 5, 'Ses peches se sont accumules jusqu\'au ciel, et Dieu s\'est souvenu de ses iniquites.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 6, 'Faites-lui ce qu\'elle a fait aux autres ; payez-la au double de ses oeuvres, versez-lui a boire le double de la coupe qu\'elle a versee aux autres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 7, 'Autant elle a eu de gloire et d\'opulence, autant donnez-lui de tourment et d\'affliction. Je suis assise en reine, dit-elle en son coeur, je ne suis pas\' veuve, et je ne connaitrai jamais le deuil.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 8, 'Voila pourquoi toutes ses plaies viendront en un meme jour, mort, et desolation, et famine, et destruction par le feu, car puissant est le Seigneur Dieu qui l\'a jugee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 9, 'Et l\'on verra pleurer et se lamenter sur elle les rois de la terre qui ont fornique avec elle et ont eu leur part de son opulence. A la vue de la fumee de son embrasement');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 10, 'et se tenant a distance par crainte de ses tourments: Malheur! malheur! diront-ils, Quoi! la grande ville! Babylone! la puissante ville! en une heure est venu ton jugement!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 11, 'Et les marchands de la terre pleureront et se desoleront sur elle, car personne n\'achete plus leurs marchandises:');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 12, 'marchandises d\'or et d\'argent, et pierres precieuses, et perles, et fin lin, et pourpre, et soie, et ecarlate, et bois de thuia, et objets en ivoire, et objets en bois precieux, et en bronze, et en fer, et en marbre ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 13, 'et cinname, et amome, et parfums, et huiles aromatiques, et encens, et vin, et huile, et fleur de farine, et froment, et boeufs, et brebis, et chevaux, et chars, et corps et ames d\'hommes.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 14, 'Et toute cette moisson de choses que convoitait ton ame s\'en est allee loin de toi, et toutes ces choses brillantes et magnifiques sont perdues pour toi, et tu ne le trouveras plus jamais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 15, 'Les marchands de toutes ces choses qui se sont enrichis par elles, se tenant a distance par crainte de ses tourments, pleurant');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 16, 'et se desolant : Malheur! malheur! diront-ils, Quoi! la grande ville vetue d\'ecarlate, et de pourpre, et de fin lin, paree d\'or et de pierres precieuses et de perles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 17, 'En une heure ont peri tant de richesses! Et les pilotes et les marins qui venaient vers elle, et les matelots, et tous ceux qui trafiquent de la mer, s\'arretant a distance,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 18, 'se repandent en cris a la vue de la fumee de son incendie: Quelle ville, disent-ils, egalait la grande ville?');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 19, 'et ils jettent de la poussiere sur leur tete et se repandent en cris, en pleurs, et en lamentations. Malheur! malheur! disent-ils, grande ville qui enrichissait de ses tresors tous ceux qui avaient des vaisseaux sur la mer ; en une seule heure elle a ete changee en desert!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 20, 'Rejouis-toi de sa ruine, o ciel ; et vous, les saints, et les apotres, et les prophetes, rejouissez-vous, car Dieu a venge votre cause sur elle.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 21, 'Et un ange d\'une force extraordinaire prit une pierre grosse comme une meule et la lanca dans la mer en disant : C\'est avec cette violence que sera precipitee Babylone, la grande ville, et on ne la retrouvera plus jamais.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 22, 'Et la voix des joueurs de harpe, et des musiciens, et des joueurs de flute et de trompette ne resonnera plus dans tes murs ; ni artisan, ni metier ne se trouvera desormais chez toi ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 23, 'le bruit de la meule ne s\'y fera point entendre ; la lumiere de la lampe n\'y brillera plus jamais ; et la voix du fiance et celle de la fiancee ne s\'y feront plus entendre ; car tes marchands etaient les grands de la terre, et ce sont tes enchantements qui ont egare toutes les nations,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 18, 24, 'et c\'est la qu\'on a trouve le sang des prophetes, et des saints, et de tous ceux qui ont ete egorges sur la terre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 1, 'Apres cela, j\'entendis dans le ciel une grande voix comme celle d\'une multitude innombrable disant : Alleluia! Salut, et gloire, et puissance a notre Dieu,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 2, 'car ses jugements sont vrais et justes, et il a juge la grande prostituee qui corrompait la terre par sa prostitution, et il a venge le sang de ses serviteurs verse par sa main!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 3, 'Et une seconde fois, ils dirent : Alleluia! la fumee de son incendie monte aux siecles des siecles!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 4, 'Alors les vingt-quatre vieillards et les quatre animaux se prosternerent et adorerent Dieu, assis sur le trone, disant: Amen! alleluia!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 5, 'Et une voix sortit du trone qui disait : Louez notre Dieu, vous tous qui etes ses serviteurs, et qui le craignez, petits et grands.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 6, 'Et j\'entendis une voix comme celle d\'une multitude innombrable, et comme la voix des grandes eaux, et comme le bruit de forts coups de tonnerre disant : Alleluia! car le Seigneur notre Dieu, le Tout-Puissant est entre dans son regne.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 7, 'Rejouissons-nous, et livrons-nous a l\'allegresse, et rendons-lui gloire, car voici l\'heure des noces de l\'agneau, et son epouse s\'est paree,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 8, 'et il lui a ete donne de revetir une robe de fin lin, brillant du plus pur eclat. (Le fin lin symbolise les actes de vertu des saints.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 9, 'Et il me dit: Ecris: Heureux les invites au festin des noces de l\'agneau. Et il me dit: Ce sont la les veritables paroles de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 10, 'Et je me prosternai a ses pieds pour l\'adorer, et il me dit : Garde-toi de le faire! je suis un serviteur comme toi, et tes freres qui gardent le temoignage de Jesus. Adore Dieu. (En effet, le temoignage de Jesus c\'est l\'esprit de la prophetie.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 11, 'Et je vis le ciel ouvert, et voici, un cheval blanc ; et celui qui le montait s\'appelle fidele et veridique, et il juge et combat avec justice.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 12, 'Ses yeux etaient une flamme ardente et, sur sa tete, plusieurs diademes. Il portait ecrit un nom que nul ne conne que lui-meme,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 13, 'et il etait vetu d\'un manteau teint de sang, et son nom est : La Parole de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 14, 'Et les armees du ciel le suivaient sur des chevaux blancs, revetus d\'un fin lin, blanc et pur.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 15, 'Et de sa bouche sort une epee aigue dont il doit frapper les nations. Et il les gouvernera avec une verge de fer, et il foulera le cuve du vin brulant de la colere du Dieu Tout-Puissant.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 16, 'Et sur son manteau et sur sa cuisse est ecrit le nom : Roi des rois et Seigneur des seigneurs.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 17, 'Et je vis un ange debout dans le soleil, et il cria d\'une voix eclatante a tous les oiseaux qui volaient au zenith : Venez, assemblez-vous pour le grand festin de Dieu ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 18, 'venez manger la chair des rois, et la chair des tribuns, et la chair des forts, et la chair des chevaux et de leurs cavaliers, et la chair de tous, hommes libres et esclaves, petits et grands!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 19, 'Et je vis la bete, et les rois de la terre, et leurs armees reunies pour faire la guerre a celui qui etait assis sur le cheval et a son armee.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 20, 'Et la bete fut saisie, et avec elle le faux prophete qui avait fait des miracles devant elle, par lesquels il avait seduit ceux qui avaient accepte le signe de la bete et qui avaient adore son image ; tous deux furent jetes vivants dans l\'etang de feu ardent et de soufre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 19, 21, 'Et les autres furent tues par le glaive qui sortait de la bouche de celui qui etait assis sur le cheval, et tous les oiseaux se rassasierent de leur chair.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 1, 'Et je vis descendre du ciel un ange, tenant a la main la clef de l\'abime et une grande chaine.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 2, 'Et il saisit le Dragon, le Serpent antique, c\'est-a-dire le Diable, Satan, et le lia pour mille ans,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 3, 'et le precipita dans l\'abime, et en ferma l\'entree sur lui, et la scella d\'un sceau pour qu\'il n\'egarat plus les nations jusqu\'a ce que les mille ans fussent accomplis. Apres cela, il doit etre delivre pour quelque temps.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 4, 'Et je vis des trones, et sur eux s\'assirent des personnages qui furent charges d\'un jugement. Et je vis les ames de ceux qui avaient ete frappes de la hache a cause du temoignage de Jesus et de la parole de Dieu, et les ames de ceux qui n\'avaient adore ni la bete ni son image et qui n\'avaient point recu son signe ni sur leur front ni sur leurs mains. Et ils vecurent et regnerent mille ans avec le Christ.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 5, '(Les autres morts ne vecurent pas avant que les mille ans fussent accomplis.) C\'est la premiere resurrection.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 6, 'Heureux et saints ceux qui participent a la premiere resurrection. Sur eux la seconde mort n\'a point de pouvoir ; mais ils seront pretres de Dieu et du Christ et regneront mille ans avec lui.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 7, 'Et quand les mille ans seront accomplis, Satan sera delivre de sa prison,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 8, 'et il en sortira pour egarer les nations aux quatre coins de la terre, Gog et Magog, et les conduire au combat, armees aussi nombreuses que le sable de la mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 9, 'Et elles se repandirent sur la surface de la terre, et assiegerent le camp des saints, la cite aimee ; mais le feu du ciel descendit sur elles et les devora ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 10, 'et le Diable qui les seduisait fut jete dans l\'etang de feu et de soufre, ou sont aussi la bete et le faux prophete, et ils y seront tourmentes nuit et jour aux siecles des siecles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 11, 'Et je vis un grand trone blanc et Celui qui y etait assis. A sa vue le ciel et la terre s\'enfuirent, et il n\'y eut plus nulle part de place pour eux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 12, 'Et je vis les morts, grands et petits, comparaissant devant le trone, et des livres furent ouverts. On ouvrit aussi un autre livre, le livre de vie. Et les morts furent juges selon leurs oeuvres, d\'apres ce qui etait ecrit dans ces livres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 13, 'Et la mer rendit les morts qu\'elle renfermait ; et la Mort et la Demeure-des-morts rendirent les morts qu\'ils renfermaient, et tous furent juges selon leurs oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 14, 'Puis la Mort et la Demeure-des-morts furent jetes dans l\'etang de feu ; cet etang de feu est la seconde mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 20, 15, 'Et ceux dont les noms ne sont pas trouves ecrits dans le livre de vie sont jetes dans l\'etang de feu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 1, 'Et je vis un ciel nouveau et une terre nouvelle ; car le premier ciel et la premiere terre avaient disparu, et il n\'y avait plus de mer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 2, 'Et je vis la cite sainte, la Jerusalem nouvelle descendre du ciel d\'aupres de Dieu, vetue comme une fiancee paree pour son epoux.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 3, 'Et j\'entendis une grande voix qui sortait du trone, disant : Voici le tabernacle de Dieu avec les hommes! et il habitera avec eux ; et les hommes seront son peuple, et Dieu lui-meme sera avec eux ,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 4, 'et il essuiera toute larme de leurs yeux ; et la mort ne sera plus, et il n\'y aura plus ni douleurs, ni cris, ni peine, car tout ce qui etait a disparu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 5, 'Et Celui qui etait assis sur le trone dit : Voila que je renouvelle toutes choses. Et il dit : Ecris, car ces paroles sont sures et vraies ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 6, 'et il me dit : C\'en est fait! Je suis l\'Alpha et l\'Omega, le commencement et la fin. Celui qui a soif, je le ferai boire gratuitement a la source de vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 7, 'Le vainqueur possedera toutes ces choses: et je serai son Dieu et il sera mon fils.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 8, 'Quant aux timides, et aux incredules, et aux abominables, et aux meurtriers, et aux fornicateurs, et aux auteurs de malefice, et aux idolatres, et a tous les menteurs, leur part c\'est l\'etang ardent de feu et de soufre, la seconde mort.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 9, 'Et l\'un des sept anges qui avaient tenu les sept coupes pleines des sept dernieres plaies, vint a moi et me parla : Viens, dit-il, je vais te montrer la fiancee, l\'epouse de l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 10, 'Et il me transporta en esprit sur une grande et haute montagne, et il me montra la cite sainte, Jerusalem, qui descendait du ciel, d\'aupres de Dieu, penetree de la gloire de Dieu.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 11, 'Son eclat etait celui d\'une pierre precieuse, une pierre de jaspe cristallin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 12, 'Elle avait une grande et haute muraille percee de douze portes, et sur les portes douze anges, et des noms ecrits qui sont les noms des douze tribus des enfants d\'Israel.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 13, 'A l\'Orient, trois portes ; et au Nord, trois portes ; et au Midi, trois portes ; et a l\'Occident, trois portes ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 14, 'et la muraille de la ville avait douze assises de pierre, et sur elles les douze noms des douze apotres de l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 15, 'Et celui qui me parlait tenait une mesure, un baton en or, pour mesurer la ville, ses portes et sa muraille.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 16, 'Et la ville formait un carre, la longueur egale a la largeur. Et la ville mesuree avec le baton avait douze mille stades ; la longueur, la largeur et la hauteur etaient egales.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 17, 'Et il mesura la muraille, elle avait cent quarante-quatre coudees (c\'est d\'une mesure ordinaire que l\'ange se servait).');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 18, 'Et la muraille etait construite en jaspe, et la ville etait d\'un or pur semblable a un verre transparent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 19, 'Les assises de la muraille de la ville etaient ornees de pierres precieuses de toute espece: la premiere de jaspe, la seconde de saphir, la troisieme de calcedoine, la quatrieme d\'emeraude,');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 20, 'la cinquieme de sardoine, la sixieme de cornaline, la septieme de chrysolithe, la huitieme de beryl, la neuvieme de topaze, la dixieme de chrysoprase, la onzieme d\'hyacinthe, la douzieme d\'amethyste.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 21, 'Et les douze portes etaient douze perles ; chaque porte composee d\'une seule perle. Et la place de la ville etait en or pur comme du verre transparent.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 22, 'Et je n\'y vis point de Temple, car le Seigneur Dieu Tout-Puissant en est le Temple ainsi que l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 23, 'Et la ville n\'a besoin ni de soleil, ni de lune pour l\'eclairer, car la gloire de Dieu l\'eclairait et son lustre etait l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 24, 'Et les nations marcheront a sa lumiere, et les rois de la terre lui feront hommage de leur gloire ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 25, 'et ses portes ne se fermeront jamais le jour ; quant a la nuit, il n\'y en aura plus ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 26, 'et l\'on y portera les richesses et les tresors des nations ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 21, 27, 'et il n\'y entrera jamais rien de profane, personne qui se livre a l\'abomination et au mensonge ; seuls ceux qui sont inscrits au livre de vie de l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 1, 'Et l\'ange me montra le fleuve de vie, transparent comme le cristal, sortant du trone de Dieu et de l\'agneau.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 2, 'Au milieu de la place de la ville et sur les bords du fleuve, des deux cotes, se trouve l\'arbre de vie, qui produit douze especes de fruit, qui donne chaque mois une de ces especes, et les feuilles de cet arbre servent a la guerison des nations ;');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 3, 'et il n\'y aura plus d\'anatheme ; et la sera le trone de Dieu et de l\'agneau, et ses serviteurs l\'adoreront');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 4, 'et verront sa face, et son nom sera ecrit sur leurs fronts.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 5, 'Et la nuit ne sera plus ; et on n\'aura besoin ni de la lumiere d\'une lampe, ni de la lumiere du soleil, parce que le Seigneur Dieu les eclairera, et ils regneront aux siecles des siecles.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 6, 'Et il me dit: Ces paroles sont sures et vraies. Et le Seigneur, le Dieu des esprits des prophetes, a envoye son ange pour montrer a ses serviteurs ce qui doit arriver bientot.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 7, 'Et voici je viens bientot. Heureux celui qui garde les paroles de la prophetie de ce livre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 8, 'Et c\'est moi, Jean, qui vis et entendis ces choses et, apres les avoir vues et entendues, je tombai aux pieds de l\'ange, qui me les montrait, pour l\'adorer.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 9, 'Et il me dit: Garde-toi de le faire! Je suis un serviteur comme toit, et comme tes freres les prophetes, et comme ceux qui gardent les paroles de ce livre. Adore Dieu!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 10, 'Et il me dit : Ne scelle pas les discours de la prophetie de ce livre, car le temps est proche!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 11, 'Que l\'injuste devienne plus injuste encore! que celui qui est il souille se souille encore! que le juste pratique encore plus la justice! que le saint se sanctifie encore!');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 12, 'Voici je viens bientot ; et j\'apporte avec moi ma remuneration. Je la decernerai a chacun selon ses oeuvres.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 13, 'Je suis l\'Alpha et l\'Omega, le premier et le dernier, le commencement et la fin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 14, 'Heureux ceux qui lavent leurs robes pour avoir droit a l\'arbre de vie, et pour passer par les portes et entrer dans la ville.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 15, 'Dehors, les chiens, et les artisans de malefices, et les fornicateurs, et les meurtriers, et les idolatres, et quiconque aime et commet le mensonge !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 16, 'Moi, Jesus, j\'ai envoye mon ange pour vous attester ces choses dans les Eglises. Je suis la tige et le rejeton de David, l\'etoile brillante du matin.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 17, 'Viens, disent l\'Esprit et l\'epouse.  Que celui qui entend cet appel dise aussi : Viens. Que celui qui a soif vienne. Que celui qui le veut recoive gratuitement de l\'eau qui donne la vie.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 18, '(Je declare ceci a quiconque entend les paroles de la prophetie renfermee en ce livre : Si quelqu\'un y ajoute quoi que ce soit, Dieu le frappera des plaies decrites en ce livre.');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 19, 'Et si quelqu\'un retranche quoi que ce soit aux discours du livre de cette prophetie, Dieu lui otera sa part de l\'arbre de vie et de la ville sainte, decrits en ce livre.)');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 20, 'Oui, je viens bientot, dit celui qui atteste ces choses. Amen! viens, Seigneur Jesus !');
INSERT INTO `FreStapfer1889_verses` (`book_id`, `chapter`, `verse`, `text`) VALUES (66, 22, 21, 'La grace du Seigneur Jesus soit avec les fideles. ');
